INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (1, 1, 'Dilema Arjuna', 'अर्जुनविषादयोग', 'Arjun Viṣhād Yog', 'Bab pertama Bhagavad Gita - "Arjuna Vishada Yoga" memperkenalkan latar belakang, pengaturan, karakter, dan keadaan yang menyebabkan pertempuran epik Mahabharata, yang diperjuangkan antara Pandawa dan Kurawa. Bab ini menguraikan alasan-alasan yang menyebabkan terungkapnya Bhagavad Gita.\nSaat kedua pasukan berdiri siap untuk pertempuran, prajurit perkasa Arjuna, saat mengamati para prajurit di kedua belah pihak, menjadi semakin sedih dan tertekan karena ketakutan kehilangan kerabat dan teman-temannya serta dosa-dosa yang diakibatkan oleh pembunuhan kerabatnya sendiri. Maka, ia menyerah kepada Dewa Krishna, mencari solusi. Demikianlah, muncullah kebijaksanaan Bhagavad Gita.', 47);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (2, 2, 'Pengetahuan Transendental', 'सांख्ययोग', 'Sānkhya Yog', 'Bab kedua Bhagavad Gita adalah "Sankhya Yoga". Ini adalah bab terpenting Bhagavad Gita karena Dewa Krishna memadatkan ajaran seluruh Gita dalam bab ini. Bab ini adalah esensi dari seluruh Gita.\n"Sankhya Yoga" dapat dikategorikan menjadi 4 topik utama -\n1. Arjuna sepenuhnya menyerahkan diri kepada Dewa Krishna dan menerima posisinya sebagai murid dan Krishna sebagai Gurunya. Ia meminta Krishna untuk membimbingnya tentang bagaimana menghilangkan kesedihannya.\n2. Penjelasan tentang penyebab utama dari semua kesedihan, yaitu ketidaktahuan tentang sifat sejati Diri.\n3. Karma Yoga - disiplin tindakan tanpa pamrih tanpa terikat pada buahnya.\n4. Deskripsi Manusia Sempurna - Orang yang pikirannya tenang dan terfokus.', 72);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (3, 3, 'Jalan Pelayanan Tanpa Pamrih', 'कर्मयोग', 'Karm Yog', 'Bab ketiga Bhagavad Gita adalah "Karma Yoga" atau "Jalan Pelayanan Tanpa Pamrih". Di sini Dewa Krishna menekankan pentingnya karma dalam hidup. Dia mengungkapkan bahwa penting bagi setiap manusia untuk terlibat dalam beberapa jenis aktivitas di dunia material ini. Selanjutnya, dia menjelaskan jenis tindakan yang mengarah pada ikatan dan jenis yang mengarah pada pembebasan. Orang-orang yang terus melakukan tugas masing-masing secara eksternal demi kesenangan Yang Mahatinggi, tanpa terikat pada imbalannya, akan mendapatkan pembebasan pada akhirnya.', 43);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (4, 4, 'Jalan Pengetahuan dan Disiplin Tindakan', 'ज्ञानकर्मसंन्यासयोग', 'Jñāna Karm Sanyās Yog', 'Bab keempat Bhagavad Gita adalah "Jnana Karma Sanyasa Yoga". Dalam bab ini, Krishna memuliakan Karma Yoga dan menyampaikan Pengetahuan Transendental (pengetahuan tentang jiwa dan Kebenaran Tertinggi) kepada Arjuna. Dia mengungkapkan alasan di balik penampilannya di dunia material ini. Dia mengungkapkan bahwa meskipun dia abadi, dia bereinkarnasi dari waktu ke waktu untuk menegakkan kembali dharma dan perdamaian di Bumi ini. Kelahiran dan aktivitasnya bersifat abadi dan tidak pernah tercemar oleh cacat material. Orang-orang yang mengetahui dan memahami Kebenaran ini terlibat dalam pengabdian kepadanya dengan iman penuh dan akhirnya mencapai Dia. Mereka tidak perlu lahir di dunia ini lagi.', 42);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (5, 5, 'Jalan Pelepasan', 'कर्मसंन्यासयोग', 'Karm Sanyās Yog', 'Bab kelima Bhagavad Gita adalah "Karma Sanyasa Yoga". Dalam bab ini, Krishna membandingkan jalan pelepasan dalam tindakan (Karma Sanyas) dan tindakan dengan ketidakmelekatan (Karma Yoga) serta menjelaskan bahwa keduanya adalah sarana untuk mencapai tujuan yang sama dan kita dapat memilih salah satunya. Orang yang bijaksana seharusnya melakukan tugas duniawinya tanpa terikat pada hasil tindakannya dan mendedikasikannya kepada Tuhan. Dengan cara ini, mereka tetap tidak terpengaruh oleh dosa dan akhirnya mencapai pembebasan.', 29);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (6, 6, 'Jalan Meditasi', 'ध्यानयोग', 'Dhyān Yog', 'Bab keenam Bhagavad Gita adalah "Dhyana Yoga". Dalam bab ini, Krishna mengungkapkan "Yoga Meditasi" dan cara mempraktikkan Yoga ini. Dia membahas peran tindakan dalam mempersiapkan diri untuk Meditasi, bagaimana melakukan tugas dalam pengabdian memurnikan pikiran seseorang dan meningkatkan kesadaran spiritual seseorang. Dia menjelaskan secara rinci rintangan yang dihadapi seseorang saat mencoba mengendalikan pikiran mereka dan metode yang tepat yang dengannya seseorang dapat menaklukkan pikiran mereka. Dia mengungkapkan bagaimana seseorang dapat memusatkan pikiran mereka pada Paramatma dan bersatu dengan Tuhan.', 47);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (7, 7, 'Pengetahuan Diri dan Pencerahan', 'ज्ञानविज्ञानयोग', 'Jñāna Vijñāna Yog', 'Bab ketujuh Bhagavad Gita adalah "Gyaan Vigyana Yoga". Dalam bab ini, Krishna mengungkapkan bahwa Dia adalah Kebenaran Tertinggi, penyebab utama dan kekuatan penopang segalanya. Dia mengungkapkan energi ilusinya di dunia material ini yang disebut Maya, yang sangat sulit diatasi, tetapi mereka yang menyerahkan pikiran mereka kepada-Nya akan mencapai Dia dengan mudah. Dia juga menjelaskan empat jenis orang yang menyerah kepada-Nya dalam pengabdian dan empat jenis yang tidak. Krishna menegaskan bahwa Dia adalah Realitas Utama dan mereka yang menyadari Kebenaran ini mencapai puncak realisasi spiritual dan bersatu dengan Tuhan.', 30);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (8, 8, 'Jalan Tuhan Yang Abadi', 'अक्षरब्रह्मयोग', 'Akṣhar Brahma Yog', 'Bab kedelapan Bhagavad Gita adalah "Akshara Brahma Yoga". Dalam bab ini, Krishna mengungkapkan pentingnya pikiran terakhir sebelum kematian. Jika kita dapat mengingat Krishna pada saat kematian, kita pasti akan mencapai-Nya. Oleh karena itu, sangat penting untuk senantiasa menyadari Tuhan setiap saat, memikirkan-Nya dan melantunkan Nama-Nya setiap saat. Dengan menyerap pikiran mereka sepenuhnya dalam Dia melalui pengabdian yang konstan, seseorang dapat melampaui keberadaan material ini ke tempat tinggal tertinggi Tuhan.', 28);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (9, 9, 'Yoga melalui Raja Ilmu Pengetahuan', 'राजविद्याराजगुह्ययोग', 'Rāja Vidyā Yog', 'Bab kesembilan Bhagavad Gita adalah "Raja Vidya Yoga". Dalam bab ini, Krishna menjelaskan bahwa Dia adalah Yang Mahatinggi dan bagaimana keberadaan material ini diciptakan, dipelihara, dan dihancurkan oleh Yogamaya-Nya dan semua makhluk datang dan pergi di bawah pengawasan-Nya. Dia mengungkapkan Peran dan Pentingnya Bhakti (pelayanan bakti transendental) menuju Kebangkitan Spiritual kita. Dalam pengabdian tersebut, seseorang harus hidup untuk Tuhan, mempersembahkan segala yang dimilikinya kepada-Nya dan melakukan segalanya hanya untuk-Nya. Orang yang mengikuti pengabdian tersebut menjadi bebas dari ikatan dunia material ini dan bersatu dengan Tuhan.', 34);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (10, 10, 'Yoga melalui Apresiasi Kemuliaan Ilahi Tak Terbatas', 'विभूतियोग', 'Vibhūti Yog', 'Bab kesepuluh Bhagavad Gita adalah "Vibhooti Yoga". Dalam bab ini, Krishna mengungkapkan Diri-Nya sebagai penyebab dari semua penyebab. Dia menjelaskan berbagai manifestasi dan kemuliaan-Nya untuk meningkatkan Bhakti Arjuna. Arjuna sepenuhnya yakin akan posisi Tuhan yang utama dan menyatakan Dia sebagai Pribadi Tertinggi. Dia berdoa kepada Krishna untuk menjelaskan lebih banyak tentang kemuliaan ilahi-Nya yang seperti nektar untuk didengar.', 42);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (11, 11, 'Yoga melalui Melihat Wujud Kosmis Tuhan', 'विश्वरूपदर्शनयोग', 'Viśhwarūp Darśhan Yog', 'Bab kesebelas Bhagavad Gita adalah "Vishwaroopa Darshana Yoga". Dalam bab ini, Arjuna meminta Krishna untuk menyingkapkan Wujud Kosmis Universal-Nya yang meliputi semua alam semesta, seluruh keberadaan. Arjuna dianugerahi penglihatan ilahi untuk dapat melihat keseluruhan ciptaan dalam tubuh Tuhan Tertinggi Krishna.', 55);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (12, 12, 'Yoga Pengabdian', 'भक्तियोग', 'Bhakti Yog', 'Bab kedua belas Bhagavad Gita adalah "Bhakti Yoga". Dalam bab ini, Krishna menekankan keunggulan Bhakti Yoga (jalan pengabdian) di atas semua jenis disiplin spiritual lainnya dan mengungkapkan berbagai aspek pengabdian. Dia lebih lanjut menjelaskan bahwa para pemuja yang melakukan pelayanan bakti murni kepada-Nya, dengan kesadaran mereka yang menyatu dalam Diri-Nya dan semua tindakan mereka didedikasikan kepada-Nya, dengan cepat dibebaskan dari siklus hidup dan mati. Dia juga menjelaskan berbagai kualitas para pemuja yang sangat dicintai-Nya.', 20);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (13, 13, 'Yoga melalui Membedakan Medan dan Pengenal Medan', 'क्षेत्र-क्षेत्रज्ञविभागयोग', 'Kṣhetra Kṣhetrajña Vibhāg Yog', 'Bab ketiga belas Bhagavad Gita adalah "Ksetra Ksetrajna Vibhaaga Yoga". Kata "kshetra" berarti "ladang", dan "kshetrajna" berarti "pengenal ladang". Kita dapat menganggap tubuh material kita sebagai ladang dan jiwa abadi kita sebagai pengenal ladang. Dalam bab ini, Krishna membedakan antara tubuh fisik dan jiwa abadi. Dia menjelaskan bahwa tubuh fisik bersifat sementara dan fana, sedangkan jiwa bersifat permanen dan abadi. Tubuh fisik dapat dihancurkan tetapi jiwa tidak akan pernah bisa dihancurkan. Bab ini kemudian menjelaskan Tuhan, yang adalah Jiwa Tertinggi. Semua jiwa individu berasal dari Jiwa Tertinggi. Seseorang yang memahami dengan jelas perbedaan antara tubuh, Jiwa, dan Jiwa Tertinggi akan mencapai realisasi Brahman.', 35);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (14, 14, 'Yoga melalui Pemahaman Tiga Guna Alam Material', 'गुणत्रयविभागयोग', 'Guṇa Traya Vibhāg Yog', 'Bab keempat belas Bhagavad Gita adalah "Gunatraya Vibhaga Yoga". Dalam bab ini, Krishna mengungkapkan tiga guna (mode) dari alam material – kebaikan, nafsu, dan kebodohan yang mempengaruhi segala sesuatu dalam keberadaan material. Dia lebih lanjut menjelaskan karakteristik esensial dari setiap mode ini, penyebabnya dan bagaimana mereka mempengaruhi entitas hidup yang terpengaruh olehnya. Dia kemudian mengungkapkan berbagai karakteristik orang yang telah melampaui guna-guna ini. Bab ini diakhiri dengan Krishna mengingatkan kita tentang kekuatan pengabdian murni kepada Tuhan dan bagaimana keterikatan pada Tuhan dapat membantu kita melampaui guna-guna ini.', 27);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (15, 15, 'Yoga dari Pribadi Ilahi Tertinggi', 'पुरुषोत्तमयोग', 'Puruṣhottam Yog', 'Bab kelima belas Bhagavad Gita adalah "Purushottama Yoga". Dalam bahasa Sansekerta, Purusha berarti "Tuhan Yang Mahahadir", dan Purushottam berarti aspek Tuhan yang abadi & transendental. Krishna mengungkapkan bahwa tujuan dari pengetahuan Transendental tentang Tuhan ini adalah untuk melepaskan diri dari ikatan dunia material dan untuk memahami Krishna sebagai Pribadi Ilahi Tertinggi, yang merupakan pengendali dan penopang abadi dunia. Seseorang yang memahami Kebenaran Utama ini menyerah kepada-Nya dan terlibat dalam pelayanan bakti kepada-Nya.', 20);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (16, 16, 'Yoga melalui Membedakan Sifat Ilahi dan Setan', 'दैवासुरसम्पद्विभागयोग', 'Daivāsura Sampad Vibhāg Yog', 'Bab keenam belas Bhagavad Gita adalah "Daivasura Sampad Vibhaga Yoga". Dalam bab ini, Krishna menjelaskan secara eksplisit dua jenis sifat di antara manusia - ilahi dan setan. Mereka yang memiliki kualitas setan mengasosiasikan diri dengan mode nafsu dan kebodohan tidak mengikuti peraturan kitab suci dan menganut pandangan materialistis. Orang-orang ini mencapai kelahiran yang lebih rendah dan ikatan material lebih lanjut. Tetapi orang-orang yang memiliki kualitas ilahi, mengikuti instruksi kitab suci, mengasosiasikan diri dengan mode kebaikan dan memurnikan pikiran melalui praktik spiritual. Ini mengarah pada peningkatan kualitas ilahi dan mereka akhirnya mencapai realisasi spiritual.', 24);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (17, 17, 'Yoga melalui Membedakan Tiga Pembagian Iman', 'श्रद्धात्रयविभागयोग', 'Śhraddhā Traya Vibhāg Yog', 'Bab ketujuh belas Bhagavad Gita adalah "Sraddhatraya Vibhaga Yoga". Dalam bab ini, Krishna menjelaskan tiga jenis iman yang sesuai dengan tiga mode alam material. Dewa Krishna lebih lanjut mengungkapkan bahwa sifat imanlah yang menentukan kualitas hidup dan karakter makhluk hidup. Mereka yang memiliki iman dalam nafsu dan kebodohan melakukan tindakan yang menghasilkan hasil material sementara, sementara mereka yang memiliki iman dalam kebaikan melakukan tindakan sesuai dengan instruksi kitab suci dan karenanya hati mereka semakin dimurnikan.', 28);
INSERT INTO chapters (id, chapter_number, name, name_hindi, name_sanskrit, summary, verses_count) VALUES (18, 18, 'Yoga melalui Kesempurnaan Pelepasan dan Penyerahan Diri', 'मोक्षसंन्यासयोग', 'Mokṣha Sanyās Yog', 'Bab kedelapan belas Bhagavad Gita adalah "Moksha Sanyas Yoga". Arjuna meminta Tuhan untuk menjelaskan perbedaan antara dua jenis pelepasan - sanyas (pelepasan tindakan) dan tyaag (pelepasan keinginan). Krishna menjelaskan bahwa seorang sanyasi adalah orang yang meninggalkan keluarga dan masyarakat untuk mempraktikkan disiplin spiritual, sedangkan seorang tyaagi adalah orang yang melakukan tugasnya tanpa terikat pada hasil tindakannya dan mendedikasikannya kepada Tuhan. Krishna merekomendasikan jenis pelepasan kedua - tyaag. Krishna kemudian memberikan analisis rinci tentang efek tiga mode alam material. Dia menyatakan bahwa jalan spiritualitas tertinggi adalah pelayanan cinta yang murni dan tanpa syarat kepada Pribadi Ilahi Tertinggi, Krishna. Jika kita selalu mengingat Dia, terus melantunkan Nama-Nya dan mendedikasikan semua tindakan kita kepada-Nya, berlindung kepada-Nya dan menjadikan-Nya tujuan tertinggi kita, maka dengan rahmat-Nya, kita pasti akan mengatasi semua rintangan dan kesulitan dan dibebaskan dari siklus kelahiran dan kematian ini.', 78);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (1, 'धृतराष्ट्र उवाच\n\nधर्मक्षेत्रे कुरुक्षेत्रे समवेता युयुत्सवः।\n\nमामकाः पाण्डवाश्चैव किमकुर्वत सञ्जय।।1.1।।\n ', 'dhṛitarāśhtra uvācha\ndharma-kṣhetre kuru-kṣhetre samavetā yuyutsavaḥ\nmāmakāḥ pāṇḍavāśhchaiva kimakurvata sañjaya\n', 'dhṛitarāśhtraḥ uvācha—Dhritarashtra said; dharma-kṣhetre—the land of dharma; kuru-kṣhetre—at Kurukshetra; samavetāḥ—having gathered; yuyutsavaḥ—desiring to fight; māmakāḥ—my sons; pāṇḍavāḥ—the sons of Pandu; cha—and; eva—certainly; kim—what; akurvata—did they do; sañjaya—Sanjay\n', 'https://gita.github.io/gita/data/verse_recitation/1/1.mp3', 1, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (2, 'सञ्जय उवाच\n\nदृष्ट्वा तु पाण्डवानीकं व्यूढं दुर्योधनस्तदा।\n\nआचार्यमुपसङ्गम्य राजा वचनमब्रवीत्।।1.2।।\n ', 'sañjaya uvācha\ndṛiṣhṭvā tu pāṇḍavānīkaṁ vyūḍhaṁ duryodhanastadā\nāchāryamupasaṅgamya rājā vachanamabravīt\n', 'sanjayaḥ uvācha—Sanjay said; dṛiṣhṭvā—on observing; tu—but; pāṇḍava-anīkam—the Pandava army; vyūḍham—standing in a military formation; duryodhanaḥ—King Duryodhan; tadā—then; āchāryam—teacher; upasaṅgamya—approached; rājā—the king; vachanam—words; abravīt—spoke\n', 'https://gita.github.io/gita/data/verse_recitation/1/2.mp3', 1, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (3, 'पश्यैतां पाण्डुपुत्राणामाचार्य महतीं चमूम्।\n\nव्यूढां द्रुपदपुत्रेण तव शिष्येण धीमता।।1.3।।\n ', 'paśhyaitāṁ pāṇḍu-putrāṇām āchārya mahatīṁ chamūm\nvyūḍhāṁ drupada-putreṇa tava śhiṣhyeṇa dhīmatā\n', 'paśhya—behold; etām—this; pāṇḍu-putrāṇām—of the sons of Pandu; āchārya—respected teacher; mahatīm—mighty; chamūm—army; vyūḍhām—arrayed in a military formation; drupada-putreṇa—son of Drupad, Dhrishtadyumna; tava—by your; śhiṣhyeṇa—disciple; dhī-matā—intelligent\n', 'https://gita.github.io/gita/data/verse_recitation/1/3.mp3', 1, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (4, 'अत्र शूरा महेष्वासा भीमार्जुनसमा युधि।\n\nयुयुधानो विराटश्च द्रुपदश्च महारथः।।1.4।।\n ', 'atra śhūrā maheṣhvāsā bhīmārjuna-samā yudhi\nyuyudhāno virāṭaśhcha drupadaśhcha mahā-rathaḥ\n', 'atra—here; śhūrāḥ—powerful warriors; mahā-iṣhu-āsāḥ—great bowmen; bhīma-arjuna-samāḥ—equal to Bheem and Arjun; yudhi—in military prowess; yuyudhānaḥ—Yuyudhan; virāṭaḥ—Virat; cha—and; drupadaḥ—Drupad; cha—also; mahā-rathaḥ—warriors who could single handedly match the strength of ten thousand ordinary warriors;\n', 'https://gita.github.io/gita/data/verse_recitation/1/4.mp3', 1, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (5, 'धृष्टकेतुश्चेकितानः काशिराजश्च वीर्यवान्।\n\nपुरुजित्कुन्तिभोजश्च शैब्यश्च नरपुङ्गवः।।1.5।।\n ', 'dhṛiṣhṭaketuśhchekitānaḥ kāśhirājaśhcha vīryavān\npurujit kuntibhojaśhcha śhaibyaśhcha nara-puṅgavaḥ\nyudhāmanyuśhcha vikrānta uttamaujāśhcha vīryavān\n', 'dhṛiṣhṭaketuḥ—Dhrishtaketu; chekitānaḥ—Chekitan; kāśhirājaḥ—Kashiraj; cha—and; vīrya-vān—heroic; purujit—Purujit; kuntibhojaḥ—Kuntibhoj; cha—and; śhaibyaḥ—Shaibya; cha—and; nara-puṅgavaḥ—best of men; yudhāmanyuḥ—Yudhamanyu; cha—and; vikrāntaḥ—courageous; uttamaujāḥ—Uttamauja; cha—and; vīrya-vān—gallant;\n', 'https://gita.github.io/gita/data/verse_recitation/1/5.mp3', 1, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (6, 'युधामन्युश्च विक्रान्त उत्तमौजाश्च वीर्यवान्।\n\nसौभद्रो द्रौपदेयाश्च सर्व एव महारथाः।।1.6।।\n ', 'saubhadro draupadeyāśhcha sarva eva mahā-rathāḥ\n', 'saubhadraḥ—the son of Subhadra; draupadeyāḥ—the sons of Draupadi; cha—and; sarve—all; eva—indeed; mahā-rathāḥ—warriors who could single handedly match the strength of ten thousand ordinary warriors\n', 'https://gita.github.io/gita/data/verse_recitation/1/6.mp3', 1, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (7, 'अस्माकं तु विशिष्टा ये तान्निबोध द्विजोत्तम।\n\nनायका मम सैन्यस्य संज्ञार्थं तान्ब्रवीमि ते।।1.7।।\n ', 'asmākaṁ tu viśhiṣhṭā ye tānnibodha dwijottama\nnāyakā mama sainyasya sanjñārthaṁ tānbravīmi te\n', 'asmākam—ours; tu—but; viśhiṣhṭāḥ—special; ye—who; tān—them; nibodha—be informed; dwija-uttama—best of Brahmnis; nāyakāḥ—principal generals; mama—our; sainyasya—of army; sanjñā-artham—for information; tān—them; bravīmi—I recount; te—unto you\n', 'https://gita.github.io/gita/data/verse_recitation/1/7.mp3', 1, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (8, 'भवान्भीष्मश्च कर्णश्च कृपश्च समितिञ्जयः।\n\nअश्वत्थामा विकर्णश्च सौमदत्तिस्तथैव च।।1.8।।\n ', 'bhavānbhīṣhmaśhcha karṇaśhcha kṛipaśhcha samitiñjayaḥ\naśhvatthāmā vikarṇaśhcha saumadattis tathaiva cha\n', 'bhavān—yourself; bhīṣhmaḥ—Bheeshma; cha—and; karṇaḥ—Karna; cha—and; kṛipaḥ—Kripa; cha—and; samitim-jayaḥ—victorious in battle; aśhvatthāmā—Ashvatthama; vikarṇaḥ—Vikarna; cha—and; saumadattiḥ—Bhurishrava; tathā—thus; eva—even; cha—also\n', 'https://gita.github.io/gita/data/verse_recitation/1/8.mp3', 1, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (9, 'अन्ये च बहवः शूरा मदर्थे त्यक्तजीविताः।\n\nनानाशस्त्रप्रहरणाः सर्वे युद्धविशारदाः।।1.9।।\n ', 'anye cha bahavaḥ śhūrā madarthe tyaktajīvitāḥ\nnānā-śhastra-praharaṇāḥ sarve yuddha-viśhāradāḥ\n', 'anye—others; cha—also; bahavaḥ—many; śhūrāḥ—heroic warriors; mat-arthe—for my sake; tyakta-jīvitāḥ—prepared to lay down their lives; nānā-śhastra-praharaṇāḥ—equipped with various kinds of weapons; sarve—all; yuddha-viśhāradāḥ—skilled in the art of warfare\n', 'https://gita.github.io/gita/data/verse_recitation/1/9.mp3', 1, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (10, 'अपर्याप्तं तदस्माकं बलं भीष्माभिरक्षितम्।\n\nपर्याप्तं त्विदमेतेषां बलं भीमाभिरक्षितम्।।1.10।।\n ', 'aparyāptaṁ tadasmākaṁ balaṁ bhīṣhmābhirakṣhitam\nparyāptaṁ tvidameteṣhāṁ balaṁ bhīmābhirakṣhitam\n', 'aparyāptam—unlimited; tat—that; asmākam—ours; balam—strength; bhīṣhma—by Grandsire Bheeshma; abhirakṣhitam—safely marshalled; paryāptam—limited; tu—but; idam—this; eteṣhām—their; balam—strength; bhīma—Bheem; abhirakṣhitam—carefully marshalled\n', 'https://gita.github.io/gita/data/verse_recitation/1/10.mp3', 1, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (11, 'अयनेषु च सर्वेषु यथाभागमवस्थिताः।\n\nभीष्ममेवाभिरक्षन्तु भवन्तः सर्व एव हि।।1.11।।\n ', 'ayaneṣhu cha sarveṣhu yathā-bhāgamavasthitāḥ\nbhīṣhmamevābhirakṣhantu bhavantaḥ sarva eva hi\n', 'ayaneṣhu—at the strategic points; cha—also; sarveṣhu—all; yathā-bhāgam—in respective position; avasthitāḥ—situated; bhīṣhmam—to Grandsire Bheeshma; eva—only; abhirakṣhantu—defend; bhavantaḥ—you; sarve—all; eva hi—even as\n', 'https://gita.github.io/gita/data/verse_recitation/1/11.mp3', 1, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (12, 'तस्य संजनयन्हर्षं कुरुवृद्धः पितामहः।\n\nसिंहनादं विनद्योच्चैः शङ्खं दध्मौ प्रतापवान्।।1.12।।\n ', 'tasya sañjanayan harṣhaṁ kuru-vṛiddhaḥ pitāmahaḥ\nsiṁha-nādaṁ vinadyochchaiḥ śhaṅkhaṁ dadhmau pratāpavān\n', 'tasya—his; sañjanayan—causing; harṣham—joy; kuru-vṛiddhaḥ—the grand old man of the Kuru dynasty (Bheeshma); pitāmahaḥ—grandfather; sinha-nādam—lion’s roar; vinadya—sounding; uchchaiḥ—very loudly; śhaṅkham—conch shell; dadhmau—blew; pratāpa-vān—the glorious\n', 'https://gita.github.io/gita/data/verse_recitation/1/12.mp3', 1, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (13, 'ततः शङ्खाश्च भेर्यश्च पणवानकगोमुखाः।\n\nसहसैवाभ्यहन्यन्त स शब्दस्तुमुलोऽभवत्।।1.13।।\n ', 'tataḥ śhaṅkhāśhcha bheryaśhcha paṇavānaka-gomukhāḥ\nsahasaivābhyahanyanta sa śhabdastumulo ’bhavat\n', 'tataḥ—thereafter; śhaṅkhāḥ—conches; cha—and; bheryaḥ—bugles; cha—and; paṇava-ānaka—drums and kettledrums; go-mukhāḥ—trumpets; sahasā—suddenly; eva—indeed; abhyahanyanta—blared forth; saḥ—that; śhabdaḥ—sound; tumulaḥ—overwhelming; abhavat—was\n', 'https://gita.github.io/gita/data/verse_recitation/1/13.mp3', 1, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (14, 'ततः श्वेतैर्हयैर्युक्ते महति स्यन्दने स्थितौ।\n\nमाधवः पाण्डवश्चैव दिव्यौ शङ्खौ प्रदध्मतुः।।1.14।।\n ', 'tataḥ śhvetairhayairyukte mahati syandane sthitau\nmādhavaḥ pāṇḍavaśhchaiva divyau śhaṅkhau pradadhmatuḥ\n', 'tataḥ—then; śhvetaiḥ—by white; hayaiḥ—horses; yukte—yoked; mahati—glorious; syandane—chariot; sthitau—seated; mādhavaḥ—Shree Krishna, the husband of the goddess of fortune, Lakshmi; pāṇḍavaḥ—Arjun; cha—and; eva—also; divyau—Divine; śhaṅkhau—conch shells; pradadhmatuḥ—blew\n', 'https://gita.github.io/gita/data/verse_recitation/1/14.mp3', 1, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (15, 'पाञ्चजन्यं हृषीकेशो देवदत्तं धनंजयः।\n\nपौण्ड्रं दध्मौ महाशङ्खं भीमकर्मा वृकोदरः।।1.15।।\n ', 'pāñchajanyaṁ hṛiṣhīkeśho devadattaṁ dhanañjayaḥ\npauṇḍraṁ dadhmau mahā-śhaṅkhaṁ bhīma-karmā vṛikodaraḥ\n', 'pāñchajanyam—the conch shell named Panchajanya; hṛiṣhīka-īśhaḥ—Shree Krishna, the Lord of the mind and senses; devadattam—the conch shell named Devadutta; dhanam-jayaḥ—Arjun, the winner of wealth; pauṇḍram—the conch named Paundra; dadhmau—blew; mahā-śhaṅkham—mighty conch; bhīma-karmā—one who performs herculean tasks; vṛika-udaraḥ—Bheem, the voracious eater\n', 'https://gita.github.io/gita/data/verse_recitation/1/15.mp3', 1, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (16, 'अनन्तविजयं राजा कुन्तीपुत्रो युधिष्ठिरः।\n\nनकुलः सहदेवश्च सुघोषमणिपुष्पकौ।।1.16।।\n ', 'anantavijayaṁ rājā kuntī-putro yudhiṣhṭhiraḥ\nnakulaḥ sahadevaśhcha sughoṣha-maṇipuṣhpakau\n', 'ananta-vijayam—the conch named Anantavijay; rājā—king; kuntī-putraḥ—son of Kunti; yudhiṣhṭhiraḥ—Yudhishthir; nakulaḥ—Nakul; sahadevaḥ—Sahadev; cha—and; sughoṣha-maṇipuṣhpakau—the conche shells named Sughosh and Manipushpak;\n', 'https://gita.github.io/gita/data/verse_recitation/1/16.mp3', 1, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (17, 'काश्यश्च परमेष्वासः शिखण्डी च महारथः।\n\nधृष्टद्युम्नो विराटश्च सात्यकिश्चापराजितः।।1.17।।\n ', 'kāśhyaśhcha parameṣhvāsaḥ śhikhaṇḍī cha mahā-rathaḥ\ndhṛiṣhṭadyumno virāṭaśhcha sātyakiśh chāparājitaḥ\n', 'kāśhyaḥ—King of Kashi; cha—and; parama-iṣhu-āsaḥ—the excellent archer; śhikhaṇḍī—Shikhandi; cha—also; mahā-rathaḥ—warriors who could single handedly match the strength of ten thousand ordinary warriors; dhṛiṣhṭadyumnaḥ—Dhrishtadyumna; virāṭaḥ—Virat; cha—and; sātyakiḥ—Satyaki; cha—and; aparājitaḥ—invincible;\n', 'https://gita.github.io/gita/data/verse_recitation/1/17.mp3', 1, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (18, 'द्रुपदो द्रौपदेयाश्च सर्वशः पृथिवीपते।\n\nसौभद्रश्च महाबाहुः शङ्खान्दध्मुः पृथक्पृथक्।।1.18।।\n ', 'drupado draupadeyāśhcha sarvaśhaḥ pṛithivī-pate\nsaubhadraśhcha mahā-bāhuḥ śhaṅkhāndadhmuḥ pṛithak pṛithak\n', 'drupadaḥ—Drupad; draupadeyāḥ—the five sons of Draupadi; cha—and; sarvaśhaḥ—all; pṛithivī-pate—Ruler of the earth; saubhadraḥ—Abhimanyu, the son of Subhadra; cha—also; mahā-bāhuḥ—the mighty-armed; śhaṅkhān—conch shells; dadhmuḥ—blew; pṛithak pṛithak—individually\n', 'https://gita.github.io/gita/data/verse_recitation/1/18.mp3', 1, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (19, 'स घोषो धार्तराष्ट्राणां हृदयानि व्यदारयत्।\n\nनभश्च पृथिवीं चैव तुमुलो व्यनुनादयन्।।1.19।।\n ', 'sa ghoṣho dhārtarāṣhṭrāṇāṁ hṛidayāni vyadārayat\nnabhaśhcha pṛithivīṁ chaiva tumulo nunādayan\n', 'saḥ—that; ghoṣhaḥ—sound; dhārtarāṣhṭrāṇām—of Dhritarashtra’s sons; hṛidayāni—hearts; vyadārayat—shattered; nabhaḥ—the sky; cha—and; pṛithivīm—the earth; cha—and; eva—certainly; tumulaḥ—terrific sound; abhyanunādayan—thundering\n', 'https://gita.github.io/gita/data/verse_recitation/1/19.mp3', 1, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (20, 'अथ व्यवस्थितान् दृष्ट्वा धार्तराष्ट्रान्कपिध्वजः।\n\nप्रवृत्ते शस्त्रसंपाते धनुरुद्यम्य पाण्डवः।।1.20।।\n ', 'atha vyavasthitān dṛiṣhṭvā dhārtarāṣhṭrān kapi-dhwajaḥ\npravṛitte śhastra-sampāte dhanurudyamya pāṇḍavaḥ\nhṛiṣhīkeśhaṁ tadā vākyam idam āha mahī-pate\n', 'atha—thereupon; vyavasthitān—arrayed; dṛiṣhṭvā—seeing; dhārtarāṣhṭrān—Dhritarashtra’s sons; kapi-dwajaḥ—the Monkey Bannered; pravṛitte—about to commence; śhastra-sampāte—to use the weapons; dhanuḥ—bow; udyamya—taking up; pāṇḍavaḥ—Arjun, the son of Pandu; hṛiṣhīkeśham—to Shree Krishna; tadā—at that time; vākyam—words; idam—these; āha—said; mahī-pate—King\n', 'https://gita.github.io/gita/data/verse_recitation/1/20.mp3', 1, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (21, 'अर्जुन उवाच\n\nहृषीकेशं तदा वाक्यमिदमाह महीपते।\n\nसेनयोरुभयोर्मध्ये रथं स्थापय मेऽच्युत।।1.21।।\n ', 'arjuna uvācha\nsenayor ubhayor madhye rathaṁ sthāpaya me ’chyuta\n', 'arjunaḥ uvācha—Arjun said; senayoḥ—armies; ubhayoḥ—both; madhye—in the middle; ratham—chariot; sthāpaya—place; me—my; achyuta—Shree Krishna, the infallible One;\n', 'https://gita.github.io/gita/data/verse_recitation/1/21.mp3', 1, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (22, 'यावदेतान्निरीक्षेऽहं योद्धुकामानवस्थितान्।\n\nकैर्मया सह योद्धव्यमस्मिन्रणसमुद्यमे।।1.22।।\n ', 'yāvadetān nirīkṣhe ’haṁ yoddhu-kāmān avasthitān\nkairmayā saha yoddhavyam asmin raṇa-samudyame\n', 'yāvat—as many as; etān—these; nirīkṣhe—look; aham—I; yoddhu-kāmān—for the battle; avasthitān—arrayed; kaiḥ—with whom; mayā—by me; saha—together; yoddhavyam—must fight; asmin—in this; raṇa-samudyame—great combat\n', 'https://gita.github.io/gita/data/verse_recitation/1/22.mp3', 1, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (23, 'योत्स्यमानानवेक्षेऽहं य एतेऽत्र समागताः।\n\nधार्तराष्ट्रस्य दुर्बुद्धेर्युद्धे प्रियचिकीर्षवः।।1.23।।\n ', 'yotsyamānān avekṣhe ’haṁ ya ete ’tra samāgatāḥ\ndhārtarāṣhṭrasya durbuddher yuddhe priya-chikīrṣhavaḥ\n', 'yotsyamānān—those who have come to fight; avekṣhe aham—I desire to see; ye—who; ete—those; atra—here; samāgatāḥ—assembled; dhārtarāṣhṭrasya—of Dhritarashtra’s son; durbuddheḥ—evil-minded; yuddhe—in the fight; priya-chikīrṣhavaḥ—wishing to please\n', 'https://gita.github.io/gita/data/verse_recitation/1/23.mp3', 1, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (24, 'संजय उवाच\n\nएवमुक्तो हृषीकेशो गुडाकेशेन भारत।\n\nसेनयोरुभयोर्मध्ये स्थापयित्वा रथोत्तमम्।।1.24।।\n ', 'sañjaya uvācha\nevam ukto hṛiṣhīkeśho guḍākeśhena bhārata\nsenayor ubhayor madhye sthāpayitvā rathottamam\n', 'sañjayaḥ uvācha—Sanjay said; evam—thus; uktaḥ—addressed; hṛiṣhīkeśhaḥ—Shree Krishna, the Lord of the senses; guḍākeśhena—by Arjun, the conqueror of sleep; bhārata—descendant of Bharat; senayoḥ—armies; ubhayoḥ—the two; madhye—between; sthāpayitvā—having drawn; ratha-uttamam—magnificent chariot\n', 'https://gita.github.io/gita/data/verse_recitation/1/24.mp3', 1, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (25, 'भीष्मद्रोणप्रमुखतः सर्वेषां च महीक्षिताम्।\n\nउवाच पार्थ पश्यैतान्समवेतान्कुरूनिति।।1.25।।\n ', 'bhīṣhma-droṇa-pramukhataḥ sarveṣhāṁ cha mahī-kṣhitām\nuvācha pārtha paśhyaitān samavetān kurūn iti\n', 'bhīṣhma—Grandsire Bheeshma; droṇa—Dronacharya; pramukhataḥ—in the presence; sarveṣhām—all; cha—and; mahī-kṣhitām—other kings; uvācha—said; pārtha—Arjun, the son of Pritha; paśhya—behold; etān—these; samavetān—gathered; kurūn—descendants of Kuru; iti—thus\n', 'https://gita.github.io/gita/data/verse_recitation/1/25.mp3', 1, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (26, 'तत्रापश्यत्स्थितान्पार्थः पितृ़नथ पितामहान्।\n\nआचार्यान्मातुलान्भ्रातृ़न्पुत्रान्पौत्रान्सखींस्तथा।।1.26।।\n ', 'tatrāpaśhyat sthitān pārthaḥ pitṝīn atha pitāmahān\nāchāryān mātulān bhrātṝīn putrān pautrān sakhīṁs tathā\nśhvaśhurān suhṛidaśh chaiva senayor ubhayor api\n', 'tatra—there; apaśhyat—saw; sthitān—stationed; pārthaḥ—Arjun; pitṝīn—fathers; atha—thereafter; pitāmahān—grandfathers; āchāryān—teachers; mātulān—maternal uncles; bhrātṝīn—brothers; putrān—sons; pautrān—grandsons; sakhīn—friends; tathā—also; śhvaśhurān—fathers-in-law; suhṛidaḥ—well-wishers; cha—and; eva—indeed; senayoḥ—armies; ubhayoḥ—in both armies; api—also\n', 'https://gita.github.io/gita/data/verse_recitation/1/26.mp3', 1, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (27, 'श्वशुरान्सुहृदश्चैव सेनयोरुभयोरपि।\n\nतान्समीक्ष्य स कौन्तेयः सर्वान्बन्धूनवस्थितान्।।1.27।।\n ', 'tān samīkṣhya sa kaunteyaḥ sarvān bandhūn avasthitān\nkṛipayā parayāviṣhṭo viṣhīdann idam abravīt\n', 'tān—these; samīkṣhya—on seeing; saḥ—they; kaunteyaḥ—Arjun, the son of Kunti; sarvān—all; bandhūn—relatives; avasthitān—present; kṛipayā—by compassion; parayā—great; āviṣhṭaḥ—overwhelmed; viṣhīdan—deep sorrow; idam—this; abravīt—spoke\n', 'https://gita.github.io/gita/data/verse_recitation/1/27.mp3', 1, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (28, 'अर्जुन उवाच\n\nकृपया परयाऽऽविष्टो विषीदन्निदमब्रवीत्।\n\nदृष्ट्वेमं स्वजनं कृष्ण युयुत्सुं समुपस्थितम्।।1.28।।\n ', 'arjuna uvācha\ndṛiṣhṭvemaṁ sva-janaṁ kṛiṣhṇa yuyutsuṁ samupasthitam\n', 'arjunaḥ uvācha—Arjun said; dṛiṣhṭvā—on seeing; imam—these; sva-janam—kinsmen; kṛiṣhṇa—Krishna; yuyutsum—eager to fight; samupasthitam—present; \n', 'https://gita.github.io/gita/data/verse_recitation/1/28.mp3', 1, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (29, 'सीदन्ति मम गात्राणि मुखं च परिशुष्यति।\n\nवेपथुश्च शरीरे मे रोमहर्षश्च जायते।।1.29।।\n ', 'sīdanti mama gātrāṇi mukhaṁ cha pariśhuṣhyati\nvepathuśh cha śharīre me roma-harṣhaśh cha jāyate\n', 'sīdanti—quivering; mama—my; gātrāṇi—limbs; mukham—mouth; cha—and; pariśhuṣhyati—is drying up\nvepathuḥ—shuddering; cha—and; śharīre—on the body; me—my; roma-harṣhaḥ—standing of bodily hair on end; cha—also; jāyate—is happening;\n', 'https://gita.github.io/gita/data/verse_recitation/1/29.mp3', 1, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (30, 'गाण्डीवं स्रंसते हस्तात्त्वक्चैव परिदह्यते।\n\nन च शक्नोम्यवस्थातुं भ्रमतीव च मे मनः।।1.30।।\n ', 'gāṇḍīvaṁ sraṁsate hastāt tvak chaiva paridahyate\nna cha śhaknomy avasthātuṁ bhramatīva cha me manaḥ\n', 'gāṇḍīvam—Arjun’s bow; sraṁsate—is slipping; hastāt—from (my) hand; tvak—skin; cha—and; eva—indeed; paridahyate—is burning all over; na—not; cha—and; śhaknomi—am able; avasthātum—remain steady; bhramati iva—whirling like; cha—and; me—my; manaḥ—mind;\n', 'https://gita.github.io/gita/data/verse_recitation/1/30.mp3', 1, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (31, 'निमित्तानि च पश्यामि विपरीतानि केशव।\n\nन च श्रेयोऽनुपश्यामि हत्वा स्वजनमाहवे।।1.31।।\n ', 'nimittāni cha paśhyāmi viparītāni keśhava\nna cha śhreyo ’nupaśhyāmi hatvā sva-janam āhave\n', 'nimittāni—omens; cha—and; paśhyāmi—I see; viparītāni—misfortune; keśhava—Shree Krishna, killer of the Keshi demon; na—not; cha—also; śhreyaḥ—good; anupaśhyāmi—I foresee; hatvā—from killing; sva-janam—kinsmen; āhave—in battle\n', 'https://gita.github.io/gita/data/verse_recitation/1/31.mp3', 1, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (32, 'न काङ्क्षे विजयं कृष्ण न च राज्यं सुखानि च।\n\nकिं नो राज्येन गोविन्द किं भोगैर्जीवितेन वा।।1.32।।\n ', 'na kāṅkṣhe vijayaṁ kṛiṣhṇa na cha rājyaṁ sukhāni cha\nkiṁ no rājyena govinda kiṁ bhogair jīvitena vā\n', 'na—nor; kāṅkṣhe—do I desire; vijayam—victory; kṛiṣhṇa—Krishna; na—nor; cha—as well; rājyam—kingdom; sukhāni—happiness; cha—also; kim—what; naḥ—to us; rājyena—by kingdom; govinda—Krishna, he who gives pleasure to the senses, he who is fond of cows; kim—what?; bhogaiḥ—pleasures; jīvitena—life; vā—or;\n', 'https://gita.github.io/gita/data/verse_recitation/1/32.mp3', 1, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (33, 'येषामर्थे काङ्क्षितं नो राज्यं भोगाः सुखानि च।\n\nत इमेऽवस्थिता युद्धे प्राणांस्त्यक्त्वा धनानि च।।1.33।।\n ', 'yeṣhām arthe kāṅkṣhitaṁ no rājyaṁ bhogāḥ sukhāni cha\nta ime ’vasthitā yuddhe prāṇāṁs tyaktvā dhanāni cha\n', 'yeṣhām—for whose; arthe—sake; kāṅkṣhitam—coveted for; naḥ—by us; rājyam—kingdom; bhogāḥ—pleasures; sukhāni—happiness; cha—also; te—they; ime—these; avasthitāḥ—situated; yuddhe—for battle; prāṇān—lives; tyaktvā—giving up; dhanāni—wealth; cha—also\n', 'https://gita.github.io/gita/data/verse_recitation/1/33.mp3', 1, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (34, 'आचार्याः पितरः पुत्रास्तथैव च पितामहाः।\n\nमातुलाः श्चशुराः पौत्राः श्यालाः सम्बन्धिनस्तथा।।1.34।।\n ', 'āchāryāḥ pitaraḥ putrās tathaiva cha pitāmahāḥ\nmātulāḥ śhvaśhurāḥ pautrāḥ śhyālāḥ sambandhinas tathā\n', 'āchāryāḥ—teachers; pitaraḥ—fathers; putrāḥ—sons; tathā—as well; eva—indeed; cha—also; pitāmahāḥ—grandfathers; mātulāḥ—maternal uncles; śhvaśhurāḥ—fathers-in-law; pautrāḥ—grandsons; śhyālāḥ—brothers-in-law; sambandhinaḥ—kinsmen; tathā—as well; \n', 'https://gita.github.io/gita/data/verse_recitation/1/34.mp3', 1, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (35, 'एतान्न हन्तुमिच्छामि घ्नतोऽपि मधुसूदन।\n\nअपि त्रैलोक्यराज्यस्य हेतोः किं नु महीकृते।।1.35।।\n ', 'etān na hantum ichchhāmi ghnato ’pi madhusūdana\napi trailokya-rājyasya hetoḥ kiṁ nu mahī-kṛite\n', 'etān—these; na—not; hantum—to slay; ichchhāmi—I wish; ghnataḥ—killed; api—even though; madhusūdana—Shree Krishna, killer of the demon Madhu; api—even though; trai-lokya-rājyasya—dominion over three worlds; hetoḥ—for the sake of; kim nu—what to speak of; mahī-kṛite—for the earth\n', 'https://gita.github.io/gita/data/verse_recitation/1/35.mp3', 1, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (36, 'निहत्य धार्तराष्ट्रान्नः का प्रीतिः स्याज्जनार्दन।\n\nपापमेवाश्रयेदस्मान्हत्वैतानाततायिनः।।1.36।।\n ', 'nihatya dhārtarāṣhṭrān naḥ kā prītiḥ syāj janārdana\npāpam evāśhrayed asmān hatvaitān ātatāyinaḥ\n', 'nihatya—by killing; dhārtarāṣhṭrān—the sons of Dhritarashtra; naḥ—our; kā—what; prītiḥ—pleasure; syāt—will there be; janārdana—he who looks after the public, Shree Krishna; pāpam—vices; eva—certainly; āśhrayet—must come upon; asmān—us; hatvā—by killing; etān—all these; ātatāyinaḥ—aggressors;\n', 'https://gita.github.io/gita/data/verse_recitation/1/36.mp3', 1, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (37, 'तस्मान्नार्हा वयं हन्तुं धार्तराष्ट्रान्स्वबान्धवान्।\n\nस्वजनं हि कथं हत्वा सुखिनः स्याम माधव।।1.37।।\n ', 'tasmān nārhā vayaṁ hantuṁ dhārtarāṣhṭrān sa-bāndhavān \nsva-janaṁ hi kathaṁ hatvā sukhinaḥ syāma mādhava\n', 'tasmāt—hence; na—never; arhāḥ—behoove; vayam—we; hantum—to kill; dhārtarāṣhṭrān—the sons of Dhritarashtra; sva-bāndhavān—along with friends; sva-janam—kinsmen; hi—certainly; katham—how; hatvā—by killing; sukhinaḥ—happy; syāma—will we become; mādhava—Shree Krishna, the husband of Yogmaya\n', 'https://gita.github.io/gita/data/verse_recitation/1/37.mp3', 1, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (38, 'यद्यप्येते न पश्यन्ति लोभोपहतचेतसः।\n\nकुलक्षयकृतं दोषं मित्रद्रोहे च पातकम्।।1.38।।\n ', 'yady apy ete na paśhyanti lobhopahata-chetasaḥ\nkula-kṣhaya-kṛitaṁ doṣhaṁ mitra-drohe cha pātakam\n', 'yadi api—even though; ete—they; na—not; paśhyanti—see; lobha—greed; upahata—overpowered; chetasaḥ—thoughts; kula-kṣhaya-kṛitam—in annihilating their relatives; doṣham—fault; mitra-drohe—to wreak treachery upon friends; cha—and; pātakam—sin;\n', 'https://gita.github.io/gita/data/verse_recitation/1/38.mp3', 1, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (39, 'कथं न ज्ञेयमस्माभिः पापादस्मान्निवर्तितुम्।\n\nकुलक्षयकृतं दोषं प्रपश्यद्भिर्जनार्दन।।1.39।।\n ', 'kathaṁ na jñeyam asmābhiḥ pāpād asmān nivartitum\nkula-kṣhaya-kṛitaṁ doṣhaṁ prapaśhyadbhir janārdana\n', 'katham—why; na—not; jñeyam—should be known; asmābhiḥ—we; pāpāt—from sin; asmāt—these; nivartitum—to turn away; kula-kṣhaya—killing the kindered; kṛitam—done; doṣham—crime; prapaśhyadbhiḥ—who can see; janārdana—he who looks after the public, Shree Krishna\n', 'https://gita.github.io/gita/data/verse_recitation/1/39.mp3', 1, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (40, 'कुलक्षये प्रणश्यन्ति कुलधर्माः सनातनाः।\n\nधर्मे नष्टे कुलं कृत्स्नमधर्मोऽभिभवत्युत।।1.40।।\n ', 'kula-kṣhaye praṇaśhyanti kula-dharmāḥ sanātanāḥ\ndharme naṣhṭe kulaṁ kṛitsnam adharmo ’bhibhavaty uta\n', 'kula-kṣhaye—in the destruction of a dynasty; praṇaśhyanti—are vanquished; kula-dharmāḥ—family traditions; sanātanāḥ—eternal; dharme—religion; naṣhṭe—is destroyed; kulam—family; kṛitsnam—the whole; adharmaḥ—irreligion; abhibhavati—overcome; uta—indeed\n', 'https://gita.github.io/gita/data/verse_recitation/1/40.mp3', 1, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (41, 'अधर्माभिभवात्कृष्ण प्रदुष्यन्ति कुलस्त्रियः।\n\nस्त्रीषु दुष्टासु वार्ष्णेय जायते वर्णसङ्करः।।1.41।।\n ', 'adharmābhibhavāt kṛiṣhṇa praduṣhyanti kula-striyaḥ\nstrīṣhu duṣhṭāsu vārṣhṇeya jāyate varṇa-saṅkaraḥ\n', 'adharma—irreligion; abhibhavāt—preponderance; kṛiṣhṇa—Shree Krishna; praduṣhyanti—become immoral; kula-striyaḥ—women of the family; strīṣhu—of women; duṣhṭāsu—become immoral; vārṣhṇeya—descendant of Vrishni; jāyate—are born; varṇa-saṅkaraḥ—unwanted progeny\n', 'https://gita.github.io/gita/data/verse_recitation/1/41.mp3', 1, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (42, 'सङ्करो नरकायैव कुलघ्नानां कुलस्य च।\n\nपतन्ति पितरो ह्येषां लुप्तपिण्डोदकक्रियाः।।1.42।।\n ', 'saṅkaro narakāyaiva kula-ghnānāṁ kulasya cha\npatanti pitaro hy eṣhāṁ lupta-piṇḍodaka-kriyāḥ\n', 'saṅkaraḥ—unwanted children; narakāya—hellish; eva—indeed; kula-ghnānām—for those who destroy the family; kulasya—of the family; cha—also; patanti—fall; pitaraḥ—ancestors; hi—verily; eṣhām—their; lupta—deprived of; piṇḍodaka-kriyāḥ—performances of sacrificial offerings\n', 'https://gita.github.io/gita/data/verse_recitation/1/42.mp3', 1, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (43, 'दोषैरेतैः कुलघ्नानां वर्णसङ्करकारकैः।\n\nउत्साद्यन्ते जातिधर्माः कुलधर्माश्च शाश्वताः।।1.43।।\n ', 'doṣhair etaiḥ kula-ghnānāṁ varṇa-saṅkara-kārakaiḥ\nutsādyante jāti-dharmāḥ kula-dharmāśh cha śhāśhvatāḥ\n', 'doṣhaiḥ—through evil deeds; etaiḥ—these; kula-ghnānām—of those who destroy the family; varṇa-saṅkara—unwanted progeny; kārakaiḥ—causing; utsādyante—are ruined; jāti-dharmāḥ—social and family welfare activities; kula-dharmāḥ—family traditions; cha—and; śhāśhvatāḥ—eternal\n', 'https://gita.github.io/gita/data/verse_recitation/1/43.mp3', 1, 43);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (44, 'उत्सन्नकुलधर्माणां मनुष्याणां जनार्दन।\n\nनरकेऽनियतं वासो भवतीत्यनुशुश्रुम।।1.44।।\n ', 'utsanna-kula-dharmāṇāṁ manuṣhyāṇāṁ janārdana\nnarake ‘niyataṁ vāso bhavatītyanuśhuśhruma\n', 'utsanna—destroyed; kula-dharmāṇām—whose family traditions; manuṣhyāṇām—of such human beings; janārdana—he who looks after the public, Shree Krishna; narake—in hell; aniyatam—indefinite; vāsaḥ—dwell; bhavati—is; iti—thus; anuśhuśhruma—I have heard from the learned\n', 'https://gita.github.io/gita/data/verse_recitation/1/44.mp3', 1, 44);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (45, 'अहो बत महत्पापं कर्तुं व्यवसिता वयम्।\n\nयद्राज्यसुखलोभेन हन्तुं स्वजनमुद्यताः।।1.45।।\n ', 'aho bata mahat pāpaṁ kartuṁ vyavasitā vayam\nyad rājya-sukha-lobhena hantuṁ sva-janam udyatāḥ\n', 'aho—alas; bata—how; mahat—great; pāpam—sins; kartum—to perform; vyavasitāḥ—have decided; vayam—we; yat—because; rājya-sukha-lobhena—driven by the desire for kingly pleasure; hantum—to kill; sva-janam—kinsmen; udyatāḥ—intending;\n', 'https://gita.github.io/gita/data/verse_recitation/1/45.mp3', 1, 45);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (46, 'यदि मामप्रतीकारमशस्त्रं शस्त्रपाणयः।\n\nधार्तराष्ट्रा रणे हन्युस्तन्मे क्षेमतरं भवेत्।।1.46।।\n ', 'yadi mām apratīkāram aśhastraṁ śhastra-pāṇayaḥ\ndhārtarāṣhṭrā raṇe hanyus tan me kṣhemataraṁ bhavet\n', 'yadi—if; mām—me; apratīkāram—unresisting; aśhastram—unarmed; śhastra-pāṇayaḥ—those with weapons in hand; dhārtarāṣhṭrāḥ—the sons of Dhritarashtra; raṇe—on the battlefield; hanyuḥ—shall kill; tat—that; me—to me; kṣhema-taram—better; bhavet—would be\n', 'https://gita.github.io/gita/data/verse_recitation/1/46.mp3', 1, 46);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (47, 'सञ्जय उवाच\n\nएवमुक्त्वाऽर्जुनः संख्ये रथोपस्थ उपाविशत्।\n\nविसृज्य सशरं चापं शोकसंविग्नमानसः।।1.47।।\n ', 'sañjaya uvācha\nevam uktvārjunaḥ saṅkhye rathopastha upāviśhat\nvisṛijya sa-śharaṁ chāpaṁ śhoka-saṁvigna-mānasaḥ\n', 'sañjayaḥ uvācha—Sanjay said; evam uktvā—speaking thus; arjunaḥ—Arjun; saṅkhye—in the battlefield; ratha upasthe—on the chariot; upāviśhat—sat; visṛijya—casting aside; sa-śharam—along with arrows; chāpam—the bow; śhoka—with grief; saṁvigna—distressed; mānasaḥ—mind\n', 'https://gita.github.io/gita/data/verse_recitation/1/47.mp3', 1, 47);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (48, 'सञ्जय उवाच\n\nतं तथा कृपयाऽविष्टमश्रुपूर्णाकुलेक्षणम्।\n\nविषीदन्तमिदं वाक्यमुवाच मधुसूदनः।।2.1।।\n ', 'sañjaya uvācha\ntaṁ tathā kṛipayāviṣhṭamaśhru pūrṇākulekṣhaṇam\nviṣhīdantamidaṁ vākyam uvācha madhusūdanaḥ\n', 'sañjayaḥ uvācha—Sanjay said; tam—to him (Arjun); tathā—thus; kṛipayā—with pity; āviṣhṭam—overwhelmed; aśhru-pūrṇa—full of tears; ākula—distressed; īkṣhaṇam—eyes; viṣhīdantam—grief-stricken; idam—these; vākyam—words; uvācha—said; madhusūdanaḥ—Shree Krishn, slayer of the Madhu demon\n', 'https://gita.github.io/gita/data/verse_recitation/2/48.mp3', 2, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (49, 'श्री भगवानुवाच\n\nकुतस्त्वा कश्मलमिदं विषमे समुपस्थितम्।\n\nअनार्यजुष्टमस्वर्ग्यमकीर्तिकरमर्जुन।।2.2।।\n ', 'śhrī bhagavān uvācha\nkutastvā kaśhmalamidaṁ viṣhame samupasthitam\nanārya-juṣhṭamaswargyam akīrti-karam arjuna\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; kutaḥ—wherefrom; tvā—to you; kaśhmalam—delusion; idam—this; viṣhame—in this hour of peril; samupasthitam—overcome; anārya—crude person; juṣhṭam—practiced; aswargyam—which does not lead to the higher abodes; akīrti-karam—leading to disgrace; arjuna—Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/2/49.mp3', 2, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (50, 'क्लैब्यं मा स्म गमः पार्थ नैतत्त्वय्युपपद्यते।\n\nक्षुद्रं हृदयदौर्बल्यं त्यक्त्वोत्तिष्ठ परन्तप।।2.3।।\n ', 'klaibyaṁ mā sma gamaḥ pārtha naitat tvayyupapadyate\nkṣhudraṁ hṛidaya-daurbalyaṁ tyaktvottiṣhṭha parantapa\n', 'klaibyam—unmanliness; mā sma—do not; gamaḥ—yield to; pārtha—Arjun, the son of Pritha; na—not; etat—this; tvayi—to you; upapadyate—befitting; kṣhudram—petty; hṛidaya—heart; daurbalyam—weakness; tyaktvā—giving up; uttiṣhṭha—arise; param-tapa—conqueror of enemies\n', 'https://gita.github.io/gita/data/verse_recitation/2/50.mp3', 2, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (51, 'अर्जुन उवाच\n\nकथं भीष्ममहं संख्ये द्रोणं च मधुसूदन।\n\nइषुभिः प्रतियोत्स्यामि पूजार्हावरिसूदन।।2.4।।\n ', 'arjuna uvācha\nkathaṁ bhīṣhmam ahaṁ sankhye droṇaṁ cha madhusūdana\niṣhubhiḥ pratiyotsyāmi pūjārhāvari-sūdana\n', 'arjunaḥ uvācha—Arjun said; katham—how; bhīṣhmam—Bheeshma; aham—I; sankhye—in battle; droṇam—Dronacharya; cha—and; madhu-sūdana—Shree Krishn, slayer of the Madhu demon; iṣhubhiḥ—with arrows; pratiyotsyāmi—shall I shoot; pūjā-arhau—worthy of worship; ari-sūdana—destroyer of enemies\n', 'https://gita.github.io/gita/data/verse_recitation/2/51.mp3', 2, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (52, 'गुरूनहत्वा हि महानुभावान्\n\nश्रेयो भोक्तुं भैक्ष्यमपीह लोके।\n\nहत्वार्थकामांस्तु गुरूनिहैव\n\nभुञ्जीय भोगान् रुधिरप्रदिग्धान्।।2.5।।\n ', 'gurūnahatvā hi mahānubhāvān\nśhreyo bhoktuṁ bhaikṣhyamapīha loke\nhatvārtha-kāmāṁstu gurūnihaiva\nbhuñjīya bhogān rudhira-pradigdhān\n', 'gurūn—teachers; ahatvā—not killing; hi—certainly; mahā-anubhāvān—noble elders; śhreyaḥ—better; bhoktum—to enjoy life; bhaikṣhyam—by begging; api—even; iha loke—in this world; hatvā—killing; artha—gain; kāmān—desiring; tu—but; gurūn—noble elders; iha—in this world; eva—certainly; bhuñjīya—enjoy; bhogān—pleasures; rudhira—blood; pradigdhān—tainted with\n', 'https://gita.github.io/gita/data/verse_recitation/2/52.mp3', 2, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (53, 'न चैतद्विद्मः कतरन्नो गरीयो\n\nयद्वा जयेम यदि वा नो जयेयुः।\n\nयानेव हत्वा न जिजीविषाम\n\nस्तेऽवस्थिताः प्रमुखे धार्तराष्ट्राः।।2.6।।\n ', 'na chaitadvidmaḥ kataranno garīyo\nyadvā jayema yadi vā no jayeyuḥ\nyāneva hatvā na jijīviṣhāmas\nte ’vasthitāḥ pramukhe dhārtarāṣhṭrāḥ\n', 'na—not; cha—and; etat—this; vidmaḥ—we know; katarat—which; naḥ—for us; garīyaḥ—is preferable; yat vā—whether; jayema—we may conquer; yadi—if; vā—or; naḥ—us; jayeyuḥ—they may conquer; yān—whom; eva—certainly; hatvā—after killing; na—not; jijīviṣhāmaḥ—we desire to live; te—they; avasthitāḥ—are standing; pramukhe—before us; dhārtarāṣhṭrāḥ—the sons of Dhritarashtra\n', 'https://gita.github.io/gita/data/verse_recitation/2/53.mp3', 2, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (54, 'कार्पण्यदोषोपहतस्वभावः\n\nपृच्छामि त्वां धर्मसंमूढचेताः।\n\nयच्छ्रेयः स्यान्निश्िचतं ब्रूहि तन्मे\n\nशिष्यस्तेऽहं शाधि मां त्वां प्रपन्नम्।।2.7।।\n ', 'kārpaṇya-doṣhopahata-svabhāvaḥ\npṛichchhāmi tvāṁ dharma-sammūḍha-chetāḥ\nyach-chhreyaḥ syānniśhchitaṁ brūhi tanme\nśhiṣhyaste ’haṁ śhādhi māṁ tvāṁ prapannam\n', 'kārpaṇya-doṣha—the flaw of cowardice; upahata—besieged; sva-bhāvaḥ—nature; pṛichchhāmi—I am asking; tvām—to you; dharma—duty; sammūḍha—confused; chetāḥ—in heart; yat—what; śhreyaḥ—best; syāt—may be; niśhchitam—decisively; brūhi—tell; tat—that; me—to me; śhiṣhyaḥ—disciple; te—your; aham—I; śhādhi—please instruct; mām—me; tvām—unto you; prapannam—surrendered\n', 'https://gita.github.io/gita/data/verse_recitation/2/54.mp3', 2, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (55, 'न हि प्रपश्यामि ममापनुद्या\n\nद्यच्छोकमुच्छोषणमिन्द्रियाणाम्।\n\nअवाप्य भूमावसपत्नमृद्धम्\n\nराज्यं सुराणामपि चाधिपत्यम्।।2.8।।\n ', 'na hi prapaśhyāmi mamāpanudyād\nyach-chhokam uchchhoṣhaṇam-indriyāṇām\navāpya bhūmāv-asapatnamṛiddhaṁ\nrājyaṁ surāṇāmapi chādhipatyam\n', 'na—not; hi—certainly; prapaśhyāmi—I see; mama—my; apanudyāt—drive away; yat—which; śhokam—anguish; uchchhoṣhaṇam—is drying up; indriyāṇām—of the senses; avāpya—after achieving; bhūmau—on the earth; asapatnam—unrivalled; ṛiddham—prosperous; rājyam—kingdom; surāṇām—like the celestial gods; api—even; cha—also; ādhipatyam—sovereignty\n', 'https://gita.github.io/gita/data/verse_recitation/2/55.mp3', 2, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (56, 'सञ्जय उवाच\n\nएवमुक्त्वा हृषीकेशं गुडाकेशः परन्तप।\n\nन योत्स्य इति गोविन्दमुक्त्वा तूष्णीं बभूव ह।।2.9।।\n ', 'sañjaya uvācha\nevam-uktvā hṛiṣhīkeśhaṁ guḍākeśhaḥ parantapa\nna yotsya iti govindam uktvā tūṣhṇīṁ babhūva ha\n', 'sañjayaḥ uvācha—Sanjay said; evam—thus; uktvā—having spoken; hṛiṣhīkeśham—to Shree Krishna, the master of the mind and senses; guḍākeśhaḥ—Arjun, the conquerer of sleep; parantapaḥ—Arjun, the chastiser of the enemies; na yotsye—I shall not fight; iti—thus; govindam—Krishna, the giver of pleasure to the senses; uktvā—having addressed; tūṣhṇīm—silent; babhūva—became ha\n', 'https://gita.github.io/gita/data/verse_recitation/2/56.mp3', 2, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (57, 'तमुवाच हृषीकेशः प्रहसन्निव भारत।\n\nसेनयोरुभयोर्मध्ये विषीदन्तमिदं वचः।।2.10।।\n ', 'tam-uvācha hṛiṣhīkeśhaḥ prahasanniva bhārata\nsenayorubhayor-madhye viṣhīdantam-idaṁ vachaḥ\n', 'tam—to him; uvācha—said; hṛiṣhīkeśhaḥ—Shree Krishna, the master of mind and senses; prahasan—smilingly; iva—as if; bhārata—Dhritarashtra, descendant of Bharat; senayoḥ—of the armies; ubhayoḥ—of both; madhye—in the midst of; viṣhīdantam—to the grief-stricken; idam—this; vachaḥ—words\n', 'https://gita.github.io/gita/data/verse_recitation/2/57.mp3', 2, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (58, 'श्री भगवानुवाच\n\nअशोच्यानन्वशोचस्त्वं प्रज्ञावादांश्च भाषसे।\n\nगतासूनगतासूंश्च नानुशोचन्ति पण्डिताः।।2.11।।\n ', 'śhrī bhagavān uvācha\naśhochyān-anvaśhochas-tvaṁ prajñā-vādānśh cha bhāṣhase\ngatāsūn-agatāsūnśh-cha nānuśhochanti paṇḍitāḥ\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; aśhochyān—not worthy of grief; anvaśhochaḥ—are mourning; tvam—you; prajñā-vādān—words of wisdom; cha—and; bhāṣhase—speaking; gata āsūn—the dead; agata asūn—the living; cha—and; na—never; anuśhochanti—lament; paṇḍitāḥ—the wise\n', 'https://gita.github.io/gita/data/verse_recitation/2/58.mp3', 2, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (59, 'न त्वेवाहं जातु नासं न त्वं नेमे जनाधिपाः।\n\nन चैव न भविष्यामः सर्वे वयमतः परम्।।2.12।।\n ', 'na tvevāhaṁ jātu nāsaṁ na tvaṁ neme janādhipāḥ\nna chaiva na bhaviṣhyāmaḥ sarve vayamataḥ param\n', 'na—never; tu—however; eva—certainly; aham—I; jātu—at any time; na—nor; āsam—exist; na—nor; tvam—you; na—nor; ime—these; jana-adhipāḥ—kings; na—never; cha—also; eva—indeed; na bhaviṣhyāmaḥ—shall not exist; sarve vayam—all of us; ataḥ—from now; param—after\n', 'https://gita.github.io/gita/data/verse_recitation/2/59.mp3', 2, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (60, 'देहिनोऽस्मिन्यथा देहे कौमारं यौवनं जरा।\n\nतथा देहान्तरप्राप्तिर्धीरस्तत्र न मुह्यति।।2.13।।\n ', 'dehino ’smin yathā dehe kaumāraṁ yauvanaṁ jarā\ntathā dehāntara-prāptir dhīras tatra na muhyati\n', 'dehinaḥ—of the embodied; asmin—in this; yathā—as; dehe—in the body; kaumāram—childhood; yauvanam—youth; jarā—old age; tathā—similarly; deha-antara—another body; prāptiḥ—achieves; dhīraḥ—the wise; tatra—thereupon; na muhyati—are not deluded\n', 'https://gita.github.io/gita/data/verse_recitation/2/60.mp3', 2, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (61, 'मात्रास्पर्शास्तु कौन्तेय शीतोष्णसुखदुःखदाः।\n\nआगमापायिनोऽनित्यास्तांस्तितिक्षस्व भारत।।2.14।।\n ', 'mātrā-sparśhās tu kaunteya śhītoṣhṇa-sukha-duḥkha-dāḥ\nāgamāpāyino ’nityās tans-titikṣhasva bhārata\n', 'mātrā-sparśhāḥ—contact of the senses with the sense objects; tu—indeed; kaunteya—Arjun, the son of Kunti; śhīta—winter; uṣhṇa—summer; sukha—happiness; duḥkha—distress; dāḥ—give; āgama—come; apāyinaḥ—go; anityāḥ—non-permanent; tān—them; titikṣhasva—tolerate; bhārata—descendant of the Bharat\n', 'https://gita.github.io/gita/data/verse_recitation/2/61.mp3', 2, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (62, 'यं हि न व्यथयन्त्येते पुरुषं पुरुषर्षभ।\n\nसमदुःखसुखं धीरं सोऽमृतत्वाय कल्पते।।2.15।।\n ', 'yaṁ hi na vyathayantyete puruṣhaṁ puruṣharṣhabha\nsama-duḥkha-sukhaṁ dhīraṁ so ’mṛitatvāya kalpate\n', 'yam—whom; hi—verily; na—not; vyathayanti—distressed; ete—these; puruṣham—person; puruṣha-ṛiṣhabha—the noblest amongst men, Arjun; sama—equipoised; duḥkha—distress; sukham—happiness; dhīram—steady; saḥ—that person; amṛitatvāya—for liberation; kalpate—becomes eligible\n', 'https://gita.github.io/gita/data/verse_recitation/2/62.mp3', 2, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (63, 'नासतो विद्यते भावो नाभावो विद्यते सतः।\n\nउभयोरपि दृष्टोऽन्तस्त्वनयोस्तत्त्वदर्शिभिः।।2.16।।\n ', 'nāsato vidyate bhāvo nābhāvo vidyate sataḥ\nubhayorapi dṛiṣhṭo ’nta stvanayos tattva-darśhibhiḥ\n', 'na—no; asataḥ—of the temporary; vidyate—there is; bhāvaḥ—is; na—no; abhāvaḥ—cessation; vidyate—is; sataḥ—of the eternal; ubhayoḥ—of the two; api—also; dṛiṣhṭaḥ—observed; antaḥ—conclusion; tu—verily; anayoḥ—of these; tattva—of the truth; darśhibhiḥ—by the seers\n', 'https://gita.github.io/gita/data/verse_recitation/2/63.mp3', 2, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (64, 'अविनाशि तु तद्विद्धि येन सर्वमिदं ततम्।\n\nविनाशमव्ययस्यास्य न कश्चित् कर्तुमर्हति।।2.17।।\n ', 'avināśhi tu tadviddhi yena sarvam idaṁ tatam\nvināśham avyayasyāsya na kaśhchit kartum arhati\n', 'avināśhi—indestructible; tu—indeed; tat—that; viddhi—know; yena—by whom; sarvam—entire; idam—this; tatam—pervaded; vināśham—destruction; avyayasya—of the imperishable; asya—of it; na kaśhchit—no one; kartum—to cause; arhati—is able\n', 'https://gita.github.io/gita/data/verse_recitation/2/64.mp3', 2, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (65, 'अन्तवन्त इमे देहा नित्यस्योक्ताः शरीरिणः।\n\nअनाशिनोऽप्रमेयस्य तस्माद्युध्यस्व भारत।।2.18।।\n ', 'antavanta ime dehā nityasyoktāḥ śharīriṇaḥ\nanāśhino ’prameyasya tasmād yudhyasva bhārata\n', 'anta-vantaḥ—having an end; ime—these; dehāḥ—material bodies; nityasya—eternally; uktāḥ—are said; śharīriṇaḥ—of the embodied soul; anāśhinaḥ—indestructible; aprameyasya—immeasurable; tasmāt—therefore; yudhyasva—fight; bhārata—descendant of Bharat, Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/2/65.mp3', 2, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (66, 'य एनं वेत्ति हन्तारं यश्चैनं मन्यते हतम्।\n\nउभौ तौ न विजानीतो नायं हन्ति न हन्यते।।2.19।।\n ', 'ya enaṁ vetti hantāraṁ yaśh chainaṁ manyate hatam\nubhau tau na vijānīto nāyaṁ hanti na hanyate\n', 'yaḥ—one who; enam—this; vetti—knows; hantāram—the slayer; yaḥ—one who; cha—and; enam—this; manyate—thinks; hatam—slain; ubhau—both; tau—they; na—not; vijānītaḥ—in knowledge; na—neither; ayam—this; hanti—slays; na—nor; hanyate—is killed\n', 'https://gita.github.io/gita/data/verse_recitation/2/66.mp3', 2, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (67, 'न जायते म्रियते वा कदाचि\n\nन्नायं भूत्वा भविता वा न भूयः।\n\nअजो नित्यः शाश्वतोऽयं पुराणो\n\nन हन्यते हन्यमाने शरीरे।।2.20।।\n ', 'na jāyate mriyate vā kadāchin\nnāyaṁ bhūtvā bhavitā vā na bhūyaḥ\najo nityaḥ śhāśhvato ’yaṁ purāṇo\nna hanyate hanyamāne śharīre\n', 'na jāyate—is not born; mriyate—dies; vā—or; kadāchit—at any time; na—not; ayam—this; bhūtvā—having once existed; bhavitā—will be; vā—or; na—not; bhūyaḥ—further; ajaḥ—unborn; nityaḥ—eternal; śhāśhvataḥ—immortal; ayam—this; purāṇaḥ—the ancient; na hanyate—is not destroyed; hanyamāne—is destroyed; śharīre—when the body\n', 'https://gita.github.io/gita/data/verse_recitation/2/67.mp3', 2, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (68, 'वेदाविनाशिनं नित्यं य एनमजमव्ययम्।\n\nकथं स पुरुषः पार्थ कं घातयति हन्ति कम्।।2.21।।\n ', 'vedāvināśhinaṁ nityaṁ ya enam ajam avyayam\nkathaṁ sa puruṣhaḥ pārtha kaṁ ghātayati hanti kam\n', 'veda—knows; avināśhinam—imperishable; nityam—eternal; yaḥ—who; enam—this; ajam—unborn; avyayam—immutable; katham—how; saḥ—that; puruṣhaḥ—person; pārtha—Parth; kam—whom; ghātayati—causes to be killed; hanti—kills; kam—whom\n', 'https://gita.github.io/gita/data/verse_recitation/2/68.mp3', 2, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (69, 'वासांसि जीर्णानि यथा विहाय\n\nनवानि गृह्णाति नरोऽपराणि।\n\nतथा शरीराणि विहाय जीर्णा\n\nन्यन्यानि संयाति नवानि देही।।2.22।।\n ', 'vāsānsi jīrṇāni yathā vihāya\nnavāni gṛihṇāti naro ’parāṇi\ntathā śharīrāṇi vihāya jīrṇānya\nnyāni sanyāti navāni dehī\n', 'vāsānsi—garments; jīrṇāni—worn-out; yathā—as; vihāya—sheds; navāni—new; gṛihṇāti—accepts; naraḥ—a person; aparāṇi—others; tathā—likewise; śharīrāṇi—bodies; vihāya—casting off; jirṇāni—worn-out; anyāni—other; sanyāti—enters; navāni—new; dehī—the embodied soul\n', 'https://gita.github.io/gita/data/verse_recitation/2/69.mp3', 2, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (70, 'नैनं छिन्दन्ति शस्त्राणि नैनं दहति पावकः।\n\nन चैनं क्लेदयन्त्यापो न शोषयति मारुतः।।2.23।।\n ', 'nainaṁ chhindanti śhastrāṇi nainaṁ dahati pāvakaḥ\nna chainaṁ kledayantyāpo na śhoṣhayati mārutaḥ\n', 'na—not; enam—this soul; chhindanti—shred; śhastrāṇi—weapons; na—nor; enam—this soul; dahati—burns; pāvakaḥ—fire; na—not; cha—and; enam—this soul; kledayanti—moisten; āpaḥ—water; na—nor; śhoṣhayati—dry; mārutaḥ—wind\n', 'https://gita.github.io/gita/data/verse_recitation/2/70.mp3', 2, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (71, 'अच्छेद्योऽयमदाह्योऽयमक्लेद्योऽशोष्य एव च।\n\nनित्यः सर्वगतः स्थाणुरचलोऽयं सनातनः।।2.24।।\n ', 'achchhedyo ’yam adāhyo ’yam akledyo ’śhoṣhya eva cha\nnityaḥ sarva-gataḥ sthāṇur achalo ’yaṁ sanātanaḥ\n', 'achchhedyaḥ—unbreakable; ayam—this soul; adāhyaḥ—incombustible; ayam—this soul; akledyaḥ—cannot be dampened; aśhoṣhyaḥ—cannot be dried; eva—indeed; cha—and; nityaḥ—everlasting; sarva-gataḥ—all-pervading; sthāṇuḥ—unalterable; achalaḥ—immutable; ayam—this soul; sanātanaḥ—primordial\n', 'https://gita.github.io/gita/data/verse_recitation/2/71.mp3', 2, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (72, 'अव्यक्तोऽयमचिन्त्योऽयमविकार्योऽयमुच्यते।\n\nतस्मादेवं विदित्वैनं नानुशोचितुमर्हसि।।2.25।।\n ', 'avyakto ’yam achintyo ’yam avikāryo ’yam uchyate\ntasmādevaṁ viditvainaṁ nānuśhochitum arhasi\n', 'avyaktaḥ—unmanifested; ayam—this soul; achintyaḥ—inconceivable; ayam—this soul; avikāryaḥ—unchangeable; ayam—this soul; uchyate—is said; tasmāt—therefore; evam—thus; viditvā—having known; enam—this soul; na—not; anuśhochitum—to grieve; arhasi—befitting\n', 'https://gita.github.io/gita/data/verse_recitation/2/72.mp3', 2, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (73, 'अथ चैनं नित्यजातं नित्यं वा मन्यसे मृतम्।\n\nतथापि त्वं महाबाहो नैवं शोचितुमर्हसि।।2.26।।\n ', 'atha chainaṁ nitya-jātaṁ nityaṁ vā manyase mṛitam\ntathāpi tvaṁ mahā-bāho naivaṁ śhochitum arhasi\n', 'atha—if, however; cha—and; enam—this soul; nitya-jātam—taking constant birth; nityam—always; vā—or; manyase—you think; mṛitam—dead; tathā api—even then; tvam—you; mahā-bāho—mighty-armed one, Arjun; na—not; evam—like this; śhochitum—grieve; arhasi—befitting\n', 'https://gita.github.io/gita/data/verse_recitation/2/73.mp3', 2, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (74, 'जातस्य हि ध्रुवो मृत्युर्ध्रुवं जन्म मृतस्य च।\n\nतस्मादपरिहार्येऽर्थे न त्वं शोचितुमर्हसि।।2.27।।\n ', 'jātasya hi dhruvo mṛityur dhruvaṁ janma mṛitasya cha\ntasmād aparihārye ’rthe na tvaṁ śhochitum arhasi\n', 'jātasya—for one who has been born; hi—for; dhruvaḥ—certain; mṛityuḥ—death; dhruvam—certain; janma—birth; mṛitasya—for the dead; cha—and; tasmāt—therefore; aparihārye arthe—in this inevitable situation; na—not; tvam—you; śhochitum—lament; arhasi—befitting\n', 'https://gita.github.io/gita/data/verse_recitation/2/74.mp3', 2, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (75, 'अव्यक्तादीनि भूतानि व्यक्तमध्यानि भारत।\n\nअव्यक्तनिधनान्येव तत्र का परिदेवना।।2.28।।\n ', 'avyaktādīni bhūtāni vyakta-madhyāni bhārata\navyakta-nidhanānyeva tatra kā paridevanā\n', 'avyakta-ādīni—unmanifest before birth; bhūtāni—created beings; vyakta—manifest; madhyāni—in the middle; bhārata—Arjun, scion of Bharat; avyakta—unmanifest; nidhanāni—on death; eva—indeed; tatra—therefore; kā—why; paridevanā—grieve\n', 'https://gita.github.io/gita/data/verse_recitation/2/75.mp3', 2, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (76, 'आश्चर्यवत्पश्यति कश्चिदेन\n\nमाश्चर्यवद्वदति तथैव चान्यः।\n\nआश्चर्यवच्चैनमन्यः श्रृणोति\n\nश्रुत्वाप्येनं वेद न चैव कश्चित्।।2.29।।\n ', 'āśhcharya-vat paśhyati kaśhchid enan\nāśhcharya-vad vadati tathaiva chānyaḥ\nāśhcharya-vach chainam anyaḥ śhṛiṇoti\nśhrutvāpyenaṁ veda na chaiva kaśhchit\n', 'āśhcharya-vat—as amazing; paśhyati—see; kaśhchit—someone; enam—this soul; āśhcharya-vat—as amazing; vadati—speak of; tathā—thus; eva—indeed; cha—and; anyaḥ—other; āśhcharya-vat—similarly amazing; cha—also; enam—this soul; anyaḥ—others; śhṛiṇoti—hear; śhrutvā—having heard; api—even; enam—this soul; veda—understand; na—not; cha—and; eva—even; kaśhchit—some\n', 'https://gita.github.io/gita/data/verse_recitation/2/76.mp3', 2, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (77, 'देही नित्यमवध्योऽयं देहे सर्वस्य भारत।\n\nतस्मात्सर्वाणि भूतानि न त्वं शोचितुमर्हसि।।2.30।।\n ', 'dehī nityam avadhyo ’yaṁ dehe sarvasya bhārata\ntasmāt sarvāṇi bhūtāni na tvaṁ śhochitum arhasi\n', 'dehī—the soul that dwells within the body; nityam—always; avadhyaḥ—immortal; ayam—this soul; dehe—in the body; sarvasya—of everyone; bhārata—descendant of Bharat, Arjun; tasmāt—therefore; sarvāṇi—for all; bhūtāni—living entities; na—not; tvam—you; śhochitum—mourn; arhasi—should\n', 'https://gita.github.io/gita/data/verse_recitation/2/77.mp3', 2, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (78, 'स्वधर्ममपि चावेक्ष्य न विकम्पितुमर्हसि।\n\nधर्म्याद्धि युद्धाछ्रेयोऽन्यत्क्षत्रियस्य न विद्यते।।2.31।।\n ', 'swa-dharmam api chāvekṣhya na vikampitum arhasi\ndharmyāddhi yuddhāch chhreyo ’nyat kṣhatriyasya na vidyate\n', 'swa-dharmam—one’s duty in accordance with the Vedas; api—also; cha—and; avekṣhya—considering; na—not; vikampitum—to waver; arhasi—should; dharmyāt—for righteousness; hi—indeed; yuddhāt—than fighting; śhreyaḥ—better; anyat—another; kṣhatriyasya—of a warrior; na—not; vidyate—exists\n', 'https://gita.github.io/gita/data/verse_recitation/2/78.mp3', 2, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (79, 'यदृच्छया चोपपन्नं स्वर्गद्वारमपावृतम्।\n\nसुखिनः क्षत्रियाः पार्थ लभन्ते युद्धमीदृशम्।।2.32।।\n ', 'yadṛichchhayā chopapannaṁ swarga-dvāram apāvṛitam\nsukhinaḥ kṣhatriyāḥ pārtha labhante yuddham īdṛiśham\n', 'yadṛichchhayā—unsought; cha—and; upapannam—come; swarga—celestial abodes; dvāram—door; apāvṛitam—wide open; sukhinaḥ—happy; kṣhatriyāḥ—warriors; pārtha—Arjun, the son of Pritha; labhante—obtain; yuddham—war; īdṛiśham—such\n', 'https://gita.github.io/gita/data/verse_recitation/2/79.mp3', 2, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (80, 'अथ चैत्त्वमिमं धर्म्यं संग्रामं न करिष्यसि।\n\nततः स्वधर्मं कीर्तिं च हित्वा पापमवाप्स्यसि।।2.33।।\n ', 'atha chet tvam imaṁ dharmyaṁ saṅgrāmaṁ na kariṣhyasi\ntataḥ sva-dharmaṁ kīrtiṁ cha hitvā pāpam avāpsyasi\n', 'atha chet—if, however; tvam—you; imam—this; dharmyam saṅgrāmam—righteous war; na—not; kariṣhyasi—act; tataḥ—then; sva-dharmam—one’s duty in accordance with the Vedas; kīrtim—reputation; cha—and; hitvā—abandoning; pāpam—sin; avāpsyasi—will incur\n', 'https://gita.github.io/gita/data/verse_recitation/2/80.mp3', 2, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (81, 'अकीर्तिं चापि भूतानि कथयिष्यन्ति तेऽव्ययाम्।\n\nसंभावितस्य चाकीर्तिर्मरणादतिरिच्यते।।2.34।।\n ', 'akīrtiṁ chāpi bhūtāni\nkathayiṣhyanti te ’vyayām\nsambhāvitasya chākīrtir\nmaraṇād atirichyate\n', 'akīrtim—infamy; cha—and; api—also; bhūtāni—people; kathayiṣhyanti—will speak; te—of your; avyayām—everlasting; sambhāvitasya—of a respectable person; cha—and; akīrtiḥ—infamy; maraṇāt—than death; atirichyate—is greater\n', 'https://gita.github.io/gita/data/verse_recitation/2/81.mp3', 2, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (82, 'भयाद्रणादुपरतं मंस्यन्ते त्वां महारथाः।\n\nयेषां च त्वं बहुमतो भूत्वा यास्यसि लाघवम्।।2.35।।\n ', 'bhayād raṇād uparataṁ mansyante tvāṁ mahā-rathāḥ\nyeṣhāṁ cha tvaṁ bahu-mato bhūtvā yāsyasi lāghavam\n', 'bhayāt—out of fear; raṇāt—from the battlefield; uparatam—have fled; maṁsyante—will think; tvām—you; mahā-rathāḥ—warriors who could single handedly match the strength of ten thousand ordinary warriors; yeṣhām—for whom; cha—and; tvam—you; bahu-mataḥ—high esteemed; bhūtvā—having been; yāsyasi—you will loose; lāghavam—decreased in value\n', 'https://gita.github.io/gita/data/verse_recitation/2/82.mp3', 2, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (83, 'अवाच्यवादांश्च बहून् वदिष्यन्ति तवाहिताः।\n\nनिन्दन्तस्तव सामर्थ्यं ततो दुःखतरं नु किम्।।2.36।।\n ', 'avāchya-vādānśh cha bahūn vadiṣhyanti tavāhitāḥ\nnindantastava sāmarthyaṁ tato duḥkhataraṁ nu kim\n', 'avāchya-vādān—using harsh words; cha—and; bahūn—many; vadiṣhyanti—will say; tava—your; ahitāḥ—enemies; nindantaḥ—defame; tava—your; sāmarthyam—might; tataḥ—than that; duḥkha-taram—more painful; nu—indeed; kim—what\n', 'https://gita.github.io/gita/data/verse_recitation/2/83.mp3', 2, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (84, 'हतो वा प्राप्स्यसि स्वर्गं जित्वा वा भोक्ष्यसे महीम्।\n\nतस्मादुत्तिष्ठ कौन्तेय युद्धाय कृतनिश्चयः।।2.37।।\n ', 'hato vā prāpsyasi swargaṁ jitvā vā bhokṣhyase mahīm\ntasmād uttiṣhṭha kaunteya yuddhāya kṛita-niśhchayaḥ\n', 'hataḥ—slain; vā—or; prāpsyasi—you will attain; swargam—celestial abodes; jitvā—by achieving victory; vā—or; bhokṣhyase—you shall enjoy; mahīm—the kingdom on earth; tasmāt—therefore; uttiṣhṭha—arise; kaunteya—Arjun, the son of Kunti; yuddhāya—for fight; kṛita-niśhchayaḥ—with determination\n', 'https://gita.github.io/gita/data/verse_recitation/2/84.mp3', 2, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (85, 'सुखदुःखे समे कृत्वा लाभालाभौ जयाजयौ।\n\nततो युद्धाय युज्यस्व नैवं पापमवाप्स्यसि।।2.38।।\n ', 'sukha-duḥkhe same kṛitvā lābhālābhau jayājayau\ntato yuddhāya yujyasva naivaṁ pāpam avāpsyasi\n', 'sukha—happiness; duḥkhe—in distress; same kṛitvā—treating alike; lābha-alābhau—gain and loss; jaya-ajayau—victory and defeat; tataḥ—thereafter; yuddhāya—for fighting; yujyasva—engage; na—never; evam—thus; pāpam—sin; avāpsyasi—shall incur\n', 'https://gita.github.io/gita/data/verse_recitation/2/85.mp3', 2, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (86, 'एषा तेऽभिहिता सांख्ये बुद्धिर्योगे त्विमां श्रृणु।\n\nबुद्ध्यायुक्तो यया पार्थ कर्मबन्धं प्रहास्यसि।।2.39।।\n ', 'eṣhā te ’bhihitā sānkhye\nbuddhir yoge tvimāṁ śhṛiṇu\nbuddhyā yukto yayā pārtha\nkarma-bandhaṁ prahāsyasi\n', 'eṣhā—hitherto; te—to you; abhihitā—explained; sānkhye—by analytical knowledge; buddhiḥ yoge—by the yog of intellect; tu—indeed; imām—this; śhṛiṇu—listen; buddhyā—by understanding; yuktaḥ—united; yayā—by which; pārtha—Arjun, the son of Pritha; karma-bandham—bondage of karma; prahāsyasi—you shall be released from\n', 'https://gita.github.io/gita/data/verse_recitation/2/86.mp3', 2, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (87, 'नेहाभिक्रमनाशोऽस्ति प्रत्यवायो न विद्यते।\n\nस्वल्पमप्यस्य धर्मस्य त्रायते महतो भयात्।।2.40।।\n ', 'nehābhikrama-nāśho ’sti pratyavāyo na vidyate\nsvalpam apyasya dharmasya trāyate mahato bhayāt\n', 'na—not; iha—in this; abhikrama—efforts; nāśhaḥ—loss; asti—there is; pratyavāyaḥ—adverse result; na—not; vidyate—is; su-alpam—a little; api—even; asya—of this; dharmasya—occupation; trāyate—saves; mahataḥ—from great; bhayāt—danger\n', 'https://gita.github.io/gita/data/verse_recitation/2/87.mp3', 2, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (88, 'व्यवसायात्मिका बुद्धिरेकेह कुरुनन्दन।\n\nबहुशाखा ह्यनन्ताश्च बुद्धयोऽव्यवसायिनाम्।।2.41।।\n ', 'vyavasāyātmikā buddhir ekeha kuru-nandana\nbahu-śhākhā hyanantāśh cha buddhayo ’vyavasāyinām\n', 'vyavasāya-ātmikā—resolute; buddhiḥ—intellect; ekā—single; iha—on this path; kuru-nandana—descendent of the Kurus; bahu-śhākhāḥ—many-branched; hi—indeed; anantāḥ—endless; cha—also; buddhayaḥ—intellect; avyavasāyinām—of the irresolute\n', 'https://gita.github.io/gita/data/verse_recitation/2/88.mp3', 2, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (89, 'यामिमां पुष्पितां वाचं प्रवदन्त्यविपश्चितः।\n\nवेदवादरताः पार्थ नान्यदस्तीति वादिनः।।2.42।।\n ', 'yāmimāṁ puṣhpitāṁ vāchaṁ pravadanty-avipaśhchitaḥ\nveda-vāda-ratāḥ pārtha nānyad astīti vādinaḥ\n kāmātmānaḥ swarga-parā janma-karma-phala-pradām\nkriyā-viśheṣha-bahulāṁ bhogaiśhwarya-gatiṁ prati\n', 'yām imām—all these; puṣhpitām—flowery; vācham—words; pravadanti—speak; avipaśhchitaḥ—those with limited understanding; veda-vāda-ratāḥ—attached to the flowery words of the Vedas; pārtha—Arjun, the son of Pritha; na anyat—no other; asti—is; iti—thus; vādinaḥ—advocate;\n kāma-ātmānaḥ—desirous of sensual pleasure; swarga-parāḥ—aiming to achieve the heavenly planets; janma-karma-phala—high birth and fruitive results; pradāṁ—awarding; kriyā-viśheṣha—pompous ritualistic ceremonies; bahulām—various; bhoga—gratification; aiśhwarya—luxury; gatim—progress; prati—toward\n', 'https://gita.github.io/gita/data/verse_recitation/2/89.mp3', 2, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (90, 'कामात्मानः स्वर्गपरा जन्मकर्मफलप्रदाम्।\n\nक्रियाविशेषबहुलां भोगैश्वर्यगतिं प्रति।।2.43।।\n ', 'kāmātmānaḥ svarga-parā \n\n janma-karma-phala-pradām\n\n kriyā-viśeṣa-bahulāṁ\n\n bhogaiśvarya-gatiṁ prati\n', 'kāmaātmānaḥ—desirous   of  sense   gratification;  svarga-parāḥ—aiming to  achieve heavenly    planets;    janma-karma-phala-pradām—resulting  in  fruitive    action, good        birth,     etc.;      kriyā-viśeṣa—pompous       ceremonies;        bahulām—various; bhoga—sense       enjoyment;     aiśvarya—opulence;     gatim—progress;    prati—towards.', 'https://gita.github.io/gita/data/verse_recitation/2/90.mp3', 2, 43);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (91, 'भोगैश्वर्यप्रसक्तानां तयापहृतचेतसाम्।\n\nव्यवसायात्मिका बुद्धिः समाधौ न विधीयते।।2.44।।\n ', 'bhogaiśwvarya-prasaktānāṁ tayāpahṛita-chetasām\nvyavasāyātmikā buddhiḥ samādhau na vidhīyate\n', 'bhoga—gratification; aiśhwarya—luxury; prasaktānām—whose minds are deeply attached; tayā—by that; apahṛita-chetasām—bewildered in intellect; vyavasāya-ātmikā—resolute; buddhiḥ—intellect; samādhau—fulfilment; na—never; vidhīyate—occurs\n', 'https://gita.github.io/gita/data/verse_recitation/2/91.mp3', 2, 44);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (92, 'त्रैगुण्यविषया वेदा निस्त्रैगुण्यो भवार्जुन।\n\nनिर्द्वन्द्वो नित्यसत्त्वस्थो निर्योगक्षेम आत्मवान्।।2.45।।\n ', 'trai-guṇya-viṣhayā vedā nistrai-guṇyo bhavārjuna\nnirdvandvo nitya-sattva-stho niryoga-kṣhema ātmavān\n', 'trai-guṇya—of the three modes of material nature; viṣhayāḥ—subject matter; vedāḥ—Vedic scriptures; nistrai-guṇyaḥ—above the three modes of material nature, transcendental; bhava—be; arjuna—Arjun; nirdvandvaḥ—free from dualities; nitya-sattva-sthaḥ—eternally fixed in truth; niryoga-kṣhemaḥ—unconcerned about gain and preservation; ātma-vān—situated in the self\n', 'https://gita.github.io/gita/data/verse_recitation/2/92.mp3', 2, 45);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (93, 'यावानर्थ उदपाने सर्वतः संप्लुतोदके।\n\nतावान्सर्वेषु वेदेषु ब्राह्मणस्य विजानतः।।2.46।।\n ', 'yāvān artha udapāne sarvataḥ samplutodake\ntāvānsarveṣhu vedeṣhu brāhmaṇasya vijānataḥ\n', 'yāvān—whatever; arthaḥ—purpose; uda-pāne—a well of water; sarvataḥ—in all respects; sampluta-udake—by a large lake; tāvān—that many; sarveṣhu—in all; vedeṣhu—Vedas; brāhmaṇasya—one who realizes the Absolute Truth; vijānataḥ—who is in complete knowledge\n', 'https://gita.github.io/gita/data/verse_recitation/2/93.mp3', 2, 46);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (94, 'कर्मण्येवाधिकारस्ते मा फलेषु कदाचन।\n\nमा कर्मफलहेतुर्भूर्मा ते सङ्गोऽस्त्वकर्मणि।।2.47।।\n ', 'karmaṇy-evādhikāras te mā phaleṣhu kadāchana\nmā karma-phala-hetur bhūr mā te saṅgo ’stvakarmaṇi\n', 'karmaṇi—in prescribed duties; eva—only; adhikāraḥ—right; te—your; mā—not; phaleṣhu—in the fruits; kadāchana—at any time; mā—never; karma-phala—results of the activities; hetuḥ—cause; bhūḥ—be; mā—not; te—your; saṅgaḥ—attachment; astu—must be; akarmaṇi—in inaction\n', 'https://gita.github.io/gita/data/verse_recitation/2/94.mp3', 2, 47);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (95, 'योगस्थः कुरु कर्माणि सङ्गं त्यक्त्वा धनञ्जय।\n\nसिद्ध्यसिद्ध्योः समो भूत्वा समत्वं योग उच्यते।।2.48।।\n ', 'yoga-sthaḥ kuru karmāṇi saṅgaṁ tyaktvā dhanañjaya\nsiddhy-asiddhyoḥ samo bhūtvā samatvaṁ yoga uchyate\n', 'yoga-sthaḥ—being steadfast in yog; kuru—perform; karmāṇi—duties; saṅgam—attachment; tyaktvā—having abandoned; dhanañjaya—Arjun; siddhi-asiddhyoḥ—in success and failure; samaḥ—equipoised; bhūtvā—becoming; samatvam—equanimity; yogaḥ—Yog; uchyate—is called\n', 'https://gita.github.io/gita/data/verse_recitation/2/95.mp3', 2, 48);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (96, 'दूरेण ह्यवरं कर्म बुद्धियोगाद्धनञ्जय।\n\nबुद्धौ शरणमन्विच्छ कृपणाः फलहेतवः।।2.49।।\n ', 'dūreṇa hy-avaraṁ karma buddhi-yogād dhanañjaya\nbuddhau śharaṇam anvichchha kṛipaṇāḥ phala-hetavaḥ\n', 'dūreṇa—(discrad) from far away; hi—certainly; avaram—inferior; karma—reward-seeking actions; buddhi-yogāt—with the intellect established in Divine knowledge; dhanañjaya—Arjun; buddhau—divine knowledge and insight; śharaṇam—refuge; anvichchha—seek; kṛipaṇāḥ—miserly; phala-hetavaḥ—those seeking fruits of their work\n', 'https://gita.github.io/gita/data/verse_recitation/2/96.mp3', 2, 49);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (97, 'बुद्धियुक्तो जहातीह उभे सुकृतदुष्कृते।\n\nतस्माद्योगाय युज्यस्व योगः कर्मसु कौशलम्।।2.50।।\n ', 'buddhi-yukto jahātīha ubhe sukṛita-duṣhkṛite\ntasmād yogāya yujyasva yogaḥ karmasu kauśhalam\n', 'buddhi-yuktaḥ—endowed with wisdom; jahāti—get rid of; iha—in this life; ubhe—both; sukṛita-duṣhkṛite—good and bad deeds; tasmāt—therefore; yogāya—for Yog; yujyasva—strive for; yogaḥ—yog is; karmasu kauśhalam—the art of working skillfully\n', 'https://gita.github.io/gita/data/verse_recitation/2/97.mp3', 2, 50);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (98, 'कर्मजं बुद्धियुक्ता हि फलं त्यक्त्वा मनीषिणः।\n\nजन्मबन्धविनिर्मुक्ताः पदं गच्छन्त्यनामयम्।।2.51।।\n ', 'karma-jaṁ buddhi-yuktā hi phalaṁ tyaktvā manīṣhiṇaḥ\njanma-bandha-vinirmuktāḥ padaṁ gachchhanty-anāmayam\n', 'karma-jam—born of fruitive actions; buddhi-yuktāḥ—endowed with equanimity of intellect; hi—as; phalam—fruits; tyaktvā—abandoning; manīṣhiṇaḥ—the wise; janma-bandha-vinirmuktāḥ—freedom from the bondage of life and death; padam—state; gachchhanti—attain; anāmayam—devoid of sufferings\n', 'https://gita.github.io/gita/data/verse_recitation/2/98.mp3', 2, 51);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (99, 'यदा ते मोहकलिलं बुद्धिर्व्यतितरिष्यति।\n\nतदा गन्तासि निर्वेदं श्रोतव्यस्य श्रुतस्य च।।2.52।।\n ', 'yadā te moha-kalilaṁ buddhir vyatitariṣhyati\ntadā gantāsi nirvedaṁ śhrotavyasya śhrutasya cha\n', 'yadā—when; te—your; moha—delusion; kalilam—quagmire; buddhiḥ—intellect; vyatitariṣhyati—crosses; tadā—then; gantāsi—you shall acquire; nirvedam—indifferent; śhrotavyasya—to what is yet to be heard; śhrutasya—to what has been heard; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/2/99.mp3', 2, 52);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (100, 'श्रुतिविप्रतिपन्ना ते यदा स्थास्यति निश्चला।\n\nसमाधावचला बुद्धिस्तदा योगमवाप्स्यसि।।2.53।।\n ', 'śhruti-vipratipannā te yadā sthāsyati niśhchalā\nsamādhāv-achalā buddhis tadā yogam avāpsyasi\n', 'śhruti-vipratipannā—not allured by the fruitive sections of the Vedas; te—your; yadā—when; sthāsyati—remains; niśhchalā—steadfast; samādhau—in divine consciousness; achalā—steadfast; buddhiḥ—intellect; tadā—at that time; yogam—Yog; avāpsyasi—you will attain\n', 'https://gita.github.io/gita/data/verse_recitation/2/100.mp3', 2, 53);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (101, 'अर्जुन उवाच\n\nस्थितप्रज्ञस्य का भाषा समाधिस्थस्य केशव।\n\nस्थितधीः किं प्रभाषेत किमासीत व्रजेत किम्।।2.54।।\n ', 'arjuna uvācha\nsthita-prajñasya kā bhāṣhā samādhi-sthasya keśhava\nsthita-dhīḥ kiṁ prabhāṣheta kim āsīta vrajeta kim\n', 'arjunaḥ uvācha—Arjun said; sthita-prajñasya—one with steady intellect; kā—what; bhāṣhā—talk; samādhi-sthasya—situated in divine consciousness; keśhava—Shree Krishna, killer of the Keshi Demon; sthita-dhīḥ—enlightened person; kim—what; prabhāṣheta—talks; kim—how; āsīta—sits; vrajeta—walks; kim—how\n', 'https://gita.github.io/gita/data/verse_recitation/2/101.mp3', 2, 54);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (102, 'श्री भगवानुवाच\n\nप्रजहाति यदा कामान् सर्वान् पार्थ मनोगतान्।\n\nआत्मन्येवात्मना तुष्टः स्थितप्रज्ञस्तदोच्यते।।2.55।।\n ', 'śhrī bhagavān uvācha\nprajahāti yadā kāmān sarvān pārtha mano-gatān\nātmany-evātmanā tuṣhṭaḥ sthita-prajñas tadochyate\n', 'śhrī-bhagavān uvācha—The Supreme Lord said; prajahāti—discards; yadā—when; kāmān—selfish desires; sarvān—all; pārtha—Arjun, the son of Pritha; manaḥ-gatān—of the mind; ātmani—of the self; eva—only; ātmanā—by the purified mind; tuṣhṭaḥ—satisfied; sthita-prajñaḥ—one with steady intellect; tadā—at that time; uchyate—is said\n', 'https://gita.github.io/gita/data/verse_recitation/2/102.mp3', 2, 55);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (103, 'दुःखेष्वनुद्विग्नमनाः सुखेषु विगतस्पृहः।\n\nवीतरागभयक्रोधः स्थितधीर्मुनिरुच्यते।।2.56।।\n ', 'duḥkheṣhv-anudvigna-manāḥ sukheṣhu vigata-spṛihaḥ\nvīta-rāga-bhaya-krodhaḥ sthita-dhīr munir uchyate\n', 'duḥkheṣhu—amidst miseries; anudvigna-manāḥ—one whose mind is undisturbed; sukheṣhu—in pleasure; vigata-spṛihaḥ—without craving; vīta—free from; rāga—attachment; bhaya—fear; krodhaḥ—anger; sthita-dhīḥ—enlightened person; muniḥ—a sage; uchyate—is called\n', 'https://gita.github.io/gita/data/verse_recitation/2/103.mp3', 2, 56);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (104, 'यः सर्वत्रानभिस्नेहस्तत्तत्प्राप्य शुभाशुभम्।\n\nनाभिनन्दति न द्वेष्टि तस्य प्रज्ञा प्रतिष्ठिता।।2.57।।\n ', 'yaḥ sarvatrānabhisnehas tat tat prāpya śhubhāśhubham\nnābhinandati na dveṣhṭi tasya prajñā pratiṣhṭhitā\n', 'yaḥ—who; sarvatra—in all conditions; anabhisnehaḥ—unattached; tat—that; tat—that; prāpya—attaining; śhubha—good; aśhubham—evil; na—neither; abhinandati—delight in; na—nor; dveṣhṭi—dejected by; tasya—his; prajñā—knowledge; pratiṣhṭhitā—is fixed\n', 'https://gita.github.io/gita/data/verse_recitation/2/104.mp3', 2, 57);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (105, 'यदा संहरते चायं कूर्मोऽङ्गानीव सर्वशः।\n\nइन्द्रियाणीन्द्रियार्थेभ्यस्तस्य प्रज्ञा प्रतिष्ठिता।।2.58।।\n ', 'yadā sanharate chāyaṁ kūrmo ’ṅgānīva sarvaśhaḥ\nindriyāṇīndriyārthebhyas tasya prajñā pratiṣhṭhitā\n', 'yadā—when; sanharate—withdraw; cha—and; ayam—this; kūrmaḥ—tortoise; aṅgāni—limbs; iva—as; sarvaśhaḥ—fully; indriyāṇi—senses; indriya-arthebhyaḥ—from the sense objects; tasya—his; prajñā—divine wisdom; pratiṣhṭhitā—fixed in\n', 'https://gita.github.io/gita/data/verse_recitation/2/105.mp3', 2, 58);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (106, 'विषया विनिवर्तन्ते निराहारस्य देहिनः।\n\nरसवर्जं रसोऽप्यस्य परं दृष्ट्वा निवर्तते।।2.59।।\n ', 'viṣhayā vinivartante nirāhārasya dehinaḥ\nrasa-varjaṁ raso ’pyasya paraṁ dṛiṣhṭvā nivartate\n', 'viṣhayāḥ—objects for senses; vinivartante—restrain; nirāhārasya—practicing self restraint; dehinaḥ—for the embodied; rasa-varjam—cessation of taste; rasaḥ—taste; api—however; asya—person’s; param—the Supreme; dṛiṣhṭvā—on realization; nivartate—ceases to be\n', 'https://gita.github.io/gita/data/verse_recitation/2/106.mp3', 2, 59);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (107, 'यततो ह्यपि कौन्तेय पुरुषस्य विपश्चितः।\n\nइन्द्रियाणि प्रमाथीनि हरन्ति प्रसभं मनः।।2.60।।\n ', 'yatato hyapi kaunteya puruṣhasya vipaśhchitaḥ\nindriyāṇi pramāthīni haranti prasabhaṁ manaḥ\n', 'yatataḥ—while practicing self-control; hi—for; api—even; kaunteya—Arjun, the son of Kunti; puruṣhasya—of a person; vipaśhchitaḥ—one endowed with discrimination; indriyāṇi—the senses; pramāthīni—turbulent; haranti—carry away; prasabham—forcibly; manaḥ—the mind\n', 'https://gita.github.io/gita/data/verse_recitation/2/107.mp3', 2, 60);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (108, 'तानि सर्वाणि संयम्य युक्त आसीत मत्परः।\n\nवशे हि यस्येन्द्रियाणि तस्य प्रज्ञा प्रतिष्ठिता।।2.61।।\n ', 'tāni sarvāṇi sanyamya yukta āsīta mat-paraḥ\nvaśhe hi yasyendriyāṇi tasya prajñā pratiṣhṭhitā\n', 'tāni—them; sarvāṇi—all; sanyamya—subduing; yuktaḥ—united; āsīta—seated; mat-paraḥ—toward me (Shree Krishna); vaśhe—control; hi—certainly; yasya—whose; indriyāṇi—senses; tasya—their; prajñā—perfect knowledge pratiṣhṭhitā\n', 'https://gita.github.io/gita/data/verse_recitation/2/108.mp3', 2, 61);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (109, 'ध्यायतो विषयान्पुंसः सङ्गस्तेषूपजायते।\n\nसङ्गात् संजायते कामः कामात्क्रोधोऽभिजायते।।2.62।।\n ', 'dhyāyato viṣhayān puṁsaḥ saṅgas teṣhūpajāyate\nsaṅgāt sañjāyate kāmaḥ kāmāt krodho ’bhijāyate\n', 'dhyāyataḥ—contemplating; viṣhayān—sense objects; puṁsaḥ—of a person; saṅgaḥ—attachment; teṣhu—to them (sense objects); upajāyate—arises; saṅgāt—from attachment; sañjāyate—develops; kāmaḥ—desire; kāmāt—from desire; krodhaḥ—anger; abhijāyate—arises\n', 'https://gita.github.io/gita/data/verse_recitation/2/109.mp3', 2, 62);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (110, 'क्रोधाद्भवति संमोहः संमोहात्स्मृतिविभ्रमः।\n\nस्मृतिभ्रंशाद् बुद्धिनाशो बुद्धिनाशात्प्रणश्यति।।2.63।।\n ', 'krodhād bhavati sammohaḥ sammohāt smṛiti-vibhramaḥ\nsmṛiti-bhranśhād buddhi-nāśho buddhi-nāśhāt praṇaśhyati\n', 'krodhāt—from anger; bhavati—comes; sammohaḥ—clouding of judgement; sammohāt—from clouding of judgement; smṛiti—memory; vibhramaḥ—bewilderment; smṛiti-bhranśhāt—from bewilderment of memory; buddhi-nāśhaḥ—destruction of intellect; buddhi-nāśhāt—from destruction of intellect; praṇaśhyati—one is ruined\n', 'https://gita.github.io/gita/data/verse_recitation/2/110.mp3', 2, 63);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (111, 'रागद्वेषवियुक्तैस्तु विषयानिन्द्रियैश्चरन्।\n\nआत्मवश्यैर्विधेयात्मा प्रसादमधिगच्छति।।2.64।।\n ', 'rāga-dveṣha-viyuktais tu viṣhayān indriyaiśh charan\nātma-vaśhyair-vidheyātmā prasādam adhigachchhati\n', 'rāga—attachment; dveṣha—aversion; viyuktaiḥ—free; tu—but; viṣhayān—objects of the senses; indriyaiḥ—by the senses; charan—while using; ātma-vaśhyaiḥ—controlling one’s mind; vidheya-ātmā—one who controls the mind; prasādam—the Grace of God; adhigachchhati—attains\n', 'https://gita.github.io/gita/data/verse_recitation/2/111.mp3', 2, 64);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (112, 'प्रसादे सर्वदुःखानां हानिरस्योपजायते।\n\nप्रसन्नचेतसो ह्याशु बुद्धिः पर्यवतिष्ठते।।2.65।।\n ', 'prasāde sarva-duḥkhānāṁ hānir asyopajāyate\nprasanna-chetaso hyāśhu buddhiḥ paryavatiṣhṭhate\n', 'prasāde—by divine grace; sarva—all; duḥkhānām—of sorrows; hāniḥ—destruction; asya—his; upajāyate—comes; prasanna-chetasaḥ—with a tranquil mind; hi—indeed; āśhu—soon; buddhiḥ—intellect; paryavatiṣhṭhate—becomes firmly established\n', 'https://gita.github.io/gita/data/verse_recitation/2/112.mp3', 2, 65);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (113, 'नास्ति बुद्धिरयुक्तस्य न चायुक्तस्य भावना।\n\nन चाभावयतः शान्तिरशान्तस्य कुतः सुखम्।।2.66।।\n ', 'nāsti buddhir-ayuktasya na chāyuktasya bhāvanā\nna chābhāvayataḥ śhāntir aśhāntasya kutaḥ sukham\n', 'na—not; asti—is; buddhiḥ—intellect; ayuktasya—not united; na—not; cha—and; ayuktasya—not united; bhāvanā—contemplation; na—nor; cha—and; abhāvayataḥ—for those not united; śhāntiḥ—peace; aśhāntasya—of the unpeaceful; kutaḥ—where; sukham—happiness\n', 'https://gita.github.io/gita/data/verse_recitation/2/113.mp3', 2, 66);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (114, 'इन्द्रियाणां हि चरतां यन्मनोऽनुविधीयते।\n\nतदस्य हरति प्रज्ञां वायुर्नावमिवाम्भसि।।2.67।।\n ', 'indriyāṇāṁ hi charatāṁ yan mano ’nuvidhīyate\ntadasya harati prajñāṁ vāyur nāvam ivāmbhasi\n', 'indriyāṇām—of the senses; hi—indeed; charatām—roaming; yat—which; manaḥ—the mind; anuvidhīyate—becomes constantly engaged; tat—that; asya—of that; harati—carries away; prajñām—intellect; vāyuḥ—wind; nāvam—boat; iva—as; ambhasi—on the water\n', 'https://gita.github.io/gita/data/verse_recitation/2/114.mp3', 2, 67);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (115, 'तस्माद्यस्य महाबाहो निगृहीतानि सर्वशः।\n\nइन्द्रियाणीन्द्रियार्थेभ्यस्तस्य प्रज्ञा प्रतिष्ठिता।।2.68।।\n ', 'tasmād yasya mahā-bāho nigṛihītāni sarvaśhaḥ\nindriyāṇīndriyārthebhyas tasya prajñā pratiṣhṭhitā\n', 'tasmāt—therefore; yasya—whose; mahā-bāho—mighty-armed one; nigṛihītāni—restrained; sarvaśhaḥ—completely; indriyāṇi—senses; indriya-arthebhyaḥ—from sense objects; tasya—of that person; prajñā—transcendental knowledge; pratiṣhṭhitā—remains fixed\n', 'https://gita.github.io/gita/data/verse_recitation/2/115.mp3', 2, 68);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (116, 'या निशा सर्वभूतानां तस्यां जागर्ति संयमी।\n\nयस्यां जाग्रति भूतानि सा निशा पश्यतो मुनेः।।2.69।।\n ', 'yā niśhā sarva-bhūtānāṁ tasyāṁ jāgarti sanyamī\nyasyāṁ jāgrati bhūtāni sā niśhā paśhyato muneḥ\n', 'yā—which; niśhā—night; sarva-bhūtānām—of all living beings; tasyām—in that; jāgarti—is awake; sanyamī—self-controlled; yasyām—in which; jāgrati—are awake; bhūtāni—creatures; sā—that; niśhā—night; paśhyataḥ—see; muneḥ—sage\n', 'https://gita.github.io/gita/data/verse_recitation/2/116.mp3', 2, 69);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (117, 'आपूर्यमाणमचलप्रतिष्ठं\n\nसमुद्रमापः प्रविशन्ति यद्वत्।\n\nतद्वत्कामा यं प्रविशन्ति सर्वे\n\nस शान्तिमाप्नोति न कामकामी।।2.70।।\n ', 'āpūryamāṇam achala-pratiṣhṭhaṁ\nsamudram āpaḥ praviśhanti yadvat\ntadvat kāmā yaṁ praviśhanti sarve\nsa śhāntim āpnoti na kāma-kāmī\n', 'āpūryamāṇam—filled from all sides; achala-pratiṣhṭham—undisturbed; samudram—ocean; āpaḥ—waters; praviśhanti—enter; yadvat—as; tadvat—likewise; kāmāḥ—desires; yam—whom; praviśhanti—enter; sarve—all; saḥ—that person; śhāntim—peace; āpnoti—attains; na—not; kāma-kāmī—one who strives to satisfy desires\n', 'https://gita.github.io/gita/data/verse_recitation/2/117.mp3', 2, 70);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (118, 'विहाय कामान्यः सर्वान्पुमांश्चरति निःस्पृहः।\n\nनिर्ममो निरहंकारः स शांतिमधिगच्छति।।2.71।।\n ', 'vihāya kāmān yaḥ sarvān pumānśh charati niḥspṛihaḥ\nnirmamo nirahankāraḥ sa śhāntim adhigachchhati\n', 'vihāya—giving up; kāmān—material desires; yaḥ—who; sarvān—all; pumān—a person; charati—lives; niḥspṛihaḥ—free from hankering; nirmamaḥ—without a sense of proprietorship; nirahankāraḥ—without egoism; saḥ—that person; śhāntim—perfect peace; adhigachchhati—attains\n', 'https://gita.github.io/gita/data/verse_recitation/2/118.mp3', 2, 71);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (119, 'एषा ब्राह्मी स्थितिः पार्थ नैनां प्राप्य विमुह्यति।\n\nस्थित्वाऽस्यामन्तकालेऽपि ब्रह्मनिर्वाणमृच्छति।।2.72।।\n ', 'eṣhā brāhmī sthitiḥ pārtha naināṁ prāpya vimuhyati\nsthitvāsyām anta-kāle ’pi brahma-nirvāṇam ṛichchhati\n', 'eṣhā—such; brāhmī sthitiḥ—state of God-realization; pārtha—Arjun, the son of Pritha; na—never; enām—this; prāpya—having attained; vimuhyati—is deluded; sthitvā—being established; asyām—in this; anta-kāle—at the hour of death; api—even; brahma-nirvāṇam—liberation from Maya; ṛichchhati—attains\n', 'https://gita.github.io/gita/data/verse_recitation/2/119.mp3', 2, 72);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (120, 'अर्जुन उवाच\n\nज्यायसी चेत्कर्मणस्ते मता बुद्धिर्जनार्दन।\n\nतत्किं कर्मणि घोरे मां नियोजयसि केशव।।3.1।।\n ', 'arjuna uvācha\njyāyasī chet karmaṇas te matā buddhir janārdana\ntat kiṁ karmaṇi ghore māṁ niyojayasi keśhava\n', 'arjunaḥ uvācha—Arjun said; jyāyasī—superior; chet—if; karmaṇaḥ—than fruitive action; te—by you; matā—is considered; buddhiḥ—intellect; janārdana—he who looks after the public, Krishna; tat—then; kim—why; karmaṇi—action; ghore—terrible; mām—me; niyojayasi—do you engage; keśhava—Krishna, the killer of the demon named Keshi;\n', 'https://gita.github.io/gita/data/verse_recitation/3/120.mp3', 3, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (121, 'व्यामिश्रेणेव वाक्येन बुद्धिं मोहयसीव मे।\n\nतदेकं वद निश्िचत्य येन श्रेयोऽहमाप्नुयाम्।।3.2।।\n ', 'vyāmiśhreṇeva vākyena buddhiṁ mohayasīva me\ntad ekaṁ vada niśhchitya yena śhreyo ’ham āpnuyām\n', 'vyāmiśhreṇa iva—by your apparently ambiguous; vākyena—words; buddhim—intellect; mohayasi—I am getting bewildered; iva—as it were; me—my; tat—therefore; ekam—one; vada—please tell; niśhchitya—decisively; yena—by which; śhreyaḥ—the highest good; aham—I; āpnuyām—may attain\n', 'https://gita.github.io/gita/data/verse_recitation/3/121.mp3', 3, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (122, 'श्री भगवानुवाच\n\nलोकेऽस्मिन्द्विविधा निष्ठा पुरा प्रोक्ता मयानघ।\n\nज्ञानयोगेन सांख्यानां कर्मयोगेन योगिनाम्।।3.3।।\n ', 'śhrī bhagavān uvācha\nloke’smin dvi-vidhā niṣhṭhā purā proktā mayānagha\njñāna-yogena sāṅkhyānāṁ karma-yogena yoginām\n', 'śhrī-bhagavān uvācha—the Blessed Lord said; loke—in the world; asmin—this; dvi-vidhā—two kinds of; niṣhṭhā—faith; purā—previously; proktā—explained; mayā—by me (Shree Krishna); anagha—sinless; jñāna-yogena—through the path of knowledge; sānkhyānām—for those inclined toward contemplation; karma-yogena—through the path of action; yoginām—of the yogis\n', 'https://gita.github.io/gita/data/verse_recitation/3/122.mp3', 3, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (123, 'न कर्मणामनारम्भान्नैष्कर्म्यं पुरुषोऽश्नुते।\n\nन च संन्यसनादेव सिद्धिं समधिगच्छति।।3.4।।\n ', 'na karmaṇām anārambhān naiṣhkarmyaṁ puruṣho ’śhnute\nna cha sannyasanād eva siddhiṁ samadhigachchhati\n', 'na—not; karmaṇām—of actions; anārambhāt—by abstaining from; naiṣhkarmyam—freedom from karmic reactions; puruṣhaḥ—a person; aśhnute—attains; na—not; cha—and; sannyasanāt—by renunciation; eva—only; siddhim—perfection; samadhigachchhati—attains\n', 'https://gita.github.io/gita/data/verse_recitation/3/123.mp3', 3, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (124, 'न हि कश्िचत्क्षणमपि जातु तिष्ठत्यकर्मकृत्।\n\nकार्यते ह्यवशः कर्म सर्वः प्रकृतिजैर्गुणैः।।3.5।।\n ', 'na hi kaśhchit kṣhaṇam api jātu tiṣhṭhatyakarma-kṛit\nkāryate hyavaśhaḥ karma sarvaḥ prakṛiti-jair guṇaiḥ\n', 'na—not; hi—certainly; kaśhchit—anyone; kṣhaṇam—a moment; api—even; jātu—ever; tiṣhṭhati—can remain; akarma-kṛit—without action; kāryate—are performed; hi—certainly; avaśhaḥ—helpless; karma—work; sarvaḥ—all; prakṛiti-jaiḥ—born of material nature; guṇaiḥ—by the qualities\n', 'https://gita.github.io/gita/data/verse_recitation/3/124.mp3', 3, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (125, 'कर्मेन्द्रियाणि संयम्य य आस्ते मनसा स्मरन्।\n\nइन्द्रियार्थान्विमूढात्मा मिथ्याचारः स उच्यते।।3.6।।\n ', 'karmendriyāṇi sanyamya ya āste manasā smaran\nindriyārthān vimūḍhātmā mithyāchāraḥ sa uchyate\n', 'karma-indriyāṇi—the organs of action; sanyamya—restrain; yaḥ—who; āste—remain; manasā—in the mind; smaran—to remember; indriya-arthān—sense objects; vimūḍha-ātmā—the deluded; mithyā-āchāraḥ—hypocrite; saḥ—they; uchyate—are called\n', 'https://gita.github.io/gita/data/verse_recitation/3/125.mp3', 3, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (126, 'यस्त्विन्द्रियाणि मनसा नियम्यारभतेऽर्जुन।\n\nकर्मेन्द्रियैः कर्मयोगमसक्तः स विशिष्यते।।3.7।।\n ', 'yas tvindriyāṇi manasā niyamyārabhate ’rjuna\nkarmendriyaiḥ karma-yogam asaktaḥ sa viśhiṣhyate\n', 'yaḥ—who; tu—but; indriyāṇi—the senses; manasā—by the mind; niyamya—control; ārabhate—begins; arjuna—Arjun; karma-indriyaiḥ—by the working senses; karma-yogam—karm yog; asaktaḥ—without attachment; saḥ—they; viśhiṣhyate—are superior\n', 'https://gita.github.io/gita/data/verse_recitation/3/126.mp3', 3, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (127, 'नियतं कुरु कर्म त्वं कर्म ज्यायो ह्यकर्मणः।\n\nशरीरयात्रापि च ते न प्रसिद्ध्येदकर्मणः।।3.8।।\n ', 'niyataṁ kuru karma tvaṁ karma jyāyo hyakarmaṇaḥ\nśharīra-yātrāpi cha te na prasiddhyed akarmaṇaḥ\n', 'niyatam—constantly; kuru—perform; karma—Vedic duties; tvam—you; karma—action; jyāyaḥ—superior; hi—certainly; akarmaṇaḥ—than inaction; śharīra—bodily; yātrā—maintenance; api—even; cha—and; te—your; na prasiddhyet—would not be possible; akarmaṇaḥ—inaction\n ', 'https://gita.github.io/gita/data/verse_recitation/3/127.mp3', 3, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (128, 'यज्ञार्थात्कर्मणोऽन्यत्र लोकोऽयं कर्मबन्धनः।\n\nतदर्थं कर्म कौन्तेय मुक्तसंगः समाचर।।3.9।।\n ', 'yajñārthāt karmaṇo ’nyatra loko ’yaṁ karma-bandhanaḥ\ntad-arthaṁ karma kaunteya mukta-saṅgaḥ samāchara\n', 'yajña-arthāt—for the sake of sacrifice; karmaṇaḥ—than action; anyatra—else; lokaḥ—material world; ayam—this; karma-bandhanaḥ—bondage through one’s work; tat—that; artham—for the sake of; karma—action; kaunteya—Arjun, the son of Kunti; mukta-saṅgaḥ—free from attachment; samāchara—perform properly\n', 'https://gita.github.io/gita/data/verse_recitation/3/128.mp3', 3, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (129, 'सहयज्ञाः प्रजाः सृष्ट्वा पुरोवाच प्रजापतिः।\n\nअनेन प्रसविष्यध्वमेष वोऽस्त्विष्टकामधुक्।।3.10।।\n ', 'saha-yajñāḥ prajāḥ sṛiṣhṭvā purovācha prajāpatiḥ\nanena prasaviṣhyadhvam eṣha vo ’stviṣhṭa-kāma-dhuk\n', 'saha—along with; yajñāḥ—sacrifices; prajāḥ—humankind; sṛiṣhṭvā—created; purā—in beginning; uvācha—said; prajā-patiḥ—Brahma; anena—by this; prasaviṣhyadhvam—increase prosperity; eṣhaḥ—these; vaḥ—your; astu—shall be; iṣhṭa-kāma-dhuk—bestower of all wishes\n', 'https://gita.github.io/gita/data/verse_recitation/3/129.mp3', 3, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (130, 'देवान्भावयतानेन ते देवा भावयन्तु वः।\n\nपरस्परं भावयन्तः श्रेयः परमवाप्स्यथ।।3.11।।\n ', 'devān bhāvayatānena te devā bhāvayantu vaḥ\nparasparaṁ bhāvayantaḥ śhreyaḥ param avāpsyatha\n', 'devān—celestial gods; bhāvayatā—will be pleased; anena—by these (sacrifices); te—those; devāḥ—celestial gods; bhāvayantu—will be pleased; vaḥ—you; parasparam—one another; bhāvayantaḥ—pleasing one another; śhreyaḥ—prosperity; param—the supreme; avāpsyatha—shall achieve\n', 'https://gita.github.io/gita/data/verse_recitation/3/130.mp3', 3, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (131, 'इष्टान्भोगान्हि वो देवा दास्यन्ते यज्ञभाविताः।\n\nतैर्दत्तानप्रदायैभ्यो यो भुङ्क्ते स्तेन एव सः।।3.12।।\n ', 'iṣhṭān bhogān hi vo devā dāsyante yajña-bhāvitāḥ\ntair dattān apradāyaibhyo yo bhuṅkte stena eva saḥ\n', 'iṣhṭān—desired; bhogān—necessities of life; hi—certainly; vaḥ—unto you; devāḥ—the celestial gods; dāsyante—will grant; yajña-bhāvitāḥ—satisfied by sacrifice; taiḥ—by them; dattān—things granted; apradāya—without offering; ebhyaḥ—to them; yaḥ—who; bhuṅkte—enjoys; stenaḥ—thieves; eva—verily; saḥ—they\n', 'https://gita.github.io/gita/data/verse_recitation/3/131.mp3', 3, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (132, 'यज्ञशिष्टाशिनः सन्तो मुच्यन्ते सर्वकिल्बिषैः।\n\nभुञ्जते ते त्वघं पापा ये पचन्त्यात्मकारणात्।।3.13।।\n ', 'yajña-śhiṣhṭāśhinaḥ santo muchyante sarva-kilbiṣhaiḥ\nbhuñjate te tvaghaṁ pāpā ye pachantyātma-kāraṇāt\n', 'yajña-śhiṣhṭa—of remnants of food offered in sacrifice; aśhinaḥ—eaters; santaḥ—saintly persons; muchyante—are released; sarva—all kinds of; kilbiṣhaiḥ—from sins; bhuñjate—enjoy; te—they; tu—but; agham—sins; pāpāḥ—sinners; ye—who; pachanti—cook (food); ātma-kāraṇāt—for their own sake\n', 'https://gita.github.io/gita/data/verse_recitation/3/132.mp3', 3, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (133, 'अन्नाद्भवन्ति भूतानि पर्जन्यादन्नसम्भवः।\n\nयज्ञाद्भवति पर्जन्यो यज्ञः कर्मसमुद्भवः।।3.14।।\n ', 'annād bhavanti bhūtāni parjanyād anna-sambhavaḥ\nyajñād bhavati parjanyo yajñaḥ karma-samudbhavaḥ\n', 'annāt—from food; bhavanti—subsist; bhūtāni—living beings; parjanyāt—from rains; anna—of food grains; sambhavaḥ—production; yajñāt—from the performance of sacrifice; bhavati—becomes possible; parjanyaḥ—rain; yajñaḥ—performance of sacrifice; karma—prescribed duties; samudbhavaḥ—born of\n', 'https://gita.github.io/gita/data/verse_recitation/3/133.mp3', 3, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (134, 'कर्म ब्रह्मोद्भवं विद्धि ब्रह्माक्षरसमुद्भवम्।\n\nतस्मात्सर्वगतं ब्रह्म नित्यं यज्ञे प्रतिष्ठितम्।।3.15।।\n ', 'karma brahmodbhavaṁ viddhi brahmākṣhara-samudbhavam\ntasmāt sarva-gataṁ brahma nityaṁ yajñe pratiṣhṭhitam\n', 'karma—duties; brahma—in the Vedas; udbhavam—manifested; viddhi—you should know; brahma—The Vedas; akṣhara—from the Imperishable (God); samudbhavam—directly manifested; tasmāt—therefore; sarva-gatam—all-pervading; brahma—The Lord; nityam—eternally; yajñe—in sacrifice; pratiṣhṭhitam—established\n', 'https://gita.github.io/gita/data/verse_recitation/3/134.mp3', 3, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (135, 'एवं प्रवर्तितं चक्रं नानुवर्तयतीह यः।\n\nअघायुरिन्द्रियारामो मोघं पार्थ स जीवति।।3.16।।\n ', 'evaṁ pravartitaṁ chakraṁ nānuvartayatīha yaḥ\naghāyur indriyārāmo moghaṁ pārtha sa jīvati\n', 'evam—thus; pravartitam—set into motion; chakram—cycle; na—not; anuvartayati—follow; iha—in this life; yaḥ—who; agha-āyuḥ—sinful living; indriya-ārāmaḥ—for the delight of their senses; mogham—vainly; pārtha—Arjun, the son of Pritha; saḥ—they; jīvati—live\n', 'https://gita.github.io/gita/data/verse_recitation/3/135.mp3', 3, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (136, 'यस्त्वात्मरतिरेव स्यादात्मतृप्तश्च मानवः।\n\nआत्मन्येव च सन्तुष्टस्तस्य कार्यं न विद्यते।।3.17।।\n ', 'yas tvātma-ratir eva syād ātma-tṛiptaśh cha mānavaḥ\nātmanyeva cha santuṣhṭas tasya kāryaṁ na vidyate\n', 'yaḥ—who; tu—but; ātma-ratiḥ—rejoice in the self; eva—certainly; syāt—is; ātma-tṛiptaḥ—self-satisfied; cha—and; mānavaḥ—human being; ātmani—in the self; eva—certainly; cha—and; santuṣhṭaḥ—satisfied; tasya—his; kāryam—duty; na—not; vidyate—exist\n', 'https://gita.github.io/gita/data/verse_recitation/3/136.mp3', 3, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (137, 'नैव तस्य कृतेनार्थो नाकृतेनेह कश्चन।\n\nन चास्य सर्वभूतेषु कश्िचदर्थव्यपाश्रयः।।3.18।।\n ', 'naiva tasya kṛitenārtho nākṛiteneha kaśhchana\nna chāsya sarva-bhūteṣhu kaśhchid artha-vyapāśhrayaḥ\n', 'na—not; eva—indeed; tasya—his; kṛitena—by discharge of duty; arthaḥ—gain; na—not; akṛitena—without discharge of duty; iha—here; kaśhchana—whatsoever; na—never; cha—and; asya—of that person; sarva-bhūteṣhu—among all living beings; kaśhchit—any; artha—necessity; vyapāśhrayaḥ—to depend upon\n', 'https://gita.github.io/gita/data/verse_recitation/3/137.mp3', 3, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (138, 'तस्मादसक्तः सततं कार्यं कर्म समाचर।\n\nअसक्तो ह्याचरन्कर्म परमाप्नोति पूरुषः।।3.19।।\n ', 'tasmād asaktaḥ satataṁ kāryaṁ karma samāchara\nasakto hyācharan karma param āpnoti pūruṣhaḥ\n', 'tasmāt—therefore; asaktaḥ—without attachment; satatam—constantly; kāryam—duty; karma—action; samāchara—perform; asaktaḥ—unattached; hi—certainly; ācharan—performing; karma—work; param—the Supreme; āpnoti—attains; pūruṣhaḥ—a person\n', 'https://gita.github.io/gita/data/verse_recitation/3/138.mp3', 3, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (139, 'कर्मणैव हि संसिद्धिमास्थिता जनकादयः।\n\nलोकसंग्रहमेवापि संपश्यन्कर्तुमर्हसि।।3.20।।\n ', 'karmaṇaiva hi sansiddhim āsthitā janakādayaḥ\nloka-saṅgraham evāpi sampaśhyan kartum arhasi\n', 'karmaṇā—by the performance of prescribed duties; eva—only; hi—certainly; sansiddhim—perfection; āsthitāḥ—attained; janaka-ādayaḥ—King Janak and other kings; loka-saṅgraham—for the welfare of the masses; eva api—only; sampaśhyan—considering; kartum—to perform; arhasi—you should;\n', 'https://gita.github.io/gita/data/verse_recitation/3/139.mp3', 3, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (140, 'यद्यदाचरति श्रेष्ठस्तत्तदेवेतरो जनः।\n\nस यत्प्रमाणं कुरुते लोकस्तदनुवर्तते।।3.21।।\n ', 'yad yad ācharati śhreṣhṭhas tat tad evetaro janaḥ\nsa yat pramāṇaṁ kurute lokas tad anuvartate\n', 'yat yat—whatever; ācharati—does; śhreṣhṭhaḥ—the best; tat tat—that (alone); eva—certainly; itaraḥ—common; janaḥ—people; saḥ—they; yat—whichever; pramāṇam—standard; kurute—perform; lokaḥ—world; tat—that; anuvartate—pursues\n', 'https://gita.github.io/gita/data/verse_recitation/3/140.mp3', 3, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (141, 'न मे पार्थास्ति कर्तव्यं त्रिषु लोकेषु किञ्चन।\n\nनानवाप्तमवाप्तव्यं वर्त एव च कर्मणि।।3.22।।\n ', 'na me pārthāsti kartavyaṁ triṣhu lokeṣhu kiñchana\nnānavāptam avāptavyaṁ varta eva cha karmaṇi\n', 'na—not; me—mine; pārtha—Arjun; asti—is; kartavyam—duty; triṣhu—in the three; lokeṣhu—worlds; kiñchana—any; na—not; anavāptam—to be attained; avāptavyam—to be gained; varte—I am engaged; eva—yet; cha—also; karmaṇi—in prescribed duties\n', 'https://gita.github.io/gita/data/verse_recitation/3/141.mp3', 3, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (142, 'यदि ह्यहं न वर्तेयं जातु कर्मण्यतन्द्रितः।\n\nमम वर्त्मानुवर्तन्ते मनुष्याः पार्थ सर्वशः।।3.23।।\n ', 'yadi hyahaṁ na varteyaṁ jātu karmaṇyatandritaḥ\nmama vartmānuvartante manuṣhyāḥ pārtha sarvaśhaḥ\n', 'yadi—if; hi—certainly; aham—I; na—not; varteyam—thus engage; jātu—ever; karmaṇi—in the performance of prescribed duties; atandritaḥ—carefully; mama—my; vartma—path; anuvartante—follow; manuṣhyāḥ—all men; pārtha—Arjun, the son of Pritha; sarvaśhaḥ—in all respects\n', 'https://gita.github.io/gita/data/verse_recitation/3/142.mp3', 3, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (143, 'उत्सीदेयुरिमे लोका न कुर्यां कर्म चेदहम्।\n\nसङ्करस्य च कर्ता स्यामुपहन्यामिमाः प्रजाः।।3.24।।\n ', 'utsīdeyur ime lokā na kuryāṁ karma ched aham\nsankarasya cha kartā syām upahanyām imāḥ prajāḥ\n', 'utsīdeyuḥ—would perish; ime—all these; lokāḥ—worlds; na—not; kuryām—I perform; karma—prescribed duties; chet—if; aham—I; sankarasya—of uncultured population; cha—and; kartā—responsible; syām—would be; upahanyām—would destroy; imāḥ—all these; prajāḥ—living entities\n', 'https://gita.github.io/gita/data/verse_recitation/3/143.mp3', 3, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (144, 'सक्ताः कर्मण्यविद्वांसो यथा कुर्वन्ति भारत।\n\nकुर्याद्विद्वांस्तथासक्तश्िचकीर्षुर्लोकसंग्रहम्।।3.25।।\n ', 'saktāḥ karmaṇyavidvānso yathā kurvanti bhārata\nkuryād vidvāns tathāsaktaśh chikīrṣhur loka-saṅgraham\n', 'saktāḥ—attached; karmaṇi—duties; avidvānsaḥ—the ignorant; yathā—as much as; kurvanti—act; bhārata—scion of Bharat (Arjun); kuryāt—should do; vidvān—the wise; tathā—thus; asaktaḥ—unattached; chikīrṣhuḥ—wishing; loka-saṅgraham—welfare of the world\n', 'https://gita.github.io/gita/data/verse_recitation/3/144.mp3', 3, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (145, 'न बुद्धिभेदं जनयेदज्ञानां कर्मसङ्गिनाम्।\n\nजोषयेत्सर्वकर्माणि विद्वान् युक्तः समाचरन्।।3.26।।\n ', 'na buddhi-bhedaṁ janayed ajñānāṁ karma-saṅginām\njoṣhayet sarva-karmāṇi vidvān yuktaḥ samācharan\n', 'na—not; buddhi-bhedam—discord in the intellects; janayet—should create; ajñānām—of the ignorant; karma-saṅginām—who are attached to fruitive actions; joṣhayet—should inspire (them) to perform; sarva—all; karmāṇi—prescribed; vidvān—the wise; yuktaḥ—enlightened; samācharan—performing properly\n', 'https://gita.github.io/gita/data/verse_recitation/3/145.mp3', 3, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (146, 'प्रकृतेः क्रियमाणानि गुणैः कर्माणि सर्वशः।\n\nअहङ्कारविमूढात्मा कर्ताऽहमिति मन्यते।।3.27।।\n ', 'prakṛiteḥ kriyamāṇāni guṇaiḥ karmāṇi sarvaśhaḥ\nahankāra-vimūḍhātmā kartāham iti manyate\n', 'prakṛiteḥ—of material nature; kriyamāṇāni—carried out; guṇaiḥ—by the three modes; karmāṇi—activities; sarvaśhaḥ—all kinds of; ahankāra-vimūḍha-ātmā—those who are bewildered by the ego and misidentify themselves with the body; kartā—the doer; aham—I; iti—thus; manyate—thinks\n', 'https://gita.github.io/gita/data/verse_recitation/3/146.mp3', 3, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (147, 'तत्त्ववित्तु महाबाहो गुणकर्मविभागयोः।\n\nगुणा गुणेषु वर्तन्त इति मत्वा न सज्जते।।3.28।।\n ', 'tattva-vit tu mahā-bāho guṇa-karma-vibhāgayoḥ\nguṇā guṇeṣhu vartanta iti matvā na sajjate\n', 'tattva-vit—the knower of the Truth; tu—but; mahā-bāho—mighty-armed one; guṇa-karma—from guṇas and karma; vibhāgayoḥ—distinguish; guṇāḥ—modes of material nature in the shape of the senses, mind, etc; guṇeṣhu—modes of material nature in the shape of objects of perception; vartante—are engaged; iti—thus; matvā—knowing; na—never; sajjate—becomes attached\n', 'https://gita.github.io/gita/data/verse_recitation/3/147.mp3', 3, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (148, 'प्रकृतेर्गुणसम्मूढाः सज्जन्ते गुणकर्मसु।\n\nतानकृत्स्नविदो मन्दान्कृत्स्नविन्न विचालयेत्।।3.29।।\n ', 'prakṛiter guṇa-sammūḍhāḥ sajjante guṇa-karmasu\ntān akṛitsna-vido mandān kṛitsna-vin na vichālayet\n', 'prakṛiteḥ—of material nature; guṇa—by the modes of material nature; sammūḍhāḥ—deluded; sajjante—become attached; guṇa-karmasu—to results of actions; tān—those; akṛitsna-vidaḥ—persons without knowledge; mandān—the ignorant; kṛitsna-vit—persons with knowledge; na vichālayet—should not unsettle\n', 'https://gita.github.io/gita/data/verse_recitation/3/148.mp3', 3, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (149, 'मयि सर्वाणि कर्माणि संन्यस्याध्यात्मचेतसा।\n\nनिराशीर्निर्ममो भूत्वा युध्यस्व विगतज्वरः।।3.30।।\n ', 'mayi sarvāṇi karmāṇi sannyasyādhyātma-chetasā\nnirāśhīr nirmamo bhūtvā yudhyasva vigata-jvaraḥ\n', 'mayi—unto me; sarvāṇi—all; karmāṇi—works; sannyasya—renouncing completely; adhyātma-chetasā—with the thoughts resting on God; nirāśhīḥ—free from hankering for the results of the actions; nirmamaḥ—without ownership; bhūtvā—so being; yudhyasva—fight; vigata-jvaraḥ—without mental fever\n', 'https://gita.github.io/gita/data/verse_recitation/3/149.mp3', 3, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (150, 'ये मे मतमिदं नित्यमनुतिष्ठन्ति मानवाः।\n\nश्रद्धावन्तोऽनसूयन्तो मुच्यन्ते तेऽपि कर्मभिः।।3.31।।\n ', 'ye me matam idaṁ nityam anutiṣhṭhanti mānavāḥ\nśhraddhāvanto ’nasūyanto muchyante te ’pi karmabhiḥ\n', 'ye—who; me—my; matam—teachings; idam—these; nityam—constantly; anutiṣhṭhanti—abide by; mānavāḥ—human beings; śhraddhā-vantaḥ—with profound faith; anasūyantaḥ—free from cavilling; muchyante—become free; te—those; api—also; karmabhiḥ—from the bondage of karma\n', 'https://gita.github.io/gita/data/verse_recitation/3/150.mp3', 3, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (151, 'ये त्वेतदभ्यसूयन्तो नानुतिष्ठन्ति मे मतम्।\n\nसर्वज्ञानविमूढांस्तान्विद्धि नष्टानचेतसः।।3.32।।\n ', 'ye tvetad abhyasūyanto nānutiṣhṭhanti me matam\nsarva-jñāna-vimūḍhāns tān viddhi naṣhṭān achetasaḥ\n', 'ye—those; tu—but; etat—this; abhyasūyantaḥ—cavilling; na—not; anutiṣhṭhanti—follow; me—my; matam—teachings; sarva-jñāna—in all types of knowledge; vimūḍhān—deluded; tān—they are; viddhi—know; naṣhṭān—ruined; achetasaḥ—devoid of discrimination\n', 'https://gita.github.io/gita/data/verse_recitation/3/151.mp3', 3, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (152, 'सदृशं चेष्टते स्वस्याः प्रकृतेर्ज्ञानवानपि।\n\nप्रकृतिं यान्ति भूतानि निग्रहः किं करिष्यति।।3.33।।\n ', 'sadṛiśhaṁ cheṣhṭate svasyāḥ prakṛiter jñānavān api\nprakṛitiṁ yānti bhūtāni nigrahaḥ kiṁ kariṣhyati\n', 'sadṛiśham—accordingly; cheṣhṭate—act; svasyāḥ—by their own; prakṛiteḥ—modes of nature; jñāna-vān—the wise; api—even; prakṛitim—nature; yānti—follow; bhūtāni—all living beings; nigrahaḥ—repression; kim—what; kariṣhyati—will do\n', 'https://gita.github.io/gita/data/verse_recitation/3/152.mp3', 3, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (153, 'इन्द्रियस्येन्द्रियस्यार्थे रागद्वेषौ व्यवस्थितौ।\n\nतयोर्न वशमागच्छेत्तौ ह्यस्य परिपन्थिनौ।।3.34।।\n ', 'indriyasyendriyasyārthe rāga-dveṣhau vyavasthitau\ntayor na vaśham āgachchhet tau hyasya paripanthinau\n', 'indriyasya—of the senses; indriyasya arthe—in the sense objects; rāga—attachment; dveṣhau—aversion; vyavasthitau—situated; tayoḥ—of them; na—never; vaśham—be controlled; āgachchhet—should become; tau—those; hi—certainly; asya—for him; paripanthinau—foes\n', 'https://gita.github.io/gita/data/verse_recitation/3/153.mp3', 3, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (154, 'श्रेयान्स्वधर्मो विगुणः परधर्मात्स्वनुष्ठितात्।\n\nस्वधर्मे निधनं श्रेयः परधर्मो भयावहः।।3.35।।\n ', 'śhreyān swa-dharmo viguṇaḥ para-dharmāt sv-anuṣhṭhitāt\nswa-dharme nidhanaṁ śhreyaḥ para-dharmo bhayāvahaḥ\n', 'śhreyān—better; swa-dharmaḥ—personal duty; viguṇaḥ—tinged with faults; para-dharmāt—than another’s prescribed duties; su-anuṣhṭhitāt—perfectly done; swa-dharme—in one’s personal duties; nidhanam—death; śhreyaḥ—better; para-dharmaḥ—duties prescribed for others; bhaya-āvahaḥ—fraught with fear\n', 'https://gita.github.io/gita/data/verse_recitation/3/154.mp3', 3, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (155, 'अर्जुन उवाच\n\nअथ केन प्रयुक्तोऽयं पापं चरति पूरुषः।\n\nअनिच्छन्नपि वार्ष्णेय बलादिव नियोजितः।।3.36।।\n ', 'arjuna uvācha\natha kena prayukto ’yaṁ pāpaṁ charati pūruṣhaḥ\nanichchhann api vārṣhṇeya balād iva niyojitaḥ\n', 'arjunaḥ uvācha—Arjun said; atha—then; kena—by what; prayuktaḥ—impelled; ayam—one; pāpam—sins; charati—commit; pūruṣhaḥ—a person; anichchhan—unwillingly; api—even; vārṣhṇeya—he who belongs to the Vrishni clan, Shree Krishna; balāt—by force; iva—as if; niyojitaḥ—engaged\n', 'https://gita.github.io/gita/data/verse_recitation/3/155.mp3', 3, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (156, 'श्री भगवानुवाच\n\nकाम एष क्रोध एष रजोगुणसमुद्भवः।\n\nमहाशनो महापाप्मा विद्ध्येनमिह वैरिणम्।।3.37।।\n ', 'śhrī bhagavān uvācha\nkāma eṣha krodha eṣha rajo-guṇa-samudbhavaḥ\nmahāśhano mahā-pāpmā viddhyenam iha vairiṇam\n', 'śhri-bhagavān uvācha—the Supreme Lord said; kāmaḥ—desire; eṣhaḥ—this; krodhaḥ—wrath; eṣhaḥ—this; rajaḥ-guṇa—the mode of passion; samudbhavaḥ—born of; mahā-aśhanaḥ—all-devouring; mahā-pāpmā—greatly sinful; viddhi—know; enam—this; iha—in the material world; vairiṇam—the enemy\n', 'https://gita.github.io/gita/data/verse_recitation/3/156.mp3', 3, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (157, 'धूमेनाव्रियते वह्निर्यथाऽऽदर्शो मलेन च।\n\nयथोल्बेनावृतो गर्भस्तथा तेनेदमावृतम्।।3.38।।\n ', 'dhūmenāvriyate vahnir yathādarśho malena cha\nyatholbenāvṛito garbhas tathā tenedam āvṛitam\n', 'dhūmena—by smoke; āvriyate—is covered; vahniḥ—fire; yathā—just as; ādarśhaḥ—mirror; malena—by dust; cha—also; yathā—just as; ulbena—by the womb; āvṛitaḥ—is covered; garbhaḥ—embryo; tathā—similarly; tena—by that (desire); idam—this; āvṛitam—is covered\n', 'https://gita.github.io/gita/data/verse_recitation/3/157.mp3', 3, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (158, 'आवृतं ज्ञानमेतेन ज्ञानिनो नित्यवैरिणा।\n\nकामरूपेण कौन्तेय दुष्पूरेणानलेन च।।3.39।।\n ', 'āvṛitaṁ jñānam etena jñānino nitya-vairiṇā\nkāma-rūpeṇa kaunteya duṣhpūreṇānalena cha\n', 'āvṛitam—covered; jñānam—knowledge; etena—by this; jñāninaḥ—of the wise; nitya-vairiṇā—by the perpetual enemy; kāma-rūpeṇa—in the form of desires; kaunteya—Arjun the son of Kunti; duṣhpūreṇa—insatiable; analena—like fire; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/3/158.mp3', 3, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (159, 'इन्द्रियाणि मनो बुद्धिरस्याधिष्ठानमुच्यते।\n\nएतैर्विमोहयत्येष ज्ञानमावृत्य देहिनम्।।3.40।।\n ', 'indriyāṇi mano buddhir asyādhiṣhṭhānam uchyate\netair vimohayatyeṣha jñānam āvṛitya dehinam\n', 'indriyāṇi—the senses; manaḥ—the mind; buddhiḥ—the intellect; asya—of this; adhiṣhṭhānam—dwelling place; uchyate—are said to be; etaiḥ—by these; vimohayati—deludes; eṣhaḥ—this; jñānam—knowledge; āvṛitya—clouds; dehinam—the embodied soul\n', 'https://gita.github.io/gita/data/verse_recitation/3/159.mp3', 3, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (160, 'तस्मात्त्वमिन्द्रियाण्यादौ नियम्य भरतर्षभ।\n\nपाप्मानं प्रजहि ह्येनं ज्ञानविज्ञाननाशनम्।।3.41।।\n ', 'tasmāt tvam indriyāṇyādau niyamya bharatarṣhabha\npāpmānaṁ prajahi hyenaṁ jñāna-vijñāna-nāśhanam\n', 'tasmāt—therefore; tvam—you; indriyāṇi—senses; ādau—in the very beginning; niyamya—having controlled; bharata-ṛiṣhabha—Arjun, the best of the Bharatas; pāpmānam—the sinful; prajahi—slay; hi—certainly; enam—this; jñāna—knowledge; vijñāna—realization; nāśhanam—the destroyer\n', 'https://gita.github.io/gita/data/verse_recitation/3/160.mp3', 3, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (161, 'इन्द्रियाणि पराण्याहुरिन्द्रियेभ्यः परं मनः।\n\nमनसस्तु परा बुद्धिर्यो बुद्धेः परतस्तु सः।।3.42।।\n ', 'indriyāṇi parāṇyāhur indriyebhyaḥ paraṁ manaḥ\nmanasas tu parā buddhir yo buddheḥ paratas tu saḥ\n', 'indriyāṇi—senses; parāṇi—superior; āhuḥ—are said; indriyebhyaḥ—than the senses; param—superior; manaḥ—the mind; manasaḥ—than the mind; tu—but; parā—superior; buddhiḥ—intellect; yaḥ—who; buddheḥ—than the intellect; parataḥ—more superior; tu—but; saḥ—that (soul)\n', 'https://gita.github.io/gita/data/verse_recitation/3/161.mp3', 3, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (162, 'एवं बुद्धेः परं बुद्ध्वा संस्तभ्यात्मानमात्मना।\n\nजहि शत्रुं महाबाहो कामरूपं दुरासदम्।।3.43।।\n ', 'evaṁ buddheḥ paraṁ buddhvā sanstabhyātmānam ātmanā\njahi śhatruṁ mahā-bāho kāma-rūpaṁ durāsadam\n', 'evam—thus; buddheḥ—than the intellect; param—superior; buddhvā—knowing; sanstabhya—subdue; ātmānam—the lower self (senses, mind, and intellect); ātmanā—by higher self (soul); jahi—kill; śhatrum—the enemy; mahā-bāho—mighty-armed one; kāma-rūpam—in the form of desire; durāsadam—formidable\n', 'https://gita.github.io/gita/data/verse_recitation/3/162.mp3', 3, 43);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (163, 'श्री भगवानुवाच\n\nइमं विवस्वते योगं प्रोक्तवानहमव्ययम्।\n\nविवस्वान् मनवे प्राह मनुरिक्ष्वाकवेऽब्रवीत्।।4.1।।\n ', 'śhrī bhagavān uvācha\nimaṁ vivasvate yogaṁ proktavān aham avyayam\nvivasvān manave prāha manur ikṣhvākave ’bravīt\n', 'śhrī-bhagavān uvācha—the Supreme Lord Shree Krishna said; imam—this; vivasvate—to the Sun-god; yogam—the science of Yog; proktavān—taught; aham—I; avyayam—eternal; vivasvān—Sun-god; manave—to Manu, the original progenitor of humankind; prāha—told; manuḥ—Manu; ikṣhvākave—to Ikshvaku, first king of the Solar dynasty; abravīt—instructed	\n', 'https://gita.github.io/gita/data/verse_recitation/4/163.mp3', 4, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (164, 'एवं परम्पराप्राप्तमिमं राजर्षयो विदुः।\n\nस कालेनेह महता योगो नष्टः परन्तप।।4.2।।\n ', 'evaṁ paramparā-prāptam imaṁ rājarṣhayo viduḥ\nsa kāleneha mahatā yogo naṣhṭaḥ parantapa\n\n', 'evam—thus; paramparā—in a continuous tradition; prāptam—received; imam—this (science); rāja-ṛiṣhayaḥ—the saintly kings; viduḥ—understood; saḥ—that; kālena—with the long passage of time; iha—in this world; mahatā—great; yogaḥ—the science of Yog; naṣhṭaḥ—lost; parantapa—Arjun, the scorcher of foes\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/164.mp3', 4, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (165, 'स एवायं मया तेऽद्य योगः प्रोक्तः पुरातनः।\n\nभक्तोऽसि मे सखा चेति रहस्यं ह्येतदुत्तमम्।।4.3।।\n ', 'sa evāyaṁ mayā te ’dya yogaḥ proktaḥ purātanaḥ\nbhakto ’si me sakhā cheti rahasyaṁ hyetad uttamam\n\n', 'saḥ—that; eva—certainly; ayam—this; mayā—by me; te—unto you; adya—today; yogaḥ—the science of Yog; proktaḥ—reveal; purātanaḥ—ancient; bhaktaḥ—devotee; asi—you are; me—my; sakhā—friend; cha—and; iti—therefore; rahasyam—secret; hi—certainly; etat—this; uttamam—supreme\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/165.mp3', 4, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (166, 'अर्जुन उवाच\n\nअपरं भवतो जन्म परं जन्म विवस्वतः।\n\nकथमेतद्विजानीयां त्वमादौ प्रोक्तवानिति।।4.4।।\n ', 'arjuna uvācha\naparaṁ bhavato janma paraṁ janma vivasvataḥ\nkatham etad vijānīyāṁ tvam ādau proktavān iti\n\n', 'arjunaḥ uvācha—Arjun said; aparam—later; bhavataḥ—your; janma—birth; param—prior; janma—birth; vivasvataḥ—Vivasvan, the sun-god; katham—how; etat—this; vijānīyām—am I to understand; tvam—you; ādau—in the beginning; proktavān—taught; iti—thus\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/166.mp3', 4, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (167, 'श्री भगवानुवाच\n\nबहूनि मे व्यतीतानि जन्मानि तव चार्जुन।\n\nतान्यहं वेद सर्वाणि न त्वं वेत्थ परन्तप।।4.5।।\n ', 'śhrī bhagavān uvācha\nbahūni me vyatītāni janmāni tava chārjuna\ntānyahaṁ veda sarvāṇi na tvaṁ vettha parantapa\n\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; bahūni—many; me—of mine; vyatītāni—have passed; janmāni—births; tava—of yours; cha—and; arjuna—Arjun; tāni—them; aham—I; veda—know; sarvāṇi—all; na—not; tvam—you; vettha—know; parantapa—Arjun, the scorcher of foes\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/167.mp3', 4, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (168, 'अजोऽपि सन्नव्ययात्मा भूतानामीश्वरोऽपि सन्।\n\nप्रकृतिं स्वामधिष्ठाय संभवाम्यात्ममायया।।4.6।।\n ', 'ajo ’pi sannavyayātmā bhūtānām īśhvaro ’pi san\nprakṛitiṁ svām adhiṣhṭhāya sambhavāmyātma-māyayā\n\n', 'ajaḥ—unborn; api—although; san—being so; avyaya ātmā—Imperishable nature; bhūtānām—of (all) beings; īśhvaraḥ—the Lord; api—although; san—being; prakṛitim—nature; svām—of myself; adhiṣhṭhāya—situated; sambhavāmi—I manifest; ātma-māyayā—by my Yogmaya power\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/168.mp3', 4, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (169, 'यदा यदा हि धर्मस्य ग्लानिर्भवति भारत।\n\nअभ्युत्थानमधर्मस्य तदाऽऽत्मानं सृजाम्यहम्।।4.7।।\n ', 'yadā yadā hi dharmasya glānir bhavati bhārata\nabhyutthānam adharmasya tadātmānaṁ sṛijāmyaham\n\n', 'yadā yadā—whenever; hi—certainly; dharmasya—of righteousness; glāniḥ—decline; bhavati—is; bhārata—Arjun, descendant of Bharat; abhyutthānam—increase; adharmasya—of unrighteousness; tadā—at that time; ātmānam—self; sṛijāmi—manifest; aham—I\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/169.mp3', 4, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (170, 'परित्राणाय साधूनां विनाशाय च दुष्कृताम्।\n\nधर्मसंस्थापनार्थाय संभवामि युगे युगे।।4.8।।\n ', 'paritrāṇāya sādhūnāṁ vināśhāya cha duṣhkṛitām\ndharma-sansthāpanārthāya sambhavāmi yuge yuge\n\n', 'paritrāṇāya—to protect; sādhūnām—the righteous; vināśhāya—to annihilate; cha—and; duṣhkṛitām—the wicked; dharma—the eternal religion; sansthāpana-arthāya—to reestablish; sambhavāmi—I appear; yuge yuge—age after age\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/170.mp3', 4, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (171, 'जन्म कर्म च मे दिव्यमेवं यो वेत्ति तत्त्वतः।\n\nत्यक्त्वा देहं पुनर्जन्म नैति मामेति सोऽर्जुन।।4.9।।\n ', 'janma karma cha me divyam evaṁ yo vetti tattvataḥ\ntyaktvā dehaṁ punar janma naiti mām eti so ’rjuna\n\n', 'janma—birth; karma—activities; cha—and; me—of mine; divyam—divine; evam—thus; yaḥ—who; vetti—know; tattvataḥ—in truth; tyaktvā—having abandoned; deham—the body; punaḥ—again; janma—birth; na—never; eti—takes; mām—to me; eti—comes; saḥ—he; arjuna—Arjun\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/171.mp3', 4, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (172, 'वीतरागभयक्रोधा मन्मया मामुपाश्रिताः।\n\nबहवो ज्ञानतपसा पूता मद्भावमागताः।।4.10।।\n ', 'vīta-rāga-bhaya-krodhā man-mayā mām upāśhritāḥ\nbahavo jñāna-tapasā pūtā mad-bhāvam āgatāḥ\n\n', 'vīta—freed from; rāga—attachment; bhaya—fear; krodhāḥ—and anger; mat-mayā—completely absorbed in me; mām—in me; upāśhritāḥ—taking refuge (of); bahavaḥ—many (persons); jñāna—of knowledge; tapasā—by the fire of knowledge; pūtāḥ—purified; mat-bhāvam—my divine love; āgatāḥ—attained\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/172.mp3', 4, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (173, 'ये यथा मां प्रपद्यन्ते तांस्तथैव भजाम्यहम्।\n\nमम वर्त्मानुवर्तन्ते मनुष्याः पार्थ सर्वशः।।4.11।।\n ', 'ye yathā māṁ prapadyante tāns tathaiva bhajāmyaham\nmama vartmānuvartante manuṣhyāḥ pārtha sarvaśhaḥ\n\n', 'ye—who; yathā—in whatever way; mām—unto me; prapadyante—surrender; tān—them; tathā—so; eva—certainly; bhajāmi—reciprocate; aham—I; mama—my; vartma—path; anuvartante—follow; manuṣhyāḥ—men; pārtha—Arjun, the son of Pritha; sarvaśhaḥ—in all respects\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/173.mp3', 4, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (174, 'काङ्क्षन्तः कर्मणां सिद्धिं यजन्त इह देवताः।\n\nक्षिप्रं हि मानुषे लोके सिद्धिर्भवति कर्मजा।।4.12।।\n ', 'kāṅkṣhantaḥ karmaṇāṁ siddhiṁ yajanta iha devatāḥ\nkṣhipraṁ hi mānuṣhe loke siddhir bhavati karmajā\n\n', 'kāṅkṣhantaḥ—desiring; karmaṇām—material activities; siddhim—success; yajante—worship; iha—in this world; devatāḥ—the celestial gods; kṣhipram—quickly; hi—certainly; mānuṣhe—in human society; loke—within this world; siddhiḥ—rewarding; bhavati—manifest; karma-jā—from material activities\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/174.mp3', 4, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (175, 'चातुर्वर्ण्यं मया सृष्टं गुणकर्मविभागशः।\n\nतस्य कर्तारमपि मां विद्ध्यकर्तारमव्ययम्।।4.13।।\n ', 'chātur-varṇyaṁ mayā sṛiṣhṭaṁ guṇa-karma-vibhāgaśhaḥ\ntasya kartāram api māṁ viddhyakartāram avyayam\n\n', 'chātuḥ-varṇyam—the four categories of occupations; mayā—by me; sṛiṣhṭam—were created; guṇa—of quality; karma—and activities; vibhāgaśhaḥ—according to divisions; tasya—of that; kartāram—the creator; api—although; mām—me; viddhi—know; akartāram—non-doer; avyayam—unchangeable\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/175.mp3', 4, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (176, 'न मां कर्माणि लिम्पन्ति न मे कर्मफले स्पृहा।\n\nइति मां योऽभिजानाति कर्मभिर्न स बध्यते।।4.14।।\n ', 'na māṁ karmāṇi limpanti na me karma-phale spṛihā\niti māṁ yo ’bhijānāti karmabhir na sa badhyate\n\n', 'na—not; mām—me; karmāṇi—activities; limpanti—taint; na—nor; me—my; karma-phale—the fruits of action; spṛihā—desire; iti—thus; mām—me; yaḥ—who; abhijānāti—knows; karmabhiḥ—result of action; na—never; saḥ—that person; badhyate—is bound\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/176.mp3', 4, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (177, 'एवं ज्ञात्वा कृतं कर्म पूर्वैरपि मुमुक्षुभिः।\n\nकुरु कर्मैव तस्मात्त्वं पूर्वैः पूर्वतरं कृतम्।।4.15।।\n ', 'evaṁ jñātvā kṛitaṁ karma pūrvair api mumukṣhubhiḥ\nkuru karmaiva tasmāttvaṁ pūrvaiḥ pūrvataraṁ kṛitam\n\n', 'evam—thus; jñātvā—knowing; kṛitam—performed; karma—actions; pūrvaiḥ—of ancient times; api—indeed; mumukṣhubhiḥ—seekers of liberation; kuru—should perform; karma—duty; eva—certainly; tasmāt—therefore; tvam—you; pūrvaiḥ—of those ancient sages; pūrva-taram—in ancient times; kṛitam—performed\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/177.mp3', 4, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (178, 'किं कर्म किमकर्मेति कवयोऽप्यत्र मोहिताः।\n\nतत्ते कर्म प्रवक्ष्यामि यज्ज्ञात्वा मोक्ष्यसेऽशुभात्।।4.16।।\n ', 'kiṁ karma kim akarmeti kavayo ’pyatra mohitāḥ\ntat te karma pravakṣhyāmi yaj jñātvā mokṣhyase ’śhubhāt\n\n', 'kim—what; karma—action; kim—what; akarma—inaction; iti—thus; kavayaḥ—the wise; api—even; atra—in this; mohitāḥ—are confused; tat—that; te—to you; karma—action; pravakṣhyāmi—I shall explain; yat—which; jñātvā—knowing; mokṣhyase—you may free yourself; aśhubhāt—from inauspiciousness\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/178.mp3', 4, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (179, 'कर्मणो ह्यपि बोद्धव्यं बोद्धव्यं च विकर्मणः।\n\nअकर्मणश्च बोद्धव्यं गहना कर्मणो गतिः।।4.17।।\n ', 'karmaṇo hyapi boddhavyaṁ boddhavyaṁ cha vikarmaṇaḥ\nakarmaṇaśh cha boddhavyaṁ gahanā karmaṇo gatiḥ\n\n', 'karmaṇaḥ—recommended action; hi—certainly; api—also; boddhavyam—should be known; boddhavyam—must understand; cha—and; vikarmaṇaḥ—forbidden action; akarmaṇaḥ—inaction; cha—and; boddhavyam—must understand; gahanā—profound; karmaṇaḥ—of action; gatiḥ—the true path\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/179.mp3', 4, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (180, 'कर्मण्यकर्म यः पश्येदकर्मणि च कर्म यः।\n\nस बुद्धिमान् मनुष्येषु स युक्तः कृत्स्नकर्मकृत्।।4.18।।\n ', 'karmaṇyakarma yaḥ paśhyed akarmaṇi cha karma yaḥ\nsa buddhimān manuṣhyeṣhu sa yuktaḥ kṛitsna-karma-kṛit\n\n', 'karmaṇi—action; akarma—in inaction; yaḥ—who; paśhyet—see; akarmaṇi—inaction; cha—also; karma—action; yaḥ—who; saḥ—they; buddhi-mān—wise; manuṣhyeṣhu—amongst humans; saḥ—they; yuktaḥ—yogis; kṛitsna-karma-kṛit—performers all kinds of actions\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/180.mp3', 4, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (181, 'यस्य सर्वे समारम्भाः कामसङ्कल्पवर्जिताः।\n\nज्ञानाग्निदग्धकर्माणं तमाहुः पण्डितं बुधाः।।4.19।।\n ', 'yasya sarve samārambhāḥ kāma-saṅkalpa-varjitāḥ\njñānāgni-dagdha-karmāṇaṁ tam āhuḥ paṇḍitaṁ budhāḥ\n\n', 'yasya—whose; sarve—every; samārambhāḥ—undertakings; kāma—desire for material pleasures; saṅkalpa—resolve; varjitāḥ—devoid of; jñāna—divine knowledge; agni—in the fire; dagdha—burnt; karmāṇam—actions; tam—him; āhuḥ—address; paṇḍitam—a sage; budhāḥ—the wise\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/181.mp3', 4, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (182, 'त्यक्त्वा कर्मफलासङ्गं नित्यतृप्तो निराश्रयः।\n\nकर्मण्यभिप्रवृत्तोऽपि नैव किञ्चित्करोति सः।।4.20।।\n ', 'tyaktvā karma-phalāsaṅgaṁ nitya-tṛipto nirāśhrayaḥ\nkarmaṇyabhipravṛitto ’pi naiva kiñchit karoti saḥ\n\n', 'tyaktvā—having given up; karma-phala-āsaṅgam—attachment to the fruits of action; nitya—always; tṛiptaḥ—satisfied; nirāśhrayaḥ—without dependence; karmaṇi—in activities; abhipravṛittaḥ—engaged; api—despite; na—not; eva—certainly; kiñchit—anything; karoti—do; saḥ—that person\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/182.mp3', 4, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (183, 'निराशीर्यतचित्तात्मा त्यक्तसर्वपरिग्रहः।\n\nशारीरं केवलं कर्म कुर्वन्नाप्नोति किल्बिषम्।।4.21।।\n ', 'nirāśhīr yata-chittātmā tyakta-sarva-parigrahaḥ\nśhārīraṁ kevalaṁ karma kurvan nāpnoti kilbiṣham\n\n', 'nirāśhīḥ—free from expectations; yata—controlled; chitta-ātmā—mind and intellect; tyakta—having abandoned; sarva—all; parigrahaḥ—the sense of ownership; śhārīram—bodily; kevalam—only; karma—actions; kurvan—performing; na—never; āpnoti—incurs; kilbiṣham—sin\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/183.mp3', 4, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (184, 'यदृच्छालाभसन्तुष्टो द्वन्द्वातीतो विमत्सरः।\n\nसमः सिद्धावसिद्धौ च कृत्वापि न निबध्यते।।4.22।।\n ', 'yadṛichchhā-lābha-santuṣhṭo dvandvātīto vimatsaraḥ\nsamaḥ siddhāvasiddhau cha kṛitvāpi na nibadhyate\n\n', 'yadṛichchhā—which comes of its own accord; lābha—gain; santuṣhṭaḥ—contented; dvandva—duality; atītaḥ—surpassed; vimatsaraḥ—free from envy; samaḥ—equipoised; siddhau—in success; asiddhau—failure; cha—and; kṛitvā—performing; api—even; na—never; nibadhyate—is bound\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/184.mp3', 4, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (185, 'गतसङ्गस्य मुक्तस्य ज्ञानावस्थितचेतसः।\n\nयज्ञायाचरतः कर्म समग्रं प्रविलीयते।।4.23।।\n ', 'gata-saṅgasya muktasya jñānāvasthita-chetasaḥ\nyajñāyācharataḥ karma samagraṁ pravilīyate\n\n', 'gata-saṅgasya—free from material attachments; muktasya—of the liberated; jñāna-avasthita—established in divine knowledge; chetasaḥ—whose intellect; yajñāya—as a sacrifice (to God); ācharataḥ—performing; karma—action; samagram—completely; pravilīyate—are freed\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/185.mp3', 4, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (186, 'ब्रह्मार्पणं ब्रह्महविर्ब्रह्माग्नौ ब्रह्मणा हुतम्।\n\nब्रह्मैव तेन गन्तव्यं ब्रह्मकर्मसमाधिना।।4.24।।\n ', 'brahmārpaṇaṁ brahma havir brahmāgnau brahmaṇā hutam\nbrahmaiva tena gantavyaṁ brahma-karma-samādhinā\n\n', 'brahma—Brahman; arpaṇam—the ladle and other offerings; brahma—Brahman; haviḥ—the oblation; brahma—Brahman; agnau—in the sacrificial fire; brahmaṇā—by that person; hutam—offered; brahma—Brahman; eva—certainly; tena—by that; gantavyam—to be attained; brahma—Brahman; karma—offering; samādhinā—those completely absorbed in God-consciousness\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/186.mp3', 4, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (187, 'दैवमेवापरे यज्ञं योगिनः पर्युपासते।\n\nब्रह्माग्नावपरे यज्ञं यज्ञेनैवोपजुह्वति।।4.25।।\n ', 'daivam evāpare yajñaṁ yoginaḥ paryupāsate\nbrahmāgnāvapare yajñaṁ yajñenaivopajuhvati\n\n', 'daivam—the celestial gods; eva—indeed; apare—others; yajñam—sacrifice; yoginaḥ—spiritual practioners; paryupāsate—worship; brahma—of the Supreme Truth; agnau—in the fire; apare—others; yajñam—sacrifice; yajñena—by sacrifice; eva—indeed; upajuhvati—offer\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/187.mp3', 4, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (188, 'श्रोत्रादीनीन्द्रियाण्यन्ये संयमाग्निषु जुह्वति।\n\nशब्दादीन्विषयानन्य इन्द्रियाग्निषु जुह्वति।।4.26।।\n ', 'śhrotrādīnīndriyāṇyanye sanyamāgniṣhu juhvati\nśhabdādīn viṣhayānanya indriyāgniṣhu juhvati\n\n', 'śhrotra-ādīni—such as the hearing process; indriyāṇi—senses; anye—others; sanyama—restraint; agniṣhu—in the sacrficial fire; juhvati—sacrifice; śhabda-ādīn—sound vibration, etc; viṣhayān—objects of sense-gratification; anye—others; indriya—of the senses; agniṣhu—in the fire; juhvati—sacrifice\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/188.mp3', 4, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (189, 'सर्वाणीन्द्रियकर्माणि प्राणकर्माणि चापरे।\n\nआत्मसंयमयोगाग्नौ जुह्वति ज्ञानदीपिते।।4.27।।\n ', 'sarvāṇīndriya-karmāṇi prāṇa-karmāṇi chāpare\nātma-sanyama-yogāgnau juhvati jñāna-dīpite\n\n', 'sarvāṇi—all; indriya—the senses; karmāṇi—functions; prāṇa-karmāṇi—functions of the life breath; cha—and; apare—others; ātma-sanyama yogāgnau—in the fire of the controlled mind; juhvati—sacrifice; jñāna-dīpite—kindled by knowledge\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/189.mp3', 4, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (190, 'द्रव्ययज्ञास्तपोयज्ञा योगयज्ञास्तथापरे।\n\nस्वाध्यायज्ञानयज्ञाश्च यतयः संशितव्रताः।।4.28।।\n ', 'dravya-yajñās tapo-yajñā yoga-yajñās tathāpare\nswādhyāya-jñāna-yajñāśh cha yatayaḥ sanśhita-vratāḥ\n\n', 'dravya-yajñāḥ—offering one’s own wealth as sacrifice; tapaḥ-yajñāḥ—offering severe austerities as sacrifice; yoga-yajñāḥ—performance of eight-fold path of yogic practices as sacrifice; tathā—thus; apare—others; swādhyāya—cultivating knowledge by studying the scriptures; jñāna-yajñāḥ—those offer cultivation of transcendental knowledge as sacrifice; cha—also; yatayaḥ—these ascetics; sanśhita-vratāḥ—observing strict vows\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/190.mp3', 4, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (191, 'अपाने जुह्वति प्राण प्राणेऽपानं तथाऽपरे।\n\nप्राणापानगती रुद्ध्वा प्राणायामपरायणाः।।4.29।।\n ', 'apāne juhvati prāṇaṁ prāṇe ’pānaṁ tathāpare\nprāṇāpāna-gatī ruddhvā prāṇāyāma-parāyaṇāḥ\n apare niyatāhārāḥ prāṇān prāṇeṣhu juhvati\nsarve ’pyete yajña-vido yajña-kṣhapita-kalmaṣhāḥ\n', 'apāne—the incoming breath; juhvati—offer; prāṇam—the outgoing breath; prāṇe—in the outgoing breath; apānam—incoming breath; tathā—also; apare—others; prāṇa—of the outgoing breath; apāna—and the incoming breath; gatī—movement; ruddhvā—blocking; prāṇa-āyāma—control of breath; parāyaṇāḥ—wholly devoted\n apare—others; niyata—having controlled; āhārāḥ—food intake; prāṇān—life-breaths; prāṇeṣhu—life-energy; juhvati—sacrifice; sarve—all; api—also; ete—these; yajña-vidaḥ—knowers of sacrifices; yajña-kṣhapita—being cleansed by performances of sacrifices; kalmaṣhāḥ—of impurities\n', 'https://gita.github.io/gita/data/verse_recitation/4/191.mp3', 4, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (192, 'अपरे नियताहाराः प्राणान्प्राणेषु जुह्वति।\n\nसर्वेऽप्येते यज्ञविदो यज्ञक्षपितकल्मषाः।।4.30।।\n ', 'apare niyatāhārāḥ prāṇān  prāṇeṣu juhvati\n\nsarve py \'ete yajña-vido yajña-kṣapita-kalmaṣāḥ \n', 'apare—others;  niyata—controlled;  āhārāḥ—eating;  prāṇān—outgoing air; prāṇeṣu—in  the outgoing  air; sarve—all;  api—although    apparently  different;  ete—all     these;  yajñavidaḥ—conversant   with the purpose of performing; yajña—sacrifices; kṣapita—being cleansed of the result of such performances; kalmaṣāḥ—sinful    reactions;  juhvati—sacrifices.', 'https://gita.github.io/gita/data/verse_recitation/4/192.mp3', 4, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (193, 'यज्ञशिष्टामृतभुजो यान्ति ब्रह्म सनातनम्।\n\nनायं लोकोऽस्त्ययज्ञस्य कुतो़ऽन्यः कुरुसत्तम।।4.31।।\n ', 'yajña-śhiṣhṭāmṛita-bhujo yānti brahma sanātanam\nnāyaṁ loko ’styayajñasya kuto ’nyaḥ kuru-sattama\n\n', 'yajña-śhiṣhṭa amṛita-bhujaḥ—they partake of the nectarean remnants of sacrifice; yānti—go; brahma—the Absolute Truth; sanātanam—eternal; na—never; ayam—this; lokaḥ—planet; asti—is; ayajñasya—for one who performs no sacrifice; kutaḥ—how; anyaḥ—other (world); kuru-sat-tama—best of the Kurus, Arjun\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/193.mp3', 4, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (194, 'एवं बहुविधा यज्ञा वितता ब्रह्मणो मुखे।\n\nकर्मजान्विद्धि तान्सर्वानेवं ज्ञात्वा विमोक्ष्यसे।।4.32।।\n ', 'evaṁ bahu-vidhā yajñā vitatā brahmaṇo mukhe\nkarma-jān viddhi tān sarvān evaṁ jñātvā vimokṣhyase\n\n', 'evam—thus; bahu-vidhāḥ—various kinds of; yajñāḥ—sacrifices; vitatāḥ—have been described; brahmaṇaḥ—of the Vedas; mukhe—through the mouth; karma-jān—originating from works; viddhi—know; tān—them; sarvān—all; evam—thus; jñātvā—having known; vimokṣhyase—you shall be liberated\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/194.mp3', 4, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (195, 'श्रेयान्द्रव्यमयाद्यज्ञाज्ज्ञानयज्ञः परन्तप।\n\nसर्वं कर्माखिलं पार्थ ज्ञाने परिसमाप्यते।।4.33।।\n ', 'śhreyān dravya-mayād yajñāj jñāna-yajñaḥ parantapa\nsarvaṁ karmākhilaṁ pārtha jñāne parisamāpyate\n\n', 'śhreyān—superior; dravya-mayāt—of material possessions; yajñāt—than the sacrifice; jñāna-yajñaḥ—sacrifice performed in knowledge; parantapa—subduer of enemies, Arjun; sarvam—all; karma—works; akhilam—all; pārtha—Arjun, the son of Pritha; jñāne—in knowledge; parisamāpyate—culminate\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/195.mp3', 4, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (196, 'तद्विद्धि प्रणिपातेन परिप्रश्नेन सेवया।\n\nउपदेक्ष्यन्ति ते ज्ञानं ज्ञानिनस्तत्त्वदर्शिनः।।4.34।।\n ', 'tad viddhi praṇipātena paripraśhnena sevayā\nupadekṣhyanti te jñānaṁ jñāninas tattva-darśhinaḥ\n\n', 'tat—the Truth; viddhi—try to learn; praṇipātena—by approaching a spiritual master; paripraśhnena—by humble inquiries; sevayā—by rendering service; upadekṣhyanti—can impart; te—unto you; jñānam—knowledge; jñāninaḥ—the enlightened; tattva-darśhinaḥ—those who have realized the Truth\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/196.mp3', 4, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (197, 'यज्ज्ञात्वा न पुनर्मोहमेवं यास्यसि पाण्डव।\n\nयेन भूतान्यशेषेण द्रक्ष्यस्यात्मन्यथो मयि।।4.35।।\n ', 'yaj jñātvā na punar moham evaṁ yāsyasi pāṇḍava\nyena bhūtānyaśheṣheṇa drakṣhyasyātmanyatho mayi\n\n', 'yat—which; jñātvā—having known; na—never; punaḥ—again; moham—delusion; evam—like this; yāsyasi—you shall get; pāṇḍava—Arjun, the son of Pandu; yena—by this; bhūtāni—living beings; aśheṣhāṇi—all; drakṣhyasi—you will see; ātmani—within me (Shree Krishna); atho—that is to say; mayi—in me\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/197.mp3', 4, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (198, 'अपि चेदसि पापेभ्यः सर्वेभ्यः पापकृत्तमः।\n\nसर्वं ज्ञानप्लवेनैव वृजिनं सन्तरिष्यसि।।4.36।।\n ', 'api ched asi pāpebhyaḥ sarvebhyaḥ pāpa-kṛit-tamaḥ\nsarvaṁ jñāna-plavenaiva vṛijinaṁ santariṣhyasi\n\n', 'api—even; chet—if; asi—you are; pāpebhyaḥ—sinners; sarvebhyaḥ—of all; pāpa-kṛit-tamaḥ—most sinful; sarvam—all; jñāna-plavena—by the boat of divine knowledge; eva—certainly; vṛijinam—sin; santariṣhyasi—you shall cross over\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/198.mp3', 4, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (199, 'यथैधांसि समिद्धोऽग्निर्भस्मसात्कुरुतेऽर्जुन।\n\nज्ञानाग्निः सर्वकर्माणि भस्मसात्कुरुते तथा।।4.37।।\n ', 'yathaidhānsi samiddho ’gnir bhasma-sāt kurute ’rjuna\njñānāgniḥ sarva-karmāṇi bhasma-sāt kurute tathā\n\n', 'yathā—as; edhānsi—firewood; samiddhaḥ—blazing; agniḥ—fire; bhasma-sāt—to ashes; kurute—turns; arjuna—Arjun; jñāna-agniḥ—the fire of knowledge; sarva-karmāṇi—all reactions from material activities; bhasma-sāt—to ashes; kurute—it turns; tathā—similarly\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/199.mp3', 4, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (200, 'न हि ज्ञानेन सदृशं पवित्रमिह विद्यते।\n\nतत्स्वयं योगसंसिद्धः कालेनात्मनि विन्दति।।4.38।।\n ', 'na hi jñānena sadṛiśhaṁ pavitramiha vidyate\ntatsvayaṁ yogasansiddhaḥ kālenātmani vindati\n\n', 'na—not; hi—certainly; jñānena—with divine knowledge; sadṛiśham—like; pavitram—pure; iha—in this world; vidyate—exists; tat—that; svayam—oneself; yoga—practice of yog; sansiddhaḥ—he who has attained perfection; kālena—in course of time; ātmani—wihtin the heart; vindati—finds\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/200.mp3', 4, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (201, 'श्रद्धावाँल्लभते ज्ञानं तत्परः संयतेन्द्रियः।\n\nज्ञानं लब्ध्वा परां शान्तिमचिरेणाधिगच्छति।।4.39।।\n ', 'śhraddhāvān labhate jñānaṁ tat-paraḥ sanyatendriyaḥ\njñānaṁ labdhvā parāṁ śhāntim achireṇādhigachchhati\n\n', 'śhraddhā-vān—a faithful person; labhate—achieves; jñānam—divine knowledge; tat-paraḥ—devoted (to that); sanyata—controlled; indriyaḥ—senses; jñānam—transcendental knowledge; labdhvā—having achieved; parām—supreme; śhāntim—peace; achireṇa—without delay; adhigachchhati—attains\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/201.mp3', 4, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (202, 'अज्ञश्चाश्रद्दधानश्च संशयात्मा विनश्यति।\n\nनायं लोकोऽस्ति न परो न सुखं संशयात्मनः।।4.40।।\n ', 'ajñaśh chāśhraddadhānaśh cha sanśhayātmā vinaśhyati\nnāyaṁ loko ’sti na paro na sukhaṁ sanśhayātmanaḥ\n\n', 'ajñaḥ—the ignorant; cha—and; aśhraddadhānaḥ—without faith; cha—and; sanśhaya—skeptical; ātmā—a person; vinaśhyati—falls down; na—never; ayam—in this; lokaḥ—world; asti—is; na—not; paraḥ—in the next; na—not; sukham—happiness; sanśhaya-ātmanaḥ—for the skeptical soul\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/202.mp3', 4, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (203, 'योगसंन्यस्तकर्माणं ज्ञानसंछिन्नसंशयम्।\n\nआत्मवन्तं न कर्माणि निबध्नन्ति धनञ्जय।।4.41।।\n ', 'yoga-sannyasta-karmāṇaṁ jñāna-sañchhinna-sanśhayam\nātmavantaṁ na karmāṇi nibadhnanti dhanañjaya\n\n', 'yoga-sannyasta-karmāṇam—those who renounce ritualistic karm, dedicating their body, mind, and soul to God; jñāna—by knowledge; sañchhinna—dispelled; sanśhayam—doubts; ātma-vantam—situated in knowledge of the self; na—not; karmāṇi—actions; nibadhnanti—bind; dhanañjaya—Arjun, the conqueror of wealth\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/203.mp3', 4, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (204, 'तस्मादज्ञानसंभूतं हृत्स्थं ज्ञानासिनाऽऽत्मनः।\n\nछित्त्वैनं संशयं योगमातिष्ठोत्तिष्ठ भारत।।4.42।।\n ', 'tasmād ajñāna-sambhūtaṁ hṛit-sthaṁ jñānāsinātmanaḥ\nchhittvainaṁ sanśhayaṁ yogam ātiṣhṭhottiṣhṭha bhārata\n\n', 'tasmāt—therefore; ajñāna-sambhūtam—born of ignorance; hṛit-stham—situated in the heart; jñāna—of knowledge; asinā—with the sword; ātmanaḥ—of the self; chhittvā—cut asunder; enam—this; sanśhayam—doubt; yogam—in karm yog; ātiṣhṭha—take shelter; uttiṣhṭha—arise; bhārata—Arjun, descendant of Bharat\n\n', 'https://gita.github.io/gita/data/verse_recitation/4/204.mp3', 4, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (205, 'अर्जुन उवाच\n\nसंन्यासं कर्मणां कृष्ण पुनर्योगं च शंससि।\n\nयच्छ्रेय एतयोरेकं तन्मे ब्रूहि सुनिश्िचतम्।।5.1।।\n ', 'arjuna uvācha\nsannyāsaṁ karmaṇāṁ kṛiṣhṇa punar yogaṁ cha śhansasi\nyach chhreya etayor ekaṁ tan me brūhi su-niśhchitam\n\n', 'arjunaḥ uvācha—Arjun said; sanyāsam—renunciation; karmaṇām—of actions; kṛiṣhṇa—Shree Krishna; punaḥ—again; yogam—about karm yog; cha—also; śhansasi—you praise; yat—which; śhreyaḥ—more beneficial; etayoḥ—of the two; ekam—one; tat—that; me—unto me; brūhi—please tell; su-niśhchitam—conclusively\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/205.mp3', 5, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (206, 'श्री भगवानुवाच\n\nसंन्यासः कर्मयोगश्च निःश्रेयसकरावुभौ।\n\nतयोस्तु कर्मसंन्यासात्कर्मयोगो विशिष्यते।।5.2।।\n ', 'śhrī bhagavān uvācha\nsannyāsaḥ karma-yogaśh cha niḥśhreyasa-karāvubhau\ntayos tu karma-sannyāsāt karma-yogo viśhiṣhyate\n\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; sanyāsaḥ—renunciation; karma-yogaḥ—working in devotion; cha—and; niḥśhreyasa-karau—lead to the supreme goal; ubhau—both; tayoḥ—of the two; tu—but; karma-sanyāsāt—renunciation of actions; karma-yogaḥ—working in devotion; viśhiṣhyate—is superior\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/206.mp3', 5, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (207, 'ज्ञेयः स नित्यसंन्यासी यो न द्वेष्टि न काङ्क्षति।\n\nनिर्द्वन्द्वो हि महाबाहो सुखं बन्धात्प्रमुच्यते।।5.3।।\n ', 'jñeyaḥ sa nitya-sannyāsī yo na dveṣhṭi na kāṅkṣhati\nnirdvandvo hi mahā-bāho sukhaṁ bandhāt pramuchyate\n\n', 'jñeyaḥ—should be considered; saḥ—that person; nitya—always; sanyāsī—practising renunciation; yaḥ—who; na—never; dveṣhṭi—hate; na—nor; kāṅkṣhati—desire; nirdvandvaḥ—free from all dualities; hi—certainly; mahā-bāho—mighty-armed one; sukham—easily; bandhāt—from bondage; pramuchyate—is liberated\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/207.mp3', 5, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (208, 'सांख्ययोगौ पृथग्बालाः प्रवदन्ति न पण्डिताः।\n\nएकमप्यास्थितः सम्यगुभयोर्विन्दते फलम्।।5.4।।\n ', 'sānkhya-yogau pṛithag bālāḥ pravadanti na paṇḍitāḥ\nekamapyāsthitaḥ samyag ubhayor vindate phalam\n\n', 'sānkhya—renunciation of actions; yogau—karm yog; pṛithak—different; bālāḥ—the ignorant; pravadanti—say; na—never; paṇḍitāḥ—the learned; ekam—in one; api—even; āsthitaḥ—being situated; samyak—completely; ubhayoḥ—of both; vindate—achieve; phalam—the result\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/208.mp3', 5, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (209, 'यत्सांख्यैः प्राप्यते स्थानं तद्योगैरपि गम्यते।\n\nएकं सांख्यं च योगं च यः पश्यति स पश्यति।।5.5।।\n ', '\nyat sānkhyaiḥ prāpyate sthānaṁ tad yogair api gamyate\nekaṁ sānkhyaṁ cha yogaṁ cha yaḥ paśhyati sa paśhyati\n\n', 'yat—what; sānkhyaiḥ—by means of karm sanyās; prāpyate—is attained; sthānam—place; tat—that; yogaiḥ—by working in devotion; api—also; gamyate—is attained; ekam—one; sānkhyam—renunciation of actions; cha—and; yogam—karm yog; cha—and; yaḥ—who; paśhyati—sees; saḥ—that person; paśhyati—actually sees\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/209.mp3', 5, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (210, 'संन्यासस्तु महाबाहो दुःखमाप्तुमयोगतः।\n\nयोगयुक्तो मुनिर्ब्रह्म नचिरेणाधिगच्छति।।5.6।।\n ', 'sannyāsas tu mahā-bāho duḥkham āptum ayogataḥ\nyoga-yukto munir brahma na chireṇādhigachchhati\n\n', 'sanyāsaḥ—renunciation; tu—but; mahā-bāho—mighty-armed one; duḥkham—distress; āptum—attains; ayogataḥ—without karm yog; yoga-yuktaḥ—one who is adept in karm yog; muniḥ—a sage; brahma—Brahman; na chireṇa—quickly; adhigachchhati—goes\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/210.mp3', 5, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (211, 'योगयुक्तो विशुद्धात्मा विजितात्मा जितेन्द्रियः।\n\nसर्वभूतात्मभूतात्मा कुर्वन्नपि न लिप्यते।।5.7।।\n ', 'yoga-yukto viśhuddhātmā vijitātmā jitendriyaḥ\nsarva-bhūtātma-bhūtātmā kurvann api na lipyate\n\n', 'yoga-yuktaḥ—united in consciousness with God; viśhuddha-ātmā—one with purified intellect; vijita-ātmā—one who has conquered the mind; jita-indriyaḥ—having conquered the senses; sarva-bhūta-ātma-bhūta-ātmā—one who sees the Soul of all souls in every living being; kurvan—performing; api—although; na—never; lipyate—entangled\n', 'https://gita.github.io/gita/data/verse_recitation/5/211.mp3', 5, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (212, 'नैव किंचित्करोमीति युक्तो मन्येत तत्त्ववित्।\n\nपश्यन् श्रृणवन्स्पृशञ्जिघ्रन्नश्नन्गच्छन्स्वपन् श्वसन्।।5.8।।\n ', 'naiva kiñchit karomīti yukto manyeta tattva-vit\npaśhyañ śhṛiṇvan spṛiśhañjighrann aśhnangachchhan svapañśhvasan\n pralapan visṛijan gṛihṇann unmiṣhan nimiṣhann api\nindriyāṇīndriyārtheṣhu vartanta iti dhārayan\n', 'na—not; eva—certainly; kiñchit—anything; karomi—I do; iti—thus; yuktaḥ—steadfast in karm yog; manyeta—thinks; tattva-vit—one who knows the truth; paśhyan—seeing; śhṛiṇvan—hearing; spṛiśhan—touching; jighran—smelling; aśhnan—eating; gachchhan—moving; svapan—sleeping; śhvasan—breathing;\n pralapan—talking; visṛijan—giving up; gṛihṇan—accepting; unmiṣhan—opening (the eyes); nimiṣhan—closing (the eyes); api—although; indriyāṇi—the senses; indriya-artheṣhu—in sense-objects; vartante—moving; iti—thus; dhārayan—convinced\n', 'https://gita.github.io/gita/data/verse_recitation/5/212.mp3', 5, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (213, 'प्रलपन्विसृजन्गृह्णन्नुन्मिषन्निमिषन्नपि।\n\nइन्द्रियाणीन्द्रियार्थेषु वर्तन्त इति धारयन्।।5.9।।\n ', 'pralapan visṛjan gṛhṇann unmiṣan nimiṣann api indriyāṇīndriyārtheṣu vartanta iti dhārayan', 'pralapan—by talking; visṛjan—by giving up; gṛhṇan—by accepting; unmiṣan—opening; nimiṣan—closing; api—in spite of; indriyāṇi—the senses; indriya-artheṣu—in sense gratification;   vartante—let    them be so engaged; iti—thus;   dhārayan—considering.', 'https://gita.github.io/gita/data/verse_recitation/5/213.mp3', 5, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (214, 'ब्रह्मण्याधाय कर्माणि सङ्गं त्यक्त्वा करोति यः।\n\nलिप्यते न स पापेन पद्मपत्रमिवाम्भसा।।5.10।।\n ', 'brahmaṇyādhāya karmāṇi saṅgaṁ tyaktvā karoti yaḥ\nlipyate na sa pāpena padma-patram ivāmbhasā\n\n', 'brahmaṇi—to God; ādhāya—dedicating; karmāṇi—all actions; saṅgam—attachment; tyaktvā—abandoning; karoti—performs; yaḥ—who; lipyate—is affected; na—never; saḥ—that person; pāpena—by sin; padma-patram—a lotus leaf; iva—like; ambhasā—by water\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/214.mp3', 5, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (215, 'कायेन मनसा बुद्ध्या केवलैरिन्द्रियैरपि।\n\nयोगिनः कर्म कुर्वन्ति सङ्गं त्यक्त्वाऽऽत्मशुद्धये।।5.11।।\n ', 'kāyena manasā buddhyā kevalair indriyair api\nyoginaḥ karma kurvanti saṅgaṁ tyaktvātma-śhuddhaye\n\n', 'kāyena—with the body; manasā—with the mind; buddhyā—with the intellect; kevalaiḥ—only; indriyaiḥ—with the senses; api—even; yoginaḥ—the yogis; karma—actions; kurvanti—perform; saṅgam—attachment; tyaktvā—giving up; ātma—of the self; śhuddhaye—for the purification\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/215.mp3', 5, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (216, 'युक्तः कर्मफलं त्यक्त्वा शान्तिमाप्नोति नैष्ठिकीम्।\n\nअयुक्तः कामकारेण फले सक्तो निबध्यते।।5.12।।\n ', 'yuktaḥ karma-phalaṁ tyaktvā śhāntim āpnoti naiṣhṭhikīm\nayuktaḥ kāma-kāreṇa phale sakto nibadhyate\n\n', 'yuktaḥ—one who is united in consciousness with God; karma-phalam—the results of all activities; tyaktvā—giving up; śhāntim—peace; āpnoti—attains; naiṣhṭhikīm—everlasting; ayuktaḥ—one who is not united with God in consciousness; kāma-kāreṇa—impelled by desires; phale—in the result; saktaḥ—attached; nibadhyate—becomes entangled\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/216.mp3', 5, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (217, 'सर्वकर्माणि मनसा संन्यस्यास्ते सुखं वशी।\n\nनवद्वारे पुरे देही नैव कुर्वन्न कारयन्।।5.13।।\n ', 'sarva-karmāṇi manasā sannyasyāste sukhaṁ vaśhī\nnava-dvāre pure dehī naiva kurvan na kārayan\n\n', 'sarva—all; karmāṇi—activities; manasā—by the mind; sannyasya—having renounced; āste—remains; sukham—happily; vaśhī—the self-controlled; nava-dvāre—of nine gates; pure—in the city; dehī—the embodied being; na—never; eva—certainly; kurvan—doing anything; na—not; kārayan—causing to be done\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/217.mp3', 5, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (218, 'न कर्तृत्वं न कर्माणि लोकस्य सृजति प्रभुः।\n\nन कर्मफलसंयोगं स्वभावस्तु प्रवर्तते।।5.14।।\n ', 'na kartṛitvaṁ na karmāṇi lokasya sṛijati prabhuḥ\nna karma-phala-saṅyogaṁ svabhāvas tu pravartate\n\n', 'na—neither; kartṛitvam—sense of doership; na—nor; karmāṇi—actions; lokasya—of the people; sṛijati—creates; prabhuḥ—God; na—nor; karma-phala—fruits of actions; sanyogam—connection; svabhāvaḥ—one’s nature; tu—but; pravartate—is enacted\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/218.mp3', 5, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (219, 'नादत्ते कस्यचित्पापं न चैव सुकृतं विभुः।\n\nअज्ञानेनावृतं ज्ञानं तेन मुह्यन्ति जन्तवः।।5.15।।\n ', 'nādatte kasyachit pāpaṁ na chaiva sukṛitaṁ vibhuḥ\najñānenāvṛitaṁ jñānaṁ tena muhyanti jantavaḥ\n\n', 'na—not; ādatte—accepts; kasyachit—anyone’s; pāpam—sin; na—not; cha—and; eva—certainly; su-kṛitam—virtuous deeds; vibhuḥ—the omnipresent God; ajñānena—by ignorance; āvṛitam—covered; jñānam—knowledge; tena—by that; muhyanti—are deluded; jantavaḥ—the living entities\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/219.mp3', 5, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (220, 'ज्ञानेन तु तदज्ञानं येषां नाशितमात्मनः।\n\nतेषामादित्यवज्ज्ञानं प्रकाशयति तत्परम्।।5.16।।\n ', 'jñānena tu tad ajñānaṁ yeṣhāṁ nāśhitam ātmanaḥ\nteṣhām āditya-vaj jñānaṁ prakāśhayati tat param\n\n', 'jñānena—by divine knowledge; tu—but; tat—that; ajñānam—ignorance; yeṣhām—whose; nāśhitam—has been destroyed; ātmanaḥ—of the self; teṣhām—their; āditya-vat—like the sun; jñānam—knowledge; prakāśhayati—illumines; tat—that; param—Supreme Entity\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/220.mp3', 5, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (221, 'तद्बुद्धयस्तदात्मानस्तन्निष्ठास्तत्परायणाः।\n\nगच्छन्त्यपुनरावृत्तिं ज्ञाननिर्धूतकल्मषाः।।5.17।।\n ', 'tad-buddhayas tad-ātmānas tan-niṣhṭhās tat-parāyaṇāḥ\ngachchhantyapunar-āvṛittiṁ jñāna-nirdhūta-kalmaṣhāḥ\n\n', 'tat-buddhayaḥ—those whose intellect is directed toward God; tat-ātmānaḥ—those whose heart (mind and intellect) is solely absorbed in God; tat-niṣhṭhāḥ—those whose intellect has firm faith in God; tat-parāyaṇāḥ—those who strive after God as the supreme goal and refuge; gachchhanti—go; apunaḥ-āvṛittim—not returning; jñāna—by knowledge; nirdhūta—dispelled; kalmaṣhāḥ—sins\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/221.mp3', 5, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (222, 'विद्याविनयसंपन्ने ब्राह्मणे गवि हस्तिनि।\n\nशुनि चैव श्वपाके च पण्डिताः समदर्शिनः।।5.18।।\n ', 'vidyā-vinaya-sampanne brāhmaṇe gavi hastini\nśhuni chaiva śhva-pāke cha paṇḍitāḥ sama-darśhinaḥ\n\n', 'vidyā—divine knowledge; vinaya—humbleness; sampanne—equipped with; brāhmaṇe—a Brahmin; gavi—a cow; hastini—an elephant; śhuni—a dog; cha—and; eva—certainly; śhva-pāke—a dog-eater; cha—and; paṇḍitāḥ—the learned; sama-darśhinaḥ—see with equal vision\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/222.mp3', 5, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (223, 'इहैव तैर्जितः सर्गो येषां साम्ये स्थितं मनः।\n\nनिर्दोषं हि समं ब्रह्म तस्माद्ब्रह्मणि ते स्थिताः।।5.19।।\n ', 'ihaiva tair jitaḥ sargo yeṣhāṁ sāmye sthitaṁ manaḥ\nnirdoṣhaṁ hi samaṁ brahma tasmād brahmaṇi te sthitāḥ\n\n', 'iha eva—in this very life; taiḥ—by them; jitaḥ—conquer; sargaḥ—the creation; yeṣhām—whose; sāmye—in equanimity; sthitam—situated; manaḥ—mind; nirdoṣham—flawless; hi—certainly; samam—in equality; brahma—God; tasmāt—therefore; brahmaṇi—in the Absolute Truth; te—they; sthitāḥ—are seated\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/223.mp3', 5, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (224, 'न प्रहृष्येत्प्रियं प्राप्य नोद्विजेत्प्राप्य चाप्रियम्।\n\nस्थिरबुद्धिरसम्मूढो ब्रह्मविद्ब्रह्मणि स्थितः।।5.20।।\n ', 'na prahṛiṣhyet priyaṁ prāpya nodvijet prāpya chāpriyam\nsthira-buddhir asammūḍho brahma-vid brahmaṇi sthitaḥ\n\n', 'na—neither; prahṛiṣhyet—rejoice; priyam—the pleasant; prāpya—obtaining; na—nor; udvijet—become disturbed; prāpya—attaining; cha—also; apriyam—the unpleasant; sthira-buddhiḥ—steady intellect; asammūḍhaḥ—firmly situated; brahma-vit—having a firm understanding of divine knowledge; brahmaṇi—established in God; sthitaḥ—situated\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/224.mp3', 5, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (225, 'बाह्यस्पर्शेष्वसक्तात्मा विन्दत्यात्मनि यत्सुखम्।\n\nस ब्रह्मयोगयुक्तात्मा सुखमक्षयमश्नुते।।5.21।।\n ', 'bāhya-sparśheṣhvasaktātmā vindatyātmani yat sukham\nsa brahma-yoga-yuktātmā sukham akṣhayam aśhnute\n\n', 'bāhya-sparśheṣhu—external sense pleasure; asakta-ātmā—those who are unattached; vindati—find; ātmani—in the self; yat—which; sukham—bliss; saḥ—that person; brahma-yoga yukta-ātmā—those who are united with God through yog; sukham—happiness; akṣhayam—unlimited; aśhnute—experiences\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/225.mp3', 5, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (226, 'ये हि संस्पर्शजा भोगा दुःखयोनय एव ते।\n\nआद्यन्तवन्तः कौन्तेय न तेषु रमते बुधः।।5.22।।\n ', 'ye hi sansparśha-jā bhogā duḥkha-yonaya eva te\nādyantavantaḥ kaunteya na teṣhu ramate budhaḥ\n\n', 'ye—which; hi—verily; sansparśha-jāḥ—born of contact with the sense objects; bhogāḥ—pleasures; duḥkha—misery; yonayaḥ—source of; eva—verily; te—they are; ādya-antavantaḥ—having beginning and end; kaunteya—Arjun, the son of Kunti; na—never; teṣhu—in those; ramate—takes delight; budhaḥ—the wise\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/226.mp3', 5, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (227, 'शक्नोतीहैव यः सोढुं प्राक्शरीरविमोक्षणात्।\n\nकामक्रोधोद्भवं वेगं स युक्तः स सुखी नरः।।5.23।।\n ', 'śhaknotīhaiva yaḥ soḍhuṁ prāk śharīra-vimokṣhaṇāt\nkāma-krodhodbhavaṁ vegaṁ sa yuktaḥ sa sukhī naraḥ\n\n', 'śhaknoti—is able; iha eva—in the present body; yaḥ—who; soḍhum—to withstand; prāk—before; śharīra—the body; vimokṣhaṇāt—giving up; kāma—desire; krodha—anger; udbhavam—generated from; vegam—forces; saḥ—that person; yuktaḥ—yogi; saḥ—that person; sukhī—happy; naraḥ—person\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/227.mp3', 5, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (228, 'योऽन्तःसुखोऽन्तरारामस्तथान्तर्ज्योतिरेव यः।\n\nस योगी ब्रह्मनिर्वाणं ब्रह्मभूतोऽधिगच्छति।।5.24।।\n ', 'yo \'ntaḥ-sukho \'ntar-ārāmas tathāntar-jyotir eva yaḥ\nsa yogī brahma-nirvāṇaṁ brahma-bhūto \'dhigachchhati\n\n', 'yaḥ—who; antaḥ-sukhaḥ—happy within the self; antaḥ-ārāmaḥ—enjoying within the self; tathā; antaḥ-jyotiḥ—illumined by the inner light; eva—certainly; yaḥ—who; saḥ; yogī—yogi; brahma-nirvāṇam—liberation from material existence; brahmabhūtaḥ— united with the Lord; adhigachchhati—attains\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/228.mp3', 5, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (229, 'लभन्ते ब्रह्मनिर्वाणमृषयः क्षीणकल्मषाः।\n\nछिन्नद्वैधा यतात्मानः सर्वभूतहिते रताः।।5.25।।\n ', 'labhante brahma-nirvāṇam ṛiṣhayaḥ kṣhīṇa-kalmaṣhāḥ\nchhinna-dvaidhā yatātmānaḥ sarva-bhūta-hite ratāḥ\n\n', 'labhante—achieve; brahma-nirvāṇam—liberation from material existence; ṛiṣhayaḥ—holy persons; kṣhīṇa-kalmaṣhāḥ—whose sins have been purged; chhinna—annihilated; dvaidhāḥ—doubts; yata-ātmānaḥ—whose minds are disciplined; sarva-bhūta—for all living entities; hite—in welfare work; ratāḥ—rejoice\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/229.mp3', 5, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (230, 'कामक्रोधवियुक्तानां यतीनां यतचेतसाम्।\n\nअभितो ब्रह्मनिर्वाणं वर्तते विदितात्मनाम्।।5.26।।\n ', 'kāma-krodha-viyuktānāṁ yatīnāṁ yata-chetasām\nabhito brahma-nirvāṇaṁ vartate viditātmanām\n\n', 'kāma—desires; krodha—anger; vimuktānām—of those who are liberated; yatīnām—of the saintly persons; yata-chetasām—those self-realized persons who have subdued their mind; abhitaḥ—from every side; brahma—spiritual; nirvāṇam—liberation from material existence; vartate—exists; vidita-ātmanām—of those who are self-realized\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/230.mp3', 5, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (231, 'स्पर्शान्कृत्वा बहिर्बाह्यांश्चक्षुश्चैवान्तरे भ्रुवोः।\n\nप्राणापानौ समौ कृत्वा नासाभ्यन्तरचारिणौ।।5.27।।\n ', 'sparśhān kṛitvā bahir bāhyānśh chakṣhuśh chaivāntare bhruvoḥ\nprāṇāpānau samau kṛitvā nāsābhyantara-chāriṇau\n yatendriya-mano-buddhir munir mokṣha-parāyaṇaḥ\nvigatechchhā-bhaya-krodho yaḥ sadā mukta eva saḥ\n', 'sparśhān—contacts (through senses); kṛitvā—keeping; bahiḥ—outside; bāhyān—external; chakṣhuḥ—eyes; cha—and; eva—certainly; antare—between; bhruvoḥ—of the eyebrows; prāṇa-apānau—the outgoing and incoming breaths; samau—equal; kṛitvā—keeping; nāsa-abhyantara—within the nostrils; chāriṇau—moving; \n yata—controlled; indriya—senses; manaḥ—mind; buddhiḥ—intellect; muniḥ—the sage; mokṣha—liberation; parāyaṇaḥ—dedicated; vigata—free; ichchhā—desires; bhaya—fear; krodhaḥ—anger; yaḥ—who; sadā—always; muktaḥ—liberated; eva—certainly; saḥ—that person\n', 'https://gita.github.io/gita/data/verse_recitation/5/231.mp3', 5, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (232, 'यतेन्द्रियमनोबुद्धिर्मुनिर्मोक्षपरायणः।\n\nविगतेच्छाभयक्रोधो यः सदा मुक्त एव सः।।5.28।।\n ', 'yatendriya-mano-buddhir  munir   mokṣa-parāyaṇaḥ vigatecchā-bhaya-krodho yaḥ sadā    mukta   eva saḥ', 'yata—controlled;   indriya—senses; manaḥ—mind; buddhiḥ—intelligence;   muniḥ—the   transcendentalist;  mokṣa—liberation;   parāyaṇaḥ—being so  destined;   vigata—discarded;   icchā—wishes;   bhaya—fear; krodhaḥ—anger;  yaḥ—one who;    sadā—always;    muktaḥ—liberated;   eva—certainly;  saḥ—he  is', 'https://gita.github.io/gita/data/verse_recitation/5/232.mp3', 5, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (233, 'भोक्तारं यज्ञतपसां सर्वलोकमहेश्वरम्।\n\nसुहृदं सर्वभूतानां ज्ञात्वा मां शान्तिमृच्छति।।5.29।।\n ', 'bhoktāraṁ yajña-tapasāṁ sarva-loka-maheśhvaram\nsuhṛidaṁ sarva-bhūtānāṁ jñātvā māṁ śhāntim ṛichchhati\n\n', 'bhoktāram—the enjoyer; yajña—sacrifices; tapasām—austerities; sarva-loka—of all worlds; mahā-īśhvaram—the Supreme Lord; su-hṛidam—the selfless Friend; sarva—of all; bhūtānām—the living beings; jñātvā—having realized; mām—me (Lord Krishna); śhāntim—peace; ṛichchhati—attains\n\n', 'https://gita.github.io/gita/data/verse_recitation/5/233.mp3', 5, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (234, 'श्री भगवानुवाच\n\nअनाश्रितः कर्मफलं कार्यं कर्म करोति यः।\n\nस संन्यासी च योगी च न निरग्निर्न चाक्रियः।।6.1।।\n ', 'śhrī bhagavān uvācha\nanāśhritaḥ karma-phalaṁ kāryaṁ karma karoti yaḥ\nsa sannyāsī cha yogī cha na niragnir na chākriyaḥ\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; anāśhritaḥ—not desiring; karma-phalam—results of actions; kāryam—obligatory; karma—work; karoti—perform; yaḥ—one who; saḥ—that person; sanyāsī—in the renounced order; cha—and; yogī—yogi; cha—and; na—not; niḥ—without; agniḥ—fire; na—not; cha—also; akriyaḥ—without activity\n', 'https://gita.github.io/gita/data/verse_recitation/6/234.mp3', 6, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (235, 'यं संन्यासमिति प्राहुर्योगं तं विद्धि पाण्डव।\n\nन ह्यसंन्यस्तसङ्कल्पो योगी भवति कश्चन।।6.2।।\n ', 'yaṁ sannyāsam iti prāhur yogaṁ taṁ viddhi pāṇḍava\nna hyasannyasta-saṅkalpo yogī bhavati kaśhchana\n', 'yam—what; sanyāsam—renunciation; iti—thus; prāhuḥ—they say; yogam—yog; tam—that; viddhi—know; pāṇḍava—Arjun, the son of Pandu; na—not; hi—certainly; asannyasta—without giving up; saṅkalpaḥ—desire; yogī—a yogi; bhavati—becomes; kaśhchana—anyone\n', 'https://gita.github.io/gita/data/verse_recitation/6/235.mp3', 6, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (236, 'आरुरुक्षोर्मुनेर्योगं कर्म कारणमुच्यते।\n\nयोगारूढस्य तस्यैव शमः कारणमुच्यते।।6.3।।\n ', 'ārurukṣhor muner yogaṁ karma kāraṇam uchyate\nyogārūḍhasya tasyaiva śhamaḥ kāraṇam uchyate\n', 'ārurukṣhoḥ—a beginner; muneḥ—of a sage; yogam—Yog; karma—working without attachment; kāraṇam—the cause; uchyate—is said; yoga ārūḍhasya—of those who are elevated in Yog; tasya—their; eva—certainly; śhamaḥ—meditation; kāraṇam—the cause; uchyate—is said\n', 'https://gita.github.io/gita/data/verse_recitation/6/236.mp3', 6, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (237, 'यदा हि नेन्द्रियार्थेषु न कर्मस्वनुषज्जते।\n\nसर्वसङ्कल्पसंन्यासी योगारूढस्तदोच्यते।।6.4।।\n ', 'yadā hi nendriyārtheṣhu na karmasv-anuṣhajjate\nsarva-saṅkalpa-sannyāsī yogārūḍhas tadochyate\n', 'yadā—when; hi—certainly; na—not; indriya-artheṣhu—for sense-objects; na—not; karmasu—to actions; anuṣhajjate—is attachment; sarva-saṅkalpa—all desires for the fruits of actions; sanyāsī—renouncer; yoga-ārūḍhaḥ—elevated in the science of Yog; tadā—at that time; uchyate—is said\n', 'https://gita.github.io/gita/data/verse_recitation/6/237.mp3', 6, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (238, 'उद्धरेदात्मनाऽऽत्मानं नात्मानमवसादयेत्।\n\nआत्मैव ह्यात्मनो बन्धुरात्मैव रिपुरात्मनः।।6.5।।\n ', 'uddhared ātmanātmānaṁ nātmānam avasādayet\nātmaiva hyātmano bandhur ātmaiva ripur ātmanaḥ\n', 'uddharet—elevate; ātmanā—through the mind; ātmānam—the self; na—not; ātmānam—the self; avasādayet—degrade; ātmā—the mind; eva—certainly; hi—indeed; ātmanaḥ—of the self; bandhuḥ—friend; ātmā—the mind; eva—certainly; ripuḥ—enemy; ātmanaḥ—of the self\n', 'https://gita.github.io/gita/data/verse_recitation/6/238.mp3', 6, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (239, 'बन्धुरात्माऽऽत्मनस्तस्य येनात्मैवात्मना जितः।\n\nअनात्मनस्तु शत्रुत्वे वर्तेतात्मैव शत्रुवत्।।6.6।।\n ', 'bandhur ātmātmanas tasya yenātmaivātmanā jitaḥ\nanātmanas tu śhatrutve vartetātmaiva śhatru-vat\n', 'bandhuḥ—friend; ātmā—the mind; ātmanaḥ—for the person; tasya—of him; yena—by whom; ātmā—the mind; eva—certainly; ātmanā—for the person; jitaḥ—conquered; anātmanaḥ—of those with unconquered mind; tu—but; śhatrutve—for an enemy; varteta—remains; ātmā—the mind; eva—as; śhatru-vat—like an enemy\n', 'https://gita.github.io/gita/data/verse_recitation/6/239.mp3', 6, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (240, 'जितात्मनः प्रशान्तस्य परमात्मा समाहितः।\n\nशीतोष्णसुखदुःखेषु तथा मानापमानयोः।।6.7।।\n ', 'jitātmanaḥ praśhāntasya paramātmā samāhitaḥ\nśhītoṣhṇa-sukha-duḥkheṣhu tathā mānāpamānayoḥ\n', 'jita-ātmanaḥ—one who has conquered one’s mind; praśhāntasya—of the peaceful; parama-ātmā—God; samāhitaḥ—steadfast; śhīta—in cold; uṣhṇa—heat; sukha—happiness; duḥkheṣhu—and distress; tathā—also; māna—in honor; apamānayoḥ—and dishonor\n', 'https://gita.github.io/gita/data/verse_recitation/6/240.mp3', 6, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (241, 'ज्ञानविज्ञानतृप्तात्मा कूटस्थो विजितेन्द्रियः।\n\nयुक्त इत्युच्यते योगी समलोष्टाश्मकाञ्चनः।।6.8।।\n ', 'jñāna-vijñāna-tṛiptātmā kūṭa-stho vijitendriyaḥ\nyukta ityuchyate yogī sama-loṣhṭāśhma-kāñchanaḥ\n', 'jñāna—knowledge; vijñāna—realized knowledge, wisdom from within; tṛipta ātmā—one fully satisfied; kūṭa-sthaḥ—undisturbed; vijita-indriyaḥ—one who has conquered the senses; yuktaḥ—one who is in constant communion with the Supreme; iti—thus; uchyate—is said; yogī—a yogi; sama—looks equally; loṣhṭra—pebbles; aśhma—stone; kāñchanaḥ—gold\n', 'https://gita.github.io/gita/data/verse_recitation/6/241.mp3', 6, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (242, 'सुहृन्मित्रार्युदासीनमध्यस्थद्वेष्यबन्धुषु।\n\nसाधुष्वपि च पापेषु समबुद्धिर्विशिष्यते।।6.9।।\n ', 'suhṛin-mitrāryudāsīna-madhyastha-dveṣhya-bandhuṣhu\nsādhuṣhvapi cha pāpeṣhu sama-buddhir viśhiṣhyate\n', 'su-hṛit—toward the well-wishers; mitra—friends; ari—enemies; udāsīna—neutral persons; madhya-stha—mediators; dveṣhya—the envious; bandhuṣhu—relatives; sādhuṣhu—pious; api—as well as; cha—and; pāpeṣhu—the sinners; sama-buddhiḥ—of impartial intellect; viśhiṣhyate—is distinguished\n', 'https://gita.github.io/gita/data/verse_recitation/6/242.mp3', 6, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (243, 'योगी युञ्जीत सततमात्मानं रहसि स्थितः।\n\nएकाकी यतचित्तात्मा निराशीरपरिग्रहः।।6.10।।\n ', 'yogī yuñjīta satatam ātmānaṁ rahasi sthitaḥ\nekākī yata-chittātmā nirāśhīr aparigrahaḥ\n', 'yogī—a yogi; yuñjīta—should remain engaged in meditation; satatam—constantly; ātmānam—self; rahasi—in seclusion; sthitaḥ—remaining; ekākī—alone; yata-chitta-ātmā—with a controlled mind and body; nirāśhīḥ—free from desires; aparigrahaḥ—free from desires for possessions for enjoyment\n', 'https://gita.github.io/gita/data/verse_recitation/6/243.mp3', 6, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (244, 'शुचौ देशे प्रतिष्ठाप्य स्थिरमासनमात्मनः।\n\nनात्युच्छ्रितं नातिनीचं चैलाजिनकुशोत्तरम्।।6.11।।\n ', 'śhuchau deśhe pratiṣhṭhāpya sthiram āsanam ātmanaḥ\nnātyuchchhritaṁ nāti-nīchaṁ chailājina-kuśhottaram\n', 'śhuchau—in a clean; deśhe—place; pratiṣhṭhāpya—having established; sthiram—steadfast; āsanam—seat; ātmanaḥ—his own; na—not; ati—too; uchchhritam—high; na—not; ati—too; nīcham—low; chaila—cloth; ajina—a deerskin; kuśha—kuśh grass; uttaram—one over the other\n', 'https://gita.github.io/gita/data/verse_recitation/6/244.mp3', 6, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (245, 'तत्रैकाग्रं मनः कृत्वा यतचित्तेन्द्रियक्रियः।\n\nउपविश्यासने युञ्ज्याद्योगमात्मविशुद्धये।।6.12।।\n ', 'tatraikāgraṁ manaḥ kṛitvā yata-chittendriya-kriyaḥ\nupaviśhyāsane yuñjyād yogam ātma-viśhuddhaye\n', 'tatra—there; eka-agram—one-pointed; manaḥ—mind; kṛitvā—having made; yata-chitta—controlling the mind; indriya—senses; kriyaḥ—activities; upaviśhya—being seated; āsane—on the seat; yuñjyāt yogam—should strive to practice yog; ātma viśhuddhaye—for purification of the mind; \n', 'https://gita.github.io/gita/data/verse_recitation/6/245.mp3', 6, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (246, 'समं कायशिरोग्रीवं धारयन्नचलं स्थिरः।\n\nसंप्रेक्ष्य नासिकाग्रं स्वं दिशश्चानवलोकयन्।।6.13।।\n ', 'samaṁ kāya-śhiro-grīvaṁ dhārayann achalaṁ sthiraḥ\nsamprekṣhya nāsikāgraṁ svaṁ diśhaśh chānavalokayan\n', 'samam—straight; kāya—body; śhiraḥ—head; grīvam—neck; dhārayan—holding; achalam—unmoving; sthiraḥ—still; samprekṣhya—gazing; nāsika-agram—at the tip of the nose; svam—own; diśhaḥ—directions; cha—and; anavalokayan—not looking\n', 'https://gita.github.io/gita/data/verse_recitation/6/246.mp3', 6, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (247, 'प्रशान्तात्मा विगतभीर्ब्रह्मचारिव्रते स्थितः।\n\nमनः संयम्य मच्चित्तो युक्त आसीत मत्परः।।6.14।।\n ', 'praśhāntātmā vigata-bhīr brahmachāri-vrate sthitaḥ\nmanaḥ sanyamya mach-chitto yukta āsīta mat-paraḥ\n', 'praśhānta—serene; ātmā—mind; vigata-bhīḥ—fearless; brahmachāri-vrate—in the vow of celibacy; sthitaḥ—situated; manaḥ—mind; sanyamya—having controlled; mat-chittaḥ—meditate on me (Shree Krishna); yuktaḥ—engaged; āsīta—should sit; mat-paraḥ—having me as the supreme goal\n', 'https://gita.github.io/gita/data/verse_recitation/6/247.mp3', 6, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (248, 'युञ्जन्नेवं सदाऽऽत्मानं योगी नियतमानसः।\n\nशान्तिं निर्वाणपरमां मत्संस्थामधिगच्छति।।6.15।।\n ', 'yuñjann evaṁ sadātmānaṁ yogī niyata-mānasaḥ\nśhantiṁ nirvāṇa-paramāṁ mat-sansthām adhigachchhati\n', 'yuñjan—keeping the mind absorbed in God; evam—thus; sadā—constantly; ātmānam—the mind; yogī—a yogi; niyata-mānasaḥ—one with a disciplined mind; śhāntim—peace; nirvāṇa—liberation from the material bondage; paramām—supreme; mat-sansthām—abides in me; adhigachchhati—attains\n', 'https://gita.github.io/gita/data/verse_recitation/6/248.mp3', 6, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (249, 'नात्यश्नतस्तु योगोऽस्ति न चैकान्तमनश्नतः।\n\nन चातिस्वप्नशीलस्य जाग्रतो नैव चार्जुन।।6.16।।\n ', 'nātyaśhnatastu yogo ’sti na chaikāntam anaśhnataḥ\nna chāti-svapna-śhīlasya jāgrato naiva chārjuna\n', 'na—not; ati—too much; aśhnataḥ—of one who eats; tu—however; yogaḥ—Yog; asti—there is; na—not; cha—and; ekāntam—at all; anaśhnataḥ—abstaining from eating; na—not; cha—and; ati—too much; svapna-śhīlasya—of one who sleeps; jāgrataḥ—of one who does not sleep enough; na—not; eva—certainly; cha—and; arjuna—Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/6/249.mp3', 6, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (250, 'युक्ताहारविहारस्य युक्तचेष्टस्य कर्मसु।\n\nयुक्तस्वप्नावबोधस्य योगो भवति दुःखहा।।6.17।।\n ', 'yuktāhāra-vihārasya yukta-cheṣhṭasya karmasu\nyukta-svapnāvabodhasya yogo bhavati duḥkha-hā\n', 'yukta—moderate; āhāra—eating; vihārasya—recreation; yukta cheṣhṭasya karmasu—balanced in work; yukta—regulated; svapna-avabodhasya—sleep and wakefulness; yogaḥ—Yog; bhavati—becomes; duḥkha-hā—the slayer of sorrows\n', 'https://gita.github.io/gita/data/verse_recitation/6/250.mp3', 6, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (251, 'यदा विनियतं चित्तमात्मन्येवावतिष्ठते।\n\nनिःस्पृहः सर्वकामेभ्यो युक्त इत्युच्यते तदा।।6.18।।\n ', 'yadā viniyataṁ chittam ātmanyevāvatiṣhṭhate\nniḥspṛihaḥ sarva-kāmebhyo yukta ityuchyate tadā\n', 'yadā—when; viniyatam—fully controlled; chittam—the mind; ātmani—of the self; eva—certainly; avatiṣhṭhate—stays; nispṛihaḥ—free from cravings: sarva; kāmebhyaḥ—for yearning of the senses; yuktaḥ—situated in perfect Yog; iti—thus; uchyate—is said; tadā—then\n', 'https://gita.github.io/gita/data/verse_recitation/6/251.mp3', 6, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (252, 'यथा दीपो निवातस्थो नेङ्गते सोपमा स्मृता।\n\nयोगिनो यतचित्तस्य युञ्जतो योगमात्मनः।।6.19।।\n ', 'yathā dīpo nivāta-stho neṅgate sopamā smṛitā\nyogino yata-chittasya yuñjato yogam ātmanaḥ\n', 'yathā—as; dīpaḥ—a lamp; nivāta-sthaḥ—in a windless place; na—does not; iṅgate—flickers; sā—this; upamā—analogy; smṛitā—is considered; yoginaḥ—of a yogi; yata-chittasya—whose mind is disciplined; yuñjataḥ—steadily practicing; yogam—in meditation; ātmanaḥ—on the Supreme\n', 'https://gita.github.io/gita/data/verse_recitation/6/252.mp3', 6, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (253, 'यत्रोपरमते चित्तं निरुद्धं योगसेवया।\n\nयत्र चैवात्मनाऽऽत्मानं पश्यन्नात्मनि तुष्यति।।6.20।।\n ', 'yatroparamate chittaṁ niruddhaṁ yoga-sevayā\nyatra chaivātmanātmānaṁ paśhyann ātmani tuṣhyati\n', 'yatra—when; uparamate—rejoice inner joy; chittam—the mind; niruddham—restrained; yoga-sevayā—by the practice of yog; yatra—when; cha—and; eva—certainly; ātmanā—through the purified mind; ātmānam—the soul; paśhyan—behold; ātmani—in the self; tuṣhyati—is satisfied\n', 'https://gita.github.io/gita/data/verse_recitation/6/253.mp3', 6, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (254, 'सुखमात्यन्तिकं यत्तद्बुद्धिग्राह्यमतीन्द्रियम्।\n\nवेत्ति यत्र न चैवायं स्थितश्चलति तत्त्वतः।।6.21।।\n ', 'sukham ātyantikaṁ yat tad buddhi-grāhyam atīndriyam\nvetti yatra na chaivāyaṁ sthitaśh chalati tattvataḥ\n', 'sukham—happiness; ātyantikam—limitless; yat—which; tat—that; buddhi—by intellect; grāhyam—grasp; atīndriyam—transcending the senses; vetti—knows; yatra—wherein; na—never; cha—and; eva—certainly; ayam—he; sthitaḥ—situated; chalati—deviates; tattvataḥ—from the Eternal Truth\n', 'https://gita.github.io/gita/data/verse_recitation/6/254.mp3', 6, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (255, 'यं लब्ध्वा चापरं लाभं मन्यते नाधिकं ततः।\n\nयस्मिन्स्थितो न दुःखेन गुरुणापि विचाल्यते।।6.22।।\n ', 'yaṁ labdhvā chāparaṁ lābhaṁ manyate nādhikaṁ tataḥ\nyasmin sthito na duḥkhena guruṇāpi vichālyate\n', 'yam—which; labdhvā—having gained; cha—and; aparam—any other; lābham—gain; manyate—considers; na—not; adhikam—greater; tataḥ—than that; yasmin—in which; sthitaḥ—being situated; na—never; duḥkhena—by sorrow; guruṇā—(by) the greatest; api—even; vichālyate—is shaken\n', 'https://gita.github.io/gita/data/verse_recitation/6/255.mp3', 6, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (256, 'तं विद्याद् दुःखसंयोगवियोगं योगसंज्ञितम्।\n\nस निश्चयेन योक्तव्यो योगोऽनिर्विण्णचेतसा।।6.23।।\n ', 'taṁ vidyād duḥkha-sanyoga-viyogaṁ yogasaṅjñitam\nsa niśhchayena yoktavyo yogo ’nirviṇṇa-chetasā\n', 'tam—that; vidyāt—you should know; duḥkha-sanyoga-viyogam—state of severance from union with misery; yoga-saṁjñitam—is known as yog; saḥ—that; niśhchayena—resolutely; yoktavyaḥ—should be practiced; yogaḥ—yog; anirviṇṇa-chetasā—with an undeviating mind\n', 'https://gita.github.io/gita/data/verse_recitation/6/256.mp3', 6, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (257, 'सङ्कल्पप्रभवान्कामांस्त्यक्त्वा सर्वानशेषतः।\n\nमनसैवेन्द्रियग्रामं विनियम्य समन्ततः।।6.24।।\n ', 'saṅkalpa-prabhavān kāmāns tyaktvā sarvān aśheṣhataḥ\nmanasaivendriya-grāmaṁ viniyamya samantataḥ\n', 'saṅkalpa—a resolve; prabhavān—born of; kāmān—desires; tyaktvā—having abandoned; sarvān—all; aśheṣhataḥ—completely; manasā—through the mind; eva—certainly; indriya-grāmam—the group of senses; viniyamya—restraining; samantataḥ—from all sides;\n', 'https://gita.github.io/gita/data/verse_recitation/6/257.mp3', 6, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (258, 'शनैः शनैरुपरमेद् बुद्ध्या धृतिगृहीतया।\n\nआत्मसंस्थं मनः कृत्वा न किञ्चिदपि चिन्तयेत्।।6.25।।\n ', 'śhanaiḥ śhanair uparamed buddhyā dhṛiti-gṛihītayā\nātma-sansthaṁ manaḥ kṛitvā na kiñchid api chintayet\n', 'śhanaiḥ—gradually; śhanaiḥ—gradually; uparamet—attain peace; buddhyā—by intellect; dhṛiti-gṛihītayā—achieved through determination of resolve that is in accordance with scriptures; ātma-sanstham—fixed in God; manaḥ—mind; kṛitvā—having made; na—not; kiñchit—anything; api—even; chintayet—should think of\n', 'https://gita.github.io/gita/data/verse_recitation/6/258.mp3', 6, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (259, 'यतो यतो निश्चरति मनश्चञ्चलमस्थिरम्।\n\nततस्ततो नियम्यैतदात्मन्येव वशं नयेत्।।6.26।।\n ', 'yato yato niśhcharati manaśh chañchalam asthiram\ntatas tato niyamyaitad ātmanyeva vaśhaṁ nayet\n', 'yataḥ yataḥ—whenever and wherever; niśhcharati—wanders; manaḥ—the mind; chañchalam—restless; asthiram—unsteady; tataḥ tataḥ—from there; niyamya—having restrained; etat—this; ātmani—on God; eva—certainly; vaśham—control; nayet—should bring\n', 'https://gita.github.io/gita/data/verse_recitation/6/259.mp3', 6, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (260, 'प्रशान्तमनसं ह्येनं योगिनं सुखमुत्तमम्।\n\nउपैति शान्तरजसं ब्रह्मभूतमकल्मषम्।।6.27।।\n ', 'praśhānta-manasaṁ hyenaṁ yoginaṁ sukham uttamam\nupaiti śhānta-rajasaṁ brahma-bhūtam akalmaṣham\n', 'praśhānta—peaceful; manasam—mind; hi—certainly; enam—this; yoginam—yogi; sukham uttamam—the highest bliss; upaiti—attains; śhānta-rajasam—whose passions are subdued; brahma-bhūtam—endowed with God-realization; akalmaṣham—without sin\n', 'https://gita.github.io/gita/data/verse_recitation/6/260.mp3', 6, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (261, 'युञ्जन्नेवं सदाऽऽत्मानं योगी विगतकल्मषः।\n\nसुखेन ब्रह्मसंस्पर्शमत्यन्तं सुखमश्नुते।।6.28।।\n ', 'yuñjann evaṁ sadātmānaṁ yogī vigata-kalmaṣhaḥ\nsukhena brahma-sansparśham atyantaṁ sukham aśhnute\n', 'yuñjan—uniting (the self with God); evam—thus; sadā—always; ātmānam—the self; yogī—a yogi; vigata—freed from; kalmaṣhaḥ—sins; sukhena—easily; brahma-sansparśham—constantly in touch with the Supreme; atyantam—the highest; sukham—bliss; aśhnute—attains\n', 'https://gita.github.io/gita/data/verse_recitation/6/261.mp3', 6, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (262, 'सर्वभूतस्थमात्मानं सर्वभूतानि चात्मनि।\n\nईक्षते योगयुक्तात्मा सर्वत्र समदर्शनः।।6.29।।\n ', 'sarva-bhūta-stham ātmānaṁ sarva-bhūtāni chātmani\nīkṣhate yoga-yuktātmā sarvatra sama-darśhanaḥ\n', 'sarva-bhūta-stham—situated in all living beings; ātmānam—Supreme Soul; sarva—all; bhūtāni—living beings; cha—and; ātmani—in God; īkṣhate—sees; yoga-yukta-ātmā—one united in consciousness with God; sarvatra—everywhere; sama-darśhanaḥ—equal vision\n', 'https://gita.github.io/gita/data/verse_recitation/6/262.mp3', 6, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (263, 'यो मां पश्यति सर्वत्र सर्वं च मयि पश्यति।\n\nतस्याहं न प्रणश्यामि स च मे न प्रणश्यति।।6.30।।\n ', 'yo māṁ paśhyati sarvatra sarvaṁ cha mayi paśhyati\ntasyāhaṁ na praṇaśhyāmi sa cha me na praṇaśhyati\n', 'yaḥ—who; mām—me; paśhyati—see; sarvatra—everywhere; sarvam—everything; cha—and; mayi—in me; paśhyati—see; tasya—for him; aham—I; na—not; praṇaśhyāmi—lost; saḥ—that person; cha—and; me—to me; na—nor; praṇaśhyati—lost\n', 'https://gita.github.io/gita/data/verse_recitation/6/263.mp3', 6, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (264, 'सर्वभूतस्थितं यो मां भजत्येकत्वमास्थितः।\n\nसर्वथा वर्तमानोऽपि स योगी मयि वर्तते।।6.31।।\n ', 'sarva-bhūta-sthitaṁ yo māṁ bhajatyekatvam āsthitaḥ\nsarvathā vartamāno ’pi sa yogī mayi vartate\n', 'sarva-bhūta-sthitam—situated in all beings; yaḥ—who; mām—me; bhajati—worships; ekatvam—in unity; āsthitaḥ—established; sarvathā—in all kinds of; varta-mānaḥ—remain; api—although; saḥ—he; yogī—a yogi; mayi—in me; vartate—dwells\n', 'https://gita.github.io/gita/data/verse_recitation/6/264.mp3', 6, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (265, 'आत्मौपम्येन सर्वत्र समं पश्यति योऽर्जुन।\n\nसुखं वा यदि वा दुःखं सः योगी परमो मतः।।6.32।।\n ', 'ātmaupamyena sarvatra samaṁ paśhyati yo ’rjuna\nsukhaṁ vā yadi vā duḥkhaṁ sa yogī paramo mataḥ\n', 'ātma-aupamyena—similar to oneself; sarvatra—everywhere; samam—equally; paśhyati—see; yaḥ—who; arjuna—Arjun; sukham—joy; vā—or; yadi—if; vā—or; duḥkham—sorrow; saḥ—such; yogī—a yogi; paramaḥ—highest; mataḥ—is considered\n', 'https://gita.github.io/gita/data/verse_recitation/6/265.mp3', 6, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (266, 'अर्जुन उवाच\n\nयोऽयं योगस्त्वया प्रोक्तः साम्येन मधुसूदन।\n\nएतस्याहं न पश्यामि चञ्चलत्वात् स्थितिं स्थिराम्।।6.33।।\n ', 'arjuna uvācha\nyo ’yaṁ yogas tvayā proktaḥ sāmyena madhusūdana\netasyāhaṁ na paśhyāmi chañchalatvāt sthitiṁ sthirām\n', 'arjunaḥ uvācha—Arjun said; yaḥ—which; ayam—this; yogaḥ—system of Yog; tvayā—by you; proktaḥ—described; sāmyena—by equanimity; madhu-sūdana—Shree Krishna, the killer of the demon named Madhu; etasya—of this; aham—I; na—do not; paśhyāmi—see; chañchalatvāt—due to restlessness; sthitim—situation; sthirām—steady\n', 'https://gita.github.io/gita/data/verse_recitation/6/266.mp3', 6, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (267, 'चञ्चलं हि मनः कृष्ण प्रमाथि बलवद्दृढम्।\n\nतस्याहं निग्रहं मन्ये वायोरिव सुदुष्करम्।।6.34।।\n ', 'chañchalaṁ hi manaḥ kṛiṣhṇa pramāthi balavad dṛiḍham\ntasyāhaṁ nigrahaṁ manye vāyor iva su-duṣhkaram\n', 'chañchalam—restless; hi—certainly; manaḥ—mind; kṛiṣhṇa—Shree Krishna; pramāthi—turbulent; bala-vat—strong; dṛiḍham—obstinate; tasya—its; aham—I; nigraham—control; manye—think; vāyoḥ—of the wind; iva—like; su-duṣhkaram—difficult to perform\n', 'https://gita.github.io/gita/data/verse_recitation/6/267.mp3', 6, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (268, 'श्री भगवानुवाच\n\nअसंशयं महाबाहो मनो दुर्निग्रहं चलं।\n\nअभ्यासेन तु कौन्तेय वैराग्येण च गृह्यते।।6.35।।\n ', 'śhrī bhagavān uvācha\nasanśhayaṁ mahā-bāho mano durnigrahaṁ chalam\nabhyāsena tu kaunteya vairāgyeṇa cha gṛihyate\n', 'śhrī-bhagavān uvācha—Lord Krishna said; asanśhayam—undoubtedly; mahā-bāho—mighty-armed one; manaḥ—the mind; durnigraham—difficult to restrain; chalam—restless; abhyāsena—by practice; tu—but; kaunteya—Arjun, the son of Kunti; vairāgyeṇa—by detachment; cha—and; gṛihyate—can be controlled\n', 'https://gita.github.io/gita/data/verse_recitation/6/268.mp3', 6, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (269, 'असंयतात्मना योगो दुष्प्राप इति मे मतिः।\n\nवश्यात्मना तु यतता शक्योऽवाप्तुमुपायतः।।6.36।।\n ', 'asaṅyatātmanā yogo duṣhprāpa iti me matiḥ\nvaśhyātmanā tu yatatā śhakyo ’vāptum upāyataḥ\n', 'asanyata-ātmanā—one whose mind is unbridled; yogaḥ—Yog; duṣhprāpaḥ—difficult to attain; iti—thus; me—my; matiḥ—opinion; vaśhya-ātmanā—by one whose mind is controlled; tu—but; yatatā—one who strives; śhakyaḥ—possible; avāptum—to achieve; upāyataḥ—by right means\n', 'https://gita.github.io/gita/data/verse_recitation/6/269.mp3', 6, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (270, 'अर्जुन उवाच\n\nअयतिः श्रद्धयोपेतो योगाच्चलितमानसः।\n\nअप्राप्य योगसंसिद्धिं कां गतिं कृष्ण गच्छति।।6.37।।\n ', 'arjuna uvācha\nayatiḥ śhraddhayopeto yogāch chalita-mānasaḥ\naprāpya yoga-sansiddhiṁ kāṅ gatiṁ kṛiṣhṇa gachchhati\n', 'arjunaḥ uvācha—Arjun said; ayatiḥ—lax; śhraddhayā—with faith; upetaḥ—possessed; yogāt—from Yog; chalita-mānasaḥ—whose mind becomes deviated; aprāpya—failing to attain; yoga-sansiddhim—the highest perfection in yog; kām—which; gatim—destination; kṛiṣhṇa—Shree Krishna; gachchhati—goes\n', 'https://gita.github.io/gita/data/verse_recitation/6/270.mp3', 6, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (271, 'कच्चिन्नोभयविभ्रष्टश्छिन्नाभ्रमिव नश्यति।\n\nअप्रतिष्ठो महाबाहो विमूढो ब्रह्मणः पथि।।6.38।।\n ', 'kachchin nobhaya-vibhraṣhṭaśh chhinnābhram iva naśhyati\napratiṣhṭho mahā-bāho vimūḍho brahmaṇaḥ pathi\n', 'kachchit—whether; na—not; ubhaya—both; vibhraṣhṭaḥ—deviated from; chhinna—broken; abhram—cloud; iva—like; naśhyati—perishes; apratiṣhṭhaḥ—without any support; mahā-bāho—mighty-armed Krishna; vimūḍhaḥ—bewildered; brahmaṇaḥ—of God-realization; pathi—one on the path\n', 'https://gita.github.io/gita/data/verse_recitation/6/271.mp3', 6, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (272, 'एतन्मे संशयं कृष्ण छेत्तुमर्हस्यशेषतः।\n\nत्वदन्यः संशयस्यास्य छेत्ता न ह्युपपद्यते।।6.39।।\n ', 'etan me sanśhayaṁ kṛiṣhṇa chhettum arhasyaśheṣhataḥ\ntvad-anyaḥ sanśhayasyāsya chhettā na hyupapadyate\n', 'etat—this; me—my; sanśhayam—doubt; kṛiṣhṇa—Krishna; chhettum—to dispel; arhasi—you can; aśheṣhataḥ—completely; tvat—than you; anyaḥ—other; sanśhayasya—of doubt; asya—this; chhettā—a dispeller; na—never; hi—certainly; upapadyate—is fit\n', 'https://gita.github.io/gita/data/verse_recitation/6/272.mp3', 6, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (273, 'श्री भगवानुवाच\n\nपार्थ नैवेह नामुत्र विनाशस्तस्य विद्यते।\n\nनहि कल्याणकृत्कश्िचद्दुर्गतिं तात गच्छति।।6.40।।\n ', 'śhrī bhagavān uvācha\npārtha naiveha nāmutra vināśhas tasya vidyate\nna hi kalyāṇa-kṛit kaśhchid durgatiṁ tāta gachchhati\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; pārtha—Arjun, the son of Pritha; na eva—never; iha—in this world; na—never; amutra—in the next world; vināśhaḥ—destruction; tasya—his; vidyate—exists; na—never; hi—certainly; kalyāṇa-kṛit—one who strives for God-realization; kaśhchit—anyone; durgatim—evil destination; tāta—my friend; gachchhati—goes\n', 'https://gita.github.io/gita/data/verse_recitation/6/273.mp3', 6, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (274, 'प्राप्य पुण्यकृतां लोकानुषित्वा शाश्वतीः समाः।\n\nशुचीनां श्रीमतां गेहे योगभ्रष्टोऽभिजायते।।6.41।।\n ', 'prāpya puṇya-kṛitāṁ lokān uṣhitvā śhāśhvatīḥ samāḥ\nśhuchīnāṁ śhrīmatāṁ gehe yoga-bhraṣhṭo’bhijāyate\n', 'prāpya—attain; puṇya-kṛitām—of the virtuous; lokān—abodes; uṣhitvā—after dwelling; śhāśhvatīḥ—many; samāḥ—ages; śhuchīnām—of the pious; śhrī-matām—of the prosperous; gehe—in the house; yoga-bhraṣhṭaḥ—the unsuccessful yogis; abhijāyate—take birth; \n', 'https://gita.github.io/gita/data/verse_recitation/6/274.mp3', 6, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (275, 'अथवा योगिनामेव कुले भवति धीमताम्।\n\nएतद्धि दुर्लभतरं लोके जन्म यदीदृशम्।।6.42।।\n ', 'atha vā yoginām eva kule bhavati dhīmatām\netad dhi durlabhataraṁ loke janma yad īdṛiśham\n', 'atha vā—else; yoginām—of those endowed with divine wisdom; eva—certainly; kule—in the family; bhavati—take birth; dhī-matām—of the wise; etat—this; hi—certainly; durlabha-taram—very rare; loke—in this world; janma—birth; yat—which; īdṛiśham—like this\n', 'https://gita.github.io/gita/data/verse_recitation/6/275.mp3', 6, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (276, 'तत्र तं बुद्धिसंयोगं लभते पौर्वदेहिकम्।\n\nयतते च ततो भूयः संसिद्धौ कुरुनन्दन।।6.43।।\n ', 'tatra taṁ buddhi-sanyogaṁ labhate paurva-dehikam\nyatate cha tato bhūyaḥ sansiddhau kuru-nandana\n', 'tatra—there; tam—that; buddhi-sanyogam—reawaken their wisdom; labhate—obtains; paurva-dehikam—from the previous lives; yatate—strives; cha—and; tataḥ—thereafter; bhūyaḥ—again; sansiddhau—for perfection; kuru-nandana—Arjun, descendant of the Kurus\n', 'https://gita.github.io/gita/data/verse_recitation/6/276.mp3', 6, 43);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (277, 'पूर्वाभ्यासेन तेनैव ह्रियते ह्यवशोऽपि सः।\n\nजिज्ञासुरपि योगस्य शब्दब्रह्मातिवर्तते।।6.44।।\n ', 'pūrvābhyāsena tenaiva hriyate hyavaśho ’pi saḥ\njijñāsur api yogasya śhabda-brahmātivartate\n', 'pūrva—past; abhyāsena—discipline; tena—by that; eva—certainly; hriyate—is attracted; hi—surely; avaśhaḥ—helplessly; api—although; saḥ—that person; jijñāsuḥ—inquisitive; api—even; yogasya—about yog; śhabda-brahma—fruitive portion of the Vedas; ativartate—transcends\n', 'https://gita.github.io/gita/data/verse_recitation/6/277.mp3', 6, 44);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (278, 'प्रयत्नाद्यतमानस्तु योगी संशुद्धकिल्बिषः।\n\nअनेकजन्मसंसिद्धस्ततो याति परां गतिम्।।6.45।।\n ', 'prayatnād yatamānas tu yogī sanśhuddha-kilbiṣhaḥ\naneka-janma-sansiddhas tato yāti parāṁ gatim\n', 'prayatnāt—with great effort; yatamānaḥ—endeavoring; tu—and; yogī—a yogi; sanśhuddha—purified; kilbiṣhaḥ—from material desires; aneka—after many, many; janma—births; sansiddhaḥ—attain perfection; tataḥ—then; yāti—attains; parām—the highest; gatim—path\n', 'https://gita.github.io/gita/data/verse_recitation/6/278.mp3', 6, 45);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (279, 'तपस्विभ्योऽधिको योगी ज्ञानिभ्योऽपि मतोऽधिकः।\n\nकर्मिभ्यश्चाधिको योगी तस्माद्योगी भवार्जुन।।6.46।।\n ', 'tapasvibhyo ’dhiko yogī\njñānibhyo ’pi mato ’dhikaḥ\nkarmibhyaśh chādhiko yogī\ntasmād yogī bhavārjuna\n', 'tapasvibhyaḥ—than the ascetics; adhikaḥ—superior; yogī—a yogi; jñānibhyaḥ—than the persons of learning; api—even; mataḥ—considered; adhikaḥ—superior; karmibhyaḥ—than the ritualistic performers; cha—and; adhikaḥ—superior; yogī—a yogi; tasmāt—therefore; yogī—a yogi; bhava—just become; arjuna—Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/6/279.mp3', 6, 46);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (280, 'योगिनामपि सर्वेषां मद्गतेनान्तरात्मना।\n\nश्रद्धावान्भजते यो मां स मे युक्ततमो मतः।।6.47।।\n ', 'yoginām api sarveṣhāṁ mad-gatenāntar-ātmanā\nśhraddhāvān bhajate yo māṁ sa me yuktatamo mataḥ\n', 'yoginām—of all yogis; api—however; sarveṣhām—all types of; mat-gatena—absorbed in me (God); antaḥ—inner; ātmanā—with the mind; śhraddhā-vān—with great faith; bhajate—engage in devotion; yaḥ—who; mām—to me; saḥ—he; me—by me; yukta-tamaḥ—the highest yogi; mataḥ—is considered\n', 'https://gita.github.io/gita/data/verse_recitation/6/280.mp3', 6, 47);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (281, 'श्री भगवानुवाच\n\nमय्यासक्तमनाः पार्थ योगं युञ्जन्मदाश्रयः।\n\nअसंशयं समग्रं मां यथा ज्ञास्यसि तच्छृणु।।7.1।।\n ', 'śhrī bhagavān uvācha\nmayyāsakta-manāḥ pārtha yogaṁ yuñjan mad-āśhrayaḥ\nasanśhayaṁ samagraṁ māṁ yathā jñāsyasi tach chhṛiṇu\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; mayi—to me; āsakta-manāḥ—with the mind attached; pārtha—Arjun, the son of Pritha; yogam—bhakti yog; yuñjan—practicing; mat-āśhrayaḥ—surrendering to me; asanśhayam—free from doubt; samagram—completely; mām—me; yathā—how; jñāsyasi—you shall know; tat—that; śhṛiṇu—listen\n', 'https://gita.github.io/gita/data/verse_recitation/7/281.mp3', 7, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (282, 'ज्ञानं तेऽहं सविज्ञानमिदं वक्ष्याम्यशेषतः।\n\nयज्ज्ञात्वा नेह भूयोऽन्यज्ज्ञातव्यमवशिष्यते।।7.2।।\n ', 'jñānaṁ te ’haṁ sa-vijñānam idaṁ vakṣhyāmyaśheṣhataḥ\nyaj jñātvā neha bhūyo ’nyaj jñātavyam-avaśhiṣhyate\n', 'jñānam—knowledge; te—unto you; aham—I; sa—with; vijñānam—wisdom; idam—this; vakṣhyāmi—shall reveal; aśheṣhataḥ—in full; yat—which; jñātvā—having known; na—not; iha—in this world; bhūyaḥ—further; anyat—anything else; jñātavyam—to be known; avaśhiṣhyate—remains\n', 'https://gita.github.io/gita/data/verse_recitation/7/282.mp3', 7, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (283, 'मनुष्याणां सहस्रेषु कश्िचद्यतति सिद्धये।\n\nयततामपि सिद्धानां कश्िचन्मां वेत्ति तत्त्वतः।।7.3।।\n ', 'manuṣhyāṇāṁ sahasreṣhu kaśhchid yatati siddhaye\nyatatām api siddhānāṁ kaśhchin māṁ vetti tattvataḥ\n', 'manuṣhyāṇām—of men; sahasreṣhu—out of many thousands; kaśhchit—someone; yatati—strives; siddhaye—for perfection; yatatām—of those who strive; api—even; siddhānām—of those who have achieved perfection; kaśhchit—someone; mām—me; vetti—knows; tattvataḥ—in truth\n', 'https://gita.github.io/gita/data/verse_recitation/7/283.mp3', 7, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (284, 'भूमिरापोऽनलो वायुः खं मनो बुद्धिरेव च।\n\nअहङ्कार इतीयं मे भिन्ना प्रकृतिरष्टधा।।7.4।।\n ', 'bhūmir-āpo ’nalo vāyuḥ khaṁ mano buddhir eva cha\nahankāra itīyaṁ me bhinnā prakṛitir aṣhṭadhā\n', 'bhūmiḥ—earth; āpaḥ—water; analaḥ—fire; vāyuḥ—air; kham—space; manaḥ—mind; buddhiḥ—intellect; eva—certainly; cha—and; ahankāraḥ—ego; iti—thus; iyam—all these; me—my; bhinnā—divisions; prakṛitiḥ—material energy; aṣhṭadhā—eightfold\n', 'https://gita.github.io/gita/data/verse_recitation/7/284.mp3', 7, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (285, 'अपरेयमितस्त्वन्यां प्रकृतिं विद्धि मे पराम्।\n\nजीवभूतां महाबाहो ययेदं धार्यते जगत्।।7.5।।\n ', 'apareyam itas tvanyāṁ prakṛitiṁ viddhi me parām\njīva-bhūtāṁ mahā-bāho yayedaṁ dhāryate jagat\n', 'aparā—inferior; iyam—this; itaḥ—besides this; tu—but; anyām—another; prakṛitim—energy; viddhi—know; me—my; parām—superior; jīva-bhūtām—living beings; mahā-bāho—mighty-armed one; yayā—by whom; idam—this; dhāryate—the basis; jagat—the material world\n', 'https://gita.github.io/gita/data/verse_recitation/7/285.mp3', 7, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (286, 'एतद्योनीनि भूतानि सर्वाणीत्युपधारय।\n\nअहं कृत्स्नस्य जगतः प्रभवः प्रलयस्तथा।।7.6।।\n ', 'etad-yonīni bhūtāni sarvāṇītyupadhāraya\nahaṁ kṛitsnasya jagataḥ prabhavaḥ pralayas tathā\n', 'etat yonīni—these two (energies) are the source of; bhūtāni—living beings; sarvāṇi—all; iti—that; upadhāraya—know; aham—I; kṛitsnasya—entire; jagataḥ—creation; prabhavaḥ—the source; pralayaḥ—dissolution; tathā—and\n', 'https://gita.github.io/gita/data/verse_recitation/7/286.mp3', 7, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (287, 'मत्तः परतरं नान्यत्किञ्चिदस्ति धनञ्जय।\n\nमयि सर्वमिदं प्रोतं सूत्रे मणिगणा इव।।7.7।।\n ', 'mattaḥ parataraṁ nānyat kiñchid asti dhanañjaya\nmayi sarvam idaṁ protaṁ sūtre maṇi-gaṇā iva\n', 'mattaḥ—than me; para-taram—superior; na—not; anyat kiñchit—anything else; asti—there is; dhanañjaya—Arjun, conqueror of wealth; mayi—in me; sarvam—all; idam—which we see; protam—is strung; sūtre—on a thread; maṇi-gaṇāḥ—beads; iva—like\n', 'https://gita.github.io/gita/data/verse_recitation/7/287.mp3', 7, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (288, 'रसोऽहमप्सु कौन्तेय प्रभास्मि शशिसूर्ययोः।\n\nप्रणवः सर्ववेदेषु शब्दः खे पौरुषं नृषु।।7.8।।\n ', 'raso ’ham apsu kaunteya prabhāsmi śhaśhi-sūryayoḥ\npraṇavaḥ sarva-vedeṣhu śhabdaḥ khe pauruṣhaṁ nṛiṣhu\n', 'rasaḥ—taste; aham—I; apsu—in water; kaunteya—Arjun, the son of Kunti; prabhā—the radiance; asmi—I am; śhaśhi-sūryayoḥ—of the moon and the sun; praṇavaḥ—the sacred syllable Om; sarva—in all; vedeṣhu—Vedas; śhabdaḥ—sound; khe—in ether; pauruṣham—ability; nṛiṣhu—in humans\n', 'https://gita.github.io/gita/data/verse_recitation/7/288.mp3', 7, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (289, 'पुण्यो गन्धः पृथिव्यां च तेजश्चास्मि विभावसौ।\n\nजीवनं सर्वभूतेषु तपश्चास्मि तपस्विषु।।7.9।।\n ', 'puṇyo gandhaḥ pṛithivyāṁ cha tejaśh chāsmi vibhāvasau\njīvanaṁ sarva-bhūteṣhu tapaśh chāsmi tapasviṣhu\n', 'puṇyaḥ—pure; gandhaḥ—fragrance; pṛithivyām—of the earth; cha—and; tejaḥ—brilliance; cha—and; asmi—I am; vibhāvasau—in the fire; jīvanam—the life-force; sarva—in all; bhūteṣhu—beings; tapaḥ—penance; cha—and; asmi—I am; tapasviṣhu—of the ascetics\n', 'https://gita.github.io/gita/data/verse_recitation/7/289.mp3', 7, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (290, 'बीजं मां सर्वभूतानां विद्धि पार्थ सनातनम्।\n\nबुद्धिर्बुद्धिमतामस्मि तेजस्तेजस्विनामहम्।।7.10।।\n ', 'bījaṁ māṁ sarva-bhūtānāṁ viddhi pārtha sanātanam\nbuddhir buddhimatām asmi tejas tejasvinām aham\n', 'bījam—the seed; mām—me; sarva-bhūtānām—of all beings; viddhi—know; pārtha—Arjun, the son of Pritha; sanātanam—the eternal; buddhiḥ—intellect; buddhi-matām—of the intelligent; asmi—(I) am; tejaḥ—splendor; tejasvinām—of the splendid; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/7/290.mp3', 7, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (291, 'बलं बलवतां चाहं कामरागविवर्जितम्।\n\nधर्माविरुद्धो भूतेषु कामोऽस्मि भरतर्षभ।।7.11।।\n ', 'balaṁ balavatāṁ chāhaṁ kāma-rāga-vivarjitam\ndharmāviruddho bhūteṣhu kāmo ’smi bharatarṣhabha\n', 'balam—strength; bala-vatām—of the strong; cha—and; aham—I; kāma—desire; rāga—passion; vivarjitam—devoid of; dharma-aviruddhaḥ—not conflicting with dharma; bhūteṣhu—in all beings; kāmaḥ—sexual activity; asmi—(I) am; bharata-ṛiṣhabha—Arjun, the best of the Bharats\n', 'https://gita.github.io/gita/data/verse_recitation/7/291.mp3', 7, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (292, 'ये चैव सात्त्विका भावा राजसास्तामसाश्च ये।\n\nमत्त एवेति तान्विद्धि नत्वहं तेषु ते मयि।।7.12।।\n ', 'ye chaiva sāttvikā bhāvā rājasās tāmasāśh cha ye\nmatta eveti tān viddhi na tvahaṁ teṣhu te mayi\n', 'ye—whatever; cha—and; eva—certainly; sāttvikāḥ—in the mode of goodness; bhāvāḥ—states of material existence; rājasāḥ—in the mode of passion; tāmasāḥ—in the mode of ignorance; cha—and; ye—whatever; mattaḥ—from me; eva—certainly; iti—thus; tān—those; viddhi—know; na—not; tu—but; aham—I; teṣhu—in them; te—they; mayi—in me\n', 'https://gita.github.io/gita/data/verse_recitation/7/292.mp3', 7, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (293, 'त्रिभिर्गुणमयैर्भावैरेभिः सर्वमिदं जगत्।\n\nमोहितं नाभिजानाति मामेभ्यः परमव्ययम्।।7.13।।\n ', 'tribhir guṇa-mayair bhāvair ebhiḥ sarvam idaṁ jagat\nmohitaṁ nābhijānāti māmebhyaḥ param avyayam\n', 'tribhiḥ—by three; guṇa-mayaiḥ—consisting of the modes of material nature; bhāvaiḥ—states; ebhiḥ—all these; sarvam—whole; idam—this; jagat—universe; mohitam—deluded; na—not; abhijānāti—know; mām—me; ebhyaḥ—these; param—the supreme; avyayam—imperishable\n', 'https://gita.github.io/gita/data/verse_recitation/7/293.mp3', 7, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (294, 'दैवी ह्येषा गुणमयी मम माया दुरत्यया।\n\nमामेव ये प्रपद्यन्ते मायामेतां तरन्ति ते।।7.14।।\n ', 'daivī hyeṣhā guṇa-mayī mama māyā duratyayā\nmām eva ye prapadyante māyām etāṁ taranti te\n', 'daivī—divine; hi—certainly; eṣhā—this; guṇa-mayī—consisting of the three modes of nature; mama—my; māyā—one of God’s energies. It that veils God’s true nature from souls who have not yet attained the eligibility for God-realization; duratyayā—very difficult to overcome; mām—unto me; eva—certainly; ye—who; prapadyante—surrender; māyām etām—this Maya; taranti—cross over; te—they\n', 'https://gita.github.io/gita/data/verse_recitation/7/294.mp3', 7, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (295, 'न मां दुष्कृतिनो मूढाः प्रपद्यन्ते नराधमाः।\n\nमाययापहृतज्ञाना आसुरं भावमाश्रिताः।।7.15।।\n ', 'na māṁ duṣhkṛitino mūḍhāḥ prapadyante narādhamāḥ\nmāyayāpahṛita-jñānā āsuraṁ bhāvam āśhritāḥ\n', 'na—not; mām—unto me; duṣhkṛitinaḥ—the evil doers; mūḍhāḥ—the ignorant; prapadyante—surrender; nara-adhamāḥ—one who lazily follows one’s lower nature; māyayā—by God’s material energy; apahṛita jñānāḥ—those with deluded intellect; āsuram—demoniac; bhāvam—nature; āśhritāḥ—surrender\n', 'https://gita.github.io/gita/data/verse_recitation/7/295.mp3', 7, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (296, 'चतुर्विधा भजन्ते मां जनाः सुकृतिनोऽर्जुन।\n\nआर्तो जिज्ञासुरर्थार्थी ज्ञानी च भरतर्षभ।।7.16।।\n ', 'chatur-vidhā bhajante māṁ janāḥ sukṛitino ’rjuna\nārto jijñāsur arthārthī jñānī cha bharatarṣhabha\n', 'chatuḥ-vidhāḥ—four kinds; bhajante—worship; mām—me; janāḥ—people; su-kṛitinaḥ—those who are pious; arjuna—Arjun; ārtaḥ—the distressed; jijñāsuḥ—the seekers of knowledge; artha-arthī—the seekers of material gain; jñānī—those who are situated in knowledge; cha—and; bharata-ṛiṣhabha—The best amongst the Bharatas, Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/7/296.mp3', 7, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (297, 'तेषां ज्ञानी नित्ययुक्त एकभक्ितर्विशिष्यते।\n\nप्रियो हि ज्ञानिनोऽत्यर्थमहं स च मम प्रियः।।7.17।।\n ', 'teṣhāṁ jñānī nitya-yukta eka-bhaktir viśhiṣhyate\npriyo hi jñānino ’tyartham ahaṁ sa cha mama priyaḥ\n', 'teṣhām—amongst these; jñānī—those who are situated in knowledge; nitya-yuktaḥ—ever steadfast; eka—exclusively; bhaktiḥ—devotion; viśhiṣhyate—highest; priyaḥ—very dear; hi—certainly; jñāninaḥ—to the person in knowledge; atyartham—highly; aham—I; saḥ—he; cha—and; mama—to me; priyaḥ—dear\n', 'https://gita.github.io/gita/data/verse_recitation/7/297.mp3', 7, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (298, 'उदाराः सर्व एवैते ज्ञानी त्वात्मैव मे मतम्।\n\nआस्थितः स हि युक्तात्मा मामेवानुत्तमां गतिम्।।7.18।।\n ', 'udārāḥ sarva evaite jñānī tvātmaiva me matam\nāsthitaḥ sa hi yuktātmā mām evānuttamāṁ gatim\n', 'udārāḥ—noble; sarve—all; eva—indeed; ete—these; jñānī—those in knowledge; tu—but; ātmā eva—my very self; me—my; matam—opinion; āsthitaḥ—situated; saḥ—he; hi—certainly; yukta-ātmā—those who are united; mām—in me; eva—certainly; anuttamām—the supreme; gatim—goal\n', 'https://gita.github.io/gita/data/verse_recitation/7/298.mp3', 7, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (299, 'बहूनां जन्मनामन्ते ज्ञानवान्मां प्रपद्यते।\n\nवासुदेवः सर्वमिति स महात्मा सुदुर्लभः।।7.19।।\n ', 'bahūnāṁ janmanām ante jñānavān māṁ prapadyate\nvāsudevaḥ sarvam iti sa mahātmā su-durlabhaḥ\n', 'bahūnām—many; janmanām—births; ante—after; jñāna-vān—one who is endowed with knowledge; mām—unto me; prapadyate—surrenders; vāsudevaḥ—Shree Krishna, the son of Vasudev; sarvam—all; iti—that; saḥ—that; mahā-ātmā—great soul; su-durlabhaḥ—very rare\n', 'https://gita.github.io/gita/data/verse_recitation/7/299.mp3', 7, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (300, 'कामैस्तैस्तैर्हृतज्ञानाः प्रपद्यन्तेऽन्यदेवताः।\n\nतं तं नियममास्थाय प्रकृत्या नियताः स्वया।।7.20।।\n ', 'kāmais tais tair hṛita-jñānāḥ prapadyante ’nya-devatāḥ\ntaṁ taṁ niyamam āsthāya prakṛityā niyatāḥ svayā\n', 'kāmaiḥ—by material desires; taiḥ taiḥ—by various; hṛita-jñānāḥ—whose knowledge has been carried away; prapadyante—surrender; anya—to other; devatāḥ—celestial gods; tam tam—the various; niyamam—rules and regulations; āsthāya—following; prakṛityā—by nature; niyatāḥ—controlled; svayā—by their own\n', 'https://gita.github.io/gita/data/verse_recitation/7/300.mp3', 7, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (301, 'यो यो यां यां तनुं भक्तः श्रद्धयार्चितुमिच्छति।\n\nतस्य तस्याचलां श्रद्धां तामेव विदधाम्यहम्।।7.21।।\n ', 'yo yo yāṁ yāṁ tanuṁ bhaktaḥ śhraddhayārchitum ichchhati\ntasya tasyāchalāṁ śhraddhāṁ tām eva vidadhāmyaham\n', 'yaḥ yaḥ—whoever; yām yām—whichever; tanum—form; bhaktaḥ—devotee; śhraddhayā—with faith; architum—to worship; ichchhati—desires; tasya tasya—to him; achalām—steady; śhraddhām—faith; tām—in that; eva—certainly; vidadhāmi—bestow; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/7/301.mp3', 7, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (302, 'स तया श्रद्धया युक्तस्तस्याराधनमीहते।\n\nलभते च ततः कामान्मयैव विहितान् हि तान्।।7.22।।\n ', 'sa tayā śhraddhayā yuktas tasyārādhanam īhate\nlabhate cha tataḥ kāmān mayaiva vihitān hi tān\n', 'saḥ—he; tayā—with that; śhraddhayā—faith; yuktaḥ—endowed with; tasya—of that; ārādhanam—worship; īhate—tries to engange in; labhate—obtains; cha—and; tataḥ—from that; kāmān—desires; mayā—by me; eva—alone; vihitān—granted; hi—certainly; tān—those\n', 'https://gita.github.io/gita/data/verse_recitation/7/302.mp3', 7, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (303, 'अन्तवत्तु फलं तेषां तद्भवत्यल्पमेधसाम्।\n\nदेवान्देवयजो यान्ति मद्भक्ता यान्ति मामपि।।7.23।।\n ', 'antavat tu phalaṁ teṣhāṁ tad bhavatyalpa-medhasām\ndevān deva-yajo yānti mad-bhaktā yānti mām api\n', 'anta-vat—perishable; tu—but; phalam—fruit; teṣhām—by them; tat—that; bhavati—is; alpa-medhasām—people of small understanding; devān—to the celestial gods; deva-yajaḥ—the worshipers of the celestial gods; yānti—go; mat—my; bhaktāḥ—devotees; yānti—go; mām—to me; api—whereas\n', 'https://gita.github.io/gita/data/verse_recitation/7/303.mp3', 7, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (304, 'अव्यक्तं व्यक्ितमापन्नं मन्यन्ते मामबुद्धयः।\n\nपरं भावमजानन्तो ममाव्ययमनुत्तमम्।।7.24।।\n ', 'avyaktaṁ vyaktim āpannaṁ manyante mām abuddhayaḥ\nparaṁ bhāvam ajānanto mamāvyayam anuttamam\n', 'avyaktam—formless; vyaktim—possessing a personality; āpannam—to have assumed; manyante—think; mām—me; abuddhayaḥ—less intelligent; param—Supreme; bhāvam—nature; ajānantaḥ—not understanding; mama—my; avyayam—imperishable; anuttamam—excellent\n', 'https://gita.github.io/gita/data/verse_recitation/7/304.mp3', 7, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (305, 'नाहं प्रकाशः सर्वस्य योगमायासमावृतः।\n\nमूढोऽयं नाभिजानाति लोको मामजमव्ययम्।।7.25।।\n ', 'nāhaṁ prakāśhaḥ sarvasya yoga-māyā-samāvṛitaḥ\nmūḍho ’yaṁ nābhijānāti loko mām ajam avyayam\n', 'na—not; aham—I; prakāśhaḥ—manifest; sarvasya—to everyone; yoga-māyā—God’s supreme (divine) energy; samāvṛitaḥ—veiled; mūḍhaḥ—deluded; ayam—these; na—not; abhijānāti—know; lokaḥ—persons; mām—me; ajam—unborn; avyayam—immutable\n', 'https://gita.github.io/gita/data/verse_recitation/7/305.mp3', 7, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (306, 'वेदाहं समतीतानि वर्तमानानि चार्जुन।\n\nभविष्याणि च भूतानि मां तु वेद न कश्चन।।7.26।।\n ', 'vedāhaṁ samatītāni vartamānāni chārjuna\nbhaviṣhyāṇi cha bhūtāni māṁ tu veda na kaśhchana\n', 'veda—know; aham—I; samatītāni—the past; vartamānāni—the present; cha—and; arjuna—Arjun; bhaviṣhyāṇi—the future; cha—also; bhūtāni—all living beings; mām—me; tu—but; veda—knows; na kaśhchana—no one\n', 'https://gita.github.io/gita/data/verse_recitation/7/306.mp3', 7, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (307, 'इच्छाद्वेषसमुत्थेन द्वन्द्वमोहेन भारत।\n\nसर्वभूतानि संमोहं सर्गे यान्ति परन्तप।।7.27।।\n ', 'ichchhā-dveṣha-samutthena dvandva-mohena bhārata\nsarva-bhūtāni sammohaṁ sarge yānti parantapa\n', 'ichchhā—desire; dveṣha—aversion; samutthena—arise from; dvandva—of duality; mohena—from the illusion; bhārata—Arjun, descendant of Bharat; sarva—all; bhūtāni—living beings; sammoham—into delusion; sarge—since birth; yānti—enter; parantapa—Arjun, conqueror of enemies\n', 'https://gita.github.io/gita/data/verse_recitation/7/307.mp3', 7, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (308, 'येषां त्वन्तगतं पापं जनानां पुण्यकर्मणाम्।\n\nते द्वन्द्वमोहनिर्मुक्ता भजन्ते मां दृढव्रताः।।7.28।।\n ', 'yeṣhāṁ tvanta-gataṁ pāpaṁ janānāṁ puṇya-karmaṇām\nte dvandva-moha-nirmuktā bhajante māṁ dṛiḍha-vratāḥ\n', 'yeṣhām—whose; tu—but; anta-gatam—completely destroyed; pāpam—sins; janānām—of persons; puṇya—pious; karmaṇām—activities; te—they; dvandva—of dualities; moha—illusion; nirmuktāḥ—free from; bhajante—worship;mām; dṛiḍha-vratāḥ—with determination\n', 'https://gita.github.io/gita/data/verse_recitation/7/308.mp3', 7, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (309, 'जरामरणमोक्षाय मामाश्रित्य यतन्ति ये।\n\nते ब्रह्म तद्विदुः कृत्स्नमध्यात्मं कर्म चाखिलम्।।7.29।।\n ', 'jarā-maraṇa-mokṣhāya mām āśhritya yatanti ye\nte brahma tadviduḥ kṛitsnam adhyātmaṁ karma chākhilam\n', 'jarā—from old age; maraṇa—and death; mokṣhāya—for liberation; mām—me; āśhritya—take shelter in; yatanti—strive; ye—who; te—they; brahma—Brahman; tat—that; viduḥ—know; kṛitsnam—everything; adhyātmam—the individual self; karma—karmic action; cha—and; akhilam—entire\n', 'https://gita.github.io/gita/data/verse_recitation/7/309.mp3', 7, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (310, 'साधिभूताधिदैवं मां साधियज्ञं च ये विदुः।\n\nप्रयाणकालेऽपि च मां ते विदुर्युक्तचेतसः।।7.30।।\n ', 'sādhibhūtādhidaivaṁ māṁ sādhiyajñaṁ cha ye viduḥ\nprayāṇa-kāle ’pi cha māṁ te vidur yukta-chetasaḥ\n', 'sa-adhibhūta—governing principle of the field of matter; adhidaivam—governing principle of the celestial gods; mām—me; sa-adhiyajñam—governing principle of the Lord all sacrificial performances; cha—and; ye—who; viduḥ—know; prayāṇa—of death; kāle—at the time; api—even; cha—and; mām—me; te—they; viduḥ—know; yukta-chetasaḥ—in full consciousness of me\n', 'https://gita.github.io/gita/data/verse_recitation/7/310.mp3', 7, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (311, 'अर्जुन उवाच\n\nकिं तद्ब्रह्म किमध्यात्मं किं कर्म पुरुषोत्तम।\n\nअधिभूतं च किं प्रोक्तमधिदैवं किमुच्यते।।8.1।।\n ', 'arjuna uvācha\nkiṁ tad brahma kim adhyātmaṁ kiṁ karma puruṣhottama\nadhibhūtaṁ cha kiṁ proktam adhidaivaṁ kim uchyate\n', 'arjunaḥ uvācha—Arjun said; kim—what; tat—that; brahma—Brahman; kim—what; adhyātmam—the individual soul; kim—what; karma—the principle of karma; puruṣha-uttama—Shree Krishna, the Supreme Divine Personality; adhibhūtam—the material manifestation; cha—and; kim—what; proktam—is called; adhidaivam—the Lord of the celestial gods; kim—what; uchyate—is called; \n', 'https://gita.github.io/gita/data/verse_recitation/8/311.mp3', 8, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (312, 'अधियज्ञः कथं कोऽत्र देहेऽस्मिन्मधुसूदन।\n\nप्रयाणकाले च कथं ज्ञेयोऽसि नियतात्मभिः।।8.2।।\n ', 'adhiyajñaḥ kathaṁ ko ’tra dehe ’smin madhusūdana\nprayāṇa-kāle cha kathaṁ jñeyo ’si niyatātmabhiḥ\n', 'adhiyajñaḥ—the Lord all sacrificial performances; katham—how; kaḥ—who; atra—here; dehe—in body; asmin—this; madhusūdana—Shree Krishna, the killer of the demon named Madhu; prayāṇa-kāle—at the time of death; cha—and; katham—how; jñeyaḥ—to be known; asi—are (you); niyata-ātmabhiḥ—by those of steadfast mind\n', 'https://gita.github.io/gita/data/verse_recitation/8/312.mp3', 8, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (313, 'श्री भगवानुवाच\n\nअक्षरं ब्रह्म परमं स्वभावोऽध्यात्ममुच्यते।\n\nभूतभावोद्भवकरो विसर्गः कर्मसंज्ञितः।।8.3।।\n ', 'śhrī bhagavān uvācha\nakṣharaṁ brahma paramaṁ svabhāvo ’dhyātmam uchyate\nbhūta-bhāvodbhava-karo visargaḥ karma-sanjñitaḥ\n', 'śhrī-bhagavān uvācha—the Blessed Lord said; akṣharam—indestructible; brahma—Brahman; paramam—the Supreme; svabhāvaḥ—nature; adhyātmam—one’s own self; uchyate—is called; bhūta-bhāva-udbhava-karaḥ—Actions pertaining to the material personality of living beings, and its development; visargaḥ—creation; karma—fruitive activities; sanjñitaḥ—are called\n', 'https://gita.github.io/gita/data/verse_recitation/8/313.mp3', 8, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (314, 'अधिभूतं क्षरो भावः पुरुषश्चाधिदैवतम्।\n\nअधियज्ञोऽहमेवात्र देहे देहभृतां वर।।8.4।।\n ', 'adhibhūtaṁ kṣharo bhāvaḥ puruṣhaśh chādhidaivatam\nadhiyajño ’ham evātra dehe deha-bhṛitāṁ vara\n', 'adhibhūtam—the ever changing physical manifestation; kṣharaḥ—perishable; bhāvaḥ—nature; puruṣhaḥ—the cosmic personality of God, encompassing the material creation; cha—and; adhidaivatam—the Lord of the celestial gods; adhiyajñaḥ—the Lord of all sacrifices; aham—I; eva—certainly; atra—here; dehe—in the body; deha-bhṛitām—of the embodied; vara—O best\n', 'https://gita.github.io/gita/data/verse_recitation/8/314.mp3', 8, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (315, 'अन्तकाले च मामेव स्मरन्मुक्त्वा कलेवरम्।\n\nयः प्रयाति स मद्भावं याति नास्त्यत्र संशयः।।8.5।।\n ', 'anta-kāle cha mām eva smaran muktvā kalevaram\nyaḥ prayāti sa mad-bhāvaṁ yāti nāstyatra sanśhayaḥ\n', 'anta-kāle—at the time of death; cha—and; mām—me; eva—alone; smaran—remembering; muktvā—relinquish; kalevaram—the body; yaḥ—who; prayāti—goes; saḥ—he; mat-bhāvam—Godlike nature; yāti—achieves; na—no; asti—there is; atra—here; sanśhayaḥ—doubt\n', 'https://gita.github.io/gita/data/verse_recitation/8/315.mp3', 8, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (316, 'यं यं वापि स्मरन्भावं त्यजत्यन्ते कलेवरम्।\n\nतं तमेवैति कौन्तेय सदा तद्भावभावितः।।8.6।।\n ', 'yaṁ yaṁ vāpi smaran bhāvaṁ tyajatyante kalevaram\ntaṁ tam evaiti kaunteya sadā tad-bhāva-bhāvitaḥ\n', 'yam yam—whatever; vā—or; api—even; smaran—remembering; bhāvam—remembrance; tyajati—gives up; ante—in the end; kalevaram—the body; tam—to that; tam—to that; eva—certainly; eti—gets; kaunteya—Arjun, the son of Kunti; sadā—always; tat—that; bhāva-bhāvitaḥ—absorbed in contemplation\n', 'https://gita.github.io/gita/data/verse_recitation/8/316.mp3', 8, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (317, 'तस्मात्सर्वेषु कालेषु मामनुस्मर युध्य च।\n\nमय्यर्पितमनोबुद्धिर्मामेवैष्यस्यसंशयम्।।8.7।।\n ', 'tasmāt sarveṣhu kāleṣhu mām anusmara yudhya cha\nmayyarpita-mano-buddhir mām evaiṣhyasyasanśhayam\n', 'tasmāt—therefore; sarveṣhu—in all; kāleṣhu—times; mām—me; anusmara—remember; yudhya—fight; cha—and; mayi—to me; arpita—surrender; manaḥ—mind; buddhiḥ—intellect; mām—to me; eva—surely; eṣhyasi—you shall attain; asanśhayaḥ—without a doubt\n', 'https://gita.github.io/gita/data/verse_recitation/8/317.mp3', 8, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (318, 'अभ्यासयोगयुक्तेन चेतसा नान्यगामिना।\n\nपरमं पुरुषं दिव्यं याति पार्थानुचिन्तयन्।।8.8।।\n ', 'abhyāsa-yoga-yuktena chetasā nānya-gāminā\nparamaṁ puruṣhaṁ divyaṁ yāti pārthānuchintayan\n', 'abhyāsa-yoga—by practice of yog; yuktena—being constantly engaged in remembrance; chetasā—by the mind; na anya-gāminā—without deviating; paramam puruṣham—the Supreme Divine Personality; divyam—divine; yāti—one attains; pārtha—Arjun, the son of Pritha; anuchintayan—constant remembrance\n', 'https://gita.github.io/gita/data/verse_recitation/8/318.mp3', 8, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (319, 'कविं पुराणमनुशासितार\n\nमणोरणीयांसमनुस्मरेद्यः।\n\nसर्वस्य धातारमचिन्त्यरूप\n\nमादित्यवर्णं तमसः परस्तात्।।8.9।।\n ', 'kaviṁ purāṇam anuśhāsitāram\naṇor aṇīyānsam anusmared yaḥ\nsarvasya dhātāram achintya-rūpam\nāditya-varṇaṁ tamasaḥ parastāt\n', 'kavim—poet; purāṇam—ancient; anuśhāsitāram—the controller; aṇoḥ—than the atom; aṇīyānsam—smaller; anusmaret—always remembers; yaḥ—who; sarvasya—of everything; dhātāram—the support; achintya—inconceivable; rūpam—divine form; āditya-varṇam—effulgent like the sun; tamasaḥ—to the darkness of ignorance; parastāt—beyond;\n', 'https://gita.github.io/gita/data/verse_recitation/8/319.mp3', 8, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (320, 'प्रयाणकाले मनसाऽचलेन\n\nभक्त्या युक्तो योगबलेन चैव।\n\nभ्रुवोर्मध्ये प्राणमावेश्य सम्यक्\n\nस तं परं पुरुषमुपैति दिव्यम्।।8.10।।\n ', 'prayāṇa-kāle manasāchalena\nbhaktyā yukto yoga-balena chaiva\nbhruvor madhye prāṇam āveśhya samyak\nsa taṁ paraṁ puruṣham upaiti divyam\n', 'prayāṇa-kāle—at the time of death; manasā—mind; achalena—steadily; bhaktyā—remembering with great devotion; yuktaḥ—united; yoga-balena—through the power of yog; cha—and; eva—certainly; bhruvoḥ—the two eyebrows; madhye—between; prāṇam—life airs; āveśhya—fixing; samyak—completely; saḥ—he; tam—him; param puruṣham—the Supreme Divine Lord; upaiti—attains; divyam—divine\n', 'https://gita.github.io/gita/data/verse_recitation/8/320.mp3', 8, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (321, 'यदक्षरं वेदविदो वदन्ति\n\nविशन्ति यद्यतयो वीतरागाः।\n\nयदिच्छन्तो ब्रह्मचर्यं चरन्ति\n\nतत्ते पदं संग्रहेण प्रवक्ष्ये।।8.11।।\n ', 'yad akṣharaṁ veda-vido vadanti\nviśhanti yad yatayo vīta-rāgāḥ\nyad ichchhanto brahmacharyaṁ charanti\ntat te padaṁ saṅgraheṇa pravakṣhye\n', 'yat—which; akṣharam—Imperishable; veda-vidaḥ—scholars of the Vedas; vadanti—describe; viśhanti—enter; yat—which; yatayaḥ—great ascetics; vīta-rāgāḥ—free from attachment; yat—which; ichchhantaḥ—desiring; brahmacharyam—celibacy; charanti—practice; tat—that; te—to you; padam—goal; saṅgraheṇa—briefly; pravakṣhye—I shall explain\n', 'https://gita.github.io/gita/data/verse_recitation/8/321.mp3', 8, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (322, 'सर्वद्वाराणि संयम्य मनो हृदि निरुध्य च।\n\nमूर्ध्न्याधायात्मनः प्राणमास्थितो योगधारणाम्।।8.12।।\n ', 'sarva-dvārāṇi sanyamya mano hṛidi nirudhya cha\nmūrdhnyādhāyātmanaḥ prāṇam āsthito yoga-dhāraṇām\n', 'sarva-dvārāṇi—all gates; sanyamya—restraining; manaḥ—the mind; hṛidi—in the heart region; nirudhya—confining; cha—and; mūrdhni—in the head; ādhāya—establish; ātmanaḥ—of the self; prāṇam—the life breath; āsthitaḥ—situated (in); yoga-dhāraṇām—the yogic concentration\n', 'https://gita.github.io/gita/data/verse_recitation/8/322.mp3', 8, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (323, 'ओमित्येकाक्षरं ब्रह्म व्याहरन्मामनुस्मरन्।\n\nयः प्रयाति त्यजन्देहं स याति परमां गतिम्।।8.13।।\n ', 'oṁ ityekākṣharaṁ brahma vyāharan mām anusmaran\nyaḥ prayāti tyajan dehaṁ sa yāti paramāṁ gatim\n', 'om—sacred syllable representing the formless aspect of God; iti—thus; eka-akṣharam—one syllabled; brahma—the Absolute Truth; vyāharan—chanting; mām—me (Shree Krishna); anusmaran—remembering; yaḥ—who; prayāti—departs; tyajan—quitting; deham—the body; saḥ—he; yāti—attains; paramām—the supreme; gatim—goal\n', 'https://gita.github.io/gita/data/verse_recitation/8/323.mp3', 8, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (324, 'अनन्यचेताः सततं यो मां स्मरति नित्यशः।\n\nतस्याहं सुलभः पार्थ नित्ययुक्तस्य योगिनः।।8.14।।\n ', 'ananya-chetāḥ satataṁ yo māṁ smarati nityaśhaḥ\ntasyāhaṁ sulabhaḥ pārtha nitya-yuktasya yoginaḥ\n', 'ananya-chetāḥ—without deviation of the mind; satatam—always; yaḥ—who; mām—me; smarati—remembers; nityaśhaḥ—regularly; tasya—to him; aham—I; su-labhaḥ—easily attainable; pārtha—Arjun, the son of Pritha; nitya—constantly; yuktasya—engaged; yoginaḥ—of the yogis\n', 'https://gita.github.io/gita/data/verse_recitation/8/324.mp3', 8, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (325, 'मामुपेत्य पुनर्जन्म दुःखालयमशाश्वतम्।\n\nनाप्नुवन्ति महात्मानः संसिद्धिं परमां गताः।।8.15।।\n ', 'mām upetya punar janma duḥkhālayam aśhāśhvatam\nnāpnuvanti mahātmānaḥ sansiddhiṁ paramāṁ gatāḥ\n', 'mām—me; upetya—having attained; punaḥ—again; janma—birth; duḥkha-ālayam—place full of miseries; aśhāśhvatam—temporary; na—never; āpnuvanti—attain; mahā-ātmānaḥ—the great souls; sansiddhim—perfection; paramām—highest; gatāḥ—having achieved\n', 'https://gita.github.io/gita/data/verse_recitation/8/325.mp3', 8, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (326, 'आब्रह्मभुवनाल्लोकाः पुनरावर्तिनोऽर्जुन।\n\nमामुपेत्य तु कौन्तेय पुनर्जन्म न विद्यते।।8.16।।\n ', 'ā-brahma-bhuvanāl lokāḥ punar āvartino ’rjuna\nmām upetya tu kaunteya punar janma na vidyate\n', 'ā-brahma-bhuvanāt—up to the abode of Brahma; lokāḥ—worlds; punaḥ āvartinaḥ—subject to rebirth; arjuna—Arjun; mām—mine; upetya—having attained; tu—but; kaunteya—Arjun, the son of Kunti; punaḥ janma—rebirth; na—never; vidyate—is\n', 'https://gita.github.io/gita/data/verse_recitation/8/326.mp3', 8, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (327, 'सहस्रयुगपर्यन्तमहर्यद्ब्रह्मणो विदुः।\n\nरात्रिं युगसहस्रान्तां तेऽहोरात्रविदो जनाः।।8.17।।\n ', 'sahasra-yuga-paryantam ahar yad brahmaṇo viduḥ\nrātriṁ yuga-sahasrāntāṁ te ’ho-rātra-vido janāḥ\n', 'sahasra—one thousand; yuga—age; paryantam—until; ahaḥ—one day; yat—which; brahmaṇaḥ—of Brahma; viduḥ—know; rātrim—night; yuga-sahasra-antām—lasts one thousand yugas; te—they; ahaḥ-rātra-vidaḥ—those who know his day and night; janāḥ—people\n', 'https://gita.github.io/gita/data/verse_recitation/8/327.mp3', 8, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (328, 'अव्यक्ताद्व्यक्तयः सर्वाः प्रभवन्त्यहरागमे।\n\nरात्र्यागमे प्रलीयन्ते तत्रैवाव्यक्तसंज्ञके।।8.18।।\n ', 'avyaktād vyaktayaḥ sarvāḥ prabhavantyahar-āgame\nrātryāgame pralīyante tatraivāvyakta-sanjñake\n', 'avyaktāt—from the unmanifested; vyaktayaḥ—the manifested; sarvāḥ—all; prabhavanti—emanate; ahaḥ-āgame—at the advent of Brahma’s day; rātri-āgame—at the fall of Brahma’s night; pralīyante—they dissolve; tatra—into that; eva—certainly; avyakta-sanjñake—in that which is called the unmanifest\n', 'https://gita.github.io/gita/data/verse_recitation/8/328.mp3', 8, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (329, 'भूतग्रामः स एवायं भूत्वा भूत्वा प्रलीयते।\n\nरात्र्यागमेऽवशः पार्थ प्रभवत्यहरागमे।।8.19।।\n ', 'bhūta-grāmaḥ sa evāyaṁ bhūtvā bhūtvā pralīyate\nrātryāgame ’vaśhaḥ pārtha prabhavatyahar-āgame\n', 'bhūta-grāmaḥ—the multitude of beings; saḥ—these; eva—certainly; ayam—this; bhūtvā bhūtvā—repeatedly taking birth; pralīyate—dissolves; rātri-āgame—with the advent of night; avaśhaḥ—helpless; pārtha—Arjun, the son of Pritha; prabhavati—become manifest; ahaḥ-āgame—with the advent of day\n', 'https://gita.github.io/gita/data/verse_recitation/8/329.mp3', 8, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (330, 'परस्तस्मात्तु भावोऽन्योऽव्यक्तोऽव्यक्तात्सनातनः।\n\nयः स सर्वेषु भूतेषु नश्यत्सु न विनश्यति।।8.20।।\n ', 'paras tasmāt tu bhāvo ’nyo ’vyakto ’vyaktāt sanātanaḥ\nyaḥ sa sarveṣhu bhūteṣhu naśhyatsu na vinaśhyati\n', 'paraḥ—transcendental; tasmāt—than that; tu—but; bhāvaḥ—creation; anyaḥ—another; avyaktaḥ—unmanifest; avyaktāt—to the unmanifest; sanātanaḥ—eternal; yaḥ—who; saḥ—that; sarveṣhu—all; bhūteṣhu—in beings; naśhyatsu—cease to exist; na—never; vinaśhyati—is annihilated\n', 'https://gita.github.io/gita/data/verse_recitation/8/330.mp3', 8, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (331, 'अव्यक्तोऽक्षर इत्युक्तस्तमाहुः परमां गतिम्।\n\nयं प्राप्य न निवर्तन्ते तद्धाम परमं मम।।8.21।।\n ', 'avyakto ’kṣhara ityuktas tam āhuḥ paramāṁ gatim\nyaṁ prāpya na nivartante tad dhāma paramaṁ mama\n', 'avyaktaḥ—unmanifest; akṣharaḥ—imperishable; iti—thus; uktaḥ—is said; tam—that; āhuḥ—is called; paramām—the supreme; gatim—destination; yam—which; prāpya—having reached; na—never; nivartante—come back; tat—that; dhāma—abode; paramam—the supreme; mama—my\n', 'https://gita.github.io/gita/data/verse_recitation/8/331.mp3', 8, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (332, 'पुरुषः स परः पार्थ भक्त्या लभ्यस्त्वनन्यया।\n\nयस्यान्तःस्थानि भूतानि येन सर्वमिदं ततम्।।8.22।।\n ', 'puruṣhaḥ sa paraḥ pārtha bhaktyā labhyas tvananyayā\nyasyāntaḥ-sthāni bhūtāni yena sarvam idaṁ tatam\n', 'puruṣhaḥ—the Supreme Divine Personality; saḥ—he; paraḥ—greatest; pārtha—Arjun, the son of Pritha; bhaktyā—through devotion; labhyaḥ—is attainable; tu—indeed; ananyayā—without another; yasya—of whom; antaḥ-sthāni—situated within; bhūtāni—beings; yena—by whom; sarvam—all; idam—this; tatam—is pervaded\n', 'https://gita.github.io/gita/data/verse_recitation/8/332.mp3', 8, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (333, 'यत्र काले त्वनावृत्तिमावृत्तिं चैव योगिनः।\n\nप्रयाता यान्ति तं कालं वक्ष्यामि भरतर्षभ।।8.23।।\n ', 'yatra kāle tvanāvṛittim āvṛittiṁ chaiva yoginaḥ\nprayātā yānti taṁ kālaṁ vakṣhyāmi bharatarṣhabha\n', 'yatra—where; kāle—time; tu—certainly; anāvṛittim—no return; āvṛittim—return; cha—and; eva—certainly; yoginaḥ—a yogi; prayātāḥ—having departed; yānti—attain; tam—that; kālam—time; vakṣhyāmi—I shall describe; bharata-ṛiṣhabha—Arjun, the best of the Bharatas;\n', 'https://gita.github.io/gita/data/verse_recitation/8/333.mp3', 8, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (334, 'अग्निर्ज्योतिरहः शुक्लः षण्मासा उत्तरायणम्।\n\nतत्र प्रयाता गच्छन्ति ब्रह्म ब्रह्मविदो जनाः।।8.24।।\n ', 'agnir jyotir ahaḥ śhuklaḥ ṣhaṇ-māsā uttarāyaṇam\ntatra prayātā gachchhanti brahma brahma-vido janāḥ\n', 'agniḥ—fire; jyotiḥ—light; ahaḥ—day; śhuklaḥ—the bright fortnight of the moon; ṣhaṭ-māsāḥ—six months; uttara-ayanam—the sun’s northern course; tatra—there; prayātāḥ—departed; gachchhanti—go; brahma—Brahman; brahma-vidaḥ—those who know the Brahman; janāḥ—persons;\n', 'https://gita.github.io/gita/data/verse_recitation/8/334.mp3', 8, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (335, 'धूमो रात्रिस्तथा कृष्णः षण्मासा दक्षिणायनम्।\n\nतत्र चान्द्रमसं ज्योतिर्योगी प्राप्य निवर्तते।।8.25।।\n ', 'dhūmo rātris tathā kṛiṣhṇaḥ ṣhaṇ-māsā dakṣhiṇāyanam\ntatra chāndramasaṁ jyotir yogī prāpya nivartate\n', 'dhūmaḥ—smoke; rātriḥ—night; tathā—and; kṛiṣhṇaḥ—the dark fortnight of the moon; ṣhaṭ-māsāḥ—six months; dakṣhiṇa-ayanam—the sun’s southern course; tatra—there; chāndra-masam—lunar; jyotiḥ—light; yogī—a yogi; prāpya—attain; nivartate—comes back;\n', 'https://gita.github.io/gita/data/verse_recitation/8/335.mp3', 8, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (336, 'शुक्लकृष्णे गती ह्येते जगतः शाश्वते मते।\n\nएकया यात्यनावृत्तिमन्ययाऽऽवर्तते पुनः।।8.26।।\n ', 'śhukla-kṛiṣhṇe gatī hyete jagataḥ śhāśhvate mate\nekayā yātyanāvṛittim anyayāvartate punaḥ\n', 'śhukla—bright; kṛiṣhṇe—dark; gatī—paths; hi—certainly; ete—these; jagataḥ—of the material world; śhāśhvate—eternal; mate—opinion; ekayā—by one; yāti—goes; anāvṛittim—to non return; anyayā—by the other; āvartate—comes back; punaḥ—again\n', 'https://gita.github.io/gita/data/verse_recitation/8/336.mp3', 8, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (337, 'नैते सृती पार्थ जानन्योगी मुह्यति कश्चन।\n\nतस्मात्सर्वेषु कालेषु योगयुक्तो भवार्जुन।।8.27।।\n ', 'naite sṛitī pārtha jānan yogī muhyati kaśhchana\ntasmāt sarveṣhu kāleṣhu yoga-yukto bhavārjuna\n', 'na—never; ete—these two; sṛitī—paths; pārtha—Arjun, the son of Pritha; jānan—knowing; yogī—a yogi; muhyati—bewildered; kaśhchana—any; tasmāt—therefore; sarveṣhu kāleṣhu—always; yoga-yuktaḥ—situated in Yog; bhava—be; arjuna—Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/8/337.mp3', 8, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (338, 'वेदेषु यज्ञेषु तपःसु चैव\n\nदानेषु यत्पुण्यफलं प्रदिष्टम्।\n\nअत्येति तत्सर्वमिदं विदित्वा\n\nयोगी परं स्थानमुपैति चाद्यम्।।8.28।।\n ', 'vedeṣhu yajñeṣhu tapaḥsu chaiva\ndāneṣhu yat puṇya-phalaṁ pradiṣhṭam\natyeti tat sarvam idaṁ viditvā\nyogī paraṁ sthānam upaiti chādyam\n', 'vedeṣhu—in the study of the Vedas; yajñeṣhu—in performance of sacrifices; tapaḥsu—in austerities; cha—and; eva—certainly; dāneṣhu—in giving charities; yat—which; puṇya-phalam—fruit of merit; pradiṣhṭam—is gained; atyeti—surpasses; tat sarvam—all; idam—this; viditvā—having known; yogī—a yogi; param—Supreme; sthānam—Abode; upaiti—achieves; cha—and; ādyam—original\n', 'https://gita.github.io/gita/data/verse_recitation/8/338.mp3', 8, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (339, 'श्री भगवानुवाच\n\nइदं तु ते गुह्यतमं प्रवक्ष्याम्यनसूयवे।\n\nज्ञानं विज्ञानसहितं यज्ज्ञात्वा मोक्ष्यसेऽशुभात्।।9.1।।\n ', 'śhrī bhagavān uvācha\nidaṁ tu te guhyatamaṁ pravakṣhyāmyanasūyave\njñānaṁ vijñāna-sahitaṁ yaj jñātvā mokṣhyase ’śhubhāt\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; idam—this; tu—but; te—to you; guhya-tamam—the most confidential; pravakṣhyāmi—I shall impart; anasūyave—nonenvious; jñānam—knowledge; vijñāna—realized knowledge; sahitam—with; yat—which; jñātvā—knowing; mokṣhyase—you will be released; aśhubhāt—miseries of material existence\n', 'https://gita.github.io/gita/data/verse_recitation/9/339.mp3', 9, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (340, 'राजविद्या राजगुह्यं पवित्रमिदमुत्तमम्।\n\nप्रत्यक्षावगमं धर्म्यं सुसुखं कर्तुमव्ययम्।।9.2।।\n ', 'rāja-vidyā rāja-guhyaṁ pavitram idam uttamam\npratyakṣhāvagamaṁ dharmyaṁ su-sukhaṁ kartum avyayam\n', 'rāja-vidyā—the king of sciences; rāja-guhyam—the most profound secret; pavitram—pure; idam—this; uttamam—highest; pratyakṣha—directly perceptible; avagamam—directly realizable; dharmyam—virtuous; su-sukham—easy; kartum—to practice; avyayam—everlasting\n', 'https://gita.github.io/gita/data/verse_recitation/9/340.mp3', 9, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (341, 'अश्रद्दधानाः पुरुषा धर्मस्यास्य परन्तप।\n\nअप्राप्य मां निवर्तन्ते मृत्युसंसारवर्त्मनि।।9.3।।\n ', 'aśhraddadhānāḥ puruṣhā dharmasyāsya parantapa\naprāpya māṁ nivartante mṛityu-samsāra-vartmani\n', 'aśhraddadhānāḥ—people without faith; puruṣhāḥ—(such) persons; dharmasya—of dharma; asya—this; parantapa—Arjun, conqueror the enemies; aprāpya—without attaining; mām—me; nivartante—come back; mṛityu—death; samsāra—material existence; vartmani—in the path\n', 'https://gita.github.io/gita/data/verse_recitation/9/341.mp3', 9, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (342, 'मया ततमिदं सर्वं जगदव्यक्तमूर्तिना।\n\nमत्स्थानि सर्वभूतानि न चाहं तेष्ववस्थितः।।9.4।।\n ', 'mayā tatam idaṁ sarvaṁ jagad avyakta-mūrtinā\nmat-sthāni sarva-bhūtāni na chāhaṁ teṣhvavasthitaḥ\n', 'mayā—by me; tatam—pervaded; idam—this; sarvam—entire; jagat—cosmic manifestation; avyakta-mūrtinā—the unmanifested form; mat-sthāni—in me; sarva-bhūtāni—all living beings; na—not; cha—and; aham—I; teṣhu—in them; avasthitaḥ—dwell\n', 'https://gita.github.io/gita/data/verse_recitation/9/342.mp3', 9, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (343, 'न च मत्स्थानि भूतानि पश्य मे योगमैश्वरम्।\n\nभूतभृन्न च भूतस्थो ममात्मा भूतभावनः।।9.5।।\n ', 'na cha mat-sthāni bhūtāni paśhya me yogam aiśhwaram\nbhūta-bhṛin na cha bhūta-stho mamātmā bhūta-bhāvanaḥ\n', 'na—never; cha—and; mat-sthāni—abide in me; bhūtāni—all living beings; paśhya—behold; me—my; yogam aiśhwaram—divine energy; bhūta-bhṛit—the sustainer of all living beings; na—never; cha—yet; bhūta-sthaḥ—dwelling in; mama—my; ātmā—self; bhūta-bhāvanaḥ—the creator of all beings\n', 'https://gita.github.io/gita/data/verse_recitation/9/343.mp3', 9, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (344, 'यथाऽऽकाशस्थितो नित्यं वायुः सर्वत्रगो महान्।\n\nतथा सर्वाणि भूतानि मत्स्थानीत्युपधारय।।9.6।।\n ', 'yathākāśha-sthito nityaṁ vāyuḥ sarvatra-go mahān\ntathā sarvāṇi bhūtāni mat-sthānītyupadhāraya\n', 'yathā—as; ākāśha-sthitaḥ—rests in the sky; nityam—always; vāyuḥ—the wind; sarvatra-gaḥ—blowing everywhere; mahān—mighty; tathā—likewise; sarvāṇi bhūtāni—all living beings; mat-sthāni—rest in me; iti—thus; upadhāraya—know\n', 'https://gita.github.io/gita/data/verse_recitation/9/344.mp3', 9, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (345, 'सर्वभूतानि कौन्तेय प्रकृतिं यान्ति मामिकाम्।\n\nकल्पक्षये पुनस्तानि कल्पादौ विसृजाम्यहम्।।9.7।।\n ', 'sarva-bhūtāni kaunteya prakṛitiṁ yānti māmikām\nkalpa-kṣhaye punas tāni kalpādau visṛijāmyaham\n', 'sarva-bhūtāni—all living beings; kaunteya—Arjun, the son of Kunti; prakṛitim—primordial material energy; yānti—merge; māmikām—my; kalpa-kṣhaye—at the end of a kalpa; punaḥ—again; tāni—them; kalpa-ādau—at the beginning of a kalpa; visṛijāmi—manifest; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/9/345.mp3', 9, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (346, 'प्रकृतिं स्वामवष्टभ्य विसृजामि पुनः पुनः।\n\nभूतग्राममिमं कृत्स्नमवशं प्रकृतेर्वशात्।।9.8।।\n ', 'prakṛitiṁ svām avaṣhṭabhya visṛijāmi punaḥ punaḥ\nbhūta-grāmam imaṁ kṛitsnam avaśhaṁ prakṛiter vaśhāt\n', 'prakṛitim—the material energy; svām—my own; avaṣhṭabhya—presiding over; visṛijāmi—generate; punaḥ punaḥ—again and again; bhūta-grāmam—myriad forms; imam—these; kṛitsnam—all; avaśham—beyond their control; prakṛiteḥ—nature; vaśhāt—force\n', 'https://gita.github.io/gita/data/verse_recitation/9/346.mp3', 9, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (347, 'न च मां तानि कर्माणि निबध्नन्ति धनञ्जय।\n\nउदासीनवदासीनमसक्तं तेषु कर्मसु।।9.9।।\n ', 'na cha māṁ tāni karmāṇi nibadhnanti dhanañjaya\nudāsīna-vad āsīnam asaktaṁ teṣhu karmasu\n', 'na—none; cha—as; mām—me; tāni—those; karmāṇi—actions; nibadhnanti—bind; dhanañjaya—Arjun, conqueror of wealth; udāsīna-vat—as neutral; āsīnam—situated; asaktam—detached; teṣhu—those; karmasu—actions\n', 'https://gita.github.io/gita/data/verse_recitation/9/347.mp3', 9, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (348, 'मयाऽध्यक्षेण प्रकृतिः सूयते सचराचरम्।\n\nहेतुनाऽनेन कौन्तेय जगद्विपरिवर्तते।।9.10।।\n ', 'mayādhyakṣheṇa prakṛitiḥ sūyate sa-charācharam\nhetunānena kaunteya jagad viparivartate\n', 'mayā—by me; adhyakṣheṇa—direction; prakṛitiḥ—material energy; sūyate—brings into being; sa—both; chara-acharam—the animate and the inanimate; hetunā—reason; anena—this; kaunteya—Arjun, the son of Kunti; jagat—the material world; viparivartate—undergoes the changes\n', 'https://gita.github.io/gita/data/verse_recitation/9/348.mp3', 9, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (349, 'अवजानन्ति मां मूढा मानुषीं तनुमाश्रितम्।\n\nपरं भावमजानन्तो मम भूतमहेश्वरम्।।9.11।।\n ', 'avajānanti māṁ mūḍhā mānuṣhīṁ tanum āśhritam\nparaṁ bhāvam ajānanto mama bhūta-maheśhvaram\n', 'avajānanti—disregard; mām—me; mūḍhāḥ—dim-witted; mānuṣhīm—human; tanum—form; āśhritam—take on; param—divine; bhāvam—personality; ajānantaḥ—not knowing; mama—my; bhūta—all beings; mahā-īśhvaram—the Supreme Lord\n', 'https://gita.github.io/gita/data/verse_recitation/9/349.mp3', 9, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (350, 'मोघाशा मोघकर्माणो मोघज्ञाना विचेतसः।\n\nराक्षसीमासुरीं चैव प्रकृतिं मोहिनीं श्रिताः।।9.12।।\n ', 'moghāśhā mogha-karmāṇo mogha-jñānā vichetasaḥ\nrākṣhasīm āsurīṁ chaiva prakṛitiṁ mohinīṁ śhritāḥ\n', 'mogha-āśhāḥ—of vain hopes; mogha-karmāṇaḥ—of vain actions; mogha-jñānāḥ—of baffled knowledge; vichetasaḥ—deluded; rākṣhasīm—demoniac; āsurīm—atheistic; cha—and; eva—certainly; prakṛitim—material energy; mohinīm—bewildered; śhritāḥ—take shelter\n', 'https://gita.github.io/gita/data/verse_recitation/9/350.mp3', 9, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (351, 'महात्मानस्तु मां पार्थ दैवीं प्रकृतिमाश्रिताः।\n\nभजन्त्यनन्यमनसो ज्ञात्वा भूतादिमव्ययम्।।9.13।।\n ', 'mahātmānas tu māṁ pārtha daivīṁ prakṛitim āśhritāḥ\nbhajantyananya-manaso jñātvā bhūtādim avyayam\n', 'mahā-ātmānaḥ—the great souls; tu—but; mām—me; pārtha—Arjun, the son of Pritha; daivīm prakṛitim—divine energy; āśhritāḥ—take shelter of; bhajanti—engage in devotion; ananya-manasaḥ—with mind fixed exclusively; jñātvā—knowing; bhūta—all creation; ādim—the origin; avyayam—imperishable\n', 'https://gita.github.io/gita/data/verse_recitation/9/351.mp3', 9, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (352, 'सततं कीर्तयन्तो मां यतन्तश्च दृढव्रताः।\n\nनमस्यन्तश्च मां भक्त्या नित्ययुक्ता उपासते।।9.14।।\n ', 'satataṁ kīrtayanto māṁ yatantaśh cha dṛiḍha-vratāḥ\nnamasyantaśh cha māṁ bhaktyā nitya-yuktā upāsate\n', 'satatam—always; kīrtayantaḥ—singing divine glories; mām—me; yatantaḥ—striving; cha—and; dṛiḍha-vratāḥ—with great determination; namasyantaḥ—humbly bowing down; cha—and; mām—me; bhaktyā—loving devotion; nitya-yuktāḥ—constantly united; upāsate—worship\n', 'https://gita.github.io/gita/data/verse_recitation/9/352.mp3', 9, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (353, 'ज्ञानयज्ञेन चाप्यन्ये यजन्तो मामुपासते।\n\nएकत्वेन पृथक्त्वेन बहुधा विश्वतोमुखम्।।9.15।।\n ', 'jñāna-yajñena chāpyanye yajanto mām upāsate\nekatvena pṛithaktvena bahudhā viśhvato-mukham\n', 'jñāna-yajñena—yajña of cultivating knowledge; cha—and; api—also; anye—others; yajantaḥ—worship; mām—me; upāsate—worship; ekatvena—undifferentiated oneness; pṛithaktvena—separately; bahudhā—various; viśhwataḥ-mukham—the cosmic form\n', 'https://gita.github.io/gita/data/verse_recitation/9/353.mp3', 9, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (354, 'अहं क्रतुरहं यज्ञः स्वधाऽहमहमौषधम्।\n\nमंत्रोऽहमहमेवाज्यमहमग्निरहं हुतम्।।9.16।।\n ', 'ahaṁ kratur ahaṁ yajñaḥ svadhāham aham auṣhadham\nmantro ’ham aham evājyam aham agnir ahaṁ hutam\n', 'aham—I; kratuḥ—Vedic ritual; aham—I; yajñaḥ—sacrifice; svadhā—oblation; aham—I; aham—I; auṣhadham—medicinal herb; mantraḥ—Vedic mantra; aham—I; aham—I; eva—also; ājyam—clarified butter; aham—I; agniḥ—fire; aham—I; hutam—the act offering;\n', 'https://gita.github.io/gita/data/verse_recitation/9/354.mp3', 9, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (355, 'पिताऽहमस्य जगतो माता धाता पितामहः।\n\nवेद्यं पवित्रमोंकार ऋक् साम यजुरेव च।।9.17।।\n ', 'pitāham asya jagato mātā dhātā pitāmahaḥ\nvedyaṁ pavitram oṁkāra ṛik sāma yajur eva cha\n', 'pitā—Father; aham—I; asya—of this; jagataḥ—universe; mātā—Mother; dhātā—Sustainer; pitāmahaḥ—Grandsire; vedyam—the goal of knowledge; pavitram—the purifier; om-kāra—the sacred syllable Om; ṛik—the Rig Veda; sāma—the Sama Veda; yajuḥ—the Yajur Veda; eva—also; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/9/355.mp3', 9, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (356, 'गतिर्भर्ता प्रभुः साक्षी निवासः शरणं सुहृत्।\n\nप्रभवः प्रलयः स्थानं निधानं बीजमव्ययम्।।9.18।।\n ', 'gatir bhartā prabhuḥ sākṣhī nivāsaḥ śharaṇaṁ suhṛit\nprabhavaḥ pralayaḥ sthānaṁ nidhānaṁ bījam avyayam\n', 'gatiḥ—the supreme goal; bhartā—sustainer; prabhuḥ—master; sākṣhī—witness; nivāsaḥ—abode; śharaṇam—shelter; su-hṛit—friend; prabhavaḥ—the origin; pralayaḥ—dissolution; sthānam—store house; nidhānam—resting place; bījam—seed; avyayam—imperishable\n', 'https://gita.github.io/gita/data/verse_recitation/9/356.mp3', 9, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (357, 'तपाम्यहमहं वर्षं निगृह्णाम्युत्सृजामि च।\n\nअमृतं चैव मृत्युश्च सदसच्चाहमर्जुन।।9.19।।\n ', 'tapāmyaham ahaṁ varṣhaṁ nigṛihṇāmyutsṛijāmi cha\namṛitaṁ chaiva mṛityuśh cha sad asach chāham arjuna\n', 'tapāmi—radiate heat; aham—I; aham—I; varṣham—rain; nigṛihṇāmi—withhold; utsṛijāmi—send forth; cha—and; amṛitam—immortality; cha—and; eva—also; mṛityuḥ—death; cha—and; sat—eternal spirit; asat—temporary matter; cha—and; aham—I; arjuna—Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/9/357.mp3', 9, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (358, 'त्रैविद्या मां सोमपाः पूतपापा\n\nयज्ञैरिष्ट्वा स्वर्गतिं प्रार्थयन्ते।\n\nते पुण्यमासाद्य सुरेन्द्रलोक\n\nमश्नन्ति दिव्यान्दिवि देवभोगान्।।9.20।।\n ', 'trai-vidyā māṁ soma-pāḥ pūta-pāpā\nyajñair iṣhṭvā svar-gatiṁ prārthayante\nte puṇyam āsādya surendra-lokam\naśhnanti divyān divi deva-bhogān\n', 'trai-vidyāḥ—the science of karm kāṇḍ (Vedic Rituals); mām—me; soma-pāḥ—drinkers of the Soma juice; pūta—purified; pāpāḥ—sins; yajñaiḥ—through sacrifices; iṣhṭvā—worship; svaḥ-gatim—way to the abode of the king of heaven; prārthayante—seek; te—they; puṇyam—pious; āsādya—attain; sura-indra—of Indra; lokam—abode; aśhnanti—enjoy; divyān—celestial; divi—in heaven; deva-bhogān—the pleasures of the celestial gods\n', 'https://gita.github.io/gita/data/verse_recitation/9/358.mp3', 9, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (359, 'ते तं भुक्त्वा स्वर्गलोकं विशालं\n\nक्षीणे पुण्ये मर्त्यलोकं विशन्ति।\n\nएव त्रयीधर्ममनुप्रपन्ना\n\nगतागतं कामकामा लभन्ते।।9.21।।\n ', 'te taṁ bhuktvā swarga-lokaṁ viśhālaṁ\nkṣhīṇe puṇye martya-lokaṁ viśhanti\nevaṁ trayī-dharmam anuprapannā\ngatāgataṁ kāma-kāmā labhante\n', 'te—they; tam—that; bhuktvā—having enjoyed; swarga-lokam—heaven; viśhālam—vast; kṣhīṇe—at the exhaustion of; puṇye—stock of merits; martya-lokam—to the earthly plane; viśhanti—return; evam—thus; trayī dharmam—the karm-kāṇḍ portion of the three Vedas; anuprapannāḥ—follow; gata-āgatam—repeated coming and going; kāma-kāmāḥ—desiring objects of enjoyments; labhante—attain\n', 'https://gita.github.io/gita/data/verse_recitation/9/359.mp3', 9, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (360, 'अनन्याश्चिन्तयन्तो मां ये जनाः पर्युपासते।\n\nतेषां नित्याभियुक्तानां योगक्षेमं वहाम्यहम्।।9.22।।', 'ananyāśh chintayanto māṁ ye janāḥ paryupāsate\nteṣhāṁ nityābhiyuktānāṁ yoga-kṣhemaṁ vahāmyaham\n', 'ananyāḥ—always; chintayantaḥ—think of; mām—me; ye—those who; janāḥ—persons; paryupāsate—worship exclusively; teṣhām—of them; nitya abhiyuktānām—who are always absorbed; yoga—supply spiritual assets; kṣhemam—protect spiritual assets; vahāmi—carry; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/9/360.mp3', 9, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (361, 'येऽप्यन्यदेवता भक्ता यजन्ते श्रद्धयाऽन्विताः।\n\nतेऽपि मामेव कौन्तेय यजन्त्यविधिपूर्वकम्।।9.23।।\n ', 'ye ’pyanya-devatā-bhaktā yajante śhraddhayānvitāḥ\nte ’pi mām eva kaunteya yajantyavidhi-pūrvakam\n', 'ye—those who; api—although; anya—other; devatā—celestial gods; bhaktāḥ—devotees; yajante—worship; śhraddhayā anvitāḥ—faithfully; te—they; api—also; mām—me; eva—only; kaunteya—Arjun, the son of Kunti; yajanti—worship; avidhi-pūrvakam—by the wrong method\n', 'https://gita.github.io/gita/data/verse_recitation/9/361.mp3', 9, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (362, 'अहं हि सर्वयज्ञानां भोक्ता च प्रभुरेव च।\n\nन तु मामभिजानन्ति तत्त्वेनातश्च्यवन्ति ते।।9.24।।\n ', 'ahaṁ hi sarva-yajñānāṁ bhoktā cha prabhureva cha\nna tu mām abhijānanti tattvenātaśh chyavanti te\n', 'aham—I; hi—verily; sarva—of all; yajñānām—sacrifices; bhoktā—the enjoyer; cha—and; prabhuḥ—the Lord; eva—only; cha—and; na—not; tu—but; mām—me; abhijānanti—realize; tattvena—divine nature; ataḥ—therefore; chyavanti—fall down (wander in samsara); te—they\n', 'https://gita.github.io/gita/data/verse_recitation/9/362.mp3', 9, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (363, 'यान्ति देवव्रता देवान् पितृ़न्यान्ति पितृव्रताः।\n\nभूतानि यान्ति भूतेज्या यान्ति मद्याजिनोऽपि माम्।।9.25।।\n ', 'yānti deva-vratā devān pitṝīn yānti pitṛi-vratāḥ\nbhūtāni yānti bhūtejyā yānti mad-yājino ’pi mām\n', 'yānti—go; deva-vratāḥ—worshipers of celestial gods; devān—amongst the celestial gods; pitṝīn—to the ancestors; yānti—go; pitṛi-vratā—worshippers of ancestors; bhūtāni—to the ghosts; yānti—go; bhūta-ijyāḥ—worshippers of ghosts; yānti—go; mat—my; yājinaḥ—devotees; api—and; mām—to me\n', 'https://gita.github.io/gita/data/verse_recitation/9/363.mp3', 9, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (364, 'पत्रं पुष्पं फलं तोयं यो मे भक्त्या प्रयच्छति।\n\nतदहं भक्त्युपहृतमश्नामि प्रयतात्मनः।।9.26।।\n ', 'patraṁ puṣhpaṁ phalaṁ toyaṁ yo me bhaktyā prayachchhati\ntadahaṁ bhaktyupahṛitam aśhnāmi prayatātmanaḥ\n', 'patram—a leaf; puṣhpam—a flower; phalam—a fruit; toyam—water; yaḥ—who; me—to me; bhaktyā—with devotion; prayachchhati—offers; tat—that; aham—I; bhakti-upahṛitam—offered with devotion; aśhnāmi—partake; prayata-ātmanaḥ—one in pure consciousness\n', 'https://gita.github.io/gita/data/verse_recitation/9/364.mp3', 9, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (365, 'यत्करोषि यदश्नासि यज्जुहोषि ददासि यत्।\n\nयत्तपस्यसि कौन्तेय तत्कुरुष्व मदर्पणम्।।9.27।।\n ', 'yat karoṣhi yad aśhnāsi yaj juhoṣhi dadāsi yat\nyat tapasyasi kaunteya tat kuruṣhva mad-arpaṇam\n', 'yat—whatever; karoṣhi—you do; yat—whatever; aśhnāsi—you eat; yat—whatever; juhoṣhi—offer to the sacred fire; dadāsi—bestow as a gift; yat—whatever; yat—whatever; tapasyasi—austerities you perform; kaunteya—Arjun, the son of Kunti; tat—them; kuruṣhva—do; mad arpaṇam—as an offering to me\n', 'https://gita.github.io/gita/data/verse_recitation/9/365.mp3', 9, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (366, 'शुभाशुभफलैरेवं मोक्ष्यसे कर्मबन्धनैः।\n\nसंन्यासयोगयुक्तात्मा विमुक्तो मामुपैष्यसि।।9.28।।\n ', 'śhubhāśhubha-phalair evaṁ mokṣhyase karma-bandhanaiḥ\nsannyāsa-yoga-yuktātmā vimukto mām upaiṣhyasi\n', 'śhubha aśhubha phalaiḥ—from good and bad results; evam—thus; mokṣhyase—you shall be freed; karma—work; bandhanaiḥ—from the bondage; sanyāsa-yoga—renunciation of selfishness; yukta-ātmā—having the mind attached to me; vimuktaḥ—liberated; mām—to me; upaiṣhyasi—you shall reach\n', 'https://gita.github.io/gita/data/verse_recitation/9/366.mp3', 9, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (367, 'समोऽहं सर्वभूतेषु न मे द्वेष्योऽस्ति न प्रियः।\n\nये भजन्ति तु मां भक्त्या मयि ते तेषु चाप्यहम्।।9.29।।\n ', 'samo ’haṁ sarva-bhūteṣhu na me dveṣhyo ’sti na priyaḥ\nye bhajanti tu māṁ bhaktyā mayi te teṣhu chāpyaham\n', 'samaḥ—equally disposed; aham—I; sarva-bhūteṣhu—to all living beings; na—no one; me—to me; dveṣhyaḥ—inimical; asti—is; na—not; priyaḥ—dear; ye—who; bhajanti—worship with love; tu—but; mām—me; bhaktyā—with devotion; mayi—reside in me; te—such persons; teṣhu—in them; cha—and; api—also; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/9/367.mp3', 9, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (368, 'अपि चेत्सुदुराचारो भजते मामनन्यभाक्।\n\nसाधुरेव स मन्तव्यः सम्यग्व्यवसितो हि सः।।9.30।।\n ', 'api chet su-durāchāro bhajate mām ananya-bhāk\nsādhur eva sa mantavyaḥ samyag vyavasito hi saḥ\n', 'api—even; chet—if; su-durāchāraḥ—the vilest sinners; bhajate—worship; mām—me; ananya-bhāk—exclusive devotion; sādhuḥ—righteous; eva—certainly; saḥ—that person; mantavyaḥ—is to be considered; samyak—properly; vyavasitaḥ—resolve; hi—certainly; saḥ—that person\n', 'https://gita.github.io/gita/data/verse_recitation/9/368.mp3', 9, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (369, 'क्षिप्रं भवति धर्मात्मा शश्वच्छान्तिं निगच्छति।\n\nकौन्तेय प्रतिजानीहि न मे भक्तः प्रणश्यति।।9.31।।\n ', 'kṣhipraṁ bhavati dharmātmā śhaśhvach-chhāntiṁ nigachchhati\nkaunteya pratijānīhi na me bhaktaḥ praṇaśhyati\n', 'kṣhipram—quickly; bhavati—become; dharma-ātmā—virtuous; śhaśhvat-śhāntim—lasting peace; nigachchhati—attain; kaunteya—Arjun, the son of Kunti; pratijānīhi—declare; na—never; me—my; bhaktaḥ—devotee; praṇaśhyati—perishes\n', 'https://gita.github.io/gita/data/verse_recitation/9/369.mp3', 9, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (370, 'मां हि पार्थ व्यपाश्रित्य येऽपि स्युः पापयोनयः।\n\nस्त्रियो वैश्यास्तथा शूद्रास्तेऽपि यान्ति परां गतिम्।।9.32।।\n ', 'māṁ hi pārtha vyapāśhritya ye ’pi syuḥ pāpa-yonayaḥ\nstriyo vaiśhyās tathā śhūdrās te ’pi yānti parāṁ gatim\n', 'mām—in me; hi—certainly; pārtha—Arjun, the son of Pritha; vyapāśhritya—take refuge; ye—who; api—even; syuḥ—may be; pāpa yonayaḥ—of low birth; striyaḥ—women; vaiśhyāḥ—mercantile people; tathā—and; śhūdrāḥ—manual workers; te api—even they; yānti—go; parām—the supreme; gatim—destination\n', 'https://gita.github.io/gita/data/verse_recitation/9/370.mp3', 9, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (371, 'किं पुनर्ब्राह्मणाः पुण्या भक्ता राजर्षयस्तथा।\n\nअनित्यमसुखं लोकमिमं प्राप्य भजस्व माम्।।9.33।।\n ', 'kiṁ punar brāhmaṇāḥ puṇyā bhaktā rājarṣhayas tathā\nanityam asukhaṁ lokam imaṁ prāpya bhajasva mām\n', 'kim—what; punaḥ—then; brāhmaṇāḥ—sages; puṇyāḥ—meritorius; bhaktāḥ—devotees; rāja-ṛiṣhayaḥ—saintly kings; tathā—and; anityam—transient; asukham—joyless; lokam—world; imam—this; prāpya—having achieved; bhajasva—engage in devotion; mām—unto me\n', 'https://gita.github.io/gita/data/verse_recitation/9/371.mp3', 9, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (372, 'मन्मना भव मद्भक्तो मद्याजी मां नमस्कुरु।\n\nमामेवैष्यसि युक्त्वैवमात्मानं मत्परायणः।।9.34।।\n ', 'man-manā bhava mad-bhakto mad-yājī māṁ namaskuru\nmām evaiṣhyasi yuktvaivam ātmānaṁ mat-parāyaṇaḥ\n', 'mat-manāḥ—always think of me; bhava—be; mat—my; bhaktaḥ—devotee; mat—my; yājī—worshipper; mām—to me; namaskuru—offer obeisances; mām—to me; eva—certainly; eṣhyasi—you will come; yuktvā—united with me; evam—thus; ātmānam—your mind and body; mat-parāyaṇaḥ—having dedicated to me\n', 'https://gita.github.io/gita/data/verse_recitation/9/372.mp3', 9, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (373, 'श्री भगवानुवाच\n\nभूय एव महाबाहो श्रृणु मे परमं वचः।\n\nयत्तेऽहं प्रीयमाणाय वक्ष्यामि हितकाम्यया।।10.1।।\n ', 'śhrī bhagavān uvācha\nbhūya eva mahā-bāho śhṛiṇu me paramaṁ vachaḥ\nyatte ’haṁ prīyamāṇāya vakṣhyāmi hita-kāmyayā\n', 'śhrī-bhagavān uvācha—the Blessed Lord said; bhūyaḥ—again; eva—verily; mahā-bāho—mighty armed one; śhṛiṇu—hear; me—my; paramam—divine; vachaḥ—teachings; yat—which; te—to you; aham—I; prīyamāṇāya—you are my beloved confidant; vakṣhyāmi—say; hita-kāmyayā—for desiring your welfare\n', 'https://gita.github.io/gita/data/verse_recitation/10/373.mp3', 10, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (374, 'न मे विदुः सुरगणाः प्रभवं न महर्षयः।\n\nअहमादिर्हि देवानां महर्षीणां च सर्वशः।।10.2।।\n ', 'na me viduḥ sura-gaṇāḥ prabhavaṁ na maharṣhayaḥ\naham ādir hi devānāṁ maharṣhīṇāṁ cha sarvaśhaḥ\n', 'na—neither; me—my; viduḥ—know; sura-gaṇāḥ—the celestial gods; prabhavam—origin; na—nor; mahā-ṛiṣhayaḥ—the great sages; aham—I; ādiḥ—the source; hi—certainly; devānām—of the celestial gods; mahā-ṛiṣhīṇām—of the great seers; cha—also; sarvaśhaḥ—in every way\n', 'https://gita.github.io/gita/data/verse_recitation/10/374.mp3', 10, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (375, 'यो मामजमनादिं च वेत्ति लोकमहेश्वरम्।\n\nअसम्मूढः स मर्त्येषु सर्वपापैः प्रमुच्यते।।10.3।।\n ', 'yo māmajam anādiṁ cha vetti loka-maheśhvaram\nasammūḍhaḥ sa martyeṣhu sarva-pāpaiḥ pramuchyate\n', 'verseyaḥ—who; mām—me; ajam—unborn; anādim—beginningless; cha—and; vetti—know; loka—of the universe; mahā-īśhvaram—the Supreme Lord; asammūḍhaḥ—undeluded; saḥ—they; martyeṣhu—among mortals; sarva-pāpaiḥ—from all evils; pramuchyate—are freed from-3\n', 'https://gita.github.io/gita/data/verse_recitation/10/375.mp3', 10, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (376, 'बुद्धिर्ज्ञानमसंमोहः क्षमा सत्यं दमः शमः।\n\nसुखं दुःखं भवोऽभावो भयं चाभयमेव च।।10.4।।\n ', 'buddhir jñānam asammohaḥ kṣhamā satyaṁ damaḥ śhamaḥ\nsukhaṁ duḥkhaṁ bhavo ’bhāvo bhayaṁ chābhayameva cha\n', 'buddhiḥ—intellect; jñānam—knowledge; asammohaḥ—clarity of thought; kṣhamā—forgiveness; satyam—truthfulness; damaḥ—control over the senses; śhamaḥ—control of the mind; sukham—joy; duḥkham—sorrow; bhavaḥ—birth; abhāvaḥ—death; bhayam—fear; cha—and; abhayam—courage; eva—certainly; cha—and;\n', 'https://gita.github.io/gita/data/verse_recitation/10/376.mp3', 10, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (377, 'अहिंसा समता तुष्टिस्तपो दानं यशोऽयशः।\n\nभवन्ति भावा भूतानां मत्त एव पृथग्विधाः।।10.5।।\n ', 'ahiṁsā   samatā  tuṣṭis tapo dānaṁ   yaśo    \'yaśaḥ  bhavanti    bhāvā   bhūtānāṁ    matta   eva pṛthag-vidhāḥ', 'ahiṁsā—nonviolence;    samatā—equilibrium; tuṣṭiḥ—satisfaction;    tapaḥ—penance;  dānam—charity;  yaśaḥ—fame; ayaśaḥ—infamy;  bhavanti—become;    bhāvāḥ—natures;     bhūtānām—of living  entities;   mattaḥ—from Me; eva—certainly;  pṛthakvidhāḥ—differently    arranged.', 'https://gita.github.io/gita/data/verse_recitation/10/377.mp3', 10, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (378, 'महर्षयः सप्त पूर्वे चत्वारो मनवस्तथा।\n\nमद्भावा मानसा जाता येषां लोक इमाः प्रजाः।।10.6।।\n ', 'maharṣhayaḥ sapta pūrve chatvāro manavas tathā\nmad-bhāvā mānasā jātā yeṣhāṁ loka imāḥ prajāḥ\n', 'mahā-ṛiṣhayaḥ—the great Sages; sapta—seven; pūrve—before; chatvāraḥ—four; manavaḥ—Manus; tathā—also; mat bhāvāḥ—are born from me; mānasāḥ—mind; jātāḥ—born; yeṣhām—from them; loke—in the world; imāḥ—all these; prajāḥ—people\n', 'https://gita.github.io/gita/data/verse_recitation/10/378.mp3', 10, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (379, 'एतां विभूतिं योगं च मम यो वेत्ति तत्त्वतः।\n\nसोऽविकम्पेन योगेन युज्यते नात्र संशयः।।10.7।।\n ', 'etāṁ vibhūtiṁ yogaṁ cha mama yo vetti tattvataḥ\nso ’vikampena yogena yujyate nātra sanśhayaḥ\n', 'etām—these; vibhūtim—glories; yogam—divine powers; cha—and; mama—my; yaḥ—those who; vetti—know; tattvataḥ—in truth; saḥ—they; avikalpena—unwavering; yogena—in bhakti yog; yujyate—becomes united; na—never; atra—here; sanśhayaḥ—doubt\n', 'https://gita.github.io/gita/data/verse_recitation/10/379.mp3', 10, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (380, 'अहं सर्वस्य प्रभवो मत्तः सर्वं प्रवर्तते।\n\nइति मत्वा भजन्ते मां बुधा भावसमन्विताः।।10.8।।\n ', 'ahaṁ sarvasya prabhavo mattaḥ sarvaṁ pravartate\niti matvā bhajante māṁ budhā bhāva-samanvitāḥ\n', 'aham—I; sarvasya—of all creation; prabhavaḥ—the origin of; mattaḥ—from me; sarvam—everything; pravartate—proceeds; iti—thus; matvā—having known; bhajante—worship; mām—me; budhāḥ—the wise; bhāva-samanvitāḥ—endowed with great faith and devotion\n', 'https://gita.github.io/gita/data/verse_recitation/10/380.mp3', 10, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (381, 'मच्चित्ता मद्गतप्राणा बोधयन्तः परस्परम्।\n\nकथयन्तश्च मां नित्यं तुष्यन्ति च रमन्ति च।।10.9।।\n ', 'mach-chittā mad-gata-prāṇā bodhayantaḥ parasparam\nkathayantaśh cha māṁ nityaṁ tuṣhyanti cha ramanti cha\n', 'mat-chittāḥ—those with minds fixed on me; mat-gata-prāṇāḥ—those who have surrendered their lives to me; bodhayantaḥ—enlightening (with divine knowledge of God); parasparam—one another; kathayantaḥ—speaking; cha—and; mām—about me; nityam—continously; tuṣhyanti—satisfaction; cha—and; ramanti—(they) delight; cha—also\n', 'https://gita.github.io/gita/data/verse_recitation/10/381.mp3', 10, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (382, 'तेषां सततयुक्तानां भजतां प्रीतिपूर्वकम्।\n\nददामि बुद्धियोगं तं येन मामुपयान्ति ते।।10.10।।\n ', 'teṣhāṁ satata-yuktānāṁ bhajatāṁ prīti-pūrvakam\ndadāmi buddhi-yogaṁ taṁ yena mām upayānti te\n', 'teṣhām—to them; satata-yuktānām—ever steadfast; bhajatām—who engage in devotion; prīti-pūrvakam—with love; dadāmi—I give; buddhi-yogam—divine knowledge; tam—that; yena—by which; mām—to me; upayānti—come; te—they\n', 'https://gita.github.io/gita/data/verse_recitation/10/382.mp3', 10, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (383, 'तेषामेवानुकम्पार्थमहमज्ञानजं तमः।\n\nनाशयाम्यात्मभावस्थो ज्ञानदीपेन भास्वता।।10.11।।\n ', 'teṣhām evānukampārtham aham ajñāna-jaṁ tamaḥ\nnāśhayāmyātma-bhāva-stho jñāna-dīpena bhāsvatā\n', 'teṣhām—for them; eva—only; anukampā-artham—out of compassion; aham—I; ajñāna-jam—born of ignorance; tamaḥ—darkness; nāśhayāmi—destroy; ātma-bhāva—within their hearts; sthaḥ—dwelling; jñāna—of knowledge; dīpena—with the lamp; bhāsvatā—luminous\n', 'https://gita.github.io/gita/data/verse_recitation/10/383.mp3', 10, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (384, 'अर्जुन उवाच\n\nपरं ब्रह्म परं धाम पवित्रं परमं भवान्।\n\nपुरुषं शाश्वतं दिव्यमादिदेवमजं विभुम्।।10.12।।\n ', 'arjuna uvācha\nparaṁ brahma paraṁ dhāma pavitraṁ paramaṁ bhavān\npuruṣhaṁ śhāśhvataṁ divyam ādi-devam ajaṁ vibhum\n', 'arjunaḥ uvācha—Arjun said; param—Supreme; brahma—Brahman; param—Supreme; dhāma—Abode; pavitram—purifier; paramam—Supreme; bhavān—you; puruṣham—personality; śhāśhvatam—Eternal; divyam—Divine; ādi-devam—the Primal Being; ajam—the Unborn; vibhum—the Great;\n', 'https://gita.github.io/gita/data/verse_recitation/10/384.mp3', 10, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (385, 'आहुस्त्वामृषयः सर्वे देवर्षिर्नारदस्तथा।\n\nअसितो देवलो व्यासः स्वयं चैव ब्रवीषि मे।।10.13।।\n ', 'āhus tvām ṛiṣhayaḥ sarve devarṣhir nāradas tathā\nasito devalo vyāsaḥ svayaṁ chaiva bravīṣhi me\n', 'āhuḥ—(they) declare; tvām—you; ṛiṣhayaḥ—sages; sarve—all; deva-ṛiṣhiḥ-nāradaḥ—devarṣhi Narad; tathā—also; asitaḥ—Asit; devalaḥ—Deval; vyāsaḥ—Vyās; svayam—personally; cha—and; eva—even; bravīṣhī—you are declaring; me—to me\n', 'https://gita.github.io/gita/data/verse_recitation/10/385.mp3', 10, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (386, 'सर्वमेतदृतं मन्ये यन्मां वदसि केशव।\n\nन हि ते भगवन् व्यक्ितं विदुर्देवा न दानवाः।।10.14।।\n ', 'sarvam etad ṛitaṁ manye yan māṁ vadasi keśhava\nna hi te bhagavan vyaktiṁ vidur devā na dānavāḥ\n', 'sarvam—everything; etat—this; ṛitam—truth; manye—I accept; yat—which; mām—me; vadasi—you tell; keśhava—Shree Krishna, the killer of the demon named Keshi; na—neither; hi—verily; te—your; bhagavan—the Supreme Lord; vyaktim—personality; viduḥ—can understand; devāḥ—the celestial gods; na—nor; dānavāḥ—the demons\n', 'https://gita.github.io/gita/data/verse_recitation/10/386.mp3', 10, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (387, 'स्वयमेवात्मनाऽत्मानं वेत्थ त्वं पुरुषोत्तम।\n\nभूतभावन भूतेश देवदेव जगत्पते।।10.15।।\n ', 'swayam evātmanātmānaṁ vettha tvaṁ puruṣhottama\nbhūta-bhāvana bhūteśha deva-deva jagat-pate\n', 'swayam—yourself; eva—indeed; ātmanā—by yourself; ātmānam—yourself; vettha—know; tvam—you; puruṣha-uttama—the Supreme Personality; bhūta-bhāvana—the Creator of all beings; bhūta-īśha—the Lord of everything; deva-deva—the God of gods; jagat-pate—the Lord of the universe\n', 'https://gita.github.io/gita/data/verse_recitation/10/387.mp3', 10, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (388, 'वक्तुमर्हस्यशेषेण दिव्या ह्यात्मविभूतयः।\n\nयाभिर्विभूतिभिर्लोकानिमांस्त्वं व्याप्य तिष्ठसि।।10.16।।\n ', 'vaktum arhasyaśheṣheṇa divyā hyātma-vibhūtayaḥ\nyābhir vibhūtibhir lokān imāṁs tvaṁ vyāpya tiṣhṭhasi\n', 'vaktum—to describe; arhasi—please do; aśheṣheṇa—completely; divyāḥ—divine; hi—indeed; ātma—your own; vibhūtayaḥ—opulences; yābhiḥ—by which; vibhūtibhiḥ—opulences; lokān—all worlds; imān—these; tvam—you; vyāpya—pervade; tiṣhṭhasi—reside;\n', 'https://gita.github.io/gita/data/verse_recitation/10/388.mp3', 10, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (389, 'कथं विद्यामहं योगिंस्त्वां सदा परिचिन्तयन्।\n\nकेषु केषु च भावेषु चिन्त्योऽसि भगवन्मया।।10.17।।\n ', 'kathaṁ vidyām ahaṁ yogins tvāṁ sadā parichintayan\nkeṣhu keṣhu cha bhāveṣhu chintyo ’si bhagavan mayā\n', 'katham—how; vidyām aham—shall I know; yogin—the Supreme Master of Yogmaya; tvām—you; sadā—always; parichintayan—meditating; keṣhu—in what; keṣhu—in what; cha—and; bhāveṣhu—forms; chintyaḥ asi—to be thought of; bhagavan—the Supreme Divine Personality; mayā—by me\n', 'https://gita.github.io/gita/data/verse_recitation/10/389.mp3', 10, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (390, 'विस्तरेणात्मनो योगं विभूतिं च जनार्दन।\n\nभूयः कथय तृप्तिर्हि श्रृण्वतो नास्ति मेऽमृतम्।।10.18।।\n ', 'vistareṇātmano yogaṁ vibhūtiṁ cha janārdana\nbhūyaḥ kathaya tṛiptir hi śhṛiṇvato nāsti me ’mṛitam\n', 'vistareṇa—in detail; ātmanaḥ—your; yogam—divine glories; vibhūtim—opulences; cha—also; janaārdana—Shree Krishna, he who looks after the public; bhūyaḥ—again; kathaya—describe; tṛiptiḥ—satisfaction; hi—because; śhṛiṇvataḥ—hearing; na—not; asti—is; me—my; amṛitam—nectar\n', 'https://gita.github.io/gita/data/verse_recitation/10/390.mp3', 10, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (391, 'श्री भगवानुवाच\n\nहन्त ते कथयिष्यामि दिव्या ह्यात्मविभूतयः।\n\nप्राधान्यतः कुरुश्रेष्ठ नास्त्यन्तो विस्तरस्य मे।।10.19।।\n ', 'śhrī bhagavān uvācha\nhanta te kathayiṣhyāmi divyā hyātma-vibhūtayaḥ\nprādhānyataḥ kuru-śhreṣhṭha nāstyanto vistarasya me\n', 'śhrī-bhagavān uvācha—the Blessed Lord spoke; hanta—yes; te—to you; kathayiṣhyāmi—I shall describe; divyāḥ—divine; hi—certainly; ātma-vibhūtayaḥ—my divine glories; prādhānyataḥ—salient; kuru-śhreṣhṭha—best of the Kurus; na—not; asti—is; antaḥ—limit; vistarasya—extensive glories; me—my\n', 'https://gita.github.io/gita/data/verse_recitation/10/391.mp3', 10, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (392, 'अहमात्मा गुडाकेश सर्वभूताशयस्थितः।\n\nअहमादिश्च मध्यं च भूतानामन्त एव च।।10.20।।\n ', 'aham ātmā guḍākeśha sarva-bhūtāśhaya-sthitaḥ\naham ādiśh cha madhyaṁ cha bhūtānām anta eva cha\n', 'aham—I; ātmā—soul; guḍākeśha—Arjun, the conqueror of sleep; sarva-bhūta—of all living entities; āśhaya-sthitaḥ—seated in the heart; aham—I; ādiḥ—the beginning; cha—and; madhyam—middle; cha—and; bhūtānām—of all beings; antaḥ—end; eva—even; cha—also\n', 'https://gita.github.io/gita/data/verse_recitation/10/392.mp3', 10, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (393, 'आदित्यानामहं विष्णुर्ज्योतिषां रविरंशुमान्।\n\nमरीचिर्मरुतामस्मि नक्षत्राणामहं शशी।।10.21।।\n ', 'ādityānām ahaṁ viṣhṇur jyotiṣhāṁ ravir anśhumān\nmarīchir marutām asmi nakṣhatrāṇām ahaṁ śhaśhī\n', 'ādityānām—amongst the twelve sons of Aditi; aham—I; viṣhṇuḥ—Lord Vishnu; jyotiṣhām—amongst luminous objects; raviḥ—the sun; anśhu-mān—radiant; marīchiḥ—Marichi; marutām—of the Maruts; asmi—(I) am; nakṣhatrāṇām—amongst the stars; aham—I; śhaśhī—the moon\n', 'https://gita.github.io/gita/data/verse_recitation/10/393.mp3', 10, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (394, 'वेदानां सामवेदोऽस्मि देवानामस्मि वासवः।\n\nइन्द्रियाणां मनश्चास्मि भूतानामस्मि चेतना।।10.22।।\n ', 'vedānāṁ sāma-vedo ’smi devānām asmi vāsavaḥ\nindriyāṇāṁ manaśh chāsmi bhūtānām asmi chetanā\n', 'vedānām—amongst the Vedas; sāma-vedaḥ—the Sāma Veda; asmi—I am; devānām—of all the celestial gods; asmi—I am; vāsavaḥ̣—Indra; indriyāṇām—of amongst the senses; manaḥ—the mind; ca—and; asmi—I am; bhūtānām—amongst the living beings; asmi—I am; chetanā—consciousness\n', 'https://gita.github.io/gita/data/verse_recitation/10/394.mp3', 10, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (395, 'रुद्राणां शङ्करश्चास्मि वित्तेशो यक्षरक्षसाम्।\n\nवसूनां पावकश्चास्मि मेरुः शिखरिणामहम्।।10.23।।\n ', 'rudrāṇāṁ śhaṅkaraśh chāsmi vitteśho yakṣha-rakṣhasām\nvasūnāṁ pāvakaśh chāsmi meruḥ śhikhariṇām aham\n', 'rudrāṇām—amongst the Rudras; śhaṅkaraḥ—Lord Shiv; cha—and; asmi—I am; vitta-īśhaḥ—the god of wealth and the treasurer of the celestial gods; yakṣha—amongst the semi-divine demons; rakṣhasām—amongst the demons; vasūnām—amongst the Vasus; pāvakaḥ—Agni (fire); cha—and; asmi—I am; meruḥ—Mount Meru; śhikhariṇām—amongst the mountains; aham—I am\n', 'https://gita.github.io/gita/data/verse_recitation/10/395.mp3', 10, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (396, 'पुरोधसां च मुख्यं मां विद्धि पार्थ बृहस्पतिम्।\n\nसेनानीनामहं स्कन्दः सरसामस्मि सागरः।।10.24।।\n ', 'purodhasāṁ cha mukhyaṁ māṁ viddhi pārtha bṛihaspatim\nsenānīnām ahaṁ skandaḥ sarasām asmi sāgaraḥ\n', 'purodhasām—amongst priests; cha—and; mukhyam—the chiefs; mām—me; viddhi—know; pārtha—Arjun, the son of Pritha; bṛihaspatim—Brihaspati; senānīnām—warrior chief; aham—I; skandaḥ—Kartikeya; sarasām—amongst reservoirs of water; asmi—I am; sāgaraḥ—the ocean\n', 'https://gita.github.io/gita/data/verse_recitation/10/396.mp3', 10, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (397, 'महर्षीणां भृगुरहं गिरामस्म्येकमक्षरम्।\n\nयज्ञानां जपयज्ञोऽस्मि स्थावराणां हिमालयः।।10.25।।\n ', 'maharṣhīṇāṁ bhṛigur ahaṁ girām asmyekam akṣharam\nyajñānāṁ japa-yajño ’smi sthāvarāṇāṁ himālayaḥ\n', 'mahā-ṛiṣhīṇām—among the great seers; bhṛiguḥ—Bhrigu; aham—I; girām—amongst chants; asmi—I am; ekam akṣharam—the syllable Om; yajñānām—of sacrifices; japa-yajñaḥ—sacrifice of the devotional repetition of the divine names of God; asmi—I am; sthāvarāṇām—amongst immovable things; himālayaḥ—the Himalayas\n', 'https://gita.github.io/gita/data/verse_recitation/10/397.mp3', 10, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (398, 'अश्वत्थः सर्ववृक्षाणां देवर्षीणां च नारदः।\n\nगन्धर्वाणां चित्ररथः सिद्धानां कपिलो मुनिः।।10.26।।\n ', 'aśhvatthaḥ sarva-vṛikṣhāṇāṁ devarṣhīṇāṁ cha nāradaḥ\ngandharvāṇāṁ chitrarathaḥ siddhānāṁ kapilo muniḥ\n', 'aśhvatthaḥ—the banyan tree; sarva-vṛikṣhāṇām—amongst all trees; deva-ṛiṣhīṇām—amongst celestial sages; cha—and; nāradaḥ—Narad; gandharvāṇām—amongst the gandharvas; chitrarathaḥ—Chitrarath; siddhānām—of all those who are perfected; kapilaḥ muniḥ—sage Kapil\n', 'https://gita.github.io/gita/data/verse_recitation/10/398.mp3', 10, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (399, 'उच्चैःश्रवसमश्वानां विद्धि माममृतोद्भवम्।\n\nऐरावतं गजेन्द्राणां नराणां च नराधिपम्।।10.27।।\n ', 'uchchaiḥśhravasam aśhvānāṁ viddhi mām amṛitodbhavam\nairāvataṁ gajendrāṇāṁ narāṇāṁ cha narādhipam\n', 'uchchaiḥśhravasam—Uchchaihshrava; aśhvānām—amongst horses; viddhi—know; mām—me; amṛita-udbhavam—begotten from the churning of the ocean of nectar; airāvatam—Airavata; gaja-indrāṇām—amongst all lordly elephants; narāṇām—amongst humans; cha—and; nara-adhipam—the king\n', 'https://gita.github.io/gita/data/verse_recitation/10/399.mp3', 10, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (400, 'आयुधानामहं वज्रं धेनूनामस्मि कामधुक्।\n\nप्रजनश्चास्मि कन्दर्पः सर्पाणामस्मि वासुकिः।।10.28।।\n ', 'āyudhānām ahaṁ vajraṁ dhenūnām asmi kāmadhuk\nprajanaśh chāsmi kandarpaḥ sarpāṇām asmi vāsukiḥ\n', 'āyudhānām—amongst weapons; aham—I; vajram—the Vajra (thunderbolt); dhenūnām—amongst cows; asmi—I am; kāma-dhuk—Kamdhenu; prajanaḥ—amongst causes for procreation; cha—and; asmi—I am; kandarpaḥ—Kaamdev, the god of love; sarpāṇām—amongst serpents; asmi—I am; vāsukiḥ—serpent Vasuki\n', 'https://gita.github.io/gita/data/verse_recitation/10/400.mp3', 10, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (401, 'अनन्तश्चास्मि नागानां वरुणो यादसामहम्।\n\nपितृ़णामर्यमा चास्मि यमः संयमतामहम्।।10.29।।\n ', 'anantaśh chāsmi nāgānāṁ varuṇo yādasām aham\npitṝīṇām aryamā chāsmi yamaḥ sanyamatām aham\n', 'anantaḥ—Anant; cha—and; asmi—I am; nāgānām—amongst snakes; varuṇaḥ—the celestial god of the ocean; yādasām—amongst aquatics; aham—I; pitṝīṇām—amongst the departed ancestors; aryamā—Aryama; cha—and; asmi—am; yamaḥ—the celestial god of death; sanyamatām—amongst dispensers of law; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/10/401.mp3', 10, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (402, 'प्रह्लादश्चास्मि दैत्यानां कालः कलयतामहम्।\n\nमृगाणां च मृगेन्द्रोऽहं वैनतेयश्च पक्षिणाम्।।10.30।।\n ', 'prahlādaśh chāsmi daityānāṁ kālaḥ kalayatām aham\nmṛigāṇāṁ cha mṛigendro ’haṁ vainateyaśh cha pakṣhiṇām\n', 'prahlādaḥ—Prahlad; cha—and; asmi—I am; daityānām—of the demons; kālaḥ—time; kalayatām—of all that controls; aham—I; mṛigāṇām—amongst animals; cha—and; mṛiga-indraḥ—the lion; aham—I; vainateyaḥ—Garud; cha—and; pakṣhiṇām—amongst birds\n', 'https://gita.github.io/gita/data/verse_recitation/10/402.mp3', 10, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (403, 'पवनः पवतामस्मि रामः शस्त्रभृतामहम्।\n\nझषाणां मकरश्चास्मि स्रोतसामस्मि जाह्नवी।।10.31।।\n ', 'pavanaḥ pavatām asmi rāmaḥ śhastra-bhṛitām aham\njhaṣhāṇāṁ makaraśh chāsmi srotasām asmi jāhnavī\n', 'pavanaḥ—the wind; pavatām—of all that purifies; asmi—I am; rāmaḥ—Ram; śhastra-bhṛitām—of the carriers of weapons; aham—I am; jhaṣhāṇām—of all acquatics; makaraḥ—crocodile; cha—also; asmi—I am; srotasām—of flowing rivers; asmi—I am; jāhnavī—the Ganges\n', 'https://gita.github.io/gita/data/verse_recitation/10/403.mp3', 10, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (404, 'सर्गाणामादिरन्तश्च मध्यं चैवाहमर्जुन।\n\nअध्यात्मविद्या विद्यानां वादः प्रवदतामहम्।।10.32।।\n ', 'sargāṇām ādir antaśh cha madhyaṁ chaivāham arjuna\nadhyātma-vidyā vidyānāṁ vādaḥ pravadatām aham\n', 'sargāṇām—of all creations; ādiḥ—the beginning; antaḥ—end; cha—and; madhyam—middle; cha—and; eva—indeed; aham—I; arjuna—Arjun; adhyātma-vidyā—science of spirituality; vidyānām—amongst sciences; vādaḥ—the logical conclusion; pravadatām—of debates; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/10/404.mp3', 10, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (405, 'अक्षराणामकारोऽस्मि द्वन्द्वः सामासिकस्य च।\n\nअहमेवाक्षयः कालो धाताऽहं विश्वतोमुखः।।10.33।।\n ', 'अहमेवाक्षय: कालो धाताहं विश्वतोमुख: || 33||\n\nakṣharāṇām a-kāro ’smi dvandvaḥ sāmāsikasya cha\naham evākṣhayaḥ kālo dhātāhaṁ viśhvato-mukhaḥ\n', 'akṣharāṇām—amongst all letters; a-kāraḥ—the beginning letter “A”; asmi—I am; dvandvaḥ—the dual; sāmāsikasya—amongst grammatical compounds; cha—and; aham—I; eva—only; akṣhayaḥ—endless; kālaḥ—time; dhātā—amongst the creators; aham—I; viśhwataḥ-mukhaḥ—Brahma\n', 'https://gita.github.io/gita/data/verse_recitation/10/405.mp3', 10, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (406, 'मृत्युः सर्वहरश्चाहमुद्भवश्च भविष्यताम्।\n\nकीर्तिः श्रीर्वाक्च नारीणां स्मृतिर्मेधा धृतिः क्षमा।।10.34।।\n ', 'mṛityuḥ sarva-haraśh chāham udbhavaśh cha bhaviṣhyatām\nkīrtiḥ śhrīr vāk cha nārīṇāṁ smṛitir medhā dhṛitiḥ kṣhamā\n', 'mṛityuḥ—death; sarva-haraḥ—all-devouring; cha—and; aham—I; udbhavaḥ—the origin; cha—and; bhaviṣhyatām—those things that are yet to be; kīrtiḥ—fame; śhrīḥ—prospective; vāk—fine speech; cha—and; nārīṇām—amongst feminine qualities; smṛitiḥ—memory; medhā—intelligence; dhṛitiḥ—courage; kṣhamā—forgiveness\n', 'https://gita.github.io/gita/data/verse_recitation/10/406.mp3', 10, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (407, 'बृहत्साम तथा साम्नां गायत्री छन्दसामहम्।\n\nमासानां मार्गशीर्षोऽहमृतूनां कुसुमाकरः।।10.35।।\n ', 'bṛihat-sāma tathā sāmnāṁ gāyatrī chhandasām aham\nmāsānāṁ mārga-śhīrṣho ’ham ṛitūnāṁ kusumākaraḥ\n', 'bṛihat-sāma—the Brihatsama; tathā—also; sāmnām—amongst the hymns in the Sama Veda; gāyatrī—the Gayatri mantra; chhandasām—amongst poetic meters; aham—I; māsānām—of the twelve months; mārga-śhīrṣhaḥ—the month of November-December; aham—I; ṛitūnām—of all seasons; kusuma-ākaraḥ—spring\n', 'https://gita.github.io/gita/data/verse_recitation/10/407.mp3', 10, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (408, 'द्यूतं छलयतामस्मि तेजस्तेजस्विनामहम्।\n\nजयोऽस्मि व्यवसायोऽस्मि सत्त्वं सत्त्ववतामहम्।।10.36।।\n ', 'dyūtaṁ chhalayatām asmi tejas tejasvinām aham\njayo ’smi vyavasāyo ’smi sattvaṁ sattvavatām aham\n', 'dyūtam—gambling; chhalayatām—of all cheats; asmi—I am; tejaḥ—the splendor; tejasvinām—of the splendid; aham—I; jayaḥ—victory; asmi—I am; vyavasāyaḥ—firm resolve; asmi—I am; sattvam—virtue; sattva-vatām—of the virtuous; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/10/408.mp3', 10, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (409, 'वृष्णीनां वासुदेवोऽस्मि पाण्डवानां धनंजयः।\n\nमुनीनामप्यहं व्यासः कवीनामुशना कविः।।10.37।।\n ', 'vṛiṣhṇīnāṁ vāsudevo ’smi pāṇḍavānāṁ dhanañjayaḥ\nmunīnām apyahaṁ vyāsaḥ kavīnām uśhanā kaviḥ\n', 'vṛiṣhṇīnām—amongst the descendants of Vrishni; vāsudevaḥ—Krishna, the son of Vasudev; asmi—I am; pāṇḍavānām—amongst the Pandavas; dhanañjayaḥ—Arjun, the conqueror of wealth; munīnām—amongst the sages; api—also; aham—I; vyāsaḥ—Ved Vyas; kavīnām—amongst the great thinkers; uśhanā—Shukracharya; kaviḥ—the thinker\n', 'https://gita.github.io/gita/data/verse_recitation/10/409.mp3', 10, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (410, 'दण्डो दमयतामस्मि नीतिरस्मि जिगीषताम्।\n\nमौनं चैवास्मि गुह्यानां ज्ञानं ज्ञानवतामहम्।।10.38।।\n ', 'daṇḍo damayatām asmi nītir asmi jigīṣhatām\nmaunaṁ chaivāsmi guhyānāṁ jñānaṁ jñānavatām aham\n', 'daṇḍaḥ—punishment; damayatām—amongst means of preventing lawlessness; asmi—I am; nītiḥ—proper conduct; asmi—I am; jigīṣhatām—amongst those who seek victory; maunam—silence; cha—and; eva—also; asmi—I am; guhyānām—amongst secrets; jñānam—wisdom; jñāna-vatām—in the wise; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/10/410.mp3', 10, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (411, 'यच्चापि सर्वभूतानां बीजं तदहमर्जुन।\n\nन तदस्ति विना यत्स्यान्मया भूतं चराचरम्।।10.39।।\n ', 'yach chāpi sarva-bhūtānāṁ bījaṁ tad aham arjuna\nna tad asti vinā yat syān mayā bhūtaṁ charācharam\n', 'yat—which; cha—and; api—also; sarva-bhūtānām—of all living beings; bījam—generating seed; tat—that; aham—I; arjuna—Arjun; na—not; tat—that; asti—is; vinā—without; yat—which; syāt—may exist; mayā—me; bhūtam—creature; chara-acharam—moving and nonmoving\n', 'https://gita.github.io/gita/data/verse_recitation/10/411.mp3', 10, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (412, 'नान्तोऽस्ति मम दिव्यानां विभूतीनां परंतप।\n\nएष तूद्देशतः प्रोक्तो विभूतेर्विस्तरो मया।।10.40।।\n ', 'nānto ’sti mama divyānāṁ vibhūtīnāṁ parantapa\neṣha tūddeśhataḥ prokto vibhūter vistaro mayā\n', 'na—not; antaḥ—end; asti—is; mama—my; divyānām—divine; vibhūtīnām—manifestations; parantapa—Arjun, the conqueror of the enemies; eṣhaḥ—this; tu—but; uddeśhataḥ—just one portion; proktaḥ—declared; vibhūteḥ—of (my) glories; vistaraḥ—the breath of the topic; mayā—by me\n', 'https://gita.github.io/gita/data/verse_recitation/10/412.mp3', 10, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (413, 'यद्यद्विभूतिमत्सत्त्वं श्रीमदूर्जितमेव वा।\n\nतत्तदेवावगच्छ त्वं मम तेजोंऽशसंभवम्।।10.41।।\n ', 'yad yad vibhūtimat sattvaṁ śhrīmad ūrjitam eva vā\ntat tad evāvagachchha tvaṁ mama tejo ’nśha-sambhavam\n', 'yat yat—whatever; vibhūtimat—opulent; sattvam—being; śhrī-mat—beautiful; ūrjitam—glorious; eva—also; vā—or; tat tat—all that; eva—only; avagachchha—know; tvam—you; mama—my; tejaḥ-anśha-sambhavam—splendor; anśha—a part; sambhavam—born of\n', 'https://gita.github.io/gita/data/verse_recitation/10/413.mp3', 10, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (414, 'अथवा बहुनैतेन किं ज्ञातेन तवार्जुन।\n\nविष्टभ्याहमिदं कृत्स्नमेकांशेन स्थितो जगत्।।10.42।।\n ', 'atha vā bahunaitena kiṁ jñātena tavārjuna\nviṣhṭabhyāham idaṁ kṛitsnam ekānśhena sthito jagat\n', 'athavā—or; bahunā—detailed; etena—by this; kim—what; jñātena tava—can be known by you; arjuna—Arjun; viṣhṭabhya—pervade and support; aham—I; idam—this; kṛitsnam—entire; eka—by one; anśhena—fraction; sthitaḥ—am situated; jagat—creation\n', 'https://gita.github.io/gita/data/verse_recitation/10/414.mp3', 10, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (415, 'अर्जुन उवाच\n\nमदनुग्रहाय परमं गुह्यमध्यात्मसंज्ञितम्।\n\nयत्त्वयोक्तं वचस्तेन मोहोऽयं विगतो मम।।11.1।।\n ', 'arjuna uvācha\nmad-anugrahāya paramaṁ guhyam adhyātma-sanjñitam\nyat tvayoktaṁ vachas tena moho ’yaṁ vigato mama\n', 'arjunaḥ uvācha—Arjun said; mat-anugrahāya—out of compassion to me; paramam—supreme; guhyam—confidential; adhyātma-sanjñitam—about spiritual knowledge; yat—which; tvayā—by you; uktam—spoken; vachaḥ—words; tena—by that; mohaḥ—illusion; ayam—this; vigataḥ—is dispelled; mama—my\n', 'https://gita.github.io/gita/data/verse_recitation/11/415.mp3', 11, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (416, 'भवाप्ययौ हि भूतानां श्रुतौ विस्तरशो मया।\n\nत्वत्तः कमलपत्राक्ष माहात्म्यमपि चाव्ययम्।।11.2।।\n ', 'bhavāpyayau hi bhūtānāṁ śhrutau vistaraśho mayā\ntvattaḥ kamala-patrākṣha māhātmyam api chāvyayam\n', 'bhava—appearance; apyayau—disappearance; hi—indeed; bhūtānām—of all living beings; śhrutau—have heard; vistaraśhaḥ—in detail; mayā—by me; tvattaḥ—from you; kamala-patra-akṣha—lotus-eyed one; māhātmyam—greatness; api—also; cha—and; avyayam—eternal\n', 'https://gita.github.io/gita/data/verse_recitation/11/416.mp3', 11, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (417, 'एवमेतद्यथात्थ त्वमात्मानं परमेश्वर।\n\nद्रष्टुमिच्छामि ते रूपमैश्वरं पुरुषोत्तम।।11.3।।\n ', 'evam etad yathāttha tvam ātmānaṁ parameśhvara\ndraṣhṭum ichchhāmi te rūpam aiśhwaraṁ puruṣhottama\n', 'evam—thus; etat—this; yathā—as; āttha—have spoken; tvam—you; ātmānam—yourself; parama-īśhvara—Supreme Lord; draṣhṭum—to see; ichchhāmi—I desire; te—your; rūpam—form; aiśhwaram—divine; puruṣha-uttama—Shree Krishna, the Supreme Divine Personality\n', 'https://gita.github.io/gita/data/verse_recitation/11/417.mp3', 11, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (418, 'मन्यसे यदि तच्छक्यं मया द्रष्टुमिति प्रभो।\n\nयोगेश्वर ततो मे त्वं दर्शयाऽत्मानमव्ययम्।।11.4।।\n ', 'manyase yadi tach chhakyaṁ mayā draṣhṭum iti prabho\nyogeśhvara tato me tvaṁ darśhayātmānam avyayam\n', 'manyase—you think; yadi—if; tat—that; śhakyam—possible; mayā—by me; draṣhṭum—to behold; iti—thus; prabho—Lord; yoga-īśhvara—Lord of all mystic powers; tataḥ—then; me—to me; tvam—you; darśhaya—reveal; ātmānam—yourself; avyayam—imperishable\n', 'https://gita.github.io/gita/data/verse_recitation/11/418.mp3', 11, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (419, 'श्री भगवानुवाच\n\nपश्य मे पार्थ रूपाणि शतशोऽथ सहस्रशः।\n\nनानाविधानि दिव्यानि नानावर्णाकृतीनि च।।11.5।।\n ', 'śhrī-bhagavān uvācha\npaśhya me pārtha rūpāṇi śhataśho ’tha sahasraśhaḥ\nnānā-vidhāni divyāni nānā-varṇākṛitīni cha\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; paśhya—behold; me—my; pārtha—Arjun, the son of Pritha; rūpāṇi—forms; śhataśhaḥ—by the hundreds; atha—and; sahasraśhaḥ—thousands; nānā-vidhāni—various; divyāni—divine; nānā—various; varṇa—colors; ākṛitīni—shapes; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/11/419.mp3', 11, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (420, 'पश्यादित्यान्वसून्रुद्रानश्िवनौ मरुतस्तथा।\n\nबहून्यदृष्टपूर्वाणि पश्याऽश्चर्याणि भारत।।11.6।।\n ', 'paśhyādityān vasūn rudrān aśhvinau marutas tathā\nbahūny adṛiṣhṭa-pūrvāṇi paśhyāśhcharyāṇi bhārata\n', 'paśhya—behold; ādityān—the (twelve) sons of Aditi; vasūn—the (eight) Vasus; rudrān—the (eleven) Rudras; aśhvinau—the (twin) Ashvini Kumars; marutaḥ—the (forty-nine) Maruts; tathā—and; bahūni—many; adṛiṣhṭa—never revealed; pūrvāṇi—before; paśhya—behold; āśhcharyāṇi—marvels; bhārata—Arjun, scion of the Bharatas\n', 'https://gita.github.io/gita/data/verse_recitation/11/420.mp3', 11, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (421, 'इहैकस्थं जगत्कृत्स्नं पश्याद्य सचराचरम्।\n\nमम देहे गुडाकेश यच्चान्यद्द्रष्टुमिच्छसि।।11.7।।\n ', 'ihaika-sthaṁ jagat kṛitsnaṁ paśhyādya sa-charācharam\nmama dehe guḍākeśha yach chānyad draṣhṭum ichchhasi\n', 'iha—here; eka-stham—assembled together; jagat—the universe; kṛitsnam—entire; paśhya—behold; adya—now; sa—with; chara—the moving; acharam—the non- moving; mama—my; dehe—in this form; guḍākeśha—Arjun, the conqueror of sleep; yat—whatever; cha—also; anyat—else; draṣhṭum—to see; ichchhasi—you wish\n', 'https://gita.github.io/gita/data/verse_recitation/11/421.mp3', 11, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (422, 'न तु मां शक्यसे द्रष्टुमनेनैव स्वचक्षुषा।\n\nदिव्यं ददामि ते चक्षुः पश्य मे योगमैश्वरम्।।11.8।।\n ', 'na tu māṁ śhakyase draṣhṭum anenaiva sva-chakṣhuṣhā\ndivyaṁ dadāmi te chakṣhuḥ paśhya me yogam aiśhwaram\n', 'na—not; tu—but; mām—me; śhakyase—you can; draṣhṭum—to see; anena—with these; eva—even; sva-chakṣhuṣhā—with your physical eyes; divyam—divine; dadāmi—I give; te—to you; chakṣhuḥ—eyes; paśhya—behold; me—my; yogam aiśhwaram—majestic opulence\n', 'https://gita.github.io/gita/data/verse_recitation/11/422.mp3', 11, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (423, 'सञ्जय उवाच\n\nएवमुक्त्वा ततो राजन्महायोगेश्वरो हरिः।\n\nदर्शयामास पार्थाय परमं रूपमैश्वरम्।।11.9।।\n ', 'sañjaya uvācha\nevam uktvā tato rājan mahā-yogeśhvaro hariḥ\ndarśhayām āsa pārthāya paramaṁ rūpam aiśhwaram\n', 'sañjayaḥ uvācha—Sanjay said; evam—thus; uktvā—having spoken; tataḥ—then; rājan—King; mahā-yoga-īśhvaraḥ—the Supreme Lord of Yog; hariḥ—Shree Krishna; darśhayām āsa—displayed; pārthāya—to Arjun; paramam—divine; rūpam aiśhwaram—opulence\n', 'https://gita.github.io/gita/data/verse_recitation/11/423.mp3', 11, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (424, 'अनेकवक्त्रनयनमनेकाद्भुतदर्शनम्।\n\nअनेकदिव्याभरणं दिव्यानेकोद्यतायुधम्।।11.10।।\n ', 'aneka-vaktra-nayanam anekādbhuta-darśhanam\naneka-divyābharaṇaṁ divyānekodyatāyudham\n', 'aneka—many; vaktra—faces; nayanam—eyes; aneka—many; adbhuta—wonderful; darśhanam—had a vision of; aneka—many; divya—divine; ābharaṇam—ornaments; divya—divine; aneka—many; udyata—uplifted; āyudham—weapons;\n', 'https://gita.github.io/gita/data/verse_recitation/11/424.mp3', 11, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (425, 'दिव्यमाल्याम्बरधरं दिव्यगन्धानुलेपनम्।\n\nसर्वाश्चर्यमयं देवमनन्तं विश्वतोमुखम्।।11.11।।\n ', 'divya-mālyāmbara-dharaṁ divya-gandhānulepanam\nsarvāśhcharya-mayaṁ devam anantaṁ viśhvato-mukham\n', 'divya—divine; mālya—garlands; āmbara—garments; dharam—wearing; divya—divine; gandha—fragrances; anulepanam—anointed with; sarva—all; āśhcharya-mayam—wonderful; devam—Lord; anantam—unlimited; viśhwataḥ—all sides; mukham—face\n', 'https://gita.github.io/gita/data/verse_recitation/11/425.mp3', 11, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (426, 'दिवि सूर्यसहस्रस्य भवेद्युगपदुत्थिता।\n\nयदि भाः सदृशी सा स्याद्भासस्तस्य महात्मनः।।11.12।।\n ', 'divi sūrya-sahasrasya bhaved yugapad utthitā\nyadi bhāḥ sadṛiśhī sā syād bhāsas tasya mahātmanaḥ\n', 'divi—in the sky; sūrya—suns; sahasrasya—thousand; bhavet—were; yugapat—simultaneously; utthitā—rising; yadi—if; bhāḥ—splendor; sadṛiśhī—like; sā—that; syāt—would be; bhāsaḥ—splendor; tasya—of them; mahā-ātmanaḥ—the great personality\n', 'https://gita.github.io/gita/data/verse_recitation/11/426.mp3', 11, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (427, 'तत्रैकस्थं जगत्कृत्स्नं प्रविभक्तमनेकधा।\n\nअपश्यद्देवदेवस्य शरीरे पाण्डवस्तदा।।11.13।।\n ', 'tatraika-sthaṁ jagat kṛitsnaṁ pravibhaktam anekadhā\napaśhyad deva-devasya śharīre pāṇḍavas tadā\n', 'tatra—there; eka-stham—established in one place; jagat—the universe; kṛitsnam—entire; pravibhaktam—divided; anekadhā—many; apaśhyat—could see; deva-devasya—of the God of gods; śharīre—in the body; pāṇḍavaḥ—Arjun; tadā—at that time\n', 'https://gita.github.io/gita/data/verse_recitation/11/427.mp3', 11, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (428, 'ततः स विस्मयाविष्टो हृष्टरोमा धनञ्जयः।\n\nप्रणम्य शिरसा देवं कृताञ्जलिरभाषत।।11.14।।\n ', 'tataḥ sa vismayāviṣhṭo hṛiṣhṭa-romā dhanañjayaḥ\npraṇamya śhirasā devaṁ kṛitāñjalir abhāṣhata\n', 'tataḥ—then; saḥ—he; vismaya-āviṣhṭaḥ—full of wonder; hṛiṣhṭa-romā—with hair standing on end; dhanañjayaḥ—Arjun, the conqueror of wealth; praṇamya—bow down; śhirasā—with (his) head; devam—the Lord; kṛita-añjaliḥ—with folded hands; abhāṣhata—he addressed\n', 'https://gita.github.io/gita/data/verse_recitation/11/428.mp3', 11, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (429, 'अर्जुन उवाच\n\nपश्यामि देवांस्तव देव देहे\n\nसर्वांस्तथा भूतविशेषसङ्घान्।\n\nब्रह्माणमीशं कमलासनस्थ\n\nमृषींश्च सर्वानुरगांश्च दिव्यान्।।11.15।।\n ', 'arjuna uvācha\npaśhyāmi devāns tava deva dehe\nsarvāns tathā bhūta-viśheṣha-saṅghān\nbrahmāṇam īśhaṁ kamalāsana-stham\nṛiṣhīnśh cha sarvān uragānśh cha divyān\n', 'arjunaḥ uvācha—Arjun said; paśhyāmi—I behold; devān—all the gods; tava—your; deva—Lord; dehe—within the body; sarvān—all; tathā—as well as; bhūta viśheṣha-saṅghān—hosts of different beings; brahmāṇam—Lord Brahma; īśham—Shiv; kamala-āsana-stham—seated on the lotus flower; ṛiṣhīn—sages; cha—and; sarvān—all; uragān—serpents; cha—and; divyān—divine\n', 'https://gita.github.io/gita/data/verse_recitation/11/429.mp3', 11, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (430, 'अनेकबाहूदरवक्त्रनेत्रं\n\nपश्यामि त्वां सर्वतोऽनन्तरूपम्।\n\nनान्तं न मध्यं न पुनस्तवादिं\n\nपश्यामि विश्वेश्वर विश्वरूप।।11.16।।\n ', 'aneka-bāhūdara-vaktra-netraṁ\npaśhyāmi tvāṁ sarvato ’nanta-rūpam\nnāntaṁ na madhyaṁ na punas tavādiṁ\npaśhyāmi viśhveśhvara viśhva-rūpa\n', 'aneka—infinite; bāhu—arms; udara—stomachs; vaktra—faces; netram—eyes; paśhyāmi—I see; tvām—you; sarvataḥ—in every direction; ananta-rūpam—inifinite forms; na antam—without end; na—not; madhyam—middle; na—no; punaḥ—again; tava—your; ādim—beginning; paśhyāmi—I see; viśhwa-īśhwara—The Lord of the universe; viśhwa-rūpa—universal form\n', 'https://gita.github.io/gita/data/verse_recitation/11/430.mp3', 11, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (431, 'किरीटिनं गदिनं चक्रिणं च\n\nतेजोराशिं सर्वतोदीप्तिमन्तम्।\n\nपश्यामि त्वां दुर्निरीक्ष्यं समन्ता\n\nद्दीप्तानलार्कद्युतिमप्रमेयम्।।11.17।।\n ', 'kirīṭinaṁ gadinaṁ chakriṇaṁ cha\ntejo-rāśhiṁ sarvato dīptimantam\npaśhyāmi tvāṁ durnirīkṣhyaṁ samantād\ndīptānalārka-dyutim aprameyam\n', 'kirīṭinam—adorned with a crown; gadinam—with club; chakriṇam—with discs; cha—and; tejaḥ-rāśhim—abode of splendor; sarvataḥ—everywhere; dīpti-mantam—shining; paśhyāmi—I see; tvām—you; durnirīkṣhyam—difficult to look upon; samantāt—in all directions; dīpta-anala—blazing fire; arka—like the sun; dyutim—effulgence; aprameyam—immeasurable\n', 'https://gita.github.io/gita/data/verse_recitation/11/431.mp3', 11, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (432, 'त्वमक्षरं परमं वेदितव्यं\n\nत्वमस्य विश्वस्य परं निधानम्।\n\nत्वमव्ययः शाश्वतधर्मगोप्ता\n\nसनातनस्त्वं पुरुषो मतो मे।।11.18।।\n ', 'tvam akṣharaṁ paramaṁ veditavyaṁ\ntvam asya viśhvasya paraṁ nidhānam\ntvam avyayaḥ śhāśhvata-dharma-goptā\nsanātanas tvaṁ puruṣho mato me\n', 'tvam—you; akṣharam—the imperishable; paramam—the supreme being; veditavyam—worthy of being known; tvam—you; asya—of this; viśhwasya—of the creation; param—supreme; nidhānam—support; tvam—you; avyayaḥ—eternal; śhāśhvata-dharma-goptā—protector of the eternal religion; sanātanaḥ—everlasting; tvam—you; puruṣhaḥ—the Supreme Divine Person; mataḥ me—my opinion\n', 'https://gita.github.io/gita/data/verse_recitation/11/432.mp3', 11, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (433, 'अनादिमध्यान्तमनन्तवीर्य\n\nमनन्तबाहुं शशिसूर्यनेत्रम्।\n\nपश्यामि त्वां दीप्तहुताशवक्त्रम्\n\nस्वतेजसा विश्वमिदं तपन्तम्।।11.19।।\n ', 'स्वतेजसा विश्वमिदं तपन्तम् || 19||\n', 'anādi-madhyāntam ananta-vīryam\nananta-bāhuṁ śhaśhi-sūrya-netram\npaśhyāmi tvāṁ dīpta-hutāśha-vaktraṁ\nsva-tejasā viśhvam idaṁ tapantam\n', 'https://gita.github.io/gita/data/verse_recitation/11/433.mp3', 11, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (434, 'द्यावापृथिव्योरिदमन्तरं हि\n\nव्याप्तं त्वयैकेन दिशश्च सर्वाः।\n\nदृष्ट्वाऽद्भुतं रूपमुग्रं तवेदं\n\nलोकत्रयं प्रव्यथितं महात्मन्।।11.20।।\n ', 'dyāv ā-pṛithivyor idam antaraṁ hi\nvyāptaṁ tvayaikena diśhaśh cha sarvāḥ\ndṛiṣhṭvādbhutaṁ rūpam ugraṁ tavedaṁ\nloka-trayaṁ pravyathitaṁ mahātman\n', 'dyau-ā-pṛithivyoḥ—between heaven and earth; idam—this; antaram—space between; hi—indeed; vyāptam—pervaded; tvayā—by you; ekena—alone; diśhaḥ—directions; cha—and; sarvāḥ—all; dṛiṣhṭvā—seeing; adbhutam—wondrous; rūpam—form; ugram—terrible; tava—your; idam—this; loka—worlds; trayam—three; pravyathitam—trembling; mahā-ātman—The greatest of all beings\n', 'https://gita.github.io/gita/data/verse_recitation/11/434.mp3', 11, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (435, 'अमी हि त्वां सुरसङ्घाः विशन्ति\n\nकेचिद्भीताः प्राञ्जलयो गृणन्ति।\n\nस्वस्तीत्युक्त्वा महर्षिसिद्धसङ्घाः\n\nस्तुवन्ति त्वां स्तुतिभिः पुष्कलाभिः।।11.21।।\n ', 'amī hi tvāṁ sura-saṅghā viśhanti\nkechid bhītāḥ prāñjalayo gṛiṇanti\nsvastīty uktvā maharṣhi-siddha-saṅghāḥ\nstuvanti tvāṁ stutibhiḥ puṣhkalābhiḥ\n', 'amī—these; hi—indeed; tvām—you; sura-saṅghāḥ—assembly of celestial gods; viśhanti—are entering; kechit—some; bhītāḥ—in fear; prāñjalayaḥ—with folded hands; gṛiṇanti—praise; svasti—auspicious; iti—thus; uktvā—reciting; mahā-ṛiṣhi—great sages; siddha-saṅghāḥ—perfect beings; stuvanti—are extolling; tvām—you; stutibhiḥ—with prayers; puṣhkalābhiḥ—hymns\n', 'https://gita.github.io/gita/data/verse_recitation/11/435.mp3', 11, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (436, 'रुद्रादित्या वसवो ये च साध्या\n\nविश्वेऽश्िवनौ मरुतश्चोष्मपाश्च।\n\nगन्धर्वयक्षासुरसिद्धसङ्घा\n\nवीक्षन्ते त्वां विस्मिताश्चैव सर्वे।।11.22।।\n ', 'rudrādityā vasavo ye cha sādhyā\nviśhve ’śhvinau marutaśh choṣhmapāśh cha\ngandharva-yakṣhāsura-siddha-saṅghā\nvīkṣhante tvāṁ vismitāśh chaiva sarve\n', 'rudra—a form of Lord Shiv; ādityāḥ—the Adityas; vasavaḥ—the Vasus; ye—these; cha—and; sādhyāḥ—the Sadhyas; viśhve—the Vishvadevas; aśhvinau—the Ashvini kumars; marutaḥ—the Maruts; cha—and; uṣhma-pāḥ—the ancestors; cha—and; gandharva—Gandharvas; yakṣha—the Yakshas; asura—the demons; siddha—the perfected beings; saṅghāḥ—the assemblies; vīkṣhante—are beholding; tvām—you; vismitāḥ—in wonder; cha—and; eva—verily; sarve—all\n', 'https://gita.github.io/gita/data/verse_recitation/11/436.mp3', 11, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (437, 'रूपं महत्ते बहुवक्त्रनेत्रं\n\nमहाबाहो बहुबाहूरुपादम्।\n\nबहूदरं बहुदंष्ट्राकरालं\n\nदृष्ट्वा लोकाः प्रव्यथितास्तथाऽहम्।।11.23।।\n ', 'rūpaṁ mahat te bahu-vaktra-netraṁ\nmahā-bāho bahu-bāhūru-pādam\nbahūdaraṁ bahu-danṣhṭrā-karālaṁ\ndṛiṣhṭvā lokāḥ pravyathitās tathāham\n', 'rūpam—form; mahat—magnificent; te—your; bahu—many; vaktra—mouths; netram—eyes; mahā-bāho—mighty-armed Lord; bahu—many; bāhu—arms; ūru—thighs; pādam—legs; bahu-udaram—many stomachs; bahu-danṣhṭrā—many teeth; karālam—terrifying; dṛiṣhṭvā—seeing; lokāḥ—all the worlds; pravyathitāḥ—terror-stricken; tathā—so also; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/11/437.mp3', 11, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (438, 'नभःस्पृशं दीप्तमनेकवर्णं\n\nव्यात्ताननं दीप्तविशालनेत्रम्।\n\nदृष्ट्वा हि त्वां प्रव्यथितान्तरात्मा\n\nधृतिं न विन्दामि शमं च विष्णो।।11.24।।\n ', 'nabhaḥ-spṛiśhaṁ dīptam aneka-varṇaṁ\nvyāttānanaṁ dīpta-viśhāla-netram\ndṛiṣhṭvā hi tvāṁ pravyathitāntar-ātmā\ndhṛitiṁ na vindāmi śhamaṁ cha viṣhṇo\n', 'nabhaḥ-spṛiśham—touching the sky; dīptam—effulgent; aneka—many; varṇam—colors; vyātta—open; ānanam—mouths; dīpta—blazing; viśhāla—enormous; netram—eyes; dṛiṣhṭvā—seeing; hi—indeed; tvām—you; pravyathitāntar-ātmā—my heart is trembling with fear; dhṛitim—firmness; na—not; vindāmi—I find; śhamam—mental peace; cha—and; viṣhṇo—Lord Vishnu\n', 'https://gita.github.io/gita/data/verse_recitation/11/438.mp3', 11, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (439, 'दंष्ट्राकरालानि च ते मुखानि\n\nदृष्ट्वैव कालानलसन्निभानि।\n\nदिशो न जाने न लभे च शर्म\n\nप्रसीद देवेश जगन्निवास।।11.25।।\n ', 'danṣhṭrā-karālāni cha te mukhāni\ndṛiṣhṭvaiva kālānala-sannibhāni\ndiśho na jāne na labhe cha śharma\nprasīda deveśha jagan-nivāsa\n', 'danṣhṭrā—teeth; karālāni—terrible; cha—and; te—your; mukhāni—mouths; dṛiṣhṭvā—having seen; eva—indeed; kāla-anala—the fire of annihilation; sannibhāni—resembling; diśhaḥ—the directions; na—not; jāne—know; na—not; labhe—I obtain; cha—and; śharma—peace; prasīda—have mercy; deva-īśha—The Lord of lords; jagat-nivāsa—The shelter of the universe\n', 'https://gita.github.io/gita/data/verse_recitation/11/439.mp3', 11, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (440, 'अमी च त्वां धृतराष्ट्रस्य पुत्राः\n\nसर्वे सहैवावनिपालसङ्घैः।\n\nभीष्मो द्रोणः सूतपुत्रस्तथाऽसौ\n\nसहास्मदीयैरपि योधमुख्यैः।।11.26।।\n ', 'amī cha tvāṁ dhṛitarāśhtrasya putrāḥ\nsarve sahaivāvani-pāla-saṅghaiḥ\nbhīṣhmo droṇaḥ sūta-putras tathāsau\nsahāsmadīyair api yodha-mukhyaiḥ\n vaktrāṇi te tvaramāṇā viśhanti\ndanṣhṭrā-karālāni bhayānakāni\nkechid vilagnā daśhanāntareṣhu\nsandṛiśhyante chūrṇitair uttamāṅgaiḥ\n', 'amī—these; cha—and; tvām—you; dhṛitarāśhtrasya—of Dhritarashtra; putrāḥ—sons; sarve—all; saha—with; eva—even; avani-pāla—their allied kings; sanghaiḥ—assembly; bhīṣhmaḥ—Bheeshma; droṇaḥ—Dronacharya; sūta-putraḥ—Karna; tathā—and also; asau—this; saha—with; asmadīyaiḥ—from our side; api—also; yodha-mukhyaiḥ—generals;\n vaktrāṇi—mouths; te—your; tvaramāṇāḥ—rushing; viśhanti—enter; danṣhṭrā—teeth; karālāni—terrible; bhayānakāni—fearsome; kechit—some; vilagnāḥ—getting stuck; daśhana-antareṣhu—between the teeth; sandṛiśhyante—are seen; chūrṇitaiḥ—getting smashed; uttama-aṅgaiḥ—heads\n', 'https://gita.github.io/gita/data/verse_recitation/11/440.mp3', 11, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (441, 'वक्त्राणि ते त्वरमाणा विशन्ति\n\nदंष्ट्राकरालानि भयानकानि।\n\nकेचिद्विलग्ना दशनान्तरेषु\n\nसंदृश्यन्ते चूर्णितैरुत्तमाङ्गैः।।11.27।।\n ', 'vaktrāṇi te  tvaramāṇā   viśanti daṁṣṭrā-karālāni    bhayānakāni kecid   vilagnā daśanāntareṣu   sandṛśyante cūrṇitair   uttamāṅgaiḥ', 'vaktrāṇi—mouths;   te—Your;    tvaramāṇāḥ—fearful;     viśanti—entering;   daṁṣṭrā—teeth;  karālāni—terrible;  bhayānakāni—very    fearful;    kecit—some  of  them;   vilagnāḥ—being  attacked;   daśanāntareṣu—between   the teeth;  sandṛśyante—being   seen;   cūrṇitaiḥ—smashed;  uttama-aṅgaiḥ—by    the head', 'https://gita.github.io/gita/data/verse_recitation/11/441.mp3', 11, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (442, 'यथा नदीनां बहवोऽम्बुवेगाः\n\nसमुद्रमेवाभिमुखाः द्रवन्ति।\n\nतथा तवामी नरलोकवीरा\n\nविशन्ति वक्त्राण्यभिविज्वलन्ति।।11.28।।\n ', 'yathā nadīnāṁ bahavo ’mbu-vegāḥ\nsamudram evābhimukhā dravanti\ntathā tavāmī nara-loka-vīrā\nviśhanti vaktrāṇy abhivijvalanti\n', 'yathā—as; nadīnām—of the rivers; bahavaḥ—many; ambu-vegāḥ—water waves; samudram—the ocean; eva—indeed; abhimukhāḥ—toward; dravanti—flowing rapidly; tathā—similarly; tava—your; amī—these; nara-loka-vīrāḥ—kings of human society; viśhanti—enter; vaktrāṇi—mouths; abhivijvalanti—blazing;\n', 'https://gita.github.io/gita/data/verse_recitation/11/442.mp3', 11, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (443, 'यथा प्रदीप्तं ज्वलनं पतङ्गा\n\nविशन्ति नाशाय समृद्धवेगाः।\n\nतथैव नाशाय विशन्ति लोका\n\nस्तवापि वक्त्राणि समृद्धवेगाः।।11.29।।\n ', 'yathā pradīptaṁ jvalanaṁ pataṅgā\nviśhanti nāśhāya samṛiddha-vegāḥ\ntathaiva nāśhāya viśhanti lokās\ntavāpi vaktrāṇi samṛiddha-vegāḥ\n', 'yathā—as; pradīptam—blazing; jvalanam—fire; pataṅgāḥ—moths; viśhanti—enter; nāśhāya—to be perished; samṛiddha vegāḥ—with great speed; tathā eva—similarly; nāśhāya—to be perished; viśhanti—enter; lokāḥ—these people; tava—your; api—also; vaktrāṇi—mouths; samṛiddha-vegāḥ—with great speed\n', 'https://gita.github.io/gita/data/verse_recitation/11/443.mp3', 11, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (444, 'लेलिह्यसे ग्रसमानः समन्ता\n\nल्लोकान्समग्रान्वदनैर्ज्वलद्भिः।\n\nतेजोभिरापूर्य जगत्समग्रं\n\nभासस्तवोग्राः प्रतपन्ति विष्णो।।11.30।।\n ', 'lelihyase grasamānaḥ samantāl\nlokān samagrān vadanair jvaladbhiḥ\ntejobhir āpūrya jagat samagraṁ\nbhāsas tavogrāḥ pratapanti viṣhṇo\n', 'lelihyase—you are licking; grasamānaḥ—devouring; samantāt—on all sides; lokān—worlds; samagrān—all; vadanaiḥ—with mouths; jvaladbhiḥ—blazing; tejobhiḥ—by effulgence; āpūrya—filled with; jagat—the universe; samagram—all; bhāsaḥ—rays; tava—your; ugrāḥ—fierce; pratapanti—scorching; viṣhṇo—Lord Vishnu\n', 'https://gita.github.io/gita/data/verse_recitation/11/444.mp3', 11, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (445, 'आख्याहि मे को भवानुग्ररूपो\n\nनमोऽस्तु ते देववर प्रसीद।\n\nविज्ञातुमिच्छामि भवन्तमाद्यं\n\nन हि प्रजानामि तव प्रवृत्तिम्।।11.31।।\n ', 'ākhyāhi me ko bhavān ugra-rūpo\nnamo ’stu te deva-vara prasīda\nvijñātum ichchhāmi bhavantam ādyaṁ\nna hi prajānāmi tava pravṛittim\n', 'ākhyāhi—tell; me—me; kaḥ—who; bhavān—you; ugra-rūpaḥ—fierce form; namaḥ astu—I bow; te—to you; deva-vara—God of gods; prasīda—be merciful; vijñātum—to know; ichchhāmi—I wish; bhavantam—you; ādyam—the primeval; na—not; hi—because; prajānāmi—comprehend; tava—your; pravṛittim—workings\n', 'https://gita.github.io/gita/data/verse_recitation/11/445.mp3', 11, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (446, 'श्री भगवानुवाच\n\nकालोऽस्मि लोकक्षयकृत्प्रवृद्धो\n\nलोकान्समाहर्तुमिह प्रवृत्तः।\n\nऋतेऽपि त्वां न भविष्यन्ति सर्वे\n\nयेऽवस्थिताः प्रत्यनीकेषु योधाः।।11.32।।\n ', 'śhrī-bhagavān uvācha\nkālo ’smi loka-kṣhaya-kṛit pravṛiddho\nlokān samāhartum iha pravṛittaḥ\nṛite ’pi tvāṁ na bhaviṣhyanti sarve\nye ’vasthitāḥ pratyanīkeṣhu yodhāḥ\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; kālaḥ—time; asmi—I am; loka-kṣhaya-kṛit—the source of destruction of the worlds; pravṛiddhaḥ—mighty; lokān—the worlds; samāhartum—annihilation; iha—this world; pravṛittaḥ—participation; ṛite—without; api—even; tvām—you; na bhaviṣhyanti—shall cease to exist; sarve—all; ye—who; avasthitāḥ—arrayed; prati-anīkeṣhu—in the opposing army; yodhāḥ—the warriors\n', 'https://gita.github.io/gita/data/verse_recitation/11/446.mp3', 11, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (447, 'तस्मात्त्वमुत्तिष्ठ यशो लभस्व\n\nजित्वा शत्रून् भुङ्क्ष्व राज्यं समृद्धम्।\n\nमयैवैते निहताः पूर्वमेव\n\nनिमित्तमात्रं भव सव्यसाचिन्।।11.33।।\n ', 'tasmāt tvam uttiṣhṭha yaśho labhasva\njitvā śhatrūn bhuṅkṣhva rājyaṁ samṛiddham\nmayaivaite nihatāḥ pūrvam eva\nnimitta-mātraṁ bhava savya-sāchin\n', 'tasmāt—therefore; tvam—you; uttiṣhṭha—arise; yaśhaḥ—honor; labhasva—attain; jitvā—conquer; śhatrūn—foes; bhuṅkṣhva—enjoy; rājyam—kingdom; samṛiddham—prosperous; mayā—by me; eva—indeed; ete—these; nihatāḥ—slain; pūrvam—already; eva nimitta-mātram—only an instrument; bhava—become; savya-sāchin—Arjun, the one who can shoot arrows with both hands\n', 'https://gita.github.io/gita/data/verse_recitation/11/447.mp3', 11, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (448, 'द्रोणं च भीष्मं च जयद्रथं च\n\nकर्णं तथाऽन्यानपि योधवीरान्।\n\nमया हतांस्त्वं जहि मा व्यथिष्ठा\n\nयुध्यस्व जेतासि रणे सपत्नान्।।11.34।।\n ', 'droṇaṁ cha bhīṣhmaṁ cha jayadrathaṁ cha\nkarṇaṁ tathānyān api yodha-vīrān\nmayā hatāṁs tvaṁ jahi mā vyathiṣhṭhā\nyudhyasva jetāsi raṇe sapatnān\n', 'droṇam—Dronacharya; cha—and; bhīṣhmam—Bheeshma; cha—and; jayadratham—Jayadratha; cha—and; karṇam—Karn; tathā—also; anyān—others; api—also; yodha-vīrān—brave warriors; mayā—by me; hatān—already killed; tvam—you; jahi—slay; mā—not; vyathiṣhṭhāḥ—be disturbed; yudhyasva—fight; jetā asi—you shall be victorious; raṇe—in battle; sapatnān—enemies\n', 'https://gita.github.io/gita/data/verse_recitation/11/448.mp3', 11, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (449, 'सञ्जय उवाच\n\nएतच्छ्रुत्वा वचनं केशवस्य\n\nकृताञ्जलिर्वेपमानः किरीटी।\n\nनमस्कृत्वा भूय एवाह कृष्णं\n\nसगद्गदं भीतभीतः प्रणम्य।।11.35।।\n ', 'sañjaya uvācha\netach chhrutvā vachanaṁ keśhavasya\nkṛitāñjalir vepamānaḥ kirīṭī\nnamaskṛitvā bhūya evāha kṛiṣhṇaṁ\nsa-gadgadaṁ bhīta-bhītaḥ praṇamya\n', 'sañjayaḥ uvācha—Sanjay said; etat—thus; śhrutvā—hearing; vachanam—words; keśhavasya—of Shree Krishna; kṛita-añjaliḥ—with joined palms; vepamānaḥ—trembling; kirītī—the crowned one, Arjun; namaskṛitvā—with palms joined; bhūyaḥ—again; eva—indeed; āha—spoke; kṛiṣhṇam—to Shree Krishna; sa-gadgadam—in a faltering voice; bhīta-bhītaḥ—overwhelmed with fear; praṇamya—bowed down\n', 'https://gita.github.io/gita/data/verse_recitation/11/449.mp3', 11, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (450, 'अर्जुन उवाच\n\nस्थाने हृषीकेश तव प्रकीर्त्या\n\nजगत् प्रहृष्यत्यनुरज्यते च।\n\nरक्षांसि भीतानि दिशो द्रवन्ति\n\nसर्वे नमस्यन्ति च सिद्धसङ्घाः।।11.36।।\n ', 'arjuna uvācha\nsthāne hṛiṣhīkeśha tava prakīrtyā\njagat prahṛiṣhyaty anurajyate cha\nrakṣhānsi bhītāni diśho dravanti\nsarve namasyanti cha siddha-saṅghāḥ\n', 'arjunaḥ uvācha—Arjun said; sthāne—it is but apt; hṛiṣhīka-īśha—Shree Krishna, the master of the senses; tava—your; prakīrtyā—in praise; jagat—the universe; prahṛiṣhyati—rejoices; anurajyate—be enamored; cha—and; rakṣhānsi—the demons; bhītāni—fearfully; diśhaḥ—in all directions; dravanti—flee; sarve—all; namasyanti—bow down; cha—and; siddha-saṅghāḥ—hosts of perfected saints\n', 'https://gita.github.io/gita/data/verse_recitation/11/450.mp3', 11, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (451, 'कस्माच्च ते न नमेरन्महात्मन्\n\nगरीयसे ब्रह्मणोऽप्यादिकर्त्रे।\n\nअनन्त देवेश जगन्निवास\n\nत्वमक्षरं सदसत्तत्परं यत्।।11.37।।\n ', 'kasmāch cha te na nameran mahātman\ngarīyase brahmaṇo ’py ādi-kartre\nananta deveśha jagan-nivāsa\ntvam akṣharaṁ sad-asat tat paraṁ yat\n', 'kasmāt—why; cha—and; te—you; na nameran—should they not bow down; mahā-ātman—The Great one; garīyase—who are greater; brahmaṇaḥ—than Brahma; api—even; ādi-kartre—to the original creator; ananta—The limitless One; deva-īśha—Lord of the devatās; jagat-nivāsa—Refuge of the universe; tvam—you; akṣharam—the imperishable; sat-asat—manifest and non-manifest; tat—that; param—beyond; yat—which\n', 'https://gita.github.io/gita/data/verse_recitation/11/451.mp3', 11, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (452, 'त्वमादिदेवः पुरुषः पुराण\n\nस्त्वमस्य विश्वस्य परं निधानम्।\n\nवेत्तासि वेद्यं च परं च धाम\n\nत्वया ततं विश्वमनन्तरूप।।11.38।।\n ', 'tvam ādi-devaḥ puruṣhaḥ purāṇas\ntvam asya viśhvasya paraṁ nidhānam\nvettāsi vedyaṁ cha paraṁ cha dhāma\ntvayā tataṁ viśhvam ananta-rūpa\n', 'tvam—you; ādi-devaḥ—the original Divine God; puruṣhaḥ—personality; purāṇaḥ—primeval; tvam—you; asya—of (this); viśhwasya—universe; param—Supreme; nidhānam—resting place; vettā—the knower; asi—you are; vedyam—the object of knowledge; cha—and; param—Supreme; cha—and; dhāma—Abode; tvayā—by you; tatam—pervaded; viśhwam—the universe; ananta-rūpa—posessor of infinite forms\n', 'https://gita.github.io/gita/data/verse_recitation/11/452.mp3', 11, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (453, 'वायुर्यमोऽग्निर्वरुणः शशाङ्कः\n\nप्रजापतिस्त्वं प्रपितामहश्च।\n\nनमो नमस्तेऽस्तु सहस्रकृत्वः\n\nपुनश्च भूयोऽपि नमो नमस्ते।।11.39।।\n ', 'vāyur yamo ’gnir varuṇaḥ śhaśhāṅkaḥ\nprajāpatis tvaṁ prapitāmahaśh cha\nnamo namas te ’stu sahasra-kṛitvaḥ\npunaśh cha bhūyo ’pi namo namas te\n', 'vāyuḥ—the god of wind; yamaḥ—the god of death; agniḥ—the god of fire; varuṇaḥ—the god of water; śhaśha-aṅkaḥ—the moon-God; prajāpatiḥ—Brahma; tvam—you; prapitāmahaḥ—the great-grandfather; cha—and; namaḥ—my salutations; namaḥ—my salutations; te—unto you; astu—let there be; sahasra-kṛitvaḥ—a thousand times; punaḥ cha—and again; bhūyaḥ—again; api—also; namaḥ—(offering) my salutations; namaḥ te—offering my salutations unto you\n', 'https://gita.github.io/gita/data/verse_recitation/11/453.mp3', 11, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (454, 'नमः पुरस्तादथ पृष्ठतस्ते\n\nनमोऽस्तु ते सर्वत एव सर्व।\n\nअनन्तवीर्यामितविक्रमस्त्वं\n\nसर्वं समाप्नोषि ततोऽसि सर्वः।।11.40।।\n ', 'namaḥ purastād atha pṛiṣhṭhatas te\nnamo ’stu te sarvata eva sarva\nananta-vīryāmita-vikramas tvaṁ\nsarvaṁ samāpnoṣhi tato ’si sarvaḥ\n', 'namaḥ—offering salutations; purastāt—from the front; atha—and; pṛiṣhṭhataḥ—the rear; te—to you; namaḥ astu—I offer my salutations; te—to you; sarvataḥ—from all sides; eva—indeed; sarva—all; ananta-vīrya—infinite power; amita-vikramaḥ—infinite valor and might; tvam—you; sarvam—everything; samāpnoṣhi—pervade; tataḥ—thus; asi—(you) are; sarvaḥ—everything\n', 'https://gita.github.io/gita/data/verse_recitation/11/454.mp3', 11, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (455, 'सखेति मत्वा प्रसभं यदुक्तं\n\nहे कृष्ण हे यादव हे सखेति।\n\nअजानता महिमानं तवेदं\n\nमया प्रमादात्प्रणयेन वापि।।11.41।।\n ', 'sakheti matvā prasabhaṁ yad uktaṁ\nhe kṛiṣhṇa he yādava he sakheti\najānatā mahimānaṁ tavedaṁ\nmayā pramādāt praṇayena vāpi\n', 'sakhā—friend; iti—as; matvā—thinking; prasabham—presumptuously; yat—whatever; uktam—addressed; he kṛiṣhṇa—O Shree Krishna; he yādava—O Shree Krishna, who was born in the Yadu clan; he sakhe—O my dear mate; iti—thus; ajānatā—in ignorance; mahimānam—majesty; tava—your; idam—this; mayā—by me; pramādāt—out of negligence; praṇayena—out of affection; vā api—or else; \n', 'https://gita.github.io/gita/data/verse_recitation/11/455.mp3', 11, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (456, 'यच्चावहासार्थमसत्कृतोऽसि\n\nविहारशय्यासनभोजनेषु।\n\nएकोऽथवाप्यच्युत तत्समक्षं\n\nतत्क्षामये त्वामहमप्रमेयम्।।11.42।।\n ', 'yach chāvahāsārtham asat-kṛito ’si\nvihāra-śhayyāsana-bhojaneṣhu\neko ’tha vāpy achyuta tat-samakṣhaṁ\ntat kṣhāmaye tvām aham aprameyam\n', 'yat—whatever; cha—also; avahāsa-artham—humorously; asat-kṛitaḥ—disrespectfully; asi—you were; vihāra—while at play; śhayyā—while resting; āsana—while sitting; bhojaneṣhu—while eating; ekaḥ—(when) alone; athavā—or; api—even; achyuta—Krishna, the infallible one; tat-samakṣham—before others; tat—all that; kṣhāmaye—beg for forgiveness; tvām—from you; aham—I; aprameyam—immeasurable\n', 'https://gita.github.io/gita/data/verse_recitation/11/456.mp3', 11, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (457, 'पितासि लोकस्य चराचरस्य\n\nत्वमस्य पूज्यश्च गुरुर्गरीयान्।\n\nन त्वत्समोऽस्त्यभ्यधिकः कुतोऽन्यो\n\nलोकत्रयेऽप्यप्रतिमप्रभाव।।11.43।।\n ', 'pitāsi lokasya charācharasya\ntvam asya pūjyaśh cha gurur garīyān\nna tvat-samo ’sty abhyadhikaḥ kuto ’nyo\nloka-traye ’py apratima-prabhāva\n', 'pitā—the father; asi—you are; lokasya—of the entire universe; chara—moving; acharasya—nonmoving; tvam—you; asya—of this; pūjyaḥ—worshipable; cha—and; guruḥ—spiritual master; garīyān—glorious; na—not; tvat-samaḥ—equal to you; asti—is; abhyadhikaḥ—greater; kutaḥ—who is?; anyaḥ—other; loka-traye—in the three worlds; api—even; apratima-prabhāva—possessor of incomparable power\n', 'https://gita.github.io/gita/data/verse_recitation/11/457.mp3', 11, 43);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (458, 'तस्मात्प्रणम्य प्रणिधाय कायं\n\nप्रसादये त्वामहमीशमीड्यम्।\n\nपितेव पुत्रस्य सखेव सख्युः\n\nप्रियः प्रियायार्हसि देव सोढुम्।।11.44।।\n ', 'tasmāt praṇamya praṇidhāya kāyaṁ\nprasādaye tvām aham īśham īḍyam\npiteva putrasya sakheva sakhyuḥ\npriyaḥ priyāyārhasi deva soḍhum\n', 'tasmāt—therefore; praṇamya—bowing down; praṇidhāya—prostrating; kāyam—the body; prasādaye—to implore grace; tvām—your; aham—I; īśham—the Supreme Lord; īḍyam—adorable; pitā—father; iva—as; putrasya—with a son; sakhā—friend; iva—as; sakhyuḥ—with a friend; priyaḥ—a lover; priyāyāḥ—with the beloved; arhasi—you should; deva—Lord; soḍhum—forgive\n', 'https://gita.github.io/gita/data/verse_recitation/11/458.mp3', 11, 44);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (459, 'अदृष्टपूर्वं हृषितोऽस्मि दृष्ट्वा\n\nभयेन च प्रव्यथितं मनो मे।\n\nतदेव मे दर्शय देव रूपं\n\nप्रसीद देवेश जगन्निवास।।11.45।।\n ', 'adṛiṣhṭa-pūrvaṁ hṛiṣhito ’smi dṛiṣhṭvā\nbhayena cha pravyathitaṁ mano me\ntad eva me darśhaya deva rūpaṁ\nprasīda deveśha jagan-nivāsa\n', 'adṛiṣhṭa-pūrvam—that which has not been seen before; hṛiṣhitaḥ—great joy; asmi—I am; dṛiṣhṭvā—having seen; bhayena—with fear; cha—yet; pravyathitam—trembles; manaḥ—mind; me—my; tat—that; eva—certainly; me—to me; darśhaya—show; deva—Lord; rūpam—form; prasīda—please have mercy; deva-īśha—God of gods; jagat-nivāsa—abode of the universe\n', 'https://gita.github.io/gita/data/verse_recitation/11/459.mp3', 11, 45);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (460, 'किरीटिनं गदिनं चक्रहस्त\n\nमिच्छामि त्वां द्रष्टुमहं तथैव।\n\nतेनैव रूपेण चतुर्भुजेन\n\nसहस्रबाहो भव विश्वमूर्ते।।11.46।।\n ', 'kirīṭinaṁ gadinaṁ chakra-hastam\nichchhāmi tvāṁ draṣhṭum ahaṁ tathaiva\ntenaiva rūpeṇa chatur-bhujena\nsahasra-bāho bhava viśhva-mūrte\n', 'kirīṭinam—wearing the crown; gadinam—carrying the mace; chakra-hastam—disc in hand; ichchhāmi—I wish; tvām—you; draṣhṭum—to see; aham—I; tathā eva—similarly; tena eva—in that; rūpeṇa—form; chatuḥ-bhujena—four-armed; sahasra-bāho—thousand-armed one; bhava—be; viśhwa-mūrte—universal form\n', 'https://gita.github.io/gita/data/verse_recitation/11/460.mp3', 11, 46);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (461, 'श्री भगवानुवाच\n\nमया प्रसन्नेन तवार्जुनेदं\n\nरूपं परं दर्शितमात्मयोगात्।\n\nतेजोमयं विश्वमनन्तमाद्यं\n\nयन्मे त्वदन्येन न दृष्टपूर्वम्।।11.47।।\n ', 'śhrī-bhagavān uvācha\nmayā prasannena tavārjunedaṁ\nrūpaṁ paraṁ darśhitam ātma-yogāt\ntejo-mayaṁ viśhvam anantam ādyaṁ\nyan me tvad anyena na dṛiṣhṭa-pūrvam\n', 'śhrī-bhagavān uvācha—the Blessed Lord said; mayā—by me; prasannena—being pleased; tava—with you; arjuna—Arjun; idam—this; rūpam—form; param—divine; darśhitam—shown; ātma-yogāt—by my Yogmaya power; tejaḥ-mayam—resplendent; viśhwam—cosmic; anantam—unlimited; ādyam—primeval; yat—which; me—my; tvat anyena—other than you; na dṛiṣhṭa-pūrvam—no one has ever seen\n', 'https://gita.github.io/gita/data/verse_recitation/11/461.mp3', 11, 47);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (462, 'न वेदयज्ञाध्ययनैर्न दानै\n\nर्न च क्रियाभिर्न तपोभिरुग्रैः।\n\nएवंरूपः शक्य अहं नृलोके\n\nद्रष्टुं त्वदन्येन कुरुप्रवीर।।11.48।।\n ', 'na veda-yajñādhyayanair na dānair\nna cha kriyābhir na tapobhir ugraiḥ\nevaṁ-rūpaḥ śhakya ahaṁ nṛi-loke\ndraṣhṭuṁ tvad anyena kuru-pravīra\n', 'na—not; veda-yajña—by performance of sacrifice; adhyayanaiḥ—by study of the Vedas; na—nor; dānaiḥ—by charity; na—nor; cha—and; kriyābhiḥ—by rituals; na—not; tapobhiḥ—by austerities; ugraiḥ—severe; evam-rūpaḥ—in this form; śhakyaḥ—possible; aham—I; nṛi-loke—in the world of the mortals; draṣhṭum—to be seen; tvat—than you; anyena—by another; kuru-pravīra—the best of the Kuru warriors\n', 'https://gita.github.io/gita/data/verse_recitation/11/462.mp3', 11, 48);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (463, 'मा ते व्यथा मा च विमूढभावो\n\nदृष्ट्वा रूपं घोरमीदृङ्ममेदम्।\n\nव्यपेतभीः प्रीतमनाः पुनस्त्वं\n\nतदेव मे रूपमिदं प्रपश्य।।11.49।।\n ', 'mā te vyathā mā cha vimūḍha-bhāvo\ndṛiṣhṭvā rūpaṁ ghoram īdṛiṅ mamedam\nvyapeta-bhīḥ prīta-manāḥ punas tvaṁ\ntad eva me rūpam idaṁ prapaśhya\n', 'mā te—you shout not be; vyathā—afraid; mā—not; cha—and; vimūḍha-bhāvaḥ—bewildered state; dṛiṣhṭvā—on seeing; rūpam—form; ghoram—terrible; īdṛik—such; mama—of mine; idam—this; vyapeta-bhīḥ—free from fear; prīta-manāḥ—cheerful mind; punaḥ—again; tvam—you; tat eva—that very; me—my; rūpam—form; idam—this; prapaśhya—behold\n', 'https://gita.github.io/gita/data/verse_recitation/11/463.mp3', 11, 49);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (464, 'सञ्जय उवाच\n\nइत्यर्जुनं वासुदेवस्तथोक्त्वा\n\nस्वकं रूपं दर्शयामास भूयः।\n\nआश्वासयामास च भीतमेनं\n\nभूत्वा पुनः सौम्यवपुर्महात्मा।।11.50।।\n ', 'sañjaya uvācha\nity arjunaṁ vāsudevas tathoktvā\nsvakaṁ rūpaṁ darśhayām āsa bhūyaḥ\nāśhvāsayām āsa cha bhītam enaṁ\nbhūtvā punaḥ saumya-vapur mahātmā\n', 'sañjayaḥ uvācha—Sanjay said; iti—thus; arjunam—to Arjun; vāsudevaḥ—Krishna, the son of Vasudev; tathā—in that way; uktvā—having spoken; svakam—his personal; rūpam—form; darśhayām āsa—displayed; bhūyaḥ—again; āśhvāsayām āsa—consoled; cha—and; bhītam—frightened; enam—him; bhūtvā—becoming; punaḥ—again; saumya-vapuḥ—the gentle (two-armed) form; mahā-ātmā—the compassionate\n', 'https://gita.github.io/gita/data/verse_recitation/11/464.mp3', 11, 50);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (465, 'अर्जुन उवाच\n\nदृष्ट्वेदं मानुषं रूपं तवसौम्यं जनार्दन।\n\nइदानीमस्मि संवृत्तः सचेताः प्रकृतिं गतः।।11.51।।\n ', 'arjuna uvācha\ndṛiṣhṭvedaṁ mānuṣhaṁ rūpaṁ tava saumyaṁ janārdana\nidānīm asmi saṁvṛittaḥ sa-chetāḥ prakṛitiṁ gataḥ\n', 'arjunaḥ uvācha—Arjun said; dṛiṣhṭvā—seeing; idam—this; mānuṣham—human; rūpam—form; tava—your; saumyam—gentle; janārdana—he who looks after the public, Krishna; idānīm—now; asmi—I am; saṁvṛittaḥ—composed; sa-chetāḥ—in my mind; prakṛitim—to normality; gataḥ—have become\n', 'https://gita.github.io/gita/data/verse_recitation/11/465.mp3', 11, 51);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (466, 'श्री भगवानुवाच\n\nसुदुर्दर्शमिदं रूपं दृष्टवानसि यन्मम।\n\nदेवा अप्यस्य रूपस्य नित्यं दर्शनकाङ्क्षिणः।।11.52।।\n ', 'śhrī-bhagavān uvācha\nsu-durdarśham idaṁ rūpaṁ dṛiṣhṭavān asi yan mama\ndevā apy asya rūpasya nityaṁ darśhana-kāṅkṣhiṇaḥ\n', 'śhrī-bhagavān uvācha—the Supreme Lord said; su-durdarśham—exceedingly difficult to behold; idam—this; rūpam—form; dṛiṣhṭavān asi—that you are seeing; yat—which; mama—of mine; devāḥ—the celestial gods; api—even; asya—this; rūpasya—form; nityam—eternally; darśhana-kāṅkṣhiṇaḥ—aspiring to see;\n', 'https://gita.github.io/gita/data/verse_recitation/11/466.mp3', 11, 52);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (467, 'नाहं वेदैर्न तपसा न दानेन न चेज्यया।\n\nशक्य एवंविधो द्रष्टुं दृष्टवानसि मां यथा।।11.53।।\n ', 'nāhaṁ vedair na tapasā na dānena na chejyayā\nśhakya evaṁ-vidho draṣhṭuṁ dṛiṣhṭavān asi māṁ yathā\n', 'na—never; aham—I; vedaiḥ—by study of the Vedas; na—never; tapasā—by serious penances; na—never; dānena—by charity; na—never; cha—also; ijyayā—by worship; śhakyaḥ—it is possible; evam-vidhaḥ—like this; draṣhṭum—to see; dṛiṣhṭavān—seeing; asi—you are; mām—me; yathā—as\n', 'https://gita.github.io/gita/data/verse_recitation/11/467.mp3', 11, 53);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (468, 'भक्त्या त्वनन्यया शक्यमहमेवंविधोऽर्जुन।\n\nज्ञातुं दृष्टुं च तत्त्वेन प्रवेष्टुं च परंतप।।11.54।।\n ', 'bhaktyā tv ananyayā śhakya aham evaṁ-vidho ’rjuna\njñātuṁ draṣhṭuṁ cha tattvena praveṣhṭuṁ cha parantapa\n', 'bhaktyā—by devotion; tu—alone; ananyayā—unalloyed; śhakyaḥ—possible; aham—I; evam-vidhaḥ—like this; arjuna—Arjun; jñātum—to be known; draṣhṭum—to be seen; cha—and; tattvena—truly; praveṣhṭum—to enter into (union with me); cha—and; parantapa—scorcher of foes\n', 'https://gita.github.io/gita/data/verse_recitation/11/468.mp3', 11, 54);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (469, 'मत्कर्मकृन्मत्परमो मद्भक्तः सङ्गवर्जितः।\n\nनिर्वैरः सर्वभूतेषु यः स मामेति पाण्डव।।11.55।।\n ', 'mat-karma-kṛin mat-paramo mad-bhaktaḥ saṅga-varjitaḥ\nnirvairaḥ sarva-bhūteṣhu yaḥ sa mām eti pāṇḍava\n', 'mat-karma-kṛit—perform duties for my sake; mat-paramaḥ—considering me the Supreme; mat-bhaktaḥ—devoted to me; saṅga-varjitaḥ—free from attachment; nirvairaḥ—without malice; sarva-bhūteṣhu—toward all entities; yaḥ—who; saḥ—he; mām—to me; eti—comes; pāṇḍava—Arjun, the son of Pandu\n', 'https://gita.github.io/gita/data/verse_recitation/11/469.mp3', 11, 55);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (470, 'अर्जुन उवाचएवं सततयुक्ता ये भक्तास्त्वां पर्युपासते।येचाप्यक्षरमव्यक्तं तेषां के योगवित्तमाः।।12.1।।', 'arjuna uvācha\nevaṁ satata-yuktā ye bhaktās tvāṁ paryupāsate\nye chāpy akṣharam avyaktaṁ teṣhāṁ ke yoga-vittamāḥ\n', 'arjunaḥ uvācha—Arjun said; evam—thus; satata—steadfastly; yuktāḥ—devoted; ye—those; bhaktāḥ—devotees; tvām—you; paryupāsate—worship; ye—those; cha—and; api—also; akṣharam—the imperishable; avyaktam—the formless Brahman; teṣhām—of them; ke—who; yoga-vit-tamāḥ—more perfect in Yog\n', 'https://gita.github.io/gita/data/verse_recitation/12/470.mp3', 12, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (471, 'श्री भगवानुवाचमय्यावेश्य मनो ये मां नित्ययुक्ता उपासते।श्रद्धया परयोपेतास्ते मे युक्ततमा मताः।।12.2।।', 'śhrī-bhagavān uvācha\nmayy āveśhya mano ye māṁ nitya-yuktā upāsate\nśhraddhayā parayopetās te me yuktatamā matāḥ\n', 'śhrī-bhagavān uvācha—the Blessed Lord said; mayi—on me; āveśhya—fix; manaḥ—the mind; ye—those; mām—me; nitya yuktāḥ—always engaged; upāsate—worship; śhraddhayā—with faith; parayā—best; upetāḥ—endowed; te—they; me—by me; yukta-tamāḥ—situated highest in Yog; matāḥ—I consider\n', 'https://gita.github.io/gita/data/verse_recitation/12/471.mp3', 12, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (472, 'ये त्वक्षरमनिर्देश्यमव्यक्तं पर्युपासते।सर्वत्रगमचिन्त्यं च कूटस्थमचलं ध्रुवम्।।12.3।।', 'ye tv akṣharam anirdeśhyam avyaktaṁ paryupāsate\nsarvatra-gam achintyañcha kūṭa-stham achalandhruvam\n sanniyamyendriya-grāmaṁ sarvatra sama-buddhayaḥ\nte prāpnuvanti mām eva sarva-bhūta-hite ratāḥ\n', 'ye—who; tu—but; akṣharam—the imperishable; anirdeśhyam—the indefinable; avyaktam—the unmanifest; paryupāsate—worship; sarvatra-gam—the all-pervading; achintyam—the unthinkable; cha—and; kūṭa-stham—the unchanging; achalam—the immovable; dhruvam—the eternal; \n sanniyamya—restraining; indriya-grāmam—the senses; sarvatra—everywhere; sama-buddhayaḥ—even-minded; te—they; prāpnuvanti—attain; mām—me; eva—also; sarva-bhūta-hite—in the welfare of all beings; ratāḥ—engaged\n', 'https://gita.github.io/gita/data/verse_recitation/12/472.mp3', 12, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (473, 'संनियम्येन्द्रियग्रामं सर्वत्र समबुद्धयः।ते प्राप्नुवन्ति मामेव सर्वभूतहिते रताः।।12.4।।', 'sanniyamyendriya-grāmaṁ  sarvatra    sama-buddhayaḥ  te  prāpnuvanti mām eva sarva-bhūta-hite    ratāḥ', 'sanniyamya-controlling;    indriya-grāmam—all  the senses; sarvatra—everywhere;     sama-buddayaḥ—equally  disposed;   te-they;    prāpnuvanti—achieve;    mām—unto    Me; eva—certainly;  sarva-bhūtahite—all living  entities\'   welfare;    ratāḥ—engaged.', 'https://gita.github.io/gita/data/verse_recitation/12/473.mp3', 12, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (474, 'क्लेशोऽधिकतरस्तेषामव्यक्तासक्तचेतसाम्।\n\nअव्यक्ता हि गतिर्दुःखं देहवद्भिरवाप्यते।।12.5।।\n ', 'kleśho ’dhikataras teṣhām avyaktāsakta-chetasām\navyaktā hi gatir duḥkhaṁ dehavadbhir avāpyate\n', 'kleśhaḥ—tribulations; adhika-taraḥ—full of; teṣhām—of those; avyakta—to the unmanifest; āsakta—attached; chetasām—whose minds; avyaktā—the unmanifest; hi—indeed; gatiḥ—path; duḥkham—exceeding difficulty; deha-vadbhiḥ—for the embodied; avāpyate—is reached\n', 'https://gita.github.io/gita/data/verse_recitation/12/474.mp3', 12, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (475, 'ये तु सर्वाणि कर्माणि मयि संन्यस्य मत्पराः।अनन्येनैव योगेन मां ध्यायन्त उपासते।।12.6।।', 'ye tu sarvāṇi karmāṇi mayi sannyasya mat-paraḥ\nananyenaiva yogena māṁ dhyāyanta upāsate\n', 'ye—who; tu—but; sarvāṇi—all; karmāṇi—actions; mayi—to me; sannyasya—dedicating; mat-paraḥ—regarding me as the Supreme goal; ananyena—exclusively; eva—certainly; yogena—with devotion; mām—me; dhyāyantaḥ—meditating; upāsate—worship;\n', 'https://gita.github.io/gita/data/verse_recitation/12/475.mp3', 12, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (476, 'तेषामहं समुद्धर्ता मृत्युसंसारसागरात्।भवामि नचिरात्पार्थ मय्यावेशितचेतसाम्।।12.7।।', 'teṣhām ahaṁ samuddhartā mṛityu-saṁsāra-sāgarāt\nbhavāmi na chirāt pārtha mayy āveśhita-chetasām\n', 'teṣhām—of those; aham—I; samuddhartā—the deliverer; mṛityu-saṁsāra-sāgarāt—from the ocean of birth and death; bhavāmi—(I) become; na—not; chirāt—after a long time; pārtha—Arjun, the son of Pritha; mayi—with me; āveśhita chetasām—of those whose consciousness is united\n', 'https://gita.github.io/gita/data/verse_recitation/12/476.mp3', 12, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (477, 'मय्येव मन आधत्स्व मयि बुद्धिं निवेशय।निवसिष्यसि मय्येव अत ऊर्ध्वं न संशयः।।12.8।।', 'mayy eva mana ādhatsva mayi buddhiṁ niveśhaya\nnivasiṣhyasi mayy eva ata ūrdhvaṁ na sanśhayaḥ\n', 'mayi—on me; eva—alone; manaḥ—mind; ādhatsva—fix; mayi—on me; buddhim—intellect; niveśhaya—surrender; nivasiṣhyasi—you shall always live; mayi—in me; eva—alone; ataḥ ūrdhvam—thereafter; na—not; sanśhayaḥ—doubt\n', 'https://gita.github.io/gita/data/verse_recitation/12/477.mp3', 12, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (478, 'अथ चित्तं समाधातुं न शक्नोषि मयि स्थिरम्।अभ्यासयोगेन ततो मामिच्छाप्तुं धनञ्जय।।12.9।।', 'atha chittaṁ samādhātuṁ na śhaknoṣhi mayi sthiram\nabhyāsa-yogena tato mām ichchhāptuṁ dhanañjaya\n', 'atha—if; chittam—mind; samādhātum—to fix; na śhaknoṣhi—(you) are unable; mayi—on me; sthiram—steadily; abhyāsa-yogena—by uniting with God through repeated practice; tataḥ—then; mām—me; ichchhā—desire; āptum—to attain; dhanañjaya—Arjun, the conqueror of wealth\n', 'https://gita.github.io/gita/data/verse_recitation/12/478.mp3', 12, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (479, 'अभ्यासेऽप्यसमर्थोऽसि मत्कर्मपरमो भव।मदर्थमपि कर्माणि कुर्वन् सिद्धिमवाप्स्यसि।।12.10।।', 'abhyāse ’py asamartho ’si mat-karma-paramo bhava\nmad-artham api karmāṇi kurvan siddhim avāpsyasi\n', 'abhyāse—in practice; api—if; asamarthaḥ—unable; asi—you; mat-karma paramaḥ—devotedly work for me; bhava—be; mat-artham—for my sake; api—also; karmāṇi—work; kurvan—performing; siddhim—perfection; avāpsyasi—you shall achieve\n', 'https://gita.github.io/gita/data/verse_recitation/12/479.mp3', 12, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (480, 'अथैतदप्यशक्तोऽसि कर्तुं मद्योगमाश्रितः।सर्वकर्मफलत्यागं ततः कुरु यतात्मवान्।।12.11।।', 'athaitad apy aśhakto ’si kartuṁ mad-yogam āśhritaḥ\nsarva-karma-phala-tyāgaṁ tataḥ kuru yatātmavān\n', 'atha—if; etat—this; api—even; aśhaktaḥ—unable; asi—you are; kartum—to work; mad-yogam—with devotion to me; āśhritaḥ—taking refuge; sarva-karma—of all actions; phala-tyāgam—to renounce the fruits; tataḥ—then; kuru—do; yata-ātma-vān—be situated in the self\n', 'https://gita.github.io/gita/data/verse_recitation/12/480.mp3', 12, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (481, 'श्रेयो हि ज्ञानमभ्यासाज्ज्ञानाद्ध्यानं विशिष्यते।ध्यानात्कर्मफलत्यागस्त्यागाच्छान्तिरनन्तरम्।।12.12।।', 'śhreyo hi jñānam abhyāsāj jñānād dhyānaṁ viśhiṣhyate\ndhyānāt karma-phala-tyāgas tyāgāch chhāntir anantaram\n', 'śhreyaḥ—better; hi—for; jñānam—knowledge; abhyāsāt—than (mechanical) practice; jñānāt—than knowledge; dhyānam—meditation; viśhiṣhyate—better; dhyānāt—than meditation; karma-phala-tyāgaḥ—renunciation of the fruits of actions; tyāgāt—renunciation; śhāntiḥ—peace; anantaram—immediately\n', 'https://gita.github.io/gita/data/verse_recitation/12/481.mp3', 12, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (482, 'अद्वेष्टा सर्वभूतानां मैत्रः करुण एव च।निर्ममो निरहङ्कारः समदुःखसुखः क्षमी।।12.13।।', 'adveṣhṭā sarva-bhūtānāṁ maitraḥ karuṇa eva cha\nnirmamo nirahankāraḥ sama-duḥkha-sukhaḥ kṣhamī\n', 'adveṣhṭā—free from malice; sarva-bhūtānām—toward all living beings; maitraḥ—friendly; karuṇaḥ—compassionate; eva—indeed; cha—and; nirmamaḥ—free from attachment to possession; nirahankāraḥ—free from egoism; sama—equipoised; duḥkha—distress; sukhaḥ—happiness; kṣhamī—forgiving;\n', 'https://gita.github.io/gita/data/verse_recitation/12/482.mp3', 12, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (483, 'सन्तुष्टः सततं योगी यतात्मा दृढनिश्चयः।मय्यर्पितमनोबुद्धिर्यो मद्भक्तः स मे प्रियः।।12.14।।', 'santuṣhṭaḥ satataṁ yogī yatātmā dṛiḍha-niśhchayaḥ\nmayy arpita-mano-buddhir yo mad-bhaktaḥ sa me priyaḥ\n', 'santuṣhṭaḥ—contented; satatam—steadily; yogī—united in devotion; yata-ātmā—self-controlled; dṛiḍha-niśhchayaḥ—firm in conviction; mayi—to me; arpita—dedicated; manaḥ—mind; buddhiḥ—intellect; yaḥ—who; mat-bhaktaḥ—my devotees; saḥ—they; me—to me; priyaḥ—very dear\n', 'https://gita.github.io/gita/data/verse_recitation/12/483.mp3', 12, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (484, 'यस्मान्नोद्विजते लोको लोकान्नोद्विजते च यः।हर्षामर्षभयोद्वेगैर्मुक्तो यः स च मे प्रियः।।12.15।।', 'yasmān nodvijate loko lokān nodvijate cha yaḥ\nharṣhāmarṣha-bhayodvegair mukto yaḥ sa cha me priyaḥ\n', 'yasmāt—by whom; na—not; udvijate—are agitated; lokaḥ—people; lokāt—from people; na—not; udvijate—are disturbed; cha—and; yaḥ—who; harṣha—pleasure; amarṣha—pain; bhaya—fear; udvegaiḥ—anxiety; muktaḥ—freed; yaḥ—who; saḥ—they; cha—and; me—to me; priyaḥ—very dear\n', 'https://gita.github.io/gita/data/verse_recitation/12/484.mp3', 12, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (485, 'अनपेक्षः शुचिर्दक्ष उदासीनो गतव्यथः।सर्वारम्भपरित्यागी यो मद्भक्तः स मे प्रियः।।12.16।।', 'anapekṣhaḥ śhuchir dakṣha udāsīno gata-vyathaḥ\nsarvārambha-parityāgī yo mad-bhaktaḥ sa me priyaḥ\n', 'anapekṣhaḥ—indifferent to worldly gain; śhuchiḥ—pure; dakṣhaḥ—skillful; udāsīnaḥ—without cares; gata-vyathaḥ—untroubled; sarva-ārambha—of all undertakings; parityāgī—renouncer; saḥ—who; mat-bhaktaḥ—my devotee; saḥ—he; me—to ne; priyaḥ—very dear\n', 'https://gita.github.io/gita/data/verse_recitation/12/485.mp3', 12, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (486, 'यो न हृष्यति न द्वेष्टि न शोचति न काङ्क्षति।शुभाशुभपरित्यागी भक्ितमान्यः स मे प्रियः।।12.17।।', 'yo na hṛiṣhyati na dveṣhṭi na śhochati na kāṅkṣhati\nśhubhāśhubha-parityāgī bhaktimān yaḥ sa me priyaḥ\n', 'yaḥ—who; na—neither; hṛiṣhyati—rejoice; na—nor; dveṣhṭi—despair; na—neither; śhochati—lament; na—nor; kāṅkṣhati—hanker for gain; śhubha-aśhubha-parityāgī—who renounce both good and evil deeds; bhakti-mān—full of devotion; yaḥ—who; saḥ—that person; me—to me; priyaḥ—very dear\n', 'https://gita.github.io/gita/data/verse_recitation/12/486.mp3', 12, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (487, 'समः शत्रौ च मित्रे च तथा मानापमानयोः।शीतोष्णसुखदुःखेषु समः सङ्गविवर्जितः।।12.18।।', 'samaḥ śhatrau cha mitre cha tathā mānāpamānayoḥ\nśhītoṣhṇa-sukha-duḥkheṣhu samaḥ saṅga-vivarjitaḥ\n', 'samaḥ—alike; śhatrau—to a foe; cha—and; mitre—to a friend; cha tathā—as well as; māna-apamānayoḥ—in honor and dishonor; śhīta-uṣhṇa—in cold and heat; sukha-duḥkheṣhu—in joy and sorrow; samaḥ—equipoised; saṅga-vivarjitaḥ—free from all unfavorable association;\n', 'https://gita.github.io/gita/data/verse_recitation/12/487.mp3', 12, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (488, 'तुल्यनिन्दास्तुतिर्मौनी सन्तुष्टो येनकेनचित्।अनिकेतः स्थिरमतिर्भक्ितमान्मे प्रियो नरः।।12.19।।', 'tulya-nindā-stutir maunī santuṣhṭo yena kenachit\naniketaḥ sthira-matir bhaktimān me priyo naraḥ\n', 'tulya—alike; nindā-stutiḥ—reproach and praise; maunī—silent contemplation; santuṣhṭaḥ—contented; yena kenachit—with anything; aniketaḥ—without attachment to the place of residence; sthira—firmly fixed; matiḥ—intellect; bhakti-mān—full of devotion; me—to me; priyaḥ—very dear; naraḥ—a person\n', 'https://gita.github.io/gita/data/verse_recitation/12/488.mp3', 12, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (489, 'ये तु धर्म्यामृतमिदं यथोक्तं पर्युपासते।श्रद्दधाना मत्परमा भक्तास्तेऽतीव मे प्रियाः।।12.20।।', 'ye tu dharmyāmṛitam idaṁ yathoktaṁ paryupāsate\nśhraddadhānā mat-paramā bhaktās te ’tīva me priyāḥ\n', 'ye—who; tu—indeed; dharma—of wisdom; amṛitam—nectar; idam—this; yathā—as; uktam—declared; paryupāsate—exclusive devotion; śhraddadhānāḥ—with faith; mat-paramāḥ—intent on me as the supreme goal; bhaktāḥ—devotees; te—they; atīva—exceedingly; me—to me; priyāḥ—dear\n', 'https://gita.github.io/gita/data/verse_recitation/12/489.mp3', 12, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (490, 'अर्जुन उवाच\n\nप्रकृतिं पुरुषं चैव क्षेत्रं क्षेत्रज्ञमेव च।\n\nएतद्वेदितुमिच्छामि ज्ञानं ज्ञेयं च केशव।।13.1।।\n ', 'arjuna uvācha\nprakṛitiṁ puruṣhaṁ chaiva kṣhetraṁ kṣhetra-jñam eva cha\netad veditum ichchhāmi jñānaṁ jñeyaṁ cha keśhava\n', 'arjunaḥ uvācha—Arjun said; prakṛitim—material nature; puruṣham—the enjoyer; cha—and; eva—indeed; kṣhetram—the field of activities; kṣhetra-jñam—the knower of the field; eva—even; cha—also; etat—this; veditum—to know; ichchhāmi—I wish; jñānam—knowledge; jñeyam—the goal of knowledge; cha—and; keśhava—Krishna, the killer of the demon named Keshi\n', 'https://gita.github.io/gita/data/verse_recitation/13/490.mp3', 13, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (491, 'श्री भगवानुवाचइदं शरीरं कौन्तेय क्षेत्रमित्यभिधीयते।एतद्यो वेत्ति तं प्राहुः क्षेत्रज्ञ इति तद्विदः।।13.2।।', 'śhrī-bhagavān uvācha\nidaṁ śharīraṁ kaunteya kṣhetram ity abhidhīyate\netad yo vetti taṁ prāhuḥ kṣhetra-jña iti tad-vidaḥ\n', 'śhrī-bhagavān uvācha—the Supreme Divine Lord said; idam—this; śharīram—body; kaunteya—Arjun, the son of Kunti; kṣhetram—the field of activities; iti—thus; abhidhīyate—is termed as; etat—this; yaḥ—one who; vetti—knows; tam—that person; prāhuḥ—is called; kṣhetra-jñaḥ—the knower of the field; iti—thus; tat-vidaḥ—those who discern the truth\n', 'https://gita.github.io/gita/data/verse_recitation/13/491.mp3', 13, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (492, 'क्षेत्रज्ञं चापि मां विद्धि सर्वक्षेत्रेषु भारत।\n\nक्षेत्रक्षेत्रज्ञयोर्ज्ञानं यत्तज्ज्ञानं मतं मम।।13.3।।\n ', 'kṣhetra-jñaṁ chāpi māṁ viddhi sarva-kṣhetreṣhu bhārata\nkṣhetra-kṣhetrajñayor jñānaṁ yat taj jñānaṁ mataṁ mama\n', 'kṣhetra-jñam—the knower of the field; cha—also; api—only; mām—me; viddhi—know; sarva—all; kṣhetreṣhu—in individual fields of activities; bhārata—scion of Bharat; kṣhetra—the field of activities; kṣhetra-jñayoḥ—of the knower of the field; jñānam—understanding of; yat—which; tat—that; jñānam—knowledge; matam—opinion; mama—my\n', 'https://gita.github.io/gita/data/verse_recitation/13/492.mp3', 13, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (493, 'तत्क्षेत्रं यच्च यादृक् च यद्विकारि यतश्च यत्।स च यो यत्प्रभावश्च तत्समासेन मे श्रृणु।।13.4।।', 'tat kṣhetraṁ yach cha yādṛik cha yad-vikāri yataśh cha yat\nsa cha yo yat-prabhāvaśh cha tat samāsena me śhṛiṇu\n', 'tat—that; kṣhetram—field of activities; yat—what; cha—and; yādṛik—its nature; cha—and; yat-vikāri—how change takes place in it; yataḥ—from what; cha—also; yat—what; saḥ—he; cha—also; yaḥ—who; yat-prabhāvaḥ—what his powers are; cha—and; tat—that; samāsena—in summary; me—from me; śhṛiṇu—listen\n', 'https://gita.github.io/gita/data/verse_recitation/13/493.mp3', 13, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (494, 'ऋषिभिर्बहुधा गीतं छन्दोभिर्विविधैः पृथक्।ब्रह्मसूत्रपदैश्चैव हेतुमद्भिर्विनिश्िचतैः।।13.5।।', 'ṛiṣhibhir bahudhā gītaṁ chhandobhir vividhaiḥ pṛithak\nbrahma-sūtra-padaiśh chaiva hetumadbhir viniśhchitaiḥ\n', 'ṛiṣhibhiḥ—by great sages; bahudhā—in manifold ways; gītam—sung; chhandobhiḥ—in Vedic hymns; vividhaiḥ—various; pṛithak—variously; brahma-sūtra—the Brahma Sūtra; padaiḥ—by the hymns; cha—and; eva—especially; hetu-madbhiḥ—with logic; viniśhchitaiḥ—conclusive evidence\n', 'https://gita.github.io/gita/data/verse_recitation/13/494.mp3', 13, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (495, 'महाभूतान्यहङ्कारो बुद्धिरव्यक्तमेव च।इन्द्रियाणि दशैकं च पञ्च चेन्द्रियगोचराः।।13.6।।', 'mahā-bhūtāny ahankāro buddhir avyaktam eva cha\nindriyāṇi daśhaikaṁ cha pañcha chendriya-gocharāḥ\n', 'mahā-bhūtāni—the (five) great elements; ahankāraḥ—the ego; buddhiḥ—the intellect; avyaktam—the unmanifested primordial matter; eva—indeed; cha—and; indriyāṇi—the senses; daśha-ekam—eleven; cha—and; pañcha—five; cha—and; indriya-go-charāḥ—the (five) objects of the senses;\n', 'https://gita.github.io/gita/data/verse_recitation/13/495.mp3', 13, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (496, 'इच्छा द्वेषः सुखं दुःखं सङ्घातश्चेतनाधृतिः।एतत्क्षेत्रं समासेन सविकारमुदाहृतम्।।13.7।।', 'ichchhā dveṣhaḥ sukhaṁ duḥkhaṁ saṅghātaśh chetanā dhṛitiḥ\netat kṣhetraṁ samāsena sa-vikāram udāhṛitam\n', 'ichchhā—desire; dveṣhaḥ—aversion; sukham—happiness; duḥkham—misery; saṅghātaḥ—the aggregate; chetanā—the consciousness; dhṛitiḥ—the will; etat—all these; kṣhetram—the field of activities; samāsena—comprise of; sa-vikāram—with modifications; udāhṛitam—are said\n', 'https://gita.github.io/gita/data/verse_recitation/13/496.mp3', 13, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (497, 'अमानित्वमदम्भित्वमहिंसा क्षान्तिरार्जवम्।आचार्योपासनं शौचं स्थैर्यमात्मविनिग्रहः।।13.8।।', 'amānitvam adambhitvam ahinsā kṣhāntir ārjavam\nāchāryopāsanaṁ śhauchaṁ sthairyam ātma-vinigrahaḥ\n', 'amānitvam—humbleness; adambhitvam—freedom from hypocrisy; ahinsā—non-violence; kṣhāntiḥ—forgiveness; ārjavam—simplicity; āchārya-upāsanam—service of the Guru; śhaucham—cleanliness of body and mind; sthairyam—steadfastness; ātma-vinigrahaḥ—self-control;\n', 'https://gita.github.io/gita/data/verse_recitation/13/497.mp3', 13, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (498, 'इन्द्रियार्थेषु वैराग्यमनहङ्कार एव च।जन्ममृत्युजराव्याधिदुःखदोषानुदर्शनम्।।13.9।।', 'indriyārtheṣhu vairāgyam anahankāra eva cha\njanma-mṛityu-jarā-vyādhi-duḥkha-doṣhānudarśhanam\n', 'indriya-artheṣhu—toward objects of the senses; vairāgyam—dispassion; anahankāraḥ—absence of egotism; eva cha—and also; janma—of birth; mṛityu—death; jarā—old age; vyādhi—disease; duḥkha—evils; doṣha—faults; anudarśhanam—perception;\n', 'https://gita.github.io/gita/data/verse_recitation/13/498.mp3', 13, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (499, 'असक्ितरनभिष्वङ्गः पुत्रदारगृहादिषु।नित्यं च समचित्तत्वमिष्टानिष्टोपपत्तिषु।।13.10।।', 'asaktir anabhiṣhvaṅgaḥ putra-dāra-gṛihādiṣhu\nnityaṁ cha sama-chittatvam iṣhṭāniṣhṭopapattiṣhu\n', 'asaktiḥ—non-attachment; anabhiṣhvaṅgaḥ—absence of craving; putra—children; dāra—spouse; gṛiha-ādiṣhu—home, etc; nityam—constant; cha—and; sama-chittatvam—even-mindedness; iṣhṭa—the desirable; aniṣhṭa—undesirable; upapattiṣhu—having obtained;\n', 'https://gita.github.io/gita/data/verse_recitation/13/499.mp3', 13, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (500, 'मयि चानन्ययोगेन भक्ितरव्यभिचारिणी।विविक्तदेशसेवित्वमरतिर्जनसंसदि।।13.11।।', 'mayi chānanya-yogena bhaktir avyabhichāriṇī\nvivikta-deśha-sevitvam aratir jana-sansadi\n', 'mayi—toward me; cha—also; ananya-yogena—exclusively united; bhaktiḥ—devotion; avyabhichāriṇī—constant; vivikta—solitary; deśha—places; sevitvam—inclination for; aratiḥ—aversion; jana-sansadi—for mundane society;\n', 'https://gita.github.io/gita/data/verse_recitation/13/500.mp3', 13, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (501, 'अध्यात्मज्ञाननित्यत्वं तत्त्वज्ञानार्थदर्शनम्।एतज्ज्ञानमिति प्रोक्तमज्ञानं यदतोन्यथा।।13.12।।', 'adhyātma-jñāna-nityatvaṁ tattva-jñānārtha-darśhanam\netaj jñānam iti proktam ajñānaṁ yad ato ’nyathā\n', 'adhyātma—spiritual; jñāna—knowledge; nityatvam—constancy; tattva-jñāna—knowledge of spiritual principles; artha—for; darśhanam—philosophy; etat—all this; jñānam—knowledge; iti—thus; proktam—declared; ajñānam—ignorance; yat—what; ataḥ—to this; anyathā—contrary\n', 'https://gita.github.io/gita/data/verse_recitation/13/501.mp3', 13, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (502, 'ज्ञेयं यत्तत्प्रवक्ष्यामि यज्ज्ञात्वाऽमृतमश्नुते।अनादिमत्परं ब्रह्म न सत्तन्नासदुच्यते।।13.13।।', 'jñeyaṁ yat tat pravakṣhyāmi yaj jñātvāmṛitam aśhnute\nanādi mat-paraṁ brahma na sat tan nāsad uchyate\n', 'jñeyam—ought to be known; yat—which; tat—that; pravakṣhyāmi—I shall now reveal; yat—which; jñātvā—knowing; amṛitam—immortality; aśhnute—one achieves; anādi—beginningless; mat-param—subordinate to me; brahma—Brahman; na—not; sat—existent; tat—that; na—not; asat—non-existent; uchyate—is called\n', 'https://gita.github.io/gita/data/verse_recitation/13/502.mp3', 13, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (503, 'सर्वतः पाणिपादं तत्सर्वतोऽक्षिशिरोमुखम्।सर्वतः श्रुतिमल्लोके सर्वमावृत्य तिष्ठति।।13.14।।', 'sarvataḥ pāṇi-pādaṁ tat sarvato ’kṣhi-śhiro-mukham\nsarvataḥ śhrutimal loke sarvam āvṛitya tiṣhṭhati\n', 'sarvataḥ—everywhere; pāṇi—hands; pādam—feet; tat—that; sarvataḥ—everywhere; akṣhi—eyes; śhiraḥ—heads; mukham—faces; sarvataḥ—everywhere; śhruti-mat—having ears; loke—in the universe; sarvam—everything; āvṛitya—pervades; tiṣhṭhati—exists\n', 'https://gita.github.io/gita/data/verse_recitation/13/503.mp3', 13, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (504, 'सर्वेन्द्रियगुणाभासं सर्वेन्द्रियविवर्जितम्।असक्तं सर्वभृच्चैव निर्गुणं गुणभोक्तृ च।।13.15।।', 'sarvendriya-guṇābhāsaṁ sarvendriya-vivarjitam\nasaktaṁ sarva-bhṛich chaiva nirguṇaṁ guṇa-bhoktṛi cha\n', 'sarva—all; indriya—senses; guṇa—sense-objects; ābhāsam—the perciever; sarva—all; indriya—senses; vivarjitam—devoid of; asaktam—unattached; sarva-bhṛit—the sustainer of all; cha—yet; eva—indeed; nirguṇam—beyond the three modes of material nature; guṇa-bhoktṛi—the enjoyer of the three modes of material nature; cha—although\n', 'https://gita.github.io/gita/data/verse_recitation/13/504.mp3', 13, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (505, 'बहिरन्तश्च भूतानामचरं चरमेव च।सूक्ष्मत्वात्तदविज्ञेयं दूरस्थं चान्तिके च तत्।।13.16।।', 'bahir antaśh cha bhūtānām acharaṁ charam eva cha\nsūkṣhmatvāt tad avijñeyaṁ dūra-sthaṁ chāntike cha tat\n', 'bahiḥ—outside; antaḥ—inside; cha—and; bhūtānām—all living beings; acharam—not moving; charam—moving; eva—indeed; cha—and; sūkṣhmatvāt—due to subtlety; tat—he; avijñeyam—incomprehensible; dūra-stham—very far away; cha—and; antike—very near; cha—also; tat—he\n', 'https://gita.github.io/gita/data/verse_recitation/13/505.mp3', 13, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (506, 'अविभक्तं च भूतेषु विभक्तमिव च स्थितम्।भूतभर्तृ च तज्ज्ञेयं ग्रसिष्णु प्रभविष्णु च।।13.17।।', 'avibhaktaṁ cha bhūteṣhu vibhaktam iva cha sthitam\nbhūta-bhartṛi cha taj jñeyaṁ grasiṣhṇu prabhaviṣhṇu cha\n', 'avibhaktam—indivisible; cha—although; bhūteṣhu—amongst living beings; vibhaktam—divided; iva—apparently; cha—yet; sthitam—situated; bhūta-bhartṛi—the sustainer of all beings; cha—also; tat—that; jñeyam—to be known; grasiṣhṇu—the annihilator; prabhaviṣhṇu—the creator; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/13/506.mp3', 13, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (507, 'ज्योतिषामपि तज्ज्योतिस्तमसः परमुच्यते।ज्ञानं ज्ञेयं ज्ञानगम्यं हृदि सर्वस्य विष्ठितम्।।13.18।।', 'jyotiṣhām api taj jyotis tamasaḥ param uchyate\njñānaṁ jñeyaṁ jñāna-gamyaṁ hṛidi sarvasya viṣhṭhitam\n', 'jyotiṣhām—in all luminarie; api—and; tat—that; jyotiḥ—the source of light; tamasaḥ—the darkness; param—beyond; uchyate—is said (to be); jñānam—knowledge; jñeyam—the object of knowledge; jñāna-gamyam—the goal of knowledge; hṛidi—within the heart; sarvasya—of all living beings; viṣhṭhitam—dwells\n', 'https://gita.github.io/gita/data/verse_recitation/13/507.mp3', 13, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (508, 'इति क्षेत्रं तथा ज्ञानं ज्ञेयं चोक्तं समासतः।मद्भक्त एतद्विज्ञाय मद्भावायोपपद्यते।।13.19।।', 'iti kṣhetraṁ tathā jñānaṁ jñeyaṁ choktaṁ samāsataḥ\nmad-bhakta etad vijñāya mad-bhāvāyopapadyate\n', 'iti—thus; kṣhetram—the nature of the field; tathā—and; jñānam—the meaning of knowledge; jñeyam—the object of knowledge; cha—and; uktam—revealed; samāsataḥ—in summary; mat-bhaktaḥ—my devotee; etat—this; vijñāya—having understood; mat-bhāvāya—my divine nature; upapadyate—attain\n', 'https://gita.github.io/gita/data/verse_recitation/13/508.mp3', 13, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (509, 'प्रकृतिं पुरुषं चैव विद्ध्यनादी उभावपि।विकारांश्च गुणांश्चैव विद्धि प्रकृतिसंभवान्।।13.20।।', 'prakṛitiṁ puruṣhaṁ chaiva viddhy anādī ubhāv api\nvikārānśh cha guṇānśh chaiva viddhi prakṛiti-sambhavān\n', 'prakṛitim—material nature; puruṣham—the individual souls; cha—and; eva—indeed; viddhi—know; anādī—beginningless; ubhau—both; api—and; vikārān—transformations (of the body); cha—also; guṇān—the three modes of nature; cha—and; eva—indeed; viddhi—know; prakṛiti—material energy; sambhavān—produced by\n', 'https://gita.github.io/gita/data/verse_recitation/13/509.mp3', 13, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (510, 'कार्यकारणकर्तृत्वे हेतुः प्रकृतिरुच्यते।पुरुषः सुखदुःखानां भोक्तृत्वे हेतुरुच्यते।।13.21।।', 'kārya-kāraṇa-kartṛitve hetuḥ prakṛitir uchyate\npuruṣhaḥ sukha-duḥkhānāṁ bhoktṛitve hetur uchyate\n', 'kārya—effect; kāraṇa—cause; kartṛitve—in the matter of creation; hetuḥ—the medium; prakṛitiḥ—the material energy; uchyate—is said to be; puruṣhaḥ—the individual soul; sukha-duḥkhānām—of happiness and distress; bhoktṛitve—in experiencing; hetuḥ—is responsible; uchyate—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/13/510.mp3', 13, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (511, 'पुरुषः प्रकृतिस्थो हि भुङ्क्ते प्रकृतिजान्गुणान्।कारणं गुणसङ्गोऽस्य सदसद्योनिजन्मसु।।13.22।।', 'puruṣhaḥ prakṛiti-stho hi bhuṅkte prakṛiti-jān guṇān\nkāraṇaṁ guṇa-saṅgo ’sya sad-asad-yoni-janmasu\n', 'puruṣhaḥ—the individual soul; prakṛiti-sthaḥ—seated in the material energy; hi—indeed; bhuṅkte—desires to enjoy; prakṛiti-jān—produced by the material energy; guṇān—the three modes of nature; kāraṇam—the cause; guṇa-saṅgaḥ—the attachment (to three guṇas); asya—of its; sat-asat-yoni—in superior and inferior wombs; janmasu—of birth\n', 'https://gita.github.io/gita/data/verse_recitation/13/511.mp3', 13, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (512, 'उपद्रष्टाऽनुमन्ता च भर्ता भोक्ता महेश्वरः।परमात्मेति चाप्युक्तो देहेऽस्मिन्पुरुषः परः।।13.23।।', 'upadraṣhṭānumantā cha bhartā bhoktā maheśhvaraḥ\nparamātmeti chāpy ukto dehe ’smin puruṣhaḥ paraḥ\n', 'upadraṣhṭā—the witness; anumantā—the permitter; cha—and; bhartā—the supporter; bhoktā—the transcendental enjoyer; mahā-īśhvaraḥ—the ultimate controller; parama-ātmā—Superme Soul; iti—that; cha api—and also; uktaḥ—is said; dehe—within the body; asmin—this; puruṣhaḥ paraḥ—the Supreme Lord\n', 'https://gita.github.io/gita/data/verse_recitation/13/512.mp3', 13, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (513, 'य एवं वेत्ति पुरुषं प्रकृतिं च गुणैःसह।सर्वथा वर्तमानोऽपि न स भूयोऽभिजायते।।13.24।।', 'ya evaṁ vetti puruṣhaṁ prakṛitiṁ cha guṇaiḥ saha\nsarvathā vartamāno ’pi na sa bhūyo ’bhijāyate\n', 'yaḥ—who; evam—thus; vetti—understand; puruṣham—Puruṣh; prakṛitim—the material nature; cha—and; guṇaiḥ—the three modes of nature; saha—with; sarvathā—in every way; vartamānaḥ—situated; api—although; na—not; saḥ—they; bhūyaḥ—again; abhijāyate—take birth\n', 'https://gita.github.io/gita/data/verse_recitation/13/513.mp3', 13, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (514, 'ध्यानेनात्मनि पश्यन्ति केचिदात्मानमात्मना।अन्ये सांख्येन योगेन कर्मयोगेन चापरे।।13.25।।', 'dhyānenātmani paśhyanti kechid ātmānam ātmanā\nanye sānkhyena yogena karma-yogena chāpare\n', 'dhyānena—through meditation; ātmani—within one’s heart; paśhyanti—percieve; kechit—some; ātmānam—the Supreme soul; ātmanā—by the mind; anye—others; sānkhyena—through cultivation of knowledge; yogena—the yog system; karma-yogena—union with God with through path of action; cha—and; apare—others\n', 'https://gita.github.io/gita/data/verse_recitation/13/514.mp3', 13, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (515, 'अन्ये त्वेवमजानन्तः श्रुत्वाऽन्येभ्य उपासते।तेऽपि चातितरन्त्येव मृत्युं श्रुतिपरायणाः।।13.26।।', 'anye tv evam ajānantaḥ śhrutvānyebhya upāsate\nte ’pi chātitaranty eva mṛityuṁ śhruti-parāyaṇāḥ\n', 'anye—others; tu—still; evam—thus; ajānantaḥ—those who are unaware (of spiritual paths); śhrutvā—by hearing; anyebhyaḥ—from others; upāsate—begin to worship; te—they; api—also; cha—and; atitaranti—cross over; eva—even; mṛityum—death; śhruti-parāyaṇāḥ—devotion to hearing (from saints)\n', 'https://gita.github.io/gita/data/verse_recitation/13/515.mp3', 13, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (516, 'यावत्सञ्जायते किञ्चित्सत्त्वं स्थावरजङ्गमम्।क्षेत्रक्षेत्रज्ञसंयोगात्तद्विद्धि भरतर्षभ।।13.27।।', 'yāvat sañjāyate kiñchit sattvaṁ sthāvara-jaṅgamam\nkṣhetra-kṣhetrajña-sanyogāt tad viddhi bharatarṣhabha\n', 'yāvat—whatever; sañjāyate—manifesting; kiñchit—anything; sattvam—being; sthāvara—unmoving; jaṅgamam—moving; kṣhetra—field of activities; kṣhetra-jña—knower of the field; sanyogāt—combination of; tat—that; viddhi—know; bharata-ṛiṣhabha—best of the Bharatas\n', 'https://gita.github.io/gita/data/verse_recitation/13/516.mp3', 13, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (517, 'समं सर्वेषु भूतेषु तिष्ठन्तं परमेश्वरम्।विनश्यत्स्वविनश्यन्तं यः पश्यति स पश्यति।।13.28।।', 'samaṁ sarveṣhu bhūteṣhu tiṣhṭhantaṁ parameśhvaram\nvinaśhyatsv avinaśhyantaṁ yaḥ paśhyati sa paśhyati\n', 'samam—equally; sarveṣhu—in all; bhūteṣhu—beings; tiṣhṭhan-tam—accompanying; parama-īśhvaram—Supreme Soul; vinaśhyatsu—amongst the perishable; avinaśhyantam—the imperishable; yaḥ—who; paśhyati—see; saḥ—they; paśhyati—perceive\n', 'https://gita.github.io/gita/data/verse_recitation/13/517.mp3', 13, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (518, 'समं पश्यन्हि सर्वत्र समवस्थितमीश्वरम्।न हिनस्त्यात्मनाऽऽत्मानं ततो याति परां गतिम्।।13.29।।', 'samaṁ paśhyan hi sarvatra samavasthitam īśhvaram\nna hinasty ātmanātmānaṁ tato yāti parāṁ gatim\n', 'samam—equally; paśhyan—see; hi—indeed; sarvatra—everywhere; samavasthitam—equally present; īśhvaram—God as the Supreme soul; na—do not; hinasti—degrade; ātmanā—by one’s mind; ātmānam—the self; tataḥ—thereby; yāti—reach; parām—the supreme; gatim—destination\n', 'https://gita.github.io/gita/data/verse_recitation/13/518.mp3', 13, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (519, 'प्रकृत्यैव च कर्माणि क्रियमाणानि सर्वशः।यः पश्यति तथाऽऽत्मानमकर्तारं स पश्यति।।13.30।।', 'prakṛityaiva cha karmāṇi kriyamāṇāni sarvaśhaḥ\nyaḥ paśhyati tathātmānam akartāraṁ sa paśhyati\n', 'prakṛityā—by material nature; eva—truly; cha—also; karmāṇi—actions; kriyamāṇāni—are performed; sarvaśhaḥ—all; yaḥ—who; paśhyati—see; tathā—also; ātmānam—(embodied) soul; akartāram—actionless; saḥ—they; paśhyati—see\n', 'https://gita.github.io/gita/data/verse_recitation/13/519.mp3', 13, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (520, 'यदा भूतपृथग्भावमेकस्थमनुपश्यति।तत एव च विस्तारं ब्रह्म सम्पद्यते तदा।।13.31।।', 'yadā bhūta-pṛithag-bhāvam eka-stham anupaśhyati\ntata eva cha vistāraṁ brahma sampadyate tadā\n', 'yadā—when; bhūta—living entities; pṛithak-bhāvam—diverse variety; eka-stham—situated in the same place; anupaśhyati—see; tataḥ—thereafter; eva—indeed; cha—and; vistāram—born from; brahma—Brahman; sampadyate—(they) attain; tadā—then\n', 'https://gita.github.io/gita/data/verse_recitation/13/520.mp3', 13, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (521, 'अनादित्वान्निर्गुणत्वात्परमात्मायमव्ययः।शरीरस्थोऽपि कौन्तेय न करोति न लिप्यते।।13.32।।', 'anāditvān nirguṇatvāt paramātmāyam avyayaḥ\nśharīra-stho ’pi kaunteya na karoti na lipyate\n', 'anāditvāt—being without beginning; nirguṇatvāt—being devoid of any material qualities; parama—the Supreme; ātmā—soul; ayam—this; avyayaḥ—imperishable; śharīra-sthaḥ—dwelling in the body; api—although; kaunteya—Arjun, the the son of Kunti; na—neither; karoti—acts; na—nor; lipyate—is tainted\n', 'https://gita.github.io/gita/data/verse_recitation/13/521.mp3', 13, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (522, 'यथा सर्वगतं सौक्ष्म्यादाकाशं नोपलिप्यते।सर्वत्रावस्थितो देहे तथाऽऽत्मा नोपलिप्यते।।13.33।।', 'yathā sarva-gataṁ saukṣhmyād ākāśhaṁ nopalipyate\nsarvatrāvasthito dehe tathātmā nopalipyate\n', 'yathā—as; sarva-gatam—all-pervading; saukṣhmyāt—due to subtlety; ākāśham—the space; na—not; upalipyate—is contaminated; sarvatra—everywhere; avasthitaḥ—situated; dehe—the body; tathā—similarly; ātmā—the soul; na—not; upalipyate—is contaminated\n', 'https://gita.github.io/gita/data/verse_recitation/13/522.mp3', 13, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (523, 'यथा प्रकाशयत्येकः कृत्स्नं लोकमिमं रविः।क्षेत्रं क्षेत्री तथा कृत्स्नं प्रकाशयति भारत।।13.34।।', 'yathā prakāśhayaty ekaḥ kṛitsnaṁ lokam imaṁ raviḥ\nkṣhetraṁ kṣhetrī tathā kṛitsnaṁ prakāśhayati bhārata\n', 'yathā—as; prakāśhayati—illumines; ekaḥ—one; kṛitsnam—entire; lokam—solar system; imam—this; raviḥ—sun; kṣhetram—the body; kṣhetrī—the soul; tathā—so; kṛitsnam—entire; prakāśhayati—illumine; bhārata—Arjun, the son of Bharat\n', 'https://gita.github.io/gita/data/verse_recitation/13/523.mp3', 13, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (524, 'क्षेत्रक्षेत्रज्ञयोरेवमन्तरं ज्ञानचक्षुषा।भूतप्रकृतिमोक्षं च ये विदुर्यान्ति ते परम्।।13.35।।', 'kṣhetra-kṣhetrajñayor evam antaraṁ jñāna-chakṣhuṣhā\nbhūta-prakṛiti-mokṣhaṁ cha ye vidur yānti te param\n', 'kṣhetra—the body; kṣhetra-jñayoḥ—of the knower of the body; evam—thus; antaram—the difference; jñāna-chakṣhuṣhā—with the eyes of knowledge; bhūta—the living entity; prakṛiti-mokṣham—release from material nature; cha—and; ye—who; viduḥ—know; yānti—approach; te—they; param—the Supreme\n', 'https://gita.github.io/gita/data/verse_recitation/13/524.mp3', 13, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (525, 'श्री भगवानुवाचपरं भूयः प्रवक्ष्यामि ज्ञानानां ज्ञानमुत्तमम्।यज्ज्ञात्वा मुनयः सर्वे परां सिद्धिमितो गताः।।14.1।।', 'śhrī-bhagavān uvācha\nparaṁ bhūyaḥ pravakṣhyāmi jñānānāṁ jñānam uttamam\nyaj jñātvā munayaḥ sarve parāṁ siddhim ito gatāḥ\n', 'śhrī-bhagavān uvācha—the Divine Lord said; param—supreme; bhūyaḥ—again; pravakṣhyāmi—I shall explain; jñānānām—of all knowledge; jñānam uttamam—the supreme wisdom; yat—which; jñātvā—knowing; munayaḥ—saints; sarve—all; parām—highest; siddhim—perfection; itaḥ—through this; gatāḥ—attained\n', 'https://gita.github.io/gita/data/verse_recitation/14/525.mp3', 14, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (526, 'इदं ज्ञानमुपाश्रित्य मम साधर्म्यमागताः।सर्गेऽपि नोपजायन्ते प्रलये न व्यथन्ति च।।14.2।।', 'idaṁ jñānam upāśhritya mama sādharmyam āgatāḥ\nsarge ’pi nopajāyante pralaye na vyathanti cha\n', 'idam—this; jñānam—wisdom; upāśhritya—take refuge in; mama—mine; sādharmyam—of similar nature; āgatāḥ—having attained; sarge—at the time of creation; api—even; na—not; upajāyante—are born; pralaye—at the time of dissolution; na-vyathanti—they will not experience misery; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/14/526.mp3', 14, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (527, 'मम योनिर्महद्ब्रह्म तस्मिन् गर्भं दधाम्यहम्।संभवः सर्वभूतानां ततो भवति भारत।।14.3।।', 'mama yonir mahad brahma tasmin garbhaṁ dadhāmy aham\nsambhavaḥ sarva-bhūtānāṁ tato bhavati bhārata\n', 'mama—my; yoniḥ—womb; mahat brahma—the total material substance, prakṛiti; tasmin—in that; garbham—womb; dadhāmi—impregnate; aham—I; sambhavaḥ—birth; sarva-bhūtānām—of all living beings; tataḥ—thereby; bhavati—becomes; bhārata—Arjun, the son of Bharat; \n', 'https://gita.github.io/gita/data/verse_recitation/14/527.mp3', 14, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (528, 'सर्वयोनिषु कौन्तेय मूर्तयः सम्भवन्ति याः।तासां ब्रह्म महद्योनिरहं बीजप्रदः पिता।।14.4।।', 'sarva-yoniṣhu kaunteya mūrtayaḥ sambhavanti yāḥ\ntāsāṁ brahma mahad yonir ahaṁ bīja-pradaḥ pitā\n', 'sarva—all; yoniṣhu—species of life; kaunteya—Arjun, the son of Kunti; mūrtayaḥ—forms; sambhavanti—are produced; yāḥ—which; tāsām—of all of them; brahma-mahat—great material nature; yoniḥ—womb; aham—I; bīja-pradaḥ—seed-giving; pitā—Father\n', 'https://gita.github.io/gita/data/verse_recitation/14/528.mp3', 14, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (529, 'सत्त्वं रजस्तम इति गुणाः प्रकृतिसंभवाः।निबध्नन्ति महाबाहो देहे देहिनमव्ययम्।।14.5।।', 'sattvaṁ rajas tama iti guṇāḥ prakṛiti-sambhavāḥ\nnibadhnanti mahā-bāho dehe dehinam avyayam\n', 'sattvam—mode of goodness; rajaḥ—mode of passion; tamaḥ—mode of ignorance; iti—thus; guṇāḥ—modes; prakṛiti—material nature; sambhavāḥ—consists of; nibadhnanti—bind; mahā-bāho—mighty-armed one; dehe—in the body; dehinam—the embodied soul; avyayam—eternal\n', 'https://gita.github.io/gita/data/verse_recitation/14/529.mp3', 14, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (530, 'तत्र सत्त्वं निर्मलत्वात्प्रकाशकमनामयम्।सुखसङ्गेन बध्नाति ज्ञानसङ्गेन चानघ।।14.6।।', 'tatra sattvaṁ nirmalatvāt prakāśhakam anāmayam\nsukha-saṅgena badhnāti jñāna-saṅgena chānagha\n', 'tatra—amongst these; sattvam—mode of goodness; nirmalatvāt—being purest; prakāśhakam—illuminating; anāmayam—healthy and full of well-being; sukha—happiness; saṅgena—attachment; badhnāti—binds; jñāna—knowledge; saṅgena—attachment; cha—also; anagha—Arjun, the sinless one\n', 'https://gita.github.io/gita/data/verse_recitation/14/530.mp3', 14, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (531, 'रजो रागात्मकं विद्धि तृष्णासङ्गसमुद्भवम्।तन्निबध्नाति कौन्तेय कर्मसङ्गेन देहिनम्।।14.7।।', 'rajo rāgātmakaṁ viddhi tṛiṣhṇā-saṅga-samudbhavam\ntan nibadhnāti kaunteya karma-saṅgena dehinam\n', 'rajaḥ—mode of passion; rāga-ātmakam—of the nature of passion; viddhi—know; tṛiṣhṇā—desires; saṅga—association; samudbhavam—arises from; tat—that; nibadhnāti—binds; kaunteya—Arjun, the son of Kunti; karma-saṅgena—through attachment to fruitive actions; dehinam—the embodied soul\n', 'https://gita.github.io/gita/data/verse_recitation/14/531.mp3', 14, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (532, 'तमस्त्वज्ञानजं विद्धि मोहनं सर्वदेहिनाम्।प्रमादालस्यनिद्राभिस्तन्निबध्नाति भारत।।14.8।।', 'tamas tv ajñāna-jaṁ viddhi mohanaṁ sarva-dehinām\npramādālasya-nidrābhis tan nibadhnāti bhārata\n', 'tamaḥ—mode of ignorance; tu—but; ajñāna-jam—born of ignorance; viddhi—know; mohanam—illusion; sarva-dehinām—for all the embodied souls; pramāda—negligence; ālasya—laziness; nidrābhiḥ—and sleep; tat—that; nibadhnāti—binds; bhārata—Arjun, the son of Bharat\n', 'https://gita.github.io/gita/data/verse_recitation/14/532.mp3', 14, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (533, 'सत्त्वं सुखे सञ्जयति रजः कर्मणि भारत।ज्ञानमावृत्य तु तमः प्रमादे सञ्जयत्युत।।14.9।।', 'sattvaṁ sukhe sañjayati rajaḥ karmaṇi bhārata\njñānam āvṛitya tu tamaḥ pramāde sañjayaty uta\n', 'sattvam—mode of goodness; sukhe—to happiness; sañjayati—binds; rajaḥ—mode of passion; karmaṇi—toward actions; bhārata—Arjun, the son of Bharat; jñānam—wisdom; āvṛitya—clouds; tu—but; tamaḥ—mode of ignorance; pramāde—to delusion; sañjayati—binds; uta—indeed\n', 'https://gita.github.io/gita/data/verse_recitation/14/533.mp3', 14, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (534, 'रजस्तमश्चाभिभूय सत्त्वं भवति भारत।रजः सत्त्वं तमश्चैव तमः सत्त्वं रजस्तथा।।14.10।।', 'rajas tamaśh chābhibhūya sattvaṁ bhavati bhārata\nrajaḥ sattvaṁ tamaśh chaiva tamaḥ sattvaṁ rajas tathā\n', 'rajaḥ—mode of passion; tamaḥ—mode of ignorance; cha—and; abhibhūya—prevails; sattvam—mode of goodness; bhavati—becomes; bhārata—Arjun, the son of Bharat; rajaḥ—mode of passion; sattvam—mode of goodness; tamaḥ—mode of ignorance; cha—and; eva—indeed; tamaḥ—mode of ignorance; sattvam—mode of goodness; rajaḥ—mode of passion; tathā—also\n', 'https://gita.github.io/gita/data/verse_recitation/14/534.mp3', 14, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (535, 'सर्वद्वारेषु देहेऽस्मिन्प्रकाश उपजायते।ज्ञानं यदा तदा विद्याद्विवृद्धं सत्त्वमित्युत।।14.11।।', 'sarva-dvāreṣhu dehe ’smin prakāśha upajāyate\njñānaṁ yadā tadā vidyād vivṛiddhaṁ sattvam ity uta\n', 'sarva—all; dvāreṣhu—through the gates; dehe—body; asmin—in this; prakāśhaḥ—illumination; upajāyate—manifest; jñānam—knowledge; yadā—when; tadā—then; vidyāt—know; vivṛiddham—predominates; sattvam—mode of goodness; iti—thus; uta—certainly;\n', 'https://gita.github.io/gita/data/verse_recitation/14/535.mp3', 14, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (536, 'लोभः प्रवृत्तिरारम्भः कर्मणामशमः स्पृहा।रजस्येतानि जायन्ते विवृद्धे भरतर्षभ।।14.12।।', 'lobhaḥ pravṛittir ārambhaḥ karmaṇām aśhamaḥ spṛihā\nrajasy etāni jāyante vivṛiddhe bharatarṣhabha\n\n', 'lobhaḥ—greed; pravṛittiḥ—activity; ārambhaḥ—exertion; karmaṇām—for fruitive actions; aśhamaḥ—restlessness; spṛihā—craving; rajasi—of the mode of passion; etāni—these; jāyante—develop; vivṛiddhe—when predominates; bharata-ṛiṣhabha—the best of the Bharatas, Arjun;\n', 'https://gita.github.io/gita/data/verse_recitation/14/536.mp3', 14, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (537, 'अप्रकाशोऽप्रवृत्तिश्च प्रमादो मोह एव च।तमस्येतानि जायन्ते विवृद्धे कुरुनन्दन।।14.13।।', 'aprakāśho ’pravṛittiśh cha pramādo moha eva cha\ntamasy etāni jāyante vivṛiddhe kuru-nandana\n\n', 'aprakāśhaḥ—nescience; apravṛittiḥ—inertia; cha—and; pramādaḥ—negligence; mohaḥ—delusion; eva—indeed; cha—also; tamasi—mode of ignorance; etāni—these; jāyante—manifest; vivṛiddhe—when dominates; kuru-nandana—the joy of the Kurus, Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/14/537.mp3', 14, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (538, 'यदा सत्त्वे प्रवृद्धे तु प्रलयं याति देहभृत्।तदोत्तमविदां लोकानमलान्प्रतिपद्यते।।14.14।।', 'yadā sattve pravṛiddhe tu pralayaṁ yāti deha-bhṛit\ntadottama-vidāṁ lokān amalān pratipadyate\n', 'yadā—when; sattve—in the mode of goodness; pravṛiddhe—when premodinates; tu—indeed; pralayam—death; yāti—reach; deha-bhṛit—the embodied; tadā—then; uttama-vidām—of the learned; lokān—abodes; amalān—pure; pratipadyate—attains;\n', 'https://gita.github.io/gita/data/verse_recitation/14/538.mp3', 14, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (539, 'रजसि प्रलयं गत्वा कर्मसङ्गिषु जायते।तथा प्रलीनस्तमसि मूढयोनिषु जायते।।14.15।।', 'rajasi pralayaṁ gatvā karma-saṅgiṣhu jāyate\ntathā pralīnas tamasi mūḍha-yoniṣhu jāyate\n', 'rajasi—in the mode of passion; pralayam—death; gatvā—attaining; karma-saṅgiṣhu—among people driven by work; jāyate—are born; tathā—likewise; pralīnaḥ—dying; tamasi—in the mode of ignorance; mūḍha-yoniṣhu—in the animal kingdom; jāyate—takes birth\n', 'https://gita.github.io/gita/data/verse_recitation/14/539.mp3', 14, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (540, 'कर्मणः सुकृतस्याहुः सात्त्विकं निर्मलं फलम्।रजसस्तु फलं दुःखमज्ञानं तमसः फलम्।।14.16।।', 'karmaṇaḥ sukṛitasyāhuḥ sāttvikaṁ nirmalaṁ phalam\nrajasas tu phalaṁ duḥkham ajñānaṁ tamasaḥ phalam\n\n', 'karmaṇaḥ—of action; su-kṛitasya—pure; āhuḥ—is said; sāttvikam—mode of goodness; nirmalam—pure; phalam—result; rajasaḥ—mode of passion; tu—indeed; phalam—result; duḥkham—pain; ajñānam—ignorance; tamasaḥ—mode of ignorance; phalam—result\n', 'https://gita.github.io/gita/data/verse_recitation/14/540.mp3', 14, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (541, 'सत्त्वात्सञ्जायते ज्ञानं रजसो लोभ एव च।प्रमादमोहौ तमसो भवतोऽज्ञानमेव च।।14.17।।', 'sattvāt sañjāyate jñānaṁ rajaso lobha eva cha\npramāda-mohau tamaso bhavato ’jñānam eva cha\n', 'sattvāt—from the mode of goodness; sañjāyate—arises; jñānam—knowledge; rajasaḥ—from the mode of passion; lobhaḥ—greed; eva—indeed; cha—and; pramāda—negligence; mohau—delusion; tamasaḥ—from the mode of ignorance; bhavataḥ—arise; ajñānam—ignorance; eva—indeed; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/14/541.mp3', 14, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (542, 'ऊर्ध्वं गच्छन्ति सत्त्वस्था मध्ये तिष्ठन्ति राजसाः।जघन्यगुणवृत्तिस्था अधो गच्छन्ति तामसाः।।14.18।।', 'ūrdhvaṁ gachchhanti sattva-sthā madhye tiṣhṭhanti rājasāḥ\njaghanya-guṇa-vṛitti-sthā adho gachchhanti tāmasāḥ\n', 'ūrdhvam—upward; gachchhanti—rise; sattva-sthāḥ—those situated in the mode of goodness; madhye—in the middle; tiṣhṭhanti—stay; rājasāḥ—those in the mode of passion; jaghanya—abominable; guṇa—quality; vṛitti-sthāḥ—engaged in activities; adhaḥ—down; gachchhanti—go; tāmasāḥ—those in the mode of ignorance\n', 'https://gita.github.io/gita/data/verse_recitation/14/542.mp3', 14, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (543, 'नान्यं गुणेभ्यः कर्तारं यदा द्रष्टानुपश्यति।गुणेभ्यश्च परं वेत्ति मद्भावं सोऽधिगच्छति।।14.19।।', 'nānyaṁ guṇebhyaḥ kartāraṁ yadā draṣhṭānupaśhyati\nguṇebhyaśh cha paraṁ vetti mad-bhāvaṁ so ’dhigachchhati\n', 'na—no; anyam—other; guṇebhyaḥ—of the guṇas; kartāram—agents of action; yadā—when; draṣhṭā—the seer; anupaśhyati—see; guṇebhyaḥ—to the modes of nature; cha—and; param—transcendental; vetti—know; mat-bhāvam—my divine nature; saḥ—they; adhigachchhati—attain\n', 'https://gita.github.io/gita/data/verse_recitation/14/543.mp3', 14, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (544, 'गुणानेतानतीत्य त्रीन्देही देहसमुद्भवान्।जन्ममृत्युजरादुःखैर्विमुक्तोऽमृतमश्नुते।।14.20।।', 'guṇān etān atītya trīn dehī deha-samudbhavān\njanma-mṛityu-jarā-duḥkhair vimukto ’mṛitam aśhnute\n', 'guṇān—the three modes of material nature; etān—these; atītya—transcending; trīn—three; dehī—the embodied; deha—body; samudbhavān—produced of; janma—birth; mṛityu—death; jarā—old age; duḥkhaiḥ—misery; vimuktaḥ—freed from; amṛitam—immortality; aśhnute—attains\n', 'https://gita.github.io/gita/data/verse_recitation/14/544.mp3', 14, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (545, 'अर्जुन उवाचकैर्लिंगैस्त्रीन्गुणानेतानतीतो भवति प्रभो।किमाचारः कथं चैतांस्त्रीन्गुणानतिवर्तते।।14.21।।', 'arjuna uvācha\nkair liṅgais trīn guṇān etān atīto bhavati prabho\nkim āchāraḥ kathaṁ chaitāns trīn guṇān ativartate\n', 'arjunaḥ uvācha—Arjun inquired; kaiḥ—by what; liṅgaiḥ—symptoms; trīn—three; guṇān—modes of material nature; etān—these; atītaḥ—having transcended; bhavati—is; prabho—Lord; kim—what; āchāraḥ—conduct; katham—how; cha—and; etān—these; trīn—three; guṇān—modes of material nature; ativartate—transcend\n', 'https://gita.github.io/gita/data/verse_recitation/14/545.mp3', 14, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (546, 'श्री भगवानुवाचप्रकाशं च प्रवृत्तिं च मोहमेव च पाण्डव।न द्वेष्टि सम्प्रवृत्तानि न निवृत्तानि काङ्क्षति।।14.22।।', 'śhrī-bhagavān uvācha\nprakāśhaṁ cha pravṛittiṁ cha moham eva cha pāṇḍava\nna dveṣhṭi sampravṛittāni na nivṛittāni kāṅkṣhati\n\n', 'śhrī-bhagavān uvācha—the Supreme Divine Personality said; prakāśham—illumination; cha—and; pravṛittim—activity; cha—and; moham—delusion; eva—even; cha—and; pāṇḍava—Arjun, the son of Pandu; na dveṣhṭi—do not hate; sampravṛittāni—when present; na—nor; nivṛittāni—when absent; kāṅkṣhati—longs;\n', 'https://gita.github.io/gita/data/verse_recitation/14/546.mp3', 14, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (547, 'उदासीनवदासीनो गुणैर्यो न विचाल्यते।गुणा वर्तन्त इत्येव योऽवतिष्ठति नेङ्गते।।14.23।।', 'udāsīna-vad āsīno guṇair yo na vichālyate\nguṇā vartanta ity evaṁ yo ’vatiṣhṭhati neṅgate\n', 'udāsīna-vat—neutral; āsīnaḥ—situated; guṇaiḥ—to the modes of material nature; yaḥ—who; na—not; vichālyate—are disturbed; guṇāḥ—modes of material nature; vartante—act; iti-evam—knowing it in this way; yaḥ—who; avatiṣhṭhati—established in the self; na—not; iṅgate—wavering\n', 'https://gita.github.io/gita/data/verse_recitation/14/547.mp3', 14, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (548, 'समदुःखसुखः स्वस्थः समलोष्टाश्मकाञ्चनः।तुल्यप्रियाप्रियो धीरस्तुल्यनिन्दात्मसंस्तुतिः।।14.24।।', 'sama-duḥkha-sukhaḥ sva-sthaḥ sama-loṣhṭāśhma-kāñchanaḥ\ntulya-priyāpriyo dhīras tulya-nindātma-sanstutiḥ\n', 'sama—alike; duḥkha—distress; sukhaḥ—happiness; sva-sthaḥ—established in the self; sama—equally; loṣhṭa—a clod; aśhma—stone; kāñchanaḥ—gold; tulya—of equal value; priya—pleasant; apriyaḥ—unpleasant; dhīraḥ—steady; tulya—the same; nindā—blame; ātma-sanstutiḥ—praise;\n', 'https://gita.github.io/gita/data/verse_recitation/14/548.mp3', 14, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (549, 'मानापमानयोस्तुल्यस्तुल्यो मित्रारिपक्षयोः।सर्वारम्भपरित्यागी गुणातीतः स उच्यते।।14.25।।', 'mānāpamānayos tulyas tulyo mitrāri-pakṣhayoḥ\nsarvārambha-parityāgī guṇātītaḥ sa uchyate\n', 'māna—honor; apamānayoḥ—dishonor; tulyaḥ—equal; tulyaḥ—equal; mitra—friend; ari—foe; pakṣhayoḥ—to the parties; sarva—all; ārambha—enterprises; parityāgī—renouncer; guṇa-atītaḥ—risen above the three modes of material nature; saḥ—they; uchyate—are said to have\n', 'https://gita.github.io/gita/data/verse_recitation/14/549.mp3', 14, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (550, 'मां च योऽव्यभिचारेण भक्ितयोगेन सेवते।स गुणान्समतीत्यैतान् ब्रह्मभूयाय कल्पते।।14.26।।', 'māṁ cha yo ’vyabhichāreṇa bhakti-yogena sevate\nsa guṇān samatītyaitān brahma-bhūyāya kalpate\n\n', 'mām—me; cha—only; yaḥ—who; avyabhichāreṇa—unalloyed; bhakti-yogena—through devotion; sevate—serve; saḥ—they; guṇān—the three modes of material nature; samatītya—rise above; etān—these; brahma-bhūyāya—level of Brahman; kalpate—comes to\n', 'https://gita.github.io/gita/data/verse_recitation/14/550.mp3', 14, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (551, 'ब्रह्मणो हि प्रतिष्ठाऽहममृतस्याव्ययस्य च।शाश्वतस्य च धर्मस्य सुखस्यैकान्तिकस्य च।।14.27।।', 'brahmaṇo hi pratiṣhṭhāham amṛitasyāvyayasya cha\nśhāśhvatasya cha dharmasya sukhasyaikāntikasya cha\n', 'brahmaṇaḥ—of Brahman; hi—only; pratiṣhṭhā—the basis; aham—I; amṛitasya—of the immortal; avyayasya—of the imperishable; cha—and; śhāśhvatasya—of the eternal; cha—and; dharmasya—of the dharma; sukhasya—of bliss; aikāntikasya—unending; cha—and\n', 'https://gita.github.io/gita/data/verse_recitation/14/551.mp3', 14, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (552, 'श्री भगवानुवाचऊर्ध्वमूलमधःशाखमश्वत्थं प्राहुरव्ययम्।छन्दांसि यस्य पर्णानि यस्तं वेद स वेदवित्।।15.1।।', 'śhrī-bhagavān uvācha\nūrdhva-mūlam adhaḥ-śhākham aśhvatthaṁ prāhur avyayam\nchhandānsi yasya parṇāni yas taṁ veda sa veda-vit\n', 'śhrī-bhagavān uvācha—the Supreme Divine Personality said; ūrdhva-mūlam—with roots above; adhaḥ—downward; śhākham—branches; aśhvattham—the sacred fig tree; prāhuḥ—they speak; avyayam—eternal; chhandānsi—Vedic mantras; yasya—of which; parṇāni—leaves; yaḥ—who; tam—that; veda—knows; saḥ—he; veda-vit—the knower of the Vedas\n', 'https://gita.github.io/gita/data/verse_recitation/15/552.mp3', 15, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (553, 'अधश्चोर्ध्वं प्रसृतास्तस्य शाखा     गुणप्रवृद्धा विषयप्रवालाः।अधश्च मूलान्यनुसन्ततानि     कर्मानुबन्धीनि मनुष्यलोके।।15.2।।', 'adhaśh chordhvaṁ prasṛitās tasya śhākhā\nguṇa-pravṛiddhā viṣhaya-pravālāḥ\nadhaśh cha mūlāny anusantatāni\nkarmānubandhīni manuṣhya-loke\n', 'adhaḥ—downward; cha—and; ūrdhvam—upward; prasṛitāḥ—extended; tasya—its; śhākhāḥ—branches; guṇa—modes of material nature; pravṛiddhāḥ—nourished; viṣhaya—objects of the senses; pravālāḥ—buds; adhaḥ—downward; cha—and; mūlāni—roots; anusantatāni—keep growing; karma—actions; anubandhīni—bound; manuṣhya-loke—in the world of humans\n', 'https://gita.github.io/gita/data/verse_recitation/15/553.mp3', 15, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (554, 'न रूपमस्येह तथोपलभ्यते    नान्तो न चादिर्न च संप्रतिष्ठा।अश्वत्थमेनं सुविरूढमूल    मसङ्गशस्त्रेण दृढेन छित्त्वा।।15.3।।', 'na rūpam asyeha tathopalabhyate\nnānto na chādir na cha sampratiṣhṭhā\naśhvattham enaṁ su-virūḍha-mūlam\nasaṅga-śhastreṇa dṛiḍhena chhittvā\n\n', 'na—not; rūpam—form; asya—of this; iha—in this world; tathā—as such; upalabhyate—is perceived; na—neither; antaḥ—end; na—nor; cha—also; ādiḥ—beginning; na—never; cha—also; sampratiṣhṭhā—the basis; aśhvattham—sacred fig tree; enam—this; su-virūḍha-mūlam—deep-rooted; asaṅga-śhastreṇa—by the axe of detachment; dṛiḍhena—strong; chhittvā—having cut down;\n', 'https://gita.github.io/gita/data/verse_recitation/15/554.mp3', 15, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (555, 'ततः पदं तत्परिमार्गितव्य    यस्मिन्गता न निवर्तन्ति भूयः।तमेव चाद्यं पुरुषं प्रपद्ये    यतः प्रवृत्तिः प्रसृता पुराणी।।15.4।।', 'tataḥ padaṁ tat parimārgitavyaṁ\nyasmin gatā na nivartanti bhūyaḥ\ntam eva chādyaṁ puruṣhaṁ prapadye\nyataḥ pravṛittiḥ prasṛitā purāṇī\n', 'tataḥ—then; padam—place; tat—that; parimārgitavyam—one must search out; yasmin—where; gatāḥ—having gone; na—not; nivartanti—return; bhūyaḥ—again; tam—to him; eva—certainly; cha—and; ādyam—original; puruṣham—the Supreme Lord; prapadye—take refuge; yataḥ—whence; pravṛittiḥ—the activity; prasṛitā—streamed forth; purāṇi—very old\n', 'https://gita.github.io/gita/data/verse_recitation/15/555.mp3', 15, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (556, 'निर्मानमोहा जितसङ्गदोषा    अध्यात्मनित्या विनिवृत्तकामाः।द्वन्द्वैर्विमुक्ताः सुखदुःखसंज्ञै    र्गच्छन्त्यमूढाः पदमव्ययं तत्।।15.5।।', 'nirmāna-mohā jita-saṅga-doṣhā\nadhyātma-nityā vinivṛitta-kāmāḥ\ndvandvair vimuktāḥ sukha-duḥkha-sanjñair\ngachchhanty amūḍhāḥ padam avyayaṁ tat\n', 'niḥ—free from; māna—vanity; mohāḥ—delusion; jita—having overcome; saṅga—attachment; doṣhāḥ—evils; adhyātma-nityāḥ—dwelling constantly in the self and God; vinivṛitta—freed from; kāmāḥ—desire to enjoy senses; dvandvaiḥ—from the dualities; vimuktāḥ—liberated; sukha-duḥkha—pleasure and pain; saṁjñaiḥ—known as; gachchhanti—attain; amūḍhāḥ—unbewildered; padam—abode; avyayam—eternal; tat—that\n', 'https://gita.github.io/gita/data/verse_recitation/15/556.mp3', 15, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (557, 'न तद्भासयते सूर्यो न शशाङ्को न पावकः।यद्गत्वा न निवर्तन्ते तद्धाम परमं मम।।15.6।।', 'na tad bhāsayate sūryo na śhaśhāṅko na pāvakaḥ\nyad gatvā na nivartante tad dhāma paramaṁ mama\n\n', 'na—neither; tat—that; bhāsayate—illumine; sūryaḥ—the sun; na—nor; śhaśhāṅkaḥ—the moon; na—nor; pāvakaḥ—fire; yat—where; gatvā—having gone; na—never; nivartante—they return; tat—that; dhāma—abode; paramam—supreme; mama—mine\n', 'https://gita.github.io/gita/data/verse_recitation/15/557.mp3', 15, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (558, 'ममैवांशो जीवलोके जीवभूतः सनातनः।मनःषष्ठानीन्द्रियाणि प्रकृतिस्थानि कर्षति।।15.7।।', 'mamaivānśho jīva-loke jīva-bhūtaḥ sanātanaḥ\nmanaḥ-ṣhaṣhṭhānīndriyāṇi prakṛiti-sthāni karṣhati\n', 'mama—my; eva—only; anśhaḥ—fragmental part; jīva-loke—in the material world; jīva-bhūtaḥ—the embodied souls; sanātanaḥ—eternal; manaḥ—with the mind; ṣhaṣhṭhāni—the six; indriyāṇi—senses; prakṛiti-sthāni—bound by material nature; karṣhati—struggling\n', 'https://gita.github.io/gita/data/verse_recitation/15/558.mp3', 15, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (559, 'शरीरं यदवाप्नोति यच्चाप्युत्क्रामतीश्वरः।गृहीत्वैतानि संयाति वायुर्गन्धानिवाशयात्।।15.8।।', 'śharīraṁ yad avāpnoti yach chāpy utkrāmatīśhvaraḥ\ngṛihītvaitāni sanyāti vāyur gandhān ivāśhayāt\n', 'śharīram—the body; yat—as; avāpnoti—carries; yat—as; cha api—also; utkrāmati—leaves; īśhvaraḥ—the Lord of the material body, the embodied soul; gṛihītvā—taking; etāni—these; sanyāti—goes away; vāyuḥ—the air; gandhān—fragrance; iva—like; āśhayāt—from seats\n', 'https://gita.github.io/gita/data/verse_recitation/15/559.mp3', 15, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (560, 'श्रोत्रं चक्षुः स्पर्शनं च रसनं घ्राणमेव च।अधिष्ठाय मनश्चायं विषयानुपसेवते।।15.9।।', 'śhrotraṁ chakṣhuḥ sparśhanaṁ cha rasanaṁ ghrāṇam eva cha\nadhiṣhṭhāya manaśh chāyaṁ viṣhayān upasevate\n', 'śhrotram—ears; chakṣhuḥ—eyes; sparśhanam—the sense of touch; cha—and; rasanam—tongue; ghrāṇam—nose; eva—also; cha—and; adhiṣhṭhāya—grouped around; manaḥ—mind; cha—also; ayam—they; viṣhayān—sense objects; upasevate—savors\n', 'https://gita.github.io/gita/data/verse_recitation/15/560.mp3', 15, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (561, 'उत्क्रामन्तं स्थितं वापि भुञ्जानं वा गुणान्वितम्।विमूढा नानुपश्यन्ति पश्यन्ति ज्ञानचक्षुषः।।15.10।।', 'utkrāmantaṁ sthitaṁ vāpi bhuñjānaṁ vā guṇānvitam\nvimūḍhā nānupaśhyanti paśhyanti jñāna-chakṣhuṣhaḥ\n', 'utkrāmantam—departing; sthitam—residing; vā api—or even; bhuñjānam—enjoys; vā—or; guṇa-anvitam—under the spell of the modes of material nature; vimūḍhāḥ—the ignorant; na—not; anupaśhyanti—percieve; paśhyanti—behold; jñāna-chakṣhuṣhaḥ—those who possess the eyes of knowledge\n', 'https://gita.github.io/gita/data/verse_recitation/15/561.mp3', 15, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (562, 'यतन्तो योगिनश्चैनं पश्यन्त्यात्मन्यवस्थितम्।यतन्तोऽप्यकृतात्मानो नैनं पश्यन्त्यचेतसः।।15.11।।', 'yatanto yoginaśh chainaṁ paśhyanty ātmany avasthitam\nyatanto ‘py akṛitātmāno nainaṁ paśhyanty achetasaḥ\n', 'yatantaḥ—striving; yoginaḥ—yogis; cha—too; enam—this (the soul); paśhyanti—see; ātmani—in the body; avasthitam—enshrined; yatantaḥ—strive; api—even though; akṛita-ātmānaḥ—those whose minds are not purified; na—not; enam—this; paśhyanti—cognize; achetasaḥ—unaware\n', 'https://gita.github.io/gita/data/verse_recitation/15/562.mp3', 15, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (563, 'यदादित्यगतं तेजो जगद्भासयतेऽखिलम्।यच्चन्द्रमसि यच्चाग्नौ तत्तेजो विद्धि मामकम्।।15.12।।', 'yad āditya-gataṁ tejo jagad bhāsayate ’khilam\nyach chandramasi yach chāgnau tat tejo viddhi māmakam\n', 'yat—which; āditya-gatam—in the sun; tejaḥ—brilliance; jagat—solar system; bhāsayate—illuminates; akhilam—entire; yat—which; chandramasi—in the moon; yat—which; cha—also; agnau—in the fire; tat—that; tejaḥ—brightness; viddhi—know; māmakam—mine\n', 'https://gita.github.io/gita/data/verse_recitation/15/563.mp3', 15, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (564, 'गामाविश्य च भूतानि धारयाम्यहमोजसा।पुष्णामि चौषधीः सर्वाः सोमो भूत्वा रसात्मकः।।15.13।।', 'gām āviśhya cha bhūtāni dhārayāmy aham ojasā\npuṣhṇāmi chauṣhadhīḥ sarvāḥ somo bhūtvā rasātmakaḥ\n\n', 'gām—earth; āviśhya—permeating; cha—and; bhūtāni—living beings; dhārayāmi—sustain; aham—I; ojasā—energy; puṣhṇāmi—nourish; cha—and; auṣhadhīḥ—plants; sarvāḥ—all; somaḥ—the moon; bhūtvā—becoming; rasa-ātmakaḥ—supplying the juice of life\n', 'https://gita.github.io/gita/data/verse_recitation/15/564.mp3', 15, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (565, 'अहं वैश्वानरो भूत्वा प्राणिनां देहमाश्रितः।प्राणापानसमायुक्तः पचाम्यन्नं चतुर्विधम्।।15.14।।', 'ahaṁ vaiśhvānaro bhūtvā prāṇināṁ deham āśhritaḥ\nprāṇāpāna-samāyuktaḥ pachāmy annaṁ chatur-vidham\n\n', 'aham—I; vaiśhvānaraḥ—fire of digestion; bhūtvā—becoming; prāṇinām—of all living beings; deham—the body; āśhritaḥ—situated; prāṇa-apāna—outgoing and incoming breath; samāyuktaḥ—keeping in balance; pachāmi—I digest; annam—foods; chatuḥ-vidham—the four kinds\n', 'https://gita.github.io/gita/data/verse_recitation/15/565.mp3', 15, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (566, 'सर्वस्य चाहं हृदि सन्निविष्टो    मत्तः स्मृतिर्ज्ञानमपोहनं च।वेदैश्च सर्वैरहमेव वेद्यो    वेदान्तकृद्वेदविदेव चाहम्।।15.15।।', 'sarvasya chāhaṁ hṛidi sanniviṣhṭo\nmattaḥ smṛitir jñānam apohanaṁ cha\nvedaiśh cha sarvair aham eva vedyo\nvedānta-kṛid veda-vid eva chāham\n\n', 'sarvasya—of all living beings; cha—and; aham—I; hṛidi—in the hearts; sanniviṣhṭaḥ—seated; mattaḥ—from me; smṛitiḥ—memory; jñānam—knowledge; apohanam—forgetfulness; cha—as well as; vedaiḥ—by the Vedas; cha—and; sarvaiḥ—all; aham—I; eva—alone; vedyaḥ—to be known; vedānta-kṛit—the author of the Vedānt; veda-vit—the knower of the meaning of the Vedas; eva—alone; cha—and; aham—I\n', 'https://gita.github.io/gita/data/verse_recitation/15/566.mp3', 15, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (567, 'द्वाविमौ पुरुषौ लोके क्षरश्चाक्षर एव च।क्षरः सर्वाणि भूतानि कूटस्थोऽक्षर उच्यते।।15.16।।', 'dvāv imau puruṣhau loke kṣharaśh chākṣhara eva cha\nkṣharaḥ sarvāṇi bhūtāni kūṭa-stho ’kṣhara uchyate\n', 'dvau—two; imau—these; puruṣhau—beings; loke—in creation; kṣharaḥ—the perishable; cha—and; akṣharaḥ—the imperishable; eva—even; cha—and; kṣharaḥ—the perishable; sarvāṇi—all; bhūtāni—beings; kūṭa-sthaḥ—the liberated; akṣharaḥ—the imperishable; uchyate—is said\n', 'https://gita.github.io/gita/data/verse_recitation/15/567.mp3', 15, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (568, 'उत्तमः पुरुषस्त्वन्यः परमात्मेत्युदाहृतः।यो लोकत्रयमाविश्य बिभर्त्यव्यय ईश्वरः।।15.17।।', 'uttamaḥ puruṣhas tv anyaḥ paramātmety udāhṛitaḥ\nyo loka-trayam āviśhya bibharty avyaya īśhvaraḥ\n\n', 'uttamaḥ—the Supreme; puruṣhaḥ—Divine Personality; tu—but; anyaḥ—besides; parama-ātmā—the Supreme Soul; iti—thus; udāhṛitaḥ—is said; yaḥ—who; loka trayam—the three worlds; āviśhya—enters; bibharti—supports; avyayaḥ—indestructible; īśhvaraḥ—the controller\n', 'https://gita.github.io/gita/data/verse_recitation/15/568.mp3', 15, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (569, 'यस्मात्क्षरमतीतोऽहमक्षरादपि चोत्तमः।अतोऽस्मि लोके वेदे च प्रथितः पुरुषोत्तमः।।15.18।।', 'yasmāt kṣharam atīto ’ham akṣharād api chottamaḥ\nato ’smi loke vede cha prathitaḥ puruṣhottamaḥ\n\n', 'yasmāt—hence; kṣharam—to the perishable; atītaḥ—transcendental; aham—I; akṣharāt—to the imperishable; api—even; cha—and; uttamaḥ—transcendental; ataḥ—therefore; asmi—I am; loke—in the world; vede—in the Vedas; cha—and; prathitaḥ—celebrated; puruṣha-uttamaḥ—as the Supreme Divine Personality\n', 'https://gita.github.io/gita/data/verse_recitation/15/569.mp3', 15, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (570, 'यो मामेवमसम्मूढो जानाति पुरुषोत्तमम्।स सर्वविद्भजति मां सर्वभावेन भारत।।15.19।।', 'yo mām evam asammūḍho jānāti puruṣhottamam\nsa sarva-vid bhajati māṁ sarva-bhāvena bhārata\n\n', 'yaḥ—who; mām—me; evam—thus; asammūḍhaḥ—without a doubt; jānāti—know; puruṣha-uttamam—the Supreme Divine Personality; saḥ—they; sarva-vit—those with complete knowledge; bhajati—worship; mām—me; sarva-bhāvena—with one’s whole being; bhārata—Arjun, the son of Bharat\n', 'https://gita.github.io/gita/data/verse_recitation/15/570.mp3', 15, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (571, 'इति गुह्यतमं शास्त्रमिदमुक्तं मयाऽनघ।एतद्बुद्ध्वा बुद्धिमान्स्यात्कृतकृत्यश्च भारत।।15.20।।', 'iti guhyatamaṁ śhāstram idam uktaṁ mayānagha\netad buddhvā buddhimān syāt kṛita-kṛityaśh cha bhārata\n', 'iti—these; guhya-tamam—most secret; śhāstram—Vedic scriptures; idam—this; uktam—spoken; mayā—by me; anagha—Arjun, the sinless one; etat—this; buddhvā—understanding; buddhi-mān—enlightened; syāt—one becomes; kṛita-kṛityaḥ—who fulfills all that is to be accomplished; cha—and; bhārata—Arjun, the son of Bharat\n', 'https://gita.github.io/gita/data/verse_recitation/15/571.mp3', 15, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (572, 'श्री भगवानुवाच\n\nअभयं सत्त्वसंशुद्धिः ज्ञानयोगव्यवस्थितिः।\n\nदानं दमश्च यज्ञश्च स्वाध्यायस्तप आर्जवम्।।16.1।।\n ', 'śhrī-bhagavān uvācha\nabhayaṁ sattva-sanśhuddhir jñāna-yoga-vyavasthitiḥ\ndānaṁ damaśh cha yajñaśh cha svādhyāyas tapa ārjavam\n', 'śhrī-bhagavān uvācha—the Supreme Divine Personality said; abhayam—fearlessness; sattva-sanśhuddhiḥ—purity of mind; jñāna—knowledge; yoga—spiritual; vyavasthitiḥ—steadfastness; dānam—charity; damaḥ—control of the senses; cha—and; yajñaḥ—performance of sacrifice; cha—and; svādhyāyaḥ—study of sacred books; tapaḥ—austerity; ārjavam—straightforwardness;\n', 'https://gita.github.io/gita/data/verse_recitation/16/572.mp3', 16, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (573, 'अहिंसा सत्यमक्रोधस्त्यागः शान्तिरपैशुनम्।दया भूतेष्वलोलुप्त्वं मार्दवं ह्रीरचापलम्।।16.2।।', 'ahinsā satyam akrodhas tyāgaḥ śhāntir apaiśhunam\ndayā bhūteṣhv aloluptvaṁ mārdavaṁ hrīr achāpalam\n', 'ahinsā—non-violence; satyam—truthfulness; akrodhaḥ—absence of anger; tyāgaḥ—renunciation; śhāntiḥ—peacefulness; apaiśhunam—restraint from fault-finding; dayā—compassion; bhūteṣhu—toward all living beings; aloluptvam—absence of covetousness; mārdavam—gentleness; hrīḥ—modesty; achāpalam—lack of fickleness;\n', 'https://gita.github.io/gita/data/verse_recitation/16/573.mp3', 16, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (574, 'तेजः क्षमा धृतिः शौचमद्रोहो नातिमानिता।\n\nभवन्ति सम्पदं दैवीमभिजातस्य भारत।।16.3।।\n ', 'tejaḥ kṣhamā dhṛitiḥ śhaucham adroho nāti-mānitā\nbhavanti sampadaṁ daivīm abhijātasya bhārata\n', 'tejaḥ—vigor; kṣhamā—forgiveness; dhṛitiḥ—fortitude; śhaucham—cleanliness; adrohaḥ—bearing enmity toward none; na—not; ati-mānitā—absence of vanity; bhavanti—are; sampadam—qualities; daivīm—godly; abhijātasya—of those endowed with; bhārata—scion of Bharat\n', 'https://gita.github.io/gita/data/verse_recitation/16/574.mp3', 16, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (575, 'दम्भो दर्पोऽभिमानश्च क्रोधः पारुष्यमेव च।अज्ञानं चाभिजातस्य पार्थ सम्पदमासुरीम्।।16.4।।', 'dambho darpo ’bhimānaśh cha krodhaḥ pāruṣhyam eva cha\najñānaṁ chābhijātasya pārtha sampadam āsurīm\n', 'dambhaḥ—hypocrisy; darpaḥ—arrogance; abhimānaḥ—conceit; cha—and; krodhaḥ—anger; pāruṣhyam—harshness; eva—certainly; cha—and; ajñānam—ignorance; cha—and; abhijātasya—of those who possess; pārtha—Arjun, the son of Pritha; sampadam—qualities; āsurīm—demoniac\n', 'https://gita.github.io/gita/data/verse_recitation/16/575.mp3', 16, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (576, 'दैवी सम्पद्विमोक्षाय निबन्धायासुरी मता।मा शुचः सम्पदं दैवीमभिजातोऽसि पाण्डव।।16.5।।', 'daivī sampad vimokṣhāya nibandhāyāsurī matā\nmā śhuchaḥ sampadaṁ daivīm abhijāto ’si pāṇḍava\n', 'daivī—divine; sampat—qualities; vimokṣhāya—toward liberation; nibandhāya—to bondage; āsurī—demoniac qualities; matā—are considered; mā—do not; śhuchaḥ—grieve; sampadam—virtues; daivīm—saintly; abhijātaḥ—born; asi—you are; pāṇḍava—Arjun, the son of Pandu\n', 'https://gita.github.io/gita/data/verse_recitation/16/576.mp3', 16, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (577, 'द्वौ भूतसर्गौ लोकेऽस्मिन् दैव आसुर एव च।दैवो विस्तरशः प्रोक्त आसुरं पार्थ मे श्रृणु।।16.6।।', 'dvau bhūta-sargau loke ’smin daiva āsura eva cha\ndaivo vistaraśhaḥ prokta āsuraṁ pārtha me śhṛiṇu\n', 'dvau—two; bhūta-sargau—of created living beings; loke—in the world; asmin—this; daivaḥ—divine; āsuraḥ—demoniac; eva—certainly; cha—and; daivaḥ—the divine; vistaraśhaḥ—at great length; proktaḥ—said; āsuram—the demoniac; pārtha—Arjun, the son of Pritha; me—from me; śhṛiṇu—hear\n', 'https://gita.github.io/gita/data/verse_recitation/16/577.mp3', 16, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (578, 'प्रवृत्तिं च निवृत्तिं च जना न विदुरासुराः।न शौचं नापि चाचारो न सत्यं तेषु विद्यते।।16.7।।', 'pravṛittiṁ cha nivṛittiṁ cha janā na vidur āsurāḥ\nna śhauchaṁ nāpi chāchāro na satyaṁ teṣhu vidyate\n', 'pravṛittim—proper actions; cha—and; nivṛittim—improper actions; cha—and; janāḥ—persons; na—not; viduḥ—comprehend; āsurāḥ—those possessing demoniac nature; na—neither; śhaucham—purity; na—nor; api—even; cha—and; āchāraḥ—conduct; na—nor; satyam—truthfulness; teṣhu—in them; vidyate—exist\n', 'https://gita.github.io/gita/data/verse_recitation/16/578.mp3', 16, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (579, 'असत्यमप्रतिष्ठं ते जगदाहुरनीश्वरम्।अपरस्परसम्भूतं किमन्यत्कामहैतुकम्।।16.8।।', 'asatyam apratiṣhṭhaṁ te jagad āhur anīśhvaram\naparaspara-sambhūtaṁ kim anyat kāma-haitukam\n', 'asatyam—without absolute truth; apratiṣhṭham—without any basis; te—they; jagat—the world; āhuḥ—say; anīśhvaram—without a God; aparaspara—without cause; sambhūtam—created; kim—what; anyat—other; kāma-haitukam—for sexual gratification only\n', 'https://gita.github.io/gita/data/verse_recitation/16/579.mp3', 16, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (580, 'एतां दृष्टिमवष्टभ्य नष्टात्मानोऽल्पबुद्धयः।प्रभवन्त्युग्रकर्माणः क्षयाय जगतोऽहिताः।।16.9।।', 'etāṁ dṛiṣhṭim avaṣhṭabhya naṣhṭātmāno ’lpa-buddhayaḥ\nprabhavanty ugra-karmāṇaḥ kṣhayāya jagato ’hitāḥ\n', 'etām—such; dṛiṣhṭim—views; avaṣhṭabhya—holding; naṣhṭa—misdirected; ātmānaḥ—souls; alpa-buddhayaḥ—of small intellect; prabhavanti—arise; ugra—cruel; karmāṇaḥ—actions; kṣhayāya—destruction; jagataḥ—of the world; ahitāḥ—enemies\n', 'https://gita.github.io/gita/data/verse_recitation/16/580.mp3', 16, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (581, 'काममाश्रित्य दुष्पूरं दम्भमानमदान्विताः।मोहाद्गृहीत्वासद्ग्राहान्प्रवर्तन्तेऽशुचिव्रताः।।16.10।।', 'kāmam āśhritya duṣhpūraṁ dambha-māna-madānvitāḥ\nmohād gṛihītvāsad-grāhān pravartante ’śhuchi-vratāḥ\n', 'kāmam—lust; āśhritya—harboring; duṣhpūram—insatiable; dambha—hypocrisy; māna—arrogance; mada-anvitāḥ—clinging to false tenets; mohāt—the illusioned; gṛihītvā—being attracted to; asat—impermanent; grāhān—things; pravartante—they flourish; aśhuchi-vratāḥ—with impure resolve\n', 'https://gita.github.io/gita/data/verse_recitation/16/581.mp3', 16, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (582, 'चिन्तामपरिमेयां च प्रलयान्तामुपाश्रिताः।कामोपभोगपरमा एतावदिति निश्िचताः।।16.11।।', 'chintām aparimeyāṁ cha pralayāntām upāśhritāḥ\nkāmopabhoga-paramā etāvad iti niśhchitāḥ\n', 'chintām—anxieties; aparimeyām—endless; cha—and; pralaya-antām—until death; upāśhritāḥ—taking refuge; kāma-upabhoga—gratification of desires; paramāḥ—the purpose of life; etāvat—still; iti—thus; niśhchitāḥ—with complete assurance\n', 'https://gita.github.io/gita/data/verse_recitation/16/582.mp3', 16, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (583, 'आशापाशशतैर्बद्धाः कामक्रोधपरायणाः।ईहन्ते कामभोगार्थमन्यायेनार्थसञ्चयान्।।16.12।।', 'āśhā-pāśha-śhatair baddhāḥ kāma-krodha-parāyaṇāḥ\nīhante kāma-bhogārtham anyāyenārtha-sañchayān\n', 'āśhā-pāśha—bondage of desires; śhataiḥ—by hundreds; baddhāḥ—bound; kāma—lust; krodha—anger; parāyaṇāḥ—dedicated to; īhante—strive; kāma—lust; bhoga—gratification of the senses; artham—for; anyāyena—by unjust means; artha—wealth; sañchayān—to accumulate\n', 'https://gita.github.io/gita/data/verse_recitation/16/583.mp3', 16, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (584, 'इदमद्य मया लब्धमिमं प्राप्स्ये मनोरथम्।इदमस्तीदमपि मे भविष्यति पुनर्धनम्।।16.13।।', 'idam adya mayā labdham imaṁ prāpsye manoratham\nidam astīdam api me bhaviṣhyati punar dhanam\n', 'idam—this; adya—today; mayā—by me; labdham—gained; imam—this; prāpsye—I shall acquire; manaḥ-ratham—desire; idam—this; asti—is; idam—this; api—also; me—mine; bhaviṣhyati—in future; punaḥ—again; dhanam—wealth;\n', 'https://gita.github.io/gita/data/verse_recitation/16/584.mp3', 16, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (585, 'असौ मया हतः शत्रुर्हनिष्ये चापरानपि।ईश्वरोऽहमहं भोगी सिद्धोऽहं बलवान्सुखी।।16.14।।', 'asau mayā hataḥ śhatrur haniṣhye chāparān api\nīśhvaro ’ham ahaṁ bhogī siddho ’haṁ balavān sukhī\n', 'asau—that; mayā—by me; hataḥ—has been destroyed; śhatruḥ—enemy; haniṣhye—I shall destroy; cha—and; aparān—others; api—also; īśhvaraḥ—God; aham—I; aham—I; bhogī—the enjoyer; siddhaḥ—powerful; aham—I; bala-vān—powerful; sukhī—happy;\n', 'https://gita.github.io/gita/data/verse_recitation/16/585.mp3', 16, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (586, 'आढ्योऽभिजनवानस्मि कोऽन्योऽस्ति सदृशो मया।यक्ष्ये दास्यामि मोदिष्य इत्यज्ञानविमोहिताः।।16.15।।', 'āḍhyo ’bhijanavān asmi ko ’nyo ’sti sadṛiśho mayā\nyakṣhye dāsyāmi modiṣhya ity ajñāna-vimohitāḥ\n aneka-chitta-vibhrāntā moha-jāla-samāvṛitāḥ\nprasaktāḥ kāma-bhogeṣhu patanti narake ’śhuchau\n', 'āḍhyaḥ—wealthy; abhijana-vān—having highly placed relatives; asmi—me; kaḥ—who; anyaḥ—else; asti—is; sadṛiśhaḥ—like; mayā—to me; yakṣhye—I shall perform sacrifices; dāsyāmi—I shall give alms; modiṣhye—I shall rejoice; iti—thus; ajñāna—ignorance; vimohitāḥ—deluded\n aneka—many; chitta—imaginings; vibhrāntāḥ—led astray; moha—delusion; jāla—mesh; samāvṛitāḥ—enveloped; prasaktāḥ—addicted; kāma-bhogeṣhu—gratification of sensuous pleasures; patanti—descend; narake—to hell; aśhuchau—murky\n', 'https://gita.github.io/gita/data/verse_recitation/16/586.mp3', 16, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (587, 'अनेकचित्तविभ्रान्ता मोहजालसमावृताः।प्रसक्ताः कामभोगेषु पतन्ति नरकेऽशुचौ।।16.16।।', 'aneka-citta-vibhrāntā    moha-jāla-samāvṛtāḥ prasaktāḥ   kāma-bhogeṣu    patanti narake  \'śucau', 'aneka—numerous;    citta-vibhrāntāḥ—perplexed  by  anxieties;  moha—of illusions;  jāla—by a   network;    samāvṛtāḥ—surrounded;   prasaktāḥ—attached; kāma—lust;  bhogeṣu—sense  gratification;   patanti—glides  down; narake—into hell; aśucau—unclean.', 'https://gita.github.io/gita/data/verse_recitation/16/587.mp3', 16, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (588, 'आत्मसम्भाविताः स्तब्धा धनमानमदान्विताः।यजन्ते नामयज्ञैस्ते दम्भेनाविधिपूर्वकम्।।16.17।।', 'ātma-sambhāvitāḥ stabdhā dhana-māna-madānvitāḥ\nyajante nāma-yajñais te dambhenāvidhi-pūrvakam\n', 'ātma-sambhāvitāḥ—self-conceited; stabdhāḥ—stubborn; dhana—wealth; māna—pride; mada—arrogance; anvitāḥ—full of; yajante—perform sacrifice; nāma—in name only; yajñaiḥ—sacrifices; te—they; dambhena—ostentatiously; avidhi-pūrvakam—with no regards to the rules of the scriptures\n', 'https://gita.github.io/gita/data/verse_recitation/16/588.mp3', 16, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (589, 'अहङ्कारं बलं दर्पं कामं क्रोधं च संश्रिताः।मामात्मपरदेहेषु प्रद्विषन्तोऽभ्यसूयकाः।।16.18।।', 'ahankāraṁ balaṁ darpaṁ kāmaṁ krodhaṁ cha sanśhritāḥ\nmām ātma-para-deheṣhu pradviṣhanto ’bhyasūyakāḥ\n', 'ahankāram—egotism; balam—strength; darpam—arrogance; kāmam—desire; krodham—anger; cha—and; sanśhritāḥ—covered by; mām—me; ātma-para-deheṣhu—within one’s own and bodies of others; pradviṣhantaḥ—abuse; abhyasūyakāḥ—the demoniac\n', 'https://gita.github.io/gita/data/verse_recitation/16/589.mp3', 16, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (590, 'तानहं द्विषतः क्रूरान्संसारेषु नराधमान्।क्षिपाम्यजस्रमशुभानासुरीष्वेव योनिषु।।16.19।।', 'tān ahaṁ dviṣhataḥ krūrān sansāreṣhu narādhamān\nkṣhipāmy ajasram aśhubhān āsurīṣhv eva yoniṣhu\n', 'tān—these; aham—I; dviṣhataḥ—hateful; krūrān—cruel; sansāreṣhu—in the material world; nara-adhamān—the vile and vicious of humankind; kṣhipāmi—I hurl; ajasram—again and again; aśhubhān—inauspicious; āsurīṣhu—demoniac; eva—indeed; yoniṣhu—in to the wombs;\n', 'https://gita.github.io/gita/data/verse_recitation/16/590.mp3', 16, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (591, 'असुरीं योनिमापन्ना मूढा जन्मनि जन्मनि।मामप्राप्यैव कौन्तेय ततो यान्त्यधमां गतिम्।।16.20।।', 'āsurīṁ yonim āpannā mūḍhā janmani janmani\nmām aprāpyaiva kaunteya tato yānty adhamāṁ gatim\n', 'āsurīm—demoniac; yonim—wombs; āpannāḥ—gaining; mūḍhāḥ—the ignorant; janmani janmani—in birth after birth; mām—me; aprāpya—failing to reach; eva—even; kaunteya—Arjun, the son of Kunti; tataḥ—thereafter; yānti—go; adhamām—abominable; gatim—destination\n', 'https://gita.github.io/gita/data/verse_recitation/16/591.mp3', 16, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (592, 'त्रिविधं नरकस्येदं द्वारं नाशनमात्मनः।कामः क्रोधस्तथा लोभस्तस्मादेतत्त्रयं त्यजेत्।।16.21।।', 'tri-vidhaṁ narakasyedaṁ dvāraṁ nāśhanam ātmanaḥ\nkāmaḥ krodhas tathā lobhas tasmād etat trayaṁ tyajet\n', 'tri-vidham—three types of; narakasya—to the hell; idam—this; dvāram—gates; nāśhanam—destruction; ātmanaḥ—self; kāmaḥ—lust; krodhaḥ—anger; tathā—and; lobhaḥ—greed; tasmāt—therefore; etat—these; trayam—three; tyajet—should abandon\n', 'https://gita.github.io/gita/data/verse_recitation/16/592.mp3', 16, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (593, 'एतैर्विमुक्तः कौन्तेय तमोद्वारैस्त्रिभिर्नरः।आचरत्यात्मनः श्रेयस्ततो याति परां गतिम्।।16.22।।', 'etair vimuktaḥ kaunteya tamo-dvārais tribhir naraḥ\nācharaty ātmanaḥ śhreyas tato yāti parāṁ gatim\n', 'etaiḥ—from this; vimuktaḥ—freed; kaunteya—Arjun, the son of Kunti; tamaḥ-dvāraiḥ—gates to darkness; tribhiḥ—three; naraḥ—a person; ācharati—endeavor; ātmanaḥ—soul; śhreyaḥ—welfare; tataḥ—thereby; yāti—attain; parām—supreme; gatim—goal\n', 'https://gita.github.io/gita/data/verse_recitation/16/593.mp3', 16, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (594, 'यः शास्त्रविधिमुत्सृज्य वर्तते कामकारतः।न स सिद्धिमवाप्नोति न सुखं न परां गतिम्।।16.23।।', 'yaḥ śhāstra-vidhim utsṛijya vartate kāma-kārataḥ\nna sa siddhim avāpnoti na sukhaṁ na parāṁ gatim\n', 'yaḥ—who; śhāstra-vidhim—scriptural injunctions; utsṛijya—discarding; vartate—act; kāma-kārataḥ—under the impulse of desire; na—neither; saḥ—they; siddhim—perfection; avāpnoti—attain; na—nor; sukham—happiness; na—nor; parām—the supreme; gatim—goal\n', 'https://gita.github.io/gita/data/verse_recitation/16/594.mp3', 16, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (595, 'तस्माच्छास्त्रं प्रमाणं ते कार्याकार्यव्यवस्थितौ।ज्ञात्वा शास्त्रविधानोक्तं कर्म कर्तुमिहार्हसि।।16.24।।', 'tasmāch chhāstraṁ pramāṇaṁ te kāryākārya-vyavasthitau\njñātvā śhāstra-vidhānoktaṁ karma kartum ihārhasi\n', 'tasmāt—therefore; śhāstram—scriptures; pramāṇam—authority; te—your; kārya—duty; akārya—forbidden action; vyavasthitau—in determining; jñātvā—having understood; śhāstra—scriptures; vidhāna—injunctions; uktam—as revealed; karma—actions; kartum—perform; iha—in this world; arhasi—you should\n', 'https://gita.github.io/gita/data/verse_recitation/16/595.mp3', 16, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (596, 'अर्जुन उवाचये शास्त्रविधिमुत्सृज्य यजन्ते श्रद्धयाऽन्विताः।तेषां निष्ठा तु का कृष्ण सत्त्वमाहो रजस्तमः।।17.1।।', 'arjuna uvācha\nye śhāstra-vidhim utsṛijya yajante śhraddhayānvitāḥ\nteṣhāṁ niṣhṭhā tu kā kṛiṣhṇa sattvam āho rajas tamaḥ\n', 'arjunaḥ uvācha—Arjun said; ye—who; śhāstra-vidhim—scriptural injunctions; utsṛijya—disregard; yajante—worship; śhraddhayā-anvitāḥ—with faith; teṣhām—their; niṣhṭhā—faith; tu—indeed; kā—what; kṛiṣhṇa—Krishna; sattvam—mode of goodness; āho—or; rajaḥ—mode of passion; tamaḥ—mode of ignorance\n', 'https://gita.github.io/gita/data/verse_recitation/17/596.mp3', 17, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (597, 'श्री भगवानुवाचत्रिविधा भवति श्रद्धा देहिनां सा स्वभावजा।सात्त्विकी राजसी चैव तामसी चेति तां श्रृणु।।17.2।।', 'śhrī-bhagavān uvācha\ntri-vidhā bhavati śhraddhā dehināṁ sā svabhāva-jā\nsāttvikī rājasī chaiva tāmasī cheti tāṁ śhṛiṇu\n', 'śhrī-bhagavān uvācha—the Supreme Personality said; tri-vidhā—of three kinds; bhavati—is; śhraddhā—faith; dehinām—embodied beings; sā—which; sva-bhāva-jā—born of one’s innate nature; sāttvikī—of the mode of goodness; rājasī—of the mode of passion; cha—and; eva—certainly; tāmasī—of the mode of ignorance; cha—and; iti—thus; tām—about this; śhṛiṇu—hear\n', 'https://gita.github.io/gita/data/verse_recitation/17/597.mp3', 17, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (598, 'सत्त्वानुरूपा सर्वस्य श्रद्धा भवति भारत।श्रद्धामयोऽयं पुरुषो यो यच्छ्रद्धः स एव सः।।17.3।।', 'sattvānurūpā sarvasya śhraddhā bhavati bhārata\nśhraddhā-mayo ‘yaṁ puruṣho yo yach-chhraddhaḥ sa eva saḥ\n', 'sattva-anurūpā—conforming to the nature of one’s mind; sarvasya—all; śhraddhā—faith; bhavati—is; bhārata—Arjun, the scion of Bharat; śhraddhāmayaḥ—possessing faith; ayam—that; puruṣhaḥ—human being; yaḥ—who; yat-śhraddhaḥ—whatever the nature of their faith; saḥ—their; eva—verily; saḥ—they\n', 'https://gita.github.io/gita/data/verse_recitation/17/598.mp3', 17, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (599, 'यजन्ते सात्त्विका देवान्यक्षरक्षांसि राजसाः।प्रेतान्भूतगणांश्चान्ये यजन्ते तामसा जनाः।।17.4।।', 'yajante sāttvikā devān yakṣha-rakṣhānsi rājasāḥ\npretān bhūta-gaṇānśh chānye yajante tāmasā janāḥ\n', 'yajante—worship; sāttvikāḥ—those in the mode of goodness; devān—celestial gods; yakṣha—semi-celestial beings who exude power and wealth; rakṣhānsi—powerful beings who embody sensual enjoyment, revenge, and wrath; rājasāḥ—those in the mode of passion; pretān-bhūta-gaṇān—ghosts and spirits; cha—and; anye—others; yajante—worship; tāmasāḥ—those in the mode of ignorance; janāḥ—persons\n', 'https://gita.github.io/gita/data/verse_recitation/17/599.mp3', 17, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (600, 'अशास्त्रविहितं घोरं तप्यन्ते ये तपो जनाः।दम्भाहङ्कारसंयुक्ताः कामरागबलान्विताः।।17.5।।', 'aśhāstra-vihitaṁ ghoraṁ tapyante ye tapo janāḥ\ndambhāhankāra-sanyuktāḥ kāma-rāga-balānvitāḥ\n', 'aśhāstra-vihitam—not enjoined by the scriptures; ghoram—stern; tapyante—perform; ye—who; tapaḥ—austerities; janāḥ—people; dambha—hypocrisy; ahankāra—egotism; sanyuktāḥ—possessed of; kāma—desire; rāga—attachment; bala—force; anvitāḥ—impelled by;\n', 'https://gita.github.io/gita/data/verse_recitation/17/600.mp3', 17, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (601, 'कर्षयन्तः शरीरस्थं भूतग्राममचेतसः।मां चैवान्तःशरीरस्थं तान्विद्ध्यासुरनिश्चयान्।।17.6।।', 'karṣhayantaḥ śharīra-sthaṁ bhūta-grāmam achetasaḥ\nmāṁ chaivāntaḥ śharīra-sthaṁ tān viddhy āsura-niśhchayān\n', 'karṣhayantaḥ—torment; śharīra-stham—within the body; bhūta-grāmam—elements of the body; achetasaḥ—senseless; mām—me; cha—and; eva—even; antaḥ—within; śharīra-stham—dwelling in the body; tān—them; viddhi—know; āsura-niśhchayān—of demoniacal resolves\n', 'https://gita.github.io/gita/data/verse_recitation/17/601.mp3', 17, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (602, 'आहारस्त्वपि सर्वस्य त्रिविधो भवति प्रियः।यज्ञस्तपस्तथा दानं तेषां भेदमिमं श्रृणु।।17.7।।', 'āhāras tv api sarvasya tri-vidho bhavati priyaḥ\nyajñas tapas tathā dānaṁ teṣhāṁ bhedam imaṁ śhṛiṇu\n', 'āhāraḥ—food; tu—indeed; api—even; sarvasya—of all; tri-vidhaḥ—of three kinds; bhavati—is; priyaḥ—dear; yajñaḥ—sacrifice; tapaḥ—austerity; tathā—and; dānam—charity; teṣhām—of them; bhedam—distinctions; imam—this; śhṛiṇu—hear\n', 'https://gita.github.io/gita/data/verse_recitation/17/602.mp3', 17, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (603, 'आयुःसत्त्वबलारोग्यसुखप्रीतिविवर्धनाः।रस्याः स्निग्धाः स्थिरा हृद्या आहाराः सात्त्विकप्रियाः।।17.8।।', 'āyuḥ-sattva-balārogya-sukha-prīti-vivardhanāḥ\nrasyāḥ snigdhāḥ sthirā hṛidyā āhārāḥ sāttvika-priyāḥ\n', 'āyuḥ sattva—which promote longevity; bala—strength; ārogya—health; sukha—happiness; prīti—satisfaction; vivardhanāḥ—increase; rasyāḥ—juicy; snigdhāḥ—succulent; sthirāḥ—nourishing; hṛidyāḥ—pleasing to the heart; āhārāḥ—food; sāttvika-priyāḥ—dear to those in the mode of goodness\n', 'https://gita.github.io/gita/data/verse_recitation/17/603.mp3', 17, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (604, 'कट्वम्ललवणात्युष्णतीक्ष्णरूक्षविदाहिनः।आहारा राजसस्येष्टा दुःखशोकामयप्रदाः।।17.9।।', 'kaṭv-amla-lavaṇāty-uṣhṇa- tīkṣhṇa-rūkṣha-vidāhinaḥ\nāhārā rājasasyeṣhṭā duḥkha-śhokāmaya-pradāḥ\n', 'kaṭu—bitter; amla—sour; lavaṇa—salty; ati-uṣhṇa—very hot; tīkṣhṇa—pungent; rūkṣha—dry; vidāhinaḥ—chiliful; āhārāḥ—food; rājasasya—to persons in the mode of passion; iṣhṭāḥ—dear; duḥkha—pain; śhoka—grief; āmaya—disease; pradāḥ—produce\n', 'https://gita.github.io/gita/data/verse_recitation/17/604.mp3', 17, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (605, 'यातयामं गतरसं पूति पर्युषितं च यत्।उच्छिष्टमपि चामेध्यं भोजनं तामसप्रियम्।।17.10।।', 'yāta-yāmaṁ gata-rasaṁ pūti paryuṣhitaṁ cha yat\nuchchhiṣhṭam api chāmedhyaṁ bhojanaṁ tāmasa-priyam\n', 'yāta-yāmam—stale foods; gata-rasam—tasteless; pūti—putrid; paryuṣhitam—polluted; cha—and; yat—which; uchchhiṣhṭam—left over; api—also; cha—and; amedhyam—impure; bhojanam—foods; tāmasa—to persons in the mode of ignorance; priyam—dear\n', 'https://gita.github.io/gita/data/verse_recitation/17/605.mp3', 17, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (606, 'अफलाकाङ्क्षिभिर्यज्ञो विधिदृष्टो य इज्यते।यष्टव्यमेवेति मनः समाधाय स सात्त्विकः।।17.11।।', 'aphalākāṅkṣhibhir yajño vidhi-driṣhṭo ya ijyate\nyaṣhṭavyam eveti manaḥ samādhāya sa sāttvikaḥ\n', 'aphala-ākāṅkṣhibhiḥ—without expectation of any reward; yajñaḥ—sacrifice; vidhi-driṣhṭaḥ—that is in accordance with the scriptural injunctions; yaḥ—which; ijyate—is performed; yaṣhṭavyam-eva-iti—ought to be offered; manaḥ—mind; samādhāya—with conviction; saḥ—that; sāttvikaḥ—of the nature of goodness\n', 'https://gita.github.io/gita/data/verse_recitation/17/606.mp3', 17, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (607, 'अभिसंधाय तु फलं दम्भार्थमपि चैव यत्।इज्यते भरतश्रेष्ठ तं यज्ञं विद्धि राजसम्।।17.12।।', 'abhisandhāya tu phalaṁ dambhārtham api chaiva yat\nijyate bharata-śhreṣhṭha taṁ yajñaṁ viddhi rājasam\n', 'abhisandhāya—motivated by; tu—but; phalam—the result; dambha—pride; artham—for the sake of; api—also; cha—and; eva—certainly; yat—that which; ijyate—is performed; bharata-śhreṣhṭha—Arjun, the best of the Bharatas; tam—that; yajñam—sacrifice; viddhi—know; rājasam—in the mode of passion\n', 'https://gita.github.io/gita/data/verse_recitation/17/607.mp3', 17, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (608, 'विधिहीनमसृष्टान्नं मन्त्रहीनमदक्षिणम्।श्रद्धाविरहितं यज्ञं तामसं परिचक्षते।।17.13।।', 'vidhi-hīnam asṛiṣhṭānnaṁ mantra-hīnam adakṣhiṇam\nśhraddhā-virahitaṁ yajñaṁ tāmasaṁ parichakṣhate\n', 'vidhi-hīnam—without scriptural direction; asṛiṣhṭa-annam—without distribution of prasādam; mantra-hīnam—with no chanting of the Vedic hymns; adakṣhiṇam—with no remunerations to the priests; śhraddhā—faith; virahitam—without; yajñam—sacrifice; tāmasam—in the mode of ignorance; parichakṣhate—is to be considered\n', 'https://gita.github.io/gita/data/verse_recitation/17/608.mp3', 17, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (609, 'देवद्विजगुरुप्राज्ञपूजनं शौचमार्जवम्।ब्रह्मचर्यमहिंसा च शारीरं तप उच्यते।।17.14।।', 'deva-dwija-guru-prājña- pūjanaṁ śhaucham ārjavam\nbrahmacharyam ahinsā cha śhārīraṁ tapa uchyate\n', 'deva—the Supreme Lord; dwija—the Brahmins; guru—the spiritual master; prājña—the elders; pūjanam—worship; śhaucham—cleanliness; ārjavam—simplicity; brahmacharyam—celibacy; ahinsā—non-violence; cha—and; śhārīram—of the body; tapaḥ—austerity; uchyate—is declared as\n', 'https://gita.github.io/gita/data/verse_recitation/17/609.mp3', 17, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (610, 'अनुद्वेगकरं वाक्यं सत्यं प्रियहितं च यत्।स्वाध्यायाभ्यसनं चैव वाङ्मयं तप उच्यते।।17.15।।', 'anudvega-karaṁ vākyaṁ satyaṁ priya-hitaṁ cha yat\nsvādhyāyābhyasanaṁ chaiva vāṅ-mayaṁ tapa uchyate\n', 'anudvega-karam—not causing distress; vākyam—words; satyam—truthful; priya- hitam—beneficial; cha—and; yat—which; svādhyāya-abhyasanam—recitation of the Vedic scriptures; cha eva—as well as; vāṅ-mayam—of speech; tapaḥ—austerity; uchyate—are declared as\n', 'https://gita.github.io/gita/data/verse_recitation/17/610.mp3', 17, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (611, 'मनःप्रसादः सौम्यत्वं मौनमात्मविनिग्रहः।भावसंशुद्धिरित्येतत्तपो मानसमुच्यते।।17.16।।', 'manaḥ-prasādaḥ saumyatvaṁ maunam ātma-vinigrahaḥ\nbhāva-sanśhuddhir ity etat tapo mānasam uchyate\n', 'manaḥ-prasādaḥ—serenity of thought; saumyatvam—gentleness; maunam—silence; ātma-vinigrahaḥ—self-control; bhāva-sanśhuddhiḥ—purity of purpose; iti—thus; etat—these; tapaḥ—austerity; mānasam—of the mind; uchyate—are declared as\n', 'https://gita.github.io/gita/data/verse_recitation/17/611.mp3', 17, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (612, 'श्रद्धया परया तप्तं तपस्तत्ित्रविधं नरैः।अफलाकाङ्क्षिभिर्युक्तैः सात्त्विकं परिचक्षते।।17.17।।', 'śhraddhayā parayā taptaṁ tapas tat tri-vidhaṁ naraiḥ\naphalākāṅkṣhibhir yuktaiḥ sāttvikaṁ parichakṣhate\n', 'śhraddhayā—with faith; parayā—transcendental; taptam—practiced; tapaḥ—austerity; tat—that; tri-vidham—three-fold; naraiḥ—by persons; aphala-ākāṅkṣhibhiḥ—without yearning for material rewards; yuktaiḥ—steadfast; sāttvikam—in the mode of goodness; parichakṣhate—are designated\n', 'https://gita.github.io/gita/data/verse_recitation/17/612.mp3', 17, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (613, 'सत्कारमानपूजार्थं तपो दम्भेन चैव यत्।क्रियते तदिह प्रोक्तं राजसं चलमध्रुवम्।।17.18।।', 'satkāra-māna-pūjārthaṁ tapo dambhena chaiva yat\nkriyate tad iha proktaṁ rājasaṁ chalam adhruvam\n', 'sat-kāra—respect; māna—honor; pūjā—adoration; artham—for the sake of; tapaḥ—austerity; dambhena—with ostentation; cha—also; eva—certainly; yat—which; kriyate—is performed; tat—that; iha—in this world; proktam—is said; rājasam—in the mode of passion; chalam—flickering; adhruvam—temporary\n', 'https://gita.github.io/gita/data/verse_recitation/17/613.mp3', 17, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (614, 'मूढग्राहेणात्मनो यत्पीडया क्रियते तपः।परस्योत्सादनार्थं वा तत्तामसमुदाहृतम्।।17.19।।', 'mūḍha-grāheṇātmano yat pīḍayā kriyate tapaḥ\nparasyotsādanārthaṁ vā tat tāmasam udāhṛitam\n', 'mūḍha—those with confused notions; grāheṇa—with endeavor; ātmanaḥ—one’s own self; yat—which; pīḍayā—torturing; kriyate—is performed; tapaḥ—austerity; parasya—of others; utsādana-artham—for harming; vā—or; tat—that; tāmasam—in the mode of ignorance; udāhṛitam—is described to be\n', 'https://gita.github.io/gita/data/verse_recitation/17/614.mp3', 17, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (615, 'दातव्यमिति यद्दानं दीयतेऽनुपकारिणे।देशे काले च पात्रे च तद्दानं सात्त्विकं स्मृतम्।।17.20।।', 'dātavyam iti yad dānaṁ dīyate ‘nupakāriṇe\ndeśhe kāle cha pātre cha tad dānaṁ sāttvikaṁ smṛitam\n', 'dātavyam—worthy of charity; iti—thus; yat—which; dānam—charity; dīyate—is given; anupakāriṇe—to one who cannot give in return; deśhe—in the proper place; kāle—at the proper time; cha—and; pātre—to a worthy person; cha—and; tat—that; dānam—charity; sāttvikam—in the mode of goodness; smṛitam—is stated to be\n', 'https://gita.github.io/gita/data/verse_recitation/17/615.mp3', 17, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (616, 'यत्तु प्रत्युपकारार्थं फलमुद्दिश्य वा पुनः।दीयते च परिक्लिष्टं तद्दानं राजसं स्मृतम्।।17.21।।', 'yat tu pratyupakārārthaṁ phalam uddiśhya vā punaḥ\ndīyate cha parikliṣhṭaṁ tad dānaṁ rājasaṁ smṛitam\n', 'yat—which; tu—but; prati-upakāra-artham—with the hope of a return; phalam—reward; uddiśhya—expectation; vā—or; punaḥ—again; dīyate—is given; cha—and; parikliṣhṭam—reluctantly; tat—that; dānam—charity; rājasam—in the mode of passion; smṛitam—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/17/616.mp3', 17, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (617, 'अदेशकाले यद्दानमपात्रेभ्यश्च दीयते।असत्कृतमवज्ञातं तत्तामसमुदाहृतम्।।17.22।।', 'adeśha-kāle yad dānam apātrebhyaśh cha dīyate\nasat-kṛitam avajñātaṁ tat tāmasam udāhṛitam\n', 'adeśha—at the wrong place; kāle—at the wrong time; yat—which; dānam—charity; apātrebhyaḥ—to unworthy persons; cha—and; dīyate—is given; asat-kṛitam—without respect; avajñātam—with contempt; tat—that; tāmasam—of the nature of nescience; udāhṛitam—is held to be\n', 'https://gita.github.io/gita/data/verse_recitation/17/617.mp3', 17, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (618, ' तत्सदिति निर्देशो ब्रह्मणस्त्रिविधः स्मृतः।ब्राह्मणास्तेन वेदाश्च यज्ञाश्च विहिताः पुरा।।17.23।।', 'oṁ tat sad iti nirdeśho brahmaṇas tri-vidhaḥ smṛitaḥ\nbrāhmaṇās tena vedāśh cha yajñāśh cha vihitāḥ purā\n', 'om tat sat—syllables representing aspects of transcendence; iti—thus; nirdeśhaḥ—symbolic representatives; brahmaṇaḥ—the Supreme Absolute Truth; tri-vidhaḥ—of three kinds; smṛitaḥ—have been declared; brāhmaṇāḥ—the priests; tena—from them; vedāḥ—scriptures; cha—and; yajñāḥ—sacrifice; cha—and; vihitāḥ—came about; purā—from the beginning of creation\n', 'https://gita.github.io/gita/data/verse_recitation/17/618.mp3', 17, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (619, 'तस्मादोमित्युदाहृत्य यज्ञदानतपःक्रियाः।प्रवर्तन्ते विधानोक्ताः सततं ब्रह्मवादिनाम्।।17.24।।', 'tasmād oṁ ity udāhṛitya yajña-dāna-tapaḥ-kriyāḥ\npravartante vidhānoktāḥ satataṁ brahma-vādinām\n', 'tasmāt—therefore; om—sacred syllable om; iti—thus; udāhṛitya—by uttering; yajña—sacrifice; dāna—charity; tapaḥ—penance; kriyāḥ—performing; pravartante—begin; vidhāna-uktāḥ—according to the prescriptions of Vedic injunctions; satatam—always; brahma-vādinām—expounders of the Vedas\n', 'https://gita.github.io/gita/data/verse_recitation/17/619.mp3', 17, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (620, 'तदित्यनभिसन्धाय फलं यज्ञतपःक्रियाः।दानक्रियाश्च विविधाः क्रियन्ते मोक्षकाङ्क्षि।।17.25।।', 'tad ity anabhisandhāya phalaṁ yajña-tapaḥ-kriyāḥ\ndāna-kriyāśh cha vividhāḥ kriyante mokṣha-kāṅkṣhibhiḥ\n', 'tat—the syllable Tat; iti—thus; anabhisandhāya—without desiring; phalam—fruitive rewards; yajña—sacrifice; tapaḥ—austerity; kriyāḥ—acts; dāna—charity; kriyāḥ—acts; cha—and; vividhāḥ—various; kriyante—are done; mokṣha-kāṅkṣhibhiḥ—by seekers of freedom from material entanglements\n', 'https://gita.github.io/gita/data/verse_recitation/17/620.mp3', 17, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (621, 'सद्भावे साधुभावे च सदित्येतत्प्रयुज्यते।प्रशस्ते कर्मणि तथा सच्छब्दः पार्थ युज्यते।।17.26।।', 'sad-bhāve sādhu-bhāve cha sad ity etat prayujyate\npraśhaste karmaṇi tathā sach-chhabdaḥ pārtha yujyate\n', 'sat-bhāve—with the intention of eternal existence and goodness; sādhu-bhāve—with auspicious intention; cha—also; sat—the syllable Sat; iti—thus; etat—this; prayujyate—is used; praśhaste—auspicious; karmaṇi—action; tathā—also; sat-śhabdaḥ—the word “Sat”; pārtha—Arjun, the son of Pritha; yujyate—is used;\n', 'https://gita.github.io/gita/data/verse_recitation/17/621.mp3', 17, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (622, 'यज्ञे तपसि दाने च स्थितिः सदिति चोच्यते।कर्म चैव तदर्थीयं सदित्येवाभिधीयते।।17.27।।', 'yajñe tapasi dāne cha sthitiḥ sad iti chochyate\nkarma chaiva tad-arthīyaṁ sad ity evābhidhīyate\n', 'yajñe—in sacrifice; tapasi—in penance; dāne—in charity; cha—and; sthitiḥ—established in steadiness; sat—the syllable Sat; iti—thus; cha—and; uchyate—is pronounced; karma—action; cha—and; eva—indeed; tat-arthīyam—for such purposes; sat—the syllable Sat; iti—thus; eva—indeed; abhidhīyate—is described\n', 'https://gita.github.io/gita/data/verse_recitation/17/622.mp3', 17, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (623, 'अश्रद्धया हुतं दत्तं तपस्तप्तं कृतं च यत्।असदित्युच्यते पार्थ न च तत्प्रेत्य नो इह।।17.28।।', 'aśhraddhayā hutaṁ dattaṁ tapas taptaṁ kṛitaṁ cha yat\nasad ity uchyate pārtha na cha tat pretya no iha\n', 'aśhraddhayā—without faith; hutam—sacrifice; dattam—charity; tapaḥ—penance; taptam—practiced; kṛitam—done; cha—and; yat—which; asat—perishable; iti—thus; uchyate—are termed as; pārtha—Arjun, the son of Pritha; na—not; cha—and; tat—that; pretya—in the next world; na u—not; iha—in this world\n', 'https://gita.github.io/gita/data/verse_recitation/17/623.mp3', 17, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (624, 'अर्जुन उवाच\n\nसंन्यासस्य महाबाहो तत्त्वमिच्छामि वेदितुम्।\n\nत्यागस्य च हृषीकेश पृथक्केशिनिषूदन।।18.1।।\n ', 'arjuna uvācha\nsannyāsasya mahā-bāho tattvam ichchhāmi veditum\ntyāgasya cha hṛiṣhīkeśha pṛithak keśhi-niṣhūdana\n', 'arjunaḥ uvācha—Arjun said; sanyāsasya—of renunciation of actions; mahā-bāho—mighty-armed one; tattvam—the truth; ichchhāmi—I wish; veditum—to understand; tyāgasya—of renunciation of desires for enjoying the fruits of actions; cha—and; hṛiṣhīkeśha—Krishna, the Lord of the senses; pṛithak—distinctively; keśhī-niṣhūdana—Krishna, the killer of the Keshi demon\n', 'https://gita.github.io/gita/data/verse_recitation/18/624.mp3', 18, 1);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (625, 'श्री भगवानुवाच\n\nकाम्यानां कर्मणां न्यासं संन्यासं कवयो विदुः।\n\nसर्वकर्मफलत्यागं प्राहुस्त्यागं विचक्षणाः।।18.2।।\n ', 'śhrī-bhagavān uvācha\nkāmyānāṁ karmaṇāṁ nyāsaṁ sannyāsaṁ kavayo viduḥ\nsarva-karma-phala-tyāgaṁ prāhus tyāgaṁ vichakṣhaṇāḥ\n', 'śhrī-bhagavān uvācha—the Supreme Divine Personality said; kāmyānām—desireful; karmaṇām—of actions; nyāsam—giving up; sanyāsam—renunciation of actions; kavayaḥ—the learned; viduḥ—to understand; sarva—all; karma-phala—fruits of actions; tyāgam—renunciation of desires for enjoying the fruits of actions; prāhuḥ—declare; tyāgam—renunciation of desires for enjoying the fruits of actions; vichakṣhaṇāḥ—the wise\n', 'https://gita.github.io/gita/data/verse_recitation/18/625.mp3', 18, 2);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (626, 'त्याज्यं दोषवदित्येके कर्म प्राहुर्मनीषिणः।\n\nयज्ञदानतपःकर्म न त्याज्यमिति चापरे।।18.3।।\n ', 'tyājyaṁ doṣha-vad ity eke karma prāhur manīṣhiṇaḥ\nyajña-dāna-tapaḥ-karma na tyājyam iti chāpare\n', 'tyājyam—should be given up; doṣha-vat—as evil; iti—thus; eke—some; karma—actions; prāhuḥ—declare; manīṣhiṇaḥ—the learned; yajña—sacrifice; dāna—charity; tapaḥ—penance; karma—acts; na—never; tyājyam—should be abandoned; iti—thus; cha—and; apare—others\n', 'https://gita.github.io/gita/data/verse_recitation/18/626.mp3', 18, 3);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (627, 'निश्चयं श्रृणु मे तत्र त्यागे भरतसत्तम।त्यागो हि पुरुषव्याघ्र त्रिविधः संप्रकीर्तितः।।18.4।।', 'niśhchayaṁ śhṛiṇu me tatra tyāge bharata-sattama\ntyāgo hi puruṣha-vyāghra tri-vidhaḥ samprakīrtitaḥ\n', 'niśhchayam—conclusion; śhṛiṇu—hear; me—my; tatra—there; tyāge—about renunciation of desires for enjoying the fruits of actions; bharata-sat-tama—best of the Bharatas; tyāgaḥ—renunciation of desires for enjoying the fruits of actions; hi—indeed; puruṣha-vyāghra—tiger amongst men; tri-vidhaḥ—of three kinds; samprakīrtitaḥ—declared\n', 'https://gita.github.io/gita/data/verse_recitation/18/627.mp3', 18, 4);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (628, 'यज्ञदानतपःकर्म न त्याज्यं कार्यमेव तत्।यज्ञो दानं तपश्चैव पावनानि मनीषिणाम्।।18.5।।', 'yajña-dāna-tapaḥ-karma na tyājyaṁ kāryam eva tat\nyajño dānaṁ tapaśh chaiva pāvanāni manīṣhiṇām\n', 'yajña—sacrifice; dāna—charity; tapaḥ—penance; karma—actions; na—never; tyājyam—should be abandoned; kāryam eva—must certainly be performed; tat—that; yajñaḥ—sacrifice; dānam—charity; tapaḥ—penance; cha—and; eva—indeed; pāvanāni—purifying; manīṣhiṇām—for the wise\n', 'https://gita.github.io/gita/data/verse_recitation/18/628.mp3', 18, 5);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (629, 'एतान्यपि तु कर्माणि सङ्गं त्यक्त्वा फलानि च।कर्तव्यानीति मे पार्थ निश्िचतं मतमुत्तमम्।।18.6।।', 'etāny api tu karmāṇi saṅgaṁ tyaktvā phalāni cha\nkartavyānīti me pārtha niśhchitaṁ matam uttamam\n', 'etāni—these; api tu—must certainly be; karmāṇi—activities; saṅgam—attachment; tyaktvā—giving up; phalāni—rewards; cha—and; kartavyāni—should be done as duty; iti—such; me—my; pārtha—Arjun, the son of Pritha; niśhchitam—definite; matam—opinion; uttamam—supreme\n', 'https://gita.github.io/gita/data/verse_recitation/18/629.mp3', 18, 6);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (630, 'नियतस्य तु संन्यासः कर्मणो नोपपद्यते।मोहात्तस्य परित्यागस्तामसः परिकीर्तितः।।18.7।।', 'niyatasya tu sannyāsaḥ karmaṇo nopapadyate\nmohāt tasya parityāgas tāmasaḥ parikīrtitaḥ\n', 'niyatasya—of prescribed duties; tu—but; sanyāsaḥ—renunciation; karmaṇaḥ—actions; na—never; upapadyate—to be performed; mohāt—deluded; tasya—of that; parityāgaḥ—renunciation; tāmasaḥ—in the mode of ignorance; parikīrtitaḥ—has been declared\n', 'https://gita.github.io/gita/data/verse_recitation/18/630.mp3', 18, 7);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (631, 'दुःखमित्येव यत्कर्म कायक्लेशभयात्त्यजेत्।स कृत्वा राजसं त्यागं नैव त्यागफलं लभेत्।।18.8।।', 'duḥkham ity eva yat karma kāya-kleśha-bhayāt tyajet\nsa kṛitvā rājasaṁ tyāgaṁ naiva tyāga-phalaṁ labhet\n', 'duḥkham—troublesome; iti—as; eva—indeed; yat—which; karma—duties; kāya—bodily; kleśha—discomfort; bhayāt—out of fear; tyajet—giving up; saḥ—they; kṛitvā—having done; rājasam—in the mode of passion; tyāgam—renunciation of desires for enjoying the fruits of actions; na—never; eva—certainly; tyāga—renunciation of desires for enjoying the fruits of actions; phalam—result; labhet—attain\n', 'https://gita.github.io/gita/data/verse_recitation/18/631.mp3', 18, 8);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (632, 'कार्यमित्येव यत्कर्म नियतं क्रियतेऽर्जुन।सङ्गं त्यक्त्वा फलं चैव स त्यागः सात्त्विको मतः।।18.9।।', 'kāryam ity eva yat karma niyataṁ kriyate ‘rjuna\nsaṅgaṁ tyaktvā phalaṁ chaiva sa tyāgaḥ sāttviko mataḥ\n', 'kāryam—as a duty; iti—as; eva—indeed; yat—which; karma niyatam—obligatory actions; kriyate—are performed; arjuna—Arjun; saṅgam—attachment; tyaktvā—relinquishing; phalam—reward; cha—and; eva—certainly; saḥ—such; tyāgaḥ—renunciation of desires for enjoying the fruits of actions; sāttvikaḥ—in the mode of goodness; mataḥ—considered\n', 'https://gita.github.io/gita/data/verse_recitation/18/632.mp3', 18, 9);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (633, 'न द्वेष्ट्यकुशलं कर्म कुशले नानुषज्जते।त्यागी सत्त्वसमाविष्टो मेधावी छिन्नसंशयः।।18.10।।', 'na dveṣhṭy akuśhalaṁ karma kuśhale nānuṣhajjate\ntyāgī sattva-samāviṣhṭo medhāvī chhinna-sanśhayaḥ\n', 'na—neither; dveṣhṭi—hate; akuśhalam—disagreeable; karma—work; kuśhale—to an agreeable; na—nor; anuṣhajjate—seek; tyāgī—one who renounces desires for enjoying the fruits of actions; sattva—in the mode of goodness; samāviṣhṭaḥ—endowed with; medhāvī—intelligent; chhinna-sanśhayaḥ—those who have no doubts\n', 'https://gita.github.io/gita/data/verse_recitation/18/633.mp3', 18, 10);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (634, 'न हि देहभृता शक्यं त्यक्तुं कर्माण्यशेषतः।यस्तु कर्मफलत्यागी स त्यागीत्यभिधीयते।।18.11।।', 'na hi deha-bhṛitā śhakyaṁ tyaktuṁ karmāṇy aśheṣhataḥ\nyas tu karma-phala-tyāgī sa tyāgīty abhidhīyate\n', 'na—not; hi—indeed; deha-bhṛitā—for the embodied being; śhakyam—possible; tyaktum—to give up; karmāṇi—activities; aśheṣhataḥ—entirely; yaḥ—who; tu—but; karma-phala—fruits of actions; tyāgī—one who renounces all desires for enjoying the fruits of actions; saḥ—they; tyāgī—one who renounces all desires for enjoying the fruits of actions; iti—as; abhidhīyate—are said\n', 'https://gita.github.io/gita/data/verse_recitation/18/634.mp3', 18, 11);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (635, 'अनिष्टमिष्टं मिश्रं च त्रिविधं कर्मणः फलम्।भवत्यत्यागिनां प्रेत्य न तु संन्यासिनां क्वचित्।।18.12।।', 'aniṣhṭam iṣhṭaṁ miśhraṁ cha tri-vidhaṁ karmaṇaḥ phalam\nbhavaty atyāgināṁ pretya na tu sannyāsināṁ kvachit\n', 'aniṣhṭam—unpleasant; iṣhṭam—pleasant; miśhram—mixed; cha—and; tri-vidham—three-fold; karmaṇaḥ phalam—fruits of actions; bhavati—accrue; atyāginām—to those who are attached to persona reward; pretya—after death; na—not; tu—but; sanyāsinām—for the renouncers of actions; kvachit—ever\n', 'https://gita.github.io/gita/data/verse_recitation/18/635.mp3', 18, 12);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (636, 'पञ्चैतानि महाबाहो कारणानि निबोध मे।सांख्ये कृतान्ते प्रोक्तानि सिद्धये सर्वकर्मणाम्।।18.13।।', 'pañchaitāni mahā-bāho kāraṇāni nibodha me\nsānkhye kṛitānte proktāni siddhaye sarva-karmaṇām\n', 'pañcha—five; etāni—these; mahā-bāho—mighty-armed one; kāraṇāni—causes; nibodha—listen; me—from me; sānkhye—of Sānkya; kṛita-ante—stop reactions of karmas; proktāni—explains; siddhaye—for the accomplishment; sarva—all; karmaṇām—of karmas\n', 'https://gita.github.io/gita/data/verse_recitation/18/636.mp3', 18, 13);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (637, 'अधिष्ठानं तथा कर्ता करणं च पृथग्विधम्।विविधाश्च पृथक्चेष्टा दैवं चैवात्र पञ्चमम्।।18.14।।', 'adhiṣhṭhānaṁ tathā kartā karaṇaṁ cha pṛithag-vidham\nvividhāśh cha pṛithak cheṣhṭā daivaṁ chaivātra pañchamam\n', 'adhiṣhṭhānam—the body; tathā—also; kartā—the doer (soul); karaṇam—senses; cha—and; pṛithak-vidham—various kinds; vividhāḥ—many; cha—and; pṛithak—distinct; cheṣhṭāḥ—efforts; daivam—Divine Providence; cha eva atra—these certainly are (causes); pañchamam—the fifth\n', 'https://gita.github.io/gita/data/verse_recitation/18/637.mp3', 18, 14);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (638, 'शरीरवाङ्मनोभिर्यत्कर्म प्रारभते नरः।न्याय्यं वा विपरीतं वा पञ्चैते तस्य हेतवः।।18.15।।', 'śharīra-vāṅ-manobhir yat karma prārabhate naraḥ\nnyāyyaṁ vā viparītaṁ vā pañchaite tasya hetavaḥ\n', 'śharīra-vāk-manobhiḥ—with body, speech, or mind; yat—which; karma—action; prārabhate—performs; naraḥ—a person; nyāyyam—proper; vā—or; viparītam—improper; vā—or; pañcha—five; ete—these; tasya—their; hetavaḥ—factors;\n', 'https://gita.github.io/gita/data/verse_recitation/18/638.mp3', 18, 15);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (639, 'तत्रैवं सति कर्तारमात्मानं केवलं तु यः।पश्यत्यकृतबुद्धित्वान्न स पश्यति दुर्मतिः।।18.16।।', 'tatraivaṁ sati kartāram ātmānaṁ kevalaṁ tu yaḥ\npaśhyaty akṛita-buddhitvān na sa paśhyati durmatiḥ\n', 'tatra—there; evam sati—in spite of this; kartāram—the doer; ātmānam—the soul; kevalam—only; tu—but; yaḥ—who; paśhyati—see; akṛita-buddhitvāt—with impure intellect; na—not; saḥ—they; paśhyati—see; durmatiḥ—foolish\n', 'https://gita.github.io/gita/data/verse_recitation/18/639.mp3', 18, 16);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (640, 'यस्य नाहंकृतो भावो बुद्धिर्यस्य न लिप्यते।हत्वापि स इमाँल्लोकान्न हन्ति न निबध्यते।।18.17।।', 'yasya nāhankṛito bhāvo buddhir yasya na lipyate\nhatvā ‘pi sa imāl lokān na hanti na nibadhyate\n', 'yasya—whose; na ahankṛitaḥ—free from the ego of being the doer; bhāvaḥ—nature; buddhiḥ—intellect; yasya—whose; na lipyate—unattached; hatvā—slay; api—even; saḥ—they; imān—this; lokān—living beings; na—neither; hanti—kill; na—nor; nibadhyate—get bound\n', 'https://gita.github.io/gita/data/verse_recitation/18/640.mp3', 18, 17);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (641, 'ज्ञानं ज्ञेयं परिज्ञाता त्रिविधा कर्मचोदना।करणं कर्म कर्तेति त्रिविधः कर्मसंग्रहः।।18.18।।', 'jñānaṁ jñeyaṁ parijñātā tri-vidhā karma-chodanā\nkaraṇaṁ karma karteti tri-vidhaḥ karma-saṅgrahaḥ\n', 'jñānam—knowledge; jñeyam—the object of knowledge; parijñātā—the knower; tri-vidhā—three factors; karma-chodanā—factors that induce action; karaṇam—the instrumens of action; karma—the act; kartā—the doer; iti—thus; tri-vidhaḥ—threefold; karma-saṅgrahaḥ—constituents of action\n', 'https://gita.github.io/gita/data/verse_recitation/18/641.mp3', 18, 18);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (642, 'ज्ञानं कर्म च कर्ता च त्रिधैव गुणभेदतः।प्रोच्यते गुणसंख्याने यथावच्छृणु तान्यपि।।18.19।।', 'jñānaṁ karma cha kartā cha tridhaiva guṇa-bhedataḥ\nprochyate guṇa-saṅkhyāne yathāvach chhṛiṇu tāny api\n', 'jñānam—knowledge; karma—action; cha—and; kartā—doer; cha—also; tridhā—of three kinds; eva—certainly; guṇa-bhedataḥ—distinguished according to the three modes of material nature; prochyate—are declared; guṇa-saṅkhyāne—Sānkhya philosophy, which describes the modes of material nature; yathā-vat—as they are; śhṛiṇu—listen; tāni—them; api—also\n', 'https://gita.github.io/gita/data/verse_recitation/18/642.mp3', 18, 19);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (643, 'सर्वभूतेषु येनैकं भावमव्ययमीक्षते।अविभक्तं विभक्तेषु तज्ज्ञानं विद्धि सात्त्विकम्।।18.20।।', 'sarva-bhūteṣhu yenaikaṁ bhāvam avyayam īkṣhate\navibhaktaṁ vibhakteṣhu taj jñānaṁ viddhi sāttvikam\n', 'sarva-bhūteṣhu—within all living beings; yena—by which; ekam—one; bhāvam—nature; avyayam—imperishable; īkṣhate—one sees; avibhaktam—undivided; vibhakteṣhu—in diversity; tat—that; jñānam—knowledge; viddhi—understand; sāttvikam—in the mode of goodness\n', 'https://gita.github.io/gita/data/verse_recitation/18/643.mp3', 18, 20);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (644, 'पृथक्त्वेन तु यज्ज्ञानं नानाभावान्पृथग्विधान्।वेत्ति सर्वेषु भूतेषु तज्ज्ञानं विद्धि राजसम्।।18.21।।', 'pṛithaktvena tu yaj jñānaṁ nānā-bhāvān pṛithag-vidhān\nvetti sarveṣhu bhūteṣhu taj jñānaṁ viddhi rājasam\n', 'pṛithaktvena—unconnected; tu—however; yat—which; jñānam—knowledge; nānā-bhāvān—manifold entities; pṛithak-vidhān—of diversity; vetti—consider; sarveṣhu—in all; bhūteṣhu—living entities; tat—that; jñānam—knowledge; viddhi—know; rājasam—in the mode of passion\n', 'https://gita.github.io/gita/data/verse_recitation/18/644.mp3', 18, 21);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (645, 'यत्तु कृत्स्नवदेकस्मिन्कार्ये सक्तमहैतुकम्।अतत्त्वार्थवदल्पं च तत्तामसमुदाहृतम्।।18.22।।', 'yat tu kṛitsna-vad ekasmin kārye saktam ahaitukam\natattvārtha-vad alpaṁ cha tat tāmasam udāhṛitam\n', 'yat—which; tu—but; kṛitsna-vat—as if it encompasses the whole; ekasmin—in single; kārye—action; saktam—engrossed; ahaitukam—without a reason; atattva-artha-vat—not based on truth; alpam—fragmental; cha—and; tat—that; tāmasam—in the mode of ignorance; udāhṛitam—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/18/645.mp3', 18, 22);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (646, 'नियतं सङ्गरहितमरागद्वेषतः कृतम्।अफलप्रेप्सुना कर्म यत्तत्सात्त्विकमुच्यते।।18.23।।', 'niyataṁ saṅga-rahitam arāga-dveṣhataḥ kṛitam\naphala-prepsunā karma yat tat sāttvikam uchyate\n', 'niyatam—in accordance with scriptures; saṅga-rahitam—free from attachment; arāga-dveṣhataḥ—free from attachment and aversion; kṛitam—done; aphala-prepsunā—without desire for rewards; karma—action; yat—which; tat—that; sāttvikam—in the mode of goodness; uchyate—is called\n', 'https://gita.github.io/gita/data/verse_recitation/18/646.mp3', 18, 23);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (647, 'यत्तु कामेप्सुना कर्म साहङ्कारेण वा पुनः।क्रियते बहुलायासं तद्राजसमुदाहृतम्।।18.24।।', 'yat tu kāmepsunā karma sāhankāreṇa vā punaḥ\nkriyate bahulāyāsaṁ tad rājasam udāhṛitam\n', 'yat—which; tu—but; kāma-īpsunā—prompted by selfish desire; karma—action; sa-ahaṅkāreṇa—with pride; vā—or; punaḥ—again; kriyate—enacted; bahula-āyāsam—stressfully; tat—that; rājasam—in the nature of passion; udāhṛitam—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/18/647.mp3', 18, 24);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (648, 'अनुबन्धं क्षयं हिंसामनपेक्ष्य च पौरुषम्।मोहादारभ्यते कर्म यत्तत्तामसमुच्यते।।18.25।।', 'anubandhaṁ kṣhayaṁ hinsām anapekṣhya cha pauruṣham\nmohād ārabhyate karma yat tat tāmasam uchyate\n', 'anubandham—consequences; kṣhayam—loss; hinsām—injury; anapekṣhya—by disregarding; cha—and; pauruṣham—one’s own ability; mohāt—out of delusion; ārabhyate—is begun; karma—action; yat—which; tat—that; tāmasam—in the mode of ignorance; uchyate—is declared to be\n', 'https://gita.github.io/gita/data/verse_recitation/18/648.mp3', 18, 25);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (649, 'मुक्तसङ्गोऽनहंवादी धृत्युत्साहसमन्वितः।सिद्ध्यसिद्ध्योर्निर्विकारः कर्ता सात्त्विक उच्यते।।18.26।।', 'mukta-saṅgo ‘nahaṁ-vādī dhṛity-utsāha-samanvitaḥ\nsiddhy-asiddhyor nirvikāraḥ kartā sāttvika uchyate\n', 'mukta-saṅgaḥ—free from worldly attachment; anaham-vādī—free from ego; dhṛiti—strong resolve; utsāha—zeal; samanvitaḥ—endowed with; siddhi-asiddhyoḥ—in success and failure; nirvikāraḥ—unaffected; kartā—worker; sāttvikaḥ—in the mode of goodness; uchyate—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/18/649.mp3', 18, 26);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (650, 'रागी कर्मफलप्रेप्सुर्लुब्धो हिंसात्मकोऽशुचिः।हर्षशोकान्वितः कर्ता राजसः परिकीर्तितः।।18.27।।', 'rāgī karma-phala-prepsur lubdho hinsātmako ‘śhuchiḥ\nharṣha-śhokānvitaḥ kartā rājasaḥ parikīrtitaḥ\n', 'rāgī—craving; karma-phala—fruit of work; prepsuḥ—covet; lubdhaḥ—greedy; hinsā-ātmakaḥ—violent-natured; aśhuchiḥ—impure; harṣha-śhoka-anvitaḥ—moved by joy and sorrow; kartā—performer; rājasaḥ—in the mode of passion; parikīrtitaḥ—is declared\n', 'https://gita.github.io/gita/data/verse_recitation/18/650.mp3', 18, 27);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (651, 'अयुक्तः प्राकृतः स्तब्धः शठो नैष्कृतिकोऽलसः।विषादी दीर्घसूत्री च कर्ता तामस उच्यते।।18.28।।', 'ayuktaḥ prākṛitaḥ stabdhaḥ śhaṭho naiṣhkṛitiko ‘lasaḥ\nviṣhādī dīrgha-sūtrī cha kartā tāmasa uchyate\n', 'ayuktaḥ—undisciplined; prākṛitaḥ—vulgar; stabdhaḥ—obstinate; śhaṭhaḥ—cunning; naiṣhkṛitikaḥ—dishonest or vile; alasaḥ—slothful; viṣhādī—unhappy and morose; dīrgha-sūtrī—procrastinating; cha—and; kartā—performer; tāmasaḥ—in the mode of ignorance; uchyate—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/18/651.mp3', 18, 28);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (652, 'बुद्धेर्भेदं धृतेश्चैव गुणतस्त्रिविधं श्रृणु।प्रोच्यमानमशेषेण पृथक्त्वेन धनञ्जय।।18.29।।', 'buddher bhedaṁ dhṛiteśh chaiva guṇatas tri-vidhaṁ śhṛiṇu\nprochyamānam aśheṣheṇa pṛithaktvena dhanañjaya\n', 'buddheḥ—of intellect; bhedam—the distinctions; dhṛiteḥ—of determination; cha—and; eva—certainly; guṇataḥ tri-vidham—according to the three modes of material nature; śhṛiṇu—hear; prochyamānam—described; aśheṣheṇa—in detail; pṛithaktvena—distinctly; dhanañjaya—conqueror of wealth, Arjun\n', 'https://gita.github.io/gita/data/verse_recitation/18/652.mp3', 18, 29);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (653, 'प्रवृत्तिं च निवृत्तिं च कार्याकार्ये भयाभये।बन्धं मोक्षं च या वेत्ति बुद्धिः सा पार्थ सात्त्विकी।।18.30।।', 'pravṛittiṁ cha nivṛittiṁ cha kāryākārye bhayābhaye\nbandhaṁ mokṣhaṁ cha yā vetti buddhiḥ sā pārtha sāttvikī\n', 'pravṛittim—activities; cha—and; nivṛittim—renuncation from action; cha—and; kārya—proper action; akārye—improper action; bhaya—fear; abhaye—without fear; bandham—what is binding; mokṣham—what is liberating; cha—and; yā—which; vetti—understands; buddhiḥ—intellect; sā—that; pārtha—son of Pritha; sāttvikī—in the nature of goodness\n', 'https://gita.github.io/gita/data/verse_recitation/18/653.mp3', 18, 30);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (654, 'यया धर्ममधर्मं च कार्यं चाकार्यमेव च।अयथावत्प्रजानाति बुद्धिः सा पार्थ राजसी।।18.31।।', 'yayā dharmam adharmaṁ cha kāryaṁ chākāryam eva cha\nayathāvat prajānāti buddhiḥ sā pārtha rājasī\n', 'yayā—by which; dharmam—righteousness; adharmam—unrighteousness; cha—and; kāryam—right conduct; cha—and; akāryam—wrong conduct; eva—certainly; cha—and; ayathā-vat—confused; prajānāti—distinguish; buddhiḥ—intellect; sā—that; pārtha—Arjun, the son of Pritha; rājasī—in the mode of passion\n', 'https://gita.github.io/gita/data/verse_recitation/18/654.mp3', 18, 31);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (655, 'अधर्मं धर्ममिति या मन्यते तमसाऽऽवृता।सर्वार्थान्विपरीतांश्च बुद्धिः सा पार्थ तामसी।।18.32।।', 'adharmaṁ dharmam iti yā manyate tamasāvṛitā\nsarvārthān viparītānśh cha buddhiḥ sā pārtha tāmasī\n', 'adharmam—irreligion; dharmam—religion; iti—thus; yā—which; manyate—imagines; tamasa-āvṛitā—shrouded in darkness; sarva-arthān—all things; viparītān—opposite; cha—and; buddhiḥ—intellect; sā—that; pārtha—Arjun, the son of Pritha; tāmasī—of the nature of ignorance\n', 'https://gita.github.io/gita/data/verse_recitation/18/655.mp3', 18, 32);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (656, 'धृत्या यया धारयते मनःप्राणेन्द्रियक्रियाः।योगेनाव्यभिचारिण्या धृतिः सा पार्थ सात्त्विकी।।18.33।।', 'dhṛityā yayā dhārayate manaḥ-prāṇendriya-kriyāḥ\nyogenāvyabhichāriṇyā dhṛitiḥ sā pārtha sāttvikī\n', 'dhṛityā—by determining; yayā—which; dhārayate—sustains; manaḥ—of the mind; prāṇa—life-airs; indriya—senses; kriyāḥ—activities; yogena—through Yog; avyabhichāriṇyā—with steadfastness; dhṛitiḥ—determination; sā—that; pārtha—Arjun, the son of Pritha; sāttvikī—in the mode of goodness\n', 'https://gita.github.io/gita/data/verse_recitation/18/656.mp3', 18, 33);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (657, 'यया तु धर्मकामार्थान् धृत्या धारयतेऽर्जुन।प्रसङ्गेन फलाकाङ्क्षी धृतिः सा पार्थ राजसी।।18.34।।', 'yayā tu dharma-kāmārthān dhṛityā dhārayate ‘rjuna\nprasaṅgena phalākāṅkṣhī dhṛitiḥ sā pārtha rājasī\n', 'yayā—by which; tu—but; dharma-kāma-arthān—duty, pleasures, and wealth; dhṛityā—through steadfast will; dhārayate—holds; arjuna—Arjun; prasaṅgena—due of attachment; phala-ākāṅkṣhī—desire for rewards; dhṛitiḥ—determination; sā—that; pārtha—Arjun, the son of Pritha; rājasī—in the mode of passion\n', 'https://gita.github.io/gita/data/verse_recitation/18/657.mp3', 18, 34);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (658, 'यया स्वप्नं भयं शोकं विषादं मदमेव च।न विमुञ्चति दुर्मेधा धृतिः सा पार्थ तामसी।।18.35।।', 'yayā svapnaṁ bhayaṁ śhokaṁ viṣhādaṁ madam eva cha\nna vimuñchati durmedhā dhṛitiḥ sā pārtha tāmasī\n', 'yayā—in which; svapnam—dreaming; bhayam—fearing; śhokam—grieving; viṣhādam—despair; madam—conceit; eva—indeed; cha—and; na—not; vimuñchati—give up; durmedhā—unintelligent; dhṛitiḥ—resolve; sā—that; pārtha—Arjun, the son of Pritha; tāmasī—in the mode of ignorance\n', 'https://gita.github.io/gita/data/verse_recitation/18/658.mp3', 18, 35);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (659, 'सुखं त्विदानीं त्रिविधं श्रृणु मे भरतर्षभ।अभ्यासाद्रमते यत्र दुःखान्तं च निगच्छति।।18.36।।', 'sukhaṁ tv idānīṁ tri-vidhaṁ śhṛiṇu me bharatarṣhabha\nabhyāsād ramate yatra duḥkhāntaṁ cha nigachchhati\n yat tad agre viṣham iva pariṇāme ‘mṛitopamam\ntat sukhaṁ sāttvikaṁ proktam ātma-buddhi-prasāda-jam\n', 'sukham—happiness; tu—but; idānīm—now; tri-vidham—of three kinds; śhṛiṇu—hear; me—from me; bharata-ṛiṣhabha—Arjun, the best of the Bharatas; abhyāsāt—by practice; ramate—rejoices; yatra—in which; duḥkha-antam—end of all suffering; cha—and; nigachchhati—reaches\n yat—which; tat—that; agre—at first; viṣham iva—like poison; pariṇāme—in the end; amṛita-upamam—like nectar; tat—that; sukham—happiness; sāttvikam—in the mode of goodness; proktam—is said to be; ātma-buddhi—situated in self-knowledge; prasāda-jam—generated by the pure intellect\n', 'https://gita.github.io/gita/data/verse_recitation/18/659.mp3', 18, 36);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (660, 'यत्तदग्रे विषमिव परिणामेऽमृतोपमम्।तत्सुखं सात्त्विकं प्रोक्तमात्मबुद्धिप्रसादजम्।।18.37।।', 'yat  tad agre    viṣam   iva pariṇāme    \'mṛtopamam  tat sukhaṁ  sāttvikaṁ   proktam ātma-buddhi-prasāda-jam', 'yat—that   which;  tat—that;   agre—in the beginning;  viṣam   iva—like    poison; pariṇāme—at the end;    amṛta—nectar;   upamam—compared to; tat—that;   sukham—happiness;   sāttvikam—in    the mode    of  goodness;   proktam—is   said;    ātma—self;    buddhi—intelligence;    prasāda-jam—satisfactory.', 'https://gita.github.io/gita/data/verse_recitation/18/660.mp3', 18, 37);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (661, 'विषयेन्द्रियसंयोगाद्यत्तदग्रेऽमृतोपमम्।परिणामे विषमिव तत्सुखं राजसं स्मृतम्।।18.38।।', 'viṣhayendriya-sanyogād yat tad agre ’mṛitopamam\npariṇāme viṣham iva tat sukhaṁ rājasaṁ smṛitam\n', 'viṣhaya—with the sense objects; indriya—the senses; sanyogāt—from the contact; yat—which; tat—that; agre—at first; amṛita-upamam—like nectar; pariṇāme—at the end; viṣham iva—like poison; tat—that; sukham—happiness; rājasam—in the mode of passion; smṛitam—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/18/661.mp3', 18, 38);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (662, 'यदग्रे चानुबन्धे च सुखं मोहनमात्मनः।निद्रालस्यप्रमादोत्थं तत्तामसमुदाहृतम्।।18.39।।', 'yad agre chānubandhe cha sukhaṁ mohanam ātmanaḥ\nnidrālasya-pramādotthaṁ tat tāmasam udāhṛitam\n', 'yat—which; agre—from beginning; cha—and; anubandhe—to end; cha—and; sukham—happiness; mohanam—illusory; ātmanaḥ—of the self; nidrā—sleep; ālasya—indolence; pramāda—negligence; uttham—derived from; tat—that; tāmasam—in the mode of ignorance; udāhṛitam—is said to be\n', 'https://gita.github.io/gita/data/verse_recitation/18/662.mp3', 18, 39);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (663, 'न तदस्ति पृथिव्यां वा दिवि देवेषु वा पुनः।सत्त्वं प्रकृतिजैर्मुक्तं यदेभिः स्यात्ित्रभिर्गुणैः।।18.40।।', 'na tad asti pṛithivyāṁ vā divi deveṣhu vā punaḥ\nsattvaṁ prakṛiti-jair muktaṁ yad ebhiḥ syāt tribhir guṇaiḥ\n', 'na—no; tat—that; asti—exists; pṛithivyām—on earth; vā—or; divi—the higher celestial abodes; deveṣhu—amongst the celestial gods; vā—or; punaḥ—again; sattvam—existence; prakṛiti-jaiḥ—born of material nature; muktam—liberated; yat—that; ebhiḥ—from the influence of these; syāt—is; tribhiḥ—three; guṇaiḥ—modes of material nature\n', 'https://gita.github.io/gita/data/verse_recitation/18/663.mp3', 18, 40);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (664, 'ब्राह्मणक्षत्रियविशां शूद्राणां च परंतप।कर्माणि प्रविभक्तानि स्वभावप्रभवैर्गुणैः।।18.41।।', 'brāhmaṇa-kṣhatriya-viśhāṁ śhūdrāṇāṁ cha parantapa\nkarmāṇi pravibhaktāni svabhāva-prabhavair guṇaiḥ\n', 'brāhmaṇa—of the priestly class; kṣhatriya—the warrior and administrative class; viśhām—the mercantile and farming class; śhūdrāṇām—of the worker class; cha—and; parantapa—Arjun, subduer of the enemies; karmāṇi—duties; pravibhaktāni—distributed; svabhāva-prabhavaiḥ-guṇaiḥ—work based on one’s nature and guṇas\n', 'https://gita.github.io/gita/data/verse_recitation/18/664.mp3', 18, 41);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (665, 'शमो दमस्तपः शौचं क्षान्तिरार्जवमेव च।ज्ञानं विज्ञानमास्तिक्यं ब्रह्मकर्म स्वभावजम्।।18.42।।', 'śhamo damas tapaḥ śhauchaṁ kṣhāntir ārjavam eva cha\njñānaṁ vijñānam āstikyaṁ brahma-karma svabhāva-jam\n', 'śhamaḥ—tranquility; damaḥ—restraint; tapaḥ—austerity; śhaucham—purity; kṣhāntiḥ—patience; ārjavam—integrity; eva—certainly; cha—and; jñānam—knowledge; vijñānam—wisdom; āstikyam—belief in a hereafter; brahma—of the priestly class; karma—work; svabhāva-jam—born of one’s intrinsic qualities\n', 'https://gita.github.io/gita/data/verse_recitation/18/665.mp3', 18, 42);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (666, 'शौर्यं तेजो धृतिर्दाक्ष्यं युद्धे चाप्यपलायनम्।दानमीश्वरभावश्च क्षात्रं कर्म स्वभावजम्।।18.43।।', 'śhauryaṁ tejo dhṛitir dākṣhyaṁ yuddhe chāpy apalāyanam\ndānam īśhvara-bhāvaśh cha kṣhātraṁ karma svabhāva-jam\n', 'śhauryam—valor; tejaḥ—strength; dhṛitiḥ—fortitude; dākṣhyam yuddhe—skill in weaponry; cha—and; api—also; apalāyanam—not fleeing; dānam—large-heartedness; īśhvara—leadership; bhāvaḥ—qualities; cha—and; kṣhātram—of the warrior and administrative class; karma—work; svabhāva-jam—born of one’s intrinsic qualities\n', 'https://gita.github.io/gita/data/verse_recitation/18/666.mp3', 18, 43);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (667, 'कृषिगौरक्ष्यवाणिज्यं वैश्यकर्म स्वभावजम्।परिचर्यात्मकं कर्म शूद्रस्यापि स्वभावजम्।।18.44।।', 'kṛiṣhi-gau-rakṣhya-vāṇijyaṁ vaiśhya-karma svabhāva-jam\nparicharyātmakaṁ karma śhūdrasyāpi svabhāva-jam\n', 'kṛiṣhi—agriculture; gau-rakṣhya—dairy farming; vāṇijyam—commerce; vaiśhya—of the mercantile and farming class; karma—work; svabhāva-jam—born of one’s intrinsic qualities; paricharyā—serving through work; ātmakam—natural; karma—duty; śhūdrasya—of the worker class; api—and; svabhāva-jam—born of one’s intrinsic qualities\n', 'https://gita.github.io/gita/data/verse_recitation/18/667.mp3', 18, 44);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (668, 'स्वे स्वे कर्मण्यभिरतः संसिद्धिं लभते नरः।स्वकर्मनिरतः सिद्धिं यथा विन्दति तच्छृणु।।18.45।।', 'sve sve karmaṇy abhirataḥ sansiddhiṁ labhate naraḥ\nsva-karma-nirataḥ siddhiṁ yathā vindati tach chhṛiṇu\n', 'sve sve—respectively; karmaṇi—work; abhirataḥ—fulfilling; sansiddhim—perfection; labhate—achieve; naraḥ—a person; sva-karma—to one’s own prescribed duty; nirataḥ—engaged; siddhim—perfection; yathā—as; vindati—attains; tat—that; śhṛiṇu—hear\n', 'https://gita.github.io/gita/data/verse_recitation/18/668.mp3', 18, 45);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (669, 'यतः प्रवृत्तिर्भूतानां येन सर्वमिदं ततम्।स्वकर्मणा तमभ्यर्च्य सिद्धिं विन्दति मानवः।।18.46।।', 'yataḥ pravṛittir bhūtānāṁ yena sarvam idaṁ tatam\nsva-karmaṇā tam abhyarchya siddhiṁ vindati mānavaḥ\n', 'yataḥ—from whom; pravṛittiḥ—have come into being; bhūtānām—of all living entities; yena—by whom; sarvam—all; idam—this; tatam—pervaded; sva-karmaṇā—by one’s natural occupation; tam—him; abhyarchya—by worshipping; siddhim—perfection; vindati—attains; mānavaḥ—a person\n', 'https://gita.github.io/gita/data/verse_recitation/18/669.mp3', 18, 46);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (670, 'श्रेयान्स्वधर्मो विगुणः परधर्मात्स्वनुष्ठितात्।स्वभावनियतं कर्म कुर्वन्नाप्नोति किल्बिषम्।।18.47।।', 'śhreyān swa-dharmo viguṇaḥ para-dharmāt sv-anuṣhṭhitāt\nsvabhāva-niyataṁ karma kurvan nāpnoti kilbiṣham\n', 'śhreyān—better; swa-dharmaḥ—one’s own prescribed occupational duty; viguṇaḥ—imperfectly done; para-dharmāt—than another’s dharma; su-anuṣhṭhitāt—perfectly done; svabhāva-niyatam—according to one’s innate nature; karma—duty; kurvan—by performing; na āpnoti—does not incur; kilbiṣham—sin\n', 'https://gita.github.io/gita/data/verse_recitation/18/670.mp3', 18, 47);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (671, 'सहजं कर्म कौन्तेय सदोषमपि न त्यजेत्।सर्वारम्भा हि दोषेण धूमेनाग्निरिवावृताः।।18.48।।', 'saha-jaṁ karma kaunteya sa-doṣham api na tyajet\nsarvārambhā hi doṣheṇa dhūmenāgnir ivāvṛitāḥ\n', 'saha-jam—born of one’s nature; karma—duty; kaunteya—Arjun, the son of Kunti; sa-doṣham—with defects; api—even if; na tyajet—one should not abandon; sarva-ārambhāḥ—all endeavors; hi—indeed; doṣheṇa—with evil; dhūmena—with smoke; agniḥ—fire; iva—as; āvṛitāḥ—veiled\n', 'https://gita.github.io/gita/data/verse_recitation/18/671.mp3', 18, 48);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (672, 'असक्तबुद्धिः सर्वत्र जितात्मा विगतस्पृहः।नैष्कर्म्यसिद्धिं परमां संन्यासेनाधिगच्छति।।18.49।।', 'asakta-buddhiḥ sarvatra jitātmā vigata-spṛihaḥ\nnaiṣhkarmya-siddhiṁ paramāṁ sannyāsenādhigachchhati\n', 'asakta-buddhiḥ—those whose intellect is unattached; sarvatra—everywhere; jita-ātmā—who have mastered their mind; vigata-spṛihaḥ—free from desires; naiṣhkarmya-siddhim—state of actionlessness; paramām—highest; sanyāsena—by the practice of renunciation; adhigachchhati—attain\n', 'https://gita.github.io/gita/data/verse_recitation/18/672.mp3', 18, 49);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (673, 'सिद्धिं प्राप्तो यथा ब्रह्म तथाप्नोति निबोध मे।समासेनैव कौन्तेय निष्ठा ज्ञानस्य या परा।।18.50।।', 'siddhiṁ prāpto yathā brahma tathāpnoti nibodha me\nsamāsenaiva kaunteya niṣhṭhā jñānasya yā parā\n', 'siddhim—perfection; prāptaḥ—attained; yathā—how; brahma—Brahman; tathā—also; āpnoti—attain; nibodha—hear; me—from me; samāsena—briefly; eva—indeed; kaunteya—Arjun, the son of Kunti; niṣhṭhā—firmly fixed; jñānasya—of knowledge; yā—which; parā—transcendental\n', 'https://gita.github.io/gita/data/verse_recitation/18/673.mp3', 18, 50);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (674, 'बुद्ध्या विशुद्धया युक्तो धृत्याऽऽत्मानं नियम्य च।शब्दादीन् विषयांस्त्यक्त्वा रागद्वेषौ व्युदस्य च।।18.51।।', 'buddhyā viśhuddhayā yukto dhṛityātmānaṁ niyamya cha\nśhabdādīn viṣhayāns tyaktvā rāga-dveṣhau vyudasya cha\n', 'buddhyā—intellect; viśhuddhayā—purified; yuktaḥ—endowed with; dhṛityā—by determination; ātmānam—the intellect; niyamya—restraining; cha—and; śhabda-ādīn viṣhayān—sound and other objects of the senses; tyaktvā—abandoning; rāga-dveṣhau—attachment and aversion; vyudasya—casting aside; cha—and;\n', 'https://gita.github.io/gita/data/verse_recitation/18/674.mp3', 18, 51);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (675, 'विविक्तसेवी लघ्वाशी यतवाक्कायमानसः।ध्यानयोगपरो नित्यं वैराग्यं समुपाश्रितः।।18.52।।', 'vivikta-sevī laghv-āśhī yata-vāk-kāya-mānasaḥ\ndhyāna-yoga-paro nityaṁ vairāgyaṁ samupāśhritaḥ\n', 'vivikta-sevī—relishing solitude; laghu-āśhī—eating light; yata—controls; vāk—speech; kāya—body; mānasaḥ—and mind; dhyāna-yoga-paraḥ—engaged in meditation; nityam—always; vairāgyam—dispassion; samupāśhritaḥ—having taken shelter of;\n', 'https://gita.github.io/gita/data/verse_recitation/18/675.mp3', 18, 52);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (676, 'अहङ्कारं बलं दर्पं कामं क्रोधं परिग्रहम्।विमुच्य निर्ममः शान्तो ब्रह्मभूयाय कल्पते।।18.53।।', 'ahankāraṁ balaṁ darpaṁ kāmaṁ krodhaṁ parigraham\nvimuchya nirmamaḥ śhānto brahma-bhūyāya kalpate\n', 'ahankāram—egotism; balam—violence; darpam—arrogance; kāmam—desire; krodham—anger; parigraham—selfishness; vimuchya—being freed from; nirmamaḥ—without possessiveness of property; śhāntaḥ—peaceful; brahma-bhūyāya—union with Brahman; kalpate—is fit\n', 'https://gita.github.io/gita/data/verse_recitation/18/676.mp3', 18, 53);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (677, 'ब्रह्मभूतः प्रसन्नात्मा न शोचति न काङ्क्षति।समः सर्वेषु भूतेषु मद्भक्तिं लभते पराम्।।18.54।।', 'brahma-bhūtaḥ prasannātmā na śhochati na kāṅkṣhati\nsamaḥ sarveṣhu bhūteṣhu mad-bhaktiṁ labhate parām\n', 'brahma-bhūtaḥ—one situated in Brahman; prasanna-ātmā—mentally serene; na—neither; śhochati—grieving; na—nor; kāṅkṣhati—desiring; samaḥ—equitably disposed; sarveṣhu—toward all; bhūteṣhu—living beings; mat-bhaktim—devotion to me; labhate—attains; parām—supreme\n', 'https://gita.github.io/gita/data/verse_recitation/18/677.mp3', 18, 54);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (678, 'भक्त्या मामभिजानाति यावान्यश्चास्मि तत्त्वतः।ततो मां तत्त्वतो ज्ञात्वा विशते तदनन्तरम्।।18.55।।', 'bhaktyā mām abhijānāti yāvān yaśh chāsmi tattvataḥ\ntato māṁ tattvato jñātvā viśhate tad-anantaram\n', 'bhaktyā—by loving devotion; mām—me; abhijānāti—one comes to know; yāvān—as much as; yaḥ cha asmi—as I am; tattvataḥ—in truth; tataḥ—then; mām—me; tattvataḥ—in truth; jñātvā—having known; viśhate—enters; tat-anantaram—thereafter\n', 'https://gita.github.io/gita/data/verse_recitation/18/678.mp3', 18, 55);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (679, 'सर्वकर्माण्यपि सदा कुर्वाणो मद्व्यपाश्रयः।मत्प्रसादादवाप्नोति शाश्वतं पदमव्ययम्।।18.56।।', 'sarva-karmāṇy api sadā kurvāṇo mad-vyapāśhrayaḥ\nmat-prasādād avāpnoti śhāśhvataṁ padam avyayam\n', 'sarva—all; karmāṇi—actions; api—though; sadā—always; kurvāṇaḥ—performing; mat-vyapāśhrayaḥ—take full refuge in me; mat-prasādāt—by my grace; avāpnoti—attain; śhāśhvatam—the eternal; padam—abode; avyayam—imperishable\n', 'https://gita.github.io/gita/data/verse_recitation/18/679.mp3', 18, 56);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (680, 'चेतसा सर्वकर्माणि मयि संन्यस्य मत्परः।बुद्धियोगमुपाश्रित्य मच्चित्तः सततं भव।।18.57।।', 'chetasā sarva-karmāṇi mayi sannyasya mat-paraḥ\nbuddhi-yogam upāśhritya mach-chittaḥ satataṁ bhava\n', 'chetasā—by consciousness; sarva-karmāṇi—every activity; mayi—to me; sannyasya—dedicating; mat-paraḥ—having me as the supreme goal; buddhi-yogam—having the intellect united with God; upāśhritya—taking shelter of; mat-chittaḥ—consciousness absorbed in me; satatam—always; bhava—be\n', 'https://gita.github.io/gita/data/verse_recitation/18/680.mp3', 18, 57);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (681, 'मच्चित्तः सर्वदुर्गाणि मत्प्रसादात्तरिष्यसि।अथ चेत्त्वमहङ्कारान्न श्रोष्यसि विनङ्क्ष्यसि।।18.58।।', 'mach-chittaḥ sarva-durgāṇi mat-prasādāt tariṣhyasi\natha chet tvam ahankārān na śhroṣhyasi vinaṅkṣhyasi\n', 'mat-chittaḥ—by always remembering me; sarva—all; durgāṇi—obstacles; mat-prasādāt—by my grace; tariṣhyasi—you shall overcome; atha—but; chet—if; tvam—you; ahankārāt—due to pride; na śhroṣhyasi—do not listen; vinaṅkṣhyasi—you will perish\n', 'https://gita.github.io/gita/data/verse_recitation/18/681.mp3', 18, 58);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (682, 'यदहङ्कारमाश्रित्य न योत्स्य इति मन्यसे।मिथ्यैष व्यवसायस्ते प्रकृतिस्त्वां नियोक्ष्यति।।18.59।।', 'yad ahankāram āśhritya na yotsya iti manyase\nmithyaiṣha vyavasāyas te prakṛitis tvāṁ niyokṣhyati\n', 'yat—if; ahankāram—motivated by pride; āśhritya—taking shelter; na yotsye—I shall not fight; iti—thus; manyase—you think; mithyā eṣhaḥ—this is all false; vyavasāyaḥ—determination; te—your; prakṛitiḥ—material nature; tvām—you; niyokṣhyati—will engage\n', 'https://gita.github.io/gita/data/verse_recitation/18/682.mp3', 18, 59);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (683, 'स्वभावजेन कौन्तेय निबद्धः स्वेन कर्मणा।कर्तुं नेच्छसि यन्मोहात्करिष्यस्यवशोऽपि तत्।।18.60।।', 'swbhāva-jena kaunteya nibaddhaḥ svena karmaṇā\nkartuṁ nechchhasi yan mohāt kariṣhyasy avaśho ’pi tat\n', 'swabhāva-jena—born of one’s own material nature; kaunteya—Arjun, the son of Kunti; nibaddhaḥ—bound; svena—by your own; karmaṇā—actions; kartum—to do; na—not; ichchhasi—you wish; yat—which; mohāt—out of delusion; kariṣhyasi—you will do; avaśhaḥ—helplessly; api—even though; tat—that\n', 'https://gita.github.io/gita/data/verse_recitation/18/683.mp3', 18, 60);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (684, 'ईश्वरः सर्वभूतानां हृद्देशेऽर्जुन तिष्ठति।भ्रामयन्सर्वभूतानि यन्त्रारूढानि मायया।।18.61।।', 'īśhvaraḥ sarva-bhūtānāṁ hṛid-deśhe ‘rjuna tiṣhṭhati\nbhrāmayan sarva-bhūtāni yantrārūḍhāni māyayā\n', 'īśhvaraḥ—the Supreme Lord; sarva-bhūtānām—in all living being; hṛit-deśhe—in the hearts; arjuna—Arjun; tiṣhṭhati—dwells; bhrāmayan—causing to wander; sarva-bhūtāni—all living beings; yantra ārūḍhani—seated on a machine; māyayā—made of the material energy\n', 'https://gita.github.io/gita/data/verse_recitation/18/684.mp3', 18, 61);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (685, 'तमेव शरणं गच्छ सर्वभावेन भारत।तत्प्रसादात्परां शान्तिं स्थानं प्राप्स्यसि शाश्वतम्।।18.62।।', 'tam eva śharaṇaṁ gachchha sarva-bhāvena bhārata\ntat-prasādāt parāṁ śhāntiṁ sthānaṁ prāpsyasi śhāśhvatam\n', 'tam—unto him; eva—only; śharaṇam gachchha—surrender; sarva-bhāvena—whole-heartedly; bhārata—Arjun, the son of Bharat; tat-prasādāt—by his grace; parām—supreme; śhāntim—peace; sthānam—the abode; prāpsyasi—you will attain; śhāśhvatam—eternal\n', 'https://gita.github.io/gita/data/verse_recitation/18/685.mp3', 18, 62);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (686, 'इति ते ज्ञानमाख्यातं गुह्याद्गुह्यतरं मया।विमृश्यैतदशेषेण यथेच्छसि तथा कुरु।।18.63।।', 'iti te jñānam ākhyātaṁ guhyād guhyataraṁ mayā\nvimṛiśhyaitad aśheṣheṇa yathechchhasi tathā kuru\n', 'iti—thus; te—to you; jñānam—knowledge; ākhyātam—explained; guhyāt—than secret knowledge; guhya-taram—still more secret knowledge; mayā—by me; vimṛiśhya—pondering; etat—on this; aśheṣheṇa—completely; yathā—as; ichchhasi—you wish; tathā—so; kuru—do\n', 'https://gita.github.io/gita/data/verse_recitation/18/686.mp3', 18, 63);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (687, 'सर्वगुह्यतमं भूयः श्रृणु मे परमं वचः।इष्टोऽसि मे दृढमिति ततो वक्ष्यामि ते हितम्।।18.64।।', 'sarva-guhyatamaṁ bhūyaḥ śhṛiṇu me paramaṁ vachaḥ\niṣhṭo ‘si me dṛiḍham iti tato vakṣhyāmi te hitam\n', 'sarva-guhya-tamam—the most confidential of all; bhūyaḥ—again; śhṛiṇu—hear; me—by me; paramam—supreme; vachaḥ—instruction; iṣhṭaḥ asi—you are dear; me—to me; dṛiḍham—very; iti—thus; tataḥ—because; vakṣhyāmi—I am speaking; te—for your; hitam—benefit\n', 'https://gita.github.io/gita/data/verse_recitation/18/687.mp3', 18, 64);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (688, 'मन्मना भव मद्भक्तो मद्याजी मां नमस्कुरु।मामेवैष्यसि सत्यं ते प्रतिजाने प्रियोऽसि मे।।18.65।।', 'man-manā bhava mad-bhakto mad-yājī māṁ namaskuru\nmām evaiṣhyasi satyaṁ te pratijāne priyo ‘si me\n', 'mat-manāḥ—thinking of me; bhava—be; mat-bhaktaḥ—my devotee; mat-yājī—worship me; mām—to me; namaskuru—offer obeisance; mām—to me; eva—certainly; eṣhyasi—you will come; satyam—truly; te—to you; pratijāne—I promise; priyaḥ—dear; asi—you are; me—to me\n', 'https://gita.github.io/gita/data/verse_recitation/18/688.mp3', 18, 65);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (689, 'सर्वधर्मान्परित्यज्य मामेकं शरणं व्रज।अहं त्वा सर्वपापेभ्यो मोक्षयिष्यामि मा शुचः।।18.66।।', 'sarva-dharmān parityajya mām ekaṁ śharaṇaṁ vraja\nahaṁ tvāṁ sarva-pāpebhyo mokṣhayiṣhyāmi mā śhuchaḥ\n', 'sarva-dharmān—all varieties of dharmas; parityajya—abandoning; mām—unto me; ekam—only; śharaṇam—take refuge; vraja—take; aham—I; tvām—you; sarva—all; pāpebhyaḥ—from sinful reactions; mokṣhayiṣhyāmi—shall liberate; mā—do not; śhuchaḥ—fear\n', 'https://gita.github.io/gita/data/verse_recitation/18/689.mp3', 18, 66);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (690, 'इदं ते नातपस्काय नाभक्ताय कदाचन।न चाशुश्रूषवे वाच्यं न च मां योऽभ्यसूयति।।18.67।।', 'idaṁ te nātapaskyāya nābhaktāya kadāchana\nna chāśhuśhruṣhave vāchyaṁ na cha māṁ yo ‘bhyasūtayi\n', 'idam—this; te—by you; na—never; atapaskāya—to those who are not austere; na—never; abhaktāya—to those who are not devoted; kadāchana—at any time; na—never; cha—also; aśhuśhrūṣhave—to those who are averse to listening (to spiritual topics); vāchyam—to be spoken; na—never; cha—also; mām—toward me; yaḥ—who; abhyasūyati—those who are envious\n', 'https://gita.github.io/gita/data/verse_recitation/18/690.mp3', 18, 67);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (691, 'य इमं परमं गुह्यं मद्भक्तेष्वभिधास्यति।भक्ितं मयि परां कृत्वा मामेवैष्यत्यसंशयः।।18.68।।', 'ya idaṁ paramaṁ guhyaṁ mad-bhakteṣhv abhidhāsyati\nbhaktiṁ mayi parāṁ kṛitvā mām evaiṣhyaty asanśhayaḥ\n', 'yaḥ—who; idam—this; paramam—most; guhyam—confidential knowledge; mat-bhakteṣhu—amongst my devotees; abhidhāsyati—teaches; bhaktim—greatest act of love; mayi—to me; parām—transcendental; kṛitvā—doing; mām—to me; eva—certainly; eṣhyati—comes; asanśhayaḥ—without doubt\n', 'https://gita.github.io/gita/data/verse_recitation/18/691.mp3', 18, 68);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (692, 'न च तस्मान्मनुष्येषु कश्िचन्मे प्रियकृत्तमः।भविता न च मे तस्मादन्यः प्रियतरो भुवि।।18.69।।', 'na cha tasmān manuṣhyeṣhu kaśhchin me priya-kṛittamaḥ\nbhavitā na cha me tasmād anyaḥ priyataro bhuvi\n', 'na—no; cha—and; tasmāt—than them; manuṣhyeṣhu—amongst human beings; kaśhchit—anyone; me—to me; priya-kṛit-tamaḥ—more dear; bhavitā—will be; na—never; cha—and; me—to me; tasmāt—than them; anyaḥ—another; priya-taraḥ—dearer; bhuvi—on this earth\n', 'https://gita.github.io/gita/data/verse_recitation/18/692.mp3', 18, 69);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (693, 'अध्येष्यते च य इमं धर्म्यं संवादमावयोः।ज्ञानयज्ञेन तेनाहमिष्टः स्यामिति मे मतिः।।18.70।।', 'adhyeṣhyate cha ya imaṁ dharmyaṁ saṁvādam āvayoḥ\njñāna-yajñena tenāham iṣhṭaḥ syām iti me matiḥ\n', 'adhyeṣhyate—study; cha—and; yaḥ—who; imam—this; dharmyam—sacred; saṁvādam—dialogue; āvayoḥ—of ours; jñāna—of knowledge; yajñena-tena—through the sacrifice of knowledge; aham—I; iṣhṭaḥ—worshipped; syām—shall be; iti—such; me—my; matiḥ—opinion\n', 'https://gita.github.io/gita/data/verse_recitation/18/693.mp3', 18, 70);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (694, 'श्रद्धावाननसूयश्च श्रृणुयादपि यो नरः।सोऽपि मुक्तः शुभाँल्लोकान्प्राप्नुयात्पुण्यकर्मणाम्।।18.71।।', 'śhraddhāvān anasūyaśh cha śhṛiṇuyād api yo naraḥ\nso ‘pi muktaḥ śhubhāl lokān prāpnuyāt puṇya-karmaṇām\n', 'śhraddhā-vān—faithful; anasūyaḥ—without envy; cha—and; śhṛiṇuyāt—listen; api—certainly; yaḥ—who; naraḥ—a person; saḥ—that person; api—also; muktaḥ—liberated; śhubhān—the auspicious; lokān—abodes; prāpnuyāt—attain; puṇya-karmaṇām—of the pious\n', 'https://gita.github.io/gita/data/verse_recitation/18/694.mp3', 18, 71);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (695, 'कच्चिदेतच्छ्रुतं पार्थ त्वयैकाग्रेण चेतसा।कच्चिदज्ञानसंमोहः प्रनष्टस्ते धनञ्जय।।18.72।।', 'kachchid etach chhrutaṁ pārtha tvayaikāgreṇa chetasā\nkachchid ajñāna-sammohaḥ pranaṣhṭas te dhanañjaya\n', 'kachchit—whether; etat—this; śhrutam—heard; pārtha—Arjun, the son of Pritha; tvayā—by you; eka-agreṇa chetasā—with a concentrated mind; kachchit—whether; ajñāna—ignorance; sammohaḥ—delusion; pranaṣhṭaḥ—destroyed; te—your; dhanañjaya—Arjun, conqueror of wealth\n', 'https://gita.github.io/gita/data/verse_recitation/18/695.mp3', 18, 72);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (696, 'अर्जुन उवाचनष्टो मोहः स्मृतिर्लब्धा त्वत्प्रसादान्मयाच्युत।स्थितोऽस्मि गतसन्देहः करिष्ये वचनं तव।।18.73।।', 'arjuna uvācha\nnaṣhṭo mohaḥ smṛitir labdhā tvat-prasādān mayāchyuta\nsthito ‘smi gata-sandehaḥ kariṣhye vachanaṁ tava\n', 'arjunaḥ uvācha—Arjun said; naṣhṭaḥ—dispelled; mohaḥ—illusion; smṛitiḥ—memory; labdhā—regained; tvat-prasādāt—by your grace; mayā—by me; achyuta—Shree Krishna, the infallible one; sthitaḥ—situated; asmi—I am; gata-sandehaḥ—free from doubts; kariṣhye—I shall act; vachanam—instructions; tava—your\n', 'https://gita.github.io/gita/data/verse_recitation/18/696.mp3', 18, 73);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (697, 'सञ्जय उवाचइत्यहं वासुदेवस्य पार्थस्य च महात्मनः।संवादमिममश्रौषमद्भुतं रोमहर्षणम्।।18.74।।', 'sañjaya uvācha\nity ahaṁ vāsudevasya pārthasya cha mahātmanaḥ\nsaṁvādam imam aśhrauṣham adbhutaṁ roma-harṣhaṇam\n', 'sañjayaḥ uvācha—Sanjay said; iti—thus; aham—I; vāsudevasya—of Shree Krishna; pārthasya—Arjun; cha—and; mahā-ātmanaḥ—the noble hearted soul; saṁvādam—conversation; imam—this; aśhrauṣham—have heard; adbhutam—wonderful; roma-harṣhaṇam—which causes the hair to stand on end\n', 'https://gita.github.io/gita/data/verse_recitation/18/697.mp3', 18, 74);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (698, 'व्यासप्रसादाच्छ्रुतवानेतद्गुह्यमहं परम्।योगं योगेश्वरात्कृष्णात्साक्षात्कथयतः स्वयम्।।18.75।।', 'vyāsa-prasādāch chhrutavān etad guhyam ahaṁ param\nyogaṁ yogeśhvarāt kṛiṣhṇāt sākṣhāt kathayataḥ svayam\n', 'vyāsa-prasādāt—by the grace of Ved Vyas; śhrutavān—have heard; etat—this; guhyam—secret; aham—I; param—supreme; yogam—Yog; yoga-īśhvarāt—from the Lod of Yog; kṛiṣhṇāt—from Shree Krishna; sākṣhāt—directly; kathayataḥ—speaking; svayam—himself\n', 'https://gita.github.io/gita/data/verse_recitation/18/698.mp3', 18, 75);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (699, 'राजन्संस्मृत्य संस्मृत्य संवादमिममद्भुतम्।केशवार्जुनयोः पुण्यं हृष्यामि च मुहुर्मुहुः।।18.76।।', 'rājan sansmṛitya sansmṛitya saṁvādam imam adbhutam\nkeśhavārjunayoḥ puṇyaṁ hṛiṣhyāmi cha muhur muhuḥ\n', 'rājan—King; sansmṛitya saṁsmṛitya—repeatedly recalling; saṁvādam—dialogue; imam—this; adbhutam—astonishing; keśhava-arjunayoḥ—between Lord Shree Krishna and Arjun; puṇyam—pious; hṛiṣhyāmi—I rejoice; cha—and; muhuḥ muhuḥ—repeatedly\n', 'https://gita.github.io/gita/data/verse_recitation/18/699.mp3', 18, 76);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (700, 'तच्च संस्मृत्य संस्मृत्य रूपमत्यद्भुतं हरेः।\n\nविस्मयो मे महान् राजन् हृष्यामि च पुनः पुनः।।18.77।।\n ', 'tach cha sansmṛitya saṁsmṛitya rūpam aty-adbhutaṁ hareḥ\nvismayo ye mahān rājan hṛiṣhyāmi cha punaḥ punaḥ\n', 'tat—that; cha—and; sansmṛitya saṁsmṛitya—remembering repeatedly; rūpam—cosmic form; ati—most; adbhutam—wonderful; hareḥ—of Lord Krishna; vismayaḥ—astonishment; me—my; mahān—great; rājan—King; hṛiṣhyāmi—I am thrilled with joy; cha—and; punaḥ punaḥ—over and over again\n', 'https://gita.github.io/gita/data/verse_recitation/18/700.mp3', 18, 77);
INSERT INTO verses (id, text_hindi, text_sanskrit, text_sanskrit_meanings, audio_url, chapter_id, verse_number) VALUES (701, 'यत्र योगेश्वरः कृष्णो यत्र पार्थो धनुर्धरः।\n\nतत्र श्रीर्विजयो भूतिर्ध्रुवा नीतिर्मतिर्मम।।18.78।\n ', 'yatra yogeśhvaraḥ kṛiṣhṇo yatra pārtho dhanur-dharaḥ\ntatra śhrīr vijayo bhūtir dhruvā nītir matir mama\n', 'yatra—wherever; yoga-īśhvaraḥ—Shree Krishna, the Lord of Yog; kṛiṣhṇaḥ—Shree Krishna; yatra—wherever; pārthaḥ—Arjun, the son of Pritha; dhanuḥ-dharaḥ—the supreme archer; tatra—there; śhrīḥ—opulence; vijayaḥ—victory; bhūtiḥ—prosperity; dhruvā—unending; nītiḥ—righteousness; matiḥ mama—my opinion\n', 'https://gita.github.io/gita/data/verse_recitation/18/701.mp3', 18, 78);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1, 'Dhṛtarāṣṭra berkata: “Wahai Sanjaya, apa yang dilakukan rakyatku dan para Pandawa, yang berkumpul di medan suci Kurukshetra, bersemangat untuk bertempur?”', 1);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2, 'Dhritarashtra bertanya: “Wahai Sanjaya, apa yang terjadi saat putra-putraku dan putra-putra Pandu berkumpul di tanah suci Kurukshetra, penuh gairah untuk bertempur?”', 1);
INSERT INTO verse_translations (id, content, verse_id) VALUES (6, 'Sanjaya berkata: Raja Duryodhana, setelah melihat pasukan Pandawa dalam barisan pertempuran, mendekati gurunya Drona dan mengucapkan kata-kata ini.', 2);
INSERT INTO verse_translations (id, content, verse_id) VALUES (7, 'Sanjaya mengatakan: Kemudian, melihat barisan perang pasukan Pandawa, Raja Duryodhana mendekati Guru Drona dan berbicara kepadanya.', 2);
INSERT INTO verse_translations (id, content, verse_id) VALUES (11, 'Lihatlah, wahai guru, pasukan Pandawa yang perkasa ini, yang disusun oleh putra Drupada, murid cerdasmu.', 3);
INSERT INTO verse_translations (id, content, verse_id) VALUES (12, 'Wahai guru, saksikanlah bala tentara besar putra-putra Pandu ini, yang diatur untuk berperang oleh murid Drupada yang bijaksana.', 3);
INSERT INTO verse_translations (id, content, verse_id) VALUES (16, 'Di sana (di pasukan itu) ada para pahlawan, pemanah ulung, seperti Bhima dan Arjuna; Yuyudhana, Virata, dan Drupada, seorang prajurit perkasa;', 4);
INSERT INTO verse_translations (id, content, verse_id) VALUES (17, 'Terdapat para kesatria pemegang busur besar di sini, yang dalam pertempuran setara dengan Bhima dan Arjuna: Yuyudhana (Satyaki), Virata, dan maharatha (pengendara kereta besar) Drupada;', 4);
INSERT INTO verse_translations (id, content, verse_id) VALUES (21, 'Dhrstaketu, Cekitana, raja Kasi yang gagah berani, Purujit, Kuntibhoja, dan Saibya, yang terbaik di antara manusia;', 5);
INSERT INTO verse_translations (id, content, verse_id) VALUES (22, 'Dhrstaketu, Cekitana, dan raja Kasi (Varanasi) yang gagah perkasa; Purujit, Kuntibhoja, dan Saibya, yang terkemuka di antara umat manusia;', 5);
INSERT INTO verse_translations (id, content, verse_id) VALUES (26, 'Yudhamanyu, yang gagah berani, dan Uttamaujas, yang kuat; dan juga putra Subhadra, serta putra-putra Draupadi, semuanya adalah prajurit perkasa.', 6);
INSERT INTO verse_translations (id, content, verse_id) VALUES (27, 'Dan Yudhamanyu yang gagah berani, dan Uttamaujas yang ksatria; putra Subhadra (Abhimanyu) dan putra-putra Draupadi—mereka semua, sesungguhnya, adalah maharatha.', 6);
INSERT INTO verse_translations (id, content, verse_id) VALUES (31, 'Ketahuilah, wahai yang terbaik dari para Brahmana, mereka yang penting di pihak kita—mereka yang adalah komandan pasukanku. Aku akan menyebutkan nama mereka untuk menyegarkan ingatanmu.', 7);
INSERT INTO verse_translations (id, content, verse_id) VALUES (32, 'Tetapi, wahai yang terbaik di antara para Brahmana, harap perhatikanlah mereka yang terdepan di antara kita, para pendiri pasukanku. Aku akan menyebutkan mereka sebagai contoh.', 7);
INSERT INTO verse_translations (id, content, verse_id) VALUES (36, 'Dirimu, Bhisma, Karna, Krpa yang jaya, Asvatthama, Vikarna, dan Jayadratha, putra Somadatta;', 8);
INSERT INTO verse_translations (id, content, verse_id) VALUES (37, '(Mereka adalah): Diri Anda yang terhormat, Bhisma, Karna, dan Krpa, yang selalu jaya dalam pertempuran; Asvatthama, Vikarna, Saumadatti, dan Jayadratha.', 8);
INSERT INTO verse_translations (id, content, verse_id) VALUES (41, 'Dan ada banyak pahlawan lain yang bertekad menyerahkan nyawa demi aku. Mereka semua ahli dalam menggunakan berbagai macam senjata dan cekatan dalam pertempuran.', 9);
INSERT INTO verse_translations (id, content, verse_id) VALUES (42, 'Ada banyak pahlawan yang telah mendedikasikan hidup mereka untukku, memiliki berbagai jenis senjata dan misil, semuanya terampil dalam pertempuran.', 9);
INSERT INTO verse_translations (id, content, verse_id) VALUES (46, 'Kekuatan kita tidak memadai, dijaga oleh Bhisma, sementara kekuatan mereka memadai, dijaga oleh Bhima.', 10);
INSERT INTO verse_translations (id, content, verse_id) VALUES (47, 'Oleh karena itu, pasukan kita, di bawah perlindungan penuh Bhisma dan lainnya, tidak terbatas. Tetapi pasukan musuh ini, di bawah perlindungan Bhima dan lainnya, terbatas.', 10);
INSERT INTO verse_translations (id, content, verse_id) VALUES (51, 'Oleh karena itu, kalian semua, dengan menempati posisi masing-masing dengan kokoh di divisi masing-masing, harus menjaga Bhisma dengan segala cara.', 11);
INSERT INTO verse_translations (id, content, verse_id) VALUES (52, 'Namun, para tuan yang terhormat, kalian semua tanpa kecuali, sambil menempati semua posisi di berbagai arah yang telah dialokasikan kepada kalian masing-masing, harap lindungi Bhisma secara khusus.', 11);
INSERT INTO verse_translations (id, content, verse_id) VALUES (56, 'Kemudian kakek perkasa Bhisma, yang tertua dari klan Kuru, mengaum seperti singa dan meniup sangkakalanya dengan maksud menyemangati Duryodhana.', 12);
INSERT INTO verse_translations (id, content, verse_id) VALUES (57, 'Kakek yang gagah perkasa, yang tertua dari Kurawa, dengan suara lantang seperti auman singa, meniup terompet untuk membangkitkan semangatnya (Duryodhana).', 12);
INSERT INTO verse_translations (id, content, verse_id) VALUES (61, 'Kemudian tiba-tiba sangkakala, gendang perang, terompet, tambur, dan tanduk kerbau berseru, dan suaranya mengerikan.', 13);
INSERT INTO verse_translations (id, content, verse_id) VALUES (62, 'Segera setelah itu, terompet, gendang, tambur, terompet, dan terompet kerbau berbunyi nyaring, menciptakan suara yang gemuruh.', 13);
INSERT INTO verse_translations (id, content, verse_id) VALUES (66, 'Kemudian Sri Krishna dan Arjuna, yang berada di kereta besar mereka yang ditarik kuda putih, meniup sangkakala ilahi mereka.', 14);
INSERT INTO verse_translations (id, content, verse_id) VALUES (67, 'Kemudian Madhava (Krishna) dan putra Pandu (Arjuna), yang berada di kereta megah mereka yang ditarik kuda putih, meniup terompet ilahi mereka dengan nyaring.', 14);
INSERT INTO verse_translations (id, content, verse_id) VALUES (71, 'Sri Krishna meniup sangkakalanya, Pancajanya, Arjuna Devadatta-nya, dan Bhima dari perbuatan mengerikan meniup sangkakala besarnya, Paundra.', 15);
INSERT INTO verse_translations (id, content, verse_id) VALUES (72, 'Hrsikesa (Krsna) meniup sangkakala Pancajanya; Dhananjaya (Arjuna) meniup sangkakala Devadatta; dan Vrkodara (Bhima) dari perbuatan mengerikan meniup sangkakala besar Paundra.', 15);
INSERT INTO verse_translations (id, content, verse_id) VALUES (76, 'Raja Yudhisthira, putra Kunti, meniup sangkakalanya, Anantavijaya, dan Nakula serta Sahadeva meniup sangkakala mereka, Sughosa dan Manipuspaka.', 16);
INSERT INTO verse_translations (id, content, verse_id) VALUES (77, 'Raja Yudhisthira, putra Kunti, meniup Anantavijaya; Nakula dan Sahadeva masing-masing meniup Sughosa dan Manipuspaka.', 16);
INSERT INTO verse_translations (id, content, verse_id) VALUES (81, 'Dan Raja Kasi, pemanah ulung, Sikandi sang prajurit perkasa, Dhrstadyumna, dan Virata; dan Satyaki, yang tak terkalahkan;', 17);
INSERT INTO verse_translations (id, content, verse_id) VALUES (82, 'Dan Raja Kashi, yang memegang busur besar, dan pengendara kereta besar Sikhandin, Dhrstadyumna, Virata, dan Satyaki yang tak terkalahkan;', 17);
INSERT INTO verse_translations (id, content, verse_id) VALUES (86, 'Drupada dan putra-putra Draupadi, serta putra Subhadra yang bersenjata kuat—semuanya, wahai Raja, meniup sangkakala mereka berulang kali.', 18);
INSERT INTO verse_translations (id, content, verse_id) VALUES (87, 'Drupada dan putra-putra Draupadi, serta putra Subhadra yang perkasa, mereka semua bersama-sama, wahai raja, meniup terompet masing-masing.', 18);
INSERT INTO verse_translations (id, content, verse_id) VALUES (91, 'Dan suara gemuruh itu, yang bergema di langit dan bumi, merobek hati putra-putra Dhrtarastra.', 19);
INSERT INTO verse_translations (id, content, verse_id) VALUES (92, 'Suara gemuruh itu menembus hati putra-putra Dhrtarastra saat bergema di langit dan bumi.', 19);
INSERT INTO verse_translations (id, content, verse_id) VALUES (96, 'Kemudian Arjuna, yang panji-panjinya berhiaskan Hanuman, setelah melihat putra-putra Dhrtarastra berbaris, mengambil busurnya, sementara panah-panah mulai berterbangan.', 20);
INSERT INTO verse_translations (id, content, verse_id) VALUES (97, 'Wahai Raja, setelah itu, melihat pasukan Dhrtarastra berdiri di posisi mereka, dengan semua senjata siap beraksi, putra Pandu, Arjuna, yang memiliki lambang Hanuman di benderanya, mengangkat busurnya dan berkata demikian kepada Hrsikesa.', 20);
INSERT INTO verse_translations (id, content, verse_id) VALUES (101, 'Dan dia berkata, wahai Penguasa Bumi, kata-kata ini kepada Sri Krishna: Arjuna berkata, “Tariklah keretaku, wahai Krishna, di antara kedua pasukan.', 21);
INSERT INTO verse_translations (id, content, verse_id) VALUES (102, 'Arjuna berkata, “Wahai Acyuta, tolong tempatkan keretaku di antara kedua pasukan.”', 21);
INSERT INTO verse_translations (id, content, verse_id) VALUES (106, 'Agar aku dapat melihat dengan baik mereka yang berdiri, bersemangat untuk bertempur, dan tahu dengan siapa aku harus bertempur dalam usaha perang ini.', 22);
INSERT INTO verse_translations (id, content, verse_id) VALUES (107, 'Sampai aku mengamati mereka yang berdiri berniat untuk bertempur dan mereka yang akan terlibat dalam pertempuran denganku dalam perang yang akan datang ini.', 22);
INSERT INTO verse_translations (id, content, verse_id) VALUES (111, 'Aku ingin melihat mereka yang berkumpul di sini siap bertempur dalam pertempuran ini untuk menyenangkan putra Dhritarashtra yang berhati jahat.', 23);
INSERT INTO verse_translations (id, content, verse_id) VALUES (112, 'Mereka yang telah berkumpul di sini dan ingin mencapai apa yang diinginkan putra Dhrtarastra yang sesat dalam perang, aku melihat mereka berniat untuk bertempur.', 23);
INSERT INTO verse_translations (id, content, verse_id) VALUES (116, 'Sanjaya berkata: Demikianlah, Arjuna, Sri Krishna menarik kereta terbaik di antara kedua pasukan, di hadapan Bhisma, Drona, dan semua raja lainnya, wahai Dhrtarastra. Kemudian dia berkata, “Wahai Arjuna, lihatlah para Kaurava yang berkumpul ini.”', 24);
INSERT INTO verse_translations (id, content, verse_id) VALUES (117, 'Sanjaya berkata, “Wahai keturunan Bharata, Hrishikesa, yang diberitahu demikian oleh Gudakesa (Arjuna), menempatkan kereta yang sangat baik di antara kedua pasukan, di depan Bhishma dan Drona serta semua penguasa bumi, dan berkata, ‘Wahai Partha, lihatlah para Kurawa yang berkumpul ini!’”', 24);
INSERT INTO verse_translations (id, content, verse_id) VALUES (121, 'Sanjaya berkata: Demikianlah, Arjuna, Sri Krishna menarik kereta terbaik di antara kedua pasukan, di hadapan Bhisma, Drona, dan semua raja lainnya, wahai Dhrtarastra. Kemudian dia berkata, “Wahai Arjuna, lihatlah para Kaurava yang berkumpul ini.”', 25);
INSERT INTO verse_translations (id, content, verse_id) VALUES (122, 'Sanjaya berkata, “Wahai keturunan Bharata (Dhrtarashtra), Hrsikesa, yang diberitahu demikian oleh Gudakesa (Arjuna), menempatkan kereta yang sangat baik di antara kedua pasukan, di depan Bhisma dan Drona serta semua penguasa bumi, dan berkata, ‘Wahai Partha (Arjuna), lihatlah orang-orang dari dinasti Kuru yang berkumpul ini.’ ”', 25);
INSERT INTO verse_translations (id, content, verse_id) VALUES (126, 'Kemudian, saat Arjuna melihat, dia melihat berdiri di sana ayah-ayah, kakek-kakek, guru-guru, paman-paman, saudara-saudara, putra-putra, cucu-cucu, dan rekan-rekan.', 26);
INSERT INTO verse_translations (id, content, verse_id) VALUES (127, 'Kemudian Arjuna melihat, berbaris di antara kedua pasukan, paman-pamannya, kakek-kakeknya, guru-gurunya, paman-paman dari ibu, saudara-saudaranya, sepupu-sepupunya, putra-putranya, cucu-cucunya, rekan-rekannya, mertua-mertuanya, dan teman-temannya.', 26);
INSERT INTO verse_translations (id, content, verse_id) VALUES (131, 'Ayah mertua dan teman-teman dekat di kedua pasukan, ketika Arjuna melihat semua kerabat ini berbaris,', 27);
INSERT INTO verse_translations (id, content, verse_id) VALUES (132, 'Putra Kunti, Arjuna, melihat semua kerabat itu berbaris di sana, diliputi oleh belas kasih yang besar dan dengan sedih mengatakan ini:', 27);
INSERT INTO verse_translations (id, content, verse_id) VALUES (136, 'Dia dipenuhi belas kasihan yang mendalam dan mengucapkan kata-kata ini dalam keputusasaan:', 28);
INSERT INTO verse_translations (id, content, verse_id) VALUES (137, 'Arjuna berkata, “Wahai Krsna, melihat sanak saudara dan teman-teman yang telah berkumpul di sini dengan niat untuk bertempur ini, anggota tubuhku menjadi lemas dan mulutku menjadi sangat kering.”', 28);
INSERT INTO verse_translations (id, content, verse_id) VALUES (141, 'Anggota tubuhku melemah, mulutku menjadi kering, tubuhku gemetar, dan rambutku berdiri tegak.', 29);
INSERT INTO verse_translations (id, content, verse_id) VALUES (142, 'Dan ada gemetar di tubuhku, dan rambutku berdiri tegak; Gandiwa (busur) terlepas dari tanganku dan kulitku terasa sangat panas.', 29);
INSERT INTO verse_translations (id, content, verse_id) VALUES (146, 'Busur Gandiwa terlepas dari tanganku dan kulitku terbakar. Aku tidak bisa lagi berdiri. Pikiranku seolah-olah berputar.', 30);
INSERT INTO verse_translations (id, content, verse_id) VALUES (147, 'Terlebih lagi, wahai Kesava (Krishna), aku tidak bisa berdiri teguh, dan pikiranku sepertinya berputar. Dan aku melihat pertanda yang tidak menguntungkan.', 30);
INSERT INTO verse_translations (id, content, verse_id) VALUES (151, 'Aku melihat, Kṛṣṇa, pertanda buruk. Aku tidak melihat kebaikan dalam membunuh kerabatku dalam pertempuran.', 31);
INSERT INTO verse_translations (id, content, verse_id) VALUES (152, 'Selain itu, aku tidak melihat kebaikan apa pun yang bisa didapat dari membunuh bangsaku sendiri dalam pertempuran. Wahai Krsna, aku tidak mendambakan kemenangan, bahkan kerajaan, atau kesenangan.', 31);
INSERT INTO verse_translations (id, content, verse_id) VALUES (156, 'Aku tidak menginginkan kemenangan, atau kekaisaran, atau kesenangan. Apa yang harus kami lakukan dengan kekaisaran, wahai Krishna, atau kenikmatan, atau bahkan kehidupan?', 32);
INSERT INTO verse_translations (id, content, verse_id) VALUES (157, 'Wahai Govinda! Apa gunanya kerajaan bagi kami? Apa gunanya kesenangan dan bahkan kehidupan? Mereka yang untuk siapa kerajaan, kesenangan, dan kenikmatan kami inginkan—seperti guru, paman, mertua, cucu, ipar, serta kerabat—mereka semua berdiri berbaris untuk bertempur, mempertaruhkan nyawa dan harta benda mereka.', 32);
INSERT INTO verse_translations (id, content, verse_id) VALUES (161, 'Mereka yang demi siapa kita menginginkan kekaisaran, kenikmatan, dan kesenangan, berdiri di sini dalam perang, melepaskan kehidupan dan kekayaan.', 33);
INSERT INTO verse_translations (id, content, verse_id) VALUES (162, '- Wahai Govinda! Apa gunanya kerajaan bagi kami, atau apa gunanya kenikmatan dan penghidupan? Mereka yang demi siapa kerajaan, kenikmatan, dan kesenangan kami inginkan—seperti guru, paman, mertua, cucu, ipar, serta kerabat—mereka semua berdiri berbaris untuk bertempur, mempertaruhkan nyawa dan harta benda mereka.', 33);
INSERT INTO verse_translations (id, content, verse_id) VALUES (166, 'Semua ini harus dianggap sebagai diri sendiri. Guru, ayah, putra, kakek, paman, mertua, cucu, ipar, dan kerabat lainnya—semua ini harus dianggap sebagai diri sendiri.', 34);
INSERT INTO verse_translations (id, content, verse_id) VALUES (167, '- Wahai Govinda! Apa gunanya kerajaan bagi kami, atau apa gunanya kenikmatan dan penghidupan? Mereka yang demi siapa kerajaan, kenikmatan, dan kesenangan kami inginkan—seperti guru, paman, mertua, cucu, ipar, serta kerabat—mereka semua berdiri berbaris untuk bertempur, mempertaruhkan nyawa dan harta benda mereka.', 34);
INSERT INTO verse_translations (id, content, verse_id) VALUES (171, 'Aku tidak akan membunuh mereka, bahkan jika mereka membunuhku, demi kedaulatan tiga dunia—apalagi demi bumi ini, wahai Krsna?', 35);
INSERT INTO verse_translations (id, content, verse_id) VALUES (172, 'Wahai Madhusudana, bahkan jika aku terbunuh, aku tidak ingin membunuh mereka ini bahkan demi kerajaan yang meluas di tiga dunia; apalagi melakukannya demi bumi!', 35);
INSERT INTO verse_translations (id, content, verse_id) VALUES (176, 'Jika kita membunuh putra-putra Dhrtarastra, kebahagiaan apa yang akan kita dapatkan, wahai Krsna? Dosa saja yang akan menimpa kita jika kita membunuh para penjahat pembunuh ini.', 36);
INSERT INTO verse_translations (id, content, verse_id) VALUES (177, 'Wahai Janardana, kebahagiaan apa yang akan kita dapatkan dengan membunuh putra-putra Dhrtarastra? Kita hanya akan menanggung dosa dengan membunuh para pelaku kejahatan ini.', 36);
INSERT INTO verse_translations (id, content, verse_id) VALUES (181, 'Oleh karena itu, tidak pantas bagi kita untuk membunuh kerabat kita, putra-putra Dhrtarastra. Karena jika kita membunuh kerabat kita, wahai Krsna, bagaimana kita bisa bahagia?', 37);
INSERT INTO verse_translations (id, content, verse_id) VALUES (182, 'Oleh karena itu, tidak pantas bagi kita untuk membunuh putra-putra Dhrtarastra, yang adalah kerabat kita sendiri. Karena, wahai Madhava, bagaimana kita bisa bahagia dengan membunuh sanak saudara kita?', 37);
INSERT INTO verse_translations (id, content, verse_id) VALUES (186, 'Meskipun orang-orang ini, yang pikirannya dikuasai oleh keserakahan, tidak melihat kejahatan dalam kehancuran klan dan tidak ada dosa dalam pengkhianatan terhadap teman,', 38);
INSERT INTO verse_translations (id, content, verse_id) VALUES (187, '- Wahai Janardana, meskipun orang-orang ini, yang hatinya telah disesatkan oleh keserakahan, tidak melihat kejahatan yang timbul dari penghancuran keluarga dan dosa dalam permusuhan terhadap teman, namun bagaimana mungkin kita, yang jelas-jelas melihat kejahatan yang timbul dari penghancuran keluarga, tetap tidak menyadari perlunya menahan diri dari dosa ini?', 38);
INSERT INTO verse_translations (id, content, verse_id) VALUES (191, 'Mengapa kita tidak belajar untuk menghindari kejahatan ini—kita yang melihat kejahatan merusak klan, wahai Krishna?', 39);
INSERT INTO verse_translations (id, content, verse_id) VALUES (192, '- Wahai Janardana, meskipun orang-orang ini, yang hatinya telah disesatkan oleh keserakahan, tidak melihat kejahatan yang timbul dari penghancuran keluarga dan dosa dalam permusuhan terhadap teman, namun bagaimana mungkin kita, yang jelas-jelas melihat kejahatan yang timbul dari penghancuran keluarga, tetap tidak menyadari perlunya menahan diri dari dosa ini?', 39);
INSERT INTO verse_translations (id, content, verse_id) VALUES (196, 'Dengan runtuhnya suatu klan, tradisi kunonya musnah, dan ketika tradisi musnah, anarki melanda seluruh klan.', 40);
INSERT INTO verse_translations (id, content, verse_id) VALUES (197, 'Dari kehancuran keluarga, ritus dan kewajiban tradisional benar-benar musnah. Ketika ritus dan kewajiban musnah, kejahatan melanda seluruh keluarga.', 40);
INSERT INTO verse_translations (id, content, verse_id) VALUES (201, 'Ketika anarki merajalela, wahai Krsna, wanita-wanita klan menjadi rusak; ketika wanita-wanita menjadi rusak, terjadilah percampuran kasta.', 41);
INSERT INTO verse_translations (id, content, verse_id) VALUES (202, 'Wahai Krishna, ketika kejahatan merajalela, wanita-wanita dalam keluarga menjadi korup. Wahai keturunan Vrsni, ketika wanita-wanita menjadi korup, hal itu mengakibatkan percampuran kasta.', 41);
INSERT INTO verse_translations (id, content, verse_id) VALUES (206, 'Percampuran kasta ini menyebabkan neraka bagi klan itu sendiri dan para perusaknya; karena roh-roh leluhur mereka jatuh terdegradasi, kehilangan persembahan makanan dan air ritual.', 42);
INSERT INTO verse_translations (id, content, verse_id) VALUES (207, 'Dan percampuran dalam keluarga menyebabkan para perusak keluarga benar-benar masuk neraka. Nenek moyang mereka jatuh (ke neraka) karena kehilangan persembahan bola-bola nasi dan air.', 42);
INSERT INTO verse_translations (id, content, verse_id) VALUES (211, 'Karena dosa-dosa para perusak klan yang menyebabkan percampuran kasta, tradisi kuno klan dan kasta hancur.', 43);
INSERT INTO verse_translations (id, content, verse_id) VALUES (212, 'Karena perbuatan buruk para perusak keluarga, yang menyebabkan percampuran kasta, ritus dan kewajiban tradisional kasta dan keluarga hancur.', 43);
INSERT INTO verse_translations (id, content, verse_id) VALUES (216, 'Bagi mereka yang hukum klannya hancur, tinggal di neraka telah ditentukan, wahai Krishna; demikianlah yang kami dengar.', 44);
INSERT INTO verse_translations (id, content, verse_id) VALUES (217, 'Wahai Janardana, kami telah mendengar bahwa tinggal di neraka tidak dapat dihindari bagi mereka yang kewajiban keluarganya hancur.', 44);
INSERT INTO verse_translations (id, content, verse_id) VALUES (221, 'Sayang sekali! Kita telah bertekad untuk melakukan dosa besar, karena kita siap membunuh kerabat kita demi kedaulatan dan kenikmatan.', 45);
INSERT INTO verse_translations (id, content, verse_id) VALUES (222, 'Sungguh kasihan bahwa kita telah bertekad untuk melakukan dosa besar, bersemangat untuk membunuh sanak saudara kita sendiri demi kesenangan kerajaan!', 45);
INSERT INTO verse_translations (id, content, verse_id) VALUES (226, 'Jika putra-putra Dhrtarastra yang bersenjata lengkap membunuhku dalam pertempuran, tanpa perlawanan dan tanpa senjata, itu akan lebih baik bagiku.', 46);
INSERT INTO verse_translations (id, content, verse_id) VALUES (227, 'Jika, dalam pertempuran ini, putra-putra Dhrtarastra, bersenjata, membunuhku, yang tidak melawan dan tidak bersenjata, itu akan lebih menguntungkan bagiku.', 46);
INSERT INTO verse_translations (id, content, verse_id) VALUES (231, 'Sanjaya berkata: Setelah berbicara demikian di medan perang, Arjuna melempar busur dan panahnya dan duduk di kursi kereta, hatinya diliputi kesedihan.', 47);
INSERT INTO verse_translations (id, content, verse_id) VALUES (232, 'Sanjaya menceritakan: Setelah berkata demikian, Arjuna, dengan pikiran yang diliputi kesedihan, duduk di kereta di tengah pertempuran, membuang busurnya beserta panah-panahnya.', 47);
INSERT INTO verse_translations (id, content, verse_id) VALUES (236, 'Sanjaya berkata: Kepadanya, yang diliputi rasa kasihan, yang matanya basah oleh air mata, yang diliputi kesedihan dan menunjukkan pandangan bingung, Sri Krishna berkata sebagai berikut:', 48);
INSERT INTO verse_translations (id, content, verse_id) VALUES (237, 'Sanjaya berkata: Kepadanya yang telah dipenuhi rasa kasihan, yang matanya dipenuhi air mata dan menunjukkan kesusahan, dan yang berduka, Madhusudana mengucapkan kata-kata ini.', 48);
INSERT INTO verse_translations (id, content, verse_id) VALUES (241, 'Sang Raja berkata, “Dari mana datangnya keputusasaan ini padamu, wahai Arjuna, dalam krisis ini? Ini tidak pantas bagi orang mulia; ini memalukan dan menghalangi seseorang mencapai surga.”', 49);
INSERT INTO verse_translations (id, content, verse_id) VALUES (242, 'Yang Mahamulia berkata, “Wahai Arjuna, di tempat berbahaya ini, dari mana datangnya kekotoran ini padamu, yang dipelihara oleh orang-orang yang tidak tercerahkan, yang tidak mengarah ke surga dan membawa kehinaan?”', 49);
INSERT INTO verse_translations (id, content, verse_id) VALUES (246, 'Jangan menyerah pada ketidakjantanan, wahai Arjuna, itu tidak pantas bagimu. Singkirkan kelemahan hati yang rendah ini dan bangkitlah, wahai penakluk musuh!', 50);
INSERT INTO verse_translations (id, content, verse_id) VALUES (247, 'Wahai Partha, jangan menyerah pada ketidakjantanan. Ini tidak pantas bagimu. Wahai penghancur musuh, bangkitlah, tinggalkan kelemahan hati yang kecil.', 50);
INSERT INTO verse_translations (id, content, verse_id) VALUES (251, 'Arjuna berkata, “Wahai pembunuh musuh, bagaimana aku bisa mengarahkan panah dalam pertempuran melawan Bhisma dan Drona, yang pantas dihormati?”', 51);
INSERT INTO verse_translations (id, content, verse_id) VALUES (252, 'Arjuna berkata, “Wahai Madhusudana, wahai penghancur musuh, bagaimana aku bisa bertempur dengan panah dalam pertempuran melawan Bhisma dan Drona, yang pantas dipuja?”', 51);
INSERT INTO verse_translations (id, content, verse_id) VALUES (256, 'Lebih baik hidup dari sedekah di dunia ini daripada membunuh guru-guru yang paling terhormat ini. Jika aku membunuh guru-guruku, meskipun mereka mungkin terdegradasi oleh nafsu kekayaan, aku hanya akan menikmati kesenangan yang berlumuran darah di sini.', 52);
INSERT INTO verse_translations (id, content, verse_id) VALUES (257, 'Daripada membunuh para tetua yang berhati mulia, lebih baik di dunia ini hidup bahkan dengan sedekah. Tetapi dengan membunuh mereka, kita hanya akan menikmati kesenangan kekayaan dan hal-hal yang diinginkan yang berlumuran darah di sini.', 52);
INSERT INTO verse_translations (id, content, verse_id) VALUES (261, 'Kita tidak tahu mana yang lebih baik bagi kita: apakah kita mengalahkan mereka atau mereka mengalahkan kita. Putra-putra Dhrtarastra itu sendiri, yang jika kita bunuh, kita tidak ingin hidup, bahkan mereka berdiri berbaris melawan kita.', 53);
INSERT INTO verse_translations (id, content, verse_id) VALUES (262, 'Kami tidak tahu mana yang lebih baik bagi kami, apakah kami akan menang atau mereka akan menaklukkan kami. Putra-putra Dhrtarastra itu sendiri, yang tidak ingin kami bunuh, berdiri berhadapan.', 53);
INSERT INTO verse_translations (id, content, verse_id) VALUES (266, 'Dengan hatiku yang diliputi oleh kesalahan belas kasihan yang lemah, pikiranku bingung tentang tugasku, aku memohon padamu untuk memberitahuku dengan pasti apa yang baik bagiku. Aku adalah muridmu; ajarilah aku, yang telah berlindung padamu.', 54);
INSERT INTO verse_translations (id, content, verse_id) VALUES (267, 'Dengan sifatku yang diliputi belas kasihan yang lemah, pikiranku bingung tentang kewajiban, aku memohon kepada-Mu. Beritahukanlah kepadaku dengan pasti apa yang lebih baik; aku adalah murid-Mu, telah berlindung kepada-Mu. Ajarilah aku.', 54);
INSERT INTO verse_translations (id, content, verse_id) VALUES (271, 'Bahkan jika aku memenangkan kedaulatan tak tertandingi di bumi yang makmur atau bahkan kekuasaan atas para Dewa, aku tidak merasa bahwa itu akan menghilangkan kesedihan yang meluluhlantakkan indraku.', 55);
INSERT INTO verse_translations (id, content, verse_id) VALUES (272, 'Karena aku tidak melihat apa pun yang, bahkan setelah memperoleh kerajaan makmur yang bebas musuh di bumi ini dan bahkan kedaulatan atas para dewa, dapat menghilangkan kesedihanku yang menghancurkan indra.', 55);
INSERT INTO verse_translations (id, content, verse_id) VALUES (276, 'Sanjaya berkata, Setelah berbicara demikian kepada Sri Krsna, Arjuna, sang penakluk tidur dan penghancur musuh, berkata, “Aku tidak akan bertempur,” dan terdiam.', 56);
INSERT INTO verse_translations (id, content, verse_id) VALUES (277, 'Sanjaya berkata: Setelah berbicara demikian kepada Hrsikesa (Krsna), Gudakesa (Arjuna), sang penghancur musuh, benar-benar terdiam, berkata kepada Govinda, \'Aku tidak akan bertempur.\'', 56);
INSERT INTO verse_translations (id, content, verse_id) VALUES (281, 'Wahai Raja, kepadanya yang berduka di antara kedua pasukan, Sri Krishna mengucapkan kata-kata berikut ini, seolah-olah tersenyum (sebagai ejekan).', 57);
INSERT INTO verse_translations (id, content, verse_id) VALUES (282, 'Wahai keturunan Bharata, Hrsikesa, seolah-olah tersenyum, mengucapkan kata-kata ini kepadanya yang berduka di antara kedua pasukan:', 57);
INSERT INTO verse_translations (id, content, verse_id) VALUES (286, 'Sang Raja berkata, “Engkau berduka atas mereka yang seharusnya tidak didukakan; namun engkau berbicara kata-kata bijak. Orang bijak tidak berduka baik untuk yang mati maupun untuk yang hidup.”', 58);
INSERT INTO verse_translations (id, content, verse_id) VALUES (287, 'Sang Mahamulia berkata, “Engkau berduka atas mereka yang tidak patut didukakan; dan engkau berbicara kata-kata bijak! Orang terpelajar tidak berduka atas yang telah pergi maupun yang belum pergi.', 58);
INSERT INTO verse_translations (id, content, verse_id) VALUES (291, 'Tidak pernah ada waktu di mana Aku tidak ada, begitu pula engkau, atau raja-raja ini. Pun tidak akan ada waktu di masa depan ketika kita semua akan berhenti ada.', 59);
INSERT INTO verse_translations (id, content, verse_id) VALUES (292, 'Namun tentu saja bukan fakta bahwa aku tidak pernah ada; begitu juga engkau, atau para penguasa ini. Dan tentu saja bukan berarti kita semua akan berhenti ada setelah ini.', 59);
INSERT INTO verse_translations (id, content, verse_id) VALUES (296, 'Sama seperti diri yang berhubungan dengan tubuh melewati masa kanak-kanak, remaja, dan usia tua, demikian pula, saat mati, ia beralih ke tubuh lain. Orang bijak tidak tertipu oleh hal itu.', 60);
INSERT INTO verse_translations (id, content, verse_id) VALUES (297, 'Sebagaimana masa kanak-kanak, masa muda, dan usia lanjut dialami oleh makhluk berbadan di tubuh ini, demikian pula perolehan tubuh lain. Oleh karena itu, orang yang cerdas tidak tertipu.', 60);
INSERT INTO verse_translations (id, content, verse_id) VALUES (301, 'Sentuhan indra dengan objeknya, wahai Arjuna, menimbulkan perasaan dingin dan panas, suka dan duka. Mereka datang dan pergi, tidak pernah bertahan lama. Bertahanlah, wahai Arjuna.', 61);
INSERT INTO verse_translations (id, content, verse_id) VALUES (302, 'Tetapi kontak organ-organ dengan objek-objek adalah penyebab dingin dan panas, kebahagiaan, dan kesedihan. Mereka memiliki awal dan akhir dan bersifat sementara. Tanggunglah, wahai keturunan Bharata.', 61);
INSERT INTO verse_translations (id, content, verse_id) VALUES (306, 'Karena, dia yang tidak terpengaruh oleh hal-hal ini, wahai pemimpin manusia, dan yang baginya rasa sakit dan kesenangan adalah sama—pria yang teguh itu sendiri yang layak mendapatkan keabadian.', 62);
INSERT INTO verse_translations (id, content, verse_id) VALUES (307, 'Wahai Arjuna, yang terkemuka di antara manusia, sesungguhnya, orang yang tidak disiksa oleh hal-hal ini, orang bijak yang baginya kesedihan dan kebahagiaan adalah sama—ia layak untuk keabadian.', 62);
INSERT INTO verse_translations (id, content, verse_id) VALUES (311, 'Yang tidak nyata tidak pernah ada, yang nyata tidak pernah berhenti ada. Kebenaran tentang keduanya dilihat oleh para peramal.', 63);
INSERT INTO verse_translations (id, content, verse_id) VALUES (312, 'Tidak ada keberadaan yang tidak nyata; yang nyata tidak memiliki ketiadaan. Namun, sifat keduanya telah disadari oleh para peramal Kebenaran.', 63);
INSERT INTO verse_translations (id, content, verse_id) VALUES (316, 'Ketahuilah bahwa tak termusnahkan oleh apa pun yang melingkupi semua ini. Tidak ada seorang pun yang dapat menyebabkan kehancuran yang tak berubah ini.', 64);
INSERT INTO verse_translations (id, content, verse_id) VALUES (317, 'Tetapi ketahuilah bahwa tak termusnahkan oleh apa pun yang melingkupi semua ini. Tidak ada seorang pun yang dapat menyebabkan kehancuran yang tak berubah ini.', 64);
INSERT INTO verse_translations (id, content, verse_id) VALUES (321, 'Tubuh-tubuh Jiva (diri yang berbadan) ini dikatakan memiliki akhir, sedangkan Jiva itu sendiri abadi, tak termusnahkan, dan tak terpahami. Oleh karena itu, bertempurlah, wahai Bharata (Arjuna).', 65);
INSERT INTO verse_translations (id, content, verse_id) VALUES (322, 'Tubuh-tubuh fana ini dikatakan milik Yang Berbadan yang abadi, tak termusnahkan, dan tak terukur. Oleh karena itu, wahai keturunan Bharata, ikutlah bertempur.', 65);
INSERT INTO verse_translations (id, content, verse_id) VALUES (326, 'Dia yang menganggapnya (diri) sebagai pembunuh, dan dia yang menganggapnya sebagai terbunuh—keduanya tidak tahu. Karena, diri tidak membunuh dan tidak pula dibunuh.', 66);
INSERT INTO verse_translations (id, content, verse_id) VALUES (327, 'Dia yang menganggap Yang Satu ini sebagai pembunuh, dan dia yang menganggap Yang Satu ini sebagai yang terbunuh, keduanya tidak tahu. Yang Satu ini tidak membunuh dan tidak pula terbunuh.', 66);
INSERT INTO verse_translations (id, content, verse_id) VALUES (331, 'Ia tidak pernah lahir; ia tidak pernah mati; setelah ada sekali, ia tidak pernah berhenti ada. Tak lahir, abadi, kekal, dan purba, ia tidak hancur ketika tubuh dihancurkan.', 67);
INSERT INTO verse_translations (id, content, verse_id) VALUES (332, 'Yang Satu ini tidak pernah lahir, pun tidak pernah mati; pun, setelah ada, Ia tidak berhenti ada. Yang Satu ini tanpa kelahiran, abadi, tak lapuk, dan kuno; Ia tidak terbunuh ketika tubuh terbunuh.', 67);
INSERT INTO verse_translations (id, content, verse_id) VALUES (336, 'Dia yang tahu diri ini tak termusnahkan, tak lahir, tak berubah, dan abadi—bagaimana, wahai Arjuna, dia menyebabkan seseorang terbunuh, dan siapa yang dia bunuh?', 68);
INSERT INTO verse_translations (id, content, verse_id) VALUES (337, 'Wahai Partha, dia yang mengetahui Yang Satu ini sebagai tak termusnahkan, abadi, tak lahir, dan tak lapuk—bagaimana orang itu dapat membunuh, atau menyebabkan terbunuh, siapa?', 68);
INSERT INTO verse_translations (id, content, verse_id) VALUES (341, 'Seperti seorang pria membuang pakaian usang dan mengenakan yang baru, demikian pula diri yang berbadan membuang tubuh usangnya dan memasuki tubuh baru.', 69);
INSERT INTO verse_translations (id, content, verse_id) VALUES (342, 'Seperti setelah menolak pakaian usang, seorang pria mengambil yang baru, demikian pula, setelah menolak tubuh usang, yang berbadan bersatu dengan yang baru.', 69);
INSERT INTO verse_translations (id, content, verse_id) VALUES (346, 'Senjata tidak dapat membelah-Nya, api tidak dapat membakar-Nya, air tidak dapat membasahi-Nya, dan angin tidak dapat mengeringkan-Nya.', 70);
INSERT INTO verse_translations (id, content, verse_id) VALUES (347, 'Senjata tidak dapat memotongnya, api tidak dapat membakarnya, air tidak dapat membasahinya, dan udara tidak dapat mengeringkannya.', 70);
INSERT INTO verse_translations (id, content, verse_id) VALUES (351, 'Ia tidak dapat dibelah; Ia tidak dapat dibakar; Ia tidak dapat dibasahi, dan Ia tidak dapat dikeringkan; Ia abadi, mahakuasa, stabil, tak bergerak, dan purba.', 71);
INSERT INTO verse_translations (id, content, verse_id) VALUES (352, 'Tidak dapat dipotong, dibakar, dibasahi, atau dikeringkan; ia abadi, mahahadir, stasioner, tak bergerak, dan tak berubah.', 71);
INSERT INTO verse_translations (id, content, verse_id) VALUES (356, 'Ini (diri) dikatakan tidak termanifestasi, tak terpikirkan, dan tak berubah. Oleh karena itu, mengetahui hal ini, tidak pantas bagimu untuk berduka.', 72);
INSERT INTO verse_translations (id, content, verse_id) VALUES (357, 'Ini dikatakan tidak termanifestasi; Ini tak terpahami; Ini tak berubah. Oleh karena itu, setelah mengetahui Ini demikian, Anda tidak seharusnya berduka.', 72);
INSERT INTO verse_translations (id, content, verse_id) VALUES (361, 'Atau, jika engkau menganggap diri ini terus-menerus lahir dan terus-menerus mati, wahai yang berlengan perkasa, engkau tetap tidak pantas merasa berduka.', 73);
INSERT INTO verse_translations (id, content, verse_id) VALUES (362, 'Di sisi lain, jika Anda berpikir Yang Satu ini lahir terus-menerus atau mati terus-menerus, bahkan saat itu, wahai yang berlengan perkasa, Anda tidak seharusnya berduka.', 73);
INSERT INTO verse_translations (id, content, verse_id) VALUES (366, 'Karena, kematian pasti bagi yang lahir, dan kelahiran kembali pasti bagi yang mati; oleh karena itu, engkau tidak perlu berduka atas apa yang tak terhindarkan.', 74);
INSERT INTO verse_translations (id, content, verse_id) VALUES (367, 'Karena kematian setiap yang lahir adalah pasti, dan kelahiran kembali yang mati adalah pasti. Oleh karena itu, Anda tidak perlu berduka atas fakta yang tak terhindarkan.', 74);
INSERT INTO verse_translations (id, content, verse_id) VALUES (371, 'Wahai Arjuna, makhluk hidup memiliki awal yang tidak diketahui, pertengahan yang diketahui, dan akhir yang tidak diketahui. Apa yang perlu didukakan dari semua ini?', 75);
INSERT INTO verse_translations (id, content, verse_id) VALUES (372, 'Wahai keturunan Bharata, semua makhluk tetap tidak termanifestasi pada awalnya; mereka menjadi termanifestasi di tengah. Setelah kematian, mereka tentu menjadi tidak termanifestasi. Apa yang dapat didukakan bagi mereka?', 75);
INSERT INTO verse_translations (id, content, verse_id) VALUES (376, 'Seseorang menganggap Ini (diri) sebagai keajaiban; yang lain berbicara tentang Ini sebagai keajaiban; namun yang lain mendengar tentang Ini sebagai keajaiban; dan bahkan setelah mendengar tentang Ini, seseorang tidak mengetahui Ini.', 76);
INSERT INTO verse_translations (id, content, verse_id) VALUES (377, 'Seseorang mengvisualisasikannya sebagai keajaiban; orang lain membicarakannya sebagai keajaiban; orang lain mendengarnya sebagai keajaiban; dan ada pula yang tidak menyadarinya bahkan setelah mendengarnya.', 76);
INSERT INTO verse_translations (id, content, verse_id) VALUES (381, 'Diri dalam tubuh, wahai Arjuna, adalah abadi dan tak termusnahkan. Ini berlaku untuk semua diri dalam semua tubuh. Oleh karena itu, tidak pantas bagimu untuk merasa berduka atas makhluk apa pun.', 77);
INSERT INTO verse_translations (id, content, verse_id) VALUES (382, 'Wahai keturunan Bharata, diri yang berbadan ini yang ada di dalam tubuh setiap orang tidak akan pernah bisa dibunuh; oleh karena itu, Anda tidak perlu berduka atas semua makhluk ini.', 77);
INSERT INTO verse_translations (id, content, verse_id) VALUES (386, 'Selanjutnya, mempertimbangkan pula kewajibanmu sendiri, tidak pantas bagimu untuk ragu. Karena, bagi seorang Ksatria, tidak ada kebaikan yang lebih besar daripada perang yang benar.', 78);
INSERT INTO verse_translations (id, content, verse_id) VALUES (387, 'Bahkan mempertimbangkan tugas Anda sendiri, Anda tidak boleh ragu, karena tidak ada yang lebih baik bagi seorang Ksatriya daripada pertempuran yang adil.', 78);
INSERT INTO verse_translations (id, content, verse_id) VALUES (391, 'Bahagia adalah para Ksatria, wahai Arjuna, yang mengalami perang semacam itu secara spontan; itu membuka gerbang menuju surga.', 79);
INSERT INTO verse_translations (id, content, verse_id) VALUES (392, 'Wahai putra Partha, berbahagialah para Ksatria yang menghadapi pertempuran semacam itu, yang datang tanpa dicari dan merupakan gerbang terbuka menuju surga.', 79);
INSERT INTO verse_translations (id, content, verse_id) VALUES (396, 'Tetapi jika engkau tidak memerangi perang yang benar ini, engkau akan berpaling dari tugas dan kehormatanmu, dan akan menanggung dosa.', 80);
INSERT INTO verse_translations (id, content, verse_id) VALUES (397, 'Di sisi lain, jika Anda tidak memerangi pertempuran yang adil ini, maka Anda akan menanggung dosa, meninggalkan tugas dan ketenaran Anda sendiri.', 80);
INSERT INTO verse_translations (id, content, verse_id) VALUES (401, 'Selanjutnya, orang-orang akan membicarakan keburukanmu selamanya, dan bagi orang yang terbiasa dihormati, kehinaan lebih buruk daripada kematian.', 81);
INSERT INTO verse_translations (id, content, verse_id) VALUES (402, 'Orang-orang juga akan membicarakan kehinaanmu yang tak berkesudahan, dan bagi orang terhormat, kehinaan lebih buruk daripada kematian.', 81);
INSERT INTO verse_translations (id, content, verse_id) VALUES (406, 'Para prajurit besar akan berpikir bahwa engkau telah lari dari pertempuran karena ketakutan. Mereka yang sangat menghormatimu sekarang akan membicarakanmu dengan merendahkan.', 82);
INSERT INTO verse_translations (id, content, verse_id) VALUES (407, 'Para pengendara kereta perang yang hebat akan menganggap Anda telah mundur dari pertarungan karena ketakutan, dan Anda akan jatuh dalam aib di hadapan mereka yang sebelumnya menganggap Anda terhormat.', 82);
INSERT INTO verse_translations (id, content, verse_id) VALUES (411, 'Musuh-musuhmu, memfitnah kemampuanmu, akan menggunakan kata-kata yang seharusnya tidak pernah diucapkan. Apa yang bisa lebih menyakitkan dari itu?', 83);
INSERT INTO verse_translations (id, content, verse_id) VALUES (412, 'Dan musuh-musuhmu akan mengucapkan banyak kata-kata tidak senonoh sambil merendahkan kekuatanmu. Apa yang bisa lebih menyakitkan dari itu?', 83);
INSERT INTO verse_translations (id, content, verse_id) VALUES (416, 'Jika terbunuh, engkau akan memenangkan surga; atau jika menang, engkau akan menikmati bumi. Oleh karena itu, bangkitlah, wahai Arjuna, bertekad untuk bertempur.', 84);
INSERT INTO verse_translations (id, content, verse_id) VALUES (417, 'Baik dengan terbunuh, Anda akan mencapai surga, atau dengan menang, Anda akan menikmati bumi. Oleh karena itu, wahai Arjuna, bangkitlah dengan tekad untuk bertempur.', 84);
INSERT INTO verse_translations (id, content, verse_id) VALUES (421, 'Dengan menyamakan kesenangan dan rasa sakit, untung dan rugi, kemenangan dan kekalahan, bersiaplah untuk bertempur. Dengan demikian, engkau tidak akan menanggung dosa apa pun.', 85);
INSERT INTO verse_translations (id, content, verse_id) VALUES (422, 'Memperlakukan kebahagiaan dan kesedihan, untung dan rugi, serta penaklukan dan kekalahan dengan ketenangan yang sama, kemudian terlibatlah dalam pertempuran. Dengan demikian, Anda tidak akan menanggung dosa.', 85);
INSERT INTO verse_translations (id, content, verse_id) VALUES (426, 'Buddhi mengenai Diri (Sankhya) ini telah Aku sampaikan kepadamu. Sekarang dengarkan ini sehubungan dengan Yoga, dengan mengikuti ini kamu akan terbebas dari ikatan Karma.', 86);
INSERT INTO verse_translations (id, content, verse_id) VALUES (427, 'Wahai Partha, kebijaksanaan ini telah disampaikan kepadamu dari sudut pandang Realisasi Diri. Tetapi dengarkanlah kebijaksanaan ini dari sudut pandang Yoga, yang dengannya, Anda akan terbebas dari ikatan tindakan.', 86);
INSERT INTO verse_translations (id, content, verse_id) VALUES (431, 'Di sini, tidak ada kerugian upaya, pun tidak ada penumpukan kejahatan. Sedikit saja Dharma (disebut Karma Yoga) ini melindungi seseorang dari ketakutan besar.', 87);
INSERT INTO verse_translations (id, content, verse_id) VALUES (432, 'Di sini, tidak ada pemborosan upaya; pun tidak ada bahaya. Sedikit saja kebenaran ini menyelamatkan seseorang dari ketakutan besar.', 87);
INSERT INTO verse_translations (id, content, verse_id) VALUES (436, 'Dalam hal ini (Karma Yoga), wahai Arjuna, pikiran yang teguh berpusat pada satu titik; pikiran yang tidak teguh bercabang banyak dan tak berkesudahan.', 88);
INSERT INTO verse_translations (id, content, verse_id) VALUES (437, 'Wahai keturunan dinasti Kuru, dalam hal ini ada satu keyakinan yang terarah. Pikiran orang-orang yang tidak teguh, bagaimanapun, memiliki banyak cabang dan tak terhitung jumlahnya.', 88);
INSERT INTO verse_translations (id, content, verse_id) VALUES (441, '- Wahai Partha, orang-orang yang tidak bijaksana, yang bergembira dalam surat-surat Veda, berkata, "Tidak ada yang lain." Mereka hanya penuh dengan keinginan duniawi dan mereka mendambakan surga. Mereka mengucapkan kata-kata berbunga-bunga yang menawarkan kegembiraan sebagai buah kerja. Mereka memandang Veda sebagai seluruhnya terdiri dari berbagai ritual untuk mencapai kesenangan dan kekuasaan. Mereka yang begitu terikat pada kesenangan dan kekuasaan tertarik oleh perkataan itu (menawarkan pahala surgawi) dan tidak mampu mengembangkan kehendak yang teguh dari pikiran yang terkonsentrasi.', 89);
INSERT INTO verse_translations (id, content, verse_id) VALUES (442, 'Wahai putra Prtha, orang-orang yang tidak bijaksana ini mengucapkan perkataan bunga-bunga ini, yang menjanjikan kelahiran sebagai hasil dari ritus dan kewajiban, dan penuh dengan berbagai ritus khusus yang dimaksudkan untuk mencapai kenikmatan dan kemewahan, tetap tenggelam dalam ucapan-ucapan Veda dan menyatakan bahwa tidak ada yang lain; pikiran mereka penuh dengan keinginan dan surga sebagai tujuan mereka.', 89);
INSERT INTO verse_translations (id, content, verse_id) VALUES (446, '- Wahai Partha, orang-orang yang tidak bijaksana, yang bergembira dalam ajaran Veda, berkata, "Tidak ada yang lain." Mereka hanya penuh dengan keinginan duniawi dan mereka mendambakan surga. Mereka mengucapkan kata-kata berbunga-bunga yang menawarkan kegembiraan sebagai buah kerja. Mereka memandang Veda sebagai seluruhnya terdiri dari berbagai ritual untuk mencapai kesenangan dan kekuasaan. Mereka yang begitu terikat pada kesenangan dan kekuasaan tertarik oleh perkataan itu (menawarkan pahala surgawi) dan tidak mampu mengembangkan kehendak yang teguh dari pikiran yang terkonsentrasi.', 90);
INSERT INTO verse_translations (id, content, verse_id) VALUES (447, 'Wahai putra Prtha, orang-orang yang tidak bijaksana ini mengucapkan perkataan bunga-bunga ini, yang menjanjikan kelahiran sebagai hasil dari ritus dan kewajiban, dan penuh dengan berbagai ritus khusus yang dimaksudkan untuk mencapai kenikmatan dan kemewahan, tetap tenggelam dalam ucapan-ucapan Veda dan menyatakan bahwa tidak ada yang lain; pikiran mereka penuh dengan keinginan dan surga sebagai tujuan mereka.', 90);
INSERT INTO verse_translations (id, content, verse_id) VALUES (451, '- Wahai Partha, orang-orang yang tidak bijaksana, yang bergembira dalam ajaran Veda, berkata, "Tidak ada yang lain." Mereka hanya penuh dengan keinginan duniawi dan mereka mendambakan surga. Mereka mengucapkan kata-kata berbunga-bunga yang menawarkan kegembiraan sebagai buah kerja. Mereka memandang Veda sebagai seluruhnya terdiri dari berbagai ritual untuk mencapai kesenangan dan kekuasaan. Mereka yang begitu terikat pada kesenangan dan kekuasaan tertarik oleh perkataan itu (menawarkan pahala surgawi) dan tidak mampu mengembangkan kehendak yang teguh dari pikiran yang terkonsentrasi.', 91);
INSERT INTO verse_translations (id, content, verse_id) VALUES (452, 'Keyakinan yang terarah tidak dapat terbentuk dalam pikiran mereka yang menyukai kenikmatan dan kemewahan, dan yang inteleknya terbawa oleh hal itu.', 91);
INSERT INTO verse_translations (id, content, verse_id) VALUES (456, 'Veda memiliki tiga Guna sebagai ranahnya, wahai Arjuna. Engkau harus bebas dari tiga Guna dan bebas dari pasangan lawan. Tetaplah dalam Sattva murni; jangan pernah peduli untuk memperoleh sesuatu dan melindungi apa yang telah diperoleh, tetapi tetaplah dalam Diri.', 92);
INSERT INTO verse_translations (id, content, verse_id) VALUES (457, 'Wahai Arjuna, Veda [Artinya hanya bagian yang membahas ritus dan kewajiban (karma-kanda).] memiliki tiga kualitas sebagai objeknya. Anda bebas dari keduniawian, bebas dari pasangan dualitas, selalu seimbang dalam kualitas sattva, tanpa (keinginan untuk) akuisisi dan perlindungan, dan terkumpul sendiri.', 92);
INSERT INTO verse_translations (id, content, verse_id) VALUES (461, 'Apa gunanya waduk air bagi orang yang haus ketika semua sisinya banjir—itulah satu-satunya kegunaan semua Veda bagi seorang Brahmana yang mengetahui.', 93);
INSERT INTO verse_translations (id, content, verse_id) VALUES (462, 'Seorang Brahmana dengan kesadaran memiliki kegunaan yang sama dalam semua Veda seperti yang dimiliki seseorang di sumur ketika ada banjir di sekelilingnya.', 93);
INSERT INTO verse_translations (id, content, verse_id) VALUES (466, 'Engkau hanya memiliki hak untuk bekerja, tetapi tidak untuk buahnya. Jangan terdorong oleh hasil kerjamu, pun jangan terikat pada kelambanan.', 94);
INSERT INTO verse_translations (id, content, verse_id) VALUES (467, 'Hak Anda hanyalah untuk bertindak, bukan untuk hasilnya. Jangan menjadi penyebab hasil tindakan. Jangan memiliki kecenderungan terhadap kelambanan.', 94);
INSERT INTO verse_translations (id, content, verse_id) VALUES (471, 'Dengan melepaskan keterikatan dan menetap dalam Yoga, lakukanlah pekerjaan, memandang keberhasilan dan kegagalan dengan pikiran yang seimbang. Keseimbangan pikiran dikatakan sebagai Yoga.', 95);
INSERT INTO verse_translations (id, content, verse_id) VALUES (472, 'Dengan menetap dalam Yoga, wahai Dhananjaya (Arjuna), lakukanlah tindakan, singkirkan keterikatan dan tetaplah seimbang dalam keberhasilan dan kegagalan. Keseimbangan disebut Yoga.', 95);
INSERT INTO verse_translations (id, content, verse_id) VALUES (476, 'Tindakan yang dilakukan dengan keterikatan, wahai Arjuna, jauh lebih rendah daripada tindakan yang dilakukan dengan keseimbangan pikiran. Carilah perlindungan dalam keseimbangan pikiran; mereka yang bertindak dengan motif hasil akan sengsara.', 96);
INSERT INTO verse_translations (id, content, verse_id) VALUES (477, 'Wahai Dhananjaya, sesungguhnya, tindakan lebih rendah dari yoga kebijaksanaan. Carilah perlindungan dalam kebijaksanaan. Mereka yang haus akan pahala patut dikasihani.', 96);
INSERT INTO verse_translations (id, content, verse_id) VALUES (481, 'Seseorang dengan keseimbangan pikiran menyingkirkan baik kebaikan maupun kejahatan di sini dan sekarang. Oleh karena itu, berjuanglah untuk Yoga; karena Yoga adalah keterampilan dalam bertindak.', 97);
INSERT INTO verse_translations (id, content, verse_id) VALUES (482, 'Dilengkapi dengan kebijaksanaan, seseorang menolak baik kebajikan maupun kejahatan di sini. Oleh karena itu, baktikan diri Anda pada (Karma-)yoga; karena yoga adalah keterampilan dalam bertindak.', 97);
INSERT INTO verse_translations (id, content, verse_id) VALUES (486, 'Orang bijak, yang memiliki keseimbangan pikiran, melepaskan buah yang lahir dari tindakan, dan terbebas dari ikatan kelahiran, pergi ke wilayah di luar semua penyakit.', 98);
INSERT INTO verse_translations (id, content, verse_id) VALUES (487, 'Bagi mereka yang mengabdi pada kebijaksanaan, mereka menjadi orang yang tercerahkan dengan melepaskan buah dari tindakan mereka, dan dengan demikian mereka mencapai keadaan di luar kejahatan, setelah dibebaskan dari ikatan kelahiran.', 98);
INSERT INTO verse_translations (id, content, verse_id) VALUES (491, 'Ketika intelekmu telah melampaui belitan delusi, engkau sendiri akan merasa jijik dengan apa yang telah kau dengar dan apa yang akan kau dengar.', 99);
INSERT INTO verse_translations (id, content, verse_id) VALUES (492, 'Ketika pikiran Anda melampaui kekeruhan delusi, maka Anda akan memperoleh ketidakmelekatan terhadap apa yang harus didengar dan apa yang telah didengar.', 99);
INSERT INTO verse_translations (id, content, verse_id) VALUES (496, 'Ketika intelekmu, setelah tercerahkan oleh pendengaran dari-Ku dan tertanam kokoh, berdiri teguh dalam pikiran yang terkonsentrasi, maka engkau akan mencapai penglihatan Diri.', 100);
INSERT INTO verse_translations (id, content, verse_id) VALUES (497, 'Ketika pikiran Anda, yang telah bingung karena mendengar, menjadi tak tergoyahkan dan teguh dalam Diri, maka Anda akan mencapai Yoga yang timbul dari diskriminasi.', 100);
INSERT INTO verse_translations (id, content, verse_id) VALUES (501, 'Arjuna berkata, “Bagaimana cara bicara orang yang memiliki kebijaksanaan teguh, yang telah mencapai pengendalian pikiran? Apa yang diucapkan orang yang memiliki kebijaksanaan teguh, wahai Krsna? Bagaimana dia duduk? Bagaimana dia bergerak?”', 101);
INSERT INTO verse_translations (id, content, verse_id) VALUES (502, 'Arjuna berkata, “Wahai Kesawa, bagaimana deskripsi seorang yang memiliki kebijaksanaan mantap yang tenggelam dalam dirinya sendiri? Bagaimana dia berbicara, duduk, dan bergerak?”', 101);
INSERT INTO verse_translations (id, content, verse_id) VALUES (506, 'Sang Raja berkata, “Ketika seseorang meninggalkan semua keinginan pikiran dan merasa puas dengan dirinya sendiri, maka dia dikatakan memiliki kebijaksanaan yang teguh.”', 102);
INSERT INTO verse_translations (id, content, verse_id) VALUES (507, 'Yang Mahamulia berkata, “Wahai Partha, ketika seseorang sepenuhnya meninggalkan semua keinginan yang telah memasuki pikiran dan tetap puas hanya dengan Diri, maka dia disebut seorang yang memiliki kebijaksanaan mantap.”', 102);
INSERT INTO verse_translations (id, content, verse_id) VALUES (511, 'Dia yang pikirannya tidak terganggu oleh rasa sakit, yang tidak merindukan kesenangan, yang bebas dari keinginan, ketakutan, dan kemarahan—dia disebut seorang bijak yang memiliki kebijaksanaan teguh.', 103);
INSERT INTO verse_translations (id, content, verse_id) VALUES (512, 'Bhikkhu itu disebut seorang yang memiliki kebijaksanaan mantap ketika pikirannya tidak terganggu bahkan dalam kesedihan, ia bebas dari kerinduan akan kesenangan, dan telah melampaui keterikatan, ketakutan, dan kemarahan.', 103);
INSERT INTO verse_translations (id, content, verse_id) VALUES (516, 'Dia yang tidak memiliki cinta di mana pun, yang, setelah menemukan kebaikan atau kejahatan, tidak bersukacita maupun membenci—kebijaksanaannya teguh.', 104);
INSERT INTO verse_translations (id, content, verse_id) VALUES (517, 'Kebijaksanaan orang itu tetap teguh yang tidak memiliki keterikatan pada apa pun di mana pun, yang tidak menyambut maupun menolak apa pun, apa pun yang baik atau buruk, ketika mereka menemukannya.', 104);
INSERT INTO verse_translations (id, content, verse_id) VALUES (521, 'Ketika seseorang mampu menarik indranya dari objek indra di semua sisi, seperti kura-kura menarik anggota tubuhnya, maka kebijaksanaannya teguh.', 105);
INSERT INTO verse_translations (id, content, verse_id) VALUES (522, 'Dan ketika seseorang sepenuhnya menarik indranya dari objek-objek indra, seperti kura-kura menarik anggota tubuhnya, maka kebijaksanaannya tetap teguh.', 105);
INSERT INTO verse_translations (id, content, verse_id) VALUES (526, 'Objek-objek indra, kecuali kesenangan yang dibawanya, berpaling dari penghuni tubuh yang berpantang. Bahkan kesenangan pun berpaling darinya ketika Diri, yang tertinggi atas indra, terlihat.', 106);
INSERT INTO verse_translations (id, content, verse_id) VALUES (527, 'Objek-objek mundur dari orang yang berpantang, kecuali selera untuk mereka. Bahkan selera ini hilang setelah realisasi Yang Mutlak.', 106);
INSERT INTO verse_translations (id, content, verse_id) VALUES (531, 'Indra yang bergejolak, wahai Arjuna, dapat secara paksa menguasai pikiran orang bijak sekalipun, meskipun dia selalu berusaha.', 107);
INSERT INTO verse_translations (id, content, verse_id) VALUES (532, 'Karena, wahai putra Kunti, indra-indra yang bergejolak dapat secara paksa merebut pikiran orang yang cerdas, bahkan saat mereka berusaha keras.', 107);
INSERT INTO verse_translations (id, content, verse_id) VALUES (536, 'Setelah mengendalikan semua indra, biarkan dia tetap dalam kontemplasi, menganggapku sebagai yang tertinggi; karena pengetahuannya teguh ketika indranya terkendali.', 108);
INSERT INTO verse_translations (id, content, verse_id) VALUES (537, 'Mengendalikan semuanya, seseorang harus tetap terfokus pada-Ku sebagai yang tertinggi. Karena, kebijaksanaan orang yang indranya terkendali menjadi teguh.', 108);
INSERT INTO verse_translations (id, content, verse_id) VALUES (541, 'Bagi orang yang memikirkan objek-objek indra, timbullah keterikatan pada mereka; dari keterikatan timbullah keinginan, dan dari keinginan timbullah kemarahan.', 109);
INSERT INTO verse_translations (id, content, verse_id) VALUES (542, '- Dalam kasus seseorang yang merenungkan objek, timbullah keterikatan pada mereka. Dari keterikatan tumbuh kerinduan, dan dari kerinduan muncullah kemarahan.', 109);
INSERT INTO verse_translations (id, content, verse_id) VALUES (546, 'Kemarahan menyebabkan delusi, yang menyebabkan hilangnya ingatan; hilangnya ingatan menyebabkan hancurnya diskriminasi, dan dengan hancurnya diskriminasi, seseorang hilang.', 110);
INSERT INTO verse_translations (id, content, verse_id) VALUES (547, 'Dari kemarahan timbul delusi; dari delusi, kegagalan ingatan; dari kegagalan ingatan, hilangnya pemahaman; dan dari hilangnya pemahaman, seseorang binasa.', 110);
INSERT INTO verse_translations (id, content, verse_id) VALUES (551, 'Tetapi dia yang melalui objek indra dengan indra yang bebas dari cinta dan benci, disiplin dan terkendali, mencapai ketenangan.', 111);
INSERT INTO verse_translations (id, content, verse_id) VALUES (552, 'Tetapi dengan merasakan objek dengan organ-organ yang bebas dari daya tarik dan penolakan, dan berada di bawah kendalinya sendiri, orang yang menguasai diri mencapai ketenangan.', 111);
INSERT INTO verse_translations (id, content, verse_id) VALUES (556, 'Dalam ketenangan itu, semua kesedihan hilang; karena dalam kasus orang dengan pikiran yang tenang, Buddhi dengan cepat terbentuk.', 112);
INSERT INTO verse_translations (id, content, verse_id) VALUES (557, 'Ketika ada ketenangan, semua kesedihannya terhapus, karena kebijaksanaan orang yang berhati tenang segera menjadi teguh.', 112);
INSERT INTO verse_translations (id, content, verse_id) VALUES (561, 'Tidak ada Buddhi bagi yang tidak terintegrasi, pun tidak ada kontemplasi diri baginya, dan tanpa kontemplasi diri, tidak ada kedamaian; dan bagi yang tidak memiliki kedamaian, di mana kebahagiaan?', 113);
INSERT INTO verse_translations (id, content, verse_id) VALUES (562, 'Bagi yang tidak stabil tidak ada kebijaksanaan, dan tidak ada meditasi bagi orang yang tidak stabil. Dan bagi orang yang tidak bermeditasi tidak ada kedamaian. Bagaimana bisa ada kebahagiaan bagi orang yang tidak memiliki kedamaian?', 113);
INSERT INTO verse_translations (id, content, verse_id) VALUES (566, 'Karena, ketika pikiran mengikuti indra, mengalami objeknya, pemahamannya terbawa olehnya, seperti angin membawa kapal di atas air.', 114);
INSERT INTO verse_translations (id, content, verse_id) VALUES (567, 'Karena, pikiran yang mengikuti indra yang berkelana membawa kebijaksanaannya seperti perahu di atas air.', 114);
INSERT INTO verse_translations (id, content, verse_id) VALUES (571, 'Oleh karena itu, wahai yang berlengan perkasa, dia yang indranya terkendali dari mengejar objeknya di semua sisi, kebijaksanaannya teguh.', 115);
INSERT INTO verse_translations (id, content, verse_id) VALUES (572, 'Oleh karena itu, wahai yang perkasa, kebijaksanaan ini menjadi teguh ketika semua indranya ditarik dari objeknya.', 115);
INSERT INTO verse_translations (id, content, verse_id) VALUES (576, 'Apa yang malam bagi semua makhluk, di dalamnya yang terkendali terjaga; ketika semua makhluk terjaga, itulah malam bagi sang bijak yang melihat.', 116);
INSERT INTO verse_translations (id, content, verse_id) VALUES (577, 'Orang yang mengendalikan diri tetap terjaga saat malam bagi semua makhluk. Apa yang membuat makhluk terjaga, itu adalah malam bagi sang bijak yang membedakan.', 116);
INSERT INTO verse_translations (id, content, verse_id) VALUES (581, 'Dia yang semua keinginan masuk ke dalamnya, sama seperti air masuk ke laut yang penuh dan tidak terganggu, mencapai kedamaian, dan bukan dia yang merindukan objek keinginan.', 117);
INSERT INTO verse_translations (id, content, verse_id) VALUES (582, 'Orang itu mencapai kedamaian ke mana semua keinginan masuk, sama seperti air mengalir ke laut yang tetap tidak berubah meskipun terisi dari semua sisi. Tidak demikian halnya dengan orang yang mendambakan objek.', 117);
INSERT INTO verse_translations (id, content, verse_id) VALUES (586, 'Orang yang, meninggalkan semua keinginan, tetap hidup tanpa kerinduan, kepemilikan, dan rasa \'aku\' dan \'milikku\', mencapai kedamaian.', 118);
INSERT INTO verse_translations (id, content, verse_id) VALUES (587, 'Orang itu mencapai kedamaian yang, setelah menolak semua keinginan, bergerak bebas dari kerinduan, tanpa gagasan \'aku\' dan \'milikku\', dan tanpa kesombongan.', 118);
INSERT INTO verse_translations (id, content, verse_id) VALUES (591, 'Inilah keadaan Brahman, wahai Arjuna. Tidak seorang pun yang mencapai ini tertipu. Dengan berdiam dalam keadaan ini bahkan pada saat kematian, seseorang akan mencapai Diri.', 119);
INSERT INTO verse_translations (id, content, verse_id) VALUES (592, 'Wahai Partha, ini adalah keadaan berdiam dalam Brahman. Seseorang tidak menjadi tertipu setelah mencapai ini; seseorang mencapai identifikasi dengan Brahman dengan berdiam dalam keadaan ini bahkan di tahun-tahun terakhir hidupnya.', 119);
INSERT INTO verse_translations (id, content, verse_id) VALUES (596, 'Arjuna berkata, “Wahai Krsna, jika engkau menganggap pengetahuan lebih unggul daripada tindakan, mengapa engkau mendesakku untuk melakukan perbuatan mengerikan ini?”', 120);
INSERT INTO verse_translations (id, content, verse_id) VALUES (597, 'Arjuna berkata, “Wahai Janardana (Krishna), jika menurut Anda kebijaksanaan lebih unggul daripada tindakan, mengapa Anda mendesak saya untuk melakukan tindakan yang mengerikan ini, wahai Kesava?”', 120);
INSERT INTO verse_translations (id, content, verse_id) VALUES (601, 'Engkau membingungkan pikiranku dengan pernyataan yang tampaknya saling bertentangan; beritahukanlah kepadaku dengan pasti satu cara agar aku dapat mencapai kebaikan tertinggi.', 121);
INSERT INTO verse_translations (id, content, verse_id) VALUES (602, 'Anda membingungkan pemahaman saya, seolah-olah, dengan pernyataan yang tampaknya bertentangan! Beritahukanlah kepada saya dengan pasti yang mana di antara ini yang dapat saya gunakan untuk mencapai Kebaikan tertinggi.', 121);
INSERT INTO verse_translations (id, content, verse_id) VALUES (606, 'Sang Raja berkata, “Di dunia ini, ada dua jalan yang telah Ku-tetapkan sejak dahulu, wahai yang tanpa dosa: Jnana Yoga bagi para Sankhya dan Karma Yoga bagi para Yogin.”', 122);
INSERT INTO verse_translations (id, content, verse_id) VALUES (607, 'Yang Mahamulia berkata, wahai yang tanpa cela, dua jenis keteguhan di dunia ini telah Aku bicarakan di masa lalu—melalui Yoga Pengetahuan bagi orang-orang yang tercerahkan; melalui Yoga Tindakan bagi para yogi.', 122);
INSERT INTO verse_translations (id, content, verse_id) VALUES (611, 'Tidak ada seorang pun yang mengalami kebebasan dari tindakan (Naiskarmya) dengan berpantang dari pekerjaan; dan tidak seorang pun yang mencapai keberhasilan hanya dengan pelepasan pekerjaan.', 123);
INSERT INTO verse_translations (id, content, verse_id) VALUES (612, 'Seseorang tidak mencapai kebebasan dari tindakan dengan menahan diri dari tindakan; pun tidak mencapai pemenuhan hanya melalui pelepasan.', 123);
INSERT INTO verse_translations (id, content, verse_id) VALUES (616, 'Tidak seorang pun dapat tetap diam bahkan sesaat tanpa melakukan pekerjaan; karena setiap orang dipaksa untuk bertindak, tanpa sadar, oleh Guna yang lahir dari Alam.', 124);
INSERT INTO verse_translations (id, content, verse_id) VALUES (617, 'Karena tidak seorang pun pernah tetap diam bahkan sesaat tanpa melakukan pekerjaan, karena semua dipaksa untuk bekerja di bawah paksaan oleh guna yang lahir dari Alam.', 124);
INSERT INTO verse_translations (id, content, verse_id) VALUES (621, 'Dia yang, mengendalikan organ-organ tindakan, membiarkan pikirannya berdiam pada objek indra, adalah orang yang tertipu dan munafik.', 125);
INSERT INTO verse_translations (id, content, verse_id) VALUES (622, 'Orang yang, setelah menarik organ-organ tindakan, duduk secara mental mengingat objek-objek indra, disebut munafik, karena mereka memiliki pikiran yang tertipu.', 125);
INSERT INTO verse_translations (id, content, verse_id) VALUES (626, 'Tetapi dia yang, menundukkan indranya dengan pikiran, wahai Arjuna, mulai mempraktikkan Karma Yoga melalui organ-organ tindakan dan yang bebas dari keterikatan, unggul.', 126);
INSERT INTO verse_translations (id, content, verse_id) VALUES (627, 'Tetapi, wahai Arjuna, seorang yang terlibat dalam Karma-yoga, mengendalikan organ-organ tindakan dengan pikiran dan menjadi tanpa keterikatan, unggul.', 126);
INSERT INTO verse_translations (id, content, verse_id) VALUES (631, 'Engkau harus melakukan tindakan wajibmu; karena tindakan lebih unggul daripada tidak bertindak (Jñāna Yoga). Bagi orang yang mengikuti tidak bertindak, bahkan pemeliharaan tubuh pun tidak mungkin.', 127);
INSERT INTO verse_translations (id, content, verse_id) VALUES (632, 'Anda harus melakukan tugas-tugas wajib Anda, karena tindakan lebih unggul daripada kelambanan. Selain itu, bahkan pemeliharaan tubuh Anda tidak akan mungkin tanpa tindakan.', 127);
INSERT INTO verse_translations (id, content, verse_id) VALUES (636, 'Dunia ini terikat dalam perbudakan kerja hanya ketika pekerjaan tidak dilakukan sebagai pengorbanan. Wahai Arjuna, engkau harus melakukan pekerjaan untuk tujuan ini, bebas dari keterikatan.', 128);
INSERT INTO verse_translations (id, content, verse_id) VALUES (637, 'Manusia ini terikat oleh tindakan selain yang dimaksudkan untuk Tuhan. Tanpa terikat, wahai putra Kunti, lakukanlah tindakan untuk Dia.', 128);
INSERT INTO verse_translations (id, content, verse_id) VALUES (641, 'Pada awalnya, Tuhan semua makhluk menciptakan manusia bersama dengan pengorbanan dan berkata, "Dengan ini kamu akan makmur; ini akan menjadi sapi kelimpahan, memenuhi semua keinginanmu."', 129);
INSERT INTO verse_translations (id, content, verse_id) VALUES (642, 'Di masa lalu, setelah menciptakan makhluk bersama dengan pengorbanan, Prajapati berkata, “Dengan ini, Anda akan berlipat ganda. Biarlah ini menjadi penghasil objek keinginan Anda.”', 129);
INSERT INTO verse_translations (id, content, verse_id) VALUES (646, 'Dengan ini, senangkanlah para dewa, dan mereka akan mendukungmu. Demikianlah, dengan saling memelihara, engkau dapat memperoleh kebaikan tertinggi.', 130);
INSERT INTO verse_translations (id, content, verse_id) VALUES (647, '\'Anda memberi makan para dewa dengan ini. Biarkan para dewa itu memberi makan Anda sebagai balasannya. Dengan saling memberi makan, Anda akan mencapai Kebaikan tertinggi.\'', 130);
INSERT INTO verse_translations (id, content, verse_id) VALUES (651, 'Para dewa, yang senang dengan pengorbanan, akan memberimu kenikmatan yang kamu inginkan. Dia yang menikmati kemurahan hati para dewa tanpa memberi mereka imbalan apa pun adalah seorang pencuri belaka.', 131);
INSERT INTO verse_translations (id, content, verse_id) VALUES (652, '\'Dengan ikut serta dalam sisa-sisa pengorbanan, mereka terbebas dari semua dosa. Tetapi mereka yang tidak suci dan memasak untuk diri mereka sendiri menanggung dosa.\'', 131);
INSERT INTO verse_translations (id, content, verse_id) VALUES (656, 'Orang-orang saleh yang memakan sisa-sisa kurban dibebaskan dari segala dosa, sedangkan orang-orang berdosa yang memasak hanya untuk kepentingan diri sendiri hanya akan menanggung dosa.', 132);
INSERT INTO verse_translations (id, content, verse_id) VALUES (657, 'Dengan mengambil bagian dalam sisa-sisa pengorbanan, mereka terbebas dari semua dosa. Tetapi mereka yang tidak suci dan memasak untuk diri mereka sendiri menanggung dosa.', 132);
INSERT INTO verse_translations (id, content, verse_id) VALUES (661, 'Semua makhluk berasal dari makanan; makanan dihasilkan dari hujan; hujan berasal dari pengorbanan; dan pengorbanan lahir dari tindakan.', 133);
INSERT INTO verse_translations (id, content, verse_id) VALUES (662, 'Dari makanan lahirlah makhluk; asal makanan adalah dari hujan; hujan berasal dari pengorbanan; pengorbanan memiliki tindakan sebagai sumbernya.', 133);
INSERT INTO verse_translations (id, content, verse_id) VALUES (666, 'Ketahuilah bahwa aktivitas berasal dari Brahman, yaitu tubuh fisik; Brahman berasal dari Diri yang tak termusnahkan; oleh karena itu, Brahman yang maha meliputi selalu ada dalam pengorbanan.', 134);
INSERT INTO verse_translations (id, content, verse_id) VALUES (667, 'Ketahuilah bahwa tindakan memiliki Veda sebagai asalnya; Veda memiliki Yang Tak Berubah sebagai sumbernya. Oleh karena itu, Veda yang maha meliputi selamanya didasarkan pada pengorbanan.', 134);
INSERT INTO verse_translations (id, content, verse_id) VALUES (671, 'Dia yang tidak mengikuti roda yang digerakkan demikian di sini, hidup dalam dosa, memuaskan indra, wahai Arjuna; dia hidup sia-sia.', 135);
INSERT INTO verse_translations (id, content, verse_id) VALUES (672, 'Wahai Partha, dia yang tidak mengikuti roda yang digerakkan demikian hidup sia-sia, hidupnya berdosa, dan mereka memanjakan indra.', 135);
INSERT INTO verse_translations (id, content, verse_id) VALUES (676, 'Tetapi orang yang kesenangannya hanya ada pada Diri, yang puas dengan Diri, yang bersukacita dalam Diri, baginya tidak ada lagi yang harus dicapai.', 136);
INSERT INTO verse_translations (id, content, verse_id) VALUES (677, 'Tetapi orang yang hanya bersukacita dalam Diri, puas dengan Diri, dan hanya puas dalam Diri—baginya tidak ada kewajiban yang harus dilakukan.', 136);
INSERT INTO verse_translations (id, content, verse_id) VALUES (681, 'Dia tidak memiliki tujuan yang ingin dicapai dari pekerjaan yang dilakukan atau tidak dilakukan, pun dia tidak perlu bergantung pada tujuan apa pun.', 137);
INSERT INTO verse_translations (id, content, verse_id) VALUES (682, 'Baginya, tidak ada kekhawatiran sama sekali di sini terkait dengan pelaksanaan tindakan, pun tidak ada yang tidak dilakukan. Selain itu, ia tidak bergantung pada objek apa pun untuk melayani tujuan apa pun.', 137);
INSERT INTO verse_translations (id, content, verse_id) VALUES (686, 'Oleh karena itu, lakukanlah pekerjaanmu tanpa keterikatan, yang seharusnya dilakukan. Karena, seseorang yang bekerja tanpa keterikatan mencapai Yang Tertinggi.', 138);
INSERT INTO verse_translations (id, content, verse_id) VALUES (687, 'Oleh karena itu, tetaplah tidak terikat, selalu lakukan tugas wajib; karena, dengan melakukan tugas seseorang tanpa keterikatan, seseorang mencapai yang tertinggi.', 138);
INSERT INTO verse_translations (id, content, verse_id) VALUES (691, 'Memang, Janaka dan yang lainnya mencapai kesempurnaan melalui Karma Yoga saja. Bahkan menyadari kebutuhannya untuk membimbing dunia, engkau harus bertindak.', 139);
INSERT INTO verse_translations (id, content, verse_id) VALUES (692, 'Karena Janaka dan yang lainnya berusaha mencapai pembebasan melalui tindakan itu sendiri. Anda harus melakukan tugas Anda, dengan mempertimbangkan pencegahan umat manusia dari kesesatan.', 139);
INSERT INTO verse_translations (id, content, verse_id) VALUES (696, 'Apa pun yang dilakukan orang besar, orang lain juga melakukannya. Standar apa pun yang dia tetapkan, dunia mengikutinya.', 140);
INSERT INTO verse_translations (id, content, verse_id) VALUES (697, 'Apa pun yang dilakukan orang superior, orang lain melakukan hal yang sama! Apa pun yang mereka tegakkan sebagai otoritas, orang biasa mengikuti itu.', 140);
INSERT INTO verse_translations (id, content, verse_id) VALUES (701, 'Bagiku, Arjuna, tidak ada apa pun di ketiga dunia ini yang harus dilakukan, pun tidak ada apa pun yang belum selesai yang harus diselesaikan. Namun aku terus bekerja.', 141);
INSERT INTO verse_translations (id, content, verse_id) VALUES (702, 'Di ketiga dunia, wahai Partha, tidak ada tugas yang harus Aku penuhi; tidak ada yang tersisa atau yang harus dicapai. (Namun demikian,) Aku terus bertindak.', 141);
INSERT INTO verse_translations (id, content, verse_id) VALUES (706, 'Jika aku tidak terus bekerja tanpa lelah, wahai Arjuna, orang-orang akan mengikuti jalanku.', 142);
INSERT INTO verse_translations (id, content, verse_id) VALUES (707, 'Karena, wahai Partha, jika sewaktu-waktu Aku tidak terus waspada dalam bertindak, orang-orang akan mengikuti jalan-Ku dalam segala hal.', 142);
INSERT INTO verse_translations (id, content, verse_id) VALUES (711, 'Jika aku tidak melakukan pekerjaan itu, orang-orang ini akan hilang; dan aku akan menyebabkan kekacauan dalam hidup dan dengan demikian menghancurkan semua orang ini.', 143);
INSERT INTO verse_translations (id, content, verse_id) VALUES (712, 'Dunia-dunia ini akan hancur jika saya tidak melakukan tindakan. Dan saya akan menjadi agen percampuran (kasta), dan akan menghancurkan makhluk-makhluk ini.', 143);
INSERT INTO verse_translations (id, content, verse_id) VALUES (716, 'Sama seperti orang-orang bodoh, yang terikat pada pekerjaan mereka, bertindak, wahai Arjuna, demikian pula orang-orang terpelajar harus bertindak tanpa keterikatan, dan hanya untuk kesejahteraan dunia.', 144);
INSERT INTO verse_translations (id, content, verse_id) VALUES (717, 'Wahai keturunan dinasti Bharata, sebagaimana orang-orang yang tidak tercerahkan bertindak dengan keterikatan pada pekerjaan, demikian pula orang yang tercerahkan harus bertindak, tanpa keterikatan, ingin mencegah orang-orang tersesat.', 144);
INSERT INTO verse_translations (id, content, verse_id) VALUES (721, 'Dia seharusnya tidak membingungkan pikiran orang-orang bodoh yang terikat pada pekerjaan; sebaliknya, dia harus melakukan pekerjaan itu sendiri dengan pengabdian dan menyebabkan orang lain melakukan hal yang sama.', 145);
INSERT INTO verse_translations (id, content, verse_id) VALUES (722, 'Orang yang tercerahkan tidak boleh mengganggu kepercayaan orang-orang bodoh, yang terikat pada pekerjaan. Dia harus tetap rajin sendiri dan membuat mereka melakukan semua tugas mereka.', 145);
INSERT INTO verse_translations (id, content, verse_id) VALUES (726, 'Tindakan dilakukan dalam segala hal oleh Guna Prakrti; dia yang sifatnya tertipu oleh egoisme, bagaimanapun, berpikir, \'Akulah pelaku.\'', 146);
INSERT INTO verse_translations (id, content, verse_id) VALUES (727, 'Sementara tindakan dilakukan dalam segala hal oleh guna (kualitas) Alam, seseorang yang tertipu oleh egoisme berpikir, “Saya adalah pelakunya.”', 146);
INSERT INTO verse_translations (id, content, verse_id) VALUES (731, 'Tetapi dia yang mengetahui kebenaran tentang pembagian Guna dan bekerja, wahai yang berlengan perkasa, melalui pengetahuannya bahwa Guna \'beroperasi pada produknya sendiri,\' tidak terikat.', 147);
INSERT INTO verse_translations (id, content, verse_id) VALUES (732, 'Tetapi, wahai yang perkasa, orang yang mengetahui fakta-fakta tentang berbagai guna dan tindakan tidak menjadi terikat, berpikir demikian: \'Organ-organ bertindak pada objek-objek organ.\'', 147);
INSERT INTO verse_translations (id, content, verse_id) VALUES (736, 'Mereka yang tertipu oleh Guna Prakrti terikat pada pekerjaan Guna. Tetapi dia yang mengetahui kebenaran seutuhnya tidak boleh mengganggu orang-orang bodoh yang tidak mengetahui kebenaran seutuhnya.', 148);
INSERT INTO verse_translations (id, content, verse_id) VALUES (737, 'Mereka yang sepenuhnya tertipu oleh guna Alam menjadi terikat pada aktivitas guna. Orang yang mengetahui Segalanya tidak boleh mengganggu orang-orang yang lamban inteleknya, yang tidak mengetahui Segalanya.', 148);
INSERT INTO verse_translations (id, content, verse_id) VALUES (741, 'Serahkan semua tindakanmu kepada-Ku, dengan pikiran terfokus pada Diri, bebas dari keinginan dan keegoisan, dan bertempurlah dengan panasnya kegembiraan yang telah mereda.', 149);
INSERT INTO verse_translations (id, content, verse_id) VALUES (742, 'Bebas dari demam jiwa, persembahkan semua tindakan kepada-Ku, dengan pikiranmu terfokus pada Diri, dan jadilah bebas dari harapan dan egoisme, dan terlibatlah dalam pertempuran.', 149);
INSERT INTO verse_translations (id, content, verse_id) VALUES (746, 'Orang-orang yang, penuh iman, selalu mempraktikkan ajaran-Ku ini dan mereka yang menerimanya tanpa cela—bahkan mereka dibebaskan dari karma.', 150);
INSERT INTO verse_translations (id, content, verse_id) VALUES (747, 'Mereka yang dengan setia mengikuti ajaran-Ku ini tanpa cela, mereka juga terbebas dari tindakan mereka.', 150);
INSERT INTO verse_translations (id, content, verse_id) VALUES (751, 'Tetapi mereka yang memfitnahnya, dan mereka yang tidak mempraktikkan ajaran-Ku ini—ketahuilah bahwa mereka sama sekali tidak berakal dan tidak memiliki pengetahuan, dan karenanya tersesat.', 151);
INSERT INTO verse_translations (id, content, verse_id) VALUES (752, 'Tetapi mereka yang, menemukan kesalahan di mana tidak ada, tidak mengikuti ajaran-Ku, ketahuilah mereka—yang tertipu tentang semua pengetahuan mengenai Brahman yang berkualitas dan tidak berkualitas dan yang tanpa diskriminasi—telah hancur.', 151);
INSERT INTO verse_translations (id, content, verse_id) VALUES (756, 'Bahkan orang yang berilmu bertindak sesuai dengan sifatnya; semua makhluk mengikuti sifatnya sendiri. Apa yang akan dicapai oleh penindasan?', 152);
INSERT INTO verse_translations (id, content, verse_id) VALUES (757, 'Bahkan seorang yang berilmu berperilaku sesuai dengan sifatnya sendiri. Mereka mengikuti sifat mereka. Apa yang dapat dilakukan oleh pengekangan?', 152);
INSERT INTO verse_translations (id, content, verse_id) VALUES (761, 'Setiap indra memiliki keterikatan dan kebencian yang tetap terhadap objek yang sesuai, tetapi seseorang tidak boleh berada di bawah pengaruhnya, karena mereka adalah musuh.', 153);
INSERT INTO verse_translations (id, content, verse_id) VALUES (762, 'Daya tarik dan penolakan ditentukan sehubungan dengan objek-objek semua organ. Seseorang tidak boleh terpengaruh oleh keduanya, karena keduanya adalah lawan.', 153);
INSERT INTO verse_translations (id, content, verse_id) VALUES (766, 'Lebih baik tugas sendiri, meskipun dilakukan dengan buruk, daripada tugas orang lain yang dilakukan dengan baik. Lebih baik mati saat melakukan tugas sendiri; tugas orang lain penuh ketakutan.', 154);
INSERT INTO verse_translations (id, content, verse_id) VALUES (767, 'Tugas seseorang sendiri, meskipun cacat, lebih unggul daripada tugas orang lain yang dilakukan dengan baik. Kematian lebih baik saat melakukan tugas seseorang sendiri; tugas orang lain penuh ketakutan.', 154);
INSERT INTO verse_translations (id, content, verse_id) VALUES (771, 'Arjuna berkata, “Tetapi, wahai Krsna, apa yang mendorong seseorang untuk berbuat dosa, bahkan bertentangan dengan kehendak mereka sendiri, seolah-olah dipaksa oleh kekuatan?”', 155);
INSERT INTO verse_translations (id, content, verse_id) VALUES (772, 'Arjuna berkata, “Sekarang, wahai keturunan dinasti Vrsni (Krsna), apa yang memaksa orang ini untuk berbuat dosa bahkan bertentangan dengan kehendaknya, seolah-olah dia dipaksa?”', 155);
INSERT INTO verse_translations (id, content, verse_id) VALUES (776, 'Sang Raja berkata, “Itu adalah keinginan, itu adalah kemarahan, lahir dari guna rajas; itu adalah pemangsa besar, pendorong dosa. Ketahuilah ini sebagai musuh di sini.”', 156);
INSERT INTO verse_translations (id, content, verse_id) VALUES (777, 'Yang Mahamulia berkata, “Keinginan ini, kemarahan ini, lahir dari kualitas rajas, adalah pemangsa besar, sumber dosa yang besar. Ketahuilah ini sebagai musuh di sini.”', 156);
INSERT INTO verse_translations (id, content, verse_id) VALUES (781, 'Seperti api diselimuti asap, seperti cermin ditutupi debu, dan seperti embrio terbungkus dalam kantung ketuban, demikian pula dunia ini diselimuti keinginan.', 157);
INSERT INTO verse_translations (id, content, verse_id) VALUES (782, 'Sebagaimana api diselimuti asap, cermin dikaburkan oleh kotoran, dan janin tetap terbungkus dalam rahim, demikian pula ini diselimuti oleh itu.', 157);
INSERT INTO verse_translations (id, content, verse_id) VALUES (786, 'Pengetahuan diri yang cerdas diselimuti oleh musuh abadi ini, wahai Arjuna, yang bersifat keinginan dan sulit dipuaskan serta tak terpuaskan.', 158);
INSERT INTO verse_translations (id, content, verse_id) VALUES (787, 'Wahai putra Kunti, pengetahuan ditutupi oleh musuh abadi orang bijak ini, dalam bentuk keinginan, yang merupakan api yang tak terpadamkan.', 158);
INSERT INTO verse_translations (id, content, verse_id) VALUES (791, 'Indra, pikiran, dan intelek dikatakan sebagai alatnya; dengan ini, ia mengalahkan diri yang berbadan, menyelimuti pengetahuannya.', 159);
INSERT INTO verse_translations (id, content, verse_id) VALUES (792, 'Organ, pikiran, dan intelek dikatakan sebagai tempat tinggalnya. Yang satu ini secara beragam menipu makhluk yang berbadan, menyelimuti Pengetahuan dengan bantuan ini.', 159);
INSERT INTO verse_translations (id, content, verse_id) VALUES (796, 'Oleh karena itu, wahai Arjuna, kendalikan indramu sejak awal dan bunuhlah hal berdosa ini yang menghancurkan pengetahuan dan kebijaksanaan.', 160);
INSERT INTO verse_translations (id, content, verse_id) VALUES (797, 'Oleh karena itu, wahai keturunan dinasti Bharata, setelah pertama-tama mengendalikan organ-organ, sepenuhnya tinggalkan yang satu ini yang berdosa dan penghancur pembelajaran dan kebijaksanaan.', 160);
INSERT INTO verse_translations (id, content, verse_id) VALUES (801, 'Mereka mengatakan bahwa indra itu tinggi, pikiran lebih tinggi dari indra, intelek lebih tinggi dari pikiran, tetapi yang lebih besar dari intelek adalah itu (keinginan).', 161);
INSERT INTO verse_translations (id, content, verse_id) VALUES (802, 'Mereka mengatakan bahwa organ lebih unggul dari tubuh kasar; pikiran lebih unggul dari organ; dan intelek lebih unggul dari pikiran. Namun, Dia lebih unggul dari intelek.', 161);
INSERT INTO verse_translations (id, content, verse_id) VALUES (806, 'Demikianlah, mengetahui yang lebih tinggi dari intelek dan memusatkan pikiran dengan bantuan intelek dalam Karma Yoga, wahai Arjuna, bunuhlah musuh ini yang berbentuk keinginan, dan yang sulit diatasi.', 162);
INSERT INTO verse_translations (id, content, verse_id) VALUES (807, '[Ast, memperkenalkan ayat ini dengan, \'Tatah kim, apa yang menyusul dari itu?\'-Tr.] Memahami Diri demikian sebagai yang lebih unggul dari intelek, dan sepenuhnya menetapkan (Diri) melalui penyerapan spiritual dengan (bantuan) pikiran, wahai yang perkasa, musuh dalam bentuk keinginan, yang sulit ditundukkan, lenyap.', 162);
INSERT INTO verse_translations (id, content, verse_id) VALUES (811, 'Sang Raja berkata, “Aku mengajarkan Yoga yang tak termusnahkan ini kepada Vivasvan; Vivasvan mengajarkannya kepada Manu; Manu menyatakannya kepada Iksvaku.”', 163);
INSERT INTO verse_translations (id, content, verse_id) VALUES (812, 'Sang Mahamulia berkata, “Aku menyampaikan Yoga yang tak termusnahkan ini kepada Vivasvan, yang kemudian mengajarkannya kepada Manu, yang pada gilirannya menyampaikannya kepada Iksavaku.', 163);
INSERT INTO verse_translations (id, content, verse_id) VALUES (816, 'Demikianlah diwariskan secara berurutan, para raja bijak mengetahui hal ini (Karma Yoga). Tetapi dengan berlalunya waktu yang lama, wahai Arjuna, Yoga itu hilang dari dunia.', 164);
INSERT INTO verse_translations (id, content, verse_id) VALUES (817, 'Para raja-bijak mengetahui yoga ini, yang diterima secara berurutan. Yoga itu, wahai penghancur musuh, sekarang hilang karena berlalunya waktu yang lama.', 164);
INSERT INTO verse_translations (id, content, verse_id) VALUES (821, 'Yoga kuno yang sama itulah yang sekarang Aku ajarkan kepadamu, karena engkau adalah pemuja-Ku dan teman-Ku. Karena, ini adalah misteri yang sangat besar.', 165);
INSERT INTO verse_translations (id, content, verse_id) VALUES (822, 'Yoga kuno itu sendiri, yang ini, telah diajarkan kepadamu oleh-Ku hari ini, mengingat engkau adalah pemuja-Ku dan teman-Ku, karena Yoga ini adalah rahasia yang mendalam.', 165);
INSERT INTO verse_translations (id, content, verse_id) VALUES (826, 'Arjuna berkata, “Kelahiran-Mu lebih kemudian, dan kelahiran Vivasvan lebih dahulu. Bagaimana aku bisa memahami bahwa Engkau mengajarkannya pada awalnya?”', 166);
INSERT INTO verse_translations (id, content, verse_id) VALUES (827, 'Arjuna berkata, “Kelahiranmu lebih kemudian, sedangkan kelahiran Vivasvan lebih dahulu. Bagaimana aku bisa mengerti bahwa Engkau mengajarinya di awal?”', 166);
INSERT INTO verse_translations (id, content, verse_id) VALUES (831, 'Sang Raja berkata, “Banyak kelahiran-Ku telah berlalu, wahai Arjuna, demikian pula kelahiranmu. Aku mengetahui semuanya, tetapi engkau tidak mengetahuinya.”', 167);
INSERT INTO verse_translations (id, content, verse_id) VALUES (832, 'Sang Mahamulia berkata, “Wahai Arjuna, banyak kehidupan-Ku telah berlalu, demikian pula kehidupanmu. Aku mengetahui semuanya, tetapi engkau tidak tahu, wahai penghancur musuh!”', 167);
INSERT INTO verse_translations (id, content, verse_id) VALUES (836, 'Meskipun Aku tak lahir dan tak berubah sifat-Nya, dan Tuhan semua makhluk, namun dengan menggunakan Sifat-Ku sendiri (Prakrti), Aku lahir dari kehendak-Ku sendiri.', 168);
INSERT INTO verse_translations (id, content, verse_id) VALUES (837, 'Meskipun Aku tak lahir dan tak lapuk secara alamiah, dan Tuhan makhluk hidup, namun dengan menundukkan Prakriti-Ku, Aku lahir melalui Maya-Ku sendiri.', 168);
INSERT INTO verse_translations (id, content, verse_id) VALUES (841, 'Kapan pun ada kemerosotan Dharma, wahai Arjuna, dan kebangkitan Adharma, maka Aku menjelma.', 169);
INSERT INTO verse_translations (id, content, verse_id) VALUES (842, 'Wahai keturunan dinasti Bharata, kapan pun ada kemerosotan kebajikan dan peningkatan kejahatan, maka Aku menampakkan Diri.', 169);
INSERT INTO verse_translations (id, content, verse_id) VALUES (846, 'Demi perlindungan orang baik dan penghancuran orang jahat, demi penegakan Dharma, Aku lahir dari zaman ke zaman.', 170);
INSERT INTO verse_translations (id, content, verse_id) VALUES (847, 'Demi perlindungan orang-orang saleh, penghancuran para pelaku kejahatan, dan penegakan kebajikan, Aku menampakkan diri dalam setiap zaman.', 170);
INSERT INTO verse_translations (id, content, verse_id) VALUES (851, 'Dia yang demikian mengetahui secara benar kelahiran dan tindakan ilahi-Ku tidak akan lahir kembali setelah meninggalkan tubuh; dia akan datang kepada-Ku, wahai Arjuna.', 171);
INSERT INTO verse_translations (id, content, verse_id) VALUES (852, 'Dia yang demikian mengetahui secara benar kelahiran dan tindakan ilahi-Ku tidak akan lahir kembali setelah melepaskan tubuh. Dia mencapai-Ku, wahai Arjuna.', 171);
INSERT INTO verse_translations (id, content, verse_id) VALUES (856, 'Bebas dari keinginan, ketakutan, dan kemarahan, tenggelam dalam Aku, bergantung pada Aku, dimurnikan oleh laku tapa pengetahuan, banyak yang telah mencapai keadaan-Ku.', 172);
INSERT INTO verse_translations (id, content, verse_id) VALUES (857, 'Banyak yang tanpa keterikatan, ketakutan, dan kemarahan, yang tenggelam dalam-Ku, yang telah berlindung kepada-Ku, dan dimurnikan oleh tapa pengetahuan, telah mencapai keadaan-Ku.', 172);
INSERT INTO verse_translations (id, content, verse_id) VALUES (861, 'Barang siapa yang berlindung kepada-Ku dalam bentuk apa pun, Aku akan berpihak kepadanya dengan cara yang sama; manusia mengalami Aku sendiri dengan cara yang berbeda-beda, wahai Arjuna.', 173);
INSERT INTO verse_translations (id, content, verse_id) VALUES (862, 'Sesuai dengan cara mereka mendekati-Ku, Aku memberikan mereka pertolongan dengan cara yang sama. Wahai putra Partha, manusia mengikuti jalan-Ku dalam segala hal.', 173);
INSERT INTO verse_translations (id, content, verse_id) VALUES (866, 'Mereka yang menginginkan buah dari tindakan ritual mereka, berkorban kepada para dewa di sini; karena, kesuksesan dengan cepat datang di dunia manusia.', 174);
INSERT INTO verse_translations (id, content, verse_id) VALUES (867, 'Merindukan hasil dari tindakan mereka (ritus dan kewajiban mereka), mereka menyembah para dewa di sini. Karena, di dunia manusia, keberhasilan dari tindakan datang dengan cepat.', 174);
INSERT INTO verse_translations (id, content, verse_id) VALUES (871, 'Sistem empat kasta diciptakan oleh-Ku, sesuai dengan perbedaan Guna dan Karma. Meskipun Aku adalah penciptanya, ketahuilah Aku sebagai non-agen dan tak berubah.', 175);
INSERT INTO verse_translations (id, content, verse_id) VALUES (872, 'Empat kasta telah diciptakan oleh-Ku melalui klasifikasi guna dan kewajiban. Meskipun Aku adalah agen dari (tindakan klasifikasi) itu, tetap ketahuilah Aku sebagai non-agen dan tak berubah.', 175);
INSERT INTO verse_translations (id, content, verse_id) VALUES (876, 'Perbuatan tidak dapat mencemari Aku. Tidak ada keinginan akan buah tindakan dalam Aku. Dia yang memahami ini tidak terikat oleh tindakan.', 176);
INSERT INTO verse_translations (id, content, verse_id) VALUES (877, 'Tindakan tidak mencemari-Ku; karena Aku tidak mendambakan hasil tindakan. Seseorang yang mengenal-Ku demikian, tidak terikat oleh tindakan mereka.', 176);
INSERT INTO verse_translations (id, content, verse_id) VALUES (881, 'Mengetahui demikian, bahkan para pencari pembebasan kuno melakukan pekerjaan mereka. Oleh karena itu, lakukanlah pekerjaanmu sebagaimana yang dilakukan orang-orang kuno di masa lampau.', 177);
INSERT INTO verse_translations (id, content, verse_id) VALUES (882, 'Demikianlah setelah mengetahui, bahkan para pencari pembebasan kuno melakukan tugas-tugas mereka. Oleh karena itu, Anda harus melakukan tindakan seperti yang dilakukan sebelumnya oleh orang-orang kuno.', 177);
INSERT INTO verse_translations (id, content, verse_id) VALUES (886, 'Apa itu tindakan? Apa itu bukan tindakan? Bahkan orang bijak pun bingung mengenai hal ini. Aku akan menyatakan kepadamu jenis tindakan itu, dengan mengetahui itu engkau akan terbebas dari kejahatan.', 178);
INSERT INTO verse_translations (id, content, verse_id) VALUES (887, 'Bahkan orang yang cerdas pun bingung mengenai apa itu tindakan dan apa itu kelambanan. Aku akan memberitahukan kepadamu tentang tindakan itu, dengan mengetahui itu Anda akan terbebas dari kejahatan.', 178);
INSERT INTO verse_translations (id, content, verse_id) VALUES (891, 'Karena, ada yang harus diketahui dalam tindakan; demikian pula, ada yang harus diketahui dalam tindakan beraneka ragam; dan ada yang harus dipahami dalam tidak bertindak. Demikianlah, misteriusnya jalan tindakan.', 179);
INSERT INTO verse_translations (id, content, verse_id) VALUES (892, 'Karena ada sesuatu yang harus diketahui bahkan tentang tindakan, dan sesuatu yang harus diketahui tentang tindakan terlarang; dan sesuatu yang perlu diketahui tentang kelambanan. Sifat sebenarnya dari tindakan tidak dapat dipahami.', 179);
INSERT INTO verse_translations (id, content, verse_id) VALUES (896, 'Dia yang melihat kelambanan dalam tindakan dan juga tindakan dalam kelambanan adalah bijaksana di antara manusia. Dia layak untuk dibebaskan dan telah menyelesaikan semua tindakan.', 180);
INSERT INTO verse_translations (id, content, verse_id) VALUES (897, 'Dia yang menemukan kelambanan dalam tindakan, dan tindakan dalam kelambanan, adalah yang bijaksana [memiliki pengetahuan Brahman] di antara manusia; mereka terlibat dalam yoga dan adalah pelaku semua tindakan!', 180);
INSERT INTO verse_translations (id, content, verse_id) VALUES (901, 'Dia yang setiap usahanya bebas dari keinginan dan identifikasi ilusi (tubuh dengan diri), yang tindakannya terbakar dalam api pengetahuan—orang bijak menggambarkannya sebagai seorang bijak.', 181);
INSERT INTO verse_translations (id, content, verse_id) VALUES (902, 'Orang bijak menyebutnya terpelajar yang semua tindakannya tanpa keinginan dan pikiran, dan yang tindakannya telah terbakar habis oleh api kebijaksanaan.', 181);
INSERT INTO verse_translations (id, content, verse_id) VALUES (906, 'Setelah melepaskan keterikatan pada buah dari tindakan mereka, senantiasa puas dengan diri yang abadi, dan tidak bergantung pada siapa pun, seseorang tidak bertindak sama sekali, bahkan saat sedang bertindak.', 182);
INSERT INTO verse_translations (id, content, verse_id) VALUES (907, 'Setelah melepaskan keterikatan pada hasil tindakan, dia yang selalu puas, tidak bergantung pada apa pun, tidak benar-benar melakukan apa pun meskipun dia terlibat dalam tindakan.', 182);
INSERT INTO verse_translations (id, content, verse_id) VALUES (911, 'Bebas dari keinginan, intelek dan pikirannya terkendali, setelah menyerahkan semua kepemilikan, dan hanya melakukan pekerjaan fisik, dia tidak terjerat kejahatan.', 183);
INSERT INTO verse_translations (id, content, verse_id) VALUES (912, 'Seseorang yang tanpa bujukan, yang memiliki pikiran dan indra di bawah kendali, dan sama sekali tanpa kepemilikan, tidak menanggung dosa dengan melakukan tindakan hanya untuk pemeliharaan tubuh.', 183);
INSERT INTO verse_translations (id, content, verse_id) VALUES (916, 'Puas dengan apa yang mungkin datang secara kebetulan, naik di atas pasangan yang berlawanan, bebas dari niat buruk, seimbang dalam keberhasilan dan kegagalan, dia bertindak, namun tidak terikat.', 184);
INSERT INTO verse_translations (id, content, verse_id) VALUES (917, 'Tetap puas dengan apa yang datang tanpa diminta, setelah melampaui dualitas, bebas dari dendam, dan seimbang dalam keberhasilan dan kegagalan, ia tidak terikat bahkan dengan melakukan tindakan.', 184);
INSERT INTO verse_translations (id, content, verse_id) VALUES (921, 'Dia yang keterikatannya telah dilepaskan, yang dibebaskan, yang pikirannya tertanam dalam pengetahuan, dan yang bekerja hanya untuk pengorbanan—Karma-nya sepenuhnya larut.', 185);
INSERT INTO verse_translations (id, content, verse_id) VALUES (922, 'Orang yang terbebas, yang telah menyingkirkan keterikatan dan yang pikirannya tertanam dalam pengetahuan, tindakan mereka yang dilakukan untuk pengorbanan benar-benar hancur.', 185);
INSERT INTO verse_translations (id, content, verse_id) VALUES (926, 'Brahman adalah alat untuk mempersembahkan; Brahman adalah persembahan. Oleh Brahman persembahan dipersembahkan ke dalam api Brahman; Brahman saja yang harus dicapai oleh orang yang bermeditasi pada-Nya dalam pekerjaan mereka.', 186);
INSERT INTO verse_translations (id, content, verse_id) VALUES (927, 'Sendok adalah Brahman, persembahan adalah Brahman, dan persembahan dituangkan oleh Brahman ke dalam api Brahman. Dia yang berkonsentrasi pada Brahman sebagai tujuan hanya dapat mencapai Brahman.', 186);
INSERT INTO verse_translations (id, content, verse_id) VALUES (931, 'Beberapa yogi hanya melakukan pengorbanan yang berkaitan dengan dewa; yang lain mempersembahkan pengorbanan ke dalam api Brahman semata-mata melalui pengorbanan.', 187);
INSERT INTO verse_translations (id, content, verse_id) VALUES (932, 'Para yogi lainnya melakukan pengorbanan hanya kepada para dewa, sementara yang lain mempersembahkan Diri sebagai pengorbanan oleh diri mereka sendiri, di dalam api Brahman.', 187);
INSERT INTO verse_translations (id, content, verse_id) VALUES (936, 'Yang lain mempersembahkan pendengaran dan indra lainnya sebagai persembahan ke dalam api pengendalian. Beberapa yang lain mempersembahkan objek indra, seperti suara dan yang lainnya, ke dalam api indra mereka.', 188);
INSERT INTO verse_translations (id, content, verse_id) VALUES (937, 'Yang lain mempersembahkan organ-organ mereka, seperti telinga mereka, dll., di dalam api pengendalian diri. Yang lain mempersembahkan objek, seperti suara, dll., di dalam api organ-organ mereka.', 188);
INSERT INTO verse_translations (id, content, verse_id) VALUES (941, 'Beberapa lagi mempersembahkan sebagai persembahan fungsi-fungsi indra dan aktivitas napas vital ke dalam api Yoga pengendalian pikiran, yang dinyalakan oleh pengetahuan.', 189);
INSERT INTO verse_translations (id, content, verse_id) VALUES (942, 'Yang lain mempersembahkan semua aktivitas organ dan aktivitas kekuatan vital ke dalam api yoga pengendalian diri, yang dinyalakan oleh Pengetahuan.', 189);
INSERT INTO verse_translations (id, content, verse_id) VALUES (946, 'Menguasai diri dan teguh dalam tekad, yang lain melakukan pengorbanan benda material atau laku tapa atau yoga; sementara yang lain mempersembahkan studi kitab suci dan pengetahuan mereka.', 190);
INSERT INTO verse_translations (id, content, verse_id) VALUES (947, 'Demikian pula, yang lain adalah pelaku pengorbanan melalui kekayaan, melalui pertapaan, melalui yoga, dan melalui studi dan pengetahuan; yang lain adalah pertapa dengan sumpah yang keras.', 190);
INSERT INTO verse_translations (id, content, verse_id) VALUES (951, 'Yang lain, dengan diet terbatas, mengabdikan diri pada pengendalian napas. Beberapa mengorbankan napas ke dalam napas keluar; demikian pula, yang lain mengorbankan napas keluar ke dalam napas masuk. Beberapa yang lain, menghentikan aliran napas masuk dan keluar, mengorbankan napas masuk dan keluar.', 191);
INSERT INTO verse_translations (id, content, verse_id) VALUES (952, 'Terus-menerus mempraktikkan pengendalian kekuatan vital dengan menghentikan gerakan napas keluar dan masuk, beberapa mempersembahkan napas keluar ke dalam napas masuk sebagai pengorbanan; sementara yang lain, napas masuk ke dalam napas keluar.', 191);
INSERT INTO verse_translations (id, content, verse_id) VALUES (956, 'Semua ini mengetahui arti pengorbanan, dan melalui pengorbanan, dosa-dosa mereka terhapus. Mereka yang hidup dari makanan ambrosia, sisa-sisa pengorbanan, pergi ke Brahman yang abadi.', 192);
INSERT INTO verse_translations (id, content, verse_id) VALUES (957, 'Yang lain, setelah mengatur makanan mereka, mempersembahkan kekuatan vital dalam kekuatan vital. Semuanya adalah orang yang mengetahui pengorbanan dan dosa-dosa mereka dihancurkan oleh pengorbanan.', 192);
INSERT INTO verse_translations (id, content, verse_id) VALUES (961, 'Dunia ini bukan untuk orang yang tidak berkorban; lalu bagaimana dengan yang lain, wahai Arjuna?', 193);
INSERT INTO verse_translations (id, content, verse_id) VALUES (962, 'Mereka yang menikmati nektar yang tersisa setelah pengorbanan mencapai Brahman yang abadi. Dunia ini tidak ada lagi bagi orang yang tidak melakukan pengorbanan; lalu, bagaimana dengan dunia lain, wahai yang terbaik di antara Kurawa (Arjuna)!', 193);
INSERT INTO verse_translations (id, content, verse_id) VALUES (966, 'Demikianlah, banyak bentuk pengorbanan telah disebarkan sebagai sarana untuk mencapai Brahman (diri individual dalam sifatnya sendiri). Ketahuilah bahwa semua ini lahir dari tindakan. Mengetahui demikian, engkau akan bebas.', 194);
INSERT INTO verse_translations (id, content, verse_id) VALUES (967, 'Demikianlah, berbagai jenis pengorbanan tersebar di mulut Veda. Ketahuilah semuanya berasal dari tindakan. Mengetahui ini, Anda akan dibebaskan.', 194);
INSERT INTO verse_translations (id, content, verse_id) VALUES (971, 'Pengorbanan pengetahuan lebih unggul daripada pengorbanan material. Wahai Arjuna, semua tindakan dan segala sesuatu yang lain berujung pada pengetahuan.', 195);
INSERT INTO verse_translations (id, content, verse_id) VALUES (972, 'Wahai penghancur musuh, pengetahuan yang dianggap sebagai pengorbanan lebih besar daripada pengorbanan yang membutuhkan materi. Wahai putra Prtha, semua tindakan secara keseluruhan berpuncak pada pengetahuan.', 195);
INSERT INTO verse_translations (id, content, verse_id) VALUES (976, 'Ketahuilah ini melalui sujud, bertanya, dan pelayanan. Orang bijak yang telah menyadari kebenaran akan mengajarimu pengetahuan.', 196);
INSERT INTO verse_translations (id, content, verse_id) VALUES (977, 'Ketahuilah bahwa para bijak yang telah menyadari Kebenaran akan memberikan Pengetahuan kepada Anda melalui sujud, penyelidikan, dan pelayanan.', 196);
INSERT INTO verse_translations (id, content, verse_id) VALUES (981, 'Mengetahui ini, wahai Arjuna, engkau tidak akan jatuh lagi ke dalam delusi dengan cara ini—dengan pengetahuan itu, engkau akan melihat semua makhluk tanpa kecuali dalam dirimu sendiri dan kemudian dalam Aku.', 197);
INSERT INTO verse_translations (id, content, verse_id) VALUES (982, 'Mengetahui ini, wahai Pandawa, Anda tidak akan tertipu lagi dengan cara ini, dan melaluinya, Anda akan melihat semua makhluk, tanpa kecuali, dalam Diri dan juga dalam-Ku.', 197);
INSERT INTO verse_translations (id, content, verse_id) VALUES (986, 'Bahkan jika engkau adalah yang paling berdosa di antara semua orang berdosa, engkau dapat melintasi semua dosa hanya dengan perahu pengetahuan.', 198);
INSERT INTO verse_translations (id, content, verse_id) VALUES (987, 'Bahkan jika Anda adalah pendosa terburuk di antara semua pendosa, Anda masih bisa mengatasi semua kejahatan hanya dengan rakit Pengetahuan.', 198);
INSERT INTO verse_translations (id, content, verse_id) VALUES (991, 'Sama seperti api yang membakar mengubah bahan bakar menjadi abu, wahai Arjuna, demikian pula api pengetahuan mengubah semua karma menjadi abu.', 199);
INSERT INTO verse_translations (id, content, verse_id) VALUES (992, 'Wahai Arjuna, sama seperti api yang menyala mengurangi kayu menjadi abu, demikian pula api Pengetahuan mengurangi semua tindakan menjadi abu.', 199);
INSERT INTO verse_translations (id, content, verse_id) VALUES (996, 'Karena tidak ada pemurni yang setara dengan pengetahuan; dia yang disempurnakan dalam Karma Yoga menemukan pengetahuan ini dengan sendirinya di dalam dirinya pada waktunya.', 200);
INSERT INTO verse_translations (id, content, verse_id) VALUES (997, 'Memang, tidak ada yang memurnikan di sini sebanding dengan Pengetahuan. Seseorang yang telah menjadi sempurna seiring waktu melalui yoga, menyadari Itu dalam hatinya sendiri.', 200);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1001, 'Dia yang memiliki iman, bertekad pada itu, dan telah menguasai indranya, mencapai pengetahuan. Setelah mencapai pengetahuan, dia dengan cepat pergi menuju kedamaian tertinggi.', 201);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1002, 'Orang yang memiliki iman, rajin, dan memiliki kendali atas organ-organnya, mencapai Pengetahuan. Dengan mencapai Pengetahuan, seseorang segera mencapai Kedamaian tertinggi.', 201);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1006, 'Orang bodoh, yang tidak beriman, dan yang ragu-ragu akan binasa; bagi yang ragu-ragu, tidak ada dunia ini maupun dunia di sana, pun tidak ada kebahagiaan.', 202);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1007, 'Orang yang bodoh dan tidak beriman, serta memiliki pikiran yang ragu-ragu, binasa. Baik dunia ini maupun dunia selanjutnya, pun kebahagiaan, tidak ada bagi orang yang memiliki pikiran yang ragu-ragu.', 202);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1011, 'Tindakan tidak mengikatnya, wahai Arjuna, dia yang telah melepaskannya melalui Karma Yoga dan yang keraguannya telah dihilangkan oleh pengetahuan, dan yang demikian memiliki pikiran yang mantap.', 203);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1012, 'Wahai Dhananjaya (Arjuna), tindakan tidak mengikat orang yang telah melepaskan tindakan melalui yoga, yang keraguannya telah sepenuhnya dihilangkan oleh Pengetahuan, dan yang tidak lalai.', 203);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1016, 'Oleh karena itu, potonglah, dengan pedang pengetahuan, keraguan yang ada di hatimu yang berasal dari ketidaktahuan mengenai Diri. Latih Yoga ini, wahai Arjuna, dan bangkitlah.', 204);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1017, 'Oleh karena itu, wahai keturunan dinasti Bharata, berlindunglah pada yoga dan bangkitlah, memotong keraguan di hatimu yang timbul dari ketidaktahuan dengan pedang pengetahuan.', 204);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1021, 'Arjuna berkata, “Wahai Krsna, Engkau memuji pelepasan tindakan, dan kemudian Engkau juga memuji Karma Yoga. Tolong beritahukanlah kepadaku dengan pasti mana di antara keduanya yang lebih unggul, yang mengarah pada kebaikan tertinggi.', 205);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1022, 'Arjuna berkata, “Wahai Krsna, Anda memuji pelepasan tindakan dan, pada saat yang sama, Karma-yoga. Tolong beritahukanlah kepada saya dengan pasti yang mana yang lebih baik di antara keduanya.”', 205);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1026, 'Sang Raja berkata, “Pelepasan tindakan dan Karma Yoga keduanya mengarah pada keunggulan tertinggi; namun, dari keduanya, Karma Yoga melampaui pelepasan tindakan.”', 206);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1027, 'Yang Mahamulia berkata, “Baik pelepasan tindakan maupun Karma-yoga mengarah pada pembebasan; namun, Karma-yoga lebih unggul daripada pelepasan tindakan.”', 206);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1031, 'Dia yang tidak membenci dan tidak berhasrat, dan berada di luar pasangan yang berlawanan, harus dipahami sebagai orang yang selalu melepaskan diri. Oleh karena itu, dia mudah terbebas dari ikatan, wahai Arjuna.', 207);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1032, 'Dia yang tidak membenci dan tidak mendambakan harus dikenal sebagai orang yang terus-menerus melakukan pelepasan.', 207);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1036, 'Anak-anak, bukan orang terpelajar, berbicara tentang Sankhya dan Yoga sebagai berbeda; namun, dia yang teguh dalam salah satunya, mencapai buah keduanya.', 208);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1037, 'Orang bodoh, bukan orang terpelajar, berbicara tentang Sankhya dan (Karma-)yoga sebagai berbeda. Siapa pun yang dengan benar menggunakan salah satunya akan mendapatkan hasil keduanya.', 208);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1041, 'Keadaan yang dicapai oleh para Sankhya, sama halnya juga dicapai oleh para Yogin; yaitu, keadaan yang sama dicapai oleh mereka yang adalah Karma Yogin. Dia sendirilah yang bijaksana yang memahami bahwa Sankhya dan Yoga adalah satu dan sama, karena keduanya memiliki hasil yang sama.', 209);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1042, 'Keadaan yang dicapai oleh para Sankhya, dan yang juga dicapai oleh para yogi, dilihat oleh orang yang melihat Sankhya dan yoga sebagai satu.', 209);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1046, 'Tetapi, wahai yang perkasa, pelepasan sulit dicapai tanpa mempraktikkan Yoga. Orang bijak yang merenung dan mengikuti Yoga segera mencapai Brahman (diri atau Atman).', 210);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1047, 'Tetapi, wahai yang perkasa, pelepasan sulit dicapai tanpa (Karma-)yoga. Orang yang bermeditasi yang dilengkapi dengan yoga mencapai Brahman tanpa penundaan.', 210);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1051, 'Dia yang mengikuti Yoga dan berhati murni, yang telah menundukkan dirinya dan telah menguasai indranya, dan yang dirinya telah menjadi diri semua makhluk, bahkan saat dia bertindak, tetap tidak tercemar.', 211);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1052, 'Dilengkapi dengan yoga, berhati murni, mengendalikan tubuh, penakluk organ, Diri dari diri semua makhluk—ia tidak menjadi tercemar bahkan saat melakukan tindakan. Ketika orang ini melakukan ritus dan kewajiban nitya dan naimittika sebagai sarana untuk mencapai pencerahan penuh, dan dengan demikian menjadi sepenuhnya tercerahkan, maka, bahkan ketika ia bertindak melalui fungsi-fungsi yang tampak dari pikiran, organ, dll., ia tidak terpengaruh.', 211);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1056, 'Orang yang mengetahui kebenaran dan mengabdikan diri pada yoga harus berpikir, "Saya tidak melakukan apa-apa," meskipun mereka melihat, mendengar, menyentuh, mencium, makan, bergerak, tidur, dan bernapas.', 212);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1057, '- Tetap tenggelam dalam Diri, sang pengenal Realitas harus berpikir, \'Saya tentu saja tidak melakukan apa-apa\', bahkan saat melihat, mendengar, menyentuh, mencium, makan, bergerak, tidur, bernapas, berbicara, melepaskan, memegang, membuka, dan menutup mata—mengingat bahwa organ-organ berfungsi dalam hubungannya dengan objek-objek organ.', 212);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1061, 'Berbicara, membuang, menggenggam, membuka, dan menutup mata, dll., ia harus selalu ingat bahwa indra beroperasi di antara objek-objek indra.', 213);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1062, '- Tetap tenggelam dalam Diri, sang pengenal Realitas harus berpikir, \'Saya tentu saja tidak melakukan apa-apa\', bahkan saat melihat, mendengar, menyentuh, mencium, makan, bergerak, tidur, bernapas, berbicara, melepaskan, memegang, membuka, dan menutup mata—mengingat bahwa organ-organ berfungsi dalam hubungannya dengan objek-objek organ.', 213);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1066, 'Dia yang bertindak tanpa keterikatan, menyerahkan semua tindakan pada Brahman (Prakṛti), tidak tersentuh kejahatan, sama seperti daun teratai tidak tersentuh air.', 214);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1067, 'Seseorang yang bertindak dengan mendedikasikan tindakan mereka kepada Brahman dan dengan melepaskan keterikatan, mereka tidak menjadi tercemar oleh dosa, sama seperti daun teratai tidak oleh air.', 214);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1071, 'Yogins, melepaskan keterikatan, melakukan tindakan hanya dengan tubuh, pikiran, intelek, dan indra, untuk pemurnian diri.', 215);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1072, 'Dengan melepaskan keterikatan, para yogi melakukan pekerjaan semata-mata melalui tubuh, pikiran, intelek, dan bahkan organ-organ, untuk pemurnian diri mereka.', 215);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1076, 'Seorang yogi, melepaskan buah dari tindakan mereka, mencapai kedamaian abadi. Tetapi orang yang tidak stabil yang terikat pada buah dari tindakan mereka, didorong oleh keinginan, terikat.', 216);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1077, 'Dengan melepaskan hasil kerja dengan menjadi teguh dalam iman, seseorang mencapai kedamaian yang timbul dari keteguhan. Orang yang kurang dalam iman yang teguh, karena terikat pada hasil di bawah dorongan keinginan, menjadi terikat.', 216);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1081, 'Diri yang berbadan, secara mental menyerahkan semua tindakan sebagai milik kota sembilan gerbang (yaitu, tubuh) dan menjadi mengendalikan diri, berdiam dengan bahagia, tidak bertindak sendiri dan tidak menyebabkan tubuh bertindak.', 217);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1082, 'Orang yang berbadan yang mengendalikan diri, setelah secara mental menyerahkan semua tindakan, melanjutkan hidup bahagia di kota sembilan gerbang, tanpa melakukan atau menyebabkan orang lain melakukan apa pun.', 217);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1086, 'Sang Raja tubuh (Diri, yaitu Jiva) tidak menciptakan keagenan, pun tindakan, pun persatuan dengan buah tindakan sehubungan dengan dunia diri; hanya kecenderungan bawaan yang berfungsi.', 218);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1087, 'Diri tidak menciptakan keagenan atau objek keinginan apa pun untuk siapa pun, pun tidak berhubungan dengan hasil tindakan; sebaliknya, Alamlah yang bertindak.', 218);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1091, 'Yang Mahakuasa tidak menghilangkan dosa maupun pahala siapa pun. Pengetahuan diselimuti oleh ketidaktahuan, dan makhluk-makhluk dengan demikian tertipu.', 219);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1092, 'Yang Mahahadir tidak menerima dosa siapa pun, pun tidak kebajikan; pengetahuan tetap tertutup oleh ketidaktahuan, sehingga makhluk-makhluk menjadi tertipu.', 219);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1096, 'Tetapi bagi mereka yang ketidaktahuan ini dihancurkan oleh pengetahuan Diri, pengetahuan itu, dalam kasus mereka, adalah yang tertinggi dan bersinar terang seperti matahari.', 220);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1097, 'Tetapi dalam kasus mereka yang ketidaktahuan mereka dihancurkan oleh pengetahuan (Diri), pengetahuan mereka, seperti matahari, mengungkapkan Realitas tertinggi itu.', 220);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1101, 'Mereka yang inteleknya mengejarnya, yang pikirannya memikirkannya, yang menjalani disiplin untuknya, dan yang menjadikannya sebagai objek tertinggi, dosa-dosa mereka dibersihkan oleh pengetahuan dan mereka pergi dari mana tidak ada kembali.', 221);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1102, 'Mereka yang inteleknya terserap dalam Itu, yang Diri mereka adalah Itu, yang teguh dalam Itu, dan yang memiliki Itu sebagai Tujuan tertinggi mereka—mereka mencapai keadaan tanpa kembali, ketidakmurnian mereka telah dihilangkan oleh Pengetahuan.', 221);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1106, 'Para bijak memandang dengan mata yang sama pada orang yang berilmu dan rendah hati, seorang Brahmana, seekor sapi, seekor gajah, seekor anjing, dan pemakan anjing.', 222);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1107, 'Para terpelajar memandang dengan ketenangan yang sama pada seorang Brahmana yang berilmu dan rendah hati, seekor sapi, seekor gajah, dan bahkan seekor anjing, serta orang yang memakan daging anjing.', 222);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1111, 'Di sini, mereka yang pikirannya berdiam dalam ketenangan mengatasi samsara. Karena Brahman (diri individual), ketika tidak tercemar oleh Prakriti, adalah sama di mana-mana; oleh karena itu, mereka berdiam dalam Brahman.', 223);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1112, 'Di sini juga kesenangan dicapai oleh mereka yang pikirannya tertanam dalam kesamaan. Karena Brahman sama dalam semua dan bebas dari cacat, mereka tertanam dalam Brahman.', 223);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1116, 'Dia yang mengenal Brahman (diri individual) dan berdiam dalam Brahman, yang pikirannya teguh terfokus pada diri dan tidak tertipu oleh kesadaran tubuh—dia tidak bersukacita saat mendapatkan yang menyenangkan pun tidak berduka saat mendapatkan yang tidak menyenangkan.', 224);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1117, 'Seorang yang mengetahui Brahman, yang tertanam dalam Brahman, harus memiliki intelek yang mantap dan tidak boleh tertipu. Mereka tidak boleh gembira dengan mendapatkan apa yang diinginkan, pun tidak menjadi sedih dengan mendapatkan apa yang tidak diinginkan.', 224);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1121, 'Dia yang pikirannya terlepas dari kontak eksternal dan menemukan kebahagiaan dalam Diri—dia memiliki pikirannya yang terlibat dalam kontemplasi Brahman dan menikmati kebahagiaan yang tak lapuk.', 225);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1122, 'Dengan hatinya yang tidak terikat pada objek-objek eksternal, ia mendapatkan kebahagiaan yang ada di dalam Diri. Dengan hatinya yang terserap dalam meditasi pada Brahman, ia memperoleh Kebahagiaan abadi.', 225);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1126, 'Karena kesenangan yang lahir dari kontak adalah rahim rasa sakit. Mereka memiliki awal dan akhir, wahai Arjuna; orang bijak tidak bersukacita di dalamnya.', 226);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1127, 'Karena kenikmatan yang dihasilkan dari kontak dengan objek memang merupakan sumber kesedihan, memiliki awal dan akhir, wahai putra Kunti, yang bijaksana tidak menikmati hal itu.', 226);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1131, 'Dia yang mampu, bahkan di sini, sebelum dilepaskan dari tubuh, untuk menanggung dorongan yang dihasilkan oleh keinginan dan kemarahan, dia adalah seorang Yogin (kompeten untuk realisasi diri); dia adalah orang yang bahagia.', 227);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1132, 'Seseorang yang dapat bertahan di sini, sebelum meninggalkan tubuh, dorongan yang timbul dari keinginan dan kemarahan, orang itu adalah seorang yogi; mereka bahagia.', 227);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1136, 'Dia yang kebahagiaannya ada di dalam, yang kesenangannya ada di dalam, dan yang cahayanya ada di dalam—dia adalah seorang yogin, yang, setelah menjadi Brahman, mencapai kebahagiaan Brahman.', 228);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1137, 'Seseorang yang puas di dalam, yang kesenangannya ada di dalam, dan yang cahayanya hanya ada di dalam, yogi itu, setelah menjadi Brahman, mencapai penyerapan ke dalam Brahman.', 228);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1141, 'Para bijak, yang bebas dari pasangan yang berlawanan, yang pikirannya terkendali dengan baik, dan yang mengabdikan diri pada kesejahteraan semua makhluk, dibersihkan dari segala kotoran dan mencapai kebahagiaan Brahman.', 229);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1142, 'Para peramal yang dosa-dosanya telah melemah, yang bebas dari keraguan, yang indranya terkendali, yang terlibat dalam perbuatan baik bagi semua makhluk, mencapai penyerapan dalam Brahman.', 229);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1146, 'Bagi mereka yang bebas dari keinginan dan kemarahan, yang terbiasa berusaha keras, yang pikirannya terkendali, dan yang telah menaklukkannya—kebahagiaan Brahman sudah dekat.', 230);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1147, 'Bagi para bhikkhu yang memiliki kendali atas organ internal mereka, yang bebas dari keinginan dan kemarahan, yang telah mengenal Diri, ada penyerapan ke dalam Brahman dengan cara apa pun.', 230);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1151, 'Mematikan kontak luar, memusatkan pandangan di antara alis, menyadari napas masuk dan keluar yang bergerak di lubang hidung;', 231);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1152, '- Menjaga objek eksternal di luar, memfokuskan mata pada pertemuan alis, dan mengendalikan napas keluar dan masuk yang bergerak melalui lubang hidung, sang kontemplatif yang menguasai organ, pikiran, dan inteleknya harus sepenuhnya berniat untuk Pembebasan dan bebas dari keinginan, ketakutan, dan kemarahan. Dia yang selalu demikian benar-benar bebas.', 231);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1156, 'Sang bijak yang telah mengendalikan indra, pikiran, dan inteleknya, yang bertekad pada pembebasan sebagai tujuan akhirnya, bebas selamanya dari keinginan, ketakutan, dan kemarahan, sungguh dibebaskan selamanya.', 232);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1157, '- Menjaga objek eksternal di luar, memfokuskan mata pada pertemuan alis, dan mengendalikan napas keluar dan masuk yang bergerak melalui lubang hidung, sang kontemplatif yang menguasai organ, pikiran, dan inteleknya harus sepenuhnya berniat untuk Pembebasan dan bebas dari keinginan, ketakutan, dan kemarahan. Dia yang selalu demikian benar-benar bebas.', 232);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1161, 'Mengetahui Aku sebagai penikmat semua pengorbanan dan pertapaan, sebagai Penguasa Agung semua dunia, sebagai Sahabat setiap makhluk, dia mencapai kedamaian.', 233);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1162, 'Seseorang mencapai kedamaian dengan mengenal Aku, yang adalah Penguasa agung semua dunia, penikmat pengorbanan dan pertapaan, dan teman semua makhluk.', 233);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1166, 'Sang Raja berkata, “Dia yang melakukan pekerjaan yang seharusnya dilakukan tanpa mencari buahnya—dia adalah sannyasin dan yogin, dan bukan dia yang tidak memelihara api suci dan tidak melakukan tindakan.”', 234);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1167, 'Yang Mahamulia berkata, “Dia yang melakukan tindakan yang merupakan tugasnya, tanpa bergantung pada hasil tindakan, adalah seorang bhikkhu dan seorang yogi; tidak demikian halnya dengan dia yang tidak menjaga api dan tidak bertindak.”', 234);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1171, 'Apa yang disebut Sannyasa (Jnana Yoga), ketahuilah itu sebagai Yoga (Karma Yoga), wahai Arjuna. Karena tidak seorang pun, di antara Karma Yogin, yang identifikasi ilusinya antara tubuh dengan diri belum ditinggalkan, menjadi Karma Yogin sejati.', 235);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1172, 'Apa yang mereka sebut monastisisme, ketahuilah itu sebagai Yoga, wahai Pandawa, karena tidak seorang pun yang belum melepaskan harapan dapat menjadi seorang yogi.', 235);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1176, 'Tindakan dikatakan sebagai sarana bagi sang bijak yang ingin mendaki ketinggian Yoga; tetapi ketika ia telah mendaki ketinggian Yoga, ketenangan dikatakan sebagai sarana.', 236);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1177, 'Bagi sang bijak yang ingin naik ke (Dhyana-)yoga, tindakan dikatakan sebagai sarana. Bagi orang itu, setelah ia naik ke (Dhyana-)yoga, ketidakaktifan sendirilah yang dikatakan sebagai sarana.', 236);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1181, 'Karena, ketika seseorang kehilangan keterikatan pada hal-hal indrawi dan pada tindakan, maka mereka telah meninggalkan semua keinginan dan dikatakan telah mendaki ketinggian Yoga.', 237);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1182, 'Sesungguhnya, ketika seseorang yang telah melepaskan pikiran tentang segala sesuatu tidak terikat pada objek indra atau tindakan, ia kemudian dikatakan mantap dalam Yoga.', 237);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1186, 'Seseorang harus mengangkat dirinya sendiri dengan pikirannya sendiri dan tidak membiarkan dirinya tenggelam; karena pikiran sendirilah teman diri, dan pikiran sendirilah musuh diri.', 238);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1187, 'Seseorang harus menyelamatkan diri sendiri; seseorang tidak boleh merendahkan diri sendiri. Karena diri sendiri memang teman sendiri; diri sendiri memang musuh sendiri.', 238);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1191, 'Pikiran adalah teman bagi dia yang telah menaklukkannya. Tetapi bagi dia yang pikirannya tidak tertaklukkan, pikiran, seperti musuh, tetap memusuhi.', 239);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1192, 'Dia yang telah menaklukkan dirinya sendiri dengan dirinya, dirinya adalah temannya sendiri. Tetapi bagi orang yang belum menaklukkan dirinya sendiri, dirinya sendiri bertindak seperti musuh.', 239);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1196, 'Dari dia yang pikirannya tertaklukkan dan yang tenang, diri yang besar itu aman dalam panas dan dingin, dalam kesenangan dan rasa sakit, dan dalam kehormatan dan kehinaan.', 240);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1197, 'Diri tertinggi seseorang yang menguasai seluruh tubuh dan organ-organnya, dan yang tenang, menjadi termanifestasi. Dia harus seimbang di tengah dingin dan panas, kebahagiaan dan kesedihan, serta kehormatan dan kehinaan.', 240);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1201, 'Yogin yang pikirannya puas dengan pengetahuan diri dan juga dengan pengetahuan tentang perbedaan antara diri dan Prakrti, yang tertanam dalam diri, yang indranya terkendali, dan yang baginya bumi, batu, dan emas semuanya tampak sama, disebut terintegrasi.', 241);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1202, 'Seseorang yang pikirannya puas dengan pengetahuan dan realisasi, yang tidak tergoyahkan, yang organ-organnya terkendali, dikatakan tenggelam dalam Diri. Yogi memperlakukan semua sama, segumpal tanah, sebuah batu, dan emas.', 241);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1206, 'Dia yang memandang dengan mata yang sama orang-orang yang berkeinginan baik, teman-teman, musuh, orang yang acuh tak acuh, netral, yang membenci, kerabat, dan bahkan orang baik dan orang berdosa—dia unggul.', 242);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1207, 'Dia unggul yang memiliki pandangan yang sama terhadap seorang dermawan, seorang teman, seorang musuh, seorang netral, seorang penengah, orang yang membenci, seorang kerabat, orang baik, dan bahkan orang berdosa.', 242);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1211, 'Yogin harus senantiasa memusatkan pikirannya pada yoga, tetap berada di tempat yang sunyi sendirian, mengendalikan pikiran dan pikirannya, bebas dari keinginan dan rasa kepemilikan.', 243);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1212, 'Seorang yogi harus terus-menerus memusatkan pikirannya dengan tinggal di tempat yang sunyi, sendirian, dengan pikiran dan tubuh terkendali, bebas dari harapan, dan bebas dari perolehan.', 243);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1216, 'Setelah menetapkan bagi dirinya sendiri, di tempat yang bersih, tempat duduk yang kokoh yang tidak terlalu tinggi dan tidak terlalu rendah, dan menutupinya dengan kain, kulit rusa, dan rumput Kusa dalam urutan terbalik—', 244);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1217, 'Setelah dengan kokoh mendirikan di tempat yang bersih tempat duduknya, tidak terlalu tinggi atau terlalu rendah, terdiri dari kain, kulit, dan rumput kusa yang diletakkan secara berurutan satu di bawah yang lain;', 244);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1221, 'Duduk di kursi, dengan pikiran terkonsentrasi, mengendalikan pikiran dan indra, seseorang harus mempraktikkan Yoga untuk pemurnian diri.', 245);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1222, 'Duduk di tempat duduk itu, ia harus memusatkan pikirannya untuk pemurnian organ internal, membuat pikiran terpusat pada satu titik dan menjaga tindakan pikiran dan indra di bawah kendali.', 245);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1226, 'Memegang batang tubuh, kepala, dan leher tegak, tak bergerak dan mantap, menatap ujung hidung dan tidak melihat ke arah mana pun;', 246);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1227, 'Memegang tubuh, kepala, dan leher tegak dan diam, tetap mantap, melihat ke ujung hidungnya sendiri dan tidak melihat ke sekeliling.', 246);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1231, 'Tenang dan tak kenal takut, teguh dalam ikrar selibat mereka, mengendalikan pikiran dan memusatkan pikiran pada-Ku, mereka harus duduk dalam Yoga, bertekad pada-Ku.', 247);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1232, 'Dia harus tetap duduk dengan pikiran yang tenang, bebas dari rasa takut, teguh dalam janji selibat, dan dengan pikiran yang terfokus pada-Ku dengan mengendalikannya melalui konsentrasi, menjadikan-Ku sebagai tujuan tertinggi.', 247);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1236, 'Dengan menerapkan pikirannya dengan cara ini, Yogin yang mengendalikan pikiran, senantiasa mencapai kedamaian yang merupakan puncak kebahagiaan dan yang berdiam dalam Diri-Ku.', 248);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1237, 'Dengan memusatkan pikiran demikian, selamanya, yogi yang berhati terkendali mencapai kedamaian yang berpuncak pada pembebasan dan berdiam dalam-Ku.', 248);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1241, 'Yoga bukan untuk dia yang makan berlebihan, pun bukan untuk dia yang berpuasa berlebihan; bukan untuk dia, wahai Arjuna, yang tidur terlalu banyak, pun bukan untuk dia yang begadang terlalu lama.', 249);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1242, 'Tetapi, wahai Arjuna, Yoga bukan untuk orang yang makan berlebihan, pun bukan untuk orang yang tidak makan sama sekali; pun bukan untuk orang yang terbiasa tidur terlalu lama, pun bukan untuk orang yang tetap terjaga.', 249);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1246, 'Yoga menjadi penghancur kesedihan bagi mereka yang sederhana dalam makanan dan rekreasi, sederhana dalam tindakan mereka, dan sederhana dalam tidur dan bangun mereka.', 250);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1247, 'Yoga menjadi penghancur kesedihan bagi orang yang makan dan gerakannya teratur, yang usahanya dalam bekerja moderat, dan yang tidur dan bangunnya moderat.', 250);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1251, 'Ketika pikiran yang tertunduk hanya berdiam pada Diri, maka, bebas dari segala kerinduan akan objek keinginan, seseorang dikatakan layak untuk Yoga.', 251);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1252, 'Seseorang yang telah bebas dari kerinduan akan semua objek yang diinginkan dikatakan tenggelam dalam Diri ketika pikiran yang terkendali hanya berdiam dalam Diri.', 251);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1256, 'Lampu tidak berkedip di tempat yang tidak berangin”—itulah perumpamaan yang digunakan untuk pikiran yogi yang tenang yang mempraktikkan yoga.', 252);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1257, 'Seperti lampu yang diletakkan di tempat tanpa angin tidak berkedip, demikianlah perumpamaan untuk yogi yang pikirannya terkendali dan yang terlibat dalam konsentrasi pada Diri.', 252);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1261, 'Di mana pikiran, yang dikendalikan melalui praktik Yoga, beristirahat, dan di mana seseorang melihat Diri dengan Diri dan senang hanya dengan Diri.', 253);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1262, 'Pada saat pikiran, yang terkendali melalui praktik Yoga, ditarik, dan ketika seseorang melihat Diri dengan diri dan tetap puas hanya dalam Diri;', 253);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1266, 'Di mana seseorang mengetahui kebahagiaan tak terbatas yang dapat dipahami oleh intelek tetapi di luar jangkauan indra, dan berdiam dalam kondisi itu, seseorang tidak menyimpang darinya.', 254);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1267, 'Ketika seseorang mengalami Kebahagiaan mutlak itu, yang dapat diintuisi oleh intelek dan yang melampaui indra, dan dengan demikian telah kokoh, orang ini pasti tidak menyimpang dari Realitas.', 254);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1271, 'Setelah memperolehnya, seseorang berpendapat bahwa tidak ada keuntungan yang lebih besar; dengan berdiam di dalamnya, seseorang tidak tergerak bahkan oleh kesedihan yang paling berat.', 255);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1272, 'Memperoleh yang tidak menganggap perolehan lain lebih unggul, dan yang, ketika telah mantap dalam hal itu, tidak terganggu bahkan oleh kesedihan besar;', 255);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1276, 'Ketahuilah pembebasan dari hubungan dengan kesedihan ini sebagai Yoga. Yoga ini harus dipraktikkan dengan tekad dan dengan pikiran yang bebas dari keputusasaan.', 256);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1277, 'Seseorang harus mengetahui bahwa pemutusan kontak dengan kesedihan adalah apa yang disebut Yoga. Yoga itu harus dipraktikkan dengan ketekunan dan dengan hati yang tidak tertekan.', 256);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1281, 'Meninggalkan sepenuhnya semua keinginan yang lahir dari kehendak dan menahan pikiran dari semua indra di semua sisi,', 257);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1282, 'Dengan benar-benar menghindari semua keinginan yang timbul dari pikiran dan mengendalikan semua organ dengan pikiran dari setiap sisi;', 257);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1286, 'Sedikit demi sedikit, seseorang harus menarik diri dari objek selain diri, dengan bantuan intelek yang dipegang oleh tekad yang kuat; dan kemudian seseorang tidak boleh memikirkan hal lain, setelah memusatkan pikiran pada Diri.', 258);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1287, 'Seseorang harus secara bertahap mundur dengan intelek, yang diberkahi dengan keteguhan. Setelah membuat pikiran terfokus pada Diri, seseorang tidak boleh memikirkan apa pun sama sekali.', 258);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1291, 'Ke mana pun pikiran yang berubah-ubah dan tidak stabil berkelana, seseorang harus menundukkannya dan kemudian mengembalikannya di bawah kendali diri sendiri.', 259);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1292, 'Yogi harus menundukkan pikiran ini di bawah kendali Diri itu sendiri, menahannya dari semua penyebab yang menyebabkan pikiran yang gelisah dan tidak stabil berkelana.', 259);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1296, 'Karena kebahagiaan tertinggi datang kepada Yogin yang pikirannya damai, yang bebas dari kejahatan, yang dari padanya Rajas telah pergi, dan yang telah menjadi Brahman.', 260);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1297, 'Kebahagiaan tertinggi datang hanya kepada yogi ini yang pikirannya telah menjadi sangat tenang, yang kualitas rajasnya telah dihilangkan, yang telah menjadi identik dengan Brahman, dan tidak tercemar.', 260);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1301, 'Demikianlah, dengan mengabdikan diri pada Yoga Diri, bebas dari kotoran, yogin dengan mudah mencapai kebahagiaan tertinggi kontak dengan Brahman.', 261);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1302, 'Dengan terus-menerus memusatkan pikirannya dengan cara ini, yogi yang tanpa cela dengan mudah mencapai kebahagiaan mutlak dari kontak dengan Brahman.', 261);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1306, 'Dia yang pikirannya tertanam dalam Yoga melihat kenyataan di mana-mana; dia melihat Diri-Nya berdiam dalam semua makhluk dan semua makhluk dalam Diri-Nya.', 262);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1307, 'Seseorang yang pikirannya tenggelam dalam Diri melalui Yoga, dan yang memiliki visi kesamaan di mana-mana, melihat Diri ini ada dalam segala sesuatu, dan segala sesuatu dalam Diri mereka.', 262);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1311, 'Bagi dia yang melihat Aku dalam setiap diri dan setiap diri dalam Aku—Aku tidak hilang darinya, pun dia tidak hilang dari Aku.', 263);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1312, 'Seseorang yang melihat Aku dalam segala sesuatu dan melihat segala sesuatu dalam Aku—Aku tidak kehilangan dia dari pandangan, dan dia juga tidak hilang dari pandangan-Ku.', 263);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1316, 'Yogi yang, dengan teguh dalam kesatuan, menyembah Aku yang berdiam dalam semua makhluk—ia berdiam dalam Aku, bagaimanapun ia hidup.', 264);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1317, 'Yogi itu, yang, dengan teguh dalam kesatuan, memuja-Ku yang ada dalam segala sesuatu, ada dalam-Ku—dalam kondisi apa pun dia berada.', 264);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1321, 'Dia yang, karena kesamaan diri di mana-mana, melihat kesenangan atau rasa sakit sama di mana-mana—yogi itu, wahai Arjuna, dianggap yang terdekat.', 265);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1322, 'Wahai Arjuna, yogi itu dianggap yang terbaik yang menilai apa itu kebahagiaan dan kesedihan di semua makhluk dengan standar yang sama seperti yang akan mereka terapkan pada diri mereka sendiri.', 265);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1326, 'Arjuna berkata, “Wahai Krsna, Yoga realitas ini, yang telah Engkau nyatakan, aku tidak melihat bagaimana ia bisa stabil karena ketidakstabilan pikiran.”', 266);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1327, 'Arjuna berkata, “Wahai Madhusudana (Krsna), Yoga yang telah Anda bicarakan sebagai kesamaan ini, saya tidak melihat kelanjutannya yang stabil karena kegelisahan pikiran.”', 266);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1331, 'Karena pikiran itu berubah-ubah, wahai Krsna, tak terkendali, kuat, dan keras kepala; aku pikir mengendalikannya sama sulitnya dengan mengendalikan angin.', 267);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1332, 'Karena, wahai Kṛṣṇa, pikiran itu tidak stabil, bergejolak, kuat, dan keras kepala; saya menganggap pengendalinya sama sulitnya dengan mengendalikan angin.', 267);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1336, 'Sang Raja berkata, “Pikiran memang sulit ditundukkan dan berubah-ubah, wahai yang perkasa, tetapi, wahai putra Kunti, ia dapat dikendalikan melalui latihan dan pelaksanaan tanpa nafsu.”', 268);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1337, 'Yang Mahamulia berkata, wahai yang perkasa, tidak diragukan lagi pikiran itu tidak dapat dijinakkan dan gelisah. Tetapi, wahai putra Kunti, ia dapat dikendalikan melalui latihan dan pelepasan.', 268);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1341, 'Menurutku, Yoga sulit dicapai oleh orang yang pikirannya tidak terkendali; namun, ia dapat dicapai melalui cara yang benar oleh orang yang berusaha keras dan memiliki pikiran yang tertunduk.', 269);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1342, 'Keyakinan saya adalah bahwa Yoga sulit dicapai bagi seseorang yang memiliki pikiran yang tidak terkendali. Tetapi mungkin untuk mencapainya melalui cara-cara yang disebutkan di atas bagi seseorang yang berusaha dan memiliki pikiran yang terkendali.', 269);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1346, 'Arjuna berkata, “Jika seseorang yang memiliki iman tetapi hanya berupaya tidak memadai mendapati pikirannya berkelana dari Yoga, dan kemudian gagal mencapai kesempurnaan—ke mana dia pergi, wahai Krishna?”', 270);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1347, 'Arjuna berkata, “Wahai Krsna, tujuan apa yang dicapai seseorang yang, meskipun memiliki iman, gagal mencapai kesempurnaan dalam Yoga karena kurangnya ketekunan, dan yang pikirannya menyimpang dari Yoga?”', 270);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1351, 'Tanpa dukungan apa pun, bingung di jalan menuju Brahman, dan dengan demikian telah jatuh dari keduanya, apakah ia tidak binasa, wahai yang berlengan perkasa, seperti awan yang robek?', 271);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1352, 'Wahai yang berlengan perkasa, setelah jatuh dari kedua [jalan], tanpa dukungan, tertipu di jalan menuju Brahman, apakah ia tidak hancur seperti awan yang berserakan?', 271);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1356, 'Wahai Krsna, engkau harus sepenuhnya menghilangkan keraguanku ini, karena tidak ada yang lain yang dapat menghilangkan keraguan ini selain engkau.', 272);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1357, 'Wahai Krsna, Anda harus sepenuhnya menghilangkan keraguan saya ini, karena tidak ada yang lain selain diri Anda yang dapat menghilangkan keraguan ini!', 272);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1361, 'Sang Raja berkata, “Baik di dunia ini maupun di akhirat tidak ada kehancuran baginya. Karena tidak seorang pun yang berbuat baik pernah berakhir dengan buruk.”', 273);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1362, 'Yang Mahamulia berkata, “Wahai Partha, tentu saja tidak ada kehancuran baginya di sini atau di akhirat, karena tidak seorang pun yang berbuat baik mengalami akhir yang menyedihkan, putraku!”', 273);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1366, 'Dia yang telah jatuh dari Yoga lahir kembali di rumah orang-orang yang murni dan makmur, setelah mencapai dunia para pelaku perbuatan baik dan berdiam di sana selama bertahun-tahun.', 274);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1367, 'Mencapai dunia orang-orang saleh dan berdiam di sana selama bertahun-tahun, orang yang telah jatuh dari Yoga lahir di rumah orang-orang saleh dan makmur.', 274);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1371, 'Atau ia lahir dalam keluarga yogi yang bijaksana. Tetapi kelahiran semacam itu di dunia ini jarang terjadi.', 275);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1372, 'Atau, ia lahir dalam keluarga yogi yang bijaksana. Kelahiran seperti itu memang lebih sulit didapat di dunia.', 275);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1376, 'Di sana, wahai Arjuna, ia mendapatkan kembali watak pikiran yang ia miliki di tubuh sebelumnya, dan dari sana ia berusaha lebih keras lagi untuk mencapai keberhasilan dalam Yoga.', 276);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1377, 'Di sana ia diberkahi dengan kebijaksanaan yang diperoleh dalam tubuh sebelumnya, dan ia berusaha lebih dari sebelumnya untuk kesempurnaan, wahai keturunan dinasti Kuru.', 276);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1381, 'Dengan kekuatan praktik sebelumnya, ia terbawa maju bahkan tanpa kehendaknya. Meskipun ia adalah seorang pencari Yoga, ia melampaui Sabda-Brahman, yaitu Prakṛti atau materi.', 277);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1382, 'Karena, dengan praktik masa lalu itu, ia terbawa maju meskipun ia sendiri tidak menginginkannya! Bahkan seorang pencari Yoga melampaui hasil dari pelaksanaan ritual Veda!', 277);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1386, 'Tetapi yogi, yang berusaha keras, dibersihkan dari segala noda dan disempurnakan melalui banyak kelahiran, mencapai keadaan tertinggi.', 278);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1387, 'Namun, yogi, yang menerapkan dirinya dengan rajin, menjadi murni dari dosa dan mencapai kesempurnaan melalui banyak kelahiran, mereka mencapai Tujuan tertinggi.', 278);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1391, 'Lebih besar dari para pertapa, lebih besar dari mereka yang memiliki pengetahuan, lebih besar dari para ritualis adalah Yogin. Oleh karena itu, wahai Arjuna, jadilah seorang Yogin.', 279);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1392, 'Seorang yogi lebih tinggi dari para pertapa; mereka dianggap lebih tinggi bahkan dari para berpengetahuan. Yogi juga lebih tinggi dari para pelaku tindakan. Oleh karena itu, wahai Arjuna, jadilah seorang yogi.', 279);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1396, 'Dia yang, dengan iman, menyembah Aku, dan yang diri terdalamnya terpaku pada Aku, Aku menganggapnya sebagai Yogin terbesar.', 280);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1397, 'Bahkan di antara semua yogi, dia yang memuja-Ku dengan pikiran terfokus pada-Ku dan dengan iman dianggap oleh-Ku sebagai yang terbaik dari para yogi.', 280);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1401, 'Sang Raja berkata, “Dengan pikiranmu terfokus pada-Ku, menjadikan-Ku sebagai penopangmu, dan mempraktikkan yoga—bagaimana engkau, tanpa keraguan, dapat mengenal-Ku sepenuhnya? Dengarlah, wahai Arjuna.”', 281);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1402, 'Yang Mahamulia berkata, “Wahai Partha, dengarkanlah bagaimana, dengan pikiranmu terfokus pada-Ku, mempraktikkan Yoga Meditasi dan berlindung kepada-Ku, engkau pasti akan mengenal-Ku sepenuhnya.”', 281);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1406, 'Aku akan menyatakan kepadamu secara penuh pengetahuan tentang Tuhan ini, bersama dengan pengetahuan yang membuatnya berbeda, mengetahui hal ini tidak ada lagi yang perlu diketahui.', 282);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1407, 'Aku akan memberitahukan kepadamu secara rinci tentang Pengetahuan ini, yang digabungkan dengan realisasi, setelah mengalaminya tidak ada lagi yang perlu diketahui di sini.', 282);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1411, 'Di antara ribuan manusia, beberapa berusaha mencapai kesempurnaan; bahkan di antara mereka yang berusaha mencapai kesempurnaan, hanya beberapa yang mengenal Aku; dan di antara mereka yang mengenal Aku, hanya beberapa yang benar-benar mengenal Aku.', 283);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1412, 'Di antara ribuan manusia, jarang ada yang berupaya untuk kesempurnaan. Bahkan di antara yang sempurna yang rajin, barangkali hanya satu yang mengenal Aku dengan benar.', 283);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1416, 'Bumi, air, api, udara, eter, Manas, Buddhi, dan rasa ego—demikianlah Prakriti-Ku terbagi menjadi delapan bagian.', 284);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1417, 'Prakrti-Ku ini terbagi menjadi delapan bagian: bumi, air, api, udara, ruang, pikiran, intelek, dan egoisme.', 284);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1421, 'Inilah Prakṛti-Ku yang lebih rendah. Tetapi, wahai yang perkasa, ketahuilah bahwa sifat-Ku yang lebih tinggi adalah yang lain. Itu adalah prinsip kehidupan (Jīva-bhūta), yang olehnya alam semesta ini terpelihara.', 285);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1422, 'Wahai yang berlengan perkasa, ini adalah Prakrti yang lebih rendah. Ketahuilah Prakrti-Ku yang lain, yang bagaimanapun lebih tinggi dari ini, yang telah mengambil bentuk jiwa-jiwa individu, dan yang olehnya dunia ini terpelihara.', 285);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1426, 'Ketahuilah bahwa semua makhluk berasal dari keduanya sebagai sumber kelahirannya. Oleh karena itu, Akulah asal mula dan penghancur seluruh alam semesta.', 286);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1427, 'Pahamilah demikian bahwa segala sesuatu—yang berakal dan tidak berakal—memiliki ini sebagai sumbernya. Akulah asal mula sekaligus akhir dari seluruh alam semesta.', 286);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1431, 'Tidak ada yang lebih tinggi dari Aku, wahai Arjuna. Semua ini teruntai pada-Ku, seperti barisan permata pada seutas benang.', 287);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1432, 'Wahai Dhananjaya, tidak ada yang lebih tinggi dari diri-Ku. Semua ini terangkai pada-Ku seperti mutiara pada seutas tali.', 287);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1436, 'Akulah rasa dalam air, wahai Arjuna! Akulah cahaya di matahari dan bulan; suku kata suci Om dalam semua Veda; suara di eter; dan kejantanan dalam diri manusia, Akulah.', 288);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1437, 'Wahai putra Kunti, aku adalah rasa air, aku adalah cahaya bulan dan matahari; huruf \'Om\' dalam semua Veda, suara di angkasa, dan kejantanan dalam diri manusia.', 288);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1441, 'Akulah wangi murni di bumi; Akulah cahaya di api; Akulah kekuatan hidup dalam semua makhluk, dan pertapaan dalam diri pertapa.', 289);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1442, 'Aku juga adalah keharuman manis di bumi; Aku adalah kecemerlangan di dalam api, dan kehidupan di semua makhluk; dan Aku adalah pertapaan para pertapa.', 289);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1446, 'Ketahuilah Aku, wahai Arjuna, sebagai benih utama dari semua makhluk. Akulah kecerdasan orang yang bijaksana dan kecemerlangan orang yang cemerlang.', 290);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1447, 'Wahai Partha, ketahuilah Aku sebagai benih abadi dari semua makhluk. Akulah intelek orang yang cerdas, dan Akulah keberanian orang yang berani.', 290);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1451, 'Dalam diri yang kuat, Akulah kekuatan, tanpa keinginan dan nafsu. Dalam semua makhluk, Akulah keinginan yang tidak bertentangan dengan Dharma, wahai Arjuna.', 291);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1452, 'Dan dari yang kuat, aku adalah kekuatan yang tanpa gairah dan keterikatan. Di antara makhluk hidup, aku adalah keinginan yang tidak bertentangan dengan kebenaran, wahai keturunan dinasti Bharata.', 291);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1456, 'Ketahuilah bahwa semua keadaan Sattva, Rajas, dan Tamas berasal dari-Ku saja; namun, Aku tidak berada di dalamnya; sebaliknya, mereka berada di dalam-Ku.', 292);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1457, 'Hal-hal yang terbuat dari kualitas sattva, dan hal-hal yang terbuat dari kualitas rajas dan tamas, ketahuilah bahwa semuanya berasal dari-Ku saja. Namun, Aku tidak berada di dalamnya; mereka berada di dalam-Ku!', 292);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1461, 'Seluruh alam semesta tertipu oleh ketiga keadaan ini yang berasal dari Guna (Prakrti), dan gagal mengenal Aku, yang berada di luar mereka dan tak berubah.', 293);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1462, 'Seluruh dunia ini, yang tertipu oleh ketiga kualitas yang terbuat dari guna ini, tidak mengenal-Ku, yang melampaui ini dan tak lapuk.', 293);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1466, '(a) Karena Maya ilahi-Ku ini, yang terdiri dari tiga Guna (diambil untuk tujuan hiburan), sulit diatasi. (b) Tetapi mereka yang berlindung hanya pada-Ku akan melampaui Maya.', 294);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1467, 'Karena Maya ilahi-Ku ini, yang dibentuk oleh guna, sulit untuk dilalui, mereka yang berlindung hanya kepada-Ku dapat melampaui Maya ini.', 294);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1471, 'Para pelaku kejahatan, orang bodoh, manusia terendah, mereka yang kehilangan pengetahuan karena delusi (Maya) dan mereka yang dikuasai oleh sifat setan - mereka tidak mencari perlindungan pada-Ku.', 295);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1472, 'Para pelaku kejahatan yang bodoh, yang paling bejat di antara manusia, yang kehilangan kebijaksanaan mereka oleh Maya, dan yang menggunakan cara-cara setan, tidak berlindung kepada-Ku.', 295);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1476, 'Empat jenis orang yang berbuat baik menyembah Aku, wahai Arjuna: yang menderita, yang mencari pengetahuan, yang mencari kekayaan, dan orang-orang yang berpengetahuan.', 296);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1477, 'Wahai Arjuna, yang terkemuka dari dinasti Bharata, empat golongan orang yang berbuat kebajikan memuja-Ku: yang menderita, pencari Pengetahuan, pencari kekayaan, dan orang yang berpengetahuan.', 296);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1481, 'Dari semua ini, orang yang berpengetahuan, yang senantiasa bersama-Ku dalam Yoga dan mengabdi hanya pada Yang Esa, adalah yang terdepan; karena Aku sangat dicintai oleh orang yang berpengetahuan, dan dia juga dicintai oleh-Ku.', 297);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1482, 'Dari mereka, orang yang berpengetahuan, diberkahi dengan keteguhan yang konstan dan pengabdian yang terarah, unggul. Karena Aku sangat dicintai oleh orang yang berpengetahuan, dan ia juga dicintai olehku.', 297);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1486, 'Semua ini memang murah hati, tetapi Aku menganggap orang yang berpengetahuan sebagai diri-Ku sendiri; karena ia, yang terintegrasi, hanya mengabdikan diri pada-Ku sebagai tujuan tertinggi.', 298);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1487, 'Memang, semua ini mulia, tetapi orang yang berpengetahuan adalah Diri itu sendiri. Ini adalah pendapat saya. Karena, dengan pikiran yang teguh, ia berada di jalan yang hanya mengarah kepada-Ku, yang adalah Tujuan tertinggi.', 298);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1491, 'Pada akhirnya banyak kelahiran, orang yang berpengetahuan menemukan perlindungan pada-Ku, menyadari bahwa \'Vasudeva adalah segalanya.\' Sangat sulit menemukan orang yang berjiwa besar seperti itu.', 299);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1492, 'Pada akhir banyak kelahiran, orang yang berpengetahuan mencapai-Ku, menyadari bahwa Vasudewa adalah segalanya. Jiwa yang luhur seperti itu sangat jarang.', 299);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1496, 'Dikendalikan oleh sifat bawaan mereka, dan kehilangan pengetahuan karena berbagai keinginan, orang-orang duniawi berlindung pada dewa-dewa lain, mengamati berbagai disiplin.', 300);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1497, 'Orang-orang, yang kehilangan kebijaksanaan mereka karena keinginan akan berbagai objek dan dibimbing oleh sifat mereka sendiri, berlindung pada dewa-dewa lain, mengikuti metode yang relevan.', 300);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1501, 'Bentuk dewa mana pun yang ingin disembah oleh seorang penyembah dengan iman, Aku akan menguatkan iman mereka itu.', 301);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1502, 'Bentuk dewa apa pun yang ingin disembah oleh seorang penyembah dengan iman, Aku akan menguatkan iman mereka itu.', 301);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1506, 'Dengan iman itu, dia menyembah bentuk itu dan dengan demikian mendapatkan objek keinginannya, yang sebenarnya diberikan oleh-Ku sendiri.', 302);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1507, 'Karena diresapi dengan iman itu, orang itu terlibat dalam penyembahan bentuk itu, dan mereka mendapatkan hasil yang diinginkan itu sebagaimana yang diberikan oleh-Ku saja.', 302);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1511, 'Tetapi buah yang diperoleh oleh orang-orang yang berpemahaman kecil ini terbatas. Para penyembah dewa akan pergi kepada dewa, tetapi para penyembah-Ku akan datang kepada-Ku.', 303);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1512, 'Hasil dari mereka yang berakal lemah memang terbatas. Para penyembah dewa pergi kepada dewa, sementara para penyembah-Ku datang kepada-Ku saja.', 303);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1516, 'Tidak mengetahui sifat-Ku yang lebih tinggi, tak berubah, dan tak tertandingi, orang-orang bodoh menganggap-Ku sebagai entitas yang sekarang telah termanifestasi, meskipun Aku sebelumnya tidak termanifestasi.', 304);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1517, 'Yang tidak cerdas, tidak menyadari keadaan tertinggi-Ku yang tak berubah dan tak terlampaui, menganggap Aku telah termanifestasi dari yang tidak termanifestasi.', 304);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1521, 'Diselimuti oleh Maya-Ku, Aku tidak termanifestasi bagi semua; dunia yang tertipu ini tidak mengenal-Ku sebagai yang tak lahir dan tak berubah.', 305);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1522, 'Karena diselimuti yoga-maya, Aku tidak termanifestasi bagi semua. Dunia yang tertipu ini tidak mengenal-Ku, yang tak lahir dan tak lapuk.', 305);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1526, 'Aku mengenal semua makhluk, wahai Arjuna, yang lalu, sekarang, dan yang akan datang; tetapi tidak seorang pun mengenal Aku.', 306);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1527, 'Wahai Arjuna, Aku mengenal makhluk-makhluk masa lalu, sekarang, dan masa depan; tetapi tidak seorang pun mengenal-Ku!', 306);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1531, 'Wahai Arjuna, semua makhluk tertipu segera setelah mereka lahir, karena delusi pasangan yang berlawanan yang berasal dari keinginan dan kebencian.', 307);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1532, 'Wahai keturunan dinasti Bharata, wahai penghancur musuh, karena delusi dualitas yang timbul dari suka dan duka, semua makhluk menjadi bingung saat kelahiran mereka.', 307);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1536, 'Tetapi mereka yang telah berbuat baik, yang dosa-dosanya telah berakhir, dibebaskan dari delusi pasangan yang berlawanan. Mereka dengan teguh menyembah Aku, berpegang teguh pada sumpah mereka.', 308);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1537, 'Di sisi lain, orang-orang yang telah melakukan perbuatan baik, yang dosa mereka telah berakhir, mereka, karena bebas dari delusi dualitas dan teguh dalam keyakinan mereka, memuja-Ku.', 308);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1541, 'Mereka yang berlindung kepada-Ku dan berjuang untuk pembebasan dari usia tua dan kematian, mengenal Brahman (atau Diri) dan semua tentang sifat Diri itu, dan seluruh Karma (atau aktivitas yang mengarah pada kelahiran kembali).', 309);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1542, 'Mereka yang berusaha dengan berlindung kepada-Ku untuk bebas dari usia tua dan kematian, mereka mengenal Brahman, Diri individu, dan semua tentang tindakan. [Mereka mengenal Brahman sebagai semua entitas individu dan semua tindakan. Ayat ini menetapkan meditasi pada Brahman yang berkualitas untuk para pencari tingkat menengah. Ayat-ayat yang dimulai dengan yang ke-14 berbicara tentang realisasi Brahman yang tidak berkualitas oleh para pencari tingkat tertinggi.]', 309);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1546, 'Dan mereka yang mengenal Aku dengan Adhibhuta, Adhidaiva, dan Adhiyajna, mereka juga, dengan pikiran mereka yang terfokus dalam meditasi, mengenal Aku bahkan pada saat kematian.', 310);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1547, 'Mereka yang mengenal saya sebagai ada di bidang fisik dan ilahi, dan juga dalam konteks pengorbanan, mereka dengan pikiran yang terkonsentrasi mengenal saya bahkan pada saat kematian.', 310);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1551, 'Arjuna berkata, Wahai Pribadi Tertinggi, apakah Brahman itu? Apa yang ada di tingkat individu? Apa itu tindakan? Apa yang dikatakan ada di tingkat fisik? Dan apa yang dikatakan ada di tingkat ilahi?', 311);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1552, 'Arjuna berkata, Wahai Pribadi Tertinggi, apakah Brahman itu? Apa yang ada di tingkat individu? Apa itu tindakan? Apa yang dikatakan ada di tingkat fisik? Dan apa yang dikatakan ada di tingkat ilahi?', 311);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1556, 'Wahai Madhusudana, bagaimana entitas yang ada dalam pengorbanan di dalam tubuh ini, dan siapa dia? Dan bagaimana Engkau dapat dikenal oleh orang-orang yang terkonsentrasi pada saat kematian?', 312);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1557, 'Wahai Madhusudana, bagaimana entitas yang ada dalam pengorbanan di dalam tubuh ini, dan siapa dia? Dan bagaimana Engkau dapat dikenal oleh orang-orang yang terkonsentrasi pada saat kematian?', 312);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1561, 'Sang Mahamulia berkata, “Yang Tak Berubah adalah Brahman tertinggi; keakuan dikatakan sebagai entitas yang ada di tingkat individu. Yang dimaksud dengan tindakan adalah persembahan yang menyebabkan asal mula keberadaan segala sesuatu.”', 313);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1562, 'Yang Mahamulia berkata, “Yang Tak Berubah adalah Brahman tertinggi; keakuan dikatakan sebagai entitas yang ada di tingkat individu. Yang dimaksud dengan tindakan adalah persembahan yang menyebabkan asal mula keberadaan segala sesuatu.”', 313);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1566, 'Yang ada di tingkat fisik adalah entitas yang berubah-ubah, dan yang ada di tingkat ilahi adalah Pribadi. Wahai yang terbaik di antara makhluk berbadan, Aku Sendiri adalah entitas yang ada dalam pengorbanan di dalam tubuh ini.', 314);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1567, 'Yang ada di tingkat fisik adalah entitas yang berubah-ubah, dan yang ada di tingkat ilahi adalah Pribadi. Wahai yang terbaik di antara makhluk berbadan, Aku Sendiri adalah entitas yang ada dalam pengorbanan di dalam tubuh ini.', 314);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1571, 'Dan pada saat kematian, siapa pun yang pergi, meninggalkan tubuh sambil hanya memikirkan Aku, mencapai keadaan-Ku; tidak ada keraguan tentang ini.', 315);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1572, 'Dan pada saat kematian, siapa pun yang pergi, meninggalkan tubuh sambil hanya memikirkan-Ku, mencapai keadaan-Ku; tidak ada keraguan tentang ini.', 315);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1576, 'Wahai putra Kunti, memikirkan entitas apa pun, apa pun itu, seseorang meninggalkan tubuh pada akhirnya; ia mencapai entitas yang sama itu, setelah selalu tenggelam dalam pikirannya.', 316);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1577, 'Wahai putra Kunti, memikirkan entitas apa pun, apa pun itu, seseorang meninggalkan tubuh pada akhirnya; ia mencapai entitas yang sama itu, setelah selalu tenggelam dalam pikirannya.', 316);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1581, 'Oleh karena itu, pikirkanlah Aku setiap saat dan bertempurlah; tidak ada keraguan bahwa dengan mendedikasikan pikiran dan intelekmu kepada-Ku, engkau hanya akan mencapai Aku.', 317);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1582, 'Oleh karena itu, pikirkanlah Aku setiap saat dan bertempurlah; tidak ada keraguan bahwa dengan mendedikasikan pikiran dan intelekmu kepada-Ku, engkau hanya akan mencapai Aku.', 317);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1586, 'Wahai putra Prtha, dengan bermeditasi dengan pikiran yang terlibat dalam yoga praktik dan tidak menyimpang ke hal lain, seseorang mencapai Pribadi tertinggi yang ada di wilayah yang bersinar.', 318);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1587, 'Wahai putra Prtha, dengan bermeditasi dengan pikiran yang terlibat dalam yoga praktik dan tidak menyimpang ke hal lain, seseorang mencapai Pribadi tertinggi yang ada di wilayah yang bersinar.', 318);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1591, 'Dia yang bermeditasi pada Yang Maha Mengetahui, Yang Kuno, Sang Penguasa, lebih halus dari yang halus, Sang Penentu segala sesuatu, dengan bentuk yang tak terbayangkan, bercahaya seperti matahari, dan melampaui kegelapan—dia mencapai Pribadi Tertinggi.', 319);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1592, 'Dia yang bermeditasi pada Yang Maha Mengetahui, Yang Kuno, Sang Penguasa, lebih halus dari yang halus, Sang Penentu segala sesuatu, dengan bentuk yang tak terbayangkan, bercahaya seperti matahari, dan melampaui kegelapan—dia mencapai Pribadi Tertinggi.', 319);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1596, 'Pada saat kematian, dengan memusatkan Prana (daya hidup) sepenuhnya di antara alis dengan pikiran yang teguh, dan diliputi oleh pengabdian serta kekuatan konsentrasi, ia mencapai Pribadi Tertinggi yang cemerlang itu.', 320);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1597, 'Pada saat kematian, dengan memusatkan Prana (daya hidup) sepenuhnya di antara alis dengan pikiran yang teguh, dan diliputi oleh pengabdian serta kekuatan konsentrasi, ia mencapai Pribadi Tertinggi yang cemerlang itu.', 320);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1601, 'Aku akan secara singkat memberitahumu tentang Tujuan yang tak berubah itu, yang dinyatakan oleh para pengetahu Veda; yang dimasuki oleh orang-orang yang rajin, bebas dari keterikatan, dan yang untuk itu orang-orang mempraktikkan selibat, bercita-cita.', 321);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1602, 'Aku akan secara singkat memberitahukan kepadamu tentang Tujuan yang tak berubah itu yang dinyatakan oleh para pengetahu Veda; mereka yang rajin, bebas dari keterikatan, dan yang bercita-cita untuk itu, memasukinya. Orang-orang mempraktikkan selibat untuk itu.', 321);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1606, 'Setelah mengendalikan semua indra, mengurung pikiran di dalam hati, dan memusatkan daya hidupnya sendiri di kepala, ia harus tetap teguh dalam yoga.', 322);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1607, 'Setelah mengendalikan semua indra, mengurung pikiran di dalam hati, dan memusatkan daya hidupnya sendiri di kepala, ia harus tetap teguh dalam yoga.', 322);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1611, 'Dia yang pergi, meninggalkan tubuh sambil mengucapkan satu suku kata, yaitu "Om," yang adalah Brahman, dan memikirkan Aku, mencapai Tujuan tertinggi.', 323);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1612, 'Dia yang pergi, meninggalkan tubuh sambil mengucapkan satu suku kata, "Om," yang adalah Brahman, dan memikirkan Aku, mencapai Tujuan tertinggi.', 323);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1616, 'Wahai putra Prtha, bagi yogi yang konsentrasi dan perhatiannya tak terpecah, yang mengingat Aku tanpa henti dan untuk waktu yang lama, Aku mudah dicapai.', 324);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1617, 'Wahai putra Prtha, bagi yogi yang konsentrasi dan perhatiannya tak terpecah, yang mengingat Aku tanpa henti dan untuk waktu yang lama, Aku mudah dicapai.', 324);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1621, 'Sebagai hasil dari mencapai Aku, orang-orang yang mulia yang telah mencapai kesempurnaan tertinggi tidak mengalami kelahiran, yang merupakan tempat penderitaan dan tidak kekal.', 325);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1622, 'Sebagai hasil dari mencapai-Ku, orang-orang yang luhur yang telah mencapai kesempurnaan tertinggi tidak mengalami kelahiran kembali, yang merupakan tempat penderitaan dan tidak kekal.', 325);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1626, 'Wahai Arjuna, semua dunia bersama dengan dunia Brahma dapat kembali. Tetapi, wahai putra Kunti, tidak ada kelahiran kembali setelah mencapai Aku.', 326);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1627, 'Wahai Arjuna, semua dunia bersama dengan dunia Brahma dapat kembali. Tetapi, wahai putra Kunti, tidak ada kembali setelah mencapai-Ku.', 326);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1631, 'Orang-orang yang mengetahui apa itu siang dan malam, mengetahui hari Brahma yang berakhir dalam seribu yuga [Empat yuga (di dunia manusia), yaitu Satya, Treta, Dwapara, dan Kali terdiri dari 4.320.000 tahun. Periode ini dikalikan seribu merupakan satu hari Brahma. Malam-Nya juga berlangsung selama periode yang sama. Lihat M.S. dan V.S.A.], dan malam-Nya yang juga berakhir dalam seribu yuga.', 327);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1632, 'Orang-orang yang mengetahui apa itu siang dan malam, mengetahui hari Brahma yang berakhir dalam seribu yuga, dan malam-Nya yang juga berakhir dalam seribu yuga.', 327);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1636, 'Dengan datangnya siang, semua hal yang termanifestasi muncul dari Yang Tak Termanifestasi, dan ketika malam tiba, mereka menyatu ke dalam apa yang disebut Yang Tak Termanifestasi.', 328);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1637, 'Dengan datangnya siang, semua hal yang termanifestasi muncul dari Yang Tak Termanifestasi, dan ketika malam tiba, mereka menyatu ke dalam apa yang disebut Yang Tak Termanifestasi.', 328);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1641, 'Wahai putra Prtha, setelah lahir berulang kali, kumpulan makhluk yang sama itu lenyap dengan sendirinya saat malam tiba. Ia hidup kembali saat siang tiba.', 329);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1642, 'Wahai putra Prtha, setelah lahir berulang kali, kumpulan makhluk yang sama itu lenyap dengan sendirinya saat malam tiba. Ia hidup kembali saat siang tiba.', 329);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1646, 'Tetapi yang berbeda dari Yang Tak Termanifestasi itu adalah Realitas abadi yang tak termanifestasi lainnya, yang tidak hancur ketika semua makhluk hancur.', 330);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1647, 'Tetapi berbeda dari Yang Tak Termanifestasi itu adalah Realitas abadi yang tak termanifestasi lainnya, yang tidak hancur ketika semua makhluk hancur.', 330);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1651, 'Mereka menyebut Dia yang telah disebutkan sebagai Yang Tak Termanifestasi, Yang Tak Berubah, Tujuan tertinggi. Itulah tempat tinggal tertinggi-Ku, dari mana mereka tidak kembali setelah mencapainya.', 331);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1652, 'Mereka menyebut Dia yang telah disebutkan sebagai Yang Tak Termanifestasi, Yang Tak Berubah, Tujuan tertinggi. Itulah tempat tinggal tertinggi-Ku, dari mana mereka tidak kembali setelah mencapainya.', 331);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1656, 'Wahai putra Prtha, Pribadi tertinggi itu—yang di dalamnya semua makhluk termasuk dan yang melingkupi semua ini—sesungguhnya dicapai melalui pengabdian yang terarah.', 332);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1657, 'Wahai putra Prtha, Pribadi tertinggi itu—yang di dalamnya semua makhluk termasuk dan yang melingkupi semua ini—sesungguhnya dicapai melalui pengabdian yang terarah.', 332);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1661, 'Wahai yang terbaik dari dinasti Bharata, Aku sekarang akan membicarakan waktu di mana para yogi pergi dan mencapai Keadaan Tanpa Kembali, dan juga Keadaan Kembali.', 333);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1662, 'Wahai yang terbaik dari dinasti Bharata, Aku sekarang akan membicarakan waktu di mana para yogi pergi dan mencapai Keadaan Tanpa Kembali, dan juga Keadaan Kembali.', 333);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1666, 'Api, cahaya, siang hari, paruh terang bulan, enam bulan solstis utara—dengan mengikuti jalan ini, orang-orang yang mengenal Brahman mencapai Brahman ketika mereka mati.', 334);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1667, 'Api, cahaya, siang hari, paruh terang bulan, enam bulan solstis utara—dengan mengikuti jalan ini, orang-orang yang mengenal Brahman mencapai Brahman ketika mereka mati.', 334);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1671, 'Mengikuti Jalan ini, yogi, setelah mencapai cahaya bulan, kembali, asap, malam, juga paruh gelap bulan dan enam bulan solstis Selatan.', 335);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1672, 'Mengikuti Jalan ini, yogi, setelah mencapai cahaya bulan, kembali, asap, malam, juga paruh gelap bulan dan enam bulan solstis Selatan.', 335);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1676, 'Dua jalur dunia ini, yaitu putih dan hitam, sesungguhnya dianggap abadi. Melalui yang satu, seseorang pergi ke keadaan tidak kembali; melalui yang lain, mereka kembali lagi.', 336);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1677, 'Dua jalur dunia ini, yaitu putih dan hitam, sesungguhnya dianggap abadi. Melalui yang satu, seseorang pergi ke keadaan tidak kembali; melalui yang lain, mereka kembali lagi.', 336);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1681, 'Wahai putra Prtha, tidak ada yogi—yang teguh dalam meditasi—siapa pun yang telah mengetahui kedua jalur ini menjadi tertipu. Oleh karena itu, wahai Arjuna, teguhlah dalam yoga setiap saat.', 337);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1682, 'Wahai putra Prtha, tidak ada yogi—yang teguh dalam meditasi—siapa pun yang telah mengetahui kedua jalur ini menjadi tertipu. Oleh karena itu, wahai Arjuna, teguhlah dalam yoga setiap saat.', 337);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1686, 'Setelah mengetahui ini, yogi melampaui semua hasil perbuatan benar yang dinyatakan dalam Veda, kurban, pertapaan, dan amal, dan mencapai keadaan primordial yang tertinggi.', 338);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1687, 'Setelah mengetahui ini, yogi melampaui semua hasil perbuatan benar yang dinyatakan dalam Veda, pengorbanan, pertapaan, dan amal, dan mencapai keadaan primordial yang tertinggi.', 338);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1691, 'Sang Raja berkata, “Aku akan menyatakan kepadamu, yang tidak mengeluh, pengetahuan yang paling misterius ini, bersama dengan pengetahuan khususnya, dengan mengetahui itu engkau akan terbebas dari kejahatan.”', 339);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1692, 'Yang Mahamulia berkata: Namun, bagimu yang tidak suka mengeluh, Aku akan berbicara tentang rahasia tertinggi ini, yaitu pengetahuan yang digabungkan dengan pengalaman, dengan menyadarinya Anda akan bebas dari kejahatan.', 339);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1696, 'Ini adalah ilmu kerajaan, misteri kerajaan, pemurni tertinggi. Ini disadari melalui pengalaman langsung, selaras dengan Dharma, menyenangkan untuk dipraktikkan, dan abadi.', 340);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1697, 'Ini adalah pengetahuan yang berdaulat, kedalaman yang berdaulat, penyucian terbaik; langsung dapat direalisasikan, benar, sangat mudah dipraktikkan, dan tak lekang oleh waktu.', 340);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1701, 'Orang-orang yang tidak beriman pada Dharma ini, wahai penghancur musuh, selalu tetap tidak mencapai Aku, di jalan fana Samsara.', 341);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1702, 'Wahai penghancur musuh, mereka yang mengabaikan Dharma ini (pengetahuan tentang Diri) pasti berputar-putar, tanpa mencapai-Ku, di sepanjang jalan transmigrasi yang penuh dengan kematian.', 341);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1706, 'Seluruh alam semesta ini diliputi oleh-Ku, dalam bentuk yang tidak termanifestasi. Semua makhluk berdiam dalam-Ku, tetapi Aku tidak berdiam di dalamnya.', 342);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1707, 'Seluruh dunia ini diliputi oleh-Ku dalam wujud-Ku yang tidak termanifestasi. Semua makhluk ada dalam-Ku, tetapi Aku tidak terkandung di dalamnya!', 342);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1711, 'Namun, makhluk hidup tidak berdiam di dalam-Ku. Lihatlah Yoga ilahi-Ku. Aku adalah pemelihara semua makhluk, namun Aku tidak ada di dalamnya. Hanya kehendak-Ku sendirilah yang menyebabkan keberadaan mereka.', 343);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1712, 'Makhluk hidup tidak berdiam di dalam-Ku. Lihatlah Yoga ilahi-Ku! Aku adalah penopang dan asal mula makhluk hidup, tetapi Diri-Ku tidak terkandung di dalam makhluk hidup.', 343);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1716, 'Sebagaimana elemen udara yang kuat, bergerak di mana-mana, selalu ada dalam eter, ketahuilah bahwa demikian pula semua makhluk berdiam dalam Aku.', 344);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1717, 'Pahamilah demikian bahwa sama seperti angin yang sangat besar, bergerak ke mana-mana, selalu ada di angkasa, demikian pula semua makhluk ada di dalam-Ku.', 344);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1721, 'Semua makhluk, wahai Arjuna, masuk ke dalam Prakriti-Ku pada akhir siklus waktu; lagi, Aku mengirim mereka keluar pada awal siklus waktu.', 345);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1722, 'Wahai putra Kunti, semua makhluk pada akhir suatu siklus kembali ke Prakrti-Ku. Pada awal suatu siklus, Aku memproyeksikan mereka kembali.', 345);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1726, 'Mengendalikan Prakṛti, yang adalah milik-Ku sendiri, Aku mengirimkan kembali dan lagi kumpulan makhluk ini, yang tak berdaya di bawah kekuasaan Prakṛti.', 346);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1727, 'Dengan menjaga prakṛti-Ku sendiri di bawah kendali, Aku memproyeksikan kembali dan lagi kumpulan makhluk ini, yang tak berdaya karena pengaruh sifat mereka sendiri.', 346);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1731, 'Tetapi tindakan-tindakan ini tidak mengikat-Ku, wahai Arjuna, karena Aku tetap terlepas darinya, seolah-olah Aku tidak peduli.', 347);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1732, 'Wahai Dhananjaya, tindakan-tindakan itu tidak mengikat-Ku, karena Aku tetap tidak peduli dan tidak terikat padanya.', 347);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1736, 'Di bawah pengawasan-Ku, Prakrti melahirkan semua entitas yang bergerak dan tidak bergerak, dan dengan demikian, wahai Arjuna, dunia berputar.', 348);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1737, 'Di bawah pengawasan-Ku, Prakrti menghasilkan dunia makhluk yang bergerak dan tidak bergerak. Karena ini, wahai putra Kunti, dunia berputar.', 348);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1741, 'Orang-orang bodoh mengabaikan-Ku, yang bersemayam dalam bentuk manusia, tidak mengetahui sifat-Ku yang lebih tinggi sebagai Tuhan Tertinggi dari semua makhluk.', 349);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1742, 'Tidak mengetahui sifat tertinggi-Ku sebagai Penguasa semua makhluk, orang-orang bodoh mengabaikan-Ku, yang telah mengambil wujud manusia.', 349);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1746, 'Orang-orang yang tidak berakal memiliki sifat yang menipu dan serupa dengan sifat rakshasa dan asura. Harapan mereka sia-sia, tindakan mereka sia-sia, dan pengetahuan mereka sia-sia.', 350);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1747, 'Memiliki harapan yang sia-sia, tindakan yang sia-sia, pengetahuan yang sia-sia, dan ketidakberpikiran, mereka benar-benar menjadi memiliki watak menipu dari iblis dan setan.', 350);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1751, 'Tetapi orang-orang berjiwa besar, wahai Arjuna, yang berhubungan dengan sifat ilahi-Ku, menyembah Aku dengan pikiran yang teguh, mengenal Aku sebagai sumber tak berubah dari semua makhluk.', 351);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1752, 'Wahai putra Prtha, para bangsawan, yang memiliki sifat ilahi, sungguh memuja-Ku dengan ketulusan hati, mengenal-Ku sebagai sumber tak berubah dari semua objek.', 351);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1756, 'Dengan mendambakan persekutuan abadi dengan-Ku, mereka menyembah-Ku, senantiasa melantunkan pujian-Ku, berusaha dengan tekad yang teguh dan bersujud kepada-Ku dengan pengabdian.', 352);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1757, 'Selalu memuliakan-Ku dan berusaha, orang-orang yang bertekad teguh menyembah-Ku dengan bersujud kepada-Ku dan selalu diliputi pengabdian.', 352);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1761, 'Yang lain, selain mempersembahkan pengorbanan pengetahuan, juga menyembah Aku sebagai Yang Esa, yang bercirikan keragaman dalam berbagai cara yang tak terhitung dan beraneka ragam (dalam aspek Kosmis-Ku).', 353);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1762, 'Yang lain memang menyembah-Ku dengan secara eksklusif memuja melalui pengorbanan pengetahuan kesatuan; (yang lain menyembah-Ku) dalam banyak cara, dan (yang lain) sebagai yang beraneka ragam yang ada dalam berbagai bentuk.', 353);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1766, 'Akulah Kratu, Akulah pengorbanan, Akulah persembahan kepada leluhur, Akulah ramuan, Akulah Mantra, Akulah mentega yang dimurnikan, Akulah api, Akulah persembahan.', 354);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1767, 'Aku adalah ritual, Aku adalah pengorbanan, Aku adalah persembahan, Aku adalah tanaman obat, Aku adalah mantra, Aku adalah mentega murni, Aku adalah api, dan Aku adalah tindakan persembahan.', 354);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1771, 'Akulah ayah, ibu, pencipta, dan kakek alam semesta; Akulah pemurni. Akulah suku kata Om, demikian pula Rk, Saman, dan Yajus.', 355);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1772, 'Dari dunia ini, Aku adalah ayah, ibu, penentu, dan kakek; Aku adalah yang dapat diketahui, yang menguduskan, suku kata Om, serta Rk, Sama, dan Yajus.', 355);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1776, 'Akulah tujuan, penopang, Tuhan, saksi, tempat berdiam, perlindungan, dan teman. Akulah asal mula, kehancuran, dasar pemeliharaan, dan benih yang tak termusnahkan.', 356);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1777, 'Aku adalah buah tindakan, pemelihara, Tuhan, saksi, tempat tinggal, perlindungan, teman, asal, akhir, fondasi, simpanan, dan benih yang tak termusnahkan.', 356);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1781, 'Aku memberi panas, Aku menahan dan menurunkan hujan; Aku adalah keabadian dan juga kematian, wahai Arjuna. Aku adalah keberadaan dan ketidakberadaan.', 357);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1782, 'Wahai Arjuna, Aku memberi panas, Aku menahan dan menurunkan hujan; Aku adalah sesungguhnya nektar, dan juga kematian, keberadaan, dan ketiadaan.', 357);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1786, 'Mereka yang mahir dalam tiga Veda, yang telah disucikan dari dosa dengan meminum sari Soma, berdoa untuk jalan menuju surga dan menyembah Aku melalui pengorbanan. Mencapai alam suci kepala para dewa, mereka menikmati kesenangan surgawi di surga.', 358);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1787, 'Mereka yang mahir dalam Veda, yang meminum Soma dan disucikan dari dosa, berdoa untuk tujuan surgawi dengan menyembah-Ku melalui pengorbanan. Setelah mencapai tempat (dunia) raja dewa, yang merupakan hasil dari kebenaran, mereka menikmati kesenangan ilahi para dewa di surga.', 358);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1791, 'Setelah menikmati dunia surga yang luas, mereka kembali ke dunia manusia, pahala mereka telah habis. Demikianlah, mereka yang mengikuti ritual Veda dan tertarik oleh keinginan datang dan pergi.', 359);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1792, 'Setelah menikmati dunia surgawi yang luas itu, mereka memasuki dunia manusia setelah pahala mereka habis. Demikianlah, mereka yang mengikuti ritus dan kewajiban yang ditetapkan dalam tiga Veda, dan mendambakan kesenangan, mencapai keadaan datang dan pergi.', 359);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1796, 'Mereka yang, tidak termasuk yang lain, memikirkan-Ku dan menyembah-Ku, bercita-cita untuk persatuan abadi dengan-Ku, Aku menjaga kemakmuran dan kesejahteraan mereka (Yoga dan Ksema).', 360);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1797, 'Orang-orang yang, menjadi tidak berbeda dari-Ku dan bermeditasi, menyembah-Ku di mana-mana, bagi mereka, yang selalu terikat pada-Ku, Aku mengatur untuk mengamankan apa yang tidak mereka miliki dan melestarikan apa yang mereka miliki.', 360);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1801, 'Bahkan mereka yang mengabdi pada dewa-dewi lain dengan iman di hati mereka, hanya menyembah Aku, wahai Arjuna, meskipun tidak sesuai dengan Sastra.', 361);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1802, 'Bahkan mereka yang, mengabdikan diri kepada dewa-dewa lain dan diberkahi dengan iman, menyembah mereka, mereka juga, wahai putra Kunti, menyembah-Ku sendiri, meskipun mengikuti metode yang salah.', 361);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1806, 'Karena, Akulah satu-satunya penikmat dan satu-satunya Tuhan dari semua pengorbanan. Mereka tidak mengenal-Ku dalam sifat-Ku yang sebenarnya; oleh karena itu, mereka jatuh.', 362);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1807, 'Memang, Aku adalah penikmat dan Tuhan dari semua pengorbanan, tetapi mereka tidak mengenal-Ku dengan benar. Oleh karena itu, mereka jatuh.', 362);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1811, 'Para pemuja dewa pergi kepada dewa; para pemuja leluhur pergi kepada leluhur; para pemuja Bhuta pergi kepada Bhuta; dan mereka yang menyembah-Ku datang kepada-Ku.', 363);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1812, 'Mereka yang menyembah dewa mencapai dewa; para penyembah leluhur pergi kepada leluhur; para penyembah makhluk mencapai makhluk; dan mereka yang menyembah-Ku datang kepada-Ku.', 363);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1816, 'Siapa pun yang mempersembahkan kepada-Ku dengan pengabdian sejati sehelai daun, sekuntum bunga, sebutir buah, atau sedikit air, Aku menerima persembahan ini yang dibuat dengan pengabdian oleh mereka yang berhati murni.', 364);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1817, 'Siapa pun yang mempersembahkan kepada-Ku dengan pengabdian—sehelai daun, sekuntum bunga, sebutir buah, atau air—Aku menerima persembahan itu dari orang yang berhati murni yang telah dipersembahkan dengan pengabdian.', 364);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1821, 'Apa pun yang kau lakukan, apa pun yang kau makan, apa pun yang kau persembahkan, apa pun yang kau berikan, laku tapa apa pun yang kau lakukan, wahai Arjuna, lakukanlah itu sebagai persembahan kepada-Ku.', 365);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1822, 'Wahai putra Kunti, apa pun yang Anda lakukan, apa pun yang Anda makan, apa pun yang Anda persembahkan sebagai pengorbanan, apa pun yang Anda berikan, dan apa pun laku tapa yang Anda lakukan—semuanya Anda persembahkan kepada-Ku.', 365);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1826, 'Dengan demikian, dilengkapi dalam pikiran dengan Yoga pelepasan, engkau akan membebaskan dirimu dari ikatan Karma, yang menghasilkan buah yang menguntungkan dan tidak menguntungkan. Dengan demikian dibebaskan, engkau akan datang kepada-Ku.', 366);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1827, 'Dengan demikian, Anda akan terbebas dari ikatan dalam bentuk tindakan yang menghasilkan hasil baik dan buruk. Dengan pikiran Anda yang diliputi yoga pelepasan dan menjadi bebas, Anda akan mencapai-Ku.', 366);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1831, 'Aku sama terhadap semua ciptaan; tidak ada yang Aku benci atau Aku sayangi. Tetapi mereka yang menyembah-Ku dengan pengabdian berdiam di dalam-Ku, dan Aku di dalam mereka.', 367);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1832, 'Aku tidak memihak kepada semua makhluk; bagi-Ku tidak ada yang menjijikkan atau yang disayangi. Tetapi mereka yang menyembah-Ku dengan pengabdian, mereka ada dalam-Ku, dan Aku juga ada dalam mereka.', 367);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1836, 'Bahkan jika orang yang paling berdosa menyembah-Ku dengan pengabdian yang tak terbagi, dia harus dianggap suci, karena dia telah memutuskan dengan benar.', 368);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1837, 'Bahkan jika seorang yang berperilaku sangat buruk menyembah-Ku dengan pengabdian yang tulus, ia harus dianggap benar-benar baik; karena ia telah memutuskan dengan benar.', 368);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1841, 'Cepat, ia menjadi saleh dan memperoleh kedamaian abadi. Tegaskanlah atas nama-Ku, wahai Arjuna, bahwa pemuja-Ku tidak pernah binasa.', 369);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1842, 'Ia segera menjadi memiliki pikiran yang bajik; ia mencapai kedamaian abadi. Anda proklamirkan dengan berani, wahai putra Kunti, bahwa pemuja-Ku tidak akan binasa.', 369);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1846, 'Dengan berlindung kepada-Ku, bahkan mereka yang lahir dari kejahatan, wanita, Waisya, dan Sudra, mencapai keadaan tertinggi.', 370);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1847, 'Karena, wahai putra Prtha, bahkan mereka yang lahir dari dosa—wanita, Waisya, serta Sudra—[S. konstruksi bagian ini adalah: wanita, Waisya serta Sudra, dan bahkan yang lain yang lahir dari dosa (yaitu, mereka yang lahir rendah dan berbuat keji, yaitu Mleccha, Pukkasa, dan lain-lain). M.S. juga mengambil papa-yonayah (lahir dari dosa) sebagai frasa terpisah, dan mengklasifikasikan wanita dan lain-lain hanya sebagai mereka yang dilarang dari studi Veda, dll.-Tr.]—bahkan mereka mencapai Tujuan tertinggi dengan berlindung kepada-Ku.', 370);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1851, 'Betapa lebih lagi, bagi para Brahmana yang murni dan raja-raja bijak yang adalah pemuja-Ku! Setelah memperoleh dunia yang fana dan tanpa sukacita ini, mereka harus menyembah-Ku.', 371);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1852, 'Bagaimana dengan para Brahmana suci dan para bijak yang saleh dan bijaksana? Setelah datang ke dunia yang fana dan penuh penderitaan ini, sembahlah Aku.', 371);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1856, 'Pusatkan pikiranmu pada-Ku, jadilah pemuja-Ku, sembahlah Aku. Sujudlah kepada-Ku. Dengan demikian memusatkan pikiranmu dan menganggap-Ku sebagai tujuan tertinggi, engkau akan datang kepada-Ku.', 372);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1857, 'Pusatkan pikiranmu pada-Ku saja; serahkan dirimu kepada-Ku, korbankan dirimu untuk-Ku, dan bersujudlah kepada-Ku. Dengan memusatkan pikiranmu dan menerima-Ku sebagai tujuan tertinggi, engkau pasti akan mencapai-Ku, yang demikian adalah Diri.', 372);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1861, 'Sang Raja berkata, “Selanjutnya, wahai Arjuna, dengarkanlah firman-Ku yang Tertinggi. Menginginkan kebaikanmu, Aku akan berbicara kepadamu yang mencintai Aku.”', 373);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1862, 'Yang Mahamulia berkata, wahai yang berlengan perkasa, dengarkanlah sekali lagi ucapan tertinggi-Ku, yang akan Aku ucapkan kepadamu yang menyukainya, demi kesejahteraanmu.', 373);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1866, 'Baik pasukan dewa maupun para bijak agung tidak mengetahui kekuatan-Ku; karena Akulah sesungguhnya sumber para dewa dan para bijak agung.', 374);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1867, 'Baik para dewa maupun para resi agung tidak mengetahui keagungan-Ku, karena Aku adalah sumber dari semuanya dalam segala hal.', 374);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1871, 'Dia yang mengenal Aku sebagai tak lahir, tanpa awal, dan Penguasa agung dunia—dia di antara manusia adalah yang tidak tertipu dan dibebaskan dari segala dosa.', 375);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1872, 'Dia yang mengenal-Ku—yang tak lahir, tanpa awal, dan Penguasa agung dunia—ia, yang tidak tertipu di antara manusia, menjadi bebas dari segala dosa.', 375);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1876, 'Kecerdasan, pengetahuan, ketidak-delusi, kesabaran, kebenaran, pengendalian diri, pengendalian diri, kesenangan, rasa sakit, kegembiraan, dan depresi, ketakutan, dan keberanian;', 376);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1877, 'Kecerdasan, kebijaksanaan, tanpa delusi, pengampunan, kebenaran, pengendalian organ eksternal, pengendalian organ internal, kebahagiaan, kesedihan, kelahiran, kematian, dan tanpa rasa takut;', 376);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1881, 'Ketidak-kekerasan, realitas, keceriaan, pertapaan, kemurahan hati, ketenaran, dan kehinaan—kualitas-kualitas yang berbeda dari makhluk ini berasal dari-Ku saja.', 377);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1882, 'Tidak melukai, ketenangan, kepuasan, pertapaan, amal, ketenaran, dan kehinaan—sifat-sifat makhluk yang berbeda ini berasal dari-Ku saja.', 377);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1886, 'Tujuh bijak agung dari zaman dahulu, serta empat Manu, semuanya memiliki watak mental-Ku, lahir dari pikiran-Ku. Semua makhluk di dunia ini adalah keturunan mereka.', 378);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1887, 'Tujuh resi besar, serta empat Manu di zaman dahulu, dari mana semua makhluk ini di dunia telah turun, pikiran mereka terfokus pada-Ku, dan mereka lahir dari pikiran-Ku.', 378);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1891, 'Dia yang benar-benar mengetahui manifestasi tertinggi dan kemegahan sifat-sifat baik-Ku ini, bersatu dengan Yoga Bhakti yang tak tergoyahkan—tidak ada keraguan tentang ini.', 379);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1892, 'Seseorang yang benar-benar mengetahui keagungan dan yoga-Ku ini, ia diliputi oleh yoga yang tak tergoyahkan. Tidak ada keraguan tentang itu.', 379);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1896, 'Akulah asal mula segala sesuatu; dari-Ku segala sesuatu berasal. Berpikir demikian, orang bijak menyembah-Ku dengan segenap pengabdian (Bhava).', 380);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1897, 'Aku adalah asal mula segala sesuatu; segala sesuatu bergerak karena-Ku. Menyadari hal ini, para bijak, yang dipenuhi semangat, memuja-Ku.', 380);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1901, 'Dengan pikiran mereka terfokus pada-Ku, prana mereka terpusat pada-Ku, saling menginspirasi dan senantiasa membicarakan-Ku, mereka hidup dalam kepuasan dan kebahagiaan setiap saat.', 381);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1902, 'Dengan pikiran mereka terfokus pada-Ku, mendedikasikan hidup mereka kepada-Ku, saling mencerahkan, dan selalu membicarakan-Ku, mereka memperoleh kepuasan dan bersukacita.', 381);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1906, 'Kepada mereka yang senantiasa bersatu dengan-Ku dan yang menyembah-Ku dengan cinta yang tak terhingga, Aku dengan penuh kasih menganugerahkan watak mental (Buddhi-yoga) yang menuntun mereka kepada-Ku.', 382);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1907, 'Kepada mereka yang senantiasa berbakti dan menyembah-Ku dengan cinta, Aku memberikan penguasaan kebijaksanaan yang menuntun mereka kepada-Ku.', 382);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1911, 'Karena belas kasihan kepada mereka saja, Aku, yang berdiam dalam aktivitas mental mereka sebagai objeknya, menghilangkan kegelapan yang lahir dari ketidaktahuan dengan lampu pengetahuan yang cemerlang.', 383);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1912, 'Karena belas kasihan kepada mereka saja, Aku, yang berdiam di hati mereka, menghancurkan kegelapan yang lahir dari ketidaktahuan dengan lampu pengetahuan yang cemerlang.', 383);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1916, 'Arjuna berkata, “Engkau adalah Brahman Tertinggi, Cahaya Tertinggi, dan Penyucian Tertinggi. Semua peramal menyatakan Engkau sebagai Pribadi ilahi yang abadi, Tuhan Purba, yang tak lahir, dan mahakuasa. Demikian pula para bijak ilahi Narada, Asita, Devala, dan Vyasa menyatakan. Engkau Sendiri juga menyatakan ini.”', 384);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1917, 'Arjuna berkata, “Engkau adalah Brahman tertinggi, Cahaya tertinggi, Penyucian tertinggi. Semua resi, serta resi ilahi Narada, Asita, Devala, dan Vyasa [Meskipun Narada dan resi-resi lainnya sudah disebutkan dengan kata-kata \'semua resi\', mereka tetap disebut secara terpisah karena keunggulan mereka. Asita adalah ayah dari Devala.] menyebut Engkau Pribadi ilahi yang abadi, Tuhan Purba, yang Tak Lahir, yang Mahahadir; dan Engkau sendiri memang memberitahukan hal ini.”', 384);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1921, 'Arjuna berkata, “Engkau adalah Brahman Tertinggi, Cahaya Tertinggi, dan Penyucian Tertinggi. Semua peramal menyatakan Engkau sebagai Pribadi ilahi yang abadi, Tuhan Purba, yang tak lahir, dan mahakuasa. Demikian pula para bijak ilahi Narada, Asita, Devala, dan Vyasa menyatakan. Engkau Sendiri juga menyatakan ini.”', 385);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1922, 'Arjuna berkata, “Engkau adalah Brahman tertinggi, Cahaya tertinggi, Penyucian tertinggi. Semua resi, serta resi ilahi Narada, Asita, Devala, dan Vyasa [Meskipun Narada dan resi-resi lainnya sudah disebutkan dengan kata-kata \'semua resi\', mereka tetap disebut secara terpisah karena keunggulan mereka. Asita adalah ayah dari Devala.] menyebut Engkau Pribadi ilahi yang abadi, Tuhan Purba, yang Tak Lahir, yang Mahahadir; dan Engkau sendiri memang memberitahukan hal ini.”', 385);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1926, 'Aku menganggap benar semua yang telah Engkau katakan kepadaku, wahai Krishna. Sungguh, wahai Tuhan, baik para dewa maupun para iblis tidak mengetahui manifestasi-Mu.', 386);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1927, 'Wahai Kesava, aku menerima sebagai benar semua yang telah Engkau katakan kepadaku. Tentu saja, wahai Tuhan, baik para dewa maupun para iblis tidak dapat memahami kemuliaan-Mu.', 386);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1931, 'Wahai Pribadi Tertinggi, wahai Pencipta makhluk, wahai Tuhan makhluk, wahai Dewa para dewa, wahai Penguasa alam semesta, Engkau Sendiri mengenal Diri-Mu sendiri.', 387);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1932, 'Wahai Pribadi Tertinggi, Pencipta makhluk hidup, Penguasa makhluk hidup, Dewa para dewa, Penguasa dunia, Engkau sendiri mengenal Diri-Mu sendiri.', 387);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1936, 'Engkau harus memberitahukan kepada-Ku tanpa ragu manifestasi ilahi-Mu di mana Engkau berdiam, melingkupi semua dunia ini.', 388);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1937, 'Berkenanlah berbicara sepenuhnya tentang manifestasi ilahi-Mu sendiri, yang melaluinya Engkau ada meliputi dunia-dunia ini.', 388);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1941, 'Bagaimana aku, hamba-Mu, dapat mengenal-Mu dengan terus-menerus bermeditasi pada-Mu? Dan dengan cara apa, wahai Tuhan, aku harus bermeditasi pada-Mu?', 389);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1942, 'Wahai Yogi, bagaimana aku akan mengenal-Mu dengan tetap bermeditasi? Dan melalui objek-objek apa, wahai Tuhan, aku harus bermeditasi pada-Mu?', 389);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1946, 'Berbicaralah kepadaku lagi secara lengkap, wahai Krishna, tentang sifat-sifat dan kemuliaan-Mu. Karena aku tidak kenyang mendengar kata-kata ambrosia-Mu.', 390);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1947, 'Wahai Janardana, tolong ceritakan kepadaku lagi secara rinci yoga dan manifestasi ilahi-Mu. Karena, saat mendengar kata-kata-Mu yang seperti nektar, aku tidak puas.', 390);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1951, 'Sang Raja berkata, “Memang, Aku akan memberitahumu, wahai Arjuna, manifestasi-Ku yang baik—yang paling menonjol di antaranya. Tidak ada akhir dari luasnya mereka.”', 391);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1952, 'Yang Mahamulia berkata, wahai yang terbaik dari Kurawa, sekarang, sesuai dengan kepentingannya, Aku akan menjelaskan kepadamu kemuliaan-Ku sendiri, yang sesungguhnya ilahi. Tidak ada akhir dari manifestasi-Ku.', 391);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1956, 'Akulah Diri, wahai Arjuna, bersemayam di hati semua makhluk; Akulah awal, tengah, dan akhir semua makhluk.', 392);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1957, 'Wahai Gudakesa, Aku adalah Diri yang bersemayam di hati semua makhluk, dan Aku adalah awal, tengah, dan akhir semua makhluk.', 392);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1961, 'Dari para Adityas, Akulah Wisnu; dari benda-benda bercahaya, Akulah matahari yang bersinar. Dari para Marut, Akulah Marici, dan di antara rasi bintang, Akulah bulan.', 393);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1962, 'Di antara para Adityas [yaitu Dhata, Mitra, Aryama, Rudra, Varuna, Surya, Bhaga, Vivasvan, Pusa, Savita, Tvasta, dan Wisnu.-Tr.], Aku adalah Wisnu; di antara para penerang, matahari yang bersinar; di antara (empat puluh sembilan) Marut [Tujuh kelompok Marut adalah Avaha, Pravaha, Vivaha, Paravaha, Udvaha, Samvaha, dan Parivaha.-Tr.], Aku adalah Marici; di antara bintang-bintang, Aku adalah bulan.', 393);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1966, 'Dari Veda, Akulah Samaveda; dari dewa, Akulah Indra; dari organ indra, Akulah pikiran; dan dari makhluk hidup, Akulah kesadaran.', 394);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1967, 'Di antara Veda, Aku adalah Sama-Veda; di antara dewa, Aku adalah Indra. Di antara organ, Aku adalah pikiran, dan Aku adalah kecerdasan pada makhluk hidup.', 394);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1971, 'Dari para Rudra, Akulah Sankara. Dari para Yaksa dan Rakshasa, Akulah Penguasa Kekayaan (Kubera). Dari para Vasu, Akulah Agni; dari gunung-gunung, Akulah Meru.', 395);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1972, 'Di antara para Rudra, Aku adalah Aja, Ekapada, Ahirbudhnya, Pinaki, Aparajita, Tryam-baka, Mahesvara, Vrsakapi, Sambhu, Harana, dan Isvara; di antara para Yaksa dan goblin, Aku adalah Kubera; di antara para Vasu, Aku adalah Api; dan di antara gunung-gunung, Aku adalah Meru.', 395);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1976, 'Dari para imam keluarga, wahai Arjuna, ketahuilah Aku sebagai Brhaspati utama; dari para jenderal, Akulah Skanda; dari waduk air, Akulah samudra.', 396);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1977, 'Wahai putra Prtha, ketahuilah Aku sebagai Brhaspati, yang terkemuka di antara para imam raja. Di antara para komandan pasukan, Aku adalah Skanda; di antara hamparan air yang luas, Aku adalah laut.', 396);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1981, 'Dari para bijak agung, Akulah Bhrgu; dari kata-kata, Akulah kata bersuku kata tunggal Om; dari kurban, Akulah kurban Japa; dari benda-benda tak bergerak, Akulah Himalaya.', 397);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1982, 'Di antara para resi agung, Aku adalah Bhrgu; dari kata-kata, Aku adalah suku kata tunggal (Om). Di antara ritual, Aku adalah ritual Japa dari yang tak bergerak, Himalaya.', 397);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1986, 'Dari pohon-pohon Akulah Asvattha; dari para bijak surgawi, Akulah Narada; dari para Gandharva, Akulah Citraratha; dari yang disempurnakan, Akulah Kapila.', 398);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1987, 'Di antara semua pohon, Aku adalah pohon Ashvattha, dan di antara para resi ilahi, Narada. Di antara para Gandharva, Aku adalah Citraratha; di antara yang disempurnakan, Aku adalah resi Kapila.', 398);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1991, 'Dari kuda, ketahuilah Aku sebagai Uccaihsravas, yang lahir dari nektar. Dari gajah raja, Akulah Airavata, dan dari manusia, Akulah raja.', 399);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1992, 'Di antara kuda, ketahuilah Aku sebagai Uccaihsravas, lahir dari nektar; Airavata di antara gajah-gajah agung; dan di antara manusia, Raja manusia. [Uccaihsravas dan Airavata masing-masing adalah kuda dan gajah ilahi Indra.]', 399);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1996, 'Dari senjata, Akulah Vajra (halilintar); dari sapi, Akulah Kamadhuk; Akulah Kandarpa, penyebab keturunan; dari ular, Akulah Vasuki.', 400);
INSERT INTO verse_translations (id, content, verse_id) VALUES (1997, 'Di antara senjata Aku adalah halilintar; di antara sapi Aku adalah Kamadhenu. Aku adalah Kandarpa, Sang Leluhur, dan di antara ular Aku adalah Vasuki.', 400);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2001, 'Dari ular, Akulah Ananta; dari makhluk air, Akulah Varuna; dari leluhur, Akulah Aryama; dari penakluk, Akulah Yama.', 401);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2002, 'Di antara ular, Aku adalah Ananta; di antara dewa air, Varuna; di antara leluhur, Aryama; dan di antara pemelihara hukum dan ketertiban, Aku adalah Yama (Raja kematian).', 401);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2006, 'Dari para Daitya, Akulah Prahlada; dari para penghitung, Akulah Kematian; dari binatang buas, Akulah singa; dan dari burung-burung, Akulah Garuda, putra Vinata.', 402);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2007, 'Di antara iblis, Aku adalah Prahlada, dan di antara penghitung waktu, Aku adalah Waktu. Di antara binatang, Aku adalah singa, dan di antara burung, Aku adalah Garuda.', 402);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2011, 'Dari benda-benda bergerak, Akulah angin; dari mereka yang memegang senjata, Akulah Rama; dari nelayan, Akulah Makara; dan dari sungai-sungai, Akulah Gangga.', 403);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2012, 'Dari yang memurnikan, Aku adalah udara; di antara para pemegang senjata, Aku adalah Rama. Di antara ikan, Aku adalah hiu; di antara sungai, Aku adalah Gangga.', 403);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2016, 'Dari makhluk-makhluk, Akulah awal, tengah, dan akhir, wahai Arjuna. Dari ilmu pengetahuan, Akulah ilmu tentang Diri (diri individu dan Universal). Dari mereka yang berdebat, Akulah penalaran yang adil.', 404);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2017, 'Wahai Arjuna, dari ciptaan Aku adalah awal, tengah, dan akhir; Aku adalah pengetahuan tentang Diri di antara pengetahuan; dari mereka yang berdebat, Aku adalah vada.', 404);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2021, 'Dari huruf-huruf, Akulah huruf \'A\'; dari kata majemuk, Akulah dvandva (kopulatif); Akulah waktu yang tak termusnahkan; Akulah pencipta, menghadap ke semua sisi.', 405);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2022, 'Dari huruf-huruf, Aku adalah huruf A, dan dari kelompok kata majemuk, Aku adalah kata majemuk yang disebut Dvandva. Aku Sendiri adalah waktu yang tak terbatas; Aku adalah Pemberi dengan wajah di mana-mana.', 405);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2026, 'Akulah juga kematian, yang merenggut segalanya. Akulah asal mula semua yang akan lahir. Di antara wanita, Akulah ketenaran, kemakmuran, ucapan, ingatan, kecerdasan, ketahanan, dan pengampunan.', 406);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2027, 'Dan Aku adalah Kematian, penghancur segalanya; dan pemberi kemakmuran bagi mereka yang ditakdirkan untuk makmur. Dari yang feminin [Narinam mungkin berarti \'dari kualitas feminin\'. Menurut Sridhara Swami dan S., kata-kata ketenaran dll. menandakan dewi dari kualitas masing-masing. Menurut M.S. ketujuh dewi ini adalah istri dewa Dharma.-Tr.] (Aku adalah) ketenaran, kecantikan, ucapan, ingatan, kecerdasan, ketahanan, dan kesabaran.', 406);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2031, 'Dari himne Saman, Akulah Brhatsaman; dari meter, Akulah Gayatri; dari bulan, Akulah Margasira (Nov-Des); dan dari musim, Akulah musim bunga.', 407);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2032, 'Aku juga adalah Brhat-sama dari Sama-mantra; dari meter, Gayatri. Dari bulan, Aku adalah Marga-sirsa, dan dari musim, musim semi.', 407);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2036, 'Dari para penipu, Akulah perjudian; Akulah kecemerlangan yang cemerlang; Akulah kemenangan, Akulah usaha; Akulah kemurahan hati yang murah hati.', 408);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2037, 'Dari para penipu, Aku adalah perjudian; Aku adalah akhir yang tak tertahankan dari yang perkasa. Aku adalah keunggulan, Aku adalah usaha, Aku adalah kualitas sattva dari mereka yang memiliki sattva.', 408);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2041, 'Dari para Vrsni, Akulah Vasudewa; dari para Pandawa, Akulah Arjuna; dari para bijak, Akulah Vyasa; dan dari para peramal, Akulah Usana (Sukra).', 409);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2042, 'Dari Vrsni, Aku adalah Vasudewa; dari Pandawa, Dhananjaya (Arjuna). Dari yang bijaksana, Aku adalah Vyasa; dari yang mahatahu, Usanas yang mahatahu.', 409);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2046, 'Dari mereka yang menghukum, Akulah prinsip hukuman. Dari mereka yang mencari kemenangan, Akulah kebijakan. Dari rahasia, Akulah juga keheningan. Dan dari yang bijaksana, Akulah kebijaksanaan.', 410);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2047, 'Dari para penghukum, Aku adalah tongkat; Aku adalah kebijakan yang benar dari mereka yang ingin menaklukkan. Dan dari hal-hal rahasia, Aku sungguh keheningan; Aku adalah pengetahuan orang bijak.', 410);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2051, 'Akulah juga benih semua makhluk, wahai Arjuna. Tidak ada yang bergerak atau tidak bergerak yang ada tanpa Aku.', 411);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2052, 'Selain itu, wahai Arjuna, Aku adalah benih semua makhluk. Tidak ada apa pun, yang bergerak atau tidak bergerak, yang dapat ada tanpa-Ku.', 411);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2056, 'Tidak ada batas manifestasi ilahi-Ku. Di sini, Aku telah secara singkat menggambarkan luasnya manifestasi tersebut.', 412);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2057, 'Wahai penghancur musuh, tidak ada batas manifestasi ilahi-Ku. Penjelasan tentang manifestasi-Ku ini, bagaimanapun, telah Aku nyatakan sebagai ilustrasi.', 412);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2061, 'Makhluk apa pun yang memiliki kekuatan, atau kemegahan, atau energi, ketahuilah itu berasal dari fragmen kekuatan-Ku.', 413);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2062, 'Objek apa pun [semua makhluk hidup] yang sungguh-sungguh diberkahi dengan keagungan, memiliki kemakmuran, atau energik, ketahuilah masing-masing sebagai memiliki bagian dari kekuatan-Ku sebagai sumbernya.', 413);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2066, 'Tetapi apa gunanya semua pengetahuan yang luas ini bagimu, wahai Arjuna? Aku menopang seluruh alam semesta ini hanya dengan sebagian kecil dari kekuatan-Ku.', 414);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2067, 'Atau, di sisi lain, apa gunanya Anda mengetahui hal ini secara luas, wahai Arjuna? Aku tetap menopang seluruh ciptaan ini dengan cara khusus dengan sebagian dari Diri-Ku.', 414);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2071, 'Arjuna berkata, “Sebagai tanda perkenan kepadaku, Engkau telah memberitahuku misteri terdalam mengenai Diri; dengan itu, delusiku ini telah lenyap.”', 415);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2072, 'Arjuna berkata, “Ilusi saya ini telah hilang sebagai akibat dari ucapan yang paling rahasia dan berkaitan dengan Diri, yang diucapkan oleh Anda demi keuntungan saya.”', 415);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2076, 'Wahai Krsna, aku telah mendengar secara rinci tentang asal mula dan kehancuran semua makhluk, yang berasal dari-Mu, serta kebesaran-Mu yang tak berubah.', 416);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2077, 'Wahai engkau yang bermata seperti daun teratai, aku telah mendengar secara rinci darimu asal mula dan kehancuran makhluk hidup, serta kemuliaanmu yang tak lapuk.', 416);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2081, 'Wahai Tuhan Yang Mahatinggi, sebagaimana Engkau telah menggambarkan Diri-Mu, demikianlah Engkau. Aku ingin melihat wujud ilahi-Mu, wahai Pribadi Tertinggi.', 417);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2082, 'Wahai Tuhan Yang Mahatinggi, demikianlah, sebagaimana Engkau telah berfirman tentang Diri-Mu. Wahai Pribadi Tertinggi, aku ingin melihat wujud ilahi-Mu.', 417);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2086, 'Jika engkau mengira, wahai Tuhan, bahwa itu dapat kulihat, maka, wahai Tuhan Yoga, nyatakanlah Diri-Mu sepenuhnya kepadaku.', 418);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2087, 'Wahai Tuhan, jika Engkau menganggap mungkin bagi saya untuk melihat-Mu, maka, wahai Tuhan Yoga, tunjukkanlah kepada saya Diri-Mu yang abadi.', 418);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2091, 'Sang Raja berkata, “Lihatlah, wahai Arjuna, wujud-Ku, ratusan ribu, beraneka ragam, ilahi, bervariasi warna dan bentuknya.”', 419);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2092, 'Yang Mahamulia berkata, “Wahai putra Prtha, lihatlah wujud-Ku yang ratusan dan ribuan, berbagai jenis, surgawi, dan berbagai warna dan bentuk.”', 419);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2096, 'Lihatlah, wahai Arjuna, para Aditya, para Vasu, para Rudra, kedua Aswin, dan para Marut—banyak keajaiban yang belum pernah terlihat sebelumnya.', 420);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2097, 'Lihatlah para Adityas, para Vasus, para Rudras, kedua Ashvins, dan para Maruts. Wahai keturunan dinasti Bharata, lihatlah juga banyak keajaiban yang belum pernah terlihat sebelumnya.', 420);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2101, 'Lihatlah di sini, wahai Arjuna, seluruh alam semesta dengan benda-benda bergerak dan tak bergeraknya, semua terkumpul di satu titik dalam tubuh Dewa para dewa, dan apa pun yang ingin kau lihat.', 421);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2102, 'Lihatlah sekarang, wahai Gudakesa, seluruh Alam Semesta bersama dengan yang bergerak dan yang tidak bergerak, terkonsentrasi di tempat yang sama di dalam tubuh-Ku, serta apa pun yang ingin Anda lihat.', 421);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2106, 'Tetapi engkau tidak akan dapat melihat Aku dengan matamu sendiri. Aku memberimu mata ilahi, jadi lihatlah Yoga Kekuasaan-Ku!', 422);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2107, 'Tetapi Anda tidak dapat melihat-Ku hanya dengan mata Anda ini. Aku menganugerahkan kepadamu mata ilahi; lihatlah Yoga ilahi-Ku.', 422);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2111, 'Sanjaya berkata, “Wahai Raja, setelah berbicara, Sri Krishna, Tuhan Yoga yang agung, kemudian menyingkapkan kepada Arjuna wujud tertinggi, wujud Tuhan.”', 423);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2112, 'Sanjaya berkata, “Wahai Raja, setelah berbicara demikian, setelah itu, Hari, Sang Guru Agung Yoga, menunjukkan kepada putra Prtha bentuk ilahi tertinggi.”', 423);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2116, 'Dengan mulut dan mata yang tak terhitung jumlahnya, banyak aspek menakjubkan, banyak ornamen ilahi, dan banyak senjata ilahi.', 424);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2117, 'Memiliki banyak wajah dan mata, memiliki banyak pemandangan menakjubkan, dihiasi dengan banyak ornamen surgawi, dan memegang banyak senjata surgawi yang terangkat;', 424);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2121, 'Mengenakan karangan bunga dan pakaian surgawi, diolesi parfum ilahi, penuh keajaiban, cemerlang, tak terbatas, dan menghadap ke semua arah.', 425);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2122, 'Mengenakan kalung dan pakaian surgawi, diurapi dengan wewangian surgawi, berlimpah ruah dengan segala macam keajaiban, cemerlang, tak terbatas, dan dengan wajah di semua sisi.', 425);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2126, 'Jika seribu matahari terbit sekaligus di langit, kemegahan yang dihasilkan akan menyerupai kemegahan Yang Maha Kuasa itu.', 426);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2127, 'Jika cahaya seribu matahari menyala secara bersamaan di langit, itu mungkin menyerupai cahaya Yang Maha Luhur itu.', 426);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2131, 'Di sana, Arjuna melihat seluruh alam semesta, dengan berbagai divisinya terkumpul di satu titik dalam tubuh Dewa para dewa.', 427);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2132, 'Pada saat itu, Pandawa melihat, dalam tubuh Dewa para dewa, seluruh alam semesta yang berbeda-beda bersatu dalam satu kesatuan.', 427);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2136, 'Kemudian, Arjuna, diliputi keheranan, rambutnya berdiri tegak, menundukkan kepalanya kepada Tuhan dan berbicara dengan tangan terlipat.', 428);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2137, 'Kemudian, dipenuhi keheranan dan dengan rambut berdiri tegak, Arjuna, menundukkan kepalanya kepada Tuhan, berkata dengan tangan terlipat:', 428);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2141, 'Arjuna berkata, “Wahai Tuhan, aku melihat di dalam tubuh-Mu semua dewa, dan semua pasukan makhluk yang beragam—Brahma, Siwa, yang ada dalam Brahma, para bijak, dan ular-ular yang bercahaya.”', 429);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2142, 'Arjuna berkata, “Wahai Tuhan, aku melihat di dalam tubuh-Mu semua dewa, serta pasukan dari berbagai golongan makhluk; Brahma sang penguasa, duduk di atas singgasana teratai, dan semua resi surgawi serta ular-ular.', 429);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2146, 'Dengan tangan, perut, mulut, dan mata yang beraneka ragam, aku melihat wujud-Mu yang tak terbatas di semua sisi. Aku tidak melihat akhir, tidak tengah, pun tidak awal dari Engkau, wahai Tuhan alam semesta, wahai Engkau yang Berwujud Universal!', 430);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2147, 'Aku melihat-Mu dengan banyak lengan, perut, mulut, dan mata; dengan bentuk tak terbatas di sekeliling. Wahai Penguasa Alam Semesta, wahai Pribadi Kosmis, aku tidak melihat batas, tengah, maupun awal!', 430);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2151, 'Aku melihat-Mu sebagai kumpulan cahaya, bersinar di mana-mana, dengan mahkota, gada, dan cakram, sulit dilihat, membara seperti api yang menyala dan matahari, dan tak terukur.', 431);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2152, 'Aku melihat-Mu mengenakan mahkota, memegang gada, dan memegang cakram; kumpulan kecemerlangan yang bersinar di sekeliling, sulit dilihat dari semua sisi, memiliki cahaya api yang membakar dan matahari, dan tak terukur.', 431);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2156, 'Engkau adalah Yang Tak Termusnahkan, Yang Tertinggi yang harus disadari. Engkau adalah Substratum Tertinggi alam semesta ini. Engkau tak berubah, penjaga hukum abadi; Aku tahu Engkau adalah Pribadi Tertinggi yang abadi.', 432);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2157, 'Engkau adalah Yang Tak Berubah, Yang Tertinggi yang harus dikenal; Engkau adalah penyimpan yang paling sempurna dari Alam Semesta ini. Engkau adalah Yang Tak Terhapuskan, Pelindung agama yang selalu ada; Engkau adalah Pribadi yang abadi. Ini adalah keyakinan saya.', 432);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2161, 'Aku melihat-Mu sebagai yang tak berawal, tak bertengah, atau tak berakhir; kekuatan-Mu tak terbatas. Engkau diberkahi dengan lengan yang tak terhitung jumlahnya. Matahari dan bulan adalah mata-Mu, dan mulut-Mu mengeluarkan api yang membakar. Dengan cahaya-Mu sendiri, Engkau memperingatkan seluruh alam semesta.', 433);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2162, 'Aku melihat-Mu sebagai yang tak berawal, tak bertengah, atau tak berakhir, memiliki keberanian tak terbatas, dengan lengan yang tak terhitung, matahari dan bulan sebagai mata, mulut seperti api yang berkobar, dan memanaskan alam semesta ini dengan kecemerlangan-Mu sendiri.', 433);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2166, 'Engkau sendiri telah melingkupi ruang antara langit dan bumi, dan semua penjuru. Melihat wujud-Mu yang menakjubkan dan menakutkan, wahai Mahatman, tiga dunia sangat diliputi ketakutan.', 434);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2167, 'Memang, ruang perantara antara langit dan bumi ini, serta semua arah, diliputi oleh-Mu sendiri. Wahai Yang Agung, ketiga dunia dilanda ketakutan saat melihat wujud-Mu yang aneh dan menakutkan ini.', 434);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2171, 'Sungguh, para pasukan Dewa masuk ke dalam-Mu. Beberapa, dalam ketakutan, memuji-Mu dengan tangan terlipat, berseru "Salam!" Pasukan para bijak agung dan Siddha memuji-Mu dengan himne yang penuh makna.', 435);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2172, 'Kelompok-kelompok dewa itu masuk ke dalam-Mu; dilanda ketakutan, beberapa memuji-Mu dengan telapak tangan terlipat. Kelompok-kelompok resi agung dan makhluk yang sempurna memuji-Mu dengan himne yang rumit, berkata, \'Semoga baik!\'', 435);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2176, 'Para Rudra, para Aditya, para Vasu, para Sadhya, para Visva, para Aswin, para Marut, dan para Leluhur, serta pasukan Gandharva, Yaksa, Asura, dan Siddha—semuanya memandang-Mu dengan takjub.', 436);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2177, 'Mereka yang adalah Rudras, Adityas, Vasus, dan Sadhyas, Visvedevas, kedua Ashvins, Maruts, dan Usmapas, dan pasukan Gandharvas, Yaksas, setan, dan Siddhas—semuanya memandang-Mu, sungguh-sungguh dilanda keheranan.', 436);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2181, 'Melihat wujud-Mu yang agung dengan banyak mulut dan mata, banyak lengan, paha, dan kaki, banyak perut, dan mengerikan dengan banyak gigi, dunia gemetar, dan aku pun bergetar, wahai yang berlengan perkasa.', 437);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2182, 'Wahai yang perkasa, melihat wujud-Mu yang besar dengan banyak mulut, mata, lengan, paha, kaki, perut, dan gigi, makhluk-makhluk dilanda ketakutan, begitu pula aku.', 437);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2186, 'Ketika aku memandang-Mu menyentuh Surga Tertinggi, bersinar, beraneka warna, dengan mulut menganga dan mata yang besar, bercahaya, batinku gemetar ketakutan. Aku tidak dapat menemukan pegangan atau kedamaian, wahai Wisnu.', 438);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2187, 'Wahai Wisnu, sesungguhnya, setelah melihat wujud-Mu yang menyentuh langit, membara dengan banyak warna, bermulut terbuka, dengan mata yang menyala-nyala dan besar, pikiranku menjadi ketakutan dan aku tidak menemukan keteguhan dan kedamaian.', 438);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2191, 'Melihat mulut-Mu, memperlihatkan taring yang menggetarkan dan tampak seperti api pemusnah akhir, aku tidak mengenal penjuru bumi pun tidak menemukan ketenangan. Berbelas kasihlah, wahai Tuhan para Dewa! Wahai Tempat Bersemayam alam semesta!', 439);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2192, 'Hanya dengan melihat mulut-Mu yang dibuat mengerikan dengan gigi-gigi mereka dan menyerupai api Pembubaran, aku telah kehilangan indra arahku dan tidak menemukan kenyamanan. Berbelas kasihlah, wahai Tuhan para dewa, wahai Kediaman Alam Semesta.', 439);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2196, 'Semua putra Dhrtarastra ini, bersama dengan pasukan raja-raja, Bhisma, Drona, dan Karna, bersama dengan prajurit terkemuka dari pihak kita,', 440);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2197, 'Dan ke dalam-Mu masuk semua putra Dhrtarastra itu, bersama dengan kerumunan para penguasa bumi, Bhisma, Drona, dan putra seorang Suta, Karna, bersama dengan prajurit terkemuka kami.', 440);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2201, 'Bergegas masuk ke dalam mulut-Mu yang menakutkan dengan taring yang mengerikan, menghancurkan beberapa yang terjebak di antara gigi menjadi bubuk.', 441);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2202, 'Mereka dengan cepat memasuki mulut-Mu yang mengerikan dengan gigi-gigi kejam! Beberapa terlihat menempel di sela-sela gigi, dengan kepala hancur!', 441);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2206, 'Sebagaimana banyak arus sungai mengalir menuju samudra, demikian pula para pahlawan dunia manusia ini masuk ke dalam mulut-Mu yang menyala-nyala.', 442);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2207, 'Sebagaimana banyak arus air sungai bergegas menuju laut saja, demikian pula para pahlawan dunia manusia itu memasuki mulut-Mu yang membara.', 442);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2211, 'Sebagaimana ngengat bergegas dengan cepat ke dalam api yang menyala, menyebabkan kehancuran mereka, demikian pula orang-orang ini bergegas dengan cepat ke dalam mulut-Mu, menyebabkan kehancuran mereka.', 443);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2212, 'Sebagaimana ngengat bergegas dengan kecepatan tinggi ke dalam api yang membakar untuk kehancuran, demikian pula makhluk hidup bergegas ke dalam mulut-Mu dengan kecepatan tinggi untuk kehancuran.', 443);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2216, 'Melahap semua dunia di setiap sisi dengan mulut-Mu yang menyala, Engkau menjilatnya. Sinar api-Mu, memenuhi seluruh alam semesta dengan cahayanya, membakarnya, wahai Wisnu.', 444);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2217, 'Engkau menjilat bibir-Mu saat Engkau melahap semua makhluk dari setiap sisi dengan mulut yang menyala yang memenuhi seluruh dunia dengan panas.', 444);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2221, 'Beritahukanlah kepadaku, siapakah Engkau dengan wujud yang mengerikan ini? Salam kepada-Mu, wahai Tuhan Yang Mahatinggi. Berbelas kasihlah kepadaku. Aku ingin mengenal-Mu, Yang Purba. Aku tidak mengetahui aktivitas-Mu.', 445);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2222, 'Beritahukanlah kepadaku siapa Engkau, dengan wujud yang garang. Salam kepada-Mu, wahai Tuhan Tertinggi; berbelas kasihlah. Aku ingin sepenuhnya mengenal-Mu, yang adalah Yang Utama. Karena aku tidak memahami tindakan-Mu!', 445);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2226, 'Sang Raja berkata, “Aku adalah Waktu yang menghancurkan dunia. Memanifestasikan diri-Ku sepenuhnya, Aku telah mulai menghancurkan dunia di sini. Bahkan tanpamu, tidak seorang pun dari para prajurit yang berbaris di barisan musuh akan bertahan.”', 446);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2227, 'Yang Mahamulia berkata, “Aku adalah Waktu penghancur dunia, yang telah tumbuh dewasa dan kini terlibat dalam memusnahkan makhluk hidup. Bahkan tanpamu, semua prajurit yang berbaris di pasukan yang berhadapan akan musnah!”', 446);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2231, 'Oleh karena itu, bangkitlah, raih kemuliaan. Kalahkan musuhmu, nikmati kerajaan yang makmur. Oleh-Ku mereka telah terbunuh. Engkau hanyalah alat, wahai Arjuna, engkau pemanah ulung!', 447);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2232, 'Oleh karena itu, bangkit dan raih ketenaran; dan, mengalahkan musuh, nikmati kerajaan yang makmur. Mereka ini telah terbunuh oleh-Ku bahkan sebelumnya; jadilah engkau hanya alat, wahai Savyasacin (Arjuna).', 447);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2236, 'Bunuh Drona, Bhisma, Jayadratha, Karna, serta para prajurit perkasa lainnya yang telah ditentukan oleh-Ku. Janganlah merasa cemas. Berperanglah; engkau akan mengalahkan lawan-lawanmu dalam pertempuran.', 448);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2237, 'Anda menghancurkan Drona, Bhisma, Jayadratha, Karna, dan para prajurit heroik lainnya yang telah terbunuh oleh-Ku. Jangan takut. Berperanglah! Anda akan menaklukkan musuh dalam pertempuran.', 448);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2241, 'Sanjaya berkata, Setelah mendengar ucapan Krsna ini, Arjuna bersujud kepada-Nya; dan gemetar ketakutan, ia bersujud lagi, dan dengan tangan terlipat, suaranya tercekat karena emosi, ia berbicara kepada Krsna.', 449);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2242, 'Sanjaya berkata: Mendengar ucapan Kesava ini, Kiriti (Arjuna), dengan tangan terlipat dan gemetar, bersujud, lalu, dengan suara yang tersendat, menundukkan diri dan diliputi rasa takut, berkata lagi kepada Krsna:', 449);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2246, 'Arjuna berkata, Benar, wahai Krsna, bahwa pujian-Mu seharusnya menggerakkan dunia untuk sukacita dan cinta. Para rakshasa melarikan diri ketakutan di semua sisi, dan semua pasukan siddha bersujud kepada-Mu.', 450);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2247, 'Arjuna berkata, “Wahai Hrsikesa, pantaslah dunia bergembira dan tertarik oleh pujian-Mu; bahwa para Raksasa, yang dilanda ketakutan, lari ke segala arah; dan bahwa semua kelompok Siddha bersujud kepada-Mu.”', 450);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2251, '(a) Dan mengapa mereka tidak, wahai Mahatma, bersujud kepada-Mu yang agung, yang adalah Pencipta pertama, bahkan dari Brahma?\n(b) Wahai Yang Tak Terbatas, Tuhan para dewa, wahai Engkau yang memiliki alam semesta sebagai tempat berdiam-Mu! Engkau adalah diri individual yang tak termusnahkan, yang ada dan yang tidak ada, dan yang melampaui keduanya.', 451);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2252, 'Dan mengapa mereka tidak bersujud kepada-Mu, wahai Yang Agung, yang lebih besar dari segalanya dan yang merupakan Pencipta pertama bahkan dari Brahma! Wahai Yang Tak Terbatas, Tuhan tertinggi, Tempat Bersemayam Alam Semesta, Engkau adalah Yang Tak Berubah, ada dan tiada, dan yang Transendental.', 451);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2256, '(a) Engkau adalah Tuhan Purba dan Yang Kuno. Engkau adalah tempat peristirahatan tertinggi alam semesta...\n(b)...Engkau adalah Yang Mengetahui dan objek pengetahuan, dan Tempat Bersemayam Tertinggi. Melalui Engkau, wahai yang tak terbatas bentuknya, alam semesta ini diliputi.', 452);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2257, 'Engkau adalah Dewa yang utama, Pribadi yang kuno; Engkau adalah Perlindungan tertinggi dunia ini. Engkau adalah yang mengetahui dan objek pengetahuan, dan Kediaman tertinggi. Wahai Engkau dengan bentuk tak terbatas, Engkau meliputi Alam Semesta!', 452);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2261, '(a) Engkau adalah Yayu, Yama, Agni, Waruna, Sashanka, Prajapati, dan kakek buyut.\n(b) Salam bagi-Mu seribu kali! Salam bagi-Mu lagi dan lagi!', 453);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2262, 'Engkau adalah udara, kematian, api, dewa air, bulan, Tuhan makhluk hidup, dan kakek buyut. Salam bagimu seribu kali! Salam bagimu lagi dan lagi! Salam!', 453);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2266, '(a) Salam bagi-Mu dari depan dan belakang! Salam bagi-Mu dari semua sisi, wahai Segala!.\n\n(b) . O Engkau yang kekuatan dan tindakan heroiknya tak terbatas! Engkau meliputi semua makhluk, dan oleh karena itu, Engkau adalah segalanya.', 454);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2267, 'Salam bagimu di Timur dan di belakang. Salam bagimu di semua sisi memang, wahai Segala! Engkau memiliki kekuatan tak terbatas dan kepahlawanan tak terbatas. Engkau meliputi segalanya; demikianlah Engkau adalah segalanya!', 454);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2271, 'Tidak menyadari kebesaran-Mu, dan entah karena kelalaian atau cinta, atau menganggap-Mu sebagai teman, apa pun yang telah kukatakan dengan kasar, seperti \'Wahai Krsna, wahai Yadawa, wahai Kawan,\'', 455);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2272, 'Tanpa mengetahui kebesaran-Mu, apa pun yang kukatakan dengan tergesa-gesa, tanpa sengaja, atau bahkan karena keakraban, menganggap-Mu sebagai teman, menyapa-Mu sebagai \'Wahai Krsna,\' \'Wahai Yadawa,\' \'Wahai teman,\' dll.', 455);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2276, 'Dan perlakuan tidak hormat apa pun yang telah ditunjukkan kepada-Mu dalam candaan, saat bermain, beristirahat, duduk, atau makan, baik sendirian maupun di hadapan orang lain, wahai Acyuta—aku memohon pengampunan-Mu, Engkau yang tak terpahami.', 456);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2277, 'Dan bahwa Engkau telah diperlakukan tidak sopan karena bercanda—saat berjalan, saat di tempat tidur, saat duduk, saat makan, secara pribadi, atau, wahai Acyuta, bahkan di depan umum—untuk itu, aku memohon maaf kepada-Mu, Yang Tak Terpahami.', 456);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2281, 'Engkau adalah ayah dunia ini, dari semua yang bergerak dan tidak bergerak. Engkau adalah gurunya dan yang paling layak dihormati. Tidak ada yang setara dengan-Mu. Bagaimana mungkin ada di tiga dunia yang lebih besar dari-Mu, wahai Yang Keagungan-Nya tak tertandingi?', 457);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2282, 'Engkau adalah Ayah dari semua makhluk, baik yang bergerak maupun yang tidak bergerak; bagi-Mu, Sang Guru, mereka layak disembah, dan Engkau lebih besar dari seorang guru. Tidak ada yang setara dengan-Mu; bagaimana mungkin ada yang lebih besar bahkan di ketiga dunia, wahai Engkau yang memiliki kekuatan tak tertandingi?', 457);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2286, 'Oleh karena itu, bersujud dan merendahkan tubuhku, aku memohon belas kasihan-Mu, wahai Tuhan yang patut disembah. Seperti seorang ayah mendengarkan putranya atau seorang teman kepada temannya, pantaslah, wahai Tuhan, Engkau, yang kukasihi, bersabar denganku yang Engkau kasihi.', 458);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2287, 'Oleh karena itu, dengan bersujud dan merendahkan tubuhku, aku berusaha menenangkan-Mu yang adalah Tuhan dan patut disembah. Wahai Tuhan, Engkau seharusnya memaafkan kesalahan-kesalahanku seperti seorang ayah akan memaafkan kesalahan seorang putra, seperti seorang teman akan memaafkan kesalahan seorang teman, dan seperti seorang kekasih akan memaafkan kesalahan seorang kekasih.', 458);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2291, 'Melihat sesuatu yang belum pernah dilihat sebelumnya, aku sangat gembira. Tetapi pikiranku juga diliputi rasa takut. Tunjukkan kepadaku, wahai Tuhan! Bentuk-Mu yang lain. Wahai Tuhan para dewa! Berbelas kasihlah, wahai Tempat Bersemayam alam semesta!', 459);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2292, 'Aku gembira melihat sesuatu yang belum pernah kulihat sebelumnya, dan pikiranku dilanda ketakutan. Wahai Tuhan, tunjukkanlah wujud itu; wahai Tuhan Yang Mahatinggi, wahai Kediaman Alam Semesta, berbelas kasihlah!', 459);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2296, 'Aku ingin melihat-Mu selalu seperti sebelumnya, dengan mahkota dan gada dan cakram di tangan. Ambillah lagi wujud berlengan empat itu, wahai Engkau yang berlengan seribu, Berwujud Universal!', 460);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2297, 'Aku ingin melihat-Mu seperti sebelumnya, mengenakan mahkota, memegang gada, dan memegang cakram di tangan. Wahai Engkau yang berlengan seribu, wahai Engkau yang berwujud Kosmis, muncullah dengan wujud yang sama dengan empat tangan.', 460);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2301, 'Sang Raja berkata, “Dengan rahmat-Ku, wahai Arjuna, Wujud Tertinggi ini, cemerlang, universal, tak terbatas, dan purba—belum pernah terlihat sebelumnya oleh siapa pun kecuali engkau—telah disingkapkan kepadamu melalui kehendak-Ku sendiri.”', 461);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2302, 'Yang Mahamulia berkata, Karena rahmat, wahai Arjuna, wujud tertinggi, bercahaya, kosmis, tak terbatas, purba ini—yang belum pernah dilihat oleh siapa pun selain Anda sebelumnya—telah ditunjukkan kepada Anda oleh-Ku melalui kekuatan Yoga-Ku sendiri.', 461);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2306, 'Baik melalui studi Veda, maupun dengan kurban, maupun dengan pembacaan kitab suci, maupun dengan pemberian, maupun dengan ritual, maupun dengan laku tapa yang keras, Aku tidak dapat disadari dalam bentuk seperti ini di dunia manusia oleh siapa pun kecuali engkau. Wahai Arjuna!', 462);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2307, 'Bukan melalui studi Veda dan pengorbanan, bukan dengan hadiah, bukan pula dengan ritual, pun bukan dengan pertapaan yang keras, Aku, dalam wujud ini, dapat dilihat di dunia manusia oleh siapa pun selain Anda, wahai yang paling berani di antara Kurawa, yang belum menerima rahmat-Ku.', 462);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2311, 'Engkau tidak perlu takut lagi, pun tidak perlu bingung, melihat wujud-Ku yang menakutkan ini. Bebas dari ketakutan dan dengan hati yang gembira, lihatlah sekali lagi wujud-Ku yang lain.', 463);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2312, 'Semoga Anda tidak takut, dan semoga tidak ada kebingungan saat melihat wujud-Ku yang begitu mengerikan ini. Menjadi bebas dari rasa takut dan gembira dalam pikiran sekali lagi, lihatlah wujud-Ku yang sebelumnya ini.', 463);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2316, 'Sanjaya berkata, Setelah berbicara demikian kepada Arjuna, Sri Krsna sekali lagi menampakkan wujud-Nya sendiri. Sang Mahatman, dengan kembali mengambil wujud yang lembut, menenangkan kembali orang yang telah dilanda ketakutan.', 464);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2317, 'Sanjaya berkata, Demikianlah, setelah berbicara kepada Arjuna dengan cara itu, Vasudewa menunjukkan kembali wujud-Nya sendiri. Dan Dia, Yang Agung, menenangkan kembali orang yang ketakutan ini dengan kembali menjadi tenang dalam wujud-Nya.', 464);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2321, 'Arjuna berkata, “Setelah melihat wujud manusia-Mu yang menyenangkan ini, wahai Krsna, aku sekarang tenang dalam pikiran dan telah kembali ke sifat alamiku.”', 465);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2322, 'Arjuna berkata, “Wahai Janardana, setelah melihat wujud manusia-Mu yang tenang ini, aku sekarang menjadi tenang dalam pikiran dan kembali ke sifat asliku.”', 465);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2326, 'Sang Raja berkata, “Sangat sulit untuk melihat wujud-Ku ini yang telah engkau lihat. Bahkan para dewa pun selalu ingin melihat wujud ini.”', 466);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2327, 'Yang Mahamulia berkata, “Wujud-Ku ini, yang telah Anda lihat, sangat sulit untuk dilihat; bahkan para dewa pun selalu mendambakan penglihatan wujud ini.”', 466);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2331, 'Sri Krishna berkata, “Aku tidak dapat dilihat dalam bentuk seperti yang telah engkau lihat Aku, bukan oleh Veda, bukan oleh laku tapa, bukan oleh pemberian, bukan oleh kurban.”', 467);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2332, 'Bukan melalui Veda, bukan oleh pertapaan, bukan oleh hadiah, pun bukan oleh pengorbanan Aku dapat dilihat dalam wujud ini sebagaimana Anda telah melihat-Ku.', 467);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2336, 'Tetapi dengan pengabdian yang tulus, wahai Arjuna, adalah mungkin untuk benar-benar mengenal, melihat, dan masuk ke dalam-Ku, yang berwujud ini, wahai penindas musuh.', 468);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2337, 'Tetapi, wahai Arjuna, dengan pengabdian yang terarah, Aku, dalam wujud ini, dapat dikenal dan dilihat dalam kenyataan, dan juga dimasuki, wahai penghancur musuh.', 468);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2341, 'Siapa pun yang bekerja untuk-Ku, menganggap-Ku sebagai yang tertinggi dan mengabdikan diri kepada-Ku, bebas dari keterikatan dan tanpa permusuhan terhadap makhluk apa pun, mereka datang kepada-Ku, wahai Arjuna.', 469);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2342, 'Wahai putra Pandu, dia yang bekerja untuk-Ku, menerima-Ku sebagai tujuan tertinggi, mengabdikan diri kepada-Ku, tanpa keterikatan dan bebas dari permusuhan terhadap semua makhluk—dia mencapai-Ku.', 469);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2346, 'Arjuna berkata: Mana di antara ini yang memiliki pengetahuan yoga yang lebih besar—para penyembah yang bermeditasi pada-Mu, yang senantiasa terintegrasi, atau mereka yang bermeditasi pada Yang Tak Termusnahkan dan Yang Tak Termanifestasi?', 470);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2347, 'Arjuna berkata: “Mana di antara para penyembah ini, yang selalu berbakti dan bermeditasi pada-Mu, dan mereka yang bermeditasi pada Yang Tak Termanifestasi dan Tak Berubah, adalah yang paling baik mengalami yoga [di sini, yoga berarti samadhi, penyerapan spiritual]?”', 470);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2351, 'Sang Raja berkata, “Mereka yang, senantiasa terintegrasi dengan-Ku dan memiliki iman tertinggi, menyembah-Ku, memusatkan pikiran mereka pada-Ku—mereka ini dianggap oleh-Ku sebagai yang tertinggi di antara para yogi.”', 471);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2352, 'Yang Mahamulia berkata, “Mereka yang bermeditasi pada-Ku, memusatkan pikiran mereka pada-Ku dengan pengabdian yang teguh, diberkahi dengan iman tertinggi—mereka dianggap sebagai yogi yang paling sempurna menurut-Ku.”', 471);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2356, 'Tetapi mereka yang bermeditasi pada Yang Tak Termusnahkan, yang tak terdefinisi, yang tak termanifestasi, mahahadir, tak terpikirkan, umum bagi semua makhluk, tak bergerak, dan konstan—', 472);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2357, 'Mereka, bagaimanapun, yang bermeditasi dalam segala cara pada yang tak berubah, yang tak terdefinisi, yang tak termanifestasi, yang maha meliputi, tak terpahami, tak berubah, tak bergerak, dan konstan.', 472);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2361, 'Setelah menundukkan semua indra, menjadi seimbang, dan terlibat dalam kesejahteraan semua makhluk, mereka pun datang kepada-Ku saja.', 473);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2362, 'Dengan mengendalikan penuh semua organ dan tetap seimbang setiap saat, mereka, yang terlibat dalam kesejahteraan semua makhluk, mencapai-Ku saja.', 473);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2366, 'Lebih besar kesulitannya bagi mereka yang pikirannya demikian terikat pada yang tak termanifestasi, karena jalan yang tak termanifestasi sulit dicapai oleh makhluk berbadan.', 474);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2367, 'Bagi mereka yang pikirannya terikat pada Yang Tak Termanifestasi, perjuangannya lebih besar; karena Tujuan, yaitu Yang Tak Termanifestasi, sulit dicapai oleh yang berbadan.', 474);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2371, 'Bagi mereka yang mendedikasikan semua tindakan mereka kepada-Ku, menjadikan-Ku sebagai tujuan tertinggi mereka, bertekad pada-Ku, dan menyembah-Ku, bermeditasi pada-Ku dengan pengabdian eksklusif;', 475);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2372, 'Adapun mereka yang, setelah mendedikasikan semua tindakan kepada-Ku dan menerima-Ku sebagai yang tertinggi, bermeditasi pada-Ku dengan konsentrasi tunggal saja-.', 475);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2376, 'Mereka yang pikirannya demikian terfokus pada-Ku, Aku segera menjadi penyelamat mereka dari samudra kehidupan fana.', 476);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2377, 'Wahai putra Prtha, bagi mereka yang pikirannya tenggelam dalam-Ku, Aku menjadi, tanpa penundaan, penyelamat dari lautan dunia, yang penuh dengan kematian.', 476);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2381, 'Pusatkan pikiranmu pada-Ku saja; dan biarkan intelekmu masuk ke dalam-Ku. Maka, engkau akan hidup di dalam-Ku saja; tidak ada keraguan.', 477);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2382, 'Pusatkan pikiranmu hanya pada-Ku; di dalam-Ku saja biarlah intelek beristirahat. Tidak ada keraguan bahwa setelah ini Anda akan berdiam di dalam-Ku saja.', 477);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2386, 'Jika sekarang engkau tidak mampu memusatkan pikiranmu pada-Ku, maka carilah untuk mencapai-Ku, wahai Arjuna, melalui praktik pengulangan.', 478);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2387, 'Jika, bagaimanapun, Anda tidak dapat memantapkan pikiran pada-Ku, maka, wahai Dhananjaya, berusahalah untuk mencapai-Ku melalui yoga praktik.', 478);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2391, 'Jika engkau tidak mampu melakukan praktik pengulangan ini, maka abdikanlah dirimu pada perbuatan-Ku. Karena bahkan dengan bekerja demi Aku, engkau akan mencapai kesempurnaan.', 479);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2392, 'Jika Anda tidak dapat berlatih sekalipun, berniatlah untuk bekerja demi-Ku. Dengan melakukan pekerjaan demi-Ku juga, Anda akan mencapai kesempurnaan. [Identifikasi dengan Brahman.]', 479);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2396, 'Jika engkau tidak mampu melakukan hal ini—yaitu, berlindung pada Yoga-Ku—maka, dengan pengendalian diri, lepaskanlah buah dari setiap tindakan.', 480);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2397, 'Jika Anda tidak dapat melakukan hal ini sekalipun, maka berlindunglah pada Yoga untuk-Ku dan setelah itu, kendalikan pikiran Anda dan lepaskan hasil dari semua pekerjaan Anda.', 480);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2401, 'Pengetahuan tentang Diri jauh lebih baik daripada praktik berulang (mengingat Tuhan). Meditasi lebih baik daripada pengetahuan ini; pelepasan buah tindakan lebih baik daripada meditasi. Dari pelepasan demikian, kedamaian akan muncul.', 481);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2402, 'Pengetahuan tentu lebih unggul dari praktik; meditasi melampaui pengetahuan. Pelepasan hasil karya melampaui meditasi. Dari pelepasan, kedamaian segera menyusul.', 481);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2406, 'Dia yang tidak pernah membenci makhluk apa pun, yang ramah dan penuh belas kasihan, yang bebas dari perasaan "Aku" dan "milikku," yang memandang semua rasa sakit dan kesenangan sama, dan yang tahan banting;', 482);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2407, 'Dia yang tidak membenci makhluk apa pun, yang ramah dan penuh belas kasihan, yang tidak memiliki konsep \'milikku\' dan tidak memiliki rasa egoisme, yang tetap sama dalam kesedihan dan kebahagiaan, dan yang pemaaf;', 482);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2411, 'Dia yang puas, yang senantiasa bermeditasi, yang mengendalikan diri, dan yang teguh dalam keyakinannya, dan yang pikiran dan akalnya terdedikasi kepada-Ku—dia dikasihi oleh-Ku.', 483);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2412, 'Dia yang selalu puas, yang seorang yogi, yang menguasai diri, yang memiliki keyakinan teguh, dan yang telah mendedikasikan pikiran dan inteleknya kepada-Ku—dia yang merupakan pemuja-Ku demikian dikasihi oleh-Ku.', 483);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2416, 'Dia yang dari padanya dunia tidak punya alasan untuk takut, yang tidak takut pada dunia, yang bebas dari kegembiraan dan ketidaksabaran, ketakutan, dan kebencian - dia dikasihi olehku.', 484);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2417, 'Dia, karena dia, dunia tidak terganggu, dan dia tidak terganggu oleh dunia, yang bebas dari kegembiraan, ketidaksabaran, ketakutan, dan kecemasan, dikasihi olehku.', 484);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2421, 'Dia yang bebas dari keinginan, yang murni, ahli, acuh tak acuh, dan bebas dari penderitaan, yang telah melepaskan setiap usaha—dia dikasihi olehku.', 485);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2422, 'Dia yang tidak memiliki keinginan, yang murni, cekatan, tidak memihak, bebas dari rasa takut, dan yang telah melepaskan setiap usaha—pemuja-Ku seperti itu dikasihi oleh-Ku.', 485);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2426, 'Dia yang tidak bersukacita, pun tidak membenci, pun tidak berduka, pun tidak berhasrat, yang melepaskan baik kebaikan maupun kejahatan, dan yang penuh pengabdian kepada-Ku—pemuja seperti itu dikasihi oleh-Ku.', 486);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2427, 'Dia yang tidak bersukacita, pun tidak cemas, pun tidak meratapi, pun tidak mendambakan; yang melepaskan baik yang baik maupun yang buruk, dan dipenuhi pengabdian—dia dikasihi oleh-Ku.', 486);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2431, 'Dia yang sama terhadap musuh dan teman, kehormatan dan kehinaan, yang sama terhadap dingin dan panas, kesenangan dan rasa sakit, dan yang bebas dari segala keterikatan;', 487);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2432, 'Dia yang sama terhadap teman dan musuh, dan dalam kehormatan dan kehinaan; yang sama di bawah dingin, panas, kebahagiaan, dan kesedihan; yang bebas dari keterikatan pada semua.', 487);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2436, 'Dia yang menganggap sama baik celaan maupun pujian, yang diam dan puas dengan takdir apa pun, yang tidak memiliki rumah, yang teguh pikirannya, dan yang mengabdikan diri kepada-Ku—orang seperti itu dikasihi oleh-Ku.', 488);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2437, 'Orang yang baginya celaan dan pujian adalah sama, yang diam, puas dengan apa pun, tunawisma, bertekad teguh, dan penuh pengabdian dikasihi olehku.', 488);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2441, 'Tetapi para penyembah yang mengikuti nektar kewajiban yang luhur ini, sebagaimana diajarkan di atas, yang penuh iman dan menganggap Aku sebagai Yang Mahatinggi, sangat dikasihi oleh-Ku.', 489);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2442, 'Tetapi para penyembah yang menerima-Ku sebagai Tujuan tertinggi, dan dengan iman mencari ambrosia ini—nektar dalam bentuk kebajikan—yang tidak dapat dibedakan dari kebajikan-kebajikan sebagaimana yang disebutkan di atas, mereka sangat dikasihi oleh-Ku.', 489);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2448, 'Arjuna berkata, “Aku ingin belajar tentang Alam dan Roh, medan dan yang mengetahui medan, pengetahuan dan yang seharusnya diketahui, wahai Kesava.”', 490);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2450, 'Arjuna bertanya, “Tuanku, siapakah Tuhan itu, apakah Alam itu, apakah Materi itu, dan apakah Diri itu? Apakah Kebijaksanaan itu, dan apakah yang layak diketahui? Saya ingin hal ini dijelaskan.”', 490);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2451, 'Sang Raja berkata, “Wahai Arjuna, tubuh ini disebut Medan, Ksetra. Mereka yang mengenal diri menyebut yang mengetahuinya sebagai Pengenal Medan, Ksetrajna.”', 491);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2452, 'Yang Mahamulia berkata, “Wahai putra Kunti, tubuh ini disebut sebagai \'ladang\'. Mereka yang ahli dalam hal ini menyebut dia yang menyadarinya sebagai \'pengenal ladang\'.”', 491);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2456, 'Dan ketahuilah Aku juga sebagai Yang Mengetahui semua Medan, wahai Arjuna. Dalam pandangan-Ku, pengetahuan tentang Medan dan Yang Mengetahui adalah pengetahuan yang sejati.', 492);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2457, 'Dan, wahai keturunan dinasti Bharata, pahamilah Aku sebagai \'Pengenal medan\' di semua medan. Menurut pendapat-Ku, itu adalah Pengetahuan yang merupakan pengetahuan tentang medan dan pengenal medan.', 492);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2461, 'Dengarkanlah secara singkat dari-Ku apa itu Medan, seperti apa, apa modifikasinya, tujuan apa yang dilayaninya, dan apa itu; serta siapa Diri itu dan apa kekuatannya.', 493);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2462, 'Dengarkan dari saya secara singkat tentang semua itu, apa itu ladang dan bagaimana keadaannya; apa perubahannya, dan apa yang menyebabkan apa efeknya; dan siapa Dia, dan apa kekuatan-Nya.', 493);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2466, 'Telah dinyanyikan oleh para bijak dengan berbagai cara, dalam berbagai himne yang khas, dan juga dalam kata-kata Brahma-sutra yang beralasan kuat dan konklusif.', 494);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2467, 'Telah dinyanyikan dengan berbagai cara oleh para Rsis, secara terpisah oleh berbagai cabang teks Veda, dan juga oleh kalimat-kalimat yang rasional dan meyakinkan itu sendiri yang menunjukkan dan mengarah ke Brahman.', 494);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2471, 'Unsur-unsur besar, Ahankara, Buddhi, Avyakta, sepuluh indra, dan yang satu, di samping itu, lima objek indra;', 495);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2472, 'Unsur-unsur besar, egoisme, intelek, dan Yang Tak Termanifestasi itu sendiri; sepuluh organ dan yang satu, dan lima objek indra;', 495);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2476, 'Keinginan, kebencian, kesenangan, dan rasa sakit, serta kombinasi yang membentuk dasar kesadaran—atau diri individual—telah dijelaskan secara singkat di sini, beserta modifikasinya.', 496);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2477, 'Keinginan, penolakan, kebahagiaan, kesedihan, kumpulan (tubuh dan organ), kepekaan, ketabahan—ladang ini, bersama dengan modifikasinya, telah dibicarakan secara singkat.', 496);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2481, 'Kerendahan hati, tanpa kepura-puraan, tidak melukai, kesabaran, ketulusan, pelayanan kepada guru, kemurnian, keteguhan, dan pengendalian diri;', 497);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2482, 'Kerendahan hati, tanpa kepura-puraan, tidak melukai, kesabaran, ketulusan, pelayanan kepada guru, kebersihan, keteguhan, dan pengendalian tubuh serta organ-organnya;', 497);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2486, 'Tidak adanya keinginan akan objek-objek indra, dan juga tidak adanya egotisme, pengakuan akan kejahatan dalam kelahiran, kematian, usia tua, penyakit, dan kesedihan;', 498);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2487, 'Tanpa keterikatan pada objek-objek indra, dan juga tanpa egoisme; melihat kejahatan dalam kelahiran, kematian, penuaan, penyakit, dan penderitaan;', 498);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2491, 'Tidak ada keterikatan, tidak ada keterikatan pada anak, istri, rumah, dan sebagainya, dan keseimbangan pikiran yang konstan sehubungan dengan semua peristiwa yang diinginkan dan tidak diinginkan;', 499);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2492, 'Tanpa keterikatan dan tanpa kesukaan terhadap anak laki-laki, istri, rumah, dll., dan ketenangan pikiran yang konstan terhadap perolehan yang diinginkan maupun yang tidak diinginkan;', 499);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2496, 'Terus-menerus mengabdikan diri pada-Ku saja, berlindung di tempat-tempat sunyi, dan tidak menyukai keramaian.', 500);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2497, 'Dan pengabdian yang tak tergoyahkan kepada-Ku dengan konsentrasi terarah; kecenderungan untuk mundur ke tempat yang bersih; kurangnya kesenangan dalam keramaian orang;', 500);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2501, 'Perenungan konstan tentang pengetahuan yang berkaitan dengan diri, refleksi untuk mencapai pengetahuan tentang kebenaran—ini dinyatakan sebagai pengetahuan, dan yang bertentangan dengannya adalah ketidaktahuan.', 501);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2502, 'Keteguhan dalam pengetahuan tentang Diri, kontemplasi tentang Tujuan pengetahuan Realitas—ini disebut sebagai Pengetahuan. Ketidaktahuan adalah yang selain ini.', 501);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2506, 'Aku akan menyatakan yang harus diketahui, dengan mengetahui itu seseorang mencapai Diri yang abadi. Itu adalah Brahman yang tak berawal, memiliki Aku sebagai Yang Tertinggi (Anadi Matparam); dikatakan itu bukan keberadaan maupun non-keberadaan.', 502);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2507, 'Aku akan berbicara tentang yang harus diketahui, dengan menyadarinya seseorang mencapai keabadian. Brahman tertinggi tidak memiliki awal; itu disebut bukan keberadaan maupun non-keberadaan.', 502);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2511, 'Tangan dan kakinya ada di mana-mana; mata, kepala, dan mulutnya ada di mana-mana; telinganya ada di semua sisi; dan Ia ada, meliputi segala sesuatu.', 503);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2512, 'Yang dapat diketahui, yang memiliki tangan dan kaki di mana-mana, yang memiliki mata, kepala, dan mulut di mana-mana, yang memiliki telinga di mana-mana, ada dalam makhluk dengan melingkupi mereka semua.', 503);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2516, 'Bersinar oleh fungsi indra, namun tanpa indra, terpisah namun menopang semua, tanpa Guna namun mengalami Guna;', 504);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2517, 'Bersinar melalui fungsi semua organ, namun tanpa semua organ; tidak terikat, dan sungguh penopang semua; tanpa kualitas, dan pengamat kualitas.', 504);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2521, 'Ia ada di dalam dan di luar semua makhluk; Ia tidak bergerak namun bergerak; Ia sangat halus sehingga tidak ada yang dapat memahaminya; Ia jauh namun sangat dekat.', 505);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2522, 'Ada di luar dan di dalam semua makhluk, bergerak dan tidak bergerak, Ia tak terpahami karena kehalusannya. Ia jauh, namun dekat.', 505);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2526, 'Tidak terbagi namun tampak terbagi di antara makhluk hidup, Diri ini harus dikenal sebagai penopang elemen. Ia melahap mereka dan menyebabkan generasi mereka.', 506);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2527, 'Yang Dapat Diketahui, meskipun tak terbagi, tampak ada sebagai terbagi dalam semua makhluk, dan Ia adalah penopang, pemakan, dan pencetus semua makhluk.', 506);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2531, 'Cahaya semua cahaya dikatakan berada di luar Tamas (kegelapan); itu dikenal sebagai pengetahuan, dapat dicapai melalui pengetahuan, dan ada di hati semua.', 507);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2532, 'Itulah Cahaya semua cahaya; Ia dikatakan berada di luar kegelapan. Itu adalah Pengetahuan, yang Dapat Diketahui, dan yang Dikenal. Ia secara khusus berada di hati semua.', 507);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2536, 'Demikianlah, Ksetra, pengetahuan, dan objek pengetahuan telah dijelaskan secara singkat. Dengan mengetahui ini, penyembah-Ku menjadi layak mencapai keadaan-Ku.', 508);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2537, 'Demikianlah, secara singkat, ladang, Pengetahuan, dan yang Dapat Diketahui telah dibicarakan. Dengan memahami ini, penyembah-Ku menjadi memenuhi syarat untuk keadaan-Ku.', 508);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2541, 'Ketahuilah bahwa baik Prakṛti maupun Diri (Puruṣa) adalah tanpa awal; ketahuilah bahwa semua modifikasi dan atribut lahir dari Prakṛti.', 509);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2542, 'Ketahuilah bahwa baik Alam maupun jiwa individu benar-benar tanpa awal; ketahuilah bahwa modifikasi serta kualitas lahir dari Alam.', 509);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2546, 'Prakṛti dikatakan sebagai penyebab agen bagi tubuh (Kārya) dan organ indra (Karaṇa); Diri dikatakan sebagai penyebab pengalaman kesenangan dan rasa sakit.', 510);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2547, 'Sehubungan dengan sumber tubuh dan organ-organnya, Alam dikatakan sebagai penyebab; jiwa adalah penyebab sejauh kenikmatan kebahagiaan dan kesedihan diperhatikan.', 510);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2551, '(a) Memang, diri yang bersemayam dalam Prakrti mengalami Guna yang lahir dari Prakrti. (b) Keterikatannya pada Guna inilah penyebab kelahiran di rahim baik dan buruk.', 511);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2552, 'Karena jiwa bersemayam di Alam, maka ia mengalami kualitas yang lahir dari Alam. Kontak dengan kualitas adalah penyebab kelahirannya di rahim yang baik dan buruk.', 511);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2556, 'Diri dalam tubuh disebut pengamat, penyetuju, penopang, pengalam, penguasa agung, dan diri tertinggi.', 512);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2557, 'Dia yang adalah Saksi, Pemberi Izin, Penopang, Pengalam, Penguasa agung, dan yang juga disebut sebagai Diri transendental, adalah Pribadi tertinggi dalam tubuh ini.', 512);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2561, 'Dia yang memahami Diri dan Prakriti, beserta Guna, tidak akan lahir kembali, tidak peduli dalam keadaan apa pun dia berada.', 513);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2562, 'Dia yang mengenal demikian Pribadi, Alam, dan Kualitas tidak akan lahir kembali, tidak peduli bagaimana dia hidup.', 513);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2566, 'Beberapa melihat Diri di dalam diri (tubuh) melalui meditasi pikiran, yang lain melalui Sankhya Yoga, dan yang lainnya melalui Karma Yoga.', 514);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2567, 'Beberapa menyadari Diri dalam intelek mereka melalui meditasi, yang lain melalui Sankhya-Yoga, dan yang lainnya melalui Karma-Yoga, dengan bantuan organ internal.', 514);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2571, 'Tetapi beberapa, yang tidak mengetahui hal ini, setelah mendengar dari orang lain, menyembah sesuai dengan itu—mereka ini pun, yang mengabdikan diri pada apa yang mereka dengar, melampaui kematian.', 515);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2572, 'Yang lain, lagi, yang tidak mengetahui demikian, mulai berpikir setelah mendengar dari orang lain; mereka juga, yang mengabdikan diri untuk mendengar, pasti mengatasi kematian.', 515);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2576, 'Makhluk apa pun yang lahir, baik bergerak maupun diam, ketahuilah, wahai Arjuna, bahwa itu adalah melalui kombinasi Ksetra (tubuh) dan Ksetrajna (pengenal Medan).', 516);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2577, 'Wahai keturunan dinasti Bharata, objek apa pun, bergerak atau tidak bergerak, yang muncul, ketahuilah itu berasal dari kombinasi ladang dan Pengenal ladang!', 516);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2581, 'Dia yang melihat penguasa tertinggi berdiam sama di semua tubuh dan tidak pernah binasa, bahkan ketika mereka binasa, dia sungguh melihat.', 517);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2582, 'Dia yang melihat Tuhan Yang Mahatinggi ada dengan benar di semua makhluk, dan Yang Tak Terhapuskan di antara yang fana, sungguh melihat.', 517);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2586, 'Karena, melihat penguasa berdiam sama di setiap tempat, dia tidak melukai dirinya sendiri dengan pikirannya sendiri dan oleh karena itu mencapai tujuan tertinggi.', 518);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2587, 'Karena, dengan benar-benar melihat Tuhan, yang hadir di mana-mana secara sama, ia tidak melukai Diri oleh Diri, ia oleh karena itu mencapai Tujuan tertinggi.', 518);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2591, 'Dia yang melihat bahwa semua tindakan dilakukan secara universal oleh Prakrti saja, dan demikian pula bahwa diri bukanlah pelakunya, dia sungguh melihat.', 519);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2592, 'Dan dia yang melihat tindakan dilakukan dengan berbagai cara oleh Alam itu sendiri, dan juga Diri sebagai non-agen, ia melihat.', 519);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2596, 'Ketika ia melihat mode-mode keberadaan independen dari semua makhluk terpusat dalam satu, dan perluasan mereka dari itu saja, maka ia mencapai Brahman.', 520);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2597, 'Ketika seseorang menyadari bahwa keadaan keberagaman makhluk hidup berakar pada Yang Esa, dan bahwa manifestasi mereka juga berasal dari Itu, maka seseorang menjadi identik dengan Brahman.', 520);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2601, 'Diri tertinggi ini, meskipun berdiam dalam tubuh, tidak berubah, wahai Arjuna, karena tanpa awal. Ia tidak bertindak dan tidak tercemar, karena ia tanpa Guna.', 521);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2602, 'Tanpa awal dan tanpa akhir, wahai putra Kunti, Diri tertinggi yang tak berubah ini tidak bertindak, pun tidak terpengaruh [Juga diterjemahkan sebagai tercemar.-Tr.], meskipun Ia ada dalam tubuh.', 521);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2606, 'Sebagaimana eter yang meliputi segalanya tidak tercemar karena kehalusannya, demikian pula, diri yang berdiam dalam tubuh tidak tercemar di mana-mana.', 522);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2607, 'Sebagaimana ruang yang meliputi segalanya tidak tercemar, karena kehalusannya, demikian pula Diri, yang hadir di mana-mana dalam tubuh, tidak tercemar.', 522);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2611, 'Sebagaimana satu matahari menerangi seluruh dunia ini, demikian pula Pengenal Medan (Ksetrin, diri), wahai Arjuna, menerangi seluruh Medan (tubuh).', 523);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2612, 'Sebagaimana satu matahari menerangi seluruh dunia ini, demikian pula, wahai keturunan dinasti Bharata, Pengenal ladang menerangi seluruh ladang.', 523);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2616, 'Mereka yang demikian membedakan, dengan mata pengetahuan, perbedaan antara tubuh atau Medan (Ksetra) dan pengenal tubuh atau pengenal Medan (Ksetrajna), dan sarana pembebasan dari Prakrti yang termanifestasi, mereka mencapai Yang Tertinggi.', 524);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2617, 'Mereka yang, melalui mata kebijaksanaan, mengetahui perbedaan antara ladang dan Pengenal ladang, dan pemusnahan Matriks makhluk hidup, mencapai Yang Tertinggi.', 524);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2621, 'Sang Raja berkata, “Aku akan menyatakan lagi jenis pengetahuan lain: Ini adalah yang terbaik dari semua bentuk pengetahuan, dengan mengetahui itu semua bijak telah mencapai keadaan kesempurnaan di luar dunia ini.”', 525);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2622, 'Yang Mahamulia berkata, “Aku akan berbicara lagi tentang pengetahuan tertinggi, yang terbaik dari semua pengetahuan, dengan menyadarinya semua kontemplatif telah mencapai kesempurnaan tertinggi dari sini.”', 525);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2626, 'Dengan berlindung pada pengetahuan ini, mengambil bagian dalam Sifat-Ku, mereka tidak lahir pada saat penciptaan pun tidak menderita pada saat kehancuran.', 526);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2627, 'Mereka yang mencapai identitas dengan-Ku dengan berlindung pada Pengetahuan ini tidak lahir bahkan selama penciptaan, pun tidak menderita sakit selama pembubaran.', 526);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2631, 'Rahim-Ku adalah Brahman agung (yaitu Prakṛti). Di dalamnya, Aku menanam benih. Dari itu, wahai Arjuna, datanglah kelahiran semua makhluk.', 527);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2632, 'Rahimku adalah penopang yang agung. Di dalamnya, aku menanam benih. Dari itu, wahai keturunan dinasti Bharata, segala sesuatu dilahirkan.', 527);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2636, 'Bentuk apa pun yang dihasilkan dalam rahim apa pun, wahai Arjuna, Prakṛti adalah rahim besar mereka, dan Akulah ayah yang menaburkan benih.', 528);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2637, 'Wahai putra Kunti, bentuk apa pun yang lahir dari semua rahim, di antaranya penopang yang agung adalah rahim; aku adalah ayah yang menanam benih.', 528);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2641, 'Guna Sattva, Rajas, dan Tamas muncul dari Prakrti dan mengikat Diri yang tak berubah dalam tubuh, wahai Arjuna.', 529);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2642, 'Wahai yang perkasa, kualitas-kualitas, yaitu. sattva, rajas, dan tamas, lahir dari Alam, adalah perwujudan yang tak berubah yang melekat pada tubuh.', 529);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2646, 'Dari semua ini, sattva, karena tanpa kotoran, bersifat bercahaya dan bebas dari penyakit. Ia mengikat, wahai Arjuna, melalui keterikatan pada kesenangan dan pengetahuan.', 530);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2647, 'Di antara mereka, sattva, karena murni dan nirmala (transparan-murni, yaitu, mampu melawan segala bentuk ketidaktahuan, dan karenanya pencerah, yaitu, penyingkap Kesadaran), tidak berbahaya. Wahai yang tanpa dosa, ia mengikat melalui keterikatan pada kebahagiaan dan keterikatan pada pengetahuan.', 530);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2651, 'Ketahuilah, wahai Arjuna, bahwa Rajas bersifat gairah yang berasal dari kehausan dan keterikatan. Ia mengikat diri yang berbadan dengan keterikatan pada tindakan.', 531);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2652, 'Ketahuilah rajas sebagai sifat gairah, lahir dari kerinduan dan keterikatan. Wahai putra Kunti, ia mengikat yang berbadan melalui keterikatan pada tindakan.', 531);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2656, 'Ketahuilah bahwa Tamas lahir dari pengetahuan palsu dan menipu semua makhluk berbadan. Ia mengikat, wahai Arjuna, dengan kelalaian, kemalasan, dan tidur.', 532);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2657, 'Di sisi lain, ketahuilah tamas, yang menipu semua makhluk berbadan, lahir dari ketidaktahuan. Wahai keturunan dinasti Bharata, ia mengikat melalui kelalaian, kemalasan, dan tidur.', 532);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2661, 'Sattva menimbulkan keterikatan pada kesenangan, Rajas pada tindakan, wahai Arjuna. Tetapi Tamas, mengaburkan pengetahuan, menimbulkan keterikatan pada kelalaian.', 533);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2662, 'Wahai keturunan dinasti Bharata, sattva mengikat seseorang pada kebahagiaan, rajas pada tindakan, sementara tamas, mengaburkan pengetahuan, juga mengarah pada kelalaian.', 533);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2666, 'Mengatasi Rajas dan Tamas, Sattva mendominasi, wahai Arjuna. Mengatasi Tamas dan Sattva, Rajas mendominasi. Mengatasi Rajas dan Sattva, Tamas mendominasi.', 534);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2667, 'Wahai keturunan dinasti Bharata, sattva meningkat dengan menundukkan rajas dan tamas; rajas meningkat dengan menguasai sattva dan tamas; dan tamas meningkat dengan mendominasi sattva dan rajas.', 534);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2671, 'Ketika pengetahuan, seperti cahaya, menerangi dari semua gerbang (yaitu, indra), maka seseorang harus tahu bahwa Sattva mendominasi.', 535);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2672, 'Ketika cahaya pengetahuan memancar melalui semua pintu indra dalam tubuh ini, maka seseorang harus tahu bahwa sattva telah sangat meningkat.', 535);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2676, 'Keserakahan, aktivitas, pelaksanaan pekerjaan, kegelisahan, dan kerinduan—ini muncul, wahai Arjuna, ketika Rajas mendominasi.', 536);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2677, 'Wahai yang terbaik dari dinasti Bharata, ketika rajas menjadi dominan, ini muncul: ketamakan, kegelisahan, pelaksanaan tindakan, agitasi, dan kerinduan.', 536);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2681, 'Ketidak-terangan, ketidakaktifan, kelalaian, dan bahkan delusi—ini muncul, wahai Arjuna, ketika Tamas berkuasa.', 537);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2682, 'Wahai keturunan dinasti Kuru, ketika tamas mendominasi, ini muncul tanpa kecuali: ketidak-diskriminasi, ketidakaktifan, kelalaian, dan delusi.', 537);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2686, 'Jika diri yang berbadan larut ketika Sattva berkuasa, maka ia menuju ke dunia murni mereka yang mengenal yang tertinggi.', 538);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2687, 'Ketika seorang yang berbadan mengalami kematian sementara sattva secara eksklusif dominan, maka ia mencapai dunia yang tidak tercemar dari mereka yang mengetahui yang tertinggi (entitas).', 538);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2691, '(a) Ketika Rajas berkuasa, seseorang menemui kehancuran dan lahir di antara mereka yang terikat pada pekerjaan.\n\n(b) Demikian pula, ketika Tamas berkuasa, seseorang yang telah menemui kehancuran lahir di rahim makhluk yang kurang cerdas.', 539);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2692, 'Ketika seseorang meninggal saat rajas mendominasi, mereka dilahirkan di antara orang-orang yang terikat pada aktivitas. Demikian pula, ketika seseorang meninggal saat tamas mendominasi, mereka dilahirkan di antara spesies yang bodoh.', 539);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2696, 'Dikatakan bahwa buah dari perbuatan baik itu murni dan bersifat Sattva; buah dari Rajas adalah rasa sakit; dan buah dari Tamas adalah ketidaktahuan.', 540);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2697, 'Mereka mengatakan bahwa hasil dari pekerjaan baik itu murni dan lahir dari Sattva. Tetapi hasil dari Rajas adalah kesedihan; hasil dari Tamas adalah ketidaktahuan.', 540);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2701, 'Dari Sattva timbul pengetahuan, dari Rajas timbul keserakahan, dari Tamas timbul kelalaian dan delusi, dan dari itu timbul ketidaktahuan.', 541);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2702, 'Dari sattva lahirlah pengetahuan, dan dari rajas, keserakahan memang. Dari tamas lahirlah kelalaian, delusi, dan ketidaktahuan, tentu saja.', 541);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2706, 'Mereka yang berdiam dalam Sattva naik ke atas; mereka yang berdiam dalam Rajas tetap di tengah; dan mereka, yang berdiam dalam kecenderungan Tamas turun ke bawah.', 542);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2707, 'Orang-orang yang sesuai dengan sattva naik lebih tinggi; mereka yang sesuai dengan rajas tetap di tengah; mereka yang sesuai dengan tamas, yang sesuai dengan tindakan kualitas terendah, turun.', 542);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2711, 'Ketika peramal melihat tidak ada agen tindakan selain Guna, dan mengetahui apa yang berada di luar Guna, ia mencapai keadaan-Ku.', 543);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2712, 'Ketika saksi tidak melihat agen selain kualitas, dan mengetahui yang lebih unggul dari kualitas, ia mencapai sifat-Ku.', 543);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2716, 'Diri yang berbadan, melampaui ketiga Guna yang muncul dalam tubuh ini, dan dibebaskan dari kelahiran, kematian, penuaan, dan rasa sakit, mencapai keabadian.', 544);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2717, 'Setelah melampaui ketiga kualitas ini, yang merupakan asal mula tubuh, yang berbadan menjadi bebas dari kelahiran, kematian, usia tua, dan kesedihan, dan mengalami Keabadian.', 544);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2721, 'Arjuna berkata, “Apa tanda-tanda orang yang telah melampaui ketiga Guna? Bagaimana perilaku mereka? Dan bagaimana mereka melampaui ketiga Guna?”', 545);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2722, 'Arjuna berkata, “Wahai Tuhan, dengan tanda-tanda apa seseorang dikenal yang telah melampaui ketiga kualitas ini? Bagaimana perilakunya, dan bagaimana ia melampaui ketiga kualitas ini?”', 545);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2726, 'Sang Raja berkata, “Dia tidak membenci pencerahan, pun tidak aktivitas, pun tidak delusi, wahai Arjuna, saat ini berkuasa, pun tidak merindukannya saat mereka berhenti.”', 546);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2727, 'Yang Mahamulia berkata, “Wahai putra Pandu, ia tidak membenci pencerahan (pengetahuan), aktivitas, pun delusi ketika mereka muncul, pun tidak merindukan mereka ketika mereka menghilang.”', 546);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2731, 'Dia yang duduk seperti orang yang acuh tak acuh, tidak terganggu oleh Guna; yang tahu, \'Guna yang bergerak,\' dan dengan demikian tetap tidak tergoyahkan;', 547);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2732, 'Dia yang, duduk seperti orang yang acuh tak acuh, tidak terganggu oleh ketiga kualitas; dia yang, berpikir bahwa kualitas saja yang bertindak, tetap teguh dan tidak bergerak dengan pasti;', 547);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2736, 'Dia yang sama dalam kesenangan dan rasa sakit, yang berdiam dalam Diri, yang memandang segumpal tanah, batu, dan emas sebagai nilai yang sama, yang tetap sama terhadap hal-hal yang disayangi dan dibenci, dan yang cerdas, yang menganggap sama baik celaan maupun pujian terhadap dirinya sendiri.', 548);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2737, 'Dia yang sama dalam kesedihan dan kebahagiaan, yang tertanam dalam Diri-nya sendiri, yang bagi lumpur tanah, besi, dan emas adalah sama, yang bagi yang menyenangkan dan yang tidak menyenangkan adalah sama, yang bijaksana, yang bagi celaan dan pujiannya sendiri adalah sama;', 548);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2741, 'Dia yang sama dalam kehormatan dan kehinaan, dan sama terhadap teman dan musuh, dan yang telah meninggalkan semua usaha—dia dikatakan telah naik di atas Guna.', 549);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2742, 'Dia yang sama dalam kehormatan dan kehinaan, yang bersikap adil terhadap teman dan musuh, yang telah melepaskan semua usaha—dia dikatakan telah melampaui dualitas.', 549);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2746, 'Dan dia yang, dengan pengabdian yang teguh melalui Yoga, melayani Aku, dia, melampaui Guna, menjadi layak untuk keadaan Brahman.', 550);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2747, 'Dan dia yang melayani-Ku melalui Yoga Pengabdian yang tak tergoyahkan, ia, setelah melampaui kualitas-kualitas ini, memenuhi syarat untuk menjadi Brahman.', 550);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2751, 'Karena Akulah dasar Brahman, yang abadi, tak berubah, dan Dharma yang kekal, dan kebahagiaan yang sempurna.', 551);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2752, 'Karena Aku adalah Kediaman Brahman—yang tak termusnahkan, tak berubah, abadi, Dharma, dan Kebahagiaan mutlak.', 551);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2756, 'Sang Raja berkata, “Mereka berbicara tentang pohon Asvattha yang tak berubah dengan akar di atas dan cabang di bawah; daunnya adalah Veda. Dia yang mengetahuinya mengenal Veda.”', 552);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2757, 'Yang Mahamulia berkata, “Mereka mengatakan bahwa pohon peepul, yang akarnya ke atas dan cabangnya ke bawah, dan yang daunnya adalah Veda, tidak dapat dihancurkan. Dia yang menyadarinya adalah seorang yang mengetahui Veda.”', 552);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2761, '(a) Cabang-cabangnya memanjang ke atas dan ke bawah, dipelihara oleh Guna. Tunasnya adalah objek indra. (b) Dan akar sekundernya memanjang ke bawah, menghasilkan tindakan yang mengikat di dunia manusia.', 553);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2762, 'Cabang-cabang Pohon itu, memanjang ke bawah dan ke atas, diperkuat oleh kualitas dan memiliki objek indra sebagai tunasnya. Dan akar-akar, yang diikuti oleh tindakan, menyebar ke bawah di dunia manusia [Menurut A.G. dan M.S. manusya-loke berarti tubuh yang dibedakan oleh Brahmanhood dll.].', 553);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2766, 'Bentuknya tidak terlihat di sini, pun akhirnya, pun awalnya, pun penopangnya. Setelah memotong Asvattha yang berakar kuat ini dengan kapak detasemen yang kuat.', 554);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2767, 'Bentuknya tidak terlihat di sini dengan cara itu; pun akhirnya, pun awalnya, pun kelanjutannya. Setelah menebang pohon Peepul ini yang akarnya telah tumbuh dengan baik, dengan pedang detasemen yang kuat.', 554);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2771, 'Kemudian, seseorang harus mencari tujuan itu, dengan mencapainya seseorang tidak pernah kembali. Seseorang harus mencari perlindungan pada Pribadi Purba itu dari mana aktivitas kuno ini mengalir keluar.', 555);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2772, 'Setelah itu, keadaan itu harus dicari, pergi ke mana mereka tidak kembali lagi: Aku berlindung pada Pribadi Purba itu sendiri, dari mana Manifestasi abadi telah terjadi.', 555);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2776, 'Tanpa delusi gagasan sesat mengenai diri, menang atas kejahatan keterikatan, senantiasa mengabdikan diri pada Diri, berpaling dari keinginan dan dibebaskan dari dualitas seperti kesenangan dan rasa sakit, yang tidak tertipu pergi ke keadaan yang tak termusnahkan itu.', 556);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2777, 'Para bijak, bebas dari kesombongan dan ketidak-diskriminasi, yang telah menaklukkan kejahatan asosiasi [kebencian dan cinta yang timbul dari asosiasi dengan musuh dan teman], yang senantiasa mengabdikan diri pada spiritualitas, sepenuhnya bebas dari keinginan, dan bebas dari dualitas kebahagiaan dan kesedihan, mencapai keadaan yang tak lapuk itu.', 556);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2781, 'Cahaya tertinggi itu, setelah mencapainya mereka tidak kembali lagi, adalah milik-Ku; baik matahari maupun bulan maupun api tidak meneranginya.', 557);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2782, 'Baik matahari maupun bulan maupun api tidak menerangi Itu. Itulah Kediaman tertinggi-Ku, ke mana mereka tidak kembali.', 557);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2786, 'Bagian kekal dari Diri-Ku, setelah menjadi diri yang terikat di dunia kehidupan, menarik indra, yang pikirannya adalah yang keenam, dan yang berdiam di Prakriti.', 558);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2787, 'Ini memang bagian dari-Ku yang, menjadi jiwa individu yang abadi di wilayah makhluk hidup, menarik organ-organ, yang memiliki pikiran sebagai yang keenam, dan yang berdiam di Alam.', 558);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2791, 'Tubuh apa pun yang diperoleh tuannya dan dari tubuh apa pun ia pergi, ia melanjutkan perjalanannya, membawa indra-indra ini seperti angin membawa aroma dari tempatnya.', 559);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2792, 'Ketika sang master meninggalkannya dan bahkan ketika ia mengambil tubuh, ia pergi membawa ini bersamanya, sama seperti angin membawa bau-bauan dari wadahnya.', 559);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2796, 'Menguasai telinga, mata, indra peraba, lidah, hidung, dan pikiran, Ia mengalami objek-objek indra ini.', 560);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2797, 'Yang satu ini menikmati objek-objek dengan menguasai telinga, mata, kulit, lidah, hidung, dan pikiran.', 560);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2801, 'Orang-orang yang tertipu tidak melihatnya bersatu dengan Guna ketika pergi, tinggal, atau mengalami. Namun, mereka yang memiliki mata pengetahuan, melihatnya.', 561);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2802, 'Orang-orang yang tertipu secara beragam tidak melihatnya bahkan ketika ia pergi, berdiam, mengalami, atau dalam hubungan dengan kualitas; mereka yang memiliki mata pengetahuan melihat.', 561);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2806, 'Para yogi yang berusaha keras melihatnya berdiam dalam diri mereka. Tetapi, meskipun berusaha keras, mereka yang pikirannya tidak halus, tanpa kecerdasan, tidak melihatnya.', 562);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2807, 'Dan para yogi yang rajin melihat yang satu ini ada di dalam diri mereka. Mereka yang tidak membedakan yang kurang menguasai diri, bagaimanapun, tidak melihat yang satu ini, meskipun mereka rajin.', 562);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2811, 'Ketahuilah bahwa cahaya yang menerangi seluruh alam semesta di matahari, bulan, dan api adalah milik-Ku.', 563);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2812, 'Cahaya di matahari yang menerangi seluruh dunia, yang ada di bulan, dan yang ada di api—ketahuilah cahaya itu adalah milik-Ku.', 563);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2816, 'Dan, memasuki bumi, Aku menopang semua makhluk dengan kekuatan-Ku. Aku memberi makan semua tumbuhan, menjadi Soma yang berair.', 564);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2817, 'Dan memasuki Bumi, Aku menopang makhluk hidup melalui kekuatan-Ku; dan memberi makan semua tumbuhan dengan menjadi Soma, yang bersifat getah.', 564);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2821, 'Menjadi api pencernaan, Aku berfungsi di dalam tubuh semua makhluk hidup. Bersatu dengan napas masuk dan keluar, Aku mencerna empat jenis makanan.', 565);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2822, 'Mengambil wujud Vaisvanara, dan bersemayam dalam tubuh makhluk hidup, Aku, dalam hubungannya dengan Prana dan Apana, mencerna empat jenis makanan.', 565);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2826, 'Dan Aku bersemayam di hati semua. Dari-Ku datang ingatan, pengetahuan, dan penghilangannya juga. Sesungguhnya, Aku sendiri yang harus dikenal dari semua Veda. Aku mewujudkan buah dari ritual Veda; Aku sendiri yang mengetahui Veda.', 566);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2827, 'Dan Aku bersemayam di hati semua. Dari-Ku datang ingatan, pengetahuan, dan kehilangannya. Aku sendiri adalah objek yang harus diketahui melalui semua Veda; Aku juga adalah pencetus Wedanta, dan Aku sendiri yang mengetahui Veda.', 566);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2831, 'Ada dua jenis orang yang disebutkan dalam sastra: yang fana (ksara) dan yang abadi (aksara). Semua makhluk fana, sedangkan yang abadi disebut yang tak berubah (kutastha).', 567);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2832, 'Ada dua jenis orang di dunia: yang berubah dan yang tak berubah. Yang berubah terdiri dari segala sesuatu; yang ada sebagai Maya disebut yang tak berubah.', 567);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2836, 'Ada Pribadi Tertinggi selain ini. Dia disebut sebagai Diri Tertinggi (Paramatma) dalam semua Veda. Dia, sebagai Yang Tak Berubah dan Tuhan, memasuki dunia tiga kali lipat dan menopangnya.', 568);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2837, 'Tetapi Pribadi Tertinggi berbeda; Ia disebut sebagai Diri transendental, yang meliputi ketiga dunia dan menopangnya, dan Ia adalah Tuhan yang tak termusnahkan.', 568);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2841, 'Karena Aku melampaui pribadi yang fana dan juga lebih tinggi dari yang tak termusnahkan, maka Aku disebut dalam Smrti dan Veda sebagai Pribadi Tertinggi (Purusotama).', 569);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2842, 'Karena Aku transendental terhadap yang dapat berubah dan di atas bahkan yang tak berubah, maka Aku terkenal di dunia dan dalam Veda sebagai Pribadi tertinggi.', 569);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2846, 'Dia yang, tanpa delusi, demikian mengenal Aku sebagai Diri Tertinggi, mengenal segalanya, wahai Arjuna, dan menyembah Aku dalam segala hal.', 570);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2847, 'Wahai keturunan dinasti Bharata, dia yang, bebas dari delusi, mengenal-Ku, Pribadi tertinggi, demikian, adalah mahatahu dan memuja-Ku dengan segenap keberadaannya.', 570);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2851, 'Demikianlah, wahai yang tanpa dosa, ajaran yang paling misterius ini telah Aku sampaikan kepadamu. Dengan memahami ini, seseorang akan menjadi benar-benar bijaksana dan memenuhi kewajibannya.', 571);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2852, 'Wahai yang tanpa dosa, Aku telah mengucapkan kitab suci yang paling rahasia ini. Memahami ini, seseorang menjadi bijaksana dan memenuhi kewajibannya, wahai keturunan dinasti Bharata.', 571);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2856, 'Sang Raja berkata, “Keberanian, kemurnian pikiran, pengabdian pada meditasi pengetahuan diri, sedekah, pengendalian diri, penyembahan, studi Veda, pertapaan, dan kejujuran—', 572);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2857, 'Yang Mahamulia berkata: Tanpa rasa takut, kemurnian pikiran, ketekunan dalam pengetahuan dan yoga, amal, pengendalian organ eksternal, pengorbanan, studi kitab suci, pertapaan, dan kejujuran.', 572);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2861, 'Tidak melukai, kebenaran, bebas dari kemarahan, pelepasan, ketenangan, tidak memfitnah orang lain, belas kasihan terhadap semua makhluk, bebas dari keinginan, kelembutan, rasa malu, dan bebas dari ketidakstabilan;', 573);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2862, 'Tidak melukai, kejujuran, tanpa kemarahan, pelepasan, pengendalian diri batin, tanpa fitnah, kebaikan kepada makhluk hidup, tanpa keserakahan, kelembutan, kerendahan hati, dan bebas dari kegelisahan;', 573);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2866, 'Kemegahan, kesabaran, ketahanan, kemurnian, bebas dari kebencian dan keangkuhan—ini, wahai Arjuna, adalah milik orang yang lahir dengan takdir ilahi.', 574);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2867, 'Kekuatan, pengampunan, ketabahan, kemurnian, bebas dari kejahatan, tanpa keangkuhan—ini, wahai keturunan dinasti Bharata, adalah kualitas orang yang ditakdirkan untuk memiliki sifat ilahi.', 574);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2871, 'Kemewahan, kesombongan, keangkuhan, kemarahan, kekasaran, dan ketidaktahuan—ini, wahai Arjuna, adalah milik orang yang lahir dengan takdir iblis.', 575);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2872, 'Wahai putra Prtha, atribut orang yang ditakdirkan untuk memiliki sifat setan adalah pamer agama, kesombongan, keangkuhan, keangkuhan diri, kemarahan, kekasaran, dan ketidaktahuan.', 575);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2876, 'Takdir ilahi dikatakan mengarah pada pembebasan, sedangkan takdir iblis mengarah pada perbudakan. Jangan berduka, wahai Arjuna, karena engkau lahir dengan takdir ilahi.', 576);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2877, 'Sifat ilahi adalah Pembebasan, sedangkan yang setan dianggap sebagai perbudakan yang tak terhindarkan. Jangan berduka, wahai putra Pandu! Anda ditakdirkan untuk memiliki sifat ilahi.', 576);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2881, 'Ada dua jenis makhluk di dunia ini: yang ilahi dan yang iblis. Yang ilahi telah dijelaskan panjang lebar. Dengarkan dari-Ku, wahai Arjuna, tentang yang iblis.', 577);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2882, 'Di dunia ini, ada dua jenis ciptaan makhluk: yang ilahi dan yang setan. Yang ilahi telah dibicarakan secara rinci. Dengarkan tentang yang setan dari-Ku, wahai putra Prtha.', 577);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2886, 'Orang-orang iblis tidak mengetahui tindakan maupun pelepasan. Mereka tidak memiliki kebersihan, pun perilaku yang benar, pun kebenaran.', 578);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2887, 'Orang-orang setan tidak memahami apa yang harus dilakukan dan apa yang tidak boleh dilakukan; pun mereka tidak memiliki kemurnian, perilaku yang baik, atau kejujuran.', 578);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2891, 'Mereka berpendapat: \'Alam semesta ini tanpa kebenaran, tanpa dasar, dan tanpa Tuhan (Isvara). Apa lagi yang bisa ada tanpa kausalitas timbal balik? Ini memiliki nafsu sebagai penyebabnya.\'', 579);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2892, 'Mereka mengatakan bahwa dunia itu tidak nyata, tidak memiliki dasar, dan tanpa Tuhan. Itu lahir dari persatuan timbal balik yang disebabkan oleh nafsu! Penyebab lain apa yang mungkin ada?', 579);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2896, 'Dengan pandangan ini, orang-orang yang kehilangan jiwa dan pemahaman yang lemah ini melakukan perbuatan kejam, yang mengarah pada kehancuran dunia.', 580);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2897, 'Berpegang pada pandangan ini, mereka yang berkarakter bejat, berakal lemah, dan melakukan tindakan yang menakutkan dan berbahaya, menjadi kuat dan membawa kehancuran bagi dunia.', 580);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2901, 'Beralih ke keinginan yang tak terpuaskan, merebut kekayaan yang diperoleh secara tidak adil melalui delusi, dan mengikuti sumpah-sumpah yang tidak saleh, mereka bertindak, penuh dengan pamer, kesombongan, dan keangkuhan.', 581);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2902, 'Menyerahkan diri pada nafsu yang tak terpadamkan, dipenuhi kesombongan, kebanggaan, dan keangkuhan, mengadopsi kata sifat yang buruk karena delusi, dan memiliki tekad yang tidak murni, mereka terlibat dalam tindakan.', 581);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2906, 'Terobsesi oleh kekhawatiran tak terbatas yang berakhir dengan kehancuran, memandang kenikmatan keinginan sebagai tujuan tertinggi mereka, dan yakin bahwa inilah segalanya;', 582);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2907, 'Dikelilingi oleh kekhawatiran tak terhitung yang hanya berakhir dengan kematian, menganggap kenikmatan objek yang diinginkan sebagai tujuan tertinggi, mereka merasa yakin bahwa inilah segalanya.', 582);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2911, 'Terikat oleh ratusan belenggu harapan, menyerah pada keinginan dan kemarahan, mereka berusaha secara tidak adil mengumpulkan kekayaan untuk pemuasan keinginan mereka.', 583);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2912, 'Terikat oleh ratusan belenggu dalam bentuk harapan, menyerahkan diri sepenuhnya pada nafsu dan kemarahan, mereka berusaha mengumpulkan kekayaan melalui cara-cara kotor untuk kenikmatan objek-objek yang diinginkan.', 583);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2916, '\'Ini telah kudapatkan hari ini, dan keinginan ini akan kucapai. Kekayaan ini milikku, dan ini juga akan menjadi milikku di kemudian hari.', 584);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2917, '\'Aku telah mendapatkan ini hari ini; aku akan memperoleh objek yang diinginkan ini. Ini sudah di tangan; dan kekayaan ini juga akan datang kepadaku.\'', 584);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2921, '\'Aku telah membunuh musuh ini, dan aku akan membunuh yang lain juga. Aku adalah Tuhan, penikmat, yang sukses, yang kuat, dan yang bahagia.\'', 585);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2922, '\'Aku telah membunuh musuh itu, dan aku akan membunuh yang lain juga. Aku adalah Tuhan, Sang Penikmat, teguh, perkasa, dan gembira.\'', 585);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2926, '\'Aku kaya dan berdarah biru; siapa lagi yang setara denganku? Aku akan berkorban, aku akan bersedekah, aku akan bersukacita.\' Demikianlah mereka berpikir, tertipu oleh ketidaktahuan.', 586);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2927, '\'Aku kaya dan berdarah bangsawan; siapa lagi yang seperti aku? Aku akan melakukan pengorbanan; aku akan memberi, aku akan bersukacita\'—demikianlah, mereka tertipu oleh kurangnya kebijaksanaan dalam berbagai cara.', 586);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2931, 'Bingung oleh banyak pikiran, terjerat dalam jaring delusi, kecanduan kesenangan indrawi, mereka jatuh ke dalam Naraka yang menyedihkan.', 587);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2932, 'Bingung oleh berbagai pikiran, terjerat dalam jaring delusi, dan tenggelam dalam kenikmatan objek-objek yang diinginkan, mereka jatuh ke dalam neraka yang kotor.', 587);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2936, 'Sombong, merasa diri cukup, mabuk oleh kekayaan dan kebanggaan, mereka melakukan pengorbanan hanya dalam nama, secara pamer dan tidak sesuai dengan ajaran kitab suci.', 588);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2937, 'Sombong, angkuh, dipenuhi kesombongan dan mabuk kekayaan, mereka melakukan pengorbanan yang hanya berupa nama, secara pamer dan tanpa mengindahkan perintah.', 588);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2941, 'Bergantung pada egoisme, kekuasaan, dan kebanggaan mereka, serta keinginan dan kemarahan mereka, orang-orang jahat ini membenci Aku di dalam tubuh mereka sendiri dan di dalam tubuh orang lain.', 589);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2942, 'Menggunakan egoisme, kekuasaan, keangkuhan, nafsu, dan kemarahan, membenci-Ku dalam tubuh mereka sendiri dan orang lain, mereka menjadi iri secara alami.', 589);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2946, 'Mereka yang membenci, kejam, yang paling keji dan paling tidak beruntung di antara umat manusia, Aku lemparkan selamanya ke dalam siklus kelahiran dan kematian, ke dalam rahim iblis.', 590);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2947, 'Aku melemparkan selamanya orang-orang yang membenci, kejam, pelaku kejahatan di dunia, yang paling keji dari umat manusia, sungguh ke dalam golongan setan.', 590);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2951, 'Jatuh ke rahim iblis dalam kelahiran demi kelahiran, orang-orang yang tertipu ini, tidak mencapai Aku, semakin tenggelam ke tingkat terendah, wahai Arjuna.', 591);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2952, 'Terlahir di antara spesies setan dalam kelahiran demi kelahiran, jiwa-jiwa, tanpa pernah mencapai-Ku, wahai putra Kunti, mencapai kondisi yang lebih rendah dari itu.', 591);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2956, 'Keinginan, kemarahan, dan keserakahan—ini adalah tiga gerbang menuju Neraka, yang merusak diri. Oleh karena itu, seseorang harus meninggalkan ketiganya.', 592);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2957, 'Pintu neraka ini, yang menghancurkan jiwa, terdiri dari tiga jenis—nafsu, kemarahan, dan keserakahan. Oleh karena itu, seseorang harus meninggalkan ketiganya.', 592);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2961, 'Dia yang telah dibebaskan dari tiga gerbang kegelapan ini, wahai Arjuna, bekerja demi kebaikan diri; demikianlah, mereka mencapai keadaan tertinggi.', 593);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2962, 'Wahai putra Kunti, seseorang yang bebas dari ketiga pintu kegelapan ini berusaha demi kebaikan jiwa; dengan demikian, mereka mencapai Tujuan tertinggi.', 593);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2966, 'Dia yang, meninggalkan perintah-perintah Sastra, bertindak di bawah pengaruh keinginan, tidak mencapai kesempurnaan maupun kesenangan, pun keadaan tertinggi.', 594);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2967, 'Mengabaikan perintah-perintah kitab suci, dia yang bertindak di bawah dorongan nafsu tidak mencapai kesempurnaan, pun kebahagiaan, pun tujuan tertinggi.', 594);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2971, 'Oleh karena itu, biarkan Sastra menjadi otoritasmu untuk menentukan apa yang harus dilakukan dan apa yang tidak boleh dilakukan. Mengetahui apa yang diperintahkan dalam perintah Sastra, engkau harus melakukan pekerjaan di sini.', 595);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2972, 'Oleh karena itu, kitab suci adalah otoritas Anda dalam menentukan apa yang harus dilakukan dan apa yang tidak boleh dilakukan. Setelah memahami tugas Anda sebagaimana yang disajikan oleh perintah kitab suci, Anda harus melakukannya di sini.', 595);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2976, 'Arjuna berkata, “Sekarang, wahai Krsna, bagaimana kedudukan atau dasar mereka yang, meskipun mengabaikan perintah Sastra, masih menyembah dengan iman? Apakah itu Sattva, Rajas, atau Tamas?”', 596);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2977, 'Arjuna berkata, Tetapi, wahai Krsna, bagaimana keadaan mereka yang, diberkahi dengan iman, melakukan pengorbanan, mendistribusikan kekayaan, dll. untuk menghormati dewa dan lainnya, sementara mengabaikan perintah-perintah kitab suci? Apakah itu sattva, rajas, atau tamas?', 596);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2981, 'Sang Raja berkata, “Iman makhluk berbadan itu ada tiga macam, lahir dari sifat bawaannya sendiri dan terdiri dari Sattva, Rajas, dan Tamas. Sekarang, dengarkanlah dari-Ku tentang hal itu.”', 597);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2982, 'Yang Mahamulia berkata, “Iman makhluk berbadan itu, yang lahir dari sifat mereka sendiri, adalah tiga macam—lahir dari sattva, rajas, dan tamas. Dengarkanlah tentang itu.”', 597);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2986, 'Wahai Arjuna, iman setiap orang sesuai dengan organ batin mereka (Antahkarana). Setiap orang terdiri dari iman; apa pun iman mereka, itulah diri mereka.', 598);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2987, 'Wahai keturunan dinasti Bharata, iman semua makhluk sesuai dengan pikiran mereka. Orang ini terdiri dari iman sebagai faktor dominan. Dia memang adalah apa pun yang imannya.', 598);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2991, 'Tipe sattvic menyembah para dewa; tipe rajasic menyembah yaksas dan rakshasa; dan yang lain, tipe tamasic, menyembah leluhur yang telah meninggal dan pasukan bhuta.', 599);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2992, 'Mereka yang memiliki kualitas sattva menyembah dewa; mereka yang memiliki rajas, dewa-dewa kecil dan iblis; dan mereka yang memiliki tamas, hantu dan pasukan roh.', 599);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2996, 'Orang-orang yang mempraktikkan laku tapa yang mengerikan yang tidak diperintahkan oleh kitab suci, yang suka pamer dan sombong, dan didorong oleh kekuatan keinginan dan nafsu indrawi;', 600);
INSERT INTO verse_translations (id, content, verse_id) VALUES (2997, 'Orang-orang yang, karena suka pamer dan bangga, dan memiliki gairah, keterikatan, dan kekuatan, melakukan pertapaan yang keras yang tidak disetujui oleh kitab suci;', 600);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3001, 'Orang-orang bodoh ini, yang menyiksa kumpulan elemen dalam tubuh mereka dan Aku, yang berdiam di dalam tubuh—ketahuilah mereka adalah iblis dalam tekad mereka.', 601);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3002, '(Dan mereka yang,) karena tidak membedakan, menyiksa semua organ dalam tubuh serta bahkan Aku yang bersemayam dalam tubuh—ketahuilah mereka memiliki keyakinan setan.', 601);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3006, 'Bahkan makanan yang disukai semua orang ada tiga macam; demikian pula kurban, laku tapa, dan pemberian. Dengarkanlah perbedaan di antara mereka.', 602);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3007, 'Makanan, yang disayangi semua orang, ada tiga jenis; demikian pula pengorbanan, pertapaan, dan amal juga ada tiga jenis. Dengarkan klasifikasi dari hal-hal ini.', 602);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3011, 'Makanan yang meningkatkan umur panjang, kewaspadaan intelektual, kekuatan, kesehatan, kesenangan, dan kebahagiaan, serta yang manis, berminyak, bergizi, dan menyenangkan, disukai oleh pria sattvic.', 603);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3012, 'Makanan yang meningkatkan kehidupan, keteguhan pikiran, kekuatan, kesehatan, kebahagiaan, dan kegembiraan, serta yang berair, berminyak, substansial, dan menyenangkan, disukai oleh orang yang diberkahi dengan sattva.', 603);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3016, 'Makanan yang pahit, asam, sangat asin, terlalu panas, sangat pedas, kering, dan membakar semuanya disukai oleh pria Rajasika; mereka menyebabkan rasa sakit, kesedihan, dan penyakit.', 604);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3017, 'Makanan yang pahit, asam, asin, sangat panas, pedas, kering, dan membakar, menghasilkan rasa sakit, kesedihan, dan penyakit, disukai oleh orang yang memiliki rajas.', 604);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3021, 'Makanan yang sudah basi, hambar, busuk, busuk, ditolak, kotor, disukai oleh mereka yang memiliki sifat Tamasik.', 605);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3022, 'Makanan yang tidak dimasak dengan benar, kurang sari, busuk dan basi, serta yang tidak layak untuk pengorbanan, disukai oleh orang yang memiliki tamas.', 605);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3026, 'Kurban (penyembahan) yang ditandai dengan Sattva adalah apa yang dipersembahkan oleh mereka yang tidak menginginkan buah dan memiliki keyakinan bahwa itu harus dilakukan sebagaimana yang ditentukan dalam kitab suci.', 606);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3027, 'Pengorbanan itu yang sesuai dengan perintah, dilakukan oleh orang-orang yang tidak mendambakan hasil, dan dengan keyakinan mental bahwa itu pasti wajib, dilakukan melalui sattva.', 606);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3031, 'Tetapi kurban yang dipersembahkan dengan maksud memperoleh pahala dan untuk pamer, ketahuilah itu, wahai Arjuna, bersifat Rajasika.', 607);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3032, 'Tetapi pengorbanan itu yang dilakukan dengan tujuan untuk hasil, serta untuk pamer—ketahuilah pengorbanan itu dilakukan melalui rajas, wahai yang terbesar di antara keturunan Bharata.', 607);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3036, 'Kurban yang tidak memiliki otoritas, yang menggunakan persembahan yang tidak disetujui oleh Sastra, yang dilakukan tanpa pembacaan himne dan tanpa pemberian dan iman—itu, kata mereka, ditandai dengan Tamas.', 608);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3037, 'Mereka menyatakan bahwa pengorbanan itu \'dilakukan melalui tamas\' yang bertentangan dengan perintah, di mana tidak ada makanan yang dibagikan, di mana tidak ada mantra yang digunakan, di mana tidak ada persembahan yang diberikan kepada para imam, dan yang tanpa iman.', 608);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3041, 'Menyembah para dewa, dua kali lahir, para guru, para yang tercerahkan, mempraktikkan kemurnian, kejujuran, kesucian, dan tidak melukai—ini semua disebut laku tapa tubuh.', 609);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3042, 'Pemujaan dewa, dua kali lahir, orang yang terhormat, dan orang bijak; kemurnian, kejujuran, selibat, dan tanpa luka—ini dikatakan sebagai pertapaan fisik.', 609);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3046, 'Ucapan yang tidak menyebabkan kejutan, jujur, menyenangkan, dan bermanfaat, dan juga praktik pembacaan kitab suci, disebut laku tapa ucapan.', 610);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3047, 'Ucapan yang tidak menyebabkan rasa sakit, yang benar, menyenangkan, dan bermanfaat; serta praktik mempelajari kitab suci—dikatakan sebagai pertapaan ucapan.', 610);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3051, 'Ketenangan pikiran, kebajikan, keheningan, pengendalian diri, dan kemurnian pikiran—ini dikenal sebagai laku tapa pikiran.', 611);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3052, 'Ketenangan pikiran, kelembutan, kehati-hatian, penarikan pikiran, kemurnian hati—inilah yang disebut pertapaan mental.', 611);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3056, 'Tiga kali lipat laku tapa, yang dipraktikkan dengan iman tertinggi oleh orang-orang yang tidak menginginkan buah dan mengabdikan diri—mereka menyebutnya laku tapa Sattva.', 612);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3057, 'Ketika pertapaan tiga kali lipat itu dilakukan dengan iman tertinggi oleh orang-orang yang tidak mendambakan hasil dan mengendalikan diri, mereka menyebutnya lahir dari sattva.', 612);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3061, 'Laku tapa yang dipraktikkan dengan pamer untuk memperoleh rasa hormat, pujian, dan penghormatan, di sini dikatakan bersifat Rajasa. Ia tidak stabil dan tidak kekal.', 613);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3062, 'Pertapaan itu yang dilakukan untuk mendapatkan nama, dihormati dan disembah, dan juga secara pamer—itu disebut lahir dari rajas, milik dunia ini, tidak pasti, dan sementara.', 613);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3066, 'Laku tapa yang dipraktikkan dari gagasan yang salah dengan menyiksa diri atau untuk melukai orang lain dikatakan bersifat Tamasika.', 614);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3067, 'Pertapaan itu yang dilakukan dengan niat bodoh, menyebabkan rasa sakit pada diri sendiri atau untuk menghancurkan orang lain, dikatakan lahir dari tamas.', 614);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3071, 'Pemberian yang diberikan dengan perasaan bahwa itu adalah kewajiban seseorang untuk memberi, kepada orang yang tidak membalas, pada tempat dan waktu yang tepat kepada orang yang layak—itu dikatakan bersifat sattvika.', 615);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3072, 'Pemberian itu disebut lahir dari sattva yang diberikan dengan gagasan bahwa itu harus diberikan, kepada orang yang tidak akan membalas budi, di tempat yang tepat, waktu yang tepat, dan kepada orang yang tepat.', 615);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3076, 'Tetapi yang diberikan sebagai pertimbangan untuk sesuatu yang diterima atau dengan harapan imbalan di masa depan, atau dengan enggan, disebut pemberian Rajasika.', 616);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3077, 'Tetapi hadiah yang diberikan dengan harapan timbal balik, atau dengan keinginan akan hasilnya, dan dengan enggan, dianggap lahir dari rajas.', 616);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3081, 'Pemberian yang diberikan di tempat yang salah, waktu yang salah, kepada penerima yang tidak layak, tanpa rasa hormat dan dengan penghinaan, disebut pemberian yang bersifat Tamasa.', 617);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3082, 'Hadiah yang diberikan pada tempat dan waktu yang tidak sesuai, dan kepada orang yang tidak layak, tanpa rasa hormat yang pantas dan dengan penghinaan, dinyatakan lahir dari tamas.', 617);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3086, 'Om, Tat, Sat—demikianlah, Brahman dilambangkan dengan ungkapan tiga kali lipat ini. Di masa lalu, Brahmana, Veda, dan kurban ditentukan dalam kaitannya dengan ini.', 618);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3087, '“Om-tat-sat”—ini dianggap sebagai sebutan tiga kali lipat Brahman. Para Brahmana, Veda, dan pengorbanan ditetapkan oleh itu di masa lalu.', 618);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3091, 'Oleh karena itu, tindakan kurban, pemberian, dan laku tapa yang diperintahkan Veda, yang dijelaskan oleh Veda, atau yang termasuk tiga kasta pertama, selalu dimulai setelah mengucapkan Om di awal.', 619);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3092, 'Oleh karena itu, tindakan pengorbanan, amal, dan pertapaan, sebagaimana yang ditetapkan melalui perintah, dari mereka yang mempelajari dan menjelaskan Veda, selalu dimulai setelah mengucapkan suku kata \'Om\'.', 619);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3096, 'Tindakan kurban, laku tapa, dan berbagai pemberian dilakukan tanpa bertujuan untuk pahala oleh mereka yang mencari pembebasan, setelah mengucapkan Tat.', 620);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3097, 'Setelah mengucapkan kata “tat,” tindakan pengorbanan dan pertapaan, serta berbagai tindakan amal, dilakukan tanpa mempedulikan hasil oleh orang-orang yang bercita-cita untuk pembebasan.', 620);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3101, 'Istilah "Sat" ini digunakan dalam arti keberadaan dan kebaikan. Dan demikian juga, wahai Arjuna, kata "Sat" diterapkan pada tindakan yang menguntungkan.', 621);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3102, 'Kata “sat” ini digunakan untuk sesuatu yang muncul dan seseorang menjadi baik. Demikian pula, wahai putra Prtha, kata “sat” digunakan untuk upacara yang menguntungkan.', 621);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3106, 'Pengabdian pada kurban, laku tapa, dan pemberian juga disebut Sat; dan demikian pula, setiap tindakan untuk tujuan tersebut dikenal sebagai Sat.', 622);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3107, 'Dan keteguhan dalam pengorbanan, pertapaan, dan amal disebut sebagai sat (baik). Dan bahkan tindakan yang dimaksudkan untuk ini, sungguh, disebut sebagai sat.', 622);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3111, 'Persembahan atau pemberian apa pun yang dibuat, laku tapa apa pun yang dilakukan, dan tindakan apa pun yang dilakukan tanpa iman, itu disebut Asat, wahai Arjuna. Itu tidak bernilai di sini maupun di akhirat.', 623);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3112, 'Wahai putra Prtha, apa pun yang dipersembahkan dalam pengorbanan dan diberikan dalam amal, serta pertapaan apa pun yang dilakukan atau apa pun yang dilakukan tanpa iman, dikatakan tidak berguna. Dan itu tidak ada konsekuensinya setelah kematian, pun di sini.', 623);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3116, 'Arjuna berkata, “Aku ingin mengetahui kebenaran tentang pelepasan dan pengorbanan secara terpisah, wahai Krishna.”', 624);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3117, 'Arjuna berkata, Wahai Hrsikesa yang perkasa, wahai pembunuh Kesi, aku ingin mengetahui secara terpisah kebenaran tentang sannyasa dan juga tentang tyaga.', 624);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3121, 'Sang Raja berkata, “Para bijak berpendapat bahwa Sannyasa adalah penyerahan semua pekerjaan yang dimotivasi oleh keinginan, dan yang bijak menyatakan Tyaga sebagai penyerahan buah dari semua pekerjaan.', 625);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3122, 'Yang Mahamulia berkata, “Orang-orang terpelajar mengetahui sannyasa sebagai penyerahan tindakan yang dilakukan dengan keinginan akan pahala. Para ahli menyebut penyerahan hasil dari semua pekerjaan \'tyaga\'.”', 625);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3126, 'Beberapa bijak mengatakan bahwa semua tindakan harus ditinggalkan sebagai kejahatan; yang lain menyatakan bahwa pekerjaan seperti kurban, pemberian, dan laku tapa tidak boleh ditinggalkan.', 626);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3127, 'Beberapa orang terpelajar mengatakan bahwa tindakan, yang penuh kejahatan, harus ditinggalkan, dan yang lain mengatakan bahwa praktik pengorbanan, amal, dan pertapaan tidak boleh ditinggalkan.', 626);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3131, 'Dengarkanlah keputusan-Ku, wahai Arjuna, mengenai pengabaian; karena pengabaian dinyatakan ada tiga macam.', 627);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3132, 'Wahai yang paling mulia di antara keturunan Bharata, dengarkanlah dari-Ku kesimpulan tegas mengenai tyaga itu. Karena, wahai yang terbesar di antara manusia, tyaga telah dengan jelas dinyatakan ada tiga macam.', 627);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3136, '(a) Tindakan kurban, pemberian, dan laku tapa tidak boleh ditinggalkan; tetapi harus dilakukan... (b) ...Karena kurban, pemberian, dan laku tapa adalah sarana pemurnian bagi orang bijak.', 628);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3137, 'Praktik pengorbanan, amal, dan pertapaan tidak boleh ditinggalkan; itu harus dilakukan. Pengorbanan, amal, dan pertapaan memang merupakan pemurni bagi orang bijak.', 628);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3141, 'Ini adalah keputusan-Ku yang telah ditetapkan dan final bahwa bahkan tindakan-tindakan ini harus dilakukan, wahai Arjuna, dengan melepaskan keterikatan dan meninggalkan buahnya.', 629);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3142, 'Tetapi bahkan tindakan-tindakan ini harus dilakukan dengan melepaskan keterikatan dan kerinduan akan hasil. Ini adalah kesimpulan-Ku yang teguh dan pasti, wahai Partha.', 629);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3146, 'Tetapi pelepasan tindakan wajib tidaklah pantas; pengabaian ini melalui delusi dinyatakan bersifat Tamasik.', 630);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3147, 'Meninggalkan tindakan wajib sehari-hari tidak dapat dibenarkan; menyerahkan hal-hal itu melalui delusi dikatakan berakar pada tamas.', 630);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3151, 'Dia yang melepaskan tindakan yang menyakitkan karena takut akan penderitaan fisik melakukan pengabaian Rajasika; namun, dia tidak memperoleh buah dari pengabaian tersebut.', 631);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3152, 'Tindakan apa pun yang mungkin seseorang tinggalkan hanya karena menyakitkan, karena takut akan penderitaan fisik, dia, yang telah melakukan pelepasan berdasarkan rajas, pasti tidak akan memperoleh buah dari pelepasan.', 631);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3156, 'Ketika tindakan dilakukan sebagaimana mestinya, wahai Arjuna, melepaskan keterikatan dan hasilnya, pengabaian semacam itu dianggap Sattvika.', 632);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3157, 'Apa pun tugas wajib yang dilakukan hanya karena itu adalah tugas wajib, wahai Arjuna, dengan melepaskan keterikatan dan juga hasilnya—pelepasan itu dianggap didasarkan pada sattva.', 632);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3161, 'Orang yang telah meninggalkan, diliputi Sattva, bijaksana, dan yang keraguannya telah dihilangkan—orang seperti itu tidak membenci tindakan yang tidak menyenangkan pun tidak melekat pada tindakan yang menyenangkan.', 633);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3162, 'Orang yang melakukan pelepasan, diliputi sattva, bijaksana dan bebas dari keraguan, tidak membenci tindakan yang tidak pantas, pun tidak menjadi terikat pada aktivitas yang pantas.', 633);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3166, 'Karena, tidak mungkin bagi orang yang berbadan untuk sepenuhnya meninggalkan tindakan. Tetapi dia yang melepaskan hasil kerjanya disebut orang yang telah meninggalkannya.', 634);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3167, 'Karena tidak mungkin bagi orang yang memegang tubuh untuk sepenuhnya meninggalkan tindakan, oleh karena itu dia yang melepaskan hasil tindakannya disebut orang yang melakukan pelepasan.', 634);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3171, 'Tidak diinginkan, diinginkan, dan campuran—demikianlah, buah kerja ada tiga macam yang menimpa setelah kematian bagi mereka yang belum melepaskan; tetapi bagi mereka yang telah melepaskan, tidak ada yang menimpa sama sekali.', 635);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3172, 'Tiga kali lipat hasil tindakan—yang tidak diinginkan, yang diinginkan, dan yang campuran—terjadi setelah kematian bagi mereka yang tidak melakukan pelepasan, tetapi tidak pernah bagi mereka yang melakukan pelepasan.', 635);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3176, 'Pelajari dari-Ku, wahai Arjuna, lima penyebab ini untuk penyelesaian semua tindakan, sebagaimana dijelaskan dalam Sankhya-krtanta—ilmu pemahaman yang tepat tentang hal-hal—untuk penyelesaian pekerjaan.', 636);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3177, 'Wahai yang perkasa, pelajari dari-Ku lima faktor ini untuk penyelesaian semua tindakan, yang telah dibicarakan dalam Wedanta, di mana tindakan berakhir.', 636);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3181, 'Tempat tindakan, pelaku, berbagai jenis organ, berbagai fungsi khas daya hidup, dan yang kelima di antara ini—Keilahian—semuanya ini.', 637);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3182, 'Lokasi, agen, berbagai jenis organ, banyak dan beragam aktivitas, dan yang ilahi adalah yang kelima di sini.', 637);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3186, 'Untuk tindakan apa pun yang dilakukan seseorang dengan tubuh, ucapan, dan pikiran, baik benar maupun salah, yaitu, diperintahkan atau dilarang oleh Sastra, lima hal berikut adalah penyebabnya:', 638);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3187, 'Tindakan apa pun yang dilakukan seseorang dengan tubuh, ucapan, dan pikiran, baik itu benar atau sebaliknya, kelima hal ini adalah penyebabnya.', 638);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3191, 'Oleh karena itu, dia yang, karena pemahaman yang tidak terlatih, hanya melihat diri sebagai pelaku, dia yang berakal jahat tidak benar-benar melihat.', 639);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3192, 'Oleh karena itu, siapa pun yang inteleknya tidak sempurna dan menganggap Diri Absolut sebagai agen, tidak melihat dengan benar dan memiliki intelek yang terdistorsi.', 639);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3196, 'Dia yang bebas dari gagasan \'Akulah pelaku,\' dan yang pemahamannya tidak tercemar, tidak membunuh, meskipun dia membunuh semua orang ini, pun tidak terikat.', 640);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3197, 'Dia yang tidak memiliki perasaan egoisme, yang inteleknya tidak tercemar, dia tidak membunuh, pun tidak terikat—bahkan dengan membunuh makhluk-makhluk ini!', 640);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3201, 'Pengetahuan, objek pengetahuan, dan yang mengetahui adalah tiga kali lipat pendorong tindakan. Alat, tindakan, dan pelaku adalah tiga kali lipat konstituen tindakan.', 641);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3202, 'Pengetahuan, objek pengetahuan, dan pengenal—ini adalah rangsangan rangsangan tiga kali lipat untuk tindakan. Pemahaman tindakan terbagi dalam tiga bagian: instrumen, objek, dan subjek.', 641);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3206, 'Pengetahuan, tindakan, dan pelaku dinyatakan dalam ilmu Guna ada tiga macam, sesuai dengan perbedaan Guna. Dengarkanlah pula sebagaimana adanya.', 642);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3207, 'Pengetahuan, tindakan, dan agen dinyatakan dalam ajaran tentang guna hanya ada tiga jenis, sesuai dengan perbedaan guna. Dengarkanlah tentangnya juga sebagaimana adanya.', 642);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3211, 'Ketahuilah bahwa pengetahuan itu bersifat sattvika, yang dengannya seseorang melihat dalam semua makhluk keberadaan yang tak berubah, tidak terbagi bahkan dalam yang terbagi.', 643);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3212, 'Ketahuilah bahwa pengetahuan itu berasal dari sattva, yang melaluinya seseorang melihat entitas tunggal, tak lapuk, tak terbagi dalam semua hal yang beraneka ragam.', 643);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3216, 'Tetapi pengetahuan yang melihat semua makhluk, karena sifat individunya, sebagai entitas yang berbeda—ketahuilah bahwa pengetahuan itu bersifat Rajasic.', 644);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3217, 'Tetapi ketahuilah bahwa pengetahuan itu berasal dari rajas, yang di antara semua hal memahami entitas-entitas yang berbeda dari berbagai jenis sebagai berbeda [sebagai memiliki diri yang berbeda].', 644);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3221, 'Tetapi pengetahuan itu dinyatakan bersifat Tamasika yang melekat pada satu tindakan seolah-olah itu adalah keseluruhan, yang tidak berdasarkan akal, dan yang salah serta tidak berarti.', 645);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3222, 'Tetapi itu (pengetahuan) dikatakan lahir dari tamas yang terbatas pada satu bentuk seolah-olah itu adalah segalanya, yang irasional, tidak peduli pada kebenaran, dan sepele.', 645);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3226, 'Tindakan wajib itu dikatakan bersifat Sattvika, yang dilakukan tanpa keterikatan, tanpa keinginan atau kebencian, dan oleh orang yang tidak mencari buah.', 646);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3227, 'Tindakan wajib sehari-hari yang dilakukan tanpa keterikatan dan tanpa suka atau duka, oleh orang yang tidak mendambakan pahala, dikatakan lahir dari sattva.', 646);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3231, 'Tetapi tindakan itu dikatakan bersifat Rajasic, yang dilakukan dengan usaha keras oleh orang yang berusaha memuaskan keinginannya dan di bawah dorongan egoisme.', 647);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3232, 'Tetapi tindakan itu dikatakan lahir dari rajas yang dilakukan oleh orang yang menginginkan hasil atau oleh orang yang egois, dan yang sangat melelahkan.', 647);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3236, 'Tindakan itu dikatakan bersifat Tamasika yang dilakukan melalui delusi, tanpa memperhatikan konsekuensi, kerugian, cedera, dan kapasitas diri sendiri.', 648);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3237, 'Tindakan itu dikatakan lahir dari tamas yang dilakukan karena delusi, tanpa mempertimbangkan konsekuensinya, kerugian, bahaya, dan kemampuan.', 648);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3241, 'Pelaku itu dikatakan bersifat Sattvika, yang bebas dari keterikatan, yang tidak membanggakan dirinya sendiri, yang diberkahi dengan keteguhan dan semangat, dan yang tidak terpengaruh oleh keberhasilan dan kegagalan.', 649);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3242, '[Ast. memperkenalkan ayat ini dengan \'Idanim kartrbhedah ucyate, Sekarang sedang disebutkan perbedaan di antara para agen.\'-Tr.] Agen yang bebas dari keterikatan [Keterikatan pada hasil atau gagasan keagenan.], tidak egois, diberkahi dengan ketabahan dan ketekunan, dan tidak terganggu oleh keberhasilan dan kegagalan dikatakan memiliki sattva.', 649);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3246, 'Pelaku itu disebut Rajasika yang bersemangat, yang mencari buah dari tindakannya, yang serakah, berbahaya, dan tidak murni, dan yang terombang-ambing oleh kegembiraan dan kesedihan.', 650);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3247, 'Agen yang memiliki keterikatan, yang mendambakan hasil tindakan, serakah, kejam secara alami, tidak bersih, dan tunduk pada suka dan duka, dinyatakan memiliki rajas.', 650);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3251, 'Pelaku itu dikatakan bersifat Tamasika yang tidak berkualitas, tidak halus, keras kepala, bejat, tidak jujur, malas, putus asa, dan lamban.', 651);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3252, 'Agen yang tidak stabil, naif, tidak fleksibel, menipu, jahat, malas, murung, dan suka menunda-nunda dikatakan memiliki tamas.', 651);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3256, 'Dengarkanlah sekarang, wahai Arjuna, pembagian tiga kali lipat Buddhi (akal) dan Dhrti (ketabahan) menurut Guna, yang akan dijelaskan secara lengkap dan terpisah.', 652);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3257, 'Wahai Dhananjaya, dengarkan klasifikasi intelek dan ketabahan, yang bersifat tiga kali lipat menurut guna, saat sedang dijelaskan secara rinci dan terpisah.', 652);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3261, 'Buddhi itu, wahai Arjuna, yang mengetahui aktivitas dan pelepasan, apa yang harus dilakukan dan apa yang tidak boleh dilakukan, ketakutan dan keberanian, ikatan dan pembebasan—Buddhi itu bersifat Sattvika.', 653);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3262, 'Wahai Partha, intelek itu lahir dari sattva yang memahami tindakan dan penarikan, kewajiban dan apa yang bukan kewajiban, sumber ketakutan dan keberanian, serta belenggu dan kebebasan.', 653);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3266, 'Buddhi yang memberikan pengetahuan yang salah tentang Dharma dan Adharma (kebalikannya) dan juga tentang apa yang harus dilakukan dan apa yang tidak boleh dilakukan, wahai Arjuna, bersifat Rajasic.', 654);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3267, 'Dengan itu seseorang salah memahami dharma dan adharma, dan juga apa yang harus dilakukan dan apa yang tidak boleh dilakukan—intelek itu, wahai Arjuna, adalah rajasic (nafsu).', 654);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3271, 'Buddhi itu, wahai Arjuna, yang, diselimuti kegelapan, menganggap Adharma sebagai Dharma dan memutarbalikkan setiap nilai, bersifat Tamasika.', 655);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3272, 'Wahai Partha, intelek itu lahir dari tamas yang, karena diselimuti kegelapan, menganggap kejahatan sebagai kebajikan, dan sungguh-sungguh melihat segala sesuatu bertentangan dengan apa adanya.', 655);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3276, 'Ketabahan itu, yang dengannya seseorang mempertahankan aktivitas pikiran dan daya hidup yang tak tergoyahkan, dan indra-indra ini, melalui yoga—ketabahan itu bersifat sattva.', 656);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3277, 'Wahai Partha, keteguhan yang tak tergoyahkan melalui konsentrasi, yang dengannya seseorang menahan fungsi pikiran, kekuatan vital, dan organ-organ—keteguhan itu lahir dari sattva.', 656);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3281, 'Dhrti itu, wahai Arjuna, yang dengannya seseorang, yang mendambakan buah, merindukan mereka dengan keterikatan yang kuat dan berpegang teguh pada kewajiban, keinginan, dan kekayaan—Dhrti itu bersifat rajasika.', 657);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3282, 'Tetapi, wahai Partha, keteguhan yang dengannya seseorang berpegang teguh pada kebenaran, hal-hal yang diinginkan, dan kekayaan, menginginkan buahnya seiring kesempatan muncul, keteguhan itu lahir dari rajas.', 657);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3286, 'Dhrti itu, yang dengannya orang bodoh tidak meninggalkan tidur, ketakutan, kesedihan, depresi, dan nafsu, wahai Arjuna, bersifat Tamas.', 658);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3287, 'Keteguhan itu dianggap lahir dari tamas, yang karena itu seseorang dengan intelek yang rusak tidak meninggalkan tidur, ketakutan, kesedihan, keputusasaan, serta sensualitas.', 658);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3291, 'Sekarang dengarkan dari-Ku, wahai Arjuna, pembagian tiga kali lipat kesenangan—bahwa di dalamnya seseorang bersukacita melalui praktik yang panjang dan di dalamnya seseorang mencapai akhir penderitaan.', 659);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3292, 'Sekarang dengarkanlah dari saya, wahai keturunan dinasti Bharata, mengenai tiga jenis kegembiraan: Kegembiraan yang dinikmati seseorang karena kebiasaan, dan tentu saja mencapai penghentian kesedihan; [S. dan S.S. mengambil baris kedua ayat ini bersama dengan ayat berikutnya yang mengacu pada kebahagiaan sattvika.-Tr.]', 659);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3296, 'Yang pada awalnya seperti racun tetapi pada akhirnya menjadi seperti eliksir, lahir dari keadaan pikiran yang tenang yang terfokus pada Diri—kesenangan seperti itu dikatakan bersifat Sattvika.', 660);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3297, 'Yang pada awalnya seperti racun, tetapi pada akhirnya disamakan dengan nektar, dan yang muncul dari kemurnian intelek seseorang—kegembiraan itu disebut lahir dari sattva.', 660);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3301, 'Kesenangan yang timbul dari kontak indra dengan objeknya, yang pada awalnya seperti eliksir tetapi pada akhirnya seperti racun, dikatakan bersifat Rajasic.', 661);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3302, 'Kegembiraan itu disebut lahir dari rajas, yang, timbul dari kontak organ dan objeknya, seperti nektar di awal tetapi seperti racun di akhir.', 661);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3306, 'Kesenangan yang, pada awal dan akhir, menipu diri, melalui tidur, kemalasan, dan kesalahan, dinyatakan bersifat tamasik.', 662);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3307, 'Kegembiraan itu dikatakan lahir dari tamas, yang, baik di awal maupun di akhir, menipu diri sendiri dan timbul dari tidur, kemalasan, dan kelalaian.', 662);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3311, 'Tidak ada makhluk, di bumi atau di antara dewa-dewa di surga, yang bebas dari ketiga Guna yang lahir dari Prakrti.', 663);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3312, 'Tidak ada entitas di dunia, pun di antara dewa-dewa di surga, yang dapat bebas dari ketiga guna yang lahir dari Alam.', 663);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3316, 'Tugas para Brahmana, Ksatriya, Waisya, dan Sudra dibagi dengan jelas, wahai Arjuna, menurut Guna mereka, yang lahir dari sifat bawaan mereka.', 664);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3317, 'Wahai penghancur musuh, tugas para Brahmana, Ksatriya, Waisya, dan Sudra telah sepenuhnya diklasifikasikan menurut guna yang lahir dari Alam.', 664);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3321, 'Pengendalian indra dan pikiran, laku tapa, kemurnian, kesabaran, kejujuran, pengetahuan, pengetahuan khusus, dan iman—semua ini merupakan tugas seorang Brahmana, yang lahir dari sifat bawaannya.', 665);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3322, 'Tugas alami para Brahmana adalah pengendalian organ internal dan eksternal, pertapaan, kemurnian, pengampunan, kejujuran, pengetahuan, serta kebijaksanaan [Pengetahuan mengacu pada pemahaman subjek yang disajikan oleh kitab suci; kebijaksanaan berarti menjadikannya materi pengalaman seseorang.], dan iman.', 665);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3326, 'Keberanian, ketidakterkalahkan, keteguhan, ketangkasan, dan tidak melarikan diri dalam pertempuran, kemurahan hati, dan keagungan adalah tugas seorang Ksatria, yang lahir dari sifat bawaannya.', 666);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3327, 'Tugas alami para Ksatriya adalah kepahlawanan, keberanian, ketabahan, kemampuan, dan tidak mundur dari pertempuran, kemurahan hati, dan keagungan.', 666);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3331, 'Pertanian, peternakan, dan perdagangan adalah tugas Waisya, yang lahir dari sifatnya. Dan tugas Sudra adalah pelayanan, yang lahir dari sifatnya.', 667);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3332, 'Tugas alami para Waisya adalah pertanian, peternakan, dan perdagangan. Tugas alami para Sudra juga berbentuk pelayanan.', 667);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3336, 'Mengabdikan diri pada tugasnya, seseorang mencapai kesempurnaan. Dengarkanlah sekarang bagaimana orang yang mengabdikan diri pada tugasnya mencapai kesempurnaan.', 668);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3337, 'Dengan mengabdikan diri pada tugasnya sendiri, seseorang mencapai keberhasilan penuh. Dengarkanlah bagaimana orang yang mengabdikan diri pada tugasnya sendiri mencapai keberhasilan.', 668);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3341, 'Dari siapa semua makhluk muncul dan oleh siapa semua ini diliputi—dengan menyembah Dia dengan tugasnya sendiri, seseorang mencapai kesempurnaan.', 669);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3342, 'Manusia mencapai keberhasilan dengan memuja, melalui tugasnya sendiri, Dia dari mana asal mula makhluk dan oleh siapa semua ini diliputi.', 669);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3346, 'Lebih baik tugas sendiri, meskipun dilakukan dengan buruk, daripada tugas orang lain, meskipun dilakukan dengan baik. Ketika seseorang melakukan tugas yang ditentukan oleh sifat bawaannya, ia tidak menanggung noda.', 670);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3347, 'Tugas seseorang sendiri, meskipun cacat, lebih unggul daripada tugas orang lain yang dilakukan dengan baik. Dengan melakukan tugas sesuai dengan sifatnya sendiri, seseorang tidak menanggung dosa.', 670);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3351, 'Seseorang tidak boleh meninggalkan pekerjaannya, wahai Arjuna, meskipun itu mungkin tidak sempurna; karena, semua usaha diselimuti oleh ketidaksempurnaan seperti api diselimuti asap.', 671);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3352, 'Wahai putra Kunti, seseorang tidak boleh meninggalkan tugas yang lahir dari dirinya, meskipun itu tidak sempurna. Karena semua usaha dikelilingi oleh kejahatan, seperti api diselimuti asap.', 671);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3356, 'Dia yang pemahamannya tidak terikat di semua sisi, yang dirinya terkendali, yang bebas dari keinginan—dia mencapai kesempurnaan tertinggi yang melampaui semua aktivitas melalui pelepasan.', 672);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3357, 'Dia yang inteleknya tetap tidak terikat pada segalanya, yang telah menaklukkan organ internalnya dan tanpa keinginan, mencapai melalui monastisisme kesempurnaan tertinggi yang terdiri dari keadaan bebas dari tugas.', 672);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3361, 'Pelajarilah dari-Ku, wahai Arjuna, secara singkat bagaimana orang yang telah mencapai kesempurnaan mencapai Brahman—puncak kesempurnaan pengetahuan.', 673);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3362, 'Pahamilah dengan pasti dari-Ku, secara singkat memang, wahai putra Kunti, proses itu yang dengannya seseorang yang telah mencapai keberhasilan mencapai Brahman, yang merupakan puncak kesempurnaan pengetahuan.', 673);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3366, 'Diberkahi dengan pemahaman yang murni, menundukkan pikiran dengan keteguhan, meninggalkan suara dan objek indra lainnya serta membuang cinta dan benci;', 674);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3367, 'Diberkahi dengan intelek yang murni dan mengendalikan diri dengan ketabahan, seseorang harus menolak objek yang dimulai dari suara [suara, sentuhan, bentuk, warna, rasa, dan bau - Tr.] dan menghilangkan keterikatan serta kebencian.', 674);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3371, 'Berlindung pada kesendirian, makan secukupnya, mengendalikan ucapan, tubuh, dan pikiran, senantiasa terlibat dalam yoga meditasi dan berlindung pada ketidakacuhan;', 675);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3372, 'Seseorang yang berlindung pada kesendirian, makan secukupnya, menguasai ucapan, tubuh, dan pikiran, yang bagi mereka meditasi dan konsentrasi selalu merupakan tugas tertinggi, dan yang memiliki ketidakacuhan;', 675);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3376, 'Meninggalkan egoisme, kekuasaan, kebanggaan, keinginan, kemarahan, dan kepemilikan, tanpa perasaan \'milikku\' dan tenang, dia menjadi layak untuk keadaan Brahman.', 676);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3377, 'Setelah membuang egoisme, kekuatan, kebanggaan, keinginan, kemarahan, dan harta benda yang berlebihan, dan menjadi bebas dari gagasan kepemilikan dan tenang, orang itu layak untuk menjadi Brahman.', 676);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3381, 'Setelah menyadari keadaan Brahman, dengan tenang, ia tidak berduka pun tidak berhasrat. Memperlakukan semua makhluk sama, ia mencapai pengabdian tertinggi kepada-Ku.', 677);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3382, 'Seseorang yang telah menjadi Brahman dan telah mencapai Diri yang diberkahi tidak berduka pun tidak berhasrat. Menjadi sama terhadap semua makhluk, ia mencapai pengabdian tertinggi kepada-Ku.', 677);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3386, 'Melalui pengabdian, ia mengenal Aku sepenuhnya—siapa dan apa Aku sebenarnya, siapa Aku dan bagaimana Aku. Mengetahui Aku demikian secara benar, ia segera masuk ke dalam Aku.', 678);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3387, 'Melalui pengabdian, ia mengenal Aku dalam kenyataan, tentang apa dan siapa Aku. Kemudian, setelah mengenal-Ku dengan benar, ia segera masuk ke dalam-Ku sesudahnya.', 678);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3391, 'Berlindung pada-Ku dan melakukan semua pekerjaan secara terus-menerus, seseorang, dengan rahmat-Ku, mencapai tempat tinggal yang abadi dan tak berubah.', 679);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3392, 'Selalu terlibat dalam semua tindakan, seseorang yang bagi mereka Aku adalah tempat perlindungan, mencapai keadaan abadi yang tak berubah melalui rahmat-Ku.', 679);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3396, 'Serahkan semua tindakanmu kepada-Ku dengan pikiranmu, jadikan-Aku sebagai tujuanmu, dan praktikan Buddhi-yoga, fokuskan pikiranmu pada-Ku selalu.', 680);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3397, 'Secara mental menyerahkan semua tindakan kepada-Ku dan menerima-Ku sebagai yang tertinggi, tetaplah pikiranmu selalu terfokus pada-Ku dengan berlindung pada konsentrasi intelekmu.', 680);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3401, 'Memusatkan pikiranmu pada-Ku, engkau akan, dengan rahmat-Ku, melintasi semua kesulitan. Jika, bagaimanapun, karena keangkuhan, engkau tidak mengindahkan Aku, engkau akan binasa.', 681);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3402, 'Dengan pikiran Anda terfokus pada-Ku, Anda akan melampaui semua kesulitan dengan rahmat-Ku. Namun, jika Anda tidak mendengarkan karena egoisme, Anda akan dihancurkan.', 681);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3406, 'Jika, dalam kesombonganmu, engkau berpikir, \'Aku tidak akan bertempur,\' tekadmu itu sia-sia. Alam akan memaksamu.', 682);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3407, 'Meskipun Anda berpikir \'Saya tidak akan bertempur\', mengandalkan egoisme, tekad Anda ini sia-sia. Sifat Anda akan mendorong Anda!', 682);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3411, 'Wahai Arjuna, terikat oleh tugasmu sendiri yang lahir dari watak batinmu sendiri, dan tidak memiliki kendali atas kehendakmu sendiri, engkau akan terpaksa melakukan hal yang sama yang sekarang tidak ingin kau lakukan karena delusi.', 683);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3412, 'Karena terikat oleh tugas Anda sendiri, yang lahir dari alam, wahai putra Kunti, Anda akan, tanpa daya, melakukan apa yang tidak ingin Anda lakukan, karena ketidakbijaksanaan.', 683);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3416, 'Tuhan, wahai Arjuna, bersemayam di hati setiap makhluk, memutar mereka berputar-putar, seolah-olah menunggangi roda, dengan kekuatan-Nya.', 684);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3417, 'Wahai Arjuna, Tuhan bersemayam di wilayah hati semua makhluk hidup, memutar melalui Maya semua makhluk seolah-olah menunggangi mesin!', 684);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3421, 'Carilah perlindungan pada-Nya saja, wahai Arjuna, dengan segenap keberadaanmu. Dengan rahmat-Nya, engkau akan menemukan kedamaian tertinggi dan tempat tinggal abadi.', 685);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3422, 'Berlindunglah hanya kepada-Nya dengan segenap keberadaanmu, wahai keturunan dinasti Bharata. Melalui rahmat-Nya, engkau akan mencapai kedamaian tertinggi dan tempat tinggal abadi.', 685);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3426, 'Demikianlah, pengetahuan, misteri dari segala misteri, telah dinyatakan kepadamu oleh-Ku. Renungkanlah sepenuhnya, lalu lakukanlah sesukamu.', 686);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3427, 'Kepadamu Aku telah menyampaikan pengetahuan ini, yang lebih rahasia dari rahasia apa pun. Renungkanlah ini secara keseluruhan dan lakukanlah sesukamu.', 686);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3431, 'Dengarkanlah lagi firman-Ku yang tertinggi, yang paling rahasia dari semua; karena Aku sangat menyayangimu, Aku memberitahumu apa yang baik bagimu.', 687);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3432, 'Dengarkanlah lagi ucapan tertinggi-Ku, yang paling mendalam dari semuanya. Karena Anda selalu sayang kepada-Ku, Aku akan berbicara apa yang bermanfaat bagi Anda.', 687);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3436, 'Pusatkan pikiranmu pada-Ku. Jadilah pemuja-Ku. Sembahlah Aku. Sujudlah di hadapan-Ku. Engkau akan datang kepada-Ku saja. Aku berjanji kepadamu dengan sungguh; karena engkau kukasihi.', 688);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3437, 'Pusatkan pikiranmu pada-Ku, baktikan dirimu kepada-Ku, persembahkan pengorbanan kepada-Ku, dan bersujudlah kepada-Ku. Dengan demikian, engkau akan datang kepada-Ku saja. Aku menjanjikan kebenaran ini kepadamu, karena engkau kukasihi.', 688);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3441, 'Meninggalkan semua Dharma sepenuhnya, carilah perlindungan pada-Ku saja. Aku akan membebaskanmu dari semua dosa; jangan berduka.', 689);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3442, 'Tinggalkan semua bentuk ritus dan kewajiban, dan berlindunglah hanya pada-Ku. Aku akan membebaskanmu dari semua dosa; jangan berduka.', 689);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3446, 'Ini tidak boleh kamu katakan kepada orang yang tidak ketat, yang bukan pemuja, pun kepada orang yang tidak berhasrat mendengarkan, pun tentu saja kepada orang yang menjelek-jelekkan Aku.', 690);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3447, 'Ini (yang telah saya ajarkan) tidak boleh diajarkan kepada orang yang tidak memiliki pertapaan, orang yang bukan penyembah, orang yang tidak memberikan pelayanan, atau orang yang mengeluh tentang saya.', 690);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3451, 'Dia yang menyatakan misteri tertinggi ini di antara para pemuja-Ku akan datang kepada-Ku, memperoleh pengabdian tertinggi kepada-Ku. Tidak ada keraguan tentang ini.', 691);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3452, 'Dia yang, dengan pengabdian tertinggi kepada-Ku, berbicara tentang rahasia tertinggi ini kepada para pemuja-Ku, tanpa ragu akan mencapai-Ku saja.', 691);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3456, 'Tidak ada seorang pun di antara manusia yang melakukan pelayanan yang lebih berharga bagi-Ku darinya. Pun tidak akan ada seorang pun di bumi yang lebih kukasihi darinya.', 692);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3457, 'Dan dibandingkan dengannya, tidak ada manusia lain yang lebih baik dalam mencapai apa yang kusukai. Selain itu, tidak akan ada orang lain di dunia yang lebih kusukai daripada dia.', 692);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3461, 'Dan dia yang akan mempelajari dialog kita, yang sesuai dengan Dharma, olehnya Aku akan disembah melalui kurban pengetahuan; itulah pandangan-Ku.', 693);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3462, 'Dan dia yang akan mempelajari percakapan suci antara kita berdua ini, yang kondusif bagi kebajikan, olehnya Aku akan dipuja melalui Pengorbanan Pengetahuan. Inilah penilaian-Ku.', 693);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3466, 'Dan orang yang mendengarkannya dengan iman dan tanpa cela, dia pun akan dibebaskan dan akan mencapai alam yang menguntungkan bagi mereka yang telah melakukan perbuatan bajik.', 694);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3467, 'Orang yang, dengan hormat dan tanpa cela, mendengarkan (ajaran) ini dengan hormat, ia pun, menjadi bebas, akan mencapai dunia yang diberkahi dari mereka yang melakukan perbuatan bajik.', 694);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3471, 'Apakah engkau telah mendengar ini, wahai Arjuna, dengan fokus satu pikiran? Apakah delusimu, yang disebabkan oleh ketidaktahuan, telah lenyap?', 695);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3472, 'Wahai Partha, apakah Anda mendengarkan ini dengan pikiran yang terfokus? Wahai Dhananjaya, apakah delusi Anda yang disebabkan oleh ketidaktahuan telah hilang?', 695);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3476, 'Arjuna berkata, “Delusiku telah dihancurkan, dan, dengan rahmat-Mu, wahai Krsna, aku telah memperoleh pengetahuan (Smrti). Bebas dari keraguan, aku berdiri teguh, dan aku akan memenuhi kehendak-Mu.”', 696);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3477, 'Arjuna berkata, “Wahai Acyuta, delusi saya telah dihancurkan dan ingatan saya telah kembali melalui rahmat Anda. Saya berdiri dengan keraguan saya hilang; saya akan mengikuti instruksi Anda.”', 696);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3481, 'Sanjaya berkata, Demikianlah aku telah mendengar dialog yang menakjubkan ini antara Vasudewa dan Arjuna yang berjiwa besar, yang membuat rambutku berdiri tegak.', 697);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3482, 'Sanjaya berkata, “Saya demikian mendengar percakapan ini antara Vasudewa dan Partha yang berjiwa besar, yang unik dan membuat bulu kuduk berdiri.”', 697);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3486, 'Dengan rahmat Vyasa, aku telah mendengar misteri tertinggi Yoga ini, yang dinyatakan secara pribadi oleh Krsna, Tuhan Yoga.', 698);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3487, 'Melalui karunia Vyasa, saya mendengar rahasia ini mengenai Yoga tertinggi dari Krishna, Tuhan Yoga, saat Dia sendiri yang berbicara!', 698);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3491, 'Wahai Raja, mengingat berulang kali dialog yang menakjubkan dan menguntungkan ini antara Sri Krishna dan Arjuna, aku bersukacita berulang kali.', 699);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3492, 'Dan, wahai Raja, saat berulang kali mengingat dialog suci yang unik ini antara Kesava dan Arjuna, saya bersukacita setiap saat.', 699);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3496, 'Dan, mengingat berulang kali bentuk Hari yang paling menakjubkan itu, keheranan saya sangat besar, wahai Raja, dan saya bersukacita berulang kali.', 700);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3497, 'Wahai Raja, berulang kali mengingat bentuk Hari yang sangat luar biasa itu, saya dilanda keheranan dan bersukacita berulang kali.', 700);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3501, 'Di mana pun ada Sri Krsna, Tuhan Yoga, dan Arjuna sang pemanah, di sana selalu ada keberuntungan, kemenangan, kekayaan, dan moralitas yang baik. Ini adalah keyakinan teguh saya.', 701);
INSERT INTO verse_translations (id, content, verse_id) VALUES (3502, 'Di mana ada Krishna, Tuhan yoga, dan di mana ada Partha, pemegang busur, di sana ada keberuntungan, kemenangan, kemakmuran, dan kehati-hatian yang tak pernah gagal. Demikian keyakinan saya.', 701);
